arasuperobject.biblemap.put("b4_9_6", new arasuperobject(4,9,6,"لكن كان قوم قد تنجسوا لانسان ميت فلم يحل لهم ان يعملوا الفصح في ذلك اليوم فتقدموا امام موسى وهرون في ذلك اليوم"));
arasuperobject.biblemap.put("b4_9_7", new arasuperobject(4,9,7,"وقال له اولئك الناس اننا متنجسون لانسان ميت. لماذا نترك حتى لا نقرّب قربان الرب في وقته بين بني اسرائيل"));
arasuperobject.biblemap.put("b4_9_8", new arasuperobject(4,9,8,"فقال لهم موسى قفوا لأسمع ما يامر به الرب من جهتكم"));
