arasuperobject.biblemap.put("b4_9_23", new arasuperobject(4,9,23,"حسب قول الرب كانوا ينزلون وحسب قول الرب كانوا يرتحلون وكانوا يحرسون حراسة الرب حسب قول الرب بيد موسى"));
