arasuperobject.biblemap.put("b4_11_21", new arasuperobject(4,11,21,"فقال موسى ست مئة الف ماش هو الشعب الذي انا في وسطه. وانت قد قلت اعطيهم لحما ليأكلوا شهرا من الزمان"));
arasuperobject.biblemap.put("b4_11_22", new arasuperobject(4,11,22,"أيذبح لهم غنم وبقر ليكفيهم ام يجمع لهم كل سمك البحر ليكفيهم"));
