arasuperobject.biblemap.put("b4_13_16", new arasuperobject(4,13,16,"هذه اسماء الرجال الذين ارسلهم موسى ليتجسّسوا الارض. ودعا موسى هوشع بن نون يشوع"));
arasuperobject.biblemap.put("b4_13_17", new arasuperobject(4,13,17,"فارسلهم موسى ليتجسّسوا ارض كنعان وقال لهم اصعدوا من هنا الى الجنوب واطلعوا الى الجبل"));
arasuperobject.biblemap.put("b4_13_18", new arasuperobject(4,13,18,"وانظروا الارض ما هي. والشعب الساكن فيها أقويّ هو ام ضعيف. قليل ام كثير"));
