arasuperobject.biblemap.put("b4_13_27", new arasuperobject(4,13,27,"واخبروه وقالوا قد ذهبنا الى الارض التي ارسلتنا اليها وحقا انها تفيض لبنا وعسلا وهذا ثمرها"));
arasuperobject.biblemap.put("b4_13_28", new arasuperobject(4,13,28,"غير ان الشعب الساكن في الارض معتزّ والمدن حصينة عظيمة جدا. وايضا قد رأينا بني عناق هناك"));
