arasuperobject.biblemap.put("b4_18_1", new arasuperobject(4,18,1,"وقال الرب لهرون انت وبنوك وبيت ابيك معك تحملون ذنب المقدس. وانت وبنوك معك تحملون ذنب كهنوتكم"));
arasuperobject.biblemap.put("b4_18_2", new arasuperobject(4,18,2,"وايضا اخوتك سبط لاوي سبط ابيك قرّبهم معك فيقترنوا بك ويوازروك وانت وبنوك قدام خيمة الشهادة"));
arasuperobject.biblemap.put("b4_18_3", new arasuperobject(4,18,3,"فيحفظون حراستك وحراسة الخيمة كلها ولكن الى امتعة القدس والى المذبح لا يقتربون لئلا يموتوا هم وانتم جميعا"));
arasuperobject.biblemap.put("b4_18_4", new arasuperobject(4,18,4,"يقترنون بك ويحفظون حراسة خيمة الاجتماع مع كل خدمة الخيمة. والاجنبي لا يقترب اليكم"));
arasuperobject.biblemap.put("b4_18_5", new arasuperobject(4,18,5,"بل تحفظون انتم حراسة القدس وحراسة المذبح لكي لا يكون ايضا سخط على بني اسرائيل"));
arasuperobject.biblemap.put("b4_18_6", new arasuperobject(4,18,6,"هانذا قد اخذت اخوتكم اللاويين من بين بني اسرائيل عطية لكم معطين للرب ليخدموا خدمة خيمة الاجتماع"));
arasuperobject.biblemap.put("b4_18_7", new arasuperobject(4,18,7,"واما انت وبنوك معك فتحفظون كهنوتكم مع ما للمذبح وما هو داخل الحجاب وتخدمون خدمة. عطية اعطيت كهنوتكم والاجنبي الذي يقترب يقتل"));
arasuperobject.biblemap.put("b4_18_8", new arasuperobject(4,18,8,"وقال الرب لهرون وهانذا قد اعطيتك حراسة رفائعي مع جميع اقداس بني اسرائيل لك اعطيتها حق المسحة ولبنيك فريضة دهرية"));
arasuperobject.biblemap.put("b4_18_9", new arasuperobject(4,18,9,"هذا يكون لك من قدس الاقداس من النار كل قرابينهم مع كل تقدماتهم وكل ذبائح خطاياهم وكل ذبائح آثامهم التي يردونها لي. قدس اقداس هي لك ولبنيك"));
arasuperobject.biblemap.put("b4_18_10", new arasuperobject(4,18,10,"في قدس الاقداس تاكلها. كل ذكر ياكلها. قدسا تكون لك"));
arasuperobject.biblemap.put("b4_18_11", new arasuperobject(4,18,11,"وهذه لك. الرفيعة من عطاياهم مع كل ترديدات بني اسرائيل. لك اعطيتها ولبنيك وبناتك معك فريضة دهرية. كل طاهر في بيتك ياكل منها"));
arasuperobject.biblemap.put("b4_18_12", new arasuperobject(4,18,12,"كل دسم الزيت وكل دسم المسطار والحنطة ابكارهنّ التي يعطونها للرب لك اعطيتها"));
arasuperobject.biblemap.put("b4_18_13", new arasuperobject(4,18,13,"ابكار كل ما في ارضهم التي يقدمونها للرب لك تكون. كل طاهر في بيتك ياكلها"));
arasuperobject.biblemap.put("b4_18_14", new arasuperobject(4,18,14,"كل محرم في اسرائيل يكون لك"));
arasuperobject.biblemap.put("b4_18_15", new arasuperobject(4,18,15,"كل فاتح رحم من كل جسد يقدمونه للرب من الناس ومن البهائم يكون لك غير انك تقبل فداء بكر الانسان وبكر البهيمة النجسة تقبل فداءه"));
arasuperobject.biblemap.put("b4_18_16", new arasuperobject(4,18,16,"وفداؤه من ابن شهر تقبله حسب تقويمك فضة خمسة شواقل على شاقل القدس. هو عشرون جيرة"));
arasuperobject.biblemap.put("b4_18_17", new arasuperobject(4,18,17,"لكن بكر البقر او بكر الضأن او بكر المعز لا تقبل فداءه. انه قدس. بل ترش دمه على المذبح وتوقد شحمه وقودا رائحة سرور للرب"));
arasuperobject.biblemap.put("b4_18_18", new arasuperobject(4,18,18,"ولحمه يكون لك كصدر الترديد والساق اليمنى يكون لك"));
arasuperobject.biblemap.put("b4_18_19", new arasuperobject(4,18,19,"جميع رفائع الاقداس التي يرفعها بنو اسرائيل للرب اعطيتها لك ولبنيك وبناتك معك حقا دهريا. ميثاق ملح دهريا امام الرب لك ولزرعك معك"));
arasuperobject.biblemap.put("b4_18_20", new arasuperobject(4,18,20,"وقال الرب لهرون لا تنال نصيبا في ارضهم ولا يكون لك قسم في وسطهم. انا قسمك ونصيبك في وسط بني اسرائيل"));
arasuperobject.biblemap.put("b4_18_21", new arasuperobject(4,18,21,"واما بنو لاوي فاني قد اعطيتهم كل عشر في اسرائيل ميراثا عوض خدمتهم التي يخدمونها خدمة خيمة الاجتماع"));
arasuperobject.biblemap.put("b4_18_22", new arasuperobject(4,18,22,"فلا يقترب ايضا بنو اسرائيل الى خيمة الاجتماع ليحملوا خطية للموت"));
arasuperobject.biblemap.put("b4_18_23", new arasuperobject(4,18,23,"بل اللاويون يخدمون خدمة خيمة الاجتماع وهم يحملون ذنبهم فريضة دهرية في اجيالكم. وفي وسط اسرائيل لا ينالون نصيبا"));
arasuperobject.biblemap.put("b4_18_24", new arasuperobject(4,18,24,"ان عشور بني اسرائيل التي يرفعونها للرب رفيعة قد اعطيتها للاويين نصيبا. لذلك قلت لهم في وسط بني اسرائيل لا ينالون نصيبا"));
arasuperobject.biblemap.put("b4_18_25", new arasuperobject(4,18,25,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b4_18_26", new arasuperobject(4,18,26,"واللاويون تكلمهم وتقول لهم. متى اخذتم من بني اسرائيل العشر الذي اعطيتكم اياه من عندهم نصيبا لكم ترفعون منه رفيعة الرب عشرا من العشر"));
arasuperobject.biblemap.put("b4_18_27", new arasuperobject(4,18,27,"فيحسب لكم انه رفيعتكم كالحنطة من البيدر وكالملء من المعصرة"));
arasuperobject.biblemap.put("b4_18_28", new arasuperobject(4,18,28,"فهكذا ترفعون انتم ايضا رفيعة الرب من جميع عشوركم التي تاخذون من بني اسرائيل. تعطون منها رفيعة الرب لهرون الكاهن"));
arasuperobject.biblemap.put("b4_18_29", new arasuperobject(4,18,29,"من جميع عطاياكم ترفعون كل رفيعة الرب من الكل دسمه المقدس منه"));
arasuperobject.biblemap.put("b4_18_30", new arasuperobject(4,18,30,"وتقول لهم. حين ترفعون دسمه منه يحسب للاويين كمحصول البيدر وكمحصول المعصرة"));
arasuperobject.biblemap.put("b4_18_31", new arasuperobject(4,18,31,"وتأكلونه في كل مكان انتم وبيوتكم لانه اجرة لكم عوض خدمتكم في خيمة الاجتماع"));
arasuperobject.biblemap.put("b4_18_32", new arasuperobject(4,18,32,"ولا تتحملون بسببه خطية اذا رفعتم دسمه منه. واما اقداس بني اسرائيل فلا تدنسوها لئلا تموتوا"));
arasuperobject.biblemap.put("b4_19_1", new arasuperobject(4,19,1,"وكلم الرب موسى وهرون قائلا"));
arasuperobject.biblemap.put("b4_19_2", new arasuperobject(4,19,2,"هذه فريضة الشريعة التي امر بها الرب قائلا. كلم بني اسرائيل ان ياخذوا اليك بقرة حمراء صحيحة لا عيب فيها ولم يعل عليها نير"));
arasuperobject.biblemap.put("b4_19_3", new arasuperobject(4,19,3,"فتعطونها لألعازار الكاهن فتخرج الى خارج المحلّة وتذبح قدامه"));
arasuperobject.biblemap.put("b4_19_4", new arasuperobject(4,19,4,"ويأخذ ألعازار الكاهن من دمها باصبعه وينضح من دمها الى جهة وجه خيمة الاجتماع سبع مرات"));
arasuperobject.biblemap.put("b4_19_5", new arasuperobject(4,19,5,"وتحرق البقرة امام عينيه. يحرق جلدها ولحمها ودمها مع فرثها"));
arasuperobject.biblemap.put("b4_19_6", new arasuperobject(4,19,6,"ويأخذ الكاهن خشب ارز وزوفا وقرمزا ويطرحهنّ في وسط حريق البقرة"));
arasuperobject.biblemap.put("b4_19_7", new arasuperobject(4,19,7,"ثم يغسل الكاهن ثيابه ويرحض جسده بماء وبعد ذلك يدخل المحلّة ويكون الكاهن نجسا الى المساء"));
arasuperobject.biblemap.put("b4_19_8", new arasuperobject(4,19,8,"والذي احرقها يغسل ثيابه بماء ويرحض جسده بماء ويكون نجسا الى المساء"));
arasuperobject.biblemap.put("b4_19_9", new arasuperobject(4,19,9,"ويجمع رجل طاهر رماد البقرة ويضعه خارج المحلّة في مكان طاهر فتكون لجماعة بني اسرائيل في حفظ ماء نجاسة. انها ذبيحة خطية"));
arasuperobject.biblemap.put("b4_19_10", new arasuperobject(4,19,10,"والذي جمع رماد البقرة يغسل ثيابه ويكون نجسا الى المساء. فتكون لبني اسرائيل وللغريب النازل في وسطهم فريضة دهرية"));
arasuperobject.biblemap.put("b4_19_11", new arasuperobject(4,19,11,"من مسّ ميتا ميتة انسان ما يكون نجسا سبعة ايام"));
arasuperobject.biblemap.put("b4_19_12", new arasuperobject(4,19,12,"يتطهر به في اليوم الثالث وفي اليوم السابع يكون طاهرا. وان لم يتطهّر في اليوم الثالث ففي اليوم السابع لا يكون طاهرا"));
arasuperobject.biblemap.put("b4_19_13", new arasuperobject(4,19,13,"كل من مسّ ميتا ميتة انسان قد مات ولم يتطهّر ينجّس مسكن الرب. فتقطع تلك النفس من اسرائيل. لان ماء النجاسة لم يرشّ عليها تكون نجسة. نجاستها لم تزل فيها"));
arasuperobject.biblemap.put("b4_19_14", new arasuperobject(4,19,14,"هذه هي الشريعة. اذا مات انسان في خيمة كل من دخل الخيمة وكل من كان في الخيمة يكون نجسا سبعة ايام"));
arasuperobject.biblemap.put("b4_19_15", new arasuperobject(4,19,15,"وكل اناء مفتوح ليس عليه سداد بعصابة فانه نجس"));
arasuperobject.biblemap.put("b4_19_16", new arasuperobject(4,19,16,"وكل من مسّ على وجه الصحراء قتيلا بالسيف او ميتا او عظم انسان او قبرا يكون نجسا سبعة ايام"));
arasuperobject.biblemap.put("b4_19_17", new arasuperobject(4,19,17,"فيأخذون للنجس من غبار حريق ذبيحة الخطية ويجعل عليه ماء حيّا في اناء"));
arasuperobject.biblemap.put("b4_19_18", new arasuperobject(4,19,18,"ويأخذ رجل طاهر زوفا ويغمسها في الماء وينضحه على الخيمة وعلى جميع الامتعة وعلى الانفس الذين كانوا هناك وعلى الذي مسّ العظم او القتيل او الميت او القبر"));
arasuperobject.biblemap.put("b4_19_19", new arasuperobject(4,19,19,"ينضح الطاهر على النجس في اليوم الثالث واليوم السابع. ويطهّره في اليوم السابع فيغسل ثيابه ويرحض بماء فيكون طاهرا في المساء"));
arasuperobject.biblemap.put("b4_19_20", new arasuperobject(4,19,20,"واما الانسان الذي يتنجّس ولا يتطهّر فتباد تلك النفس من بين الجماعة لانه نجّس مقدس الرب. ماء النجاسة لم يرش عليه. انه نجس"));
arasuperobject.biblemap.put("b4_19_21", new arasuperobject(4,19,21,"فتكون لهم فريضة دهرية. والذي رشّ ماء النجاسة يغسل ثيابه والذي مسّ ماء النجاسة يكون نجسا الى المساء"));
arasuperobject.biblemap.put("b4_19_22", new arasuperobject(4,19,22,"وكل ما مسّه النجس يتنجّس والنفس التي تمسّ تكون نجسة الى المساء"));
arasuperobject.biblemap.put("b4_20_1", new arasuperobject(4,20,1,"وأتى بنو اسرائيل الجماعة كلها الى برية صين في الشهر الاول واقام الشعب في قادش وماتت هناك مريم ودفنت هناك"));
arasuperobject.biblemap.put("b4_20_2", new arasuperobject(4,20,2,"ولم يكن ماء للجماعة فاجتمعوا على موسى وهرون"));
arasuperobject.biblemap.put("b4_20_3", new arasuperobject(4,20,3,"وخاصم الشعب موسى وكلموه قائلين ليتنا فنينا فناء اخوتنا امام الرب"));
arasuperobject.biblemap.put("b4_20_4", new arasuperobject(4,20,4,"لماذا أتيتما بجماعة الرب الى هذه البرية لكي نموت فيها نحن ومواشينا"));
arasuperobject.biblemap.put("b4_20_5", new arasuperobject(4,20,5,"ولماذا اصعدتمانا من مصر لتأتيا بنا الى هذا المكان الرديء. ليس هو مكان زرع وتين وكرم ورمان ولا فيه ماء للشرب"));
arasuperobject.biblemap.put("b4_20_6", new arasuperobject(4,20,6,"فأتى موسى وهرون من امام الجماعة الى باب خيمة الاجتماع وسقطا على وجهيهما فتراءى لهما مجد الرب"));
arasuperobject.biblemap.put("b4_20_7", new arasuperobject(4,20,7,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b4_20_8", new arasuperobject(4,20,8,"خذ العصا واجمع الجماعة انت وهرون اخوك وكلما الصخرة امام اعينهم ان تعطي ماءها. فتخرج لهم ماء من الصخرة وتسقي الجماعة ومواشيهم"));
arasuperobject.biblemap.put("b4_20_9", new arasuperobject(4,20,9,"فاخذ موسى العصا من امام الرب كما امره"));
arasuperobject.biblemap.put("b4_20_10", new arasuperobject(4,20,10,"وجمع موسى وهرون الجمهور امام الصخرة فقال لهم اسمعوا ايها المردة. أمن هذه الصخرة نخرج لكم ماء"));
arasuperobject.biblemap.put("b4_20_11", new arasuperobject(4,20,11,"ورفع موسى يده وضرب الصخرة بعصاه مرتين فخرج ماء غزير فشربت الجماعة ومواشيها"));
arasuperobject.biblemap.put("b4_20_12", new arasuperobject(4,20,12,"فقال الرب لموسى وهرون من اجل انكما لم تؤمنا بي حتى تقدساني امام اعين بني اسرائيل لذلك لا تدخلان هذه الجماعة الى الارض التي اعطيتهم اياها"));
arasuperobject.biblemap.put("b4_20_13", new arasuperobject(4,20,13,"هذا ماء مريبة حيث خاصم بنو اسرائيل الرب فتقدس فيهم"));
arasuperobject.biblemap.put("b4_20_14", new arasuperobject(4,20,14,"وارسل موسى رسلا من قادش الى ملك ادوم. هكذا يقول اخوك اسرائيل قد عرفت كل المشقة التي اصابتنا"));
arasuperobject.biblemap.put("b4_20_15", new arasuperobject(4,20,15,"ان آبائنا انحدروا الى مصر واقمنا في مصر اياما كثيرة واساء المصريون الينا والى آبائنا"));
arasuperobject.biblemap.put("b4_20_16", new arasuperobject(4,20,16,"فصرخنا الى الرب فسمع صوتنا وارسل ملاكا واخرجنا من مصر وها نحن في قادش مدينة في طرف تخومك"));
arasuperobject.biblemap.put("b4_20_17", new arasuperobject(4,20,17,"دعنا نمر في ارضك. لا نمر في حقل ولا في كرم ولا نشرب ماء بئر. في طريق الملك نمشي لا نميل يمينا ولا يسارا حتى نتجاوز تخومك"));
arasuperobject.biblemap.put("b4_20_18", new arasuperobject(4,20,18,"فقال له ادوم لا تمر بي لئلا اخرج للقائك بالسيف"));
arasuperobject.biblemap.put("b4_20_19", new arasuperobject(4,20,19,"فقال له بنو اسرائيل. في السكة نصعد واذا شربنا انا ومواشي من مائك ادفع ثمنه. لا شيء. امرّ برجلي فقط"));
arasuperobject.biblemap.put("b4_20_20", new arasuperobject(4,20,20,"فقال لا تمرّ وخرج ادوم للقائه بشعب غفير وبيد شديدة"));
arasuperobject.biblemap.put("b4_20_21", new arasuperobject(4,20,21,"وابى ادوم ان يسمح لاسرائيل بالمرور في تخومه فتحول اسرائيل عنه"));
arasuperobject.biblemap.put("b4_20_22", new arasuperobject(4,20,22,"فارتحل بنو اسرائيل الجماعة كلها من قادش وأتوا الى جبل هور"));
arasuperobject.biblemap.put("b4_20_23", new arasuperobject(4,20,23,"وكلم الرب موسى وهرون في جبل هور على تخم ارض ادوم قائلا"));
arasuperobject.biblemap.put("b4_20_24", new arasuperobject(4,20,24,"يضمّ هرون الى قومه لانه لا يدخل الارض التي اعطيت لبني اسرائيل لانكم عصيتم قولي عند ماء مريبة"));
arasuperobject.biblemap.put("b4_20_25", new arasuperobject(4,20,25,"خذ هرون والعازار ابنه واصعد بهما الى جبل هور"));
arasuperobject.biblemap.put("b4_20_26", new arasuperobject(4,20,26,"واخلع عن هرون ثيابه والبس ألعازار ابنه اياها. فيضمّ هرون ويموت هناك"));
arasuperobject.biblemap.put("b4_20_27", new arasuperobject(4,20,27,"ففعل موسى كما امر الرب وصعدوا الى جبل هور امام اعين كل الجماعة"));
arasuperobject.biblemap.put("b4_20_28", new arasuperobject(4,20,28,"فخلع موسى عن هرون ثيابه والبس العازار ابنه اياها. فمات هرون هناك على راس الجبل. ثم انحدر موسى والعازار عن الجبل"));
arasuperobject.biblemap.put("b4_20_29", new arasuperobject(4,20,29,"فلما رأى كل الجماعة ان هرون قد مات بكى جميع بيت اسرائيل على هرون ثلاثين يوما"));
arasuperobject.biblemap.put("b4_21_1", new arasuperobject(4,21,1,"ولما سمع الكنعاني ملك عراد الساكن في الجنوب ان اسرائيل جاء في طريق اتاريم حارب اسرائيل وسبى منهم سبيا"));
arasuperobject.biblemap.put("b4_21_2", new arasuperobject(4,21,2,"فنذر اسرائيل نذرا للرب وقال ان دفعت هؤلاء القوم الى يدي احرّم مدنهم"));
arasuperobject.biblemap.put("b4_21_3", new arasuperobject(4,21,3,"فسمع الرب لقول اسرائيل ودفع الكنعانيين فحرموهم ومدنهم. فدعي اسم المكان حرمة"));
arasuperobject.biblemap.put("b4_21_4", new arasuperobject(4,21,4,"وارتحلوا من جبل هور في طريق بحر سوف ليدوروا بارض ادوم فضاقت نفس الشعب في الطريق"));
arasuperobject.biblemap.put("b4_21_5", new arasuperobject(4,21,5,"وتكلم الشعب على الله وعلى موسى قائلين لماذا اصعدتمانا من مصر لنموت في البرية لانه لا خبز ولا ماء وقد كرهت انفسنا الطعام السخيف"));
arasuperobject.biblemap.put("b4_21_6", new arasuperobject(4,21,6,"فارسل الرب على الشعب الحيّات المحرقة فلدغت الشعب فمات قوم كثيرون من اسرائيل"));
arasuperobject.biblemap.put("b4_21_7", new arasuperobject(4,21,7,"فاتى الشعب الى موسى وقالوا قد اخطأنا اذ تكلمنا على الرب وعليك فصل الى الرب ليرفع عنا الحيّات. فصلى موسى لاجل الشعب"));
arasuperobject.biblemap.put("b4_21_8", new arasuperobject(4,21,8,"فقال الرب لموسى اصنع لك حية محرقة وضعها على راية فكل من لدغ ونظر اليها يحيا"));
arasuperobject.biblemap.put("b4_21_9", new arasuperobject(4,21,9,"فصنع موسى حية من نحاس ووضعها على الراية فكان متى لدغت حية انسانا ونظر الى حية النحاس يحيا"));
arasuperobject.biblemap.put("b4_21_10", new arasuperobject(4,21,10,"وارتحل بنو اسرائيل ونزلوا في اوبوت"));
arasuperobject.biblemap.put("b4_21_11", new arasuperobject(4,21,11,"وارتحلوا من اوبوت ونزلوا في عيّي عباريم في البرية التي قبالة موآب الى شروق الشمس"));
arasuperobject.biblemap.put("b4_21_12", new arasuperobject(4,21,12,"من هناك ارتحلوا ونزلوا في وادي زارد"));
arasuperobject.biblemap.put("b4_21_13", new arasuperobject(4,21,13,"من هناك ارتحلوا ونزلوا في عبر ارنون الذي في البرية خارجا عن تخم الاموريين. لان ارنون هو تخم موآب بين موآب والاموريين"));
arasuperobject.biblemap.put("b4_21_14", new arasuperobject(4,21,14,"لذلك يقال في كتاب حروب الرب واهب في سوفة واودية ارنون"));
arasuperobject.biblemap.put("b4_21_15", new arasuperobject(4,21,15,"ومصب الاودية الذي مال الى مسكن عار واستند الى تخم موآب"));
arasuperobject.biblemap.put("b4_21_16", new arasuperobject(4,21,16,"ومن هناك الى بئر. وهي البئر حيث قال الرب لموسى اجمع الشعب فاعطيهم ماء"));
arasuperobject.biblemap.put("b4_21_17", new arasuperobject(4,21,17,"حينئذ ترنم اسرائيل بهذا النشيد. اصعدي ايتها البئر اجيبوا لها"));
arasuperobject.biblemap.put("b4_21_18", new arasuperobject(4,21,18,"بئر حفرها رؤساء حفرها شرفاء الشعب بصولجان بعصيّهم. ومن البرية الى متّانة"));
arasuperobject.biblemap.put("b4_21_19", new arasuperobject(4,21,19,"ومن متّانة الى نحليئيل ومن نحليئيل الى باموت"));
arasuperobject.biblemap.put("b4_21_20", new arasuperobject(4,21,20,"ومن باموت الى الجواء التي في صحراء موآب عند راس الفسجة التي تشرف على وجه البرية"));
arasuperobject.biblemap.put("b4_21_21", new arasuperobject(4,21,21,"وارسل اسرائيل رسلا الى سيحون ملك الاموريين قائلا"));
arasuperobject.biblemap.put("b4_21_22", new arasuperobject(4,21,22,"دعني امرّ في ارضك. لا نميل الى حقل ولا الى كرم ولا نشرب ماء بئر. في طريق الملك نمشي حتى نتجاوز تخومك"));
arasuperobject.biblemap.put("b4_21_23", new arasuperobject(4,21,23,"فلم يسمح سيحون لاسرائيل بالمرور في تخومه بل جمع سيحون جميع قومه وخرج للقاء اسرائيل الى البرية فأتى الى ياهص وحارب اسرائيل"));
arasuperobject.biblemap.put("b4_21_24", new arasuperobject(4,21,24,"فضربه اسرائيل بحد السيف وملك ارضه من ارنون الى يبّوق الى بني عمون. لان تخم بني عمون كان قويا"));
arasuperobject.biblemap.put("b4_21_25", new arasuperobject(4,21,25,"فاخذ اسرائيل كل هذه المدن واقام اسرائيل في جميع مدن الاموريين في حشبون وفي كل قراها"));
arasuperobject.biblemap.put("b4_21_26", new arasuperobject(4,21,26,"لان حشبون كانت مدينة سيحون ملك الاموريين وكان قد حارب ملك موآب الاول واخذ كل ارضه من يده حتى ارنون"));
arasuperobject.biblemap.put("b4_21_27", new arasuperobject(4,21,27,"لذلك يقول اصحاب الامثال. ايتوا الى حشبون فتبنى وتصلح مدينة سيحون"));
arasuperobject.biblemap.put("b4_21_28", new arasuperobject(4,21,28,"لان نارا خرجت من حشبون. لهيبا من قرية سيحون. اكلت عار موآب. اهل مرتفعات ارنون"));
arasuperobject.biblemap.put("b4_21_29", new arasuperobject(4,21,29,"ويل لك يا موآب. هلكت يا امة كموش. قد صيّر بنيه هاربين وبناته في السبي لملك الاموريين سيحون"));
arasuperobject.biblemap.put("b4_21_30", new arasuperobject(4,21,30,"لكن قد رميناهم. هلكت حشبون الى ديبون. واخربنا الى نوفح التي الى ميدبا"));
arasuperobject.biblemap.put("b4_21_31", new arasuperobject(4,21,31,"فاقام اسرائيل في ارض الاموريين"));
arasuperobject.biblemap.put("b4_21_32", new arasuperobject(4,21,32,"وارسل موسى ليتجسّس يعزير فأخذوا قراها وطردوا الاموريين الذين هناك"));
arasuperobject.biblemap.put("b4_21_33", new arasuperobject(4,21,33,"ثم تحوّلوا وصعدوا في طريق باشان. فخرج عوج ملك باشان للقائهم هو وجميع قومه الى الحرب في اذرعي"));
arasuperobject.biblemap.put("b4_21_34", new arasuperobject(4,21,34,"فقال الرب لموسى لا تخف منه لاني قد دفعته الى يدك مع جميع قومه وارضه. فتفعل به كما فعلت بسيحون ملك الاموريين الساكن في حشبون"));
arasuperobject.biblemap.put("b4_21_35", new arasuperobject(4,21,35,"فضربوه وبنيه وجميع قومه حتى لم يبق له شارد وملكوا ارضه"));
arasuperobject.biblemap.put("b4_22_1", new arasuperobject(4,22,1,"وارتحل بنو اسرائيل ونزلوا في عربات موآب من عبر اردن اريحا"));
arasuperobject.biblemap.put("b4_22_2", new arasuperobject(4,22,2,"ولما رأى بالاق بن صفّور جميع ما فعل اسرائيل بالاموريين"));
arasuperobject.biblemap.put("b4_22_3", new arasuperobject(4,22,3,"فزع موآب من الشعب جدا لانه كثير وضجر موآب من قبل بني اسرائيل"));
arasuperobject.biblemap.put("b4_22_4", new arasuperobject(4,22,4,"فقال موآب لشيوخ مديان الآن يلحس الجمهور كل ما حولنا كما يلحس الثور خضرة الحقل. وكان بالاق بن صفّور ملكا لموآب في ذلك الزمان"));
arasuperobject.biblemap.put("b4_22_5", new arasuperobject(4,22,5,"فارسل رسلا الى بلعام بن بعور الى فتور التي على النهر في ارض بني شعبه ليدعوه قائلا. هوذا شعب قد خرج من مصر هوذا قد غشّى وجه الارض وهو مقيم مقابلي"));
arasuperobject.biblemap.put("b4_22_6", new arasuperobject(4,22,6,"فالآن تعال والعن لي هذا الشعب. لانه اعظم مني. لعله يمكننا ان نكسره فاطرده من الارض. لاني عرفت ان الذي تباركه مبارك والذي تلعنه ملعون"));
arasuperobject.biblemap.put("b4_22_7", new arasuperobject(4,22,7,"فانطلق شيوخ موآب وشيوخ مديان وحلوان العرافة في ايديهم وأتوا الى بلعام وكلموه بكلام بالاق"));
arasuperobject.biblemap.put("b4_22_8", new arasuperobject(4,22,8,"فقال لهم بيتوا هنا الليلة فاردّ عليكم جوابا كما يكلمني الرب. فمكث رؤساء موآب عند بلعام"));
arasuperobject.biblemap.put("b4_22_9", new arasuperobject(4,22,9,"فأتى الله الى بلعام وقال من هم هؤلاء الرجال الذين عندك"));
arasuperobject.biblemap.put("b4_22_10", new arasuperobject(4,22,10,"فقال بلعام لله. بالاق بن صفّور ملك موآب قد ارسل اليّ يقول"));
arasuperobject.biblemap.put("b4_22_11", new arasuperobject(4,22,11,"هوذا الشعب الخارج من مصر قد غشّى وجه الارض. تعال الآن العن لي اياه لعلي اقدر ان احاربه واطرده"));
arasuperobject.biblemap.put("b4_22_12", new arasuperobject(4,22,12,"فقال الله لبلعام لا تذهب معهم ولا تلعن الشعب لانه مبارك"));
arasuperobject.biblemap.put("b4_22_13", new arasuperobject(4,22,13,"فقام بلعام صباحا وقال لرؤساء بالاق انطلقوا الى ارضكم لان الرب أبى ان يسمح لي بالذهاب معكم"));
arasuperobject.biblemap.put("b4_22_14", new arasuperobject(4,22,14,"فقام رؤساء موآب واتوا الى بالاق وقالوا ابى بلعام ان ياتي معنا"));
arasuperobject.biblemap.put("b4_22_15", new arasuperobject(4,22,15,"فعاد بالاق وارسل ايضا رؤساء اكثر واعظم من اولئك"));
arasuperobject.biblemap.put("b4_22_16", new arasuperobject(4,22,16,"فأتوا الى بلعام وقالوا له. هكذا قال بالاق بن صفّور. لا تمتنع من الاتيان اليّ"));
arasuperobject.biblemap.put("b4_22_17", new arasuperobject(4,22,17,"لاني اكرمك اكراما عظيما وكل ما تقول لي افعله. فتعال الآن العن لي هذا الشعب"));
arasuperobject.biblemap.put("b4_22_18", new arasuperobject(4,22,18,"فاجاب بلعام وقال لعبيد بالاق. ولو اعطاني بالاق ملء بيته فضة وذهبا لا اقدر ان اتجاوز قول الرب الهي لاعمل صغيرا او كبيرا"));
arasuperobject.biblemap.put("b4_22_19", new arasuperobject(4,22,19,"فالآن امكثوا هنا انتم ايضا هذه الليلة لاعلم ماذا يعود الرب يكلمني به"));
arasuperobject.biblemap.put("b4_22_20", new arasuperobject(4,22,20,"فأتى الله الى بلعام ليلا وقال له ان أتى الرجال ليدعوك فقم اذهب معهم. انما تعمل الامر الذي اكلمك به فقط"));
arasuperobject.biblemap.put("b4_22_21", new arasuperobject(4,22,21,"فقام بلعام صباحا وشدّ على اتانه وانطلق مع رؤساء موآب"));
arasuperobject.biblemap.put("b4_22_22", new arasuperobject(4,22,22,"فحمي غضب الله لانه منطلق ووقف ملاك الرب في الطريق ليقاومه وهو راكب على اتانه وغلاماه معه"));
arasuperobject.biblemap.put("b4_22_23", new arasuperobject(4,22,23,"فابصرت الاتان ملاك الرب واقفا في الطريق وسيفه مسلول في يده فمالت الاتان عن الطريق ومشت في الحقل فضرب بلعام الاتان ليردها الى الطريق"));
arasuperobject.biblemap.put("b4_22_24", new arasuperobject(4,22,24,"ثم وقف ملاك الرب في خندق للكروم له حائط من هنا وحائط من هناك"));
arasuperobject.biblemap.put("b4_22_25", new arasuperobject(4,22,25,"فلما ابصرت الاتان ملاك الرب زحمت الحائط وضغطت رجل بلعام بالحائط فضربها ايضا"));
arasuperobject.biblemap.put("b4_22_26", new arasuperobject(4,22,26,"ثم اجتاز ملاك الرب ايضا ووقف في مكان ضيّق حيث ليس سبيل للنكوب يمينا او شمالا"));
arasuperobject.biblemap.put("b4_22_27", new arasuperobject(4,22,27,"فلما ابصرت الاتان ملاك الرب ربضت تحت بلعام. فحمي غضب بلعام وضرب الاتان بالقضيب"));
arasuperobject.biblemap.put("b4_22_28", new arasuperobject(4,22,28,"ففتح الرب فم الاتان فقالت لبلعام. ماذا صنعت بك حتى ضربتني الآن ثلاث دفعات"));
arasuperobject.biblemap.put("b4_22_29", new arasuperobject(4,22,29,"فقال بلعام للاتان لانك ازدريت بي. لو كان في يدي سيف لكنت الآن قد قتلتك"));
arasuperobject.biblemap.put("b4_22_30", new arasuperobject(4,22,30,"فقالت الاتان لبلعام ألست انا اتانك التي ركبت عليها منذ وجودك الى هذا اليوم. هل تعوّدت ان افعل بك هكذا. فقال لا"));
arasuperobject.biblemap.put("b4_22_31", new arasuperobject(4,22,31,"ثم كشف الرب عن عيني بلعام فابصر ملاك الرب واقفا في الطريق وسيفه مسلول في يده فخرّ ساجدا على وجهه"));
arasuperobject.biblemap.put("b4_22_32", new arasuperobject(4,22,32,"فقال له ملاك الرب لماذا ضربت اتانك الآن ثلاث دفعات. هانذا قد خرجت للمقاومة لان الطريق ورطة امامي"));
arasuperobject.biblemap.put("b4_22_33", new arasuperobject(4,22,33,"فابصرتني الاتان ومالت من قدامي الآن ثلاث دفعات. ولو لم تمل من قدامي لكنت الآن قد قتلتك واستبقيتها"));
arasuperobject.biblemap.put("b4_22_34", new arasuperobject(4,22,34,"فقال بلعام لملاك الرب اخطأت. اني لم اعلم انك واقف تلقائي في الطريق. والآن ان قبح في عينيك فاني ارجع"));
arasuperobject.biblemap.put("b4_22_35", new arasuperobject(4,22,35,"فقال ملاك الرب لبلعام اذهب مع الرجال وانما تتكلم بالكلام الذي اكلمك به فقط. فانطلق بلعام مع رؤساء بالاق"));
arasuperobject.biblemap.put("b4_22_36", new arasuperobject(4,22,36,"فلما سمع بالاق ان بلعام جاء خرج لاستقباله الى مدينة موآب التي على تخم ارنون الذي في اقصى التخوم"));
arasuperobject.biblemap.put("b4_22_37", new arasuperobject(4,22,37,"فقال بالاق لبعام ألم ارسل اليك لادعوك. لماذا لم تأت اليّ. أحقا لا اقدر ان اكرمك"));
arasuperobject.biblemap.put("b4_22_38", new arasuperobject(4,22,38,"فقال بلعام لبالاق. هانذا قد جئت اليك. ألعلي الآن استطيع ان اتكلم بشيء. الكلام الذي يضعه الله في فمي به اتكلم"));
arasuperobject.biblemap.put("b4_22_39", new arasuperobject(4,22,39,"فانطلق بلعام مع بالاق وأتيا الى قرية حصوت"));
arasuperobject.biblemap.put("b4_22_40", new arasuperobject(4,22,40,"فذبح بالاق بقرا وغنما وارسل الى بلعام والى الرؤساء الذين معه"));
arasuperobject.biblemap.put("b4_22_41", new arasuperobject(4,22,41,"وفي الصباح اخذ بالاق بلعام واصعده الى مرتفعات بعل فرأى من هناك اقصى الشعب"));
arasuperobject.biblemap.put("b4_23_1", new arasuperobject(4,23,1,"فقال بلعام لبالاق ابن لي ههنا سبعة مذابح وهيّئ لي ههنا سبعة ثيران وسبعة كباش"));
arasuperobject.biblemap.put("b4_23_2", new arasuperobject(4,23,2,"ففعل بالاق كما تكلم بلعام. واصعد بالاق وبلعام ثورا وكبشا على كل مذبح"));
arasuperobject.biblemap.put("b4_23_3", new arasuperobject(4,23,3,"فقال بلعام لبالاق قف عند محرقتك فانطلق انا لعل الرب يوافي للقائي فمهما اراني اخبرك به. ثم انطلق الى رابية"));
arasuperobject.biblemap.put("b4_23_4", new arasuperobject(4,23,4,"فوافى الله بلعام. فقال له قد رتبت سبعة مذابح واصعدت ثورا وكبشا على كل مذبح"));
arasuperobject.biblemap.put("b4_23_5", new arasuperobject(4,23,5,"فوضع الرب كلاما في فم بلعام وقال ارجع الى بالاق وتكلم هكذا"));
arasuperobject.biblemap.put("b4_23_6", new arasuperobject(4,23,6,"فرجع اليه واذا هو واقف عند محرقته هو وجميع رؤساء موآب"));
arasuperobject.biblemap.put("b4_23_7", new arasuperobject(4,23,7,"فنطق بمثله وقال. من ارام أتى بي بالاق ملك موآب من جبال المشرق. تعال العن لي يعقوب وهلم اشتم اسرائيل"));
arasuperobject.biblemap.put("b4_23_8", new arasuperobject(4,23,8,"كيف العن من لم يلعنه الله وكيف اشتم من لم يشتمه الرب"));
arasuperobject.biblemap.put("b4_23_9", new arasuperobject(4,23,9,"اني من راس الصخور اراه. ومن الآكام ابصره. هوذا شعب يسكن وحده وبين الشعوب لا يحسب"));
arasuperobject.biblemap.put("b4_23_10", new arasuperobject(4,23,10,"من احصى تراب يعقوب وربع اسرائيل بعدد. لتمت نفسي موت الابرار ولتكن آخرتي كآخرتهم"));
arasuperobject.biblemap.put("b4_23_11", new arasuperobject(4,23,11,"فقال بالاق لبلعام. ماذا فعلت بي. لتشتم اعدائي اخذتك وهوذا انت قد باركتهم"));
arasuperobject.biblemap.put("b4_23_12", new arasuperobject(4,23,12,"فاجاب وقال أما الذي يضعه الرب في فمي احترص ان اتكلم به"));
arasuperobject.biblemap.put("b4_23_13", new arasuperobject(4,23,13,"فقال له بالاق هلم معي الى مكان آخر تراه منه. انما ترى اقصاءه فقط وكله لا ترى فالعنه لي من هناك"));
arasuperobject.biblemap.put("b4_23_14", new arasuperobject(4,23,14,"فأخذه الى حقل صوفيم الى راس الفسجة وبنى سبعة مذابح واصعد ثورا وكبشا على كل مذبح"));
arasuperobject.biblemap.put("b4_23_15", new arasuperobject(4,23,15,"فقال لبالاق قف هنا عند محرقتك وانا اوافي هناك"));
arasuperobject.biblemap.put("b4_23_16", new arasuperobject(4,23,16,"فوافى الرب بلعام ووضع كلاما في فمه وقال ارجع الى بالاق وتكلم هكذا"));
arasuperobject.biblemap.put("b4_23_17", new arasuperobject(4,23,17,"فأتى اليه واذا هو واقف عند محرقته ورؤساء موآب معه. فقال له بالاق ماذا تكلم به الرب"));
arasuperobject.biblemap.put("b4_23_18", new arasuperobject(4,23,18,"فنطق بمثله وقال. قم يا بالاق واسمع. اصغ اليّ يا بن صفّور"));
arasuperobject.biblemap.put("b4_23_19", new arasuperobject(4,23,19,"ليس الله انسانا فيكذب. ولا ابن انسان فيندم. هل يقول ولا يفعل او يتكلم ولا يفي"));
arasuperobject.biblemap.put("b4_23_20", new arasuperobject(4,23,20,"اني قد أمرت ان ابارك. فانه قد بارك فلا ارده"));
arasuperobject.biblemap.put("b4_23_21", new arasuperobject(4,23,21,"لم يبصر اثما في يعقوب. ولا رأى تعبا في اسرائيل. الرب الهه معه. وهتاف ملك فيه"));
arasuperobject.biblemap.put("b4_23_22", new arasuperobject(4,23,22,"الله اخرجه من مصر. له مثل سرعة الرئم"));
arasuperobject.biblemap.put("b4_23_23", new arasuperobject(4,23,23,"انه ليس عيافة على يعقوب ولا عرافة على اسرائيل. في الوقت يقال عن يعقوب وعن اسرائيل ما فعل الله"));
arasuperobject.biblemap.put("b4_23_24", new arasuperobject(4,23,24,"هوذا شعب يقوم كلبوة ويرتفع كأسد. لا ينام حتى يأكل فريسة ويشرب دم قتلى"));
arasuperobject.biblemap.put("b4_23_25", new arasuperobject(4,23,25,"فقال بالاق لبلعام لا تلعنه لعنة ولا تباركه بركة"));
arasuperobject.biblemap.put("b4_23_26", new arasuperobject(4,23,26,"فاجاب بلعام وقال لبالاق ألم اكلمك قائلا كل ما يتكلم به الرب فاياه افعل"));
arasuperobject.biblemap.put("b4_23_27", new arasuperobject(4,23,27,"فقال بالاق لبلعام هلم آخذك الى مكان آخر. عسى ان يصلح في عيني الله ان تلعنه لي من هناك"));
arasuperobject.biblemap.put("b4_23_28", new arasuperobject(4,23,28,"فاخذ بالاق بلعام الى راس فغور المشرف على وجه البرية"));
arasuperobject.biblemap.put("b4_23_29", new arasuperobject(4,23,29,"فقال بلعام لبالاق. ابن لي ههنا سبعة مذابح وهيّئ لي ههنا سبعة ثيران وسبعة كباش"));
arasuperobject.biblemap.put("b4_23_30", new arasuperobject(4,23,30,"ففعل بالاق كما قال بلعام واصعد ثورا وكبشا على كل مذبح"));
arasuperobject.biblemap.put("b4_24_1", new arasuperobject(4,24,1,"فلما رأى بلعام انه يحسن في عيني الرب ان يبارك اسرائيل لم ينطلق كالمرة الاولى والثانية ليوافي فألا بل جعل نحو البرية وجهه"));
arasuperobject.biblemap.put("b4_24_2", new arasuperobject(4,24,2,"ورفع بلعام عينيه ورأى اسرائيل حالا حسب اسباطه. فكان عليه روح الله"));
arasuperobject.biblemap.put("b4_24_3", new arasuperobject(4,24,3,"فنطق بمثله وقال. وحي بلعام بن بعور. وحي الرجل المفتوح العينين"));
arasuperobject.biblemap.put("b4_24_4", new arasuperobject(4,24,4,"وحي الذي يسمع اقوال الله. الذي يرى رؤيا القدير مطروحا وهو مكشوف العينين"));
arasuperobject.biblemap.put("b4_24_5", new arasuperobject(4,24,5,"ما احسن خيامك يا يعقوب مساكنك يا اسرائيل"));
arasuperobject.biblemap.put("b4_24_6", new arasuperobject(4,24,6,"كاودية ممتدة كجنّات على نهر كشجرات عود غرسها الرب. كارزات على مياه"));
arasuperobject.biblemap.put("b4_24_7", new arasuperobject(4,24,7,"يجري ماء من دلائه ويكون زرعه على مياه غزيرة ويتسامى ملكه على اجاج وترتفع مملكته"));
arasuperobject.biblemap.put("b4_24_8", new arasuperobject(4,24,8,"الله اخرجه من مصر. له مثل سرعة الرئم. يأكل امما مضايقيه ويقضم عظامهم ويحطم سهامه"));
arasuperobject.biblemap.put("b4_24_9", new arasuperobject(4,24,9,"جثم كأسد ربض كلبوة. من يقيمه. مباركك مبارك ولاعنك ملعون"));
arasuperobject.biblemap.put("b4_24_10", new arasuperobject(4,24,10,"فاشتعل غضب بالاق على بلعام وصفق بيديه وقال بالاق لبلعام. لتشتم اعدائي دعوتك وهوذا انت قد باركتهم الآن ثلاث دفعات"));
arasuperobject.biblemap.put("b4_24_11", new arasuperobject(4,24,11,"فالآن اهرب الى مكانك. قلت اكرمك اكراما وهوذا الرب قد منعك عن الكرامه"));
arasuperobject.biblemap.put("b4_24_12", new arasuperobject(4,24,12,"فقال بلعام لبالاق ألم اكلم ايضا رسلك الذين ارسلت اليّ قائلا"));
arasuperobject.biblemap.put("b4_24_13", new arasuperobject(4,24,13,"ولو اعطاني بالاق ملء بيته فضة وذهبا لا اقدر ان اتجاوز قول الرب لاعمل خيرا او شرا من نفسي. الذي يتكلمه الرب اياه اتكلم"));
arasuperobject.biblemap.put("b4_24_14", new arasuperobject(4,24,14,"والآن هوذا انا منطلق الى شعبي. هلم انبئك بما يفعله هذا الشعب بشعبك في آخر الايام"));
arasuperobject.biblemap.put("b4_24_15", new arasuperobject(4,24,15,"ثم نطق بمثله وقال. وحي بلعام بن بعور. وحي الرجل المفتوح العينين"));
arasuperobject.biblemap.put("b4_24_16", new arasuperobject(4,24,16,"وحي الذي يسمع اقوال الله ويعرف معرفة العلي. الذي يرى رؤيا القدير ساقطا وهو مكشوف العينين"));
arasuperobject.biblemap.put("b4_24_17", new arasuperobject(4,24,17,"اراه ولكن ليس الآن. ابصره ولكن ليس قريبا. يبرز كوكب من يعقوب ويقوم قضيب من اسرائيل فيحطم طرفي موآب ويهلك كل بني الوغى"));
arasuperobject.biblemap.put("b4_24_18", new arasuperobject(4,24,18,"ويكون ادوم ميراثا. ويكون سعير اعداؤه ميراثا. ويصنع اسرائيل ببأس"));
arasuperobject.biblemap.put("b4_24_19", new arasuperobject(4,24,19,"ويتسلط الذي من يعقوب ويهلك الشارد من مدينة"));
arasuperobject.biblemap.put("b4_24_20", new arasuperobject(4,24,20,"ثم رأى عماليق فنطق بمثله وقال. عماليق اول الشعوب واما آخرته فالى الهلاك"));
arasuperobject.biblemap.put("b4_24_21", new arasuperobject(4,24,21,"ثم رأى القيني فنطق بمثله وقال. ليكن مسكنك متينا وعشك موضوعا في صخرة"));
arasuperobject.biblemap.put("b4_24_22", new arasuperobject(4,24,22,"لكن يكون قاين للدمار حتى متى يستأسرك اشور"));
arasuperobject.biblemap.put("b4_24_23", new arasuperobject(4,24,23,"ثم نطق بمثله وقال آه من يعيش حين يفعل ذلك"));
arasuperobject.biblemap.put("b4_24_24", new arasuperobject(4,24,24,"وتأتي سفن من ناحية كتيم وتخضع اشور وتخضع عابر فهو ايضا الى الهلاك"));
arasuperobject.biblemap.put("b4_24_25", new arasuperobject(4,24,25,"ثم قام بلعام وانطلق ورجع الى مكانه. وبالاق ايضا ذهب في طريقه"));
arasuperobject.biblemap.put("b4_25_1", new arasuperobject(4,25,1,"واقام اسرائيل في شطّيم وابتدأ الشعب يزنون مع بنات موآب"));
arasuperobject.biblemap.put("b4_25_2", new arasuperobject(4,25,2,"فدعون الشعب الى ذبائح آلهتهنّ فأكل الشعب وسجدوا لآلهتهنّ"));
arasuperobject.biblemap.put("b4_25_3", new arasuperobject(4,25,3,"وتعلّق اسرائيل ببعل فغور. فحمي غضب الرب على اسرائيل"));
arasuperobject.biblemap.put("b4_25_4", new arasuperobject(4,25,4,"فقال الرب لموسى خذ جميع رؤوس الشعب وعلّقهم للرب مقابل الشمس فيرتدّ حمو غضب الرب عن اسرائيل"));
arasuperobject.biblemap.put("b4_25_5", new arasuperobject(4,25,5,"فقال موسى لقضاة اسرائيل اقتلوا كل واحد قومه المتعلّقين ببعل فغور"));
arasuperobject.biblemap.put("b4_25_6", new arasuperobject(4,25,6,"واذا رجل من بني اسرائيل جاء وقدم الى اخوته المديانية امام عيني موسى واعين كل جماعة بني اسرائيل وهم باكون لدى باب خيمة الاجتماع"));
arasuperobject.biblemap.put("b4_25_7", new arasuperobject(4,25,7,"فلما رأى ذلك فينحاس بن ألعازار بن هرون الكاهن قام من وسط الجماعة واخذ رمحا بيده"));
