arasuperobject.biblemap.put("b4_25_12", new arasuperobject(4,25,12,"لذلك قل هانذا اعطيه ميثاقي ميثاق السلام"));
arasuperobject.biblemap.put("b4_25_13", new arasuperobject(4,25,13,"فيكون له ولنسله من بعده ميثاق كهنوت ابدي لاجل انه غار لله وكفّر عن بني اسرائيل"));
arasuperobject.biblemap.put("b4_25_14", new arasuperobject(4,25,14,"وكان اسم الرجل الاسرائيلي المقتول الذي قتل مع المديانية زمري بن سالو رئيس بيت اب من الشمعونيين"));
arasuperobject.biblemap.put("b4_25_15", new arasuperobject(4,25,15,"واسم المرأة المديانية المقتولة كزبي بنت صور. هو رئيس قبائل بيت اب في مديان"));
arasuperobject.biblemap.put("b4_25_16", new arasuperobject(4,25,16,"ثم كلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b4_25_17", new arasuperobject(4,25,17,"ضايقوا المديانيين واضربوهم"));
arasuperobject.biblemap.put("b4_25_18", new arasuperobject(4,25,18,"لانهم ضايقوكم بمكايدهم التي كادوكم بها في امر فغور وامر كزبي اختهم بنت رئيس لمديان التي قتلت يوم الوبإ بسبب فغور"));
