arasuperobject.biblemap.put("b4_26_2", new arasuperobject(4,26,2,"خذا عدد كل جماعة بني اسرائيل من ابن عشرين سنة فصاعدا حسب بيوت آبائهم كل خارج للجند في اسرائيل"));
arasuperobject.biblemap.put("b4_26_3", new arasuperobject(4,26,3,"فكلمهم موسى والعازار الكاهن في عربات موآب على اردن اريحا قائلين"));
