arasuperobject.biblemap.put("b4_26_6", new arasuperobject(4,26,6,"لحصرون عشيرة الحصرونيين. لكرمي عشيرة الكرميين"));
arasuperobject.biblemap.put("b4_26_7", new arasuperobject(4,26,7,"هذه عشائر الرأوبينيين. وكان المعدودون منهم ثلاثة واربعين الفا وسبع مئة وثلاثين"));
arasuperobject.biblemap.put("b4_26_8", new arasuperobject(4,26,8,"وابن فلّو اليآب"));
arasuperobject.biblemap.put("b4_26_9", new arasuperobject(4,26,9,"وبنو اليآب نموئيل وداثان وابيرام وهما داثان وابيرام المدعوّان من الجماعة اللذان خاصما موسى وهرون في جماعة قورح حين خاصموا الرب"));
arasuperobject.biblemap.put("b4_26_10", new arasuperobject(4,26,10,"ففتحت الارض فاها وابتلعتهما مع قورح حين مات القوم باحراق النار مئتين وخمسين رجلا. فصاروا عبرة"));
arasuperobject.biblemap.put("b4_26_11", new arasuperobject(4,26,11,"واما بنو قورح فلم يموتوا"));
arasuperobject.biblemap.put("b4_26_12", new arasuperobject(4,26,12,"بنو شمعون حسب عشائرهم. لنموئيل عشيرة النموئيليين. ليامين عشيرة اليامينيين. لياكين عشيرة الياكينيين"));
arasuperobject.biblemap.put("b4_26_13", new arasuperobject(4,26,13,"لزارح عشيرة الزارحيين. لشأول عشيرة الشأوليين"));
