arasuperobject.biblemap.put("b4_26_18", new arasuperobject(4,26,18,"هذه عشائر بني جاد حسب عددهم اربعون الفا وخمس مئة"));
arasuperobject.biblemap.put("b4_26_19", new arasuperobject(4,26,19,"ابنا يهوذا عير واونان. ومات عير واونان في ارض كنعان"));
arasuperobject.biblemap.put("b4_26_20", new arasuperobject(4,26,20,"فكان بنو يهوذا حسب عشائرهم لشيلة عشيرة الشيليين. ولفارص عشيرة الفارصيين. ولزارح عشيرة الزارحيين"));
arasuperobject.biblemap.put("b4_26_21", new arasuperobject(4,26,21,"وكان بنو فارص لحصرون عشيرة الحصرونيين. ولحامول عشيرة الحاموليين"));
arasuperobject.biblemap.put("b4_26_22", new arasuperobject(4,26,22,"هذه عشائر يهوذا حسب عددهم ستة وسبعون الفا وخمس مئة"));
arasuperobject.biblemap.put("b4_26_23", new arasuperobject(4,26,23,"بنو يسّاكر حسب عشائرهم. لتولاع عشيرة التولاعيين. ولفوّة عشيرة الفويين"));
arasuperobject.biblemap.put("b4_26_24", new arasuperobject(4,26,24,"ولياشوب عشيرة الياشوبيين. ولشمرون عشيرة الشمرونيين"));
arasuperobject.biblemap.put("b4_26_25", new arasuperobject(4,26,25,"هذه عشائر يسّاكر حسب عددهم اربعة وستون الفا وثلاث مئة"));
