arasuperobject.biblemap.put("b4_26_27", new arasuperobject(4,26,27,"هذه عشائر الزبولونيين حسب عددهم ستون الفا وخمس مئة"));
arasuperobject.biblemap.put("b4_26_28", new arasuperobject(4,26,28,"ابنا يوسف حسب عشائرهما منسّى وافرايم"));
arasuperobject.biblemap.put("b4_26_29", new arasuperobject(4,26,29,"بنو منسىّ لماكير عشيرة الماكيريين. وماكير ولد جلعاد. ولجلعاد عشيرة الجلعاديين"));
arasuperobject.biblemap.put("b4_26_30", new arasuperobject(4,26,30,"هؤلاء بنو جلعاد. لإيعزر عشيرة الإيعزريين. لحالق عشيرة الحالقيين"));
