arasuperobject.biblemap.put("b4_26_34", new arasuperobject(4,26,34,"هذه عشائر منسّى. والمعدودون منهم اثنان وخمسون الفا وسبع مئة"));
arasuperobject.biblemap.put("b4_26_35", new arasuperobject(4,26,35,"وهؤلاء بنو افرايم حسب عشائرهم. لشوتالح عشيرة الشوتالحيين. لباكر عشيرة الباكريين. لتاحن عشيرة التاحنيين"));
arasuperobject.biblemap.put("b4_26_36", new arasuperobject(4,26,36,"وهؤلاء بنو شوتالح. لعيران عشيرة العيرانيين"));
arasuperobject.biblemap.put("b4_26_37", new arasuperobject(4,26,37,"هذه عشائر بني افرايم حسب عددهم اثنان وثلاثون الفا وخمس مئة. هؤلاء بنو يوسف حسب عشائرهم"));
arasuperobject.biblemap.put("b4_26_38", new arasuperobject(4,26,38,"بنو بنيامين حسب عشائرهم. لبالع عشيرة البالعيين. لأشبيل عشيرة الأشبيليين. لأحيرام عشيرة الأحيراميين"));
