arasuperobject.biblemap.put("b4_36_4", new arasuperobject(4,36,4,"ومتى كان اليوبيل لبني اسرائيل يضاف نصيبهنّ الى نصيب السبط الذي صرن له ومن نصيب سبط آبائنا يؤخذ نصيبهنّ"));
arasuperobject.biblemap.put("b4_36_5", new arasuperobject(4,36,5,"فامر موسى بني اسرائيل حسب قول الرب قائلا. بحق تكلم سبط بني يوسف"));
arasuperobject.biblemap.put("b4_36_6", new arasuperobject(4,36,6,"هذا ما امر به الرب عن بنات صلفحاد قائلا. من حسن في اعينهنّ يكنّ له نساء ولكن لعشيرة سبط آبائهنّ يكنّ نساء"));
arasuperobject.biblemap.put("b4_36_7", new arasuperobject(4,36,7,"فلا يتحول نصيب لبني اسرائيل من سبط الى سبط بل يلازم بنو اسرائيل كل واحد نصيب سبط آبائه"));
