arasuperobject.biblemap.put("b40_5_9", new arasuperobject(40,5,9,"طوبى لصانعي السلام. لانهم ابناء الله يدعون"));
arasuperobject.biblemap.put("b40_5_10", new arasuperobject(40,5,10,"طوبى للمطرودين من اجل البر. لان لهم ملكوت السموات"));
arasuperobject.biblemap.put("b40_5_11", new arasuperobject(40,5,11,"طوبى لكم اذا عيّروكم وطردوكم وقالوا عليكم كل كلمة شريرة من اجلي كاذبين"));
arasuperobject.biblemap.put("b40_5_12", new arasuperobject(40,5,12,"افرحوا وتهللوا. لان اجركم عظيم في السموات. فانهم هكذا طردوا الانبياء الذين قبلكم"));
arasuperobject.biblemap.put("b40_5_13", new arasuperobject(40,5,13,"انتم ملح الارض. ولكن ان فسد الملح فبماذا يملح. لا يصلح بعد لشيء الا لان يطرح خارجا ويداس من الناس"));
arasuperobject.biblemap.put("b40_5_14", new arasuperobject(40,5,14,"انتم نور العالم. لا يمكن ان تخفى مدينة موضوعة على جبل"));
arasuperobject.biblemap.put("b40_5_15", new arasuperobject(40,5,15,"ولا يوقدون سراجا ويضعونه تحت المكيال بل على المنارة فيضيء لجميع الذين في البيت"));
arasuperobject.biblemap.put("b40_5_16", new arasuperobject(40,5,16,"فليضئ نوركم هكذا قدام الناس لكي يروا اعمالكم الحسنة ويمجدوا اباكم الذي في السموات"));
arasuperobject.biblemap.put("b40_5_17", new arasuperobject(40,5,17,"لا تظنوا اني جئت لانقض الناموس او الانبياء. ما جئت لانقض بل لاكمّل"));
arasuperobject.biblemap.put("b40_5_18", new arasuperobject(40,5,18,"فاني الحق اقول لكم الى ان تزول السماء والارض لا يزول حرف واحد او نقطة واحدة من الناموس حتى يكون الكل"));
arasuperobject.biblemap.put("b40_5_19", new arasuperobject(40,5,19,"فمن نقض احدى هذه الوصايا الصغرى وعلم الناس هكذا يدعى اصغر في ملكوت السموات. واما من عمل وعلّم فهذا يدعى عظيما في ملكوت السموات"));
arasuperobject.biblemap.put("b40_5_20", new arasuperobject(40,5,20,"فاني اقول لكم انكم ان لم يزد بركم على الكتبة والفريسيين لن تدخلوا ملكوت السموات"));
arasuperobject.biblemap.put("b40_5_21", new arasuperobject(40,5,21,"قد سمعتم انه قيل للقدماء لا تقتل. ومن قتل يكون مستوجب الحكم"));
arasuperobject.biblemap.put("b40_5_22", new arasuperobject(40,5,22,"واما انا فاقول لكم ان كل من يغضب على اخيه باطلا يكون مستوجب الحكم. ومن قال لاخيه رقا يكون مستوجب المجمع. ومن قال يا احمق يكون مستوجب نار جهنم"));
arasuperobject.biblemap.put("b40_5_23", new arasuperobject(40,5,23,"فان قدمت قربانك الى المذبح وهناك تذكرت ان لاخيك شيئا عليك"));
arasuperobject.biblemap.put("b40_5_24", new arasuperobject(40,5,24,"فاترك هناك قربانك قدام المذبح واذهب اولا اصطلح مع اخيك. وحينئذ تعال وقدم قربانك"));
arasuperobject.biblemap.put("b40_5_25", new arasuperobject(40,5,25,"كن مراضيا لخصمك سريعا ما دمت معه في الطريق. لئلا يسلمك الخصم الى القاضي ويسلمك القاضي الى الشرطي فتلقى في السجن"));
arasuperobject.biblemap.put("b40_5_26", new arasuperobject(40,5,26,"الحق اقول لك لا تخرج من هناك حتى توفي الفلس الاخير"));
arasuperobject.biblemap.put("b40_5_27", new arasuperobject(40,5,27,"قد سمعتم انه قيل للقدماء لا تزن"));
arasuperobject.biblemap.put("b40_5_28", new arasuperobject(40,5,28,"واما انا فاقول لكم ان كل من ينظر الى امرأة ليشتهيها فقد زنى بها في قلبه"));
arasuperobject.biblemap.put("b40_5_29", new arasuperobject(40,5,29,"فان كانت عينك اليمنى تعثرك فاقلعها وألقها عنك. لانه خير لك ان يهلك احد اعضائك ولا يلقى جسدك كله في جهنم"));
arasuperobject.biblemap.put("b40_5_30", new arasuperobject(40,5,30,"وان كانت يدك اليمنى تعثرك فاقطعها والقها عنك. لانه خير لك ان يهلك احد اعضائك ولا يلقى جسدك كله في جهنم"));
arasuperobject.biblemap.put("b40_5_31", new arasuperobject(40,5,31,"وقيل من طلق امرأته فليعطها كتاب طلاق"));
arasuperobject.biblemap.put("b40_5_32", new arasuperobject(40,5,32,"واما انا فاقول لكم ان من طلّق امرأته الا لعلّة الزنى يجعلها تزني. ومن يتزوج مطلّقة فانه يزني"));
arasuperobject.biblemap.put("b40_5_33", new arasuperobject(40,5,33,"ايضا سمعتم انه قيل للقدماء لا تحنث بل أوف للرب اقسامك"));
arasuperobject.biblemap.put("b40_5_34", new arasuperobject(40,5,34,"واما انا فاقول لكم لا تحلفوا البتة. لا بالسماء لانها كرسي الله"));
arasuperobject.biblemap.put("b40_5_35", new arasuperobject(40,5,35,"ولا بالارض لانها موطئ قدميه. ولا باورشليم لانها مدينة الملك العظيم"));
arasuperobject.biblemap.put("b40_5_36", new arasuperobject(40,5,36,"ولا تحلف براسك لانك لا تقدر ان تجعل شعرة واحدة بيضاء او سوداء"));
arasuperobject.biblemap.put("b40_5_37", new arasuperobject(40,5,37,"بل ليكن كلامكم نعم نعم لا لا. وما زاد على ذلك فهو من الشرير"));
arasuperobject.biblemap.put("b40_5_38", new arasuperobject(40,5,38,"سمعتم انه قيل عين بعين وسن بسن"));
arasuperobject.biblemap.put("b40_5_39", new arasuperobject(40,5,39,"واما انا فاقول لكم لا تقاوموا الشر. بل من لطمك على خدك الايمن فحوّل له الآخر ايضا"));
arasuperobject.biblemap.put("b40_5_40", new arasuperobject(40,5,40,"ومن اراد ان يخاصمك وياخذ ثوبك فاترك له الرداء ايضا"));
arasuperobject.biblemap.put("b40_5_41", new arasuperobject(40,5,41,"ومن سخرك ميلا واحدا فاذهب معه اثنين"));
arasuperobject.biblemap.put("b40_5_42", new arasuperobject(40,5,42,"من سألك فاعطه. ومن اراد ان يقترض منك فلا ترده"));
arasuperobject.biblemap.put("b40_5_43", new arasuperobject(40,5,43,"سمعتم انه قيل تحب قريبك وتبغض عدوك"));
arasuperobject.biblemap.put("b40_5_44", new arasuperobject(40,5,44,"واما انا فاقول لكم احبوا اعداءكم. باركوا لاعنيكم. احسنوا الى مبغضيكم. وصلّوا لاجل الذين يسيئون اليكم ويطردونكم"));
arasuperobject.biblemap.put("b40_5_45", new arasuperobject(40,5,45,"لكي تكونوا ابناء ابيكم الذي في السموات. فانه يشرق شمسه على الاشرار والصالحين ويمطر على الابرار والظالمين"));
arasuperobject.biblemap.put("b40_5_46", new arasuperobject(40,5,46,"لانه ان احببتم الذين يحبونكم فاي اجر لكم. اليس العشارون ايضا يفعلون ذلك"));
arasuperobject.biblemap.put("b40_5_47", new arasuperobject(40,5,47,"وان سلمتم على اخوتكم فقط فاي فضل تصنعون. أليس العشارون ايضا يفعلون هكذا"));
arasuperobject.biblemap.put("b40_5_48", new arasuperobject(40,5,48,"فكونوا انتم كاملين كما ان اباكم الذي في السموات هو كامل"));
arasuperobject.biblemap.put("b40_6_1", new arasuperobject(40,6,1,"احترزوا من ان تصنعوا صدقتكم قدام الناس لكي ينظروكم. والا فليس لكم اجر عند ابيكم الذي في السموات"));
arasuperobject.biblemap.put("b40_6_2", new arasuperobject(40,6,2,"فمتى صنعت صدقة فلا تصوت قدامك بالبوق كما يفعل المراؤون في المجامع وفي الازقة لكي يمجّدوا من الناس. الحق اقول لكم انهم قد استوفوا اجرهم"));
arasuperobject.biblemap.put("b40_6_3", new arasuperobject(40,6,3,"واما انت فمتى صنعت صدقة فلا تعرف شمالك ما تفعل يمينك"));
arasuperobject.biblemap.put("b40_6_4", new arasuperobject(40,6,4,"لكي تكون صدقتك في الخفاء. فابوك الذي يرى في الخفاء هو يجازيك علانية"));
arasuperobject.biblemap.put("b40_6_5", new arasuperobject(40,6,5,"ومتى صلّيت فلا تكن كالمرائين. فانهم يحبون ان يصلّوا قائمين في المجامع وفي زوايا الشوارع لكي يظهروا للناس. الحق اقول لكم انهم قد استوفوا اجرهم"));
arasuperobject.biblemap.put("b40_6_6", new arasuperobject(40,6,6,"واما انت فمتى صلّيت فادخل الى مخدعك واغلق بابك وصلّ الى ابيك الذي في الخفاء. فابوك الذي يرى في الخفاء يجازيك علانية"));
arasuperobject.biblemap.put("b40_6_7", new arasuperobject(40,6,7,"وحينما تصلّون لا تكرروا الكلام باطلا كالامم. فانهم يظنون انه بكثرة كلامهم يستجاب لهم"));
arasuperobject.biblemap.put("b40_6_8", new arasuperobject(40,6,8,"فلا تتشبهوا بهم. لان اباكم يعلم ما تحتاجون اليه قبل ان تسألوه"));
arasuperobject.biblemap.put("b40_6_9", new arasuperobject(40,6,9,"فصلّوا انتم هكذا. ابانا الذي في السموات. ليتقدس اسمك"));
arasuperobject.biblemap.put("b40_6_10", new arasuperobject(40,6,10,"ليأت ملكوتك. لتكن مشيئتك كما في السماء كذلك على الارض"));
arasuperobject.biblemap.put("b40_6_11", new arasuperobject(40,6,11,"خبزنا كفافنا اعطنا اليوم"));
arasuperobject.biblemap.put("b40_6_12", new arasuperobject(40,6,12,"واغفر لنا ذنوبنا كما نغفر نحن ايضا للمذنبين الينا"));
arasuperobject.biblemap.put("b40_6_13", new arasuperobject(40,6,13,"ولا تدخلنا في تجربة. لكن نجنا من الشرير. لان لك الملك والقوة والمجد الى الابد. آمين"));
arasuperobject.biblemap.put("b40_6_14", new arasuperobject(40,6,14,"فانه ان غفرتم للناس زلاتهم يغفر لكم ايضا ابوكم السماوي"));
arasuperobject.biblemap.put("b40_6_15", new arasuperobject(40,6,15,"وان لم تغفروا للناس زلاتهم لا يغفر لكم ابوكم ايضا زلاتكم"));
arasuperobject.biblemap.put("b40_6_16", new arasuperobject(40,6,16,"ومتى صمتم فلا تكونوا عابسين كالمرائين. فانهم يغيرون وجوههم لكي يظهروا للناس صائمين. الحق اقول لكم انهم قد استوفوا اجرهم"));
arasuperobject.biblemap.put("b40_6_17", new arasuperobject(40,6,17,"واما انت فمتى صمت فادهن راسك واغسل وجهك"));
arasuperobject.biblemap.put("b40_6_18", new arasuperobject(40,6,18,"لكي لا تظهر للناس صائما بل لابيك الذي في الخفاء. فابوك الذي يرى في الخفاء يجازيك علانية"));
arasuperobject.biblemap.put("b40_6_19", new arasuperobject(40,6,19,"لا تكنزوا لكم كنوزا على الارض حيث يفسد السوس والصدأ وحيث ينقب السارقون ويسرقون"));
arasuperobject.biblemap.put("b40_6_20", new arasuperobject(40,6,20,"بل اكنزوا لكم كنوزا في السماء حيث لا يفسد سوس ولا صدأ وحيث لا ينقب سارقون ولا يسرقون"));
arasuperobject.biblemap.put("b40_6_21", new arasuperobject(40,6,21,"لانه حيث يكون كنزك هناك يكون قلبك ايضا"));
arasuperobject.biblemap.put("b40_6_22", new arasuperobject(40,6,22,"سراج الجسد هو العين. فان كانت عينك بسيطة فجسدك كله يكون نيرا"));
arasuperobject.biblemap.put("b40_6_23", new arasuperobject(40,6,23,"وان كانت عينك شريرة فجسدك كله يكون مظلما. فان كان النور الذي فيك ظلاما فالظلام كم يكون"));
arasuperobject.biblemap.put("b40_6_24", new arasuperobject(40,6,24,"لا يقدر احد ان يخدم سيدين. لانه اما ان يبغض الواحد ويحب الآخر او يلازم الواحد ويحتقر الآخر. لا تقدرون ان تخدموا الله والمال"));
arasuperobject.biblemap.put("b40_6_25", new arasuperobject(40,6,25,"لذلك اقول لكم لا تهتموا لحياتكم بما تاكلون وبما تشربون. ولا لاجسادكم بما تلبسون. أليست الحياة افضل من الطعام والجسد افضل من اللباس"));
arasuperobject.biblemap.put("b40_6_26", new arasuperobject(40,6,26,"انظروا الى طيور السماء. انها لا تزرع ولا تحصد ولا تجمع الى مخازن. وابوكم السماوي يقوتها. ألستم انتم بالحري افضل منها"));
arasuperobject.biblemap.put("b40_6_27", new arasuperobject(40,6,27,"ومن منكم اذا اهتم يقدر ان يزيد على قامته ذراعا واحدة"));
arasuperobject.biblemap.put("b40_6_28", new arasuperobject(40,6,28,"ولماذا تهتمون باللباس. تأملوا زنابق الحقل كيف تنمو. لا تتعب ولا تغزل"));
arasuperobject.biblemap.put("b40_6_29", new arasuperobject(40,6,29,"ولكن اقول لكم انه ولا سليمان في كل مجده كان يلبس كواحدة منها"));
arasuperobject.biblemap.put("b40_6_30", new arasuperobject(40,6,30,"فان كان عشب الحقل الذي يوجد اليوم ويطرح غدا في التنور يلبسه الله هكذا أفليس بالحري جدا يلبسكم انتم يا قليلي الايمان"));
arasuperobject.biblemap.put("b40_6_31", new arasuperobject(40,6,31,"فلا تهتموا قائلين ماذا نأكل او ماذا نشرب او ماذا نلبس"));
arasuperobject.biblemap.put("b40_6_32", new arasuperobject(40,6,32,"فان هذه كلها تطلبها الامم. لان اباكم السماوي يعلم انكم تحتاجون الى هذه كلها"));
arasuperobject.biblemap.put("b40_6_33", new arasuperobject(40,6,33,"لكن اطلبوا اولا ملكوت الله وبره وهذه كلها تزاد لكم"));
arasuperobject.biblemap.put("b40_6_34", new arasuperobject(40,6,34,"فلا تهتموا للغد. لان الغد يهتم بما لنفسه. يكفي اليوم شره"));
arasuperobject.biblemap.put("b40_7_1", new arasuperobject(40,7,1,"لا تدينوا لكي لا تدانوا"));
arasuperobject.biblemap.put("b40_7_2", new arasuperobject(40,7,2,"لانكم بالدينونة التي بها تدينون تدانون. وبالكيل الذي به تكيلون يكال لكم"));
arasuperobject.biblemap.put("b40_7_3", new arasuperobject(40,7,3,"ولماذا تنظر القذى الذي في عين اخيك. واما الخشبة التي في عينك فلا تفطن لها"));
arasuperobject.biblemap.put("b40_7_4", new arasuperobject(40,7,4,"ام كيف تقول لأخيك دعني اخرج القذى من عينك وها الخشبة في عينك"));
arasuperobject.biblemap.put("b40_7_5", new arasuperobject(40,7,5,"يا مرائي اخرج اولا الخشبة من عينك. وحينئذ تبصر جيدا ان تخرج القذى من عين اخيك"));
arasuperobject.biblemap.put("b40_7_6", new arasuperobject(40,7,6,"لا تعطوا القدس للكلاب. ولا تطرحوا درركم قدام الخنازير. لئلا تدوسها بارجلها وتلتفت فتمزقكم"));
arasuperobject.biblemap.put("b40_7_7", new arasuperobject(40,7,7,"اسألوا تعطوا. اطلبوا تجدوا. اقرعوا يفتح لكم"));
arasuperobject.biblemap.put("b40_7_8", new arasuperobject(40,7,8,"لان كل من يسأل يأخذ. ومن يطلب يجد. ومن يقرع يفتح له"));
arasuperobject.biblemap.put("b40_7_9", new arasuperobject(40,7,9,"ام اي انسان منكم اذا سأله ابنه خبزا يعطيه حجرا"));
arasuperobject.biblemap.put("b40_7_10", new arasuperobject(40,7,10,"وان سأله سمكة يعطيه حية"));
arasuperobject.biblemap.put("b40_7_11", new arasuperobject(40,7,11,"فان كنتم وانتم اشرار تعرفون ان تعطوا اولادكم عطايا جيدة فكم بالحري ابوكم الذي في السموات يهب خيرات للذين يسألونه"));
arasuperobject.biblemap.put("b40_7_12", new arasuperobject(40,7,12,"فكل ما تريدون ان يفعل الناس بكم افعلوا هكذا انتم ايضا بهم. لان هذا هو الناموس والانبياء"));
arasuperobject.biblemap.put("b40_7_13", new arasuperobject(40,7,13,"ادخلوا من الباب الضيق. لانه واسع الباب ورحب الطريق الذي يؤدي الى الهلاك. وكثيرون هم الذين يدخلون منه"));
arasuperobject.biblemap.put("b40_7_14", new arasuperobject(40,7,14,"ما اضيق الباب وأكرب الطريق الذي يؤدي الى الحياة. وقليلون هم الذين يجدونه"));
arasuperobject.biblemap.put("b40_7_15", new arasuperobject(40,7,15,"احترزوا من الانبياء الكذبة الذين يأتونكم بثياب الحملان ولكنهم من داخل ذئاب خاطفة"));
arasuperobject.biblemap.put("b40_7_16", new arasuperobject(40,7,16,"من ثمارهم تعرفونهم. هل يجتنون من الشوك عنبا او من الحسك تينا"));
arasuperobject.biblemap.put("b40_7_17", new arasuperobject(40,7,17,"هكذا كل شجرة جيدة تصنع اثمارا جيدة. واما الشجرة الردية فتصنع اثمارا رديّة"));
arasuperobject.biblemap.put("b40_7_18", new arasuperobject(40,7,18,"لا تقدر شجرة جيدة ان تصنع اثمارا رديّة ولا شجرة رديّة ان تصنع اثمارا جيدة"));
arasuperobject.biblemap.put("b40_7_19", new arasuperobject(40,7,19,"كل شجرة لا تصنع ثمرا جيدا تقطع وتلقى في النار"));
arasuperobject.biblemap.put("b40_7_20", new arasuperobject(40,7,20,"فاذا من ثمارهم تعرفونهم"));
arasuperobject.biblemap.put("b40_7_21", new arasuperobject(40,7,21,"ليس كل من يقول لي يا رب يا رب يدخل ملكوت السموات. بل الذي يفعل ارادة ابي الذي في السموات"));
arasuperobject.biblemap.put("b40_7_22", new arasuperobject(40,7,22,"كثيرون سيقولون لي في ذلك اليوم يا رب يا رب أليس باسمك تنبأنا وباسمك اخرجنا شياطين وباسمك صنعنا قوات كثيرة"));
arasuperobject.biblemap.put("b40_7_23", new arasuperobject(40,7,23,"فحينئذ أصرّح لهم اني لم اعرفكم قط. اذهبوا عني يا فاعلي الاثم"));
arasuperobject.biblemap.put("b40_7_24", new arasuperobject(40,7,24,"فكل من يسمع اقوالي هذه ويعمل بها اشبهه برجل عاقل بنى بيته على الصخر"));
arasuperobject.biblemap.put("b40_7_25", new arasuperobject(40,7,25,"فنزل المطر وجاءت الانهار وهبت الرياح ووقعت على ذلك البيت فلم يسقط. لانه كان مؤسسا على الصخر"));
arasuperobject.biblemap.put("b40_7_26", new arasuperobject(40,7,26,"وكل من يسمع اقوالي هذه ولا يعمل بها يشبّه برجل جاهل بنى بيته على الرمل"));
arasuperobject.biblemap.put("b40_7_27", new arasuperobject(40,7,27,"فنزل المطر وجاءت الانهار وهبت الرياح وصدمت ذلك البيت فسقط. وكان سقوطه عظيما"));
arasuperobject.biblemap.put("b40_7_28", new arasuperobject(40,7,28,"فلما اكمل يسوع هذه الاقوال بهتت الجموع من تعليمه"));
arasuperobject.biblemap.put("b40_7_29", new arasuperobject(40,7,29,"لانه كان يعلّمهم كمن له سلطان وليس كالكتبة"));
arasuperobject.biblemap.put("b40_8_1", new arasuperobject(40,8,1,"ولما نزل من الجبل تبعته جموع كثيرة"));
arasuperobject.biblemap.put("b40_8_2", new arasuperobject(40,8,2,"واذا ابرص قد جاء وسجد له قائلا يا سيد ان اردت تقدر ان تطهرني"));
arasuperobject.biblemap.put("b40_8_3", new arasuperobject(40,8,3,"فمدّ يسوع يده ولمسه قائلا أريد فاطهر. وللوقت طهر برصه"));
arasuperobject.biblemap.put("b40_8_4", new arasuperobject(40,8,4,"فقال له يسوع انظر ان لا تقول لأحد. بل اذهب أر نفسك للكاهن وقدم القربان الذي امر به موسى شهادة لهم"));
arasuperobject.biblemap.put("b40_8_5", new arasuperobject(40,8,5,"ولما دخل يسوع كفرناحوم جاء اليه قائد مئة يطلب اليه"));
arasuperobject.biblemap.put("b40_8_6", new arasuperobject(40,8,6,"ويقول يا سيد غلامي مطروح في البيت مفلوجا متعذبا جدا"));
arasuperobject.biblemap.put("b40_8_7", new arasuperobject(40,8,7,"فقال له يسوع انا آتي واشفيه"));
arasuperobject.biblemap.put("b40_8_8", new arasuperobject(40,8,8,"فاجاب قائد المئة وقال يا سيد لست مستحقا ان تدخل تحت سقفي. لكن قل كلمة فقط فيبرأ غلامي"));
arasuperobject.biblemap.put("b40_8_9", new arasuperobject(40,8,9,"لاني انا ايضا انسان تحت سلطان. لي جند تحت يدي. اقول لهذا اذهب فيذهب ولآخر ائت فياتي ولعبدي افعل هذا فيفعل"));
arasuperobject.biblemap.put("b40_8_10", new arasuperobject(40,8,10,"فلما سمع يسوع تعجب وقال للذين يتبعون. الحق اقول لكم لم اجد ولا في اسرائيل ايمانا بمقدار هذا"));
arasuperobject.biblemap.put("b40_8_11", new arasuperobject(40,8,11,"واقول لكم ان كثيرين سيأتون من المشارق والمغارب ويتكئون مع ابراهيم واسحق ويعقوب في ملكوت السموات"));
arasuperobject.biblemap.put("b40_8_12", new arasuperobject(40,8,12,"واما بنو الملكوت فيطرحون الى الظلمة الخارجية. هناك يكون البكاء وصرير الاسنان"));
arasuperobject.biblemap.put("b40_8_13", new arasuperobject(40,8,13,"ثم قال يسوع لقائد المئة اذهب وكما آمنت ليكن لك. فبرأ غلامه في تلك الساعة"));
arasuperobject.biblemap.put("b40_8_14", new arasuperobject(40,8,14,"ولما جاء يسوع الى بيت بطرس رأى حماته مطروحة ومحمومة"));
arasuperobject.biblemap.put("b40_8_15", new arasuperobject(40,8,15,"فلمس يدها فتركتها الحمى. فقامت وخدمتهم"));
arasuperobject.biblemap.put("b40_8_16", new arasuperobject(40,8,16,"ولما صار المساء قدموا اليه مجانين كثيرين. فأخرج الارواح بكلمة وجميع المرضى شفاهم"));
arasuperobject.biblemap.put("b40_8_17", new arasuperobject(40,8,17,"لكي يتم ما قيل باشعياء النبي القائل هو اخذ اسقامنا وحمل امراضنا"));
arasuperobject.biblemap.put("b40_8_18", new arasuperobject(40,8,18,"ولما رأى يسوع جموعا كثيرة حوله امر بالذهاب الى العبر"));
arasuperobject.biblemap.put("b40_8_19", new arasuperobject(40,8,19,"فتقدم كاتب وقال له يا معلّم اتبعك اينما تمضي"));
arasuperobject.biblemap.put("b40_8_20", new arasuperobject(40,8,20,"فقال له يسوع للثعالب اوجرة ولطيور السماء اوكار. واما ابن الانسان فليس له اين يسند راسه"));
arasuperobject.biblemap.put("b40_8_21", new arasuperobject(40,8,21,"وقال له آخر من تلاميذه يا سيد ائذن لي ان امضي اولا وادفن ابي"));
arasuperobject.biblemap.put("b40_8_22", new arasuperobject(40,8,22,"فقال له يسوع اتبعني ودع الموتى يدفنون موتاهم"));
arasuperobject.biblemap.put("b40_8_23", new arasuperobject(40,8,23,"ولما دخل السفينة تبعه تلاميذه"));
arasuperobject.biblemap.put("b40_8_24", new arasuperobject(40,8,24,"واذ اضطراب عظيم قد حدث في البحر حتى غطت الامواج السفينة. وكان هو نائما"));
arasuperobject.biblemap.put("b40_8_25", new arasuperobject(40,8,25,"فتقدم تلاميذه وايقظوه قائلين يا سيد نجنا فاننا نهلك"));
arasuperobject.biblemap.put("b40_8_26", new arasuperobject(40,8,26,"فقال لهم ما بالكم خائفين يا قليلي الايمان. ثم قام وانتهر الرياح والبحر فصار هدوء عظيم"));
arasuperobject.biblemap.put("b40_8_27", new arasuperobject(40,8,27,"فتعجب الناس قائلين اي انسان هذا. فان الرياح والبحر جميعا تطيعه"));
arasuperobject.biblemap.put("b40_8_28", new arasuperobject(40,8,28,"ولما جاء الى العبر الى كورة الجرجسيين استقبله مجنونان خارجان من القبور هائجان جدا حتى لم يكن احد يقدر ان يجتاز من تلك الطريق"));
arasuperobject.biblemap.put("b40_8_29", new arasuperobject(40,8,29,"واذا هما قد صرخا قائلين ما لنا ولك يا يسوع ابن الله. أجئت الى هنا قبل الوقت لتعذبنا"));
arasuperobject.biblemap.put("b40_8_30", new arasuperobject(40,8,30,"وكان بعيدا منهم قطيع خنازير كثيرة ترعى"));
arasuperobject.biblemap.put("b40_8_31", new arasuperobject(40,8,31,"فالشياطين طلبوا اليه قائلين ان كنت تخرجنا فاذن لنا ان نذهب الى قطيع الخنازير"));
arasuperobject.biblemap.put("b40_8_32", new arasuperobject(40,8,32,"فقال لهم امضوا. فخرجوا ومضوا الى قطيع الخنازير. واذا قطيع الخنازير كله قد اندفع من على الجرف الى البحر ومات في المياه"));
arasuperobject.biblemap.put("b40_8_33", new arasuperobject(40,8,33,"اما الرعاة فهربوا ومضوا الى المدينة واخبروا عن كل شيء وعن أمر المجنونين"));
arasuperobject.biblemap.put("b40_8_34", new arasuperobject(40,8,34,"فاذا كل المدينة قد خرجت لملاقاة يسوع. ولما ابصروه طلبوا ان ينصرف عن تخومهم"));
arasuperobject.biblemap.put("b40_9_1", new arasuperobject(40,9,1,"فدخل السفينة واجتاز وجاء الى مدينته"));
arasuperobject.biblemap.put("b40_9_2", new arasuperobject(40,9,2,"واذا مفلوج يقدمونه اليه مطروحا على فراش. فلما رأى يسوع ايمانهم قال للمفلوج ثق يا بني. مغفورة لك خطاياك"));
arasuperobject.biblemap.put("b40_9_3", new arasuperobject(40,9,3,"واذا قوم من الكتبة قد قالوا في انفسهم هذا يجدّف"));
arasuperobject.biblemap.put("b40_9_4", new arasuperobject(40,9,4,"فعلم يسوع افكارهم فقال لماذا تفكرون بالشر في قلوبكم"));
arasuperobject.biblemap.put("b40_9_5", new arasuperobject(40,9,5,"ايما ايسر ان يقال مغفورة لك خطاياك. ام ان يقال قم وامش"));
arasuperobject.biblemap.put("b40_9_6", new arasuperobject(40,9,6,"ولكن لكي تعلموا ان لابن الانسان سلطانا على الارض ان يغفر الخطايا. حينئذ قال للمفلوج. قم احمل فراشك واذهب الى بيتك"));
arasuperobject.biblemap.put("b40_9_7", new arasuperobject(40,9,7,"فقام ومضى الى بيته"));
arasuperobject.biblemap.put("b40_9_8", new arasuperobject(40,9,8,"فلما رأى الجموع تعجبوا ومجدوا الله الذي اعطى الناس سلطانا مثل هذا"));
arasuperobject.biblemap.put("b40_9_9", new arasuperobject(40,9,9,"وفيما يسوع مجتاز من هناك رأى انسانا جالسا عند مكان الجباية اسمه متى. فقال له اتبعني. فقام وتبعه"));
arasuperobject.biblemap.put("b40_9_10", new arasuperobject(40,9,10,"وبينما هو متكئ في البيت اذا عشارون وخطاة كثيرون قد جاءوا واتكأوا مع يسوع وتلاميذه"));
arasuperobject.biblemap.put("b40_9_11", new arasuperobject(40,9,11,"فلما نظر الفريسيون قالوا لتلاميذه لماذا ياكل معلمكم مع العشارين والخطاة"));
arasuperobject.biblemap.put("b40_9_12", new arasuperobject(40,9,12,"فلما سمع يسوع قال لهم لا يحتاج الاصحاء الى طبيب بل المرضى"));
arasuperobject.biblemap.put("b40_9_13", new arasuperobject(40,9,13,"فاذهبوا وتعلّموا ما هو. اني اريد رحمة لا ذبيحة. لاني لم آت لادعو ابرارا بل خطاة الى التوبة"));
arasuperobject.biblemap.put("b40_9_14", new arasuperobject(40,9,14,"حينئذ اتى اليه تلاميذ يوحنا قائلين لماذا نصوم نحن والفريسيون كثيرا واما تلاميذك فلا يصومون"));
arasuperobject.biblemap.put("b40_9_15", new arasuperobject(40,9,15,"فقال لهم يسوع هل يستطيع بنو العرس ان ينوحوا ما دام العريس معهم. ولكن ستأتي ايام حين يرفع العريس عنهم فحينئذ يصومون"));
arasuperobject.biblemap.put("b40_9_16", new arasuperobject(40,9,16,"ليس احد يجعل رقعة من قطعة جديدة على ثوب عتيق. لان الملء ياخذ من الثوب فيصير الخرق اردأ"));
arasuperobject.biblemap.put("b40_9_17", new arasuperobject(40,9,17,"ولا يجعلون خمرا جديدة في زقاق عتيقة. لئلا تنشقّ الزقاق فالخمر تنصب والزقاق تتلف. بل يجعلون خمرا جديدة في زقاق جديدة فتحفظ جميعا"));
arasuperobject.biblemap.put("b40_9_18", new arasuperobject(40,9,18,"وفيما هو يكلمهم بهذا اذا رئيس قد جاء فسجد له قائلا ان ابنتي الآن ماتت. لكن تعال وضع يدك عليها فتحيا"));
arasuperobject.biblemap.put("b40_9_19", new arasuperobject(40,9,19,"فقام يسوع وتبعه هو وتلاميذه"));
arasuperobject.biblemap.put("b40_9_20", new arasuperobject(40,9,20,"واذا امرأة نازفة دم منذ اثنتي عشرة سنة قد جاءت من ورائه ومسّت هدب ثوبه"));
arasuperobject.biblemap.put("b40_9_21", new arasuperobject(40,9,21,"لانها قالت في نفسها ان مسست ثوبه فقط شفيت"));
arasuperobject.biblemap.put("b40_9_22", new arasuperobject(40,9,22,"فالتفت يسوع وابصرها فقال ثقي يا ابنة. ايمانك قد شفاك فشفيت المرأة من تلك الساعة"));
arasuperobject.biblemap.put("b40_9_23", new arasuperobject(40,9,23,"ولما جاء يسوع الى بيت الرئيس ونظر المزمرين والجمع يضجون"));
arasuperobject.biblemap.put("b40_9_24", new arasuperobject(40,9,24,"قال لهم تنحوا. فان الصبية لم تمت لكنها نائمة. فضحكوا عليه"));
arasuperobject.biblemap.put("b40_9_25", new arasuperobject(40,9,25,"فلما اخرج الجمع دخل وامسك بيدها. فقامت الصبية"));
arasuperobject.biblemap.put("b40_9_26", new arasuperobject(40,9,26,"فخرج ذلك الخبر الى تلك الارض كلها"));
arasuperobject.biblemap.put("b40_9_27", new arasuperobject(40,9,27,"وفيما يسوع مجتاز من هناك تبعه اعميان يصرخان ويقولان ارحمنا يا ابن داود"));
arasuperobject.biblemap.put("b40_9_28", new arasuperobject(40,9,28,"ولما جاء الى البيت تقدم اليه الاعميان. فقال لهما يسوع أتؤمنان اني اقدر ان افعل هذا. قالا له نعم يا سيد"));
arasuperobject.biblemap.put("b40_9_29", new arasuperobject(40,9,29,"حينئذ لمس اعينهما قائلا بحسب ايمانكما ليكن لكما"));
arasuperobject.biblemap.put("b40_9_30", new arasuperobject(40,9,30,"فانفتحت اعينهما. فانتهرهما يسوع قائلا انظرا لا يعلم احد"));
arasuperobject.biblemap.put("b40_9_31", new arasuperobject(40,9,31,"ولكنهما خرجا واشاعاه في تلك الارض كلها"));
arasuperobject.biblemap.put("b40_9_32", new arasuperobject(40,9,32,"وفيما هما خارجان اذا انسان اخرس مجنون قدموه اليه"));
arasuperobject.biblemap.put("b40_9_33", new arasuperobject(40,9,33,"فلما اخرج الشيطان تكلم الاخرس. فتعجب الجموع قائلين لم يظهر قط مثل هذا في اسرائيل"));
arasuperobject.biblemap.put("b40_9_34", new arasuperobject(40,9,34,"اما الفريسيون فقالوا برئيس الشياطين يخرج الشياطين"));
arasuperobject.biblemap.put("b40_9_35", new arasuperobject(40,9,35,"وكان يسوع يطوف المدن كلها والقرى يعلّم في مجامعها. ويكرز ببشارة الملكوت. ويشفي كل مرض وكل ضعف في الشعب"));
arasuperobject.biblemap.put("b40_9_36", new arasuperobject(40,9,36,"ولما رأى الجموع تحنن عليهم اذ كانوا منزعجين ومنطرحين كغنم لا راعي لها"));
arasuperobject.biblemap.put("b40_9_37", new arasuperobject(40,9,37,"حينئذ قال لتلاميذه الحصاد كثير ولكن الفعلة قليلون"));
arasuperobject.biblemap.put("b40_9_38", new arasuperobject(40,9,38,"فاطلبوا من رب الحصاد ان يرسل فعلة الى حصاده"));
arasuperobject.biblemap.put("b40_10_1", new arasuperobject(40,10,1,"ثم دعا تلاميذه الاثني عشر واعطاهم سلطانا على ارواح نجسة حتى يخرجوها ويشفوا كل مرض وكل ضعف"));
arasuperobject.biblemap.put("b40_10_2", new arasuperobject(40,10,2,"واما اسماء الاثني عشر رسولا فهي هذه. الاول سمعان الذي يقال له بطرس واندراوس اخوه. يعقوب بن زبدي ويوحنا اخوه"));
arasuperobject.biblemap.put("b40_10_3", new arasuperobject(40,10,3,"فيلبس وبرثولماوس. توما ومتى العشار. يعقوب بن حلفى ولباوس الملقب تداوس"));
arasuperobject.biblemap.put("b40_10_4", new arasuperobject(40,10,4,"سمعان القانوي ويهوذا الاسخريوطي الذي اسلمه"));
arasuperobject.biblemap.put("b40_10_5", new arasuperobject(40,10,5,"هؤلاء الاثنا عشر ارسلهم يسوع واوصاهم قائلا. الى طريق امم لا تمضوا والى مدينة للسامريين لا تدخلوا"));
arasuperobject.biblemap.put("b40_10_6", new arasuperobject(40,10,6,"بل اذهبوا بالحري الى خراف بيت اسرائيل الضالة"));
arasuperobject.biblemap.put("b40_10_7", new arasuperobject(40,10,7,"وفيما انتم ذاهبون اكرزوا قائلين انه قد اقترب ملكوت السموات"));
arasuperobject.biblemap.put("b40_10_8", new arasuperobject(40,10,8,"اشفوا مرضى. طهروا برصا. اقيموا موتى. اخرجوا شياطين. مجانا اخذتم مجانا اعطوا"));
arasuperobject.biblemap.put("b40_10_9", new arasuperobject(40,10,9,"لا تقتنوا ذهبا ولا فضة ولا نحاسا في مناطقكم"));
arasuperobject.biblemap.put("b40_10_10", new arasuperobject(40,10,10,"ولا مزودا للطريق ولا ثوبين ولا احذية ولا عصا. لان الفاعل مستحق طعامه"));
arasuperobject.biblemap.put("b40_10_11", new arasuperobject(40,10,11,"واية مدينة او قرية دخلتموها فافحصوا من فيها مستحق. واقيموا هناك حتى تخرجوا"));
arasuperobject.biblemap.put("b40_10_12", new arasuperobject(40,10,12,"وحين تدخلون البيت سلموا عليه"));
arasuperobject.biblemap.put("b40_10_13", new arasuperobject(40,10,13,"فان كان البيت مستحقا فليات سلامكم عليه. ولكن ان لم يكن مستحقا فليرجع سلامكم اليكم"));
arasuperobject.biblemap.put("b40_10_14", new arasuperobject(40,10,14,"ومن لا يقبلكم ولا يسمع كلامكم فاخرجوا خارجا من ذلك البيت او من تلك المدينة وانفضوا غبار ارجلكم"));
arasuperobject.biblemap.put("b40_10_15", new arasuperobject(40,10,15,"الحق اقول لكم ستكون لارض سدوم وعمورة يوم الدين حالة اكثر احتمالا مما لتلك المدينة"));
arasuperobject.biblemap.put("b40_10_16", new arasuperobject(40,10,16,"ها انا ارسلكم كغنم في وسط ذئاب. فكونوا حكماء كالحيّات وبسطاء كالحمام"));
arasuperobject.biblemap.put("b40_10_17", new arasuperobject(40,10,17,"ولكن احذروا من الناس. لانهم سيسلمونكم الى مجالس وفي مجامعهم يجلدونكم"));
arasuperobject.biblemap.put("b40_10_18", new arasuperobject(40,10,18,"وتساقون امام ولاة وملوك من اجلي شهادة لهم وللامم"));
arasuperobject.biblemap.put("b40_10_19", new arasuperobject(40,10,19,"فمتى اسلموكم فلا تهتموا كيف او بما تتكلمون. لانكم تعطون في تلك الساعة ما تتكلمون به"));
arasuperobject.biblemap.put("b40_10_20", new arasuperobject(40,10,20,"لان لستم انتم المتكلمين بل روح ابيكم الذي يتكلم فيكم"));
arasuperobject.biblemap.put("b40_10_21", new arasuperobject(40,10,21,"وسيسلم الاخ اخاه الى الموت والاب ولده. ويقوم الاولاد على والديهم ويقتلونهم"));
arasuperobject.biblemap.put("b40_10_22", new arasuperobject(40,10,22,"وتكونون مبغضين من الجميع من اجل اسمي. ولكن الذي يصبر الى المنتهى فهذا يخلص"));
arasuperobject.biblemap.put("b40_10_23", new arasuperobject(40,10,23,"ومتى طردوكم في هذه المدينة فاهربوا الى الاخرى. فاني الحق اقول لكم لا تكملون مدن اسرائيل حتى يأتي ابن الانسان"));
arasuperobject.biblemap.put("b40_10_24", new arasuperobject(40,10,24,"ليس التلميذ افضل من المعلم ولا العبد افضل من سيده"));
arasuperobject.biblemap.put("b40_10_25", new arasuperobject(40,10,25,"يكفي التلميذ ان يكون كمعلمه والعبد كسيده. ان كانوا قد لقبوا رب البيت بعلزبول فكم بالحري اهل بيته"));
arasuperobject.biblemap.put("b40_10_26", new arasuperobject(40,10,26,"فلا تخافوهم. لان ليس مكتوم لن يستعلن ولا خفي لن يعرف"));
arasuperobject.biblemap.put("b40_10_27", new arasuperobject(40,10,27,"الذي اقوله لكم في الظلمة قولوه في النور. والذي تسمعونه في الاذن نادوا به على السطوح"));
arasuperobject.biblemap.put("b40_10_28", new arasuperobject(40,10,28,"ولا تخافوا من الذين يقتلون الجسد ولكن النفس لا يقدرون ان يقتلوها. بل خافوا بالحري من الذي يقدر ان يهلك النفس والجسد كليهما في جهنم"));
arasuperobject.biblemap.put("b40_10_29", new arasuperobject(40,10,29,"أليس عصفوران يباعان بفلس. وواحد منهما لا يسقط على الارض بدون ابيكم"));
arasuperobject.biblemap.put("b40_10_30", new arasuperobject(40,10,30,"واما انتم فحتى شعور رؤوسكم جميعها محصاة"));
arasuperobject.biblemap.put("b40_10_31", new arasuperobject(40,10,31,"فلا تخافوا. انتم افضل من عصافير كثيرة"));
arasuperobject.biblemap.put("b40_10_32", new arasuperobject(40,10,32,"فكل من يعترف بي قدام الناس اعترف انا ايضا به قدام ابي الذي في السموات"));
arasuperobject.biblemap.put("b40_10_33", new arasuperobject(40,10,33,"ولكن من ينكرني قدام الناس انكره انا ايضا قدام ابي الذي في السموات"));
arasuperobject.biblemap.put("b40_10_34", new arasuperobject(40,10,34,"لا تظنوا اني جئت لألقي سلاما على الارض. ما جئت لألقي سلاما بل سيفا"));
arasuperobject.biblemap.put("b40_10_35", new arasuperobject(40,10,35,"فاني جئت لأفرّق الانسان ضد ابيه والابنة ضد امها والكنة ضد حماتها"));
arasuperobject.biblemap.put("b40_10_36", new arasuperobject(40,10,36,"واعداء الانسان اهل بيته"));
arasuperobject.biblemap.put("b40_10_37", new arasuperobject(40,10,37,"من احب ابا او اما اكثر مني فلا يستحقني. ومن احب ابنا او ابنة اكثر مني فلا يستحقني"));
arasuperobject.biblemap.put("b40_10_38", new arasuperobject(40,10,38,"ومن لا ياخذ صليبه ويتبعني فلا يستحقني"));
arasuperobject.biblemap.put("b40_10_39", new arasuperobject(40,10,39,"من وجد حياته يضيعها. ومن اضاع حياته من اجلي يجدها"));
arasuperobject.biblemap.put("b40_10_40", new arasuperobject(40,10,40,"من يقبلكم يقبلني ومن يقبلني يقبل الذي ارسلني"));
arasuperobject.biblemap.put("b40_10_41", new arasuperobject(40,10,41,"من يقبل نبيا باسم نبي فأجر نبي ياخذ. ومن يقبل بارا باسم بار فأجر بار ياخذ"));
arasuperobject.biblemap.put("b40_10_42", new arasuperobject(40,10,42,"ومن سقى احد هؤلاء الصغار كاس ماء بارد فقط باسم تلميذ فالحق اقول لكم انه لا يضيع اجره"));
arasuperobject.biblemap.put("b40_11_1", new arasuperobject(40,11,1,"ولما اكمل يسوع أمره لتلاميذه الاثني عشر انصرف من هناك ليعلّم ويكرز في مدنهم"));
arasuperobject.biblemap.put("b40_11_2", new arasuperobject(40,11,2,"اما يوحنا فلما سمع في السجن باعمال المسيح ارسل اثنين من تلاميذه"));
arasuperobject.biblemap.put("b40_11_3", new arasuperobject(40,11,3,"وقال له انت هو الآتي ام ننتظر آخر"));
arasuperobject.biblemap.put("b40_11_4", new arasuperobject(40,11,4,"فاجاب يسوع وقال لهما اذهبا واخبرا يوحنا بما تسمعان وتنظران"));
arasuperobject.biblemap.put("b40_11_5", new arasuperobject(40,11,5,"العمي يبصرون والعرج يمشون والبرص يطهرون والصم يسمعون والموتى يقومون والمساكين يبشّرون"));
arasuperobject.biblemap.put("b40_11_6", new arasuperobject(40,11,6,"وطوبى لمن لا يعثر فيّ"));
arasuperobject.biblemap.put("b40_11_7", new arasuperobject(40,11,7,"وبينما ذهب هذان ابتدأ يسوع يقول للجموع عن يوحنا ماذا خرجتم الى البرية لتنظروا. أقصبة تحركها الريح"));
arasuperobject.biblemap.put("b40_11_8", new arasuperobject(40,11,8,"لكن ماذا خرجتم لتنظروا. أإنسانا لابسا ثيابا ناعمة. هوذا الذين يلبسون الثياب الناعمة هم في بيوت الملوك"));
arasuperobject.biblemap.put("b40_11_9", new arasuperobject(40,11,9,"لكن ماذا خرجتم لتنظروا. أنبيا. نعم اقول لكم وافضل من نبي"));
arasuperobject.biblemap.put("b40_11_10", new arasuperobject(40,11,10,"فان هذا هو الذي كتب عنه ها انا ارسل امام وجهك ملاكي الذي يهيئ طريقك قدامك"));
arasuperobject.biblemap.put("b40_11_11", new arasuperobject(40,11,11,"الحق اقول لكم لم يقم بين المولودين من النساء اعظم من يوحنا المعمدان. ولكن الاصغر في ملكوت السموات اعظم منه"));
arasuperobject.biblemap.put("b40_11_12", new arasuperobject(40,11,12,"ومن ايام يوحنا المعمدان الى الآن ملكوت السموات يغصب والغاصبون يختطفونه"));
arasuperobject.biblemap.put("b40_11_13", new arasuperobject(40,11,13,"لان جميع الانبياء والناموس الى يوحنا تنبأوا"));
arasuperobject.biblemap.put("b40_11_14", new arasuperobject(40,11,14,"وان اردتم ان تقبلوا فهذا هو ايليا المزمع ان ياتي"));
arasuperobject.biblemap.put("b40_11_15", new arasuperobject(40,11,15,"من له اذنان للسمع فليسمع"));
arasuperobject.biblemap.put("b40_11_16", new arasuperobject(40,11,16,"وبمن اشبّه هذا الجيل. يشبه اولادا جالسين في الاسواق ينادون الى اصحابهم"));
arasuperobject.biblemap.put("b40_11_17", new arasuperobject(40,11,17,"ويقولون زمّرنا لكم فلم ترقصوا. نحنا لكم فلم تلطموا"));
arasuperobject.biblemap.put("b40_11_18", new arasuperobject(40,11,18,"لانه جاء يوحنا لا يأكل ولا يشرب. فيقولون فيه شيطان"));
arasuperobject.biblemap.put("b40_11_19", new arasuperobject(40,11,19,"جاء ابن الانسان يأكل ويشرب. فيقولون هوذا انسان اكول وشريب خمر. محب للعشارين والخطاة. والحكمة تبررت من بنيها"));
arasuperobject.biblemap.put("b40_11_20", new arasuperobject(40,11,20,"حينئذ ابتدأ يوبّخ المدن التي صنعت فيها اكثر قواته لانها لم تتب"));
arasuperobject.biblemap.put("b40_11_21", new arasuperobject(40,11,21,"ويل لك يا كورزين. ويل لك يا بيت صيدا. لانه لو صنعت في صور وصيدا القوات المصنوعة فيكما لتابتا قديما في المسوح والرماد"));
arasuperobject.biblemap.put("b40_11_22", new arasuperobject(40,11,22,"ولكن اقول لكم ان صور وصيدا تكون لهما حالة اكثر احتمالا يوم الدين مما لكما"));
arasuperobject.biblemap.put("b40_11_23", new arasuperobject(40,11,23,"وانت يا كفرناحوم المرتفعة الى السماء ستهبطين الى الهاوية. لانه لو صنعت في سدوم القوات المصنوعة فيك لبقيت الى اليوم"));
arasuperobject.biblemap.put("b40_11_24", new arasuperobject(40,11,24,"ولكن اقول لكم ان ارض سدوم تكون لها حالة اكثر احتمالا يوم الدين مما لك"));
arasuperobject.biblemap.put("b40_11_25", new arasuperobject(40,11,25,"في ذلك الوقت اجاب يسوع وقال احمدك ايها الآب رب السماء والارض لانك اخفيت هذه عن الحكماء والفهماء واعلنتها للاطفال"));
arasuperobject.biblemap.put("b40_11_26", new arasuperobject(40,11,26,"نعم ايها الآب لان هكذا صارت المسرة امامك"));
arasuperobject.biblemap.put("b40_11_27", new arasuperobject(40,11,27,"كل شيء قد دفع اليّ من ابي. وليس احد يعرف الابن الا الآب. ولا احد يعرف الآب الا الابن ومن اراد الابن ان يعلن له"));
arasuperobject.biblemap.put("b40_11_28", new arasuperobject(40,11,28,"تعالوا اليّ يا جميع المتعبين والثقيلي الاحمال وانا اريحكم"));
arasuperobject.biblemap.put("b40_11_29", new arasuperobject(40,11,29,"احملوا نيري عليكم وتعلموا مني. لاني وديع ومتواضع القلب. فتجدوا راحة لنفوسكم"));
arasuperobject.biblemap.put("b40_11_30", new arasuperobject(40,11,30,"لان نيري هين وحملي خفيف"));
arasuperobject.biblemap.put("b40_12_1", new arasuperobject(40,12,1,"في ذلك الوقت ذهب يسوع في السبت بين الزروع. فجاع تلاميذه وابتدأوا يقطفون سنابل ويأكلون"));
arasuperobject.biblemap.put("b40_12_2", new arasuperobject(40,12,2,"فالفريسيون لما نظروا قالوا له هوذا تلاميذك يفعلون ما لا يحل فعله في السبت"));
arasuperobject.biblemap.put("b40_12_3", new arasuperobject(40,12,3,"فقال لهم أما قرأتم ما فعله داود حين جاع هو والذين معه"));
arasuperobject.biblemap.put("b40_12_4", new arasuperobject(40,12,4,"كيف دخل بيت الله واكل خبز التقدمة الذي لم يحل اكله له ولا للذين معه بل للكهنة فقط"));
arasuperobject.biblemap.put("b40_12_5", new arasuperobject(40,12,5,"او ما قرأتم في التوراة ان الكهنة في السبت في الهيكل يدنسون السبت وهم ابرياء"));
arasuperobject.biblemap.put("b40_12_6", new arasuperobject(40,12,6,"ولكن اقول لكم ان ههنا اعظم من الهيكل"));
arasuperobject.biblemap.put("b40_12_7", new arasuperobject(40,12,7,"فلو علمتم ما هو. اني اريد رحمة لا ذبيحة. لما حكمتم على الابرياء"));
arasuperobject.biblemap.put("b40_12_8", new arasuperobject(40,12,8,"فان ابن الانسان هو رب السبت ايضا"));
arasuperobject.biblemap.put("b40_12_9", new arasuperobject(40,12,9,"ثم انصرف من هناك وجاء الى مجمعهم"));
arasuperobject.biblemap.put("b40_12_10", new arasuperobject(40,12,10,"واذا انسان يده يابسة. فسألوه قائلين هل يحل الابراء في السبوت. لكي يشتكوا عليه"));
arasuperobject.biblemap.put("b40_12_11", new arasuperobject(40,12,11,"فقال لهم اي انسان منكم يكون له خروف واحد فان سقط هذا في السبت في حفرة أفما يمسكه ويقيمه"));
arasuperobject.biblemap.put("b40_12_12", new arasuperobject(40,12,12,"فالانسان كم هو افضل من الخروف. اذا يحل فعل الخير في السبوت"));
arasuperobject.biblemap.put("b40_12_13", new arasuperobject(40,12,13,"ثم قال للانسان مدّ يدك. فمدها. فعادت صحيحة كالاخرى"));
arasuperobject.biblemap.put("b40_12_14", new arasuperobject(40,12,14,"فلما خرج الفريسيون تشاوروا عليه لكي يهلكوه"));
arasuperobject.biblemap.put("b40_12_15", new arasuperobject(40,12,15,"فعلم يسوع وانصرف من هناك. وتبعته جموع كثيرة فشفاهم جميعا"));
arasuperobject.biblemap.put("b40_12_16", new arasuperobject(40,12,16,"واوصاهم ان لا يظهروه"));
arasuperobject.biblemap.put("b40_12_17", new arasuperobject(40,12,17,"لكي يتم ما قيل باشعياء النبي القائل"));
arasuperobject.biblemap.put("b40_12_18", new arasuperobject(40,12,18,"هوذا فتاي الذي اخترته حبيبي الذي سرّت به نفسي. اضع روحي عليه فيخبر الامم بالحق"));
arasuperobject.biblemap.put("b40_12_19", new arasuperobject(40,12,19,"لا يخاصم ولا يصيح ولا يسمع احد في الشوارع صوته"));
arasuperobject.biblemap.put("b40_12_20", new arasuperobject(40,12,20,"قصبة مرضوضة لا يقصف. وفتيلة مدخنة لا يطفئ. حتى يخرج الحق الى النصرة"));
arasuperobject.biblemap.put("b40_12_21", new arasuperobject(40,12,21,"وعلى اسمه يكون رجاء الامم"));
arasuperobject.biblemap.put("b40_12_22", new arasuperobject(40,12,22,"حينئذ أحضر اليه مجنون اعمى واخرس. فشفاه حتى ان الاعمى الاخرس تكلم وابصر"));
arasuperobject.biblemap.put("b40_12_23", new arasuperobject(40,12,23,"فبهت كل الجموع وقالوا ألعل هذا هو ابن داود"));
arasuperobject.biblemap.put("b40_12_24", new arasuperobject(40,12,24,"اما الفريسيون فلما سمعوا قالوا هذا لا يخرج الشياطين الا ببعلزبول رئيس الشياطين"));
arasuperobject.biblemap.put("b40_12_25", new arasuperobject(40,12,25,"فعلم يسوع افكارهم وقال لهم كل مملكة منقسمة على ذاتها تخرب. وكل مدينة او بيت منقسم على ذاته لا يثبت"));
arasuperobject.biblemap.put("b40_12_26", new arasuperobject(40,12,26,"فان كان الشيطان يخرج الشيطان فقد انقسم على ذاته. فكيف تثبت مملكته"));
arasuperobject.biblemap.put("b40_12_27", new arasuperobject(40,12,27,"وان كنت انا ببعلزبول اخرج الشياطين فابناؤكم بمن يخرجون. لذلك هم يكونون قضاتكم"));
arasuperobject.biblemap.put("b40_12_28", new arasuperobject(40,12,28,"ولكن ان كنت انا بروح الله اخرج الشياطين فقد اقبل عليكم ملكوت الله"));
arasuperobject.biblemap.put("b40_12_29", new arasuperobject(40,12,29,"ام كيف يستطيع احد ان يدخل بيت القوي وينهب امتعته ان لم يربط القوي اولا. وحينئذ ينهب بيته"));
arasuperobject.biblemap.put("b40_12_30", new arasuperobject(40,12,30,"من ليس معي فهو علي ومن لا يجمع معي فهو يفرق"));
arasuperobject.biblemap.put("b40_12_31", new arasuperobject(40,12,31,"لذلك اقول لكم كل خطية وتجديف يغفر للناس. واما التجديف على الروح فلن يغفر للناس"));
arasuperobject.biblemap.put("b40_12_32", new arasuperobject(40,12,32,"ومن قال كلمة على ابن الانسان يغفر له. واما من قال على الروح القدس فلن يغفر له لا في هذا العالم ولا في الآتي"));
arasuperobject.biblemap.put("b40_12_33", new arasuperobject(40,12,33,"اجعلوا الشجرة جيدة وثمرها جيدا. او اجعلوا الشجرة رديّة وثمرها رديّا. لان من الثمر تعرف الشجرة"));
arasuperobject.biblemap.put("b40_12_34", new arasuperobject(40,12,34,"يا اولاد الافاعي كيف تقدرون ان تتكلموا بالصالحات وانتم اشرار. فانه من فضلة القلب يتكلم الفم"));
arasuperobject.biblemap.put("b40_12_35", new arasuperobject(40,12,35,"الانسان الصالح من الكنز الصالح في القلب يخرج الصالحات. والانسان الشرير من الكنز الشرير يخرج الشرور"));
arasuperobject.biblemap.put("b40_12_36", new arasuperobject(40,12,36,"ولكن اقول لكم ان كل كلمة بطالة يتكلم بها الناس سوف يعطون عنها حساب يوم الدين"));
arasuperobject.biblemap.put("b40_12_37", new arasuperobject(40,12,37,"لانك بكلامك تتبرر وبكلامك تدان"));
arasuperobject.biblemap.put("b40_12_38", new arasuperobject(40,12,38,"حينئذ اجاب قوم من الكتبة والفريسيين قائلين يا معلّم نريد ان نرى منك آية"));
arasuperobject.biblemap.put("b40_12_39", new arasuperobject(40,12,39,"فاجاب وقال لهم جيل شرير وفاسق يطلب آية ولا تعطى له آية الا آية يونان النبي"));
arasuperobject.biblemap.put("b40_12_40", new arasuperobject(40,12,40,"لانه كما كان يونان في بطن الحوت ثلاثة ايام وثلاث ليال هكذا يكون ابن الانسان في قلب الارض ثلاثة ايام وثلاث ليال"));
arasuperobject.biblemap.put("b40_12_41", new arasuperobject(40,12,41,"رجال نينوى سيقومون في الدين مع هذا الجيل ويدينونه لانهم تابوا بمناداة يونان. وهوذا اعظم من يونان ههنا"));
arasuperobject.biblemap.put("b40_12_42", new arasuperobject(40,12,42,"ملكة التيمن ستقوم في الدين مع هذا الجيل وتدينه. لانها اتت من اقاصي الارض لتسمع حكمة سليمان. وهوذا اعظم من سليمان ههنا"));
arasuperobject.biblemap.put("b40_12_43", new arasuperobject(40,12,43,"اذا خرج الروح النجس من الانسان يجتاز في اماكن ليس فيها ماء يطلب راحة ولا يجد"));
arasuperobject.biblemap.put("b40_12_44", new arasuperobject(40,12,44,"ثم يقول ارجع الى بيتي الذي خرجت منه. فيأتي ويجده فارغا مكنوسا مزينا"));
arasuperobject.biblemap.put("b40_12_45", new arasuperobject(40,12,45,"ثم يذهب وياخذ معه سبعة ارواح أخر اشر منه فتدخل وتسكن هناك. فتصير اواخر ذلك الانسان اشر من اوائله. هكذا يكون ايضا لهذا الجيل الشرير"));
arasuperobject.biblemap.put("b40_12_46", new arasuperobject(40,12,46,"وفيما هو يكلم الجموع اذا امه واخوته قد وقفوا خارجا طالبين ان يكلموه"));
arasuperobject.biblemap.put("b40_12_47", new arasuperobject(40,12,47,"فقال له واحد هوذا امك واخوتك واقفون خارجا طالبين ان يكلموك"));
arasuperobject.biblemap.put("b40_12_48", new arasuperobject(40,12,48,"فاجاب وقال للقائل له. من هي امي ومن هم اخوتي"));
arasuperobject.biblemap.put("b40_12_49", new arasuperobject(40,12,49,"ثم مدّ يده نحو تلاميذه وقال ها امي واخوتي"));
arasuperobject.biblemap.put("b40_12_50", new arasuperobject(40,12,50,"لان من يصنع مشيئة ابي الذي في السموات هو اخي واختي وامي"));
arasuperobject.biblemap.put("b40_13_1", new arasuperobject(40,13,1,"في ذلك اليوم خرج يسوع من البيت وجلس عند البحر"));
arasuperobject.biblemap.put("b40_13_2", new arasuperobject(40,13,2,"فاجتمع اليه جموع كثيرة حتى انه دخل السفينة وجلس. والجمع كله وقف على الشاطئ"));
arasuperobject.biblemap.put("b40_13_3", new arasuperobject(40,13,3,"فكلهم كثيرا بامثال قائلا هوذا الزارع قد خرج ليزرع"));
arasuperobject.biblemap.put("b40_13_4", new arasuperobject(40,13,4,"وفيما هو يزرع سقط بعض على الطريق. فجائت الطيور واكلته"));
arasuperobject.biblemap.put("b40_13_5", new arasuperobject(40,13,5,"وسقط آخر على الاماكن المحجرة حيث لم تكن له تربة كثيرة. فنبت حالا اذ لم يكن له عمق ارض"));
arasuperobject.biblemap.put("b40_13_6", new arasuperobject(40,13,6,"ولكن لما اشرقت الشمس احترق. واذ لم يكن له اصل جف"));
arasuperobject.biblemap.put("b40_13_7", new arasuperobject(40,13,7,"وسقط آخر على الشوك فطلع الشوك وخنقه"));
arasuperobject.biblemap.put("b40_13_8", new arasuperobject(40,13,8,"وسقط آخر على الارض الجيدة. فاعطى ثمرا. بعض مئة وآخر ستين وآخر ثلاثين"));
arasuperobject.biblemap.put("b40_13_9", new arasuperobject(40,13,9,"من له اذنان للسمع فليسمع"));
arasuperobject.biblemap.put("b40_13_10", new arasuperobject(40,13,10,"فتقدم التلاميذ وقالوا له لماذا تكلمهم بامثال"));
arasuperobject.biblemap.put("b40_13_11", new arasuperobject(40,13,11,"فاجاب وقال لهم لانه قد أعطي لكم ان تعرفوا اسرار ملكوت السموات. واما لأولئك فلم يعط"));
arasuperobject.biblemap.put("b40_13_12", new arasuperobject(40,13,12,"فان من له سيعطى ويزاد. واما من ليس له فالذي عنده سيؤخذ منه"));
arasuperobject.biblemap.put("b40_13_13", new arasuperobject(40,13,13,"من اجل هذا اكلمهم بامثال. لانهم مبصرين لا يبصرون وسامعين لا يسمعون ولا يفهمون"));
arasuperobject.biblemap.put("b40_13_14", new arasuperobject(40,13,14,"فقد تمت فيهم نبوة اشعياء القائلة تسمعون سمعا ولا تفهمون. ومبصرين تبصرون ولا تنظرون"));
arasuperobject.biblemap.put("b40_13_15", new arasuperobject(40,13,15,"لان قلب هذا الشعب قد غلظ. وآذانهم قد ثقل سماعها. وغمضوا عيونهم لئلا يبصروا بعيونهم ويسمعوا بآذانهم ويفهموا بقلوبهم ويرجعوا فاشفيهم"));
arasuperobject.biblemap.put("b40_13_16", new arasuperobject(40,13,16,"ولكن طوبى لعيونكم لانها تبصر. ولآذانكم لانها تسمع"));
arasuperobject.biblemap.put("b40_13_17", new arasuperobject(40,13,17,"فاني الحق اقول لكم ان انبياء وابرارا كثيرين اشتهوا ان يروا ما انتم ترون ولم يروا. وان يسمعوا ما انتم تسمعون ولم يسمعوا"));
arasuperobject.biblemap.put("b40_13_18", new arasuperobject(40,13,18,"فاسمعوا انتم مثل الزارع"));
arasuperobject.biblemap.put("b40_13_19", new arasuperobject(40,13,19,"كل من يسمع كلمة الملكوت ولا يفهم فيأتي الشرير ويخطف ما قد زرع في قلبه. هذا هو المزوع على الطريق"));
arasuperobject.biblemap.put("b40_13_20", new arasuperobject(40,13,20,"والمزروع على الاماكن المحجرة هو الذي يسمع الكلمة وحالا يقبلها بفرح"));
arasuperobject.biblemap.put("b40_13_21", new arasuperobject(40,13,21,"ولكن ليس له اصل في ذاته بل هو الى حين. فاذا حدث ضيق او اضطهاد من اجل الكلمة فحالا يعثر"));
arasuperobject.biblemap.put("b40_13_22", new arasuperobject(40,13,22,"والمزروع بين الشوك هو الذي يسمع الكلمة. وهم هذا العالم وغرور الغنى يخنقان الكلمة فيصير بلا ثمر"));
arasuperobject.biblemap.put("b40_13_23", new arasuperobject(40,13,23,"واما المزروع على الارض الجيدة فهو الذي يسمع الكلمة ويفهم. وهو الذي يأتي بثمر فيصنع بعض مئة وآخر ستين وآخر ثلاثين"));
arasuperobject.biblemap.put("b40_13_24", new arasuperobject(40,13,24,"قدم لهم مثلا آخر قائلا. يشبه ملكوت السموات انسانا زرع زرعا جيدا في حقله"));
arasuperobject.biblemap.put("b40_13_25", new arasuperobject(40,13,25,"وفيما الناس نيام جاء عدوه وزرع زوانا في وسط الحنطة ومضى"));
arasuperobject.biblemap.put("b40_13_26", new arasuperobject(40,13,26,"فلما طلع النبات وصنع ثمرا حينئذ ظهر الزوان ايضا"));
arasuperobject.biblemap.put("b40_13_27", new arasuperobject(40,13,27,"فجاء عبيد رب البيت وقالوا له يا سيد أليس زرعا جيدا زرعت في حقلك. فمن اين له زوان"));
arasuperobject.biblemap.put("b40_13_28", new arasuperobject(40,13,28,"فقال لهم. انسان عدو فعل هذا. فقال له العبيد أتريد ان نذهب ونجمعه"));
arasuperobject.biblemap.put("b40_13_29", new arasuperobject(40,13,29,"فقال لا. لئلا تقلعوا الحنطة مع الزوان وانتم تجمعونه"));
arasuperobject.biblemap.put("b40_13_30", new arasuperobject(40,13,30,"دعوهما ينميان كلاهما معا الى الحصاد. وفي وقت الحصاد اقول للحصادين اجمعوا اولا الزوان واحزموه حزما ليحرق. واما الحنطة فاجمعوها الى مخزني"));
arasuperobject.biblemap.put("b40_13_31", new arasuperobject(40,13,31,"قدم لهم مثلا آخر قائلا. يشبه ملكوت السموات حبة خردل اخذها انسان وزرعها في حقله"));
arasuperobject.biblemap.put("b40_13_32", new arasuperobject(40,13,32,"وهي اصغر جميع البزور. لكن متى نمت فهي اكبر البقول. وتصير شجرة حتى ان طيور السماء تأتي وتتآوى في اغصانها"));
arasuperobject.biblemap.put("b40_13_33", new arasuperobject(40,13,33,"قال لهم مثلا آخر. يشبه ملكوت السموات خميرة اخذتها امرأة وخبأتها في ثلاثة اكيال دقيق حتى اختمر الجميع"));
arasuperobject.biblemap.put("b40_13_34", new arasuperobject(40,13,34,"هذا كله كلم به يسوع الجموع بامثال. وبدون مثل لم يكن يكلمهم"));
arasuperobject.biblemap.put("b40_13_35", new arasuperobject(40,13,35,"لكي يتم ما قيل بالنبي القائل سأفتح بامثال فمي وانطق بمكتومات منذ تأسيس العالم"));
arasuperobject.biblemap.put("b40_13_36", new arasuperobject(40,13,36,"حينئذ صرف يسوع الجموع وجاء الى البيت. فتقدم اليه تلاميذه قائلين فسّر لنا مثل زوان الحقل"));
arasuperobject.biblemap.put("b40_13_37", new arasuperobject(40,13,37,"فاجاب وقال لهم. الزارع الزرع الجيد هو ابن الانسان"));
arasuperobject.biblemap.put("b40_13_38", new arasuperobject(40,13,38,"والحقل هو العالم. والزرع الجيد هو بنو الملكوت. والزوان هو بنو الشرير"));
arasuperobject.biblemap.put("b40_13_39", new arasuperobject(40,13,39,"والعدو الذي زرعه هو ابليس. والحصاد هو انقضاء العالم. والحصادون هم الملائكة"));
arasuperobject.biblemap.put("b40_13_40", new arasuperobject(40,13,40,"فكما يجمع الزوان ويحرق بالنار هكذا يكون في انقضاء هذا العالم"));
arasuperobject.biblemap.put("b40_13_41", new arasuperobject(40,13,41,"يرسل ابن الانسان ملائكته فيجمعون من ملكوته جميع المعاثر وفاعلي الاثم"));
arasuperobject.biblemap.put("b40_13_42", new arasuperobject(40,13,42,"ويطرحونهم في اتون النار. هناك يكون البكاء وصرير الاسنان"));
arasuperobject.biblemap.put("b40_13_43", new arasuperobject(40,13,43,"حينئذ يضيء الابرار كالشمس في ملكوت ابيهم. من له اذنان للسمع فليسمع"));
arasuperobject.biblemap.put("b40_13_44", new arasuperobject(40,13,44,"ايضا يشبه ملكوت السموات كنزا مخفى في حقل وجده انسان فاخفاه ومن فرحه مضى وباع كل ما كان له واشترى ذلك الحقل"));
arasuperobject.biblemap.put("b40_13_45", new arasuperobject(40,13,45,"ايضا يشبه ملكوت السموات انسانا تاجرا يطلب لآلئ حسنة"));
arasuperobject.biblemap.put("b40_13_46", new arasuperobject(40,13,46,"فلما وجد لؤلؤة واحدة كثيرة الثمن مضى وباع كل ما كان له واشتراها"));
arasuperobject.biblemap.put("b40_13_47", new arasuperobject(40,13,47,"ايضا يشبه ملكوت السموات شبكة مطروحة في البحر وجامعة من كل نوع"));
arasuperobject.biblemap.put("b40_13_48", new arasuperobject(40,13,48,"فلما امتلأت اصعدوها على الشاطئ وجلسوا وجمعوا الجياد الى اوعية. واما الاردياء فطرحوها خارجا"));
arasuperobject.biblemap.put("b40_13_49", new arasuperobject(40,13,49,"هكذا يكون في انقضاء العالم. يخرج الملائكة ويفرزون الاشرار من بين الابرار"));
arasuperobject.biblemap.put("b40_13_50", new arasuperobject(40,13,50,"ويطرحونهم في اتون النار. هناك يكون البكاء وصرير الاسنان"));
arasuperobject.biblemap.put("b40_13_51", new arasuperobject(40,13,51,"قال لهم يسوع أفهمتم هذا كله. فقالوا نعم يا سيد"));
arasuperobject.biblemap.put("b40_13_52", new arasuperobject(40,13,52,"فقال لهم. من اجل ذلك كل كاتب متعلم في ملكوت السموات يشبه رجلا رب بيت يخرج من كنزه جددا وعتقاء"));
arasuperobject.biblemap.put("b40_13_53", new arasuperobject(40,13,53,"ولما اكمل يسوع هذه الامثال انتقل من هناك"));
arasuperobject.biblemap.put("b40_13_54", new arasuperobject(40,13,54,"ولما جاء الى وطنه كان يعلّمهم في مجمعهم حتى بهتوا وقالوا من اين لهذا هذه الحكمة والقوات"));
arasuperobject.biblemap.put("b40_13_55", new arasuperobject(40,13,55,"أليس هذا ابن النجار. أليست امه تدعى مريم واخوته يعقوب ويوسي وسمعان ويهوذا"));
arasuperobject.biblemap.put("b40_13_56", new arasuperobject(40,13,56,"أوليست اخواته جميعهنّ عندنا فمن اين لهذا هذه كلها"));
arasuperobject.biblemap.put("b40_13_57", new arasuperobject(40,13,57,"فكانوا يعثرون به. واما يسوع فقال لهم ليس نبي بلا كرامة الا في وطنه وفي بيته"));
arasuperobject.biblemap.put("b40_13_58", new arasuperobject(40,13,58,"ولم يصنع هناك قوات كثيرة لعدم ايمانهم"));
arasuperobject.biblemap.put("b40_14_1", new arasuperobject(40,14,1,"في ذلك الوقت سمع هيرودس رئيس الربع خبر يسوع"));
arasuperobject.biblemap.put("b40_14_2", new arasuperobject(40,14,2,"فقال لغلمانه هذا هو يوحنا المعمدان. قد قام من الاموات ولذلك تعمل به القوات"));
arasuperobject.biblemap.put("b40_14_3", new arasuperobject(40,14,3,"فان هيرودس كان قد امسك يوحنا واوثقه وطرحه في سجن من اجل هيروديا امرأة فيلبس اخيه"));
arasuperobject.biblemap.put("b40_14_4", new arasuperobject(40,14,4,"لان يوحنا كان يقول له لا يحل ان تكون لك"));
arasuperobject.biblemap.put("b40_14_5", new arasuperobject(40,14,5,"ولما اراد ان يقتله خاف من الشعب. لانه كان عندهم مثل نبي"));
arasuperobject.biblemap.put("b40_14_6", new arasuperobject(40,14,6,"ثم لما صار مولد هيرودس رقصت ابنة هيروديا في الوسط فسرّت هيرودس"));
arasuperobject.biblemap.put("b40_14_7", new arasuperobject(40,14,7,"من ثم وعد بقسم انه مهما طلبت يعطيها"));
arasuperobject.biblemap.put("b40_14_8", new arasuperobject(40,14,8,"فهي اذ كانت قد تلقنت من امها قالت أعطيني ههنا على طبق راس يوحنا المعمدان"));
arasuperobject.biblemap.put("b40_14_9", new arasuperobject(40,14,9,"فاغتم الملك. ولكن من اجل الاقسام والمتكئين معه أمر ان يعطى"));
arasuperobject.biblemap.put("b40_14_10", new arasuperobject(40,14,10,"فارسل وقطع راس يوحنا في السجن"));
arasuperobject.biblemap.put("b40_14_11", new arasuperobject(40,14,11,"فأحضر رأسه على طبق ودفع الى الصبية. فجاءت به الى امها"));
arasuperobject.biblemap.put("b40_14_12", new arasuperobject(40,14,12,"فتقدم تلاميذه ورفعوا الجسد ودفنوه. ثم أتوا واخبروا يسوع"));
arasuperobject.biblemap.put("b40_14_13", new arasuperobject(40,14,13,"فلما سمع يسوع انصرف من هناك في سفينة الى موضع خلاء منفردا. فسمع الجموع وتبعوه مشاة من المدن"));
arasuperobject.biblemap.put("b40_14_14", new arasuperobject(40,14,14,"فلما خرج يسوع ابصر جمعا كثيرا فتحنن عليهم وشفى مرضاهم"));
arasuperobject.biblemap.put("b40_14_15", new arasuperobject(40,14,15,"ولما صار المساء تقدم اليه تلاميذه قائلين الموضع خلاء والوقت قد مضى. اصرف الجموع لكي يمضوا الى القرى ويبتاعوا لهم طعاما"));
arasuperobject.biblemap.put("b40_14_16", new arasuperobject(40,14,16,"فقال لهم يسوع لا حاجة لهم ان يمضوا. اعطوهم انتم ليأكلوا"));
arasuperobject.biblemap.put("b40_14_17", new arasuperobject(40,14,17,"فقالوا له ليس عندنا ههنا الا خمسة ارغفة وسمكتان"));
arasuperobject.biblemap.put("b40_14_18", new arasuperobject(40,14,18,"فقال ايتوني بها الى هنا"));
arasuperobject.biblemap.put("b40_14_19", new arasuperobject(40,14,19,"فامر الجموع ان يتكئوا على العشب. ثم اخذ الارغفة الخمسة والسمكتين ورفع نظره نحو السماء وبارك وكسر واعطى الارغفة للتلاميذ والتلاميذ للجموع"));
arasuperobject.biblemap.put("b40_14_20", new arasuperobject(40,14,20,"فأكل الجميع وشبعوا. ثم رفعوا ما فضل من الكسر اثنتي عشر قفة مملوءة"));
arasuperobject.biblemap.put("b40_14_21", new arasuperobject(40,14,21,"والآكلون كانوا نحو خمسة آلاف رجل ما عدا النساء والاولاد"));
arasuperobject.biblemap.put("b40_14_22", new arasuperobject(40,14,22,"وللوقت ألزم يسوع تلاميذه ان يدخلوا السفينة ويسبقوه الى العبر حتى يصرف الجموع"));
arasuperobject.biblemap.put("b40_14_23", new arasuperobject(40,14,23,"وبعدما صرف الجموع صعد الى الجبل منفردا ليصلّي. ولما صار المساء كان هناك وحده"));
arasuperobject.biblemap.put("b40_14_24", new arasuperobject(40,14,24,"واما السفينة فكانت قد صارت في وسط البحر معذبة من الامواج. لان الريح كانت مضادة"));
arasuperobject.biblemap.put("b40_14_25", new arasuperobject(40,14,25,"وفي الهزيع الرابع من الليل مضى اليهم يسوع ماشيا على البحر"));
arasuperobject.biblemap.put("b40_14_26", new arasuperobject(40,14,26,"فلما ابصره التلاميذ ماشيا على البحر اضطربوا قائلين انه خيال. ومن الخوف صرخوا"));
arasuperobject.biblemap.put("b40_14_27", new arasuperobject(40,14,27,"فللوقت كلمهم يسوع قائلا تشجعوا. انا هو. لا تخافوا"));
arasuperobject.biblemap.put("b40_14_28", new arasuperobject(40,14,28,"فاجابه بطرس وقال يا سيد ان كنت انت هو فمرني ان آتي اليك على الماء"));
arasuperobject.biblemap.put("b40_14_29", new arasuperobject(40,14,29,"فقال تعال. فنزل بطرس من السفينة ومشى على الماء ليأتي الى يسوع"));
arasuperobject.biblemap.put("b40_14_30", new arasuperobject(40,14,30,"ولكن لما رأى الريح شديدة خاف واذ ابتدأ يغرق صرخ قائلا يا رب نجني"));
arasuperobject.biblemap.put("b40_14_31", new arasuperobject(40,14,31,"ففي الحال مدّ يسوع يده وامسك به وقال له يا قليل الايمان لماذا شككت"));
arasuperobject.biblemap.put("b40_14_32", new arasuperobject(40,14,32,"ولما دخلا السفينة سكنت الريح"));
arasuperobject.biblemap.put("b40_14_33", new arasuperobject(40,14,33,"والذين في السفينة جاءوا وسجدوا له قائلين بالحقيقة انت ابن الله"));
arasuperobject.biblemap.put("b40_14_34", new arasuperobject(40,14,34,"فلما عبروا جاءوا الى ارض جنيسارت"));
arasuperobject.biblemap.put("b40_14_35", new arasuperobject(40,14,35,"فعرفه رجال ذلك المكان. فارسلوا الى جميع تلك الكورة المحيطة واحضروا اليه جميع المرضى"));
arasuperobject.biblemap.put("b40_14_36", new arasuperobject(40,14,36,"وطلبوا اليه ان يلمسوا هدب ثوبه فقط. فجميع الذين لمسوه نالوا الشفاء"));
arasuperobject.biblemap.put("b40_15_1", new arasuperobject(40,15,1,"حينئذ جاء الى يسوع كتبة وفريسيون الذين من اورشليم قائلين"));
arasuperobject.biblemap.put("b40_15_2", new arasuperobject(40,15,2,"لماذا يتعدى تلاميذك تقليد الشيوخ. فانهم لا يغسلون ايديهم حينما ياكلون خبزا"));
arasuperobject.biblemap.put("b40_15_3", new arasuperobject(40,15,3,"فاجاب وقال لهم وانتم ايضا لماذا تتعدون وصية الله بسبب تقليدكم"));
arasuperobject.biblemap.put("b40_15_4", new arasuperobject(40,15,4,"فان الله اوصى قائلا اكرم اباك وامك. ومن يشتم ابا او اما فليمت موتا"));
arasuperobject.biblemap.put("b40_15_5", new arasuperobject(40,15,5,"واما انتم فتقولون من قال لابيه او امه قربان هو الذي تنتفع به مني. فلا يكرم اباه او امه"));
arasuperobject.biblemap.put("b40_15_6", new arasuperobject(40,15,6,"فقد ابطلتم وصية الله بسبب تقليدكم"));
arasuperobject.biblemap.put("b40_15_7", new arasuperobject(40,15,7,"يا مراؤون حسنا تنبأ عنكم اشعياء قائلا"));
arasuperobject.biblemap.put("b40_15_8", new arasuperobject(40,15,8,"يقترب اليّ هذا الشعب بفمه ويكرمني بشفتيه واما قلبه فمبتعد عني بعيدا"));
arasuperobject.biblemap.put("b40_15_9", new arasuperobject(40,15,9,"وباطلا يعبدونني وهم يعلمون تعاليم هي وصايا الناس"));
arasuperobject.biblemap.put("b40_15_10", new arasuperobject(40,15,10,"ثم دعا الجمع وقال لهم اسمعوا وافهموا"));
arasuperobject.biblemap.put("b40_15_11", new arasuperobject(40,15,11,"ليس ما يدخل الفم ينجس الانسان. بل ما يخرج من الفم هذا ينجس الانسان"));
arasuperobject.biblemap.put("b40_15_12", new arasuperobject(40,15,12,"حينئذ تقدم تلاميذه وقالوا له اتعلم ان الفريسيين لما سمعوا القول نفروا"));
arasuperobject.biblemap.put("b40_15_13", new arasuperobject(40,15,13,"فاجاب وقال كل غرس لم يغرسه ابي السماوي يقلع"));
arasuperobject.biblemap.put("b40_15_14", new arasuperobject(40,15,14,"اتركوهم. هم عميان قادة عميان. وان كان اعمى يقود اعمى يسقطان كلاهما في حفرة"));
arasuperobject.biblemap.put("b40_15_15", new arasuperobject(40,15,15,"فاجاب بطرس وقال له فسّر لنا هذا المثل"));
arasuperobject.biblemap.put("b40_15_16", new arasuperobject(40,15,16,"فقال يسوع هل انتم ايضا حتى الآن غير فاهمين"));
arasuperobject.biblemap.put("b40_15_17", new arasuperobject(40,15,17,"ألا تفهمون بعد ان كل ما يدخل الفم يمضي الى الجوف ويندفع الى المخرج"));
arasuperobject.biblemap.put("b40_15_18", new arasuperobject(40,15,18,"واما ما يخرج من الفم فمن القلب يصدر. وذلك ينجس الانسان"));
arasuperobject.biblemap.put("b40_15_19", new arasuperobject(40,15,19,"لان من القلب تخرج افكار شريرة قتل زنى فسق سرقة شهادة زور تجديف"));
arasuperobject.biblemap.put("b40_15_20", new arasuperobject(40,15,20,"هذه هي التي تنجس الانسان. واما الأكل بايد غير مغسولة فلا ينجس الانسان"));
arasuperobject.biblemap.put("b40_15_21", new arasuperobject(40,15,21,"ثم خرج يسوع من هناك وانصرف الى نواحي صور وصيدا"));
arasuperobject.biblemap.put("b40_15_22", new arasuperobject(40,15,22,"واذا امرأة كنعانية خارجة من تلك التخوم صرخت اليه قائلة ارحمني يا سيد يا ابن داود. ابنتي مجنونة جدا"));
arasuperobject.biblemap.put("b40_15_23", new arasuperobject(40,15,23,"فلم يجبها بكلمة. فتقدم تلاميذه وطلبوا اليه قائلين اصرفها لانها تصيح وراءنا"));
arasuperobject.biblemap.put("b40_15_24", new arasuperobject(40,15,24,"فاجاب وقال لم أرسل الا الى خراف بيت اسرائيل الضالة"));
arasuperobject.biblemap.put("b40_15_25", new arasuperobject(40,15,25,"فأتت وسجدت له قائلة يا سيد أعنّي"));
arasuperobject.biblemap.put("b40_15_26", new arasuperobject(40,15,26,"فاجاب وقال ليس حسنا ان يؤخذ خبز البنين ويطرح للكلاب"));
arasuperobject.biblemap.put("b40_15_27", new arasuperobject(40,15,27,"فقالت نعم يا سيد. والكلاب ايضا تأكل من الفتات الذي يسقط من مائدة اربابها"));
arasuperobject.biblemap.put("b40_15_28", new arasuperobject(40,15,28,"حينئذ اجاب يسوع وقال لها يا امرأة عظيم ايمانك. ليكن لك كما تريدين. فشفيت ابنتها من تلك الساعة"));
arasuperobject.biblemap.put("b40_15_29", new arasuperobject(40,15,29,"ثم انتقل يسوع من هناك وجاء الى جانب بحر الجليل. وصعد الى الجبل وجلس هناك"));
arasuperobject.biblemap.put("b40_15_30", new arasuperobject(40,15,30,"فجاء اليه جموع كثيرة معهم عرج وعمي وخرس وشل وآخرون كثيرون. وطرحوهم عند قدمي يسوع. فشفاهم"));
arasuperobject.biblemap.put("b40_15_31", new arasuperobject(40,15,31,"حتى تعجب الجموع اذ رأوا الخرس يتكلمون والشل يصحّون والعرج يمشون والعمي يبصرون. ومجدوا اله اسرائيل"));
arasuperobject.biblemap.put("b40_15_32", new arasuperobject(40,15,32,"واما يسوع فدعا تلاميذه وقال اني اشفق على الجمع لان الآن لهم ثلاثة ايام يمكثون معي وليس لهم ما ياكلون. ولست اريد ان اصرفهم صائمين لئلا يخوّروا في الطريق"));
arasuperobject.biblemap.put("b40_15_33", new arasuperobject(40,15,33,"فقال له تلاميذه من اين لنا في البرية خبز بهذا المقدار حتى يشبع جمعا هذا عدده"));
arasuperobject.biblemap.put("b40_15_34", new arasuperobject(40,15,34,"فقال لهم يسوع كم عندكم من الخبز. فقالوا سبعة وقليل من صغار السمك"));
arasuperobject.biblemap.put("b40_15_35", new arasuperobject(40,15,35,"فأمر الجموع ان يتكئوا على الارض"));
arasuperobject.biblemap.put("b40_15_36", new arasuperobject(40,15,36,"واخذ السبع خبزات والسمك وشكر وكسر واعطى تلاميذه والتلاميذ اعطوا الجمع"));
arasuperobject.biblemap.put("b40_15_37", new arasuperobject(40,15,37,"فاكل الجميع وشبعوا. ثم رفعوا ما فضل من الكسر سبعة سلال مملوءة"));
arasuperobject.biblemap.put("b40_15_38", new arasuperobject(40,15,38,"والآكلون كانوا اربعة آلاف رجل ما عدا النساء والاولاد"));
arasuperobject.biblemap.put("b40_15_39", new arasuperobject(40,15,39,"ثم صرف الجموع وصعد الى السفينة وجاء الى تخوم مجدل"));
arasuperobject.biblemap.put("b40_16_1", new arasuperobject(40,16,1,"وجاء اليه الفريسيون والصدوقيون ليجربوه فسألوه ان يريهم آية من السماء"));
arasuperobject.biblemap.put("b40_16_2", new arasuperobject(40,16,2,"فاجاب وقال لهم اذا كان المساء قلتم صحو. لان السماء محمرة"));
arasuperobject.biblemap.put("b40_16_3", new arasuperobject(40,16,3,"وفي الصباح اليوم شتاء. لان السماء محمرة بعبوسة. يا مراؤون تعرفون ان تميّزوا وجه السماء واما علامات الازمنة فلا تستطيعون"));
arasuperobject.biblemap.put("b40_16_4", new arasuperobject(40,16,4,"جيل شرير فاسق يلتمس آية. ولا تعطى له آية الا آية يونان النبي. ثم تركهم ومضى"));
arasuperobject.biblemap.put("b40_16_5", new arasuperobject(40,16,5,"ولما جاء تلاميذه الى العبر نسوا ان ياخذوا خبزا"));
arasuperobject.biblemap.put("b40_16_6", new arasuperobject(40,16,6,"وقال لهم يسوع انظروا وتحرزوا من خمير الفريسيين والصدوقيين"));
arasuperobject.biblemap.put("b40_16_7", new arasuperobject(40,16,7,"ففكروا في انفسهم قائلين اننا لم نأخذ خبزا"));
arasuperobject.biblemap.put("b40_16_8", new arasuperobject(40,16,8,"فعلم يسوع وقال لماذا تفكرون في انفسكم يا قليلي الايمان انكم لم تأخذوا خبزا"));
arasuperobject.biblemap.put("b40_16_9", new arasuperobject(40,16,9,"أحتى الآن لا تفهمون ولا تذكرون خمس خبزات الخمسة الآلاف وكم قفة اخذتم"));
arasuperobject.biblemap.put("b40_16_10", new arasuperobject(40,16,10,"ولا سبع خبزات الاربعة الآلاف وكم سلا اخذتم"));
arasuperobject.biblemap.put("b40_16_11", new arasuperobject(40,16,11,"كيف لا تفهمون اني ليس عن الخبز قلت لكم ان تتحرزوا من خمير الفريسيين والصدوقيين"));
arasuperobject.biblemap.put("b40_16_12", new arasuperobject(40,16,12,"حينئذ فهموا انه لم يقل ان يتحرزوا من خمير الخبز بل من تعليم الفريسيين والصدوقيين"));
arasuperobject.biblemap.put("b40_16_13", new arasuperobject(40,16,13,"ولما جاء يسوع الى نواحي قيصرية فيلبس سأل تلاميذه قائلا من يقول الناس اني انا ابن الانسان"));
arasuperobject.biblemap.put("b40_16_14", new arasuperobject(40,16,14,"فقالوا. قوم يوحنا المعمدان. وآخرون ايليا. وآخرون ارميا او واحد من الانبياء"));
arasuperobject.biblemap.put("b40_16_15", new arasuperobject(40,16,15,"فقال لهم وانتم من تقولون اني انا"));
arasuperobject.biblemap.put("b40_16_16", new arasuperobject(40,16,16,"فاجاب سمعان بطرس وقال انت هو المسيح ابن الله الحي"));
arasuperobject.biblemap.put("b40_16_17", new arasuperobject(40,16,17,"فاجاب يسوع وقال له طوبى لك يا سمعان بن يونا. ان لحما ودما لم يعلن لك لكن ابي الذي في السموات"));
arasuperobject.biblemap.put("b40_16_18", new arasuperobject(40,16,18,"وانا اقول لك ايضا انت بطرس وعلى هذه الصخرة ابني كنيستي وابواب الجحيم لن تقوى عليها"));
arasuperobject.biblemap.put("b40_16_19", new arasuperobject(40,16,19,"وأعطيك مفاتيح ملكوت السموات. فكل ما تربطه على الارض يكون مربوطا في السموات. وكل ما تحله على الارض يكون محلولا في السموات"));
arasuperobject.biblemap.put("b40_16_20", new arasuperobject(40,16,20,"حينئذ اوصى تلاميذه ان لا يقولوا لاحد انه يسوع المسيح"));
arasuperobject.biblemap.put("b40_16_21", new arasuperobject(40,16,21,"من ذلك الوقت ابتدأ يسوع يظهر لتلاميذه انه ينبغي ان يذهب الى اورشليم ويتألم كثيرا من الشيوخ ورؤساء الكهنة والكتبة ويقتل وفي اليوم الثالث يقوم"));
arasuperobject.biblemap.put("b40_16_22", new arasuperobject(40,16,22,"فأخذه بطرس اليه وابتدأ ينتهره قائلا حاشاك يا رب. لا يكون لك هذا"));
arasuperobject.biblemap.put("b40_16_23", new arasuperobject(40,16,23,"فالتفت وقال لبطرس اذهب عني يا شيطان. انت معثرة لي لانك لا تهتم بما لله لكن بما للناس"));
arasuperobject.biblemap.put("b40_16_24", new arasuperobject(40,16,24,"حينئذ قال يسوع لتلاميذه ان اراد احد ان يأتي ورائي فلينكر نفسه ويحمل صليبه ويتبعني"));
arasuperobject.biblemap.put("b40_16_25", new arasuperobject(40,16,25,"فان من اراد ان يخلّص نفسه يهلكها. ومن يهلك نفسه من اجلي يجدها"));
arasuperobject.biblemap.put("b40_16_26", new arasuperobject(40,16,26,"لانه ماذا ينتفع الانسان لو ربح العالم كله وخسر نفسه. او ماذا يعطي الانسان فداء عن نفسه"));
arasuperobject.biblemap.put("b40_16_27", new arasuperobject(40,16,27,"فان ابن الانسان سوف يأتي في مجد ابيه مع ملائكته وحينئذ يجازي كل واحد حسب عمله"));
arasuperobject.biblemap.put("b40_16_28", new arasuperobject(40,16,28,"الحق اقول لكم ان من القيام ههنا قوما لا يذوقون الموت حتى يروا ابن الانسان آتيا في ملكوته"));
arasuperobject.biblemap.put("b40_17_1", new arasuperobject(40,17,1,"وبعد ستة ايام اخذ يسوع بطرس ويعقوب ويوحنا اخاه وصعد بهم الى جبل عال منفردين"));
arasuperobject.biblemap.put("b40_17_2", new arasuperobject(40,17,2,"وتغيّرت هيئته قدّامهم واضاء وجهه كالشمس وصارت ثيابه بيضاء كالنور"));
arasuperobject.biblemap.put("b40_17_3", new arasuperobject(40,17,3,"واذا موسى وايليا قد ظهرا لهم يتكلمان معه"));
arasuperobject.biblemap.put("b40_17_4", new arasuperobject(40,17,4,"فجعل بطرس يقول ليسوع يا رب جيد ان نكون ههنا. فان شئت نصنع هنا ثلاث مظال. لك واحدة ولموسى واحدة ولايليا واحدة"));
arasuperobject.biblemap.put("b40_17_5", new arasuperobject(40,17,5,"وفيما هو يتكلم اذا سحابة نيرة ظللتهم وصوت من السحابة قائلا هذا هو ابني الحبيب الذي به سررت. له اسمعوا"));
arasuperobject.biblemap.put("b40_17_6", new arasuperobject(40,17,6,"ولما سمع التلاميذ سقطوا على وجوههم وخافوا جدا"));
arasuperobject.biblemap.put("b40_17_7", new arasuperobject(40,17,7,"فجاء يسوع ولمسهم وقال قوموا ولا تخافوا"));
arasuperobject.biblemap.put("b40_17_8", new arasuperobject(40,17,8,"فرفعوا اعينهم ولم يروا احدا الا يسوع وحده"));
arasuperobject.biblemap.put("b40_17_9", new arasuperobject(40,17,9,"وفيما هم نازلون من الجبل اوصاهم يسوع قائلا لا تعلموا احدا بما رأيتم حتى يقوم ابن الانسان من الاموات"));
arasuperobject.biblemap.put("b40_17_10", new arasuperobject(40,17,10,"وسأله تلاميذه قائلين فلماذا يقول الكتبة ان ايليا ينبغي ان يأتي اولا"));
arasuperobject.biblemap.put("b40_17_11", new arasuperobject(40,17,11,"فاجاب يسوع وقال لهم ان ايليا يأتي اولا ويردّ كل شيء"));
arasuperobject.biblemap.put("b40_17_12", new arasuperobject(40,17,12,"ولكني اقول لكم ان ايليا قد جاء ولم يعرفوه بل عملوا به كل ما ارادوا. كذلك ابن الانسان ايضا سوف يتألم منهم"));
arasuperobject.biblemap.put("b40_17_13", new arasuperobject(40,17,13,"حينئذ فهم التلاميذ انه قال لهم عن يوحنا المعمدان"));
arasuperobject.biblemap.put("b40_17_14", new arasuperobject(40,17,14,"ولما جاءوا الى الجمع تقدم اليه رجل جاثيا له"));
arasuperobject.biblemap.put("b40_17_15", new arasuperobject(40,17,15,"وقائلا يا سيد ارحم ابني فانه يصرع ويتألم شديدا. ويقع كثيرا في النار وكثيرا في الماء"));
arasuperobject.biblemap.put("b40_17_16", new arasuperobject(40,17,16,"واحضرته الى تلاميذك فلم يقدروا ان يشفوه"));
arasuperobject.biblemap.put("b40_17_17", new arasuperobject(40,17,17,"فاجاب يسوع وقال ايها الجيل غير المؤمن الملتوي. الى متى اكون معكم. الى متى احتملكم. قدموه اليّ ههنا"));
arasuperobject.biblemap.put("b40_17_18", new arasuperobject(40,17,18,"فانتهره يسوع فخرج منه الشيطان فشفي الغلام من تلك الساعة"));
arasuperobject.biblemap.put("b40_17_19", new arasuperobject(40,17,19,"ثم تقدم التلاميذ الى يسوع على انفراد وقالوا لماذا لم نقدر نحن ان نخرجه"));
arasuperobject.biblemap.put("b40_17_20", new arasuperobject(40,17,20,"فقال لهم يسوع لعدم ايمانكم. فالحق اقول لكم لو كان لكم ايمان مثل حبة خردل لكنتم تقولون لهذا الجبل انتقل من هنا الى هناك فينتقل ولا يكون شيء غير ممكن لديكم"));
arasuperobject.biblemap.put("b40_17_21", new arasuperobject(40,17,21,"واما هذا الجنس فلا يخرج الا بالصلاة والصوم"));
arasuperobject.biblemap.put("b40_17_22", new arasuperobject(40,17,22,"وفيما هم يترددون في الجليل قال لهم يسوع. ابن الانسان سوف يسلم الى ايدي الناس"));
arasuperobject.biblemap.put("b40_17_23", new arasuperobject(40,17,23,"فيقتلونه وفي اليوم الثالث يقوم. فحزنوا جدا"));
arasuperobject.biblemap.put("b40_17_24", new arasuperobject(40,17,24,"ولما جاءوا الى كفر ناحوم تقدم الذين ياخذون الدرهمين الى بطرس وقالوا أما يوفي معلمكم الدرهمين"));
arasuperobject.biblemap.put("b40_17_25", new arasuperobject(40,17,25,"قال بلى. فلما دخل البيت سبقه يسوع قائلا ماذا تظن يا سمعان. ممن ياخذ ملوك الارض الجباية او الجزية أمن بنيهم ام من الاجانب"));
arasuperobject.biblemap.put("b40_17_26", new arasuperobject(40,17,26,"قال له بطرس من الاجانب. قال له يسوع فاذا البنون احرار"));
arasuperobject.biblemap.put("b40_17_27", new arasuperobject(40,17,27,"ولكن لئلا نعثرهم اذهب الى البحر وألق صنارة والسمكة التي تطلع اولا خذها ومتى فتحت فاها تجد استارا فخذه واعطهم عني وعنك"));
arasuperobject.biblemap.put("b40_18_1", new arasuperobject(40,18,1,"في تلك الساعة تقدم التلاميذ الى يسوع قائلين فمن هو اعظم في ملكوت السموات"));
arasuperobject.biblemap.put("b40_18_2", new arasuperobject(40,18,2,"فدعا يسوع اليه ولدا واقامه في وسطهم"));
arasuperobject.biblemap.put("b40_18_3", new arasuperobject(40,18,3,"وقال. الحق اقول لكم ان لم ترجعوا وتصيروا مثل الاولاد فلن تدخلوا ملكوت السموات"));
arasuperobject.biblemap.put("b40_18_4", new arasuperobject(40,18,4,"فمن وضع نفسه مثل هذا الولد فهو الاعظم في ملكوت السموات"));
arasuperobject.biblemap.put("b40_18_5", new arasuperobject(40,18,5,"ومن قبل ولدا واحدا مثل هذا باسمي فقد قبلني"));
arasuperobject.biblemap.put("b40_18_6", new arasuperobject(40,18,6,"ومن أعثر احد هؤلاء الصغار المؤمنين بي فخير له ان يعلق في عنقه حجر الرحى ويغرق في لجة البحر"));
arasuperobject.biblemap.put("b40_18_7", new arasuperobject(40,18,7,"ويل للعالم من العثرات. فلا بد ان تأتي العثرات ولكن ويل لذلك الانسان الذي به تأتي العثرة"));
arasuperobject.biblemap.put("b40_18_8", new arasuperobject(40,18,8,"فان اعثرتك يدك او رجلك فاقطعها وألقها عنك. خير لك ان تدخل الحياة اعرج او اقطع من ان تلقى في أتون النار الابدية ولك يدان او رجلان"));
arasuperobject.biblemap.put("b40_18_9", new arasuperobject(40,18,9,"وان اعثرتك عينك فاقلعها وألقها عنك. خير لك ان تدخل الحياة اعور من ان تلقى في جهنم النار ولك عينان"));
arasuperobject.biblemap.put("b40_18_10", new arasuperobject(40,18,10,"انظروا لا تحتقروا احد هؤلاء الصغار. لاني اقول لكم ان ملائكتهم في السموات كل حين ينظرون وجه ابي الذي في السموات"));
arasuperobject.biblemap.put("b40_18_11", new arasuperobject(40,18,11,"لان ابن الانسان قد جاء لكي يخلّص ما قد هلك"));
arasuperobject.biblemap.put("b40_18_12", new arasuperobject(40,18,12,"ماذا تظنون. ان كان لانسان مئة خروف وضل واحد منها أفلا يترك التسعة والتسعين على الجبال ويذهب يطلب الضال"));
arasuperobject.biblemap.put("b40_18_13", new arasuperobject(40,18,13,"وان اتفق ان يجده فالحق اقول لكم انه يفرح به اكثر من التسعة والتسعين التي لم تضل"));
arasuperobject.biblemap.put("b40_18_14", new arasuperobject(40,18,14,"هكذا ليست مشيئة امام ابيكم الذي في السموات ان يهلك احد هؤلاء الصغار"));
arasuperobject.biblemap.put("b40_18_15", new arasuperobject(40,18,15,"وان اخطأ اليك اخوك فاذهب وعاتبه بينك وبينه وحدكما. ان سمع منك فقد ربحت اخاك"));
arasuperobject.biblemap.put("b40_18_16", new arasuperobject(40,18,16,"وان لم يسمع فخذ معك ايضا واحدا او اثنين لكي تقوم كل كلمة على فم شاهدين او ثلاثة"));
arasuperobject.biblemap.put("b40_18_17", new arasuperobject(40,18,17,"وان لم يسمع منهم فقل للكنيسة. وان لم يسمع من الكنيسة فليكن عندك كالوثني والعشار"));
arasuperobject.biblemap.put("b40_18_18", new arasuperobject(40,18,18,"الحق اقول لكم كل ما تربطونه على الارض يكون مربوطا في السماء. وكل ما تحلّونه على الارض يكون محلولا في السماء"));
arasuperobject.biblemap.put("b40_18_19", new arasuperobject(40,18,19,"واقول لكم ايضا ان اتفق اثنان منكم على الارض في اي شيء يطلبانه فانه يكون لهما من قبل ابي الذي في السموات"));
arasuperobject.biblemap.put("b40_18_20", new arasuperobject(40,18,20,"لانه حيثما اجتمع اثنان او ثلاثة باسمي فهناك اكون في وسطهم"));
arasuperobject.biblemap.put("b40_18_21", new arasuperobject(40,18,21,"حينئذ تقدم اليه بطرس وقال يا رب كم مرة يخطئ اليّ اخي وانا اغفر له. هل الى سبع مرات"));
arasuperobject.biblemap.put("b40_18_22", new arasuperobject(40,18,22,"قال له يسوع لا اقول لك الى سبع مرات بل الى سبعين مرة سبع مرات"));
arasuperobject.biblemap.put("b40_18_23", new arasuperobject(40,18,23,"لذلك يشبه ملكوت السموات انسانا ملكا اراد ان يحاسب عبيده"));
arasuperobject.biblemap.put("b40_18_24", new arasuperobject(40,18,24,"فلما ابتدأ في المحاسبة قدم اليه واحد مديون بعشرة آلاف وزنة"));
arasuperobject.biblemap.put("b40_18_25", new arasuperobject(40,18,25,"واذ لم يكن له ما يوفي أمر سيده ان يباع هو وامرأته واولاده وكل ما له ويوفى الدين"));
arasuperobject.biblemap.put("b40_18_26", new arasuperobject(40,18,26,"فخر العبد وسجد له قائلا يا سيد تمهل عليّ فاوفيك الجميع"));
arasuperobject.biblemap.put("b40_18_27", new arasuperobject(40,18,27,"فتحنن سيد ذلك العبد واطلقه وترك له الدين"));
arasuperobject.biblemap.put("b40_18_28", new arasuperobject(40,18,28,"ولما خرج ذلك العبد وجد واحدا من العبيد رفقائه كان مديونا له بمئة دينار. فامسكه واخذ بعنقه قائلا أوفني ما لي عليك"));
arasuperobject.biblemap.put("b40_18_29", new arasuperobject(40,18,29,"فخرّ العبد رفيقه على قدميه وطلب اليه قائلا تمهل عليّ فاوفيك الجميع"));
arasuperobject.biblemap.put("b40_18_30", new arasuperobject(40,18,30,"فلم يرد بل مضى والقاه في سجن حتى يوفي الدين"));
arasuperobject.biblemap.put("b40_18_31", new arasuperobject(40,18,31,"فلما رأى العبيد رفقاؤه ما كان حزنوا جدا وأتوا وقصّوا على سيدهم كل ما جرى"));
arasuperobject.biblemap.put("b40_18_32", new arasuperobject(40,18,32,"فدعاه حينئذ سيده وقال له. ايها العبد الشرير كل ذلك الدين تركته لك لانك طلبت اليّ"));
arasuperobject.biblemap.put("b40_18_33", new arasuperobject(40,18,33,"أفما كان ينبغي انك انت ايضا ترحم العبد رفيقك كما رحمتك انا"));
arasuperobject.biblemap.put("b40_18_34", new arasuperobject(40,18,34,"وغضب سيده وسلمه الى المعذبين حتى يوفي كل ما كان له عليه"));
arasuperobject.biblemap.put("b40_18_35", new arasuperobject(40,18,35,"فهكذا ابي السماوي يفعل بكم ان لم تتركوا من قلوبكم كل واحد لاخيه زلاته"));
arasuperobject.biblemap.put("b40_19_1", new arasuperobject(40,19,1,"ولما اكمل يسوع هذا الكلام انتقل من الجليل وجاء الى تخوم اليهودية من عبر الاردن"));
arasuperobject.biblemap.put("b40_19_2", new arasuperobject(40,19,2,"وتبعته جموع كثيرة فشفاهم هناك"));
arasuperobject.biblemap.put("b40_19_3", new arasuperobject(40,19,3,"وجاء اليه الفريسيون ليجربوه قائلين له هل يحل للرجل ان يطلّق امرأته لكل سبب"));
arasuperobject.biblemap.put("b40_19_4", new arasuperobject(40,19,4,"فاجاب وقال لهم أما قرأتم ان الذي خلق من البدء خلقهما ذكرا وانثى"));
arasuperobject.biblemap.put("b40_19_5", new arasuperobject(40,19,5,"وقال. من اجل هذا يترك الرجل اباه وامه ويلتصق بامرأته ويكون الاثنان جسدا واحدا"));
arasuperobject.biblemap.put("b40_19_6", new arasuperobject(40,19,6,"اذا ليسا بعد اثنين بل جسد واحد. فالذي جمعه الله لا يفرقه انسان"));
arasuperobject.biblemap.put("b40_19_7", new arasuperobject(40,19,7,"قالوا له فلماذا اوصى موسى ان يعطى كتاب طلاق فتطلّق"));
arasuperobject.biblemap.put("b40_19_8", new arasuperobject(40,19,8,"قال لهم ان موسى من اجل قساوة قلوبكم أذن لكم ان تطلّقوا نساءكم. ولكن من البدء لم يكن هكذا"));
arasuperobject.biblemap.put("b40_19_9", new arasuperobject(40,19,9,"واقول لكم ان من طلّق امرأته الا بسبب الزنى وتزوج باخرى يزني. والذي يتزوج بمطلّقة يزني"));
arasuperobject.biblemap.put("b40_19_10", new arasuperobject(40,19,10,"قال له تلاميذه ان كان هكذا امر الرجل مع المرأة فلا يوافق ان يتزوج"));
arasuperobject.biblemap.put("b40_19_11", new arasuperobject(40,19,11,"فقال لهم ليس الجميع يقبلون هذا الكلام بل الذين أعطي لهم"));
arasuperobject.biblemap.put("b40_19_12", new arasuperobject(40,19,12,"لانه يوجد خصيان ولدوا هكذا من بطون امهاتهم. ويوجد خصيان خصاهم الناس. ويوجد خصيان خصوا انفسهم لاجل ملكوت السموات. من استطاع ان يقبل فليقبل"));
arasuperobject.biblemap.put("b40_19_13", new arasuperobject(40,19,13,"حينئذ قدم اليه اولاد لكي يضع يديه عليهم ويصلّي. فانتهرهم التلاميذ"));
arasuperobject.biblemap.put("b40_19_14", new arasuperobject(40,19,14,"اما يسوع فقال دعوا الاولاد يأتون اليّ ولا تمنعوهم لان لمثل هؤلاء ملكوت السموات"));
arasuperobject.biblemap.put("b40_19_15", new arasuperobject(40,19,15,"فوضع يديه عليهم ومضى من هناك"));
arasuperobject.biblemap.put("b40_19_16", new arasuperobject(40,19,16,"واذا واحد تقدم وقال له ايها المعلم الصالح اي صلاح اعمل لتكون لي الحياة الابدية"));
arasuperobject.biblemap.put("b40_19_17", new arasuperobject(40,19,17,"فقال له لماذا تدعوني صالحا. ليس احد صالحا الا واحد وهو الله. ولكن ان اردت ان تدخل الحياة فاحفظ الوصايا"));
arasuperobject.biblemap.put("b40_19_18", new arasuperobject(40,19,18,"قال له ايّة الوصايا. فقال يسوع لا تقتل. لا تزن. لا تسرق. لا تشهد بالزور"));
arasuperobject.biblemap.put("b40_19_19", new arasuperobject(40,19,19,"اكرم اباك وامك واحب قريبك كنفسك"));
arasuperobject.biblemap.put("b40_19_20", new arasuperobject(40,19,20,"قال له الشاب هذه كلها حفظتها منذ حداثتي. فماذا يعوزني بعد"));
arasuperobject.biblemap.put("b40_19_21", new arasuperobject(40,19,21,"قال له يسوع ان اردت ان تكون كاملا فاذهب وبع املاكك واعط الفقراء فيكون لك كنز في السماء وتعال اتبعني"));
arasuperobject.biblemap.put("b40_19_22", new arasuperobject(40,19,22,"فلما سمع الشاب الكلمة مضى حزينا. لانه كان ذا اموال كثيرة"));
arasuperobject.biblemap.put("b40_19_23", new arasuperobject(40,19,23,"فقال يسوع لتلاميذه الحق اقول لكم انه يعسر ان يدخل غني الى ملكوت السموات"));
arasuperobject.biblemap.put("b40_19_24", new arasuperobject(40,19,24,"واقول لكم ايضا ان مرور جمل من ثقب ابرة ايسر من ان يدخل غني الى ملكوت الله"));
arasuperobject.biblemap.put("b40_19_25", new arasuperobject(40,19,25,"فلما سمع تلاميذه بهتوا جدا قائلين. اذا من يستطيع ان يخلص"));
arasuperobject.biblemap.put("b40_19_26", new arasuperobject(40,19,26,"فنظر اليهم يسوع وقال لهم. هذا عند الناس غير مستطاع ولكن عند الله كل شيء مستطاع"));
arasuperobject.biblemap.put("b40_19_27", new arasuperobject(40,19,27,"فاجاب بطرس حينئذ وقال له ها نحن قد تركنا كل شيء وتبعناك. فماذا يكون لنا"));
arasuperobject.biblemap.put("b40_19_28", new arasuperobject(40,19,28,"فقال لهم يسوع الحق اقول لكم انكم انتم الذين تبعتموني في التجديد متى جلس ابن الانسان على كرسي مجده تجلسون انتم ايضا على اثني عشر كرسيا تدينون اسباط اسرائيل الاثني عشر"));
arasuperobject.biblemap.put("b40_19_29", new arasuperobject(40,19,29,"وكل من ترك بيوتا او اخوة او اخوات او ابا او اما او امرأة او اولادا او حقولا من اجل اسمي يأخذ مئة ضعف ويرث الحياة الابدية"));
arasuperobject.biblemap.put("b40_19_30", new arasuperobject(40,19,30,"ولكن كثيرون اولون يكونون آخرين وآخرون اولين"));
arasuperobject.biblemap.put("b40_20_1", new arasuperobject(40,20,1,"فان ملكوت السموات يشبه رجلا رب بيت خرج مع الصبح ليستأجر فعلة لكرمه"));
arasuperobject.biblemap.put("b40_20_2", new arasuperobject(40,20,2,"فأتفق مع الفعلة على دينار في اليوم وارسلهم الى كرمه"));
arasuperobject.biblemap.put("b40_20_3", new arasuperobject(40,20,3,"ثم خرج نحو الساعة الثالثة ورأى آخرين قياما في السوق بطالين"));
arasuperobject.biblemap.put("b40_20_4", new arasuperobject(40,20,4,"فقال لهم اذهبوا انتم ايضا الى الكرم فاعطيكم ما يحق لكم. فمضوا"));
arasuperobject.biblemap.put("b40_20_5", new arasuperobject(40,20,5,"وخرج ايضا نحو الساعة السادسة والتاسعة وفعل كذلك"));
arasuperobject.biblemap.put("b40_20_6", new arasuperobject(40,20,6,"ثم نحو الساعة الحادية عشرة خرج ووجد آخرين قياما بطالين. فقال لهم لماذا وقفتم هنا كل النهار بطالين"));
arasuperobject.biblemap.put("b40_20_7", new arasuperobject(40,20,7,"قالوا له لانه لم يستأجرنا احد. قال لهم اذهبوا انتم ايضا الى الكرم فتأخذوا ما يحق لكم"));
arasuperobject.biblemap.put("b40_20_8", new arasuperobject(40,20,8,"فلما كان المساء قال صاحب الكرم لوكيله. ادع الفعلة واعطهم الاجرة مبتدئا من الاخرين الى الاولين"));
arasuperobject.biblemap.put("b40_20_9", new arasuperobject(40,20,9,"فجاء اصحاب الساعة الحادية عشرة واخذوا دينارا دينارا"));
arasuperobject.biblemap.put("b40_20_10", new arasuperobject(40,20,10,"فلما جاء الاولون ظنوا انهم يأخذون اكثر. فاخذوا هم ايضا دينارا دينارا"));
arasuperobject.biblemap.put("b40_20_11", new arasuperobject(40,20,11,"وفيما هم ياخذون تذمروا على رب البيت"));
arasuperobject.biblemap.put("b40_20_12", new arasuperobject(40,20,12,"قائلين. هؤلاء الآخرون عملوا ساعة واحدة وقد ساويتهم بنا نحن الذين احتملنا ثقل النهار والحر"));
arasuperobject.biblemap.put("b40_20_13", new arasuperobject(40,20,13,"فاجاب وقال لواحد منهم. يا صاحب ما ظلمتك. أما اتفقت معي على دينار"));
arasuperobject.biblemap.put("b40_20_14", new arasuperobject(40,20,14,"فخذ الذي لك واذهب. فاني اريد ان اعطي هذا الاخير مثلك"));
arasuperobject.biblemap.put("b40_20_15", new arasuperobject(40,20,15,"او ما يحل لي ان افعل ما اريد بما لي. أم عينك شريرة لاني انا صالح"));
arasuperobject.biblemap.put("b40_20_16", new arasuperobject(40,20,16,"هكذا يكون الآخرون اولين والاولون آخرين. لان كثيرين يدعون وقليلين ينتخبون"));
arasuperobject.biblemap.put("b40_20_17", new arasuperobject(40,20,17,"وفيما كان يسوع صاعدا الى اورشليم اخذ الاثني عشر تلميذا على انفراد في الطريق وقال لهم"));
arasuperobject.biblemap.put("b40_20_18", new arasuperobject(40,20,18,"ها نحن صاعدون الى اورشليم وابن الانسان يسلم الى رؤساء الكهنة والكتبة فيحكمون عليه بالموت"));
arasuperobject.biblemap.put("b40_20_19", new arasuperobject(40,20,19,"ويسلمونه الى الامم لكي يهزأوا به ويجلدوه ويصلبوه. وفي اليوم الثالث يقوم"));
arasuperobject.biblemap.put("b40_20_20", new arasuperobject(40,20,20,"حينئذ تقدمت اليه ام ابني زبدي مع ابنيها وسجدت وطلبت منه شيئا"));
arasuperobject.biblemap.put("b40_20_21", new arasuperobject(40,20,21,"فقال لها ماذا تريدين. قالت له قل ان يجلس ابناي هذان واحد عن يمينك والآخر عن اليسار في ملكوتك"));
arasuperobject.biblemap.put("b40_20_22", new arasuperobject(40,20,22,"فاجاب يسوع وقال لستما تعلمان ما تطلبان. أتستطيعان ان تشربا الكاس التي سوف اشربها انا وان تصطبغا بالصبغة التي اصطبغ بها انا. قالا له نستطيع"));
arasuperobject.biblemap.put("b40_20_23", new arasuperobject(40,20,23,"فقال لهما اما كاسي فتشربانها وبالصبغة التي اصطبغ بها انا تصطبغان واما الجلوس عن يميني وعن يساري فليس لي ان اعطيه الا للذين اعدّ لهم من ابي"));
arasuperobject.biblemap.put("b40_20_24", new arasuperobject(40,20,24,"فلما سمع العشرة اغتاظوا من اجل الاخوين"));
arasuperobject.biblemap.put("b40_20_25", new arasuperobject(40,20,25,"فدعاهم يسوع وقال انتم تعلمون ان رؤساء الامم يسودونهم والعظماء يتسلطون عليهم"));
arasuperobject.biblemap.put("b40_20_26", new arasuperobject(40,20,26,"فلا يكون هكذا فيكم. بل من اراد ان يكون فيكم عظيما فليكن لكم خادما"));
arasuperobject.biblemap.put("b40_20_27", new arasuperobject(40,20,27,"ومن اراد ان يكون فيكم اولا فليكن لكم عبدا"));
arasuperobject.biblemap.put("b40_20_28", new arasuperobject(40,20,28,"كما ان ابن الانسان لم يأت ليخدم بل ليخدم وليبذل نفسه فدية عن كثيرين"));
arasuperobject.biblemap.put("b40_20_29", new arasuperobject(40,20,29,"وفيما هم خارجون من اريحا تبعه جمع كثير"));
arasuperobject.biblemap.put("b40_20_30", new arasuperobject(40,20,30,"واذا اعميان جالسان على الطريق. فلما سمعا ان يسوع مجتاز صرخا قائلين ارحمنا يا سيد يا ابن داود"));
arasuperobject.biblemap.put("b40_20_31", new arasuperobject(40,20,31,"فانتهرهما الجمع ليسكتا فكانا يصرخان اكثر قائلين ارحمنا يا سيد يا ابن داود"));
arasuperobject.biblemap.put("b40_20_32", new arasuperobject(40,20,32,"فوقف يسوع وناداهما وقال ماذا تريدان ان افعل بكما"));
arasuperobject.biblemap.put("b40_20_33", new arasuperobject(40,20,33,"قالا له يا سيد ان تنفتح اعيننا"));
arasuperobject.biblemap.put("b40_20_34", new arasuperobject(40,20,34,"فتحنن يسوع ولمس اعينهما فللوقت ابصرت اعينهما فتبعاه"));
arasuperobject.biblemap.put("b40_21_1", new arasuperobject(40,21,1,"ولما قربوا من اورشليم وجاءوا الى بيت فاجي عند جبل الزيتون حينئذ ارسل يسوع تلميذين"));
arasuperobject.biblemap.put("b40_21_2", new arasuperobject(40,21,2,"قائلا لهما. اذهبا الى القرية التي امامكما فللوقت تجدان اتانا مربوطة وجحشا معها فحلاهما وأتياني بهما"));
arasuperobject.biblemap.put("b40_21_3", new arasuperobject(40,21,3,"وان قال لكما احد شيئا فقولا الرب محتاج اليهما. فللوقت يرسلهما"));
arasuperobject.biblemap.put("b40_21_4", new arasuperobject(40,21,4,"فكان هذا كله لكي يتم ما قيل بالنبي القائل"));
arasuperobject.biblemap.put("b40_21_5", new arasuperobject(40,21,5,"قولوا لابنة صهيون هوذا ملكك يأتيك وديعا راكبا على اتان وجحش ابن اتان"));
arasuperobject.biblemap.put("b40_21_6", new arasuperobject(40,21,6,"فذهب التلميذان وفعلا كما امرهما يسوع"));
arasuperobject.biblemap.put("b40_21_7", new arasuperobject(40,21,7,"وأتيا بالاتان والجحش ووضعا عليهما ثيابهما فجلس عليهما"));
arasuperobject.biblemap.put("b40_21_8", new arasuperobject(40,21,8,"والجمع الاكثر فرشوا ثيابهم في الطريق. وآخرون قطعوا اغصانا من الشجر وفرشوها في الطريق"));
arasuperobject.biblemap.put("b40_21_9", new arasuperobject(40,21,9,"والجموع الذين تقدموا والذين تبعوا كانوا يصرخون قائلين أوصنا لابن داود. مبارك الآتي باسم الرب. أوصنا في الاعالي"));
arasuperobject.biblemap.put("b40_21_10", new arasuperobject(40,21,10,"ولما دخل اورشليم ارتجّت المدينة كلها قائلة من هذا"));
arasuperobject.biblemap.put("b40_21_11", new arasuperobject(40,21,11,"فقالت الجموع هذا يسوع النبي الذي من ناصرة الجليل"));
arasuperobject.biblemap.put("b40_21_12", new arasuperobject(40,21,12,"ودخل يسوع الى هيكل الله واخرج جميع الذين كانوا يبيعون ويشترون في الهيكل وقلب موائد الصيارفة وكراسي باعة الحمام"));
arasuperobject.biblemap.put("b40_21_13", new arasuperobject(40,21,13,"وقال لهم. مكتوب بيتي بيت الصلاة يدعى وانتم جعلتموه مغارة لصوص"));
arasuperobject.biblemap.put("b40_21_14", new arasuperobject(40,21,14,"وتقدم اليه عمي وعرج في الهيكل فشفاهم"));
arasuperobject.biblemap.put("b40_21_15", new arasuperobject(40,21,15,"فلما رأى رؤساء الكهنة والكتبة العجائب التي صنع والاولاد يصرخون في الهيكل ويقولون أوصنا لابن داود غضبوا"));
arasuperobject.biblemap.put("b40_21_16", new arasuperobject(40,21,16,"وقالوا له أتسمع ما يقول هؤلاء. فقال لهم يسوع نعم. أما قرأتم قط من افواه الاطفال والرضّع هيأت تسبيحا"));
arasuperobject.biblemap.put("b40_21_17", new arasuperobject(40,21,17,"ثم تركهم وخرج خارج المدينة الى بيت عنيا وبات هناك"));
arasuperobject.biblemap.put("b40_21_18", new arasuperobject(40,21,18,"وفي الصبح اذ كان راجعا الى المدينة جاع"));
arasuperobject.biblemap.put("b40_21_19", new arasuperobject(40,21,19,"فنظر شجرة تين على الطريق وجاء اليها فلم يجد فيها شيئا الا ورقا فقط. فقال لها لا يكون منك ثمر بعد الى الابد. فيبست التينة في الحال"));
arasuperobject.biblemap.put("b40_21_20", new arasuperobject(40,21,20,"فلما رأى التلاميذ ذلك تعجبوا قائلين كيف يبست التينة في الحال"));
arasuperobject.biblemap.put("b40_21_21", new arasuperobject(40,21,21,"فاجاب يسوع وقال لهم. الحق اقول لكم ان كان لكم ايمان ولا تشكّون فلا تفعلون امر التينة فقط بل ان قلتم ايضا لهذا الجبل انتقل وانطرح في البحر فيكون"));
arasuperobject.biblemap.put("b40_21_22", new arasuperobject(40,21,22,"وكل ما تطلبونه في الصلاة مؤمنين تنالونه"));
arasuperobject.biblemap.put("b40_21_23", new arasuperobject(40,21,23,"ولما جاء الى الهيكل تقدم اليه رؤساء الكهنة وشيوخ الشعب وهو يعلّم قائلين بأي سلطان تفعل هذا ومن اعطاك هذا السلطان"));
arasuperobject.biblemap.put("b40_21_24", new arasuperobject(40,21,24,"فاجاب يسوع وقال لهم وانا ايضا اسألكم كلمة واحدة فان قلتم لي عنها اقول لكم انا ايضا بأي سلطان افعل هذا"));
arasuperobject.biblemap.put("b40_21_25", new arasuperobject(40,21,25,"معمودية يوحنا من اين كانت. من السماء أم من الناس. ففكروا في انفسهم قائلين ان قلنا من السماء يقول لنا فلماذا لم تؤمنوا به"));
arasuperobject.biblemap.put("b40_21_26", new arasuperobject(40,21,26,"وان قلنا من الناس نخاف من الشعب. لان يوحنا عند الجميع مثل نبي"));
arasuperobject.biblemap.put("b40_21_27", new arasuperobject(40,21,27,"فاجابوا يسوع وقالوا لا نعلم. فقال لهم هو ايضا ولا انا اقول لكم باي سلطان افعل هذا"));
arasuperobject.biblemap.put("b40_21_28", new arasuperobject(40,21,28,"ماذا تظنون. كان لانسان ابنان فجاء الى الاول وقال يا ابني اذهب اليوم اعمل في كرمي"));
arasuperobject.biblemap.put("b40_21_29", new arasuperobject(40,21,29,"فاجاب وقال ما اريد. ولكنه ندم اخيرا ومضى"));
arasuperobject.biblemap.put("b40_21_30", new arasuperobject(40,21,30,"وجاء الى الثاني وقال كذلك. فاجاب وقال ها انا يا سيد. ولم يمض"));
arasuperobject.biblemap.put("b40_21_31", new arasuperobject(40,21,31,"فأي الاثنين عمل ارادة الاب. قالوا له الاول. قال لهم يسوع الحق اقول لكم ان العشارين والزواني يسبقونكم الى ملكوت الله"));
arasuperobject.biblemap.put("b40_21_32", new arasuperobject(40,21,32,"لان يوحنا جاءكم في طريق الحق فلم تؤمنوا به. واما العشارون والزواني فآمنوا به. وانتم اذ رأيتم لم تندموا اخيرا لتؤمنوا به"));
arasuperobject.biblemap.put("b40_21_33", new arasuperobject(40,21,33,"اسمعوا مثلا آخر. كان انسان رب بيت غرس كرما واحاطه بسياج وحفر فيه معصرة وبنى برجا وسلمه الى كرامين وسافر"));
arasuperobject.biblemap.put("b40_21_34", new arasuperobject(40,21,34,"ولما قرب وقت الاثمار ارسل عبيده الى الكرامين لياخذ اثماره"));
arasuperobject.biblemap.put("b40_21_35", new arasuperobject(40,21,35,"فاخذ الكرامون عبيده وجلدوا بعضا وقتلوا بعضا ورجموا بعضا"));
arasuperobject.biblemap.put("b40_21_36", new arasuperobject(40,21,36,"ثم ارسل ايضا عبيدا آخرين اكثر من الاولين. ففعلوا بهم كذلك"));
arasuperobject.biblemap.put("b40_21_37", new arasuperobject(40,21,37,"فاخيرا ارسل اليهم ابنه قائلا يهابون ابني"));
arasuperobject.biblemap.put("b40_21_38", new arasuperobject(40,21,38,"واما الكرامون فلما رأوا الابن قالوا فيما بينهم هذا هو الوارث هلموا نقتله وناخذ ميراثه"));
arasuperobject.biblemap.put("b40_21_39", new arasuperobject(40,21,39,"فأخذوه واخرجوه خارج الكرم وقتلوه"));
arasuperobject.biblemap.put("b40_21_40", new arasuperobject(40,21,40,"فمتى جاء صاحب الكرم ماذا يفعل بأولئك الكرامين"));
arasuperobject.biblemap.put("b40_21_41", new arasuperobject(40,21,41,"قالوا له. أولئك الاردياء يهلكهم هلاكا رديّا ويسلم الكرم الى كرامين آخرين يعطونه الاثمار في اوقاتها"));
arasuperobject.biblemap.put("b40_21_42", new arasuperobject(40,21,42,"قال لهم يسوع أما قرأتم قط في الكتب. الحجر الذي رفضه البناؤون هو قد صار راس الزاوية. من قبل الرب كان هذا وهو عجيب في اعيننا"));
arasuperobject.biblemap.put("b40_21_43", new arasuperobject(40,21,43,"لذلك اقول لكم ان ملكوت الله ينزع منكم ويعطى لأمة تعمل اثماره"));
arasuperobject.biblemap.put("b40_21_44", new arasuperobject(40,21,44,"ومن سقط على هذا الحجر يترضض ومن سقط هو عليه يسحقه"));
arasuperobject.biblemap.put("b40_21_45", new arasuperobject(40,21,45,"ولما سمع رؤساء الكهنة والفريسيون امثاله عرفوا انه تكلم عليهم"));
arasuperobject.biblemap.put("b40_21_46", new arasuperobject(40,21,46,"واذ كانوا يطلبون ان يمسكوه خافوا من الجموع لانه كان عندهم مثل نبي"));
arasuperobject.biblemap.put("b40_22_1", new arasuperobject(40,22,1,"وجعل يسوع يكلمهم ايضا بامثال قائلا"));
arasuperobject.biblemap.put("b40_22_2", new arasuperobject(40,22,2,"يشبه ملكوت السموات انسانا ملكا صنع عرسا لابنه"));
arasuperobject.biblemap.put("b40_22_3", new arasuperobject(40,22,3,"وارسل عبيده ليدعوا المدعوين الى العرس فلم يريدوا ان يأتوا"));
arasuperobject.biblemap.put("b40_22_4", new arasuperobject(40,22,4,"فارسل ايضا عبيدا آخرين قائلا قولوا للمدعوين هوذا غذائي اعددته. ثيراني ومسمناتي قد ذبحت وكل شيء معد. تعالوا الى العرس"));
arasuperobject.biblemap.put("b40_22_5", new arasuperobject(40,22,5,"ولكنهم تهاونوا ومضوا واحد الى حقله وآخر الى تجارته"));
arasuperobject.biblemap.put("b40_22_6", new arasuperobject(40,22,6,"والباقون امسكوا عبيده وشتموهم وقتلوهم"));
arasuperobject.biblemap.put("b40_22_7", new arasuperobject(40,22,7,"فلما سمع الملك غضب وارسل جنوده واهلك أولئك القاتلين واحرق مدينتهم"));
arasuperobject.biblemap.put("b40_22_8", new arasuperobject(40,22,8,"ثم قال لعبيده اما العرس فمستعد واما المدعوون فلم يكونوا مستحقين"));
arasuperobject.biblemap.put("b40_22_9", new arasuperobject(40,22,9,"فاذهبوا الى مفارق الطرق وكل من وجدتموه فادعوه الى العرس"));
arasuperobject.biblemap.put("b40_22_10", new arasuperobject(40,22,10,"فخرج أولئك العبيد الى الطرق وجمعوا كل الذين وجدوهم اشرارا وصالحين. فامتلأ العرس من المتكئين"));
arasuperobject.biblemap.put("b40_22_11", new arasuperobject(40,22,11,"فلما دخل الملك لينظر المتكئين رأى هناك انسانا لم يكن لابسا لباس العرس"));
arasuperobject.biblemap.put("b40_22_12", new arasuperobject(40,22,12,"فقال له يا صاحب كيف دخلت الى هنا وليس عليك لباس العرس. فسكت"));
arasuperobject.biblemap.put("b40_22_13", new arasuperobject(40,22,13,"حينئذ قال الملك للخدام اربطوا رجليه ويديه وخذوه واطرحوه في الظلمة الخارجية. هناك يكون البكاء وصرير الاسنان"));
arasuperobject.biblemap.put("b40_22_14", new arasuperobject(40,22,14,"لان كثيرين يدعون وقليلين ينتخبون"));
arasuperobject.biblemap.put("b40_22_15", new arasuperobject(40,22,15,"حينئذ ذهب الفريسيون وتشاوروا لكي يصطادوه بكلمة"));
arasuperobject.biblemap.put("b40_22_16", new arasuperobject(40,22,16,"فارسلوا اليه تلاميذهم مع الهيرودسيين قائلين يا معلّم نعلم انك صادق وتعلّم طريق الله بالحق ولا تبالي باحد لانك لا تنظر الى وجوه الناس"));
arasuperobject.biblemap.put("b40_22_17", new arasuperobject(40,22,17,"فقل لنا ماذا تظن. أيجوز ان تعطى جزية لقيصر ام لا"));
arasuperobject.biblemap.put("b40_22_18", new arasuperobject(40,22,18,"فعلم يسوع خبثهم وقال لماذا تجربونني يا مراؤون"));
arasuperobject.biblemap.put("b40_22_19", new arasuperobject(40,22,19,"أروني معاملة الجزية. فقدموا له دينارا"));
arasuperobject.biblemap.put("b40_22_20", new arasuperobject(40,22,20,"فقال لهم لمن هذه الصورة والكتابة"));
arasuperobject.biblemap.put("b40_22_21", new arasuperobject(40,22,21,"قالوا له لقيصر. فقال لهم اعطوا اذا ما لقيصر لقيصر وما للّه للّه"));
arasuperobject.biblemap.put("b40_22_22", new arasuperobject(40,22,22,"فلما سمعوا تعجبوا وتركوه ومضوا"));
arasuperobject.biblemap.put("b40_22_23", new arasuperobject(40,22,23,"في ذلك اليوم جاء اليه صدوقيون الذين يقولون ليس قيامة فسألوه"));
arasuperobject.biblemap.put("b40_22_24", new arasuperobject(40,22,24,"قائلين يا معلّم قال موسى ان مات احد وليس له اولاد يتزوج اخوه بامرأته ويقم نسلا لاخيه"));
arasuperobject.biblemap.put("b40_22_25", new arasuperobject(40,22,25,"فكان عندنا سبعة اخوة وتزوج الاول ومات. واذ لم يكن له نسل ترك امرأته لاخيه"));
arasuperobject.biblemap.put("b40_22_26", new arasuperobject(40,22,26,"وكذلك الثاني والثالث الى السبعة"));
arasuperobject.biblemap.put("b40_22_27", new arasuperobject(40,22,27,"وآخر الكل ماتت المرأة ايضا"));
arasuperobject.biblemap.put("b40_22_28", new arasuperobject(40,22,28,"ففي القيامة لمن من السبعة تكون زوجة. فانها كانت للجميع"));
arasuperobject.biblemap.put("b40_22_29", new arasuperobject(40,22,29,"فاجاب يسوع وقال لهم تضلون اذ لا تعرفون الكتب ولا قوة الله"));
arasuperobject.biblemap.put("b40_22_30", new arasuperobject(40,22,30,"لانهم في القيامة لا يزوجون ولا يتزوجون بل يكونون كملائكة الله في السماء"));
arasuperobject.biblemap.put("b40_22_31", new arasuperobject(40,22,31,"واما من جهة قيامة الاموات أفما قرأتم ما قيل لكم من قبل الله القائل"));
arasuperobject.biblemap.put("b40_22_32", new arasuperobject(40,22,32,"انا اله ابراهيم واله اسحق واله يعقوب. ليس الله اله اموات بل اله احياء"));
arasuperobject.biblemap.put("b40_22_33", new arasuperobject(40,22,33,"فلما سمع الجموع بهتوا من تعليمه"));
arasuperobject.biblemap.put("b40_22_34", new arasuperobject(40,22,34,"اما الفريسيون فلما سمعوا انه ابكم الصدوقيين اجتمعوا معا"));
arasuperobject.biblemap.put("b40_22_35", new arasuperobject(40,22,35,"وسأله واحد منهم وهو ناموسي ليجربه قائلا"));
arasuperobject.biblemap.put("b40_22_36", new arasuperobject(40,22,36,"يا معلّم اية وصية هي العظمى في الناموس"));
arasuperobject.biblemap.put("b40_22_37", new arasuperobject(40,22,37,"فقال له يسوع تحب الرب الهك من كل قلبك ومن كل نفسك ومن كل فكرك"));
arasuperobject.biblemap.put("b40_22_38", new arasuperobject(40,22,38,"هذه هي الوصية الاولى والعظمى"));
arasuperobject.biblemap.put("b40_22_39", new arasuperobject(40,22,39,"والثانية مثلها. تحب قريبك كنفسك"));
arasuperobject.biblemap.put("b40_22_40", new arasuperobject(40,22,40,"بهاتين الوصيتين يتعلق الناموس كله والانبياء"));
arasuperobject.biblemap.put("b40_22_41", new arasuperobject(40,22,41,"وفيما كان الفريسيون مجتمعين سألهم يسوع"));
arasuperobject.biblemap.put("b40_22_42", new arasuperobject(40,22,42,"قائلا ماذا تظنون في المسيح. ابن من هو. قالوا له ابن داود"));
arasuperobject.biblemap.put("b40_22_43", new arasuperobject(40,22,43,"قال لهم فكيف يدعوه داود بالروح ربا قائلا"));
arasuperobject.biblemap.put("b40_22_44", new arasuperobject(40,22,44,"قال الرب لربي اجلس عن يميني حتى اضع اعداءك موطئا لقدميك"));
arasuperobject.biblemap.put("b40_22_45", new arasuperobject(40,22,45,"فان كان داود يدعوه ربا فكيف يكون ابنه"));
arasuperobject.biblemap.put("b40_22_46", new arasuperobject(40,22,46,"فلم يستطع احد ان يجيبه بكلمة. ومن ذلك اليوم لم يجسر احد ان يسأله بتة"));
arasuperobject.biblemap.put("b40_23_1", new arasuperobject(40,23,1,"حينئذ خاطب يسوع الجموع وتلاميذه"));
arasuperobject.biblemap.put("b40_23_2", new arasuperobject(40,23,2,"قائلا. على كرسي موسى جلس الكتبة والفريسيون"));
arasuperobject.biblemap.put("b40_23_3", new arasuperobject(40,23,3,"فكل ما قالوا لكم ان تحفظوه فاحفظوه وافعلوه. ولكن حسب اعمالهم لا تعملوا لانهم يقولون ولا يفعلون"));
arasuperobject.biblemap.put("b40_23_4", new arasuperobject(40,23,4,"فانهم يحزمون احمالا ثقيلة عسرة الحمل ويضعونها على اكتاف الناس وهم لا يريدون ان يحركوها باصبعهم"));
arasuperobject.biblemap.put("b40_23_5", new arasuperobject(40,23,5,"وكل اعمالهم يعملونها لكي تنظرهم الناس. فيعرضون عصائبهم ويعظمون اهداب ثيابهم"));
arasuperobject.biblemap.put("b40_23_6", new arasuperobject(40,23,6,"ويحبون المتكأ الاول في الولائم والمجالس الاولى في المجامع"));
