arasuperobject.biblemap.put("b40_25_16", new arasuperobject(40,25,16,"فمضى الذي اخذ الخمس وزنات وتاجر بها فربح خمس وزنات أخر"));
arasuperobject.biblemap.put("b40_25_17", new arasuperobject(40,25,17,"وهكذا الذي اخذ الوزنتين ربح ايضا وزنتين أخريين"));
arasuperobject.biblemap.put("b40_25_18", new arasuperobject(40,25,18,"واما الذي اخذ الوزنة فمضى وحفر في الارض واخفى فضة سيده"));
arasuperobject.biblemap.put("b40_25_19", new arasuperobject(40,25,19,"وبعد زمان طويل أتى سيد اولئك العبيد وحاسبهم"));
arasuperobject.biblemap.put("b40_25_20", new arasuperobject(40,25,20,"فجاء الذي اخذ الخمس وزنات وقدم خمس وزنات أخر قائلا يا سيد خمس وزنات سلمتني. هوذا خمس وزنات أخر ربحتها فوقها"));
arasuperobject.biblemap.put("b40_25_21", new arasuperobject(40,25,21,"فقال له سيده نعمّا ايها العبد الصالح والامين كنت امينا في القليل فاقيمك على الكثير. ادخل الى فرح سيدك"));
arasuperobject.biblemap.put("b40_25_22", new arasuperobject(40,25,22,"ثم جاء الذي اخذ الوزنتين وقال يا سيد وزنتين سلمتني. هوذا وزنتان أخريان ربحتهما فوقهما"));
arasuperobject.biblemap.put("b40_25_23", new arasuperobject(40,25,23,"قال له سيده نعمّا ايها العبد الصالح والامين. كنت امينا في القليل فاقيمك على الكثير. ادخل الى فرح سيدك"));
arasuperobject.biblemap.put("b40_25_24", new arasuperobject(40,25,24,"ثم جاء ايضا الذي اخذ الوزنة الواحدة وقال. يا سيد عرفت انك انسان قاس تحصد حيث لم تزرع وتجمع حيث لم تبذر"));
arasuperobject.biblemap.put("b40_25_25", new arasuperobject(40,25,25,"فخفت ومضيت واخفيت وزنتك في الارض. هوذا الذي لك"));
arasuperobject.biblemap.put("b40_25_26", new arasuperobject(40,25,26,"فاجاب سيده وقال له ايها العبد الشرير والكسلان عرفت اني احصد حيث لم ازرع واجمع من حيث لم ابذر"));
arasuperobject.biblemap.put("b40_25_27", new arasuperobject(40,25,27,"فكان ينبغي ان تضع فضتي عند الصيارفة. فعند مجيئي كنت آخذ الذي لي مع ربا"));
arasuperobject.biblemap.put("b40_25_28", new arasuperobject(40,25,28,"فخذوا منه الوزنة واعطوها للذي له العشر وزنات"));
arasuperobject.biblemap.put("b40_25_29", new arasuperobject(40,25,29,"لان كل من له يعطى فيزداد ومن ليس له فالذي عنده يؤخذ منه"));
arasuperobject.biblemap.put("b40_25_30", new arasuperobject(40,25,30,"والعبد البطال اطرحوه الى الظلمة الخارجية. هناك يكون البكاء وصرير الاسنان"));
arasuperobject.biblemap.put("b40_25_31", new arasuperobject(40,25,31,"ومتى جاء ابن الانسان في مجده وجميع الملائكة القديسين معه فحينئذ يجلس على كرسي مجده"));
arasuperobject.biblemap.put("b40_25_32", new arasuperobject(40,25,32,"ويجتمع امامه جميع الشعوب فيميّز بعضهم من بعض كما يميّز الراعي الخراف من الجداء"));
arasuperobject.biblemap.put("b40_25_33", new arasuperobject(40,25,33,"فيقيم الخراف عن يمينه والجداء عن اليسار"));
arasuperobject.biblemap.put("b40_25_34", new arasuperobject(40,25,34,"ثم يقول الملك للذين عن يمينه تعالوا يا مباركي ابي رثوا الملكوت المعد لكم منذ تأسيس العالم"));
arasuperobject.biblemap.put("b40_25_35", new arasuperobject(40,25,35,"لاني جعت فاطعمتموني. عطشت فسقيتموني. كنت غريبا فآويتموني"));
arasuperobject.biblemap.put("b40_25_36", new arasuperobject(40,25,36,"عريانا فكسيتموني. مريضا فزرتموني. محبوسا فأتيتم اليّ"));
arasuperobject.biblemap.put("b40_25_37", new arasuperobject(40,25,37,"فيجيبه الابرار حينئذ قائلين. يا رب متى رأيناك جائعا فاطعمناك. او عطشانا فسقيناك"));
arasuperobject.biblemap.put("b40_25_38", new arasuperobject(40,25,38,"ومتى رأيناك غريبا فآويناك. او عريانا فكسوناك"));
arasuperobject.biblemap.put("b40_25_39", new arasuperobject(40,25,39,"ومتى رأيناك مريضا او محبوسا فأتينا اليك"));
arasuperobject.biblemap.put("b40_25_40", new arasuperobject(40,25,40,"فيجيب الملك ويقول لهم الحق اقول لكم بما انكم فعلتموه باحد اخوتي هؤلاء الاصاغر فبي فعلتم"));
arasuperobject.biblemap.put("b40_25_41", new arasuperobject(40,25,41,"ثم يقول ايضا للذين عن اليسار اذهبوا عني يا ملاعين الى النار الابدية المعدة لابليس وملائكته"));
arasuperobject.biblemap.put("b40_25_42", new arasuperobject(40,25,42,"لاني جعت فلم تطعموني. عطشت فلم تسقوني"));
arasuperobject.biblemap.put("b40_25_43", new arasuperobject(40,25,43,"كنت غريبا فلم تأووني. عريانا فلم تكسوني. مريضا ومحبوسا فلم تزوروني"));
arasuperobject.biblemap.put("b40_25_44", new arasuperobject(40,25,44,"حينئذ يجيبونه هم ايضا قائلين يا رب متى رأيناك جائعا او عطشانا او غريبا او عريانا او مريضا او محبوسا ولم نخدمك"));
arasuperobject.biblemap.put("b40_25_45", new arasuperobject(40,25,45,"فيجيبهم قائلا الحق اقول لكم بما انكم لم تفعلوه باحد هؤلاء الاصاغر فبي لم تفعلوا"));
arasuperobject.biblemap.put("b40_25_46", new arasuperobject(40,25,46,"فيمضي هؤلاء الى عذاب ابدي والابرار الى حياة ابدية"));
arasuperobject.biblemap.put("b40_26_1", new arasuperobject(40,26,1,"ولما اكمل يسوع هذه الاقوال كلها قال لتلاميذه"));
arasuperobject.biblemap.put("b40_26_2", new arasuperobject(40,26,2,"تعلمون انه بعد يومين يكون الفصح وابن الانسان يسلم ليصلب"));
arasuperobject.biblemap.put("b40_26_3", new arasuperobject(40,26,3,"حينئذ اجتمع رؤساء الكهنة والكتبة وشيوخ الشعب الى دار رئيس الكهنة الذي يدعى قيافا"));
arasuperobject.biblemap.put("b40_26_4", new arasuperobject(40,26,4,"وتشاوروا لكي يمسكوا يسوع بمكر ويقتلوه"));
arasuperobject.biblemap.put("b40_26_5", new arasuperobject(40,26,5,"ولكنهم قالوا ليس في العيد لئلا يكون شغب في الشعب"));
arasuperobject.biblemap.put("b40_26_6", new arasuperobject(40,26,6,"وفيما كان يسوع في بيت عنيا في بيت سمعان الابرص"));
arasuperobject.biblemap.put("b40_26_7", new arasuperobject(40,26,7,"تقدمت اليه امرأة معها قارورة طيب كثير الثمن فسكبته على راسه وهو متكئ"));
arasuperobject.biblemap.put("b40_26_8", new arasuperobject(40,26,8,"فلما رأى تلاميذه ذلك اغتاظوا قائلين لماذا هذا الاتلاف"));
arasuperobject.biblemap.put("b40_26_9", new arasuperobject(40,26,9,"لانه كان يمكن ان يباع هذا الطيب بكثير ويعطى للفقراء"));
arasuperobject.biblemap.put("b40_26_10", new arasuperobject(40,26,10,"فعلم يسوع وقال لهم لماذا تزعجون المرأة فانها قد عملت بي عملا حسنا"));
arasuperobject.biblemap.put("b40_26_11", new arasuperobject(40,26,11,"لان الفقراء معكم في كل حين. واما انا فلست معكم في كل حين"));
arasuperobject.biblemap.put("b40_26_12", new arasuperobject(40,26,12,"فانها اذ سكبت هذا الطيب على جسدي انما فعلت ذلك لاجل تكفيني"));
arasuperobject.biblemap.put("b40_26_13", new arasuperobject(40,26,13,"الحق اقول لكم حيثما يكرز بهذا الانجيل في كل العالم يخبر ايضا بما فعلته هذه تذكارا لها"));
arasuperobject.biblemap.put("b40_26_14", new arasuperobject(40,26,14,"حينئذ ذهب واحد من الاثني عشر الذي يدعى يهوذا الاسخريوطي الى رؤساء الكهنة"));
arasuperobject.biblemap.put("b40_26_15", new arasuperobject(40,26,15,"وقال ماذا تريدون ان تعطوني وانا اسلمه اليكم. فجعلوا له ثلاثين من الفضة"));
arasuperobject.biblemap.put("b40_26_16", new arasuperobject(40,26,16,"ومن ذلك الوقت كان يطلب فرصة ليسلمه"));
arasuperobject.biblemap.put("b40_26_17", new arasuperobject(40,26,17,"وفي اول ايام الفطير تقدم التلاميذ الى يسوع قائلين له اين تريد ان نعد لك لتاكل الفصح"));
arasuperobject.biblemap.put("b40_26_18", new arasuperobject(40,26,18,"فقال اذهبوا الى المدينة الى فلان وقولوا له. المعلم يقول ان وقتي قريب. عندك اصنع الفصح مع تلاميذي"));
arasuperobject.biblemap.put("b40_26_19", new arasuperobject(40,26,19,"ففعل التلاميذ كما امرهم يسوع واعدوا الفصح"));
arasuperobject.biblemap.put("b40_26_20", new arasuperobject(40,26,20,"ولما كان المساء اتكأ مع الاثني عشر"));
arasuperobject.biblemap.put("b40_26_21", new arasuperobject(40,26,21,"وفيما هم ياكلون قال الحق اقول لكم ان واحد منكم يسلمني"));
arasuperobject.biblemap.put("b40_26_22", new arasuperobject(40,26,22,"فحزنوا جدا وابتدأ كل واحد منهم يقول له هل انا هو يا رب"));
arasuperobject.biblemap.put("b40_26_23", new arasuperobject(40,26,23,"فاجاب وقال. الذي يغمس يده معي في الصحفة هو يسلمني"));
arasuperobject.biblemap.put("b40_26_24", new arasuperobject(40,26,24,"ان ابن الانسان ماض كما هو مكتوب عنه. ولكن ويل لذلك الرجل الذي به يسلم ابن الانسان. كان خيرا لذلك الرجل لو لم يولد"));
arasuperobject.biblemap.put("b40_26_25", new arasuperobject(40,26,25,"فاجاب يهوذا مسلمه وقال هل انا هو يا سيدي. قال له انت قلت"));
arasuperobject.biblemap.put("b40_26_26", new arasuperobject(40,26,26,"وفيما هم ياكلون اخذ يسوع الخبز وبارك وكسر واعطى التلاميذ وقال خذوا كلوا. هذا هو جسدي"));
arasuperobject.biblemap.put("b40_26_27", new arasuperobject(40,26,27,"واخذ الكاس وشكر واعطاهم قائلا اشربوا منها كلكم"));
arasuperobject.biblemap.put("b40_26_28", new arasuperobject(40,26,28,"لان هذا هو دمي الذي للعهد الجديد الذي يسفك من اجل كثيرين لمغفرة الخطايا"));
arasuperobject.biblemap.put("b40_26_29", new arasuperobject(40,26,29,"واقول لكم اني من الآن لا اشرب من نتاج الكرمة هذا الى ذلك اليوم حينما اشربه معكم جديدا في ملكوت ابي"));
arasuperobject.biblemap.put("b40_26_30", new arasuperobject(40,26,30,"ثم سبحوا وخرجوا الى جبل الزيتون"));
arasuperobject.biblemap.put("b40_26_31", new arasuperobject(40,26,31,"حينئذ قال لهم يسوع كلكم تشكّون فيّ في هذه الليلة لانه مكتوب اني اضرب الراعي فتتبدد خراف الرعية"));
arasuperobject.biblemap.put("b40_26_32", new arasuperobject(40,26,32,"ولكن بعد قيامي اسبقكم الى الجليل"));
arasuperobject.biblemap.put("b40_26_33", new arasuperobject(40,26,33,"فاجاب بطرس وقال له وان شك فيك الجميع فانا لا اشك ابدا"));
arasuperobject.biblemap.put("b40_26_34", new arasuperobject(40,26,34,"قال له يسوع الحق اقول لك انك في هذه الليلة قبل ان يصيح ديك تنكرني ثلاث مرات"));
arasuperobject.biblemap.put("b40_26_35", new arasuperobject(40,26,35,"قال له بطرس ولو اضطررت ان اموت معك لا انكرك. هكذا قال ايضا جميع التلاميذ"));
arasuperobject.biblemap.put("b40_26_36", new arasuperobject(40,26,36,"حينئذ جاء معهم يسوع الى ضيعة يقال لها جثسيماني فقال للتلاميذ اجلسوا ههنا حتى امضي واصلّي هناك"));
arasuperobject.biblemap.put("b40_26_37", new arasuperobject(40,26,37,"ثم اخذ معه بطرس وابني زبدي وابتدأ يحزن ويكتئب"));
arasuperobject.biblemap.put("b40_26_38", new arasuperobject(40,26,38,"فقال لهم نفسي حزينة جدا حتى الموت. امكثوا ههنا واسهروا معي"));
arasuperobject.biblemap.put("b40_26_39", new arasuperobject(40,26,39,"ثم تقدم قليلا وخرّ على وجهه وكان يصلّي قائلا يا ابتاه ان امكن فلتعبر عني هذه الكاس. ولكن ليس كما اريد انا بل كما تريد انت"));
arasuperobject.biblemap.put("b40_26_40", new arasuperobject(40,26,40,"ثم جاء الى التلاميذ فوجدهم نياما. فقال لبطرس أهكذا ما قدرتم ان تسهروا معي ساعة واحدة"));
arasuperobject.biblemap.put("b40_26_41", new arasuperobject(40,26,41,"اسهروا وصلّوا لئلا تدخلوا في تجربة. اما الروح فنشيط واما الجسد فضعيف"));
arasuperobject.biblemap.put("b40_26_42", new arasuperobject(40,26,42,"فمضى ايضا ثانية وصلّى قائلا يا ابتاه ان لم يمكن ان تعبر عني هذه الكاس الا ان اشربها فلتكن مشيئتك"));
arasuperobject.biblemap.put("b40_26_43", new arasuperobject(40,26,43,"ثم جاء فوجدهم ايضا نياما. اذ كانت اعينهم ثقيلة"));
arasuperobject.biblemap.put("b40_26_44", new arasuperobject(40,26,44,"فتركهم ومضى ايضا وصلّى ثالثة قائلا ذلك الكلام بعينه"));
arasuperobject.biblemap.put("b40_26_45", new arasuperobject(40,26,45,"ثم جاء الى تلاميذه وقال لهم ناموا الآن واستريحوا. هوذا الساعة قد اقتربت وابن الانسان يسلم الى ايدي الخطاة"));
arasuperobject.biblemap.put("b40_26_46", new arasuperobject(40,26,46,"قوموا ننطلق. هوذا الذي يسلمني قد اقترب"));
arasuperobject.biblemap.put("b40_26_47", new arasuperobject(40,26,47,"وفيما هو يتكلم اذا يهوذا واحد من الاثني عشر قد جاء ومعه جمع كثير بسيوف وعصي من عند رؤساء الكهنة وشيوخ الشعب"));
arasuperobject.biblemap.put("b40_26_48", new arasuperobject(40,26,48,"والذي اسلمه اعطاهم علامة قائلا الذي اقبّله هو هو. امسكوه"));
arasuperobject.biblemap.put("b40_26_49", new arasuperobject(40,26,49,"فللوقت تقدم الى يسوع وقال السلام يا سيدي. وقبّله"));
arasuperobject.biblemap.put("b40_26_50", new arasuperobject(40,26,50,"فقال له يسوع يا صاحب لماذا جئت. حينئذ تقدموا والقوا الايادي على يسوع وامسكوه"));
arasuperobject.biblemap.put("b40_26_51", new arasuperobject(40,26,51,"واذا واحد من الذين مع يسوع مدّ يده واستل سيفه وضرب عبد رئيس الكهنة فقطع اذنه"));
arasuperobject.biblemap.put("b40_26_52", new arasuperobject(40,26,52,"فقال له يسوع رد سيفك الى مكانه. لان كل الذين يأخذون السيف بالسيف يهلكون"));
arasuperobject.biblemap.put("b40_26_53", new arasuperobject(40,26,53,"أتظن اني لا استطيع الآن ان اطلب الى ابي فيقدم لي اكثر من اثني عشر جيشا من الملائكة"));
arasuperobject.biblemap.put("b40_26_54", new arasuperobject(40,26,54,"فكيف تكمل الكتب انه هكذا ينبغي ان يكون"));
arasuperobject.biblemap.put("b40_26_55", new arasuperobject(40,26,55,"في تلك الساعة قال يسوع للجموع كأنه على لص خرجتم بسيوف وعصي لتاخذوني. كل يوم كنت اجلس معكم اعلّم في الهيكل ولم تمسكوني"));
arasuperobject.biblemap.put("b40_26_56", new arasuperobject(40,26,56,"واما هذا كله فقد كان لكي تكمل كتب الانبياء. حينئذ تركه التلاميذ كلهم وهربوا"));
arasuperobject.biblemap.put("b40_26_57", new arasuperobject(40,26,57,"والذين امسكوا يسوع مضوا به الى قيافا رئيس الكهنة حيث اجتمع الكتبة والشيوخ"));
arasuperobject.biblemap.put("b40_26_58", new arasuperobject(40,26,58,"واما بطرس فتبعه من بعيد الى دار رئيس الكهنة فدخل الى داخل وجلس بين الخدام لينظر النهاية"));
arasuperobject.biblemap.put("b40_26_59", new arasuperobject(40,26,59,"وكان رؤساء الكهنة والشيوخ والمجمع كله يطلبون شهادة زور على يسوع لكي يقتلوه"));
arasuperobject.biblemap.put("b40_26_60", new arasuperobject(40,26,60,"فلم يجدوا. ومع انه جاء شهود زور كثيرون لم يجدوا. ولكن اخيرا تقدم شاهدا زور"));
arasuperobject.biblemap.put("b40_26_61", new arasuperobject(40,26,61,"وقالا. هذا قال اني اقدر ان انقض هيكل الله وفي ثلاثة ايام ابنيه"));
arasuperobject.biblemap.put("b40_26_62", new arasuperobject(40,26,62,"فقام رئيس الكهنة وقال له أما تجيب بشيء. ماذا يشهد به هذان عليك"));
arasuperobject.biblemap.put("b40_26_63", new arasuperobject(40,26,63,"واما يسوع فكان ساكتا. فاجاب رئيس الكهنة وقال له استحلفك بالله الحي ان تقول لنا هل انت المسيح ابن الله"));
arasuperobject.biblemap.put("b40_26_64", new arasuperobject(40,26,64,"قال له يسوع انت قلت. وايضا اقول لكم من الآن تبصرون ابن الانسان جالسا عن يمين القوة وآتيا على سحاب السماء"));
arasuperobject.biblemap.put("b40_26_65", new arasuperobject(40,26,65,"فمزّق رئيس الكهنة حينئذ ثيابه قائلا قد جدّف. ما حاجتنا بعد الى شهود. ها قد سمعتم تجديفه"));
arasuperobject.biblemap.put("b40_26_66", new arasuperobject(40,26,66,"ماذا ترون. فاجابوا وقالوا انه مستوجب الموت"));
arasuperobject.biblemap.put("b40_26_67", new arasuperobject(40,26,67,"حينئذ بصقوا في وجهه ولكموه. وآخرون لطموه"));
arasuperobject.biblemap.put("b40_26_68", new arasuperobject(40,26,68,"قائلين تنبأ لنا ايها المسيح من ضربك"));
arasuperobject.biblemap.put("b40_26_69", new arasuperobject(40,26,69,"اما بطرس فكان جالسا خارجا في الدار. فجاءت اليه جارية قائلة وانت كنت مع يسوع الجليلي"));
arasuperobject.biblemap.put("b40_26_70", new arasuperobject(40,26,70,"فانكر قدام الجميع قائلا لست ادري ما تقولين"));
arasuperobject.biblemap.put("b40_26_71", new arasuperobject(40,26,71,"ثم اذ خرج الى الدهليز رأته اخرى فقالت للذين هناك وهذا كان مع يسوع الناصري"));
arasuperobject.biblemap.put("b40_26_72", new arasuperobject(40,26,72,"فانكر ايضا بقسم اني لست اعرف الرجل"));
arasuperobject.biblemap.put("b40_26_73", new arasuperobject(40,26,73,"وبعد قليل جاء القيام وقالوا لبطرس حقا انت ايضا منهم فان لغتك تظهرك"));
arasuperobject.biblemap.put("b40_26_74", new arasuperobject(40,26,74,"فابتدأ حينئذ يلعن ويحلف اني لا اعرف الرجل. وللوقت صاح الديك"));
arasuperobject.biblemap.put("b40_26_75", new arasuperobject(40,26,75,"فتذكر بطرس كلام يسوع الذي قال له انك قبل ان يصيح الديك تنكرني ثلاث مرات. فخرج الى خارج وبكى بكاء مرا"));
arasuperobject.biblemap.put("b40_27_1", new arasuperobject(40,27,1,"ولما كان الصباح تشاور جميع رؤساء الكهنة وشيوخ الشعب على يسوع حتى يقتلوه"));
arasuperobject.biblemap.put("b40_27_2", new arasuperobject(40,27,2,"فاوثقوه ومضوا به ودفعوه الى بيلاطس البنطي الوالي"));
arasuperobject.biblemap.put("b40_27_3", new arasuperobject(40,27,3,"حينئذ لما رأى يهوذا الذي اسلمه انه قد دين ندم وردّ الثلاثين من الفضة الى رؤساء الكهنة والشيوخ"));
arasuperobject.biblemap.put("b40_27_4", new arasuperobject(40,27,4,"قائلا قد اخطأت اذ سلمت دما بريئا. فقالوا ماذا علينا. انت ابصر"));
arasuperobject.biblemap.put("b40_27_5", new arasuperobject(40,27,5,"فطرح الفضة في الهيكل وانصرف. ثم مضى وخنق نفسه"));
arasuperobject.biblemap.put("b40_27_6", new arasuperobject(40,27,6,"فاخذ رؤساء الكهنة الفضة وقالوا لا يحل ان نلقيها في الخزانة لانها ثمن دم"));
arasuperobject.biblemap.put("b40_27_7", new arasuperobject(40,27,7,"فتشاوروا واشتروا بها حقل الفخاري مقبرة للغرباء"));
arasuperobject.biblemap.put("b40_27_8", new arasuperobject(40,27,8,"لهذا سمي ذلك الحقل حقل الدم الى هذا اليوم"));
arasuperobject.biblemap.put("b40_27_9", new arasuperobject(40,27,9,"حينئذ تم ما قيل بارميا النبي القائل وأخذوا الثلاثين من الفضة ثمن المثمن الذي ثمنوه من بني اسرائيل"));
arasuperobject.biblemap.put("b40_27_10", new arasuperobject(40,27,10,"واعطوها عن حقل الفخاري كما امرني الرب"));
arasuperobject.biblemap.put("b40_27_11", new arasuperobject(40,27,11,"فوقف‎يسوع‎امام الوالي فسأله الوالي قائلا أانت ملك اليهود. فقال له يسوع انت تقول"));
arasuperobject.biblemap.put("b40_27_12", new arasuperobject(40,27,12,"وبينما كان رؤساء الكهنة والشيوخ يشتكون عليه لم يجب بشيء"));
arasuperobject.biblemap.put("b40_27_13", new arasuperobject(40,27,13,"فقال له بيلاطس أما تسمع كم يشهدون عليك"));
arasuperobject.biblemap.put("b40_27_14", new arasuperobject(40,27,14,"فلم يجبه ولا عن كلمة واحدة حتى تعجب الوالي جدا"));
arasuperobject.biblemap.put("b40_27_15", new arasuperobject(40,27,15,"وكان الوالي معتادا في العيد ان يطلق للجمع اسيرا واحدا من ارادوه"));
arasuperobject.biblemap.put("b40_27_16", new arasuperobject(40,27,16,"وكان لهم حينئذ اسير مشهور يسمى باراباس"));
arasuperobject.biblemap.put("b40_27_17", new arasuperobject(40,27,17,"ففيما هم مجتمعون قال لهم بيلاطس من تريدون ان اطلق لكم. باراباس ام يسوع الذي يدعى المسيح"));
arasuperobject.biblemap.put("b40_27_18", new arasuperobject(40,27,18,"لانه علم انهم اسلموه حسدا"));
arasuperobject.biblemap.put("b40_27_19", new arasuperobject(40,27,19,"واذ كان جالسا على كرسي الولاية ارسلت اليه امرأته قائلة اياك وذلك البار. لاني تألمت اليوم كثيرا في حلم من اجله"));
arasuperobject.biblemap.put("b40_27_20", new arasuperobject(40,27,20,"ولكن رؤساء الكهنة والشيوخ حرّضوا الجموع على ان يطلبوا باراباس ويهلكوا يسوع"));
arasuperobject.biblemap.put("b40_27_21", new arasuperobject(40,27,21,"فاجاب الوالي وقال لهم من من الاثنين تريدون ان اطلق لكم. فقالوا باراباس"));
arasuperobject.biblemap.put("b40_27_22", new arasuperobject(40,27,22,"قال لهم بيلاطس فماذا افعل بيسوع الذي يدعى المسيح. قال له الجميع ليصلب"));
arasuperobject.biblemap.put("b40_27_23", new arasuperobject(40,27,23,"فقال الوالي وايّ شر عمل. فكانوا يزدادون صراخا قائلين ليصلب"));
arasuperobject.biblemap.put("b40_27_24", new arasuperobject(40,27,24,"فلما رأى بيلاطس انه لا ينفع شيئا بل بالحري يحدث شغب اخذ ماء وغسل يديه قدام الجمع قائلا اني بريء من دم هذا البار. ابصروا انتم"));
arasuperobject.biblemap.put("b40_27_25", new arasuperobject(40,27,25,"فاجاب جميع الشعب وقالوا دمه علينا وعلى اولادنا"));
arasuperobject.biblemap.put("b40_27_26", new arasuperobject(40,27,26,"حينئذ اطلق لهم باراباس. واما يسوع فجلده واسلمه ليصلب"));
arasuperobject.biblemap.put("b40_27_27", new arasuperobject(40,27,27,"فاخذ عسكر الوالي يسوع الى دار الولاية وجمعوا عليه كل الكتيبة"));
arasuperobject.biblemap.put("b40_27_28", new arasuperobject(40,27,28,"فعروه والبسوه رداء قرمزيا"));
arasuperobject.biblemap.put("b40_27_29", new arasuperobject(40,27,29,"وضفروا اكليلا من شوك ووضعوه على راسه وقصبة في يمينه. وكانوا يجثون قدامه ويستهزئون به قائلين السلام يا ملك اليهود"));
arasuperobject.biblemap.put("b40_27_30", new arasuperobject(40,27,30,"وبصقوا عليه واخذوا القصبة وضربوه على راسه"));
arasuperobject.biblemap.put("b40_27_31", new arasuperobject(40,27,31,"وبعدما استهزئوا به نزعوا عنه الرداء والبسوه ثيابه ومضوا به للصلب"));
arasuperobject.biblemap.put("b40_27_32", new arasuperobject(40,27,32,"وفيما هم خارجون وجدوا انسانا قيروانيا اسمه سمعان فسخروه ليحمل صليبه"));
arasuperobject.biblemap.put("b40_27_33", new arasuperobject(40,27,33,"ولما أتوا الى موضع يقال له جلجثة وهو المسمى موضع الجمجمة"));
arasuperobject.biblemap.put("b40_27_34", new arasuperobject(40,27,34,"اعطوه خلا ممزوجا بمرارة ليشرب. ولما ذاق لم يرد ان يشرب"));
