arasuperobject.biblemap.put("b40_27_36", new arasuperobject(40,27,36,"ثم جلسوا يحرسونه هناك"));
arasuperobject.biblemap.put("b40_27_37", new arasuperobject(40,27,37,"وجعلوا فوق راسه علته مكتوبة هذا هو يسوع ملك اليهود"));
arasuperobject.biblemap.put("b40_27_38", new arasuperobject(40,27,38,"حينئذ صلب معه لصان واحد عن اليمين وواحد عن اليسار"));
arasuperobject.biblemap.put("b40_27_39", new arasuperobject(40,27,39,"وكان المجتازون يجدفون عليه وهم يهزون رؤوسهم"));
arasuperobject.biblemap.put("b40_27_40", new arasuperobject(40,27,40,"قائلين يا ناقض الهيكل وبانيه في ثلاثة ايام خلّص نفسك. ان كنت ابن الله فانزل عن الصليب"));
arasuperobject.biblemap.put("b40_27_41", new arasuperobject(40,27,41,"وكذلك رؤساء الكهنة ايضا وهم يستهزئون مع الكتبة والشيوخ قالوا"));
arasuperobject.biblemap.put("b40_27_42", new arasuperobject(40,27,42,"خلّص آخرين واما نفسه فما يقدر ان يخلّصها. ان كان هو ملك اسرائيل فلينزل الآن عن الصليب فنؤمن به"));
arasuperobject.biblemap.put("b40_27_43", new arasuperobject(40,27,43,"قد اتكل على الله فلينقذه الآن ان اراده. لانه قال انا ابن الله"));
arasuperobject.biblemap.put("b40_27_44", new arasuperobject(40,27,44,"وبذلك ايضا كان اللصّان اللذان صلبا معه يعيّرانه"));
arasuperobject.biblemap.put("b40_27_45", new arasuperobject(40,27,45,"ومن الساعة السادسة كانت ظلمة على كل الارض الى الساعة التاسعة"));
arasuperobject.biblemap.put("b40_27_46", new arasuperobject(40,27,46,"ونحو الساعة التاسعة صرخ يسوع بصوت عظيم قائلا ايلي ايلي لما شبقتني اي الهي الهي لماذا تركتني"));
arasuperobject.biblemap.put("b40_27_47", new arasuperobject(40,27,47,"فقوم من الواقفين هناك لما سمعوا قالوا انه ينادي ايليا"));
arasuperobject.biblemap.put("b40_27_48", new arasuperobject(40,27,48,"وللوقت ركض واحد منهم واخذ اسفنجة وملأها خلا وجعلها على قصبة وسقاه"));
arasuperobject.biblemap.put("b40_27_49", new arasuperobject(40,27,49,"واما الباقون فقالوا اترك. لنرى هل يأتي ايليا يخلّصه"));
arasuperobject.biblemap.put("b40_27_50", new arasuperobject(40,27,50,"فصرخ يسوع ايضا بصوت عظيم واسلم الروح"));
arasuperobject.biblemap.put("b40_27_51", new arasuperobject(40,27,51,"واذا حجاب الهيكل قد انشق الى اثنين من فوق الى اسفل. والارض تزلزلت والصخور تشققت"));
