arasuperobject.biblemap.put("b40_27_53", new arasuperobject(40,27,53,"وخرجوا من القبور بعد قيامته ودخلوا المدينة المقدسة وظهروا لكثيرين"));
arasuperobject.biblemap.put("b40_27_54", new arasuperobject(40,27,54,"واما قائد المئة والذين معه يحرسون يسوع فلما رأوا الزلزلة وما كان خافوا جدا وقالوا حقا كان هذا ابن الله"));
arasuperobject.biblemap.put("b40_27_55", new arasuperobject(40,27,55,"وكانت هناك نساء كثيرات ينظرن من بعيد وهنّ كنّ قد تبعن يسوع من الجليل يخدمنه"));
arasuperobject.biblemap.put("b40_27_56", new arasuperobject(40,27,56,"وبينهنّ مريم المجدلية ومريم ام يعقوب ويوسي وام ابني زبدي"));
arasuperobject.biblemap.put("b40_27_57", new arasuperobject(40,27,57,"ولما كان المساء جاء رجل غني من الرامة اسمه يوسف. وكان هو ايضا تلميذا ليسوع"));
arasuperobject.biblemap.put("b40_27_58", new arasuperobject(40,27,58,"فهذا تقدم الى بيلاطس وطلب جسد يسوع. فامر بيلاطس حينئذ ان يعطى الجسد"));
arasuperobject.biblemap.put("b40_27_59", new arasuperobject(40,27,59,"فاخذ يوسف الجسد ولفه بكتان نقي"));
arasuperobject.biblemap.put("b40_27_60", new arasuperobject(40,27,60,"ووضعه في قبره الجديد الذي كان قد نحته في الصخرة ثم دحرج حجرا كبيرا على باب القبر ومضى"));
arasuperobject.biblemap.put("b40_27_61", new arasuperobject(40,27,61,"وكانت هناك مريم المجدلية ومريم الاخرى جالستين تجاه القبر"));
arasuperobject.biblemap.put("b40_27_62", new arasuperobject(40,27,62,"وفي الغد الذي بعد الاستعداد اجتمع رؤساء الكهنة والفريسيون الى بيلاطس"));
arasuperobject.biblemap.put("b40_27_63", new arasuperobject(40,27,63,"قائلين. يا سيد قد تذكرنا ان ذلك المضل قال وهو حيّ اني بعد ثلاثة ايام اقوم"));
arasuperobject.biblemap.put("b40_27_64", new arasuperobject(40,27,64,"فمر بضبط القبر الى اليوم الثالث لئلا يأتي تلاميذه ليلا ويسرقوه ويقولوا للشعب انه قام من الاموات. فتكون الضلالة الاخيرة اشر من الاولى"));
arasuperobject.biblemap.put("b40_27_65", new arasuperobject(40,27,65,"فقال لهم بيلاطس عندكم حراس. اذهبوا واضبطوه كما تعلمون"));
arasuperobject.biblemap.put("b40_27_66", new arasuperobject(40,27,66,"فمضوا وضبطوا القبر بالحراس وختموا الحجر"));
arasuperobject.biblemap.put("b40_28_1", new arasuperobject(40,28,1,"وبعد السبت عند فجر اول الاسبوع جاءت مريم المجدلية ومريم الاخرى لتنظرا القبر"));
arasuperobject.biblemap.put("b40_28_2", new arasuperobject(40,28,2,"واذا زلزلة عظيمة حدثت. لان ملاك الرب نزل من السماء وجاء ودحرج الحجر عن الباب وجلس عليه"));
arasuperobject.biblemap.put("b40_28_3", new arasuperobject(40,28,3,"وكان منظره كالبرق ولباسه ابيض كالثلج"));
arasuperobject.biblemap.put("b40_28_4", new arasuperobject(40,28,4,"فمن خوفه ارتعد الحراس وصاروا كاموات"));
arasuperobject.biblemap.put("b40_28_5", new arasuperobject(40,28,5,"فاجاب الملاك وقال للمرأتين لا تخافا انتما. فاني اعلم انكما تطلبان يسوع المصلوب"));
arasuperobject.biblemap.put("b40_28_6", new arasuperobject(40,28,6,"ليس هو ههنا لانه قام كما قال. هلم انظرا الموضع الذي كان الرب مضطجعا فيه"));
arasuperobject.biblemap.put("b40_28_7", new arasuperobject(40,28,7,"واذهبا سريعا قولا لتلاميذه انه قد قام من الاموات. ها هو يسبقكم الى الجليل. هناك ترونه. ها انا قد قلت لكما"));
arasuperobject.biblemap.put("b40_28_8", new arasuperobject(40,28,8,"فخرجتا سريعا من القبر بخوف وفرح عظيم راكضتين لتخبرا تلاميذه"));
arasuperobject.biblemap.put("b40_28_9", new arasuperobject(40,28,9,"وفيما هما منطلقتان لتخبرا تلاميذه اذا يسوع لاقاهما وقال سلام لكما. فتقدمتا وامسكتا بقدميه وسجدتا له"));
arasuperobject.biblemap.put("b40_28_10", new arasuperobject(40,28,10,"فقال لهما يسوع لا تخافا. اذهبا قولا لاخوتي ان يذهبوا الى الجليل وهناك يرونني"));
arasuperobject.biblemap.put("b40_28_11", new arasuperobject(40,28,11,"وفيما هما ذاهبتان اذا قوم من الحراس جاءوا الى المدينة واخبروا رؤساء الكهنة بكل ما كان"));
arasuperobject.biblemap.put("b40_28_12", new arasuperobject(40,28,12,"فاجتمعوا مع الشيوخ وتشاوروا واعطوا العسكر فضة كثيرة"));
arasuperobject.biblemap.put("b40_28_13", new arasuperobject(40,28,13,"قائلين. قولوا ان تلاميذه أتوا ليلا وسرقوه ونحن نيام"));
arasuperobject.biblemap.put("b40_28_14", new arasuperobject(40,28,14,"واذا سمع ذلك عند الوالي فنحن نستعطفه ونجعلكم مطمئنين"));
arasuperobject.biblemap.put("b40_28_15", new arasuperobject(40,28,15,"فاخذوا الفضة وفعلوا كما علّموهم. فشاع هذا القول عند اليهود الى هذا اليوم"));
arasuperobject.biblemap.put("b40_28_16", new arasuperobject(40,28,16,"واما الاحد عشر تلميذا فانطلقوا الى الجليل الى الجبل حيث امرهم يسوع"));
