arasuperobject.biblemap.put("b40_28_18", new arasuperobject(40,28,18,"فتقدم يسوع وكلمهم قائلا. دفع اليّ كل سلطان في السماء وعلى الارض"));
arasuperobject.biblemap.put("b40_28_19", new arasuperobject(40,28,19,"فاذهبوا وتلمذوا جميع الامم وعمدوهم باسم الآب والابن والروح القدس"));
