arasuperobject.biblemap.put("b41_3_28", new arasuperobject(41,3,28,"الحق اقول لكم ان جميع الخطايا تغفر لبني البشر والتجاديف التي يجدفونها"));
arasuperobject.biblemap.put("b41_3_29", new arasuperobject(41,3,29,"ولكن من جدّف على الروح القدس فليس له مغفرة الى الابد بل هو مستوجب دينونة ابدية"));
arasuperobject.biblemap.put("b41_3_30", new arasuperobject(41,3,30,"لانهم قالوا ان معه روحا نجسا"));
arasuperobject.biblemap.put("b41_3_31", new arasuperobject(41,3,31,"فجاءت حينئذ اخوته وامه ووقفوا خارجا وارسلوا اليه يدعونه"));
arasuperobject.biblemap.put("b41_3_32", new arasuperobject(41,3,32,"وكان الجمع جالسا حوله فقالوا له هوذا امك واخوتك خارجا يطلبونك"));
arasuperobject.biblemap.put("b41_3_33", new arasuperobject(41,3,33,"فاجابهم قائلا من امي واخوتي"));
arasuperobject.biblemap.put("b41_3_34", new arasuperobject(41,3,34,"ثم نظر حوله الى الجالسين وقال ها امي واخوتي"));
arasuperobject.biblemap.put("b41_3_35", new arasuperobject(41,3,35,"لان من يصنع مشيئة الله هو اخي واختي وامي"));
arasuperobject.biblemap.put("b41_4_1", new arasuperobject(41,4,1,"وابتدأ ايضا يعلم عند البحر. فاجتمع اليه جمع كثير حتى انه دخل السفينة وجلس على البحر والجمع كله كان عند البحر على الارض"));
arasuperobject.biblemap.put("b41_4_2", new arasuperobject(41,4,2,"فكان يعلّمهم كثيرا بامثال وقال لهم في تعليمه"));
arasuperobject.biblemap.put("b41_4_3", new arasuperobject(41,4,3,"اسمعوا. هوذا الزارع قد خرج ليزرع"));
arasuperobject.biblemap.put("b41_4_4", new arasuperobject(41,4,4,"وفيما هو يزرع سقط بعض على الطريق فجاءت طيور السماء واكلته"));
arasuperobject.biblemap.put("b41_4_5", new arasuperobject(41,4,5,"وسقط آخر على مكان محجر حيث لم تكن له تربة كثيرة. فنبت حالا اذ لم يكن له عمق ارض"));
arasuperobject.biblemap.put("b41_4_6", new arasuperobject(41,4,6,"ولكن لما اشرقت الشمس احترق. واذ لم يكن له اصل جف"));
arasuperobject.biblemap.put("b41_4_7", new arasuperobject(41,4,7,"وسقط آخر في الشوك. فطلع الشوك وخنقه فلم يعطي ثمرا"));
arasuperobject.biblemap.put("b41_4_8", new arasuperobject(41,4,8,"وسقط آخر في الارض الجيدة. فاعطى ثمرا يصعد وينمو. فأتى واحد بثلاثين وآخر بستين وآخر بمئة"));
arasuperobject.biblemap.put("b41_4_9", new arasuperobject(41,4,9,"ثم قال لهم من له اذنان للسمع فليسمع"));
arasuperobject.biblemap.put("b41_4_10", new arasuperobject(41,4,10,"ولما كان وحده سأله الذين حوله مع الاثني عشر عن المثل"));
arasuperobject.biblemap.put("b41_4_11", new arasuperobject(41,4,11,"فقال لهم قد أعطي لكم ان تعرفوا سرّ ملكوت الله. واما الذين هم من خارج فبالامثال يكون لهم كل شيء"));
arasuperobject.biblemap.put("b41_4_12", new arasuperobject(41,4,12,"لكي يبصروا مبصرين ولا ينظروا ويسمعوا سامعين ولا يفهموا لئلا يرجعوا فتغفر لهم خطاياهم"));
arasuperobject.biblemap.put("b41_4_13", new arasuperobject(41,4,13,"ثم قال لهم أما تعلمون هذا المثل. فكيف تعرفون جميع الامثال"));
arasuperobject.biblemap.put("b41_4_14", new arasuperobject(41,4,14,"الزارع يزرع الكلمة"));
arasuperobject.biblemap.put("b41_4_15", new arasuperobject(41,4,15,"وهؤلاء هم الذين على الطريق. حيث تزرع الكلمة وحينما يسمعون يأتي الشيطان للوقت وينزع الكلمة المزروعة في قلوبهم"));
arasuperobject.biblemap.put("b41_4_16", new arasuperobject(41,4,16,"وهؤلاء كذلك هم الذين زرعوا على الاماكن المحجرة. الذين حينما يسمعون الكلمة يقبلونها للوقت بفرح"));
arasuperobject.biblemap.put("b41_4_17", new arasuperobject(41,4,17,"ولكن ليس لهم اصل في ذواتهم بل هم الى حين. فبعد ذلك اذا حدث ضيق او اضطهاد من اجل الكلمة فللوقت يعثرون"));
arasuperobject.biblemap.put("b41_4_18", new arasuperobject(41,4,18,"وهؤلاء هم الذين زرعوا بين الشوك. هؤلاء هم الذين يسمعون الكلمة"));
arasuperobject.biblemap.put("b41_4_19", new arasuperobject(41,4,19,"وهموم هذا العالم وغرور الغنى وشهوات سائر الاشياء تدخل وتخنق الكلمة فتصير بلا ثمر"));
arasuperobject.biblemap.put("b41_4_20", new arasuperobject(41,4,20,"وهؤلاء هم الذين زرعوا على الارض الجيدة. الذين يسمعون الكلمة ويقبلونها ويثمرون واحد ثلاثين وآخر ستين وآخر مئة"));
arasuperobject.biblemap.put("b41_4_21", new arasuperobject(41,4,21,"ثم قال لهم هل يؤتى بسراج ليوضع تحت المكيال او تحت السرير. أليس ليوضع على المنارة"));
arasuperobject.biblemap.put("b41_4_22", new arasuperobject(41,4,22,"لانه ليس شيء خفي لا يظهر ولا صار مكتوما الا ليعلن"));
arasuperobject.biblemap.put("b41_4_23", new arasuperobject(41,4,23,"ان كان لاحد اذنان للسمع فليسمع"));
arasuperobject.biblemap.put("b41_4_24", new arasuperobject(41,4,24,"وقال لهم انظروا ما تسمعون. بالكيل الذي به تكيلون يكال لكم ويزاد لكم ايها السامعون"));
arasuperobject.biblemap.put("b41_4_25", new arasuperobject(41,4,25,"لان من له سيعطى. واما من ليس له فالذي عنده سيؤخذ منه"));
arasuperobject.biblemap.put("b41_4_26", new arasuperobject(41,4,26,"وقال. هكذا ملكوت الله كأن انسانا يلقي البذار على الارض"));
arasuperobject.biblemap.put("b41_4_27", new arasuperobject(41,4,27,"وينام ويقوم ليلا ونهارا والبذار يطلع وينمو وهو لا يعلم كيف"));
arasuperobject.biblemap.put("b41_4_28", new arasuperobject(41,4,28,"لان الارض من ذاتها تأتي بثمر. اولا نباتا ثم سنبلا ثم قمحا ملآن في السنبل"));
arasuperobject.biblemap.put("b41_4_29", new arasuperobject(41,4,29,"واما متى ادرك الثمر فللوقت يرسل المنجل لان الحصاد قد حضر"));
arasuperobject.biblemap.put("b41_4_30", new arasuperobject(41,4,30,"وقال بماذا نشبّه ملكوت الله او باي مثل نمثله"));
arasuperobject.biblemap.put("b41_4_31", new arasuperobject(41,4,31,"مثل حبة خردل متى زرعت في الارض فهي اصغر جميع البزور التي على الارض"));
arasuperobject.biblemap.put("b41_4_32", new arasuperobject(41,4,32,"ولكن متى زرعت تطلع وتصير اكبر جميع البقول وتصنع اغصانا كبيرة حتى تستطيع طيور السماء ان تتآوى تحت ظلها"));
arasuperobject.biblemap.put("b41_4_33", new arasuperobject(41,4,33,"وبامثال كثيرة مثل هذه كان يكلمهم حسبما كانوا يستطيعون ان يسمعوا"));
arasuperobject.biblemap.put("b41_4_34", new arasuperobject(41,4,34,"وبدون مثل لم يكن يكلمهم. واما على انفراد فكان يفسر لتلاميذه كل شيء"));
arasuperobject.biblemap.put("b41_4_35", new arasuperobject(41,4,35,"وقال لهم في ذلك اليوم لما كان المساء. لنجتز الى العبر"));
arasuperobject.biblemap.put("b41_4_36", new arasuperobject(41,4,36,"فصرفوا الجمع واخذوه كما كان في السفينة. وكانت معه ايضا سفن اخرى صغيرة"));
arasuperobject.biblemap.put("b41_4_37", new arasuperobject(41,4,37,"فحدث نوء ريح عظيم فكانت الامواج تضرب الى السفينة حتى صارت تمتلئ"));
arasuperobject.biblemap.put("b41_4_38", new arasuperobject(41,4,38,"وكان هو في المؤخر على وسادة نائما. فأيقظوه وقالوا له يا معلّم أما يهمك اننا نهلك"));
arasuperobject.biblemap.put("b41_4_39", new arasuperobject(41,4,39,"فقام وانتهر الريح وقال للبحر اسكت. ابكم. فسكنت الريح وصار هدوء عظيم"));
arasuperobject.biblemap.put("b41_4_40", new arasuperobject(41,4,40,"وقال لهم ما بالكم خائفين هكذا. كيف لا ايمان لكم"));
arasuperobject.biblemap.put("b41_4_41", new arasuperobject(41,4,41,"فخافوا خوفا عظيما وقالوا بعضهم لبعض من هو هذا. فان الريح ايضا والبحر يطيعانه"));
arasuperobject.biblemap.put("b41_5_1", new arasuperobject(41,5,1,"وجاءوا الى عبر البحر الى كورة الجدريين"));
arasuperobject.biblemap.put("b41_5_2", new arasuperobject(41,5,2,"ولما خرج من السفينة للوقت استقبله من القبور انسان به روح نجس"));
arasuperobject.biblemap.put("b41_5_3", new arasuperobject(41,5,3,"كان مسكنه في القبور ولم يقدر احد ان يربطه ولا بسلاسل"));
arasuperobject.biblemap.put("b41_5_4", new arasuperobject(41,5,4,"لانه قد ربط كثيرا بقيود وسلاسل فقطع السلاسل وكسر القيود. فلم يقدر احد ان يذلله"));
arasuperobject.biblemap.put("b41_5_5", new arasuperobject(41,5,5,"وكان دائما ليلا ونهارا في الجبال وفي القبور يصيح ويجرح نفسه بالحجارة"));
arasuperobject.biblemap.put("b41_5_6", new arasuperobject(41,5,6,"فلما رأى يسوع من بعيد ركض وسجد له"));
arasuperobject.biblemap.put("b41_5_7", new arasuperobject(41,5,7,"وصرخ بصوت عظيم وقال ما لي ولك يا يسوع ابن الله العلي. استحلفك بالله ان لا تعذبني"));
arasuperobject.biblemap.put("b41_5_8", new arasuperobject(41,5,8,"لانه قال له اخرج من الانسان يا ايها الروح النجس"));
arasuperobject.biblemap.put("b41_5_9", new arasuperobject(41,5,9,"وسأله ما اسمك. فاجاب قائلا اسمي لجئون لاننا كثيرون"));
arasuperobject.biblemap.put("b41_5_10", new arasuperobject(41,5,10,"وطلب اليه كثيرا ان لا يرسلهم الى خارج الكورة"));
arasuperobject.biblemap.put("b41_5_11", new arasuperobject(41,5,11,"وكان هناك عند الجبال قطيع كبير من الخنازير يرعى"));
arasuperobject.biblemap.put("b41_5_12", new arasuperobject(41,5,12,"فطلب اليه كل الشياطين قائلين ارسلنا الى الخنازير لندخل فيها"));
arasuperobject.biblemap.put("b41_5_13", new arasuperobject(41,5,13,"فأذن لهم يسوع للوقت. فخرجت الارواح النجسة ودخلت في الخنازير. فاندفع القطيع من على الجرف الى البحر. وكان نحو الفين. فاختنق في البحر"));
arasuperobject.biblemap.put("b41_5_14", new arasuperobject(41,5,14,"واما رعاة الخنازير فهربوا واخبروا في المدينة وفي الضياع. فخرجوا ليروا ما جرى"));
arasuperobject.biblemap.put("b41_5_15", new arasuperobject(41,5,15,"وجاءوا الى يسوع فنظروا المجنون الذي كان فيه اللجئون جالسا ولابسا وعاقلا. فخافوا"));
arasuperobject.biblemap.put("b41_5_16", new arasuperobject(41,5,16,"فحدثهم الذين رأوا كيف جرى للمجنون وعن الخنازير"));
arasuperobject.biblemap.put("b41_5_17", new arasuperobject(41,5,17,"فابتدأوا يطلبون اليه ان يمضي من تخومهم"));
arasuperobject.biblemap.put("b41_5_18", new arasuperobject(41,5,18,"ولما دخل السفينة طلب اليه الذي كان مجنونا ان يكون معه"));
arasuperobject.biblemap.put("b41_5_19", new arasuperobject(41,5,19,"فلم يدعه يسوع بل قال له اذهب الى بيتك والى اهلك واخبرهم كم صنع الرب بك ورحمك"));
arasuperobject.biblemap.put("b41_5_20", new arasuperobject(41,5,20,"فمضى وابتدأ ينادي في العشر المدن كم صنع به يسوع. فتعجب الجميع"));
arasuperobject.biblemap.put("b41_5_21", new arasuperobject(41,5,21,"ولما اجتاز يسوع في السفينة ايضا الى العبر اجتمع اليه جمع كثير. وكان عند البحر"));
arasuperobject.biblemap.put("b41_5_22", new arasuperobject(41,5,22,"واذا واحد من رؤساء المجمع اسمه يايروس جاء. ولما رآه خرّ عند قدميه"));
arasuperobject.biblemap.put("b41_5_23", new arasuperobject(41,5,23,"وطلب اليه كثيرا قائلا ابنتي الصغيرة على آخر نسمة. ليتك تأتي وتضع يدك عليها لتشفى فتحيا"));
arasuperobject.biblemap.put("b41_5_24", new arasuperobject(41,5,24,"فمضى معه وتبعه جمع كثير وكانوا يزحمونه"));
arasuperobject.biblemap.put("b41_5_25", new arasuperobject(41,5,25,"وامرأة بنزف دم منذ اثنتي عشرة سنة"));
arasuperobject.biblemap.put("b41_5_26", new arasuperobject(41,5,26,"وقد تألمت كثيرا من اطباء كثيرين وانفقت كل ما عندها ولم تنتفع شيئا بل صارت الى حال اردأ"));
arasuperobject.biblemap.put("b41_5_27", new arasuperobject(41,5,27,"لما سمعت بيسوع جاءت في الجمع من وراء ومسّت ثوبه"));
arasuperobject.biblemap.put("b41_5_28", new arasuperobject(41,5,28,"لانها قالت ان مسست ولو ثيابه شفيت"));
arasuperobject.biblemap.put("b41_5_29", new arasuperobject(41,5,29,"فللوقت جف ينبوع دمها وعلمت في جسمها انها قد برئت من الداء"));
arasuperobject.biblemap.put("b41_5_30", new arasuperobject(41,5,30,"فللوقت التفت يسوع بين الجمع شاعرا في نفسه بالقوة التي خرجت منه وقال من لمس ثيابي"));
arasuperobject.biblemap.put("b41_5_31", new arasuperobject(41,5,31,"فقال له تلاميذه انت تنظر الجمع يزحمك وتقول من لمسني"));
arasuperobject.biblemap.put("b41_5_32", new arasuperobject(41,5,32,"وكان ينظر حوله ليرى التي فعلت هذا"));
arasuperobject.biblemap.put("b41_5_33", new arasuperobject(41,5,33,"واما المرأة فجاءت وهي خائفة ومرتعدة عالمة بما حصل لها فخرّت وقالت له الحق كله"));
arasuperobject.biblemap.put("b41_5_34", new arasuperobject(41,5,34,"فقال لها يا ابنة ايمانك قد شفاك. اذهبي بسلام وكوني صحيحة من دائك"));
arasuperobject.biblemap.put("b41_5_35", new arasuperobject(41,5,35,"وبينما هو يتكلم جاءوا من دار رئيس المجمع قائلين ابنتك ماتت. لماذا تتعب المعلم بعد"));
arasuperobject.biblemap.put("b41_5_36", new arasuperobject(41,5,36,"فسمع يسوع لوقته الكلمة التي قيلت فقال لرئيس المجمع لا تخف. آمن فقط"));
arasuperobject.biblemap.put("b41_5_37", new arasuperobject(41,5,37,"ولم يدع احد يتبعه الا بطرس ويعقوب ويوحنا اخا يعقوب"));
arasuperobject.biblemap.put("b41_5_38", new arasuperobject(41,5,38,"فجاء الى بيت رئيس المجمع ورأى ضجيجا. يبكون ويولولون كثيرا"));
arasuperobject.biblemap.put("b41_5_39", new arasuperobject(41,5,39,"فدخل وقال لهم لماذا تضجون وتبكون. لم تمت الصبية لكنها نائمة"));
arasuperobject.biblemap.put("b41_5_40", new arasuperobject(41,5,40,"فضحكوا عليه. اما هو فاخرج الجميع واخذ ابا الصبية وامها والذين معه ودخل حيث كانت الصبية مضطجعة"));
arasuperobject.biblemap.put("b41_5_41", new arasuperobject(41,5,41,"وامسك بيد الصبية وقال لها طليثا قومي. الذي تفسيره يا صبية لك اقول قومي"));
arasuperobject.biblemap.put("b41_5_42", new arasuperobject(41,5,42,"وللوقت قامت الصبية ومشت. لانها كانت ابنة اثنتي عشر سنة. فبهتوا بهتا عظيما"));
arasuperobject.biblemap.put("b41_5_43", new arasuperobject(41,5,43,"فاوصاهم كثيرا ان لا يعلم احد بذلك. وقال ان تعطى لتأكل"));
arasuperobject.biblemap.put("b41_6_1", new arasuperobject(41,6,1,"وخرج من هناك وجاء الى وطنه وتبعه تلاميذه"));
arasuperobject.biblemap.put("b41_6_2", new arasuperobject(41,6,2,"ولما كان السبت ابتدأ يعلم في المجمع. وكثيرون اذ سمعوا بهتوا قائلين من اين لهذا هذه. وما هذه الحكمة التي أعطيت له حتى تجري على يديه قوات مثل هذه"));
arasuperobject.biblemap.put("b41_6_3", new arasuperobject(41,6,3,"أليس هذا هو النجار ابن مريم واخو يعقوب ويوسي ويهوذا وسمعان. أوليست اخواته ههنا عندنا. فكانوا يعثرون به"));
arasuperobject.biblemap.put("b41_6_4", new arasuperobject(41,6,4,"فقال لهم يسوع ليس نبي بلا كرامة الا في وطنه وبين اقربائه وفي بيته"));
arasuperobject.biblemap.put("b41_6_5", new arasuperobject(41,6,5,"ولم يقدر ان يصنع هناك ولا قوة واحدة غير انه وضع يديه على مرضى قليلين فشفاهم"));
arasuperobject.biblemap.put("b41_6_6", new arasuperobject(41,6,6,"وتعجب من عدم ايمانهم. وصار يطوف القرى المحيطة يعلم"));
arasuperobject.biblemap.put("b41_6_7", new arasuperobject(41,6,7,"ودعا الاثني عشر وابتدأ يرسلهم اثنين اثنين. واعطاهم سلطانا على الارواح النجسة"));
arasuperobject.biblemap.put("b41_6_8", new arasuperobject(41,6,8,"واوصاهم ان لا يحملوا شيئا للطريق غير عصا فقط. لا مزودا ولا خبزا ولا نحاسا في المنطقة"));
arasuperobject.biblemap.put("b41_6_9", new arasuperobject(41,6,9,"بل يكونوا مشدودين بنعال ولا يلبسوا ثوبين"));
arasuperobject.biblemap.put("b41_6_10", new arasuperobject(41,6,10,"وقال لهم حيثما دخلتم بيتا فاقيموا فيه حتى تخرجوا من هناك"));
arasuperobject.biblemap.put("b41_6_11", new arasuperobject(41,6,11,"وكل من لا يقبلكم ولا يسمع لكم فاخرجوا من هناك وانفضوا التراب الذي تحت ارجلكم شهادة عليهم. الحق اقول لكم ستكون لارض سدوم وعمورة يوم الدين حالة اكثر احتمالا مما لتلك المدينة"));
arasuperobject.biblemap.put("b41_6_12", new arasuperobject(41,6,12,"فخرجوا وصاروا يكرزون ان يتوبوا"));
arasuperobject.biblemap.put("b41_6_13", new arasuperobject(41,6,13,"واخرجوا شياطين كثيرة ودهنوا بزيت مرضى كثيرين فشفوهم"));
arasuperobject.biblemap.put("b41_6_14", new arasuperobject(41,6,14,"فسمع هيرودس الملك. لان اسمه صار مشهورا. وقال ان يوحنا المعمدان قام من الاموات ولذلك تعمل به القوات"));
arasuperobject.biblemap.put("b41_6_15", new arasuperobject(41,6,15,"قال آخرون انه ايليا. وقال آخرون انه نبي او كأحد الانبياء"));
arasuperobject.biblemap.put("b41_6_16", new arasuperobject(41,6,16,"ولكن لما سمع هيرودس قال هذا هو يوحنا الذي قطعت انا راسه. انه قام من الاموات"));
arasuperobject.biblemap.put("b41_6_17", new arasuperobject(41,6,17,"لان هيرودس نفسه كان قد ارسل وامسك يوحنا واوثقه في السجن من اجل هيروديا امرأة فيلبس اخيه اذ كان قد تزوج بها"));
arasuperobject.biblemap.put("b41_6_18", new arasuperobject(41,6,18,"لان يوحنا كان يقول لهيرودس لا يحل ان تكون لك امرأة اخيك"));
arasuperobject.biblemap.put("b41_6_19", new arasuperobject(41,6,19,"فحنقت هيروديا عليه وارادت ان تقتله ولم تقدر"));
arasuperobject.biblemap.put("b41_6_20", new arasuperobject(41,6,20,"لان هيرودس كان يهاب يوحنا عالما انه رجل بار وقديس وكان يحفظه. واذ سمعه فعل كثيرا وسمعه بسرور"));
arasuperobject.biblemap.put("b41_6_21", new arasuperobject(41,6,21,"واذ كان يوم موافق لما صنع هيرودس في مولده عشاء لعظمائه وقواد الالوف ووجوه الجليل"));
arasuperobject.biblemap.put("b41_6_22", new arasuperobject(41,6,22,"دخلت ابنة هيروديا ورقصت فسرّت هيرودس والمتكئين معه. فقال الملك للصبية مهما اردت اطلبي مني فاعطيك"));
arasuperobject.biblemap.put("b41_6_23", new arasuperobject(41,6,23,"واقسم لها ان مهما طلبت مني لأعطينّك حتى نصف مملكتي"));
arasuperobject.biblemap.put("b41_6_24", new arasuperobject(41,6,24,"فخرجت وقالت لامها ماذا اطلب. فقالت راس يوحنا المعمدان"));
arasuperobject.biblemap.put("b41_6_25", new arasuperobject(41,6,25,"فدخلت للوقت بسرعة الى الملك وطلبت قائلة اريد ان تعطيني حالا راس يوحنا المعمدان على طبق"));
arasuperobject.biblemap.put("b41_6_26", new arasuperobject(41,6,26,"فحزن الملك جدا. ولاجل الاقسام والمتكئين لم يرد ان يردها"));
arasuperobject.biblemap.put("b41_6_27", new arasuperobject(41,6,27,"فللوقت ارسل الملك سيافا وامر ان يؤتى براسه"));
arasuperobject.biblemap.put("b41_6_28", new arasuperobject(41,6,28,"فمضى وقطع راسه في السجن. وأتى برأسه على طبق واعطاه للصبية والصبية اعطته لامها"));
arasuperobject.biblemap.put("b41_6_29", new arasuperobject(41,6,29,"ولما سمع تلاميذه جاءوا ورفعوا جثته ووضعوها في قبر"));
arasuperobject.biblemap.put("b41_6_30", new arasuperobject(41,6,30,"واجتمع الرسل الى يسوع واخبروه بكل شيء كل ما فعلوا وكل ما علّموا"));
arasuperobject.biblemap.put("b41_6_31", new arasuperobject(41,6,31,"فقال لهم تعالوا انتم منفردين الى موضع خلاء واستريحوا قليلا. لان القادمين والذاهبين كانوا كثيرين. ولم تتيسر لهم فرصة للاكل"));
arasuperobject.biblemap.put("b41_6_32", new arasuperobject(41,6,32,"فمضوا في السفينة الى موضع خلاء منفردين"));
arasuperobject.biblemap.put("b41_6_33", new arasuperobject(41,6,33,"فرآهم الجموع منطلقين وعرفه كثيرون فتراكضوا الى هناك من جميع المدن مشاة وسبقوهم واجتمعوا اليه"));
arasuperobject.biblemap.put("b41_6_34", new arasuperobject(41,6,34,"فلما خرج يسوع رأى جمعا كثيرا فتحنن عليهم اذ كانوا كخراف لا راعي لها فابتدأ يعلّمهم كثيرا"));
arasuperobject.biblemap.put("b41_6_35", new arasuperobject(41,6,35,"وبعد ساعات كثيرة تقدم اليه تلاميذه قائلين الموضع خلاء والوقت مضى"));
arasuperobject.biblemap.put("b41_6_36", new arasuperobject(41,6,36,"اصرفهم لكي يمضوا الى الضياع والقرى حوالينا ويبتاعوا لهم خبزا. لان ليس عندهم ما ياكلون"));
arasuperobject.biblemap.put("b41_6_37", new arasuperobject(41,6,37,"فاجاب وقال لهم اعطوهم انتم ليأكلوا. فقالوا له أنمضي ونبتاع خبزا بمئتي دينار ونعطيهم ليأكلوا"));
arasuperobject.biblemap.put("b41_6_38", new arasuperobject(41,6,38,"فقال لهم كم رغيفا عندكم. اذهبوا وانظروا. ولما علموا قالوا خمسة وسمكتان"));
arasuperobject.biblemap.put("b41_6_39", new arasuperobject(41,6,39,"فأمرهم ان يجعلوا الجميع يتكئون رفاقا رفاقا على العشب الاخضر"));
arasuperobject.biblemap.put("b41_6_40", new arasuperobject(41,6,40,"فاتكأوا صفوفا صفوفا مئة مئة وخمسين خمسين"));
arasuperobject.biblemap.put("b41_6_41", new arasuperobject(41,6,41,"فاخذ الارغفة الخمسة والسمكتين ورفع نظره نحو السماء وبارك ثم كسر الارغفة واعطى تلاميذه ليقدموا اليهم. وقسم السمكتين للجميع"));
arasuperobject.biblemap.put("b41_6_42", new arasuperobject(41,6,42,"فاكل الجميع وشبعوا"));
arasuperobject.biblemap.put("b41_6_43", new arasuperobject(41,6,43,"ثم رفعوا من الكسر اثنتي عشرة قفة مملوءة ومن السمك"));
arasuperobject.biblemap.put("b41_6_44", new arasuperobject(41,6,44,"وكان الذين اكلوا من الارغفة نحو خمسة آلاف رجل"));
arasuperobject.biblemap.put("b41_6_45", new arasuperobject(41,6,45,"وللوقت ألزم تلاميذه ان يدخلوا السفينة ويسبقوا الى العبر الى بيت صيدا حتى يكون قد صرف الجمع"));
arasuperobject.biblemap.put("b41_6_46", new arasuperobject(41,6,46,"وبعدما ودعهم مضى الى الجبل ليصلّي"));
arasuperobject.biblemap.put("b41_6_47", new arasuperobject(41,6,47,"ولما صار المساء كانت السفينة في وسط البحر وهو على البر وحده"));
arasuperobject.biblemap.put("b41_6_48", new arasuperobject(41,6,48,"ورآهم معذبين في الجذف. لان الريح كانت ضدهم. ونحو الهزيع الرابع من الليل اتاهم ماشيا على البحر واراد ان يتجاوزهم"));
arasuperobject.biblemap.put("b41_6_49", new arasuperobject(41,6,49,"فلما رأوه ماشيا على البحر ظنوه خيالا فصرخوا"));
arasuperobject.biblemap.put("b41_6_50", new arasuperobject(41,6,50,"لان الجميع رأوه واضطربوا. فللوقت كلمهم وقال لهم ثقوا. انا هو. لا تخافوا"));
arasuperobject.biblemap.put("b41_6_51", new arasuperobject(41,6,51,"فصعد اليهم الى السفينة فسكنت الريح. فبهتوا وتعجبوا في انفسهم جدا الى الغاية"));
arasuperobject.biblemap.put("b41_6_52", new arasuperobject(41,6,52,"لانهم لم يفهموا بالارغفة اذ كانت قلوبهم غليظة"));
arasuperobject.biblemap.put("b41_6_53", new arasuperobject(41,6,53,"فلما عبروا جاءوا الى ارض جنيسارت وارسوا"));
arasuperobject.biblemap.put("b41_6_54", new arasuperobject(41,6,54,"ولما خرجوا من السفينة للوقت عرفوه"));
arasuperobject.biblemap.put("b41_6_55", new arasuperobject(41,6,55,"فطافوا جميع تلك الكورة المحيطة وابتدأوا يحملون المرضى على اسرّة الى حيث سمعوا انه هناك"));
arasuperobject.biblemap.put("b41_6_56", new arasuperobject(41,6,56,"وحيثما دخل الى قرى او مدن او ضياع وضعوا المرضى في الاسواق وطلبوا اليه ان يلمسوا ولو هدب ثوبه. وكل من لمسه شفي"));
arasuperobject.biblemap.put("b41_7_1", new arasuperobject(41,7,1,"واجتمع اليه الفريسيون وقوم من الكتبة قادمين من اورشليم"));
arasuperobject.biblemap.put("b41_7_2", new arasuperobject(41,7,2,"ولما رأوا بعضا من تلاميذه يأكلون خبزا بايد دنسة اي غير مغسولة لاموا"));
arasuperobject.biblemap.put("b41_7_3", new arasuperobject(41,7,3,"لان الفريسيين وكل اليهود ان لم يغسلوا ايديهم باعتناء لا يأكلون. متمسكين بتقليد الشيوخ"));
arasuperobject.biblemap.put("b41_7_4", new arasuperobject(41,7,4,"ومن السوق ان لم يغتسلوا لا يأكلون. واشياء اخرى كثيرة تسلموها للتمسك بها من غسل كؤوس واباريق وآنية نحاس واسرّة"));
arasuperobject.biblemap.put("b41_7_5", new arasuperobject(41,7,5,"ثم سأله الفريسيون والكتبة لماذا لا يسلك تلاميذك حسب تقليد الشيوخ بل يأكلون خبزا بأيد غير مغسولة"));
arasuperobject.biblemap.put("b41_7_6", new arasuperobject(41,7,6,"فاجاب وقال لهم حسنا تنبأ اشعياء عنكم انتم المرائين كما هو مكتوب. هذا الشعب يكرمني بشفتيه واما قلبه فمبتعد عني بعيدا"));
arasuperobject.biblemap.put("b41_7_7", new arasuperobject(41,7,7,"وباطلا يعبدونني وهم يعلمون تعاليم هي وصايا الناس"));
arasuperobject.biblemap.put("b41_7_8", new arasuperobject(41,7,8,"لانكم تركتم وصية الله وتتمسكون بتقليد الناس. غسل الاباريق والكؤوس وامورا أخر كثيرة مثل هذه تفعلون"));
arasuperobject.biblemap.put("b41_7_9", new arasuperobject(41,7,9,"ثم قال لهم حسنا رفضتم وصية الله لتحفظوا تقليدكم"));
arasuperobject.biblemap.put("b41_7_10", new arasuperobject(41,7,10,"لان موسى قال اكرم اباك وامك. ومن يشتم ابا او اما فليمت موتا"));
arasuperobject.biblemap.put("b41_7_11", new arasuperobject(41,7,11,"واما انتم فتقولون ان قال انسان لابيه او امه قربان اي هدية هو الذي تنتفع به مني"));
arasuperobject.biblemap.put("b41_7_12", new arasuperobject(41,7,12,"فلا تدعونه في ما بعد يفعل شيئا لابيه او امه"));
arasuperobject.biblemap.put("b41_7_13", new arasuperobject(41,7,13,"مبطلين كلام الله بتقليدكم الذي سلمتموه. وأمورا كثيرة مثل هذه تفعلون"));
arasuperobject.biblemap.put("b41_7_14", new arasuperobject(41,7,14,"ثم دعا كل الجمع وقال لهم اسمعوا مني كلكم وافهموا"));
arasuperobject.biblemap.put("b41_7_15", new arasuperobject(41,7,15,"ليس شيء من خارج الانسان اذا دخل فيه يقدر ان ينجسه. لكن الاشياء التي تخرج منه هي التي تنجس الانسان"));
arasuperobject.biblemap.put("b41_7_16", new arasuperobject(41,7,16,"ان كان لاحد اذنان للسمع فليسمع"));
arasuperobject.biblemap.put("b41_7_17", new arasuperobject(41,7,17,"ولما دخل من عند الجمع الى البيت سأله تلاميذه عن المثل"));
arasuperobject.biblemap.put("b41_7_18", new arasuperobject(41,7,18,"فقال لهم أفانتم ايضا هكذا غير فاهمين. أما تفهمون ان كل ما يدخل الانسان من خارج لا يقدر ان ينجسه"));
arasuperobject.biblemap.put("b41_7_19", new arasuperobject(41,7,19,"لانه لا يدخل الى قلبه بل الى الجوف ثم يخرج الى الخلاء وذلك يطهر كل الاطعمة"));
arasuperobject.biblemap.put("b41_7_20", new arasuperobject(41,7,20,"ثم قال ان الذي يخرج من الانسان ذلك ينجس الانسان"));
arasuperobject.biblemap.put("b41_7_21", new arasuperobject(41,7,21,"لانه من الداخل من قلوب الناس تخرج الافكار الشريرة زنى فسق قتل"));
arasuperobject.biblemap.put("b41_7_22", new arasuperobject(41,7,22,"سرقة طمع خبث مكر عهارة عين شريرة تجديف كبرياء جهل"));
arasuperobject.biblemap.put("b41_7_23", new arasuperobject(41,7,23,"جميع هذه الشرور تخرج من الداخل وتنجس الانسان"));
arasuperobject.biblemap.put("b41_7_24", new arasuperobject(41,7,24,"ثم قام من هناك ومضى الى تخوم صور وصيدا. ودخل بيتا وهو يريد ان لا يعلم احد. فلم يقدر ان يختفي"));
arasuperobject.biblemap.put("b41_7_25", new arasuperobject(41,7,25,"لان امرأة كان بابنتها روح نجس سمعت به فأتت وخرّت عند قدميه"));
arasuperobject.biblemap.put("b41_7_26", new arasuperobject(41,7,26,"وكانت المرأة اممية وفي جنسها فينيقية سورية. فسألته ان يخرج الشيطان من ابنتها"));
arasuperobject.biblemap.put("b41_7_27", new arasuperobject(41,7,27,"واما يسوع فقال لها دعي البنين اولا يشبعون. لانه ليس حسنا ان يؤخذ خبز البنين ويطرح للكلاب"));
arasuperobject.biblemap.put("b41_7_28", new arasuperobject(41,7,28,"فاجابت وقالت له نعم يا سيد. والكلاب ايضا تحت المائدة تأكل من فتات البنين"));
arasuperobject.biblemap.put("b41_7_29", new arasuperobject(41,7,29,"فقال لها. لاجل هذه الكلمة اذهبي. قد خرج الشيطان من ابنتك"));
arasuperobject.biblemap.put("b41_7_30", new arasuperobject(41,7,30,"فذهبت الى بيتها ووجدت الشيطان قد خرج والابنة مطروحة على الفراش"));
arasuperobject.biblemap.put("b41_7_31", new arasuperobject(41,7,31,"ثم خرج ايضا من تخوم صور وصيدا وجاء الى بحر الجليل في وسط حدود المدن العشر"));
arasuperobject.biblemap.put("b41_7_32", new arasuperobject(41,7,32,"وجاءوا اليه بأصم أعقد وطلبوا اليه ان يضع يده عليه"));
arasuperobject.biblemap.put("b41_7_33", new arasuperobject(41,7,33,"فأخذه من بين الجمع على ناحية ووضع اصابعه في اذنيه وتفل ولمس لسانه"));
arasuperobject.biblemap.put("b41_7_34", new arasuperobject(41,7,34,"ورفع نظره نحو السماء وأنّ وقال له إفّثا. اي انفتح"));
arasuperobject.biblemap.put("b41_7_35", new arasuperobject(41,7,35,"وللوقت انفتحت اذناه وانحل رباط لسانه وتكلم مستقيما"));
arasuperobject.biblemap.put("b41_7_36", new arasuperobject(41,7,36,"فاوصاهم ان لا يقولوا لاحد. ولكن على قدر ما اوصاهم كانوا ينادون اكثر كثيرا"));
arasuperobject.biblemap.put("b41_7_37", new arasuperobject(41,7,37,"وبهتوا الى الغاية قائلين انه عمل كل شيء حسنا. جعل الصم يسمعون والخرس يتكلمون"));
arasuperobject.biblemap.put("b41_8_1", new arasuperobject(41,8,1,"في تلك الايام اذ كان الجمع كثيرا جدا ولم يكن لهم ما يأكلون دعا يسوع تلاميذه وقال لهم"));
arasuperobject.biblemap.put("b41_8_2", new arasuperobject(41,8,2,"اني اشفق على الجمع لان الآن لهم ثلاثة ايام يمكثون معي وليس لهم ما يأكلون"));
arasuperobject.biblemap.put("b41_8_3", new arasuperobject(41,8,3,"وان صرفتهم الى بيوتهم صائمين يخورون في الطريق. لان قوما منهم جاءوا من بعيد"));
arasuperobject.biblemap.put("b41_8_4", new arasuperobject(41,8,4,"فاجابه تلاميذه. من اين يستطيع احد ان يشبع هؤلاء خبزا هنا في البرية"));
arasuperobject.biblemap.put("b41_8_5", new arasuperobject(41,8,5,"فسألهم كم عندكم من الخبز. فقالوا سبعة"));
arasuperobject.biblemap.put("b41_8_6", new arasuperobject(41,8,6,"فامر الجمع ان يتكئوا على الارض. واخذ السبع خبزات وشكر وكسر واعطى تلاميذه ليقدموا فقدموا الى الجمع"));
arasuperobject.biblemap.put("b41_8_7", new arasuperobject(41,8,7,"وكان معهم قليل من صغار السمك. فبارك وقال ان يقدموا هذه ايضا"));
arasuperobject.biblemap.put("b41_8_8", new arasuperobject(41,8,8,"فاكلوا وشبعوا. ثم رفعوا فضلات الكسر سبعة سلال"));
arasuperobject.biblemap.put("b41_8_9", new arasuperobject(41,8,9,"وكان الآكلون نحو اربعة آلاف. ثم صرفهم"));
arasuperobject.biblemap.put("b41_8_10", new arasuperobject(41,8,10,"وللوقت دخل السفينة مع تلاميذه وجاء الى نواحي دلمانوثة"));
arasuperobject.biblemap.put("b41_8_11", new arasuperobject(41,8,11,"فخرج الفريسيون وابتدأوا يحاورونه طالبين منه آية من السماء لكي يجربوه"));
arasuperobject.biblemap.put("b41_8_12", new arasuperobject(41,8,12,"فتنهد بروحه وقال لماذا يطلب هذا الجيل آية. الحق اقول لكم لن يعطى هذا الجيل آية"));
arasuperobject.biblemap.put("b41_8_13", new arasuperobject(41,8,13,"ثم تركهم ودخل ايضا السفينة ومضى الى العبر"));
arasuperobject.biblemap.put("b41_8_14", new arasuperobject(41,8,14,"ونسوا ان يأخذوا خبزا ولم يكن معهم في السفينة الا رغيف واحد"));
arasuperobject.biblemap.put("b41_8_15", new arasuperobject(41,8,15,"واوصاهم قائلا انظروا وتحرزوا من خمير الفريسيين وخمير هيرودس"));
arasuperobject.biblemap.put("b41_8_16", new arasuperobject(41,8,16,"ففكروا قائلين بعضهم لبعض ليس عندنا خبز"));
arasuperobject.biblemap.put("b41_8_17", new arasuperobject(41,8,17,"فعلم يسوع وقال لهم لماذا تفكرون ان ليس عندكم خبز. ألا تشعرون بعد ولا تفهمون. أحتى الآن قلوبكم غليظة"));
arasuperobject.biblemap.put("b41_8_18", new arasuperobject(41,8,18,"ألكم اعين ولا تبصرون ولكم آذان ولا تسمعون ولا تذكرون"));
arasuperobject.biblemap.put("b41_8_19", new arasuperobject(41,8,19,"حين كسّرت الارغفة الخمسة للخمسة الآلاف كم قفة مملوءة كسرا رفعتم. قالوا له اثنتي عشرة"));
arasuperobject.biblemap.put("b41_8_20", new arasuperobject(41,8,20,"وحين السبعة للاربعة الآلاف كم سل كسر مملوّا رفعتم. قالوا سبعة"));
arasuperobject.biblemap.put("b41_8_21", new arasuperobject(41,8,21,"فقال لهم كيف لا تفهمون"));
arasuperobject.biblemap.put("b41_8_22", new arasuperobject(41,8,22,"وجاء الى بيت صيدا. فقدموا اليه اعمى وطلبوا اليه ان يلمسه"));
arasuperobject.biblemap.put("b41_8_23", new arasuperobject(41,8,23,"فاخذ بيد الاعمى واخرجه الى خارج القرية وتفل في عينيه ووضع يديه عليه وسأله هل ابصر شيئا"));
arasuperobject.biblemap.put("b41_8_24", new arasuperobject(41,8,24,"فتطلع وقال ابصر الناس كاشجار يمشون"));
arasuperobject.biblemap.put("b41_8_25", new arasuperobject(41,8,25,"ثم وضع يديه ايضا على عينيه وجعله يتطلع فعاد صحيحا وابصر كل انسان جليّا"));
arasuperobject.biblemap.put("b41_8_26", new arasuperobject(41,8,26,"فارسله الى بيته قائلا لا تدخل القرية ولا تقل لاحد في القرية"));
arasuperobject.biblemap.put("b41_8_27", new arasuperobject(41,8,27,"ثم خرج يسوع وتلاميذه الى قرى قيصرية فيلبس. وفي الطريق سأل تلاميذه قائلا لهم من يقول الناس اني انا"));
arasuperobject.biblemap.put("b41_8_28", new arasuperobject(41,8,28,"فاجابوا. يوحنا المعمدان. وآخرون ايليا. وآخرون واحد من الانبياء"));
arasuperobject.biblemap.put("b41_8_29", new arasuperobject(41,8,29,"فقال لهم وانتم من تقولون اني انا. فاجاب بطرس وقال له انت المسيح"));
arasuperobject.biblemap.put("b41_8_30", new arasuperobject(41,8,30,"فانتهرهم كي لا يقولوا لاحد عنه"));
arasuperobject.biblemap.put("b41_8_31", new arasuperobject(41,8,31,"وابتدأ يعلّمهم ان ابن الانسان ينبغي ان يتألم كثيرا ويرفض من الشيوخ ورؤساء الكهنة والكتبة ويقتل. وبعد ثلاثة ايام يقوم"));
arasuperobject.biblemap.put("b41_8_32", new arasuperobject(41,8,32,"وقال القول علانية. فاخذه بطرس اليه وابتدأ ينتهره"));
arasuperobject.biblemap.put("b41_8_33", new arasuperobject(41,8,33,"فالتفت وابصر تلاميذه فانتهر بطرس قائلا اذهب عني يا شيطان. لانك لا تهتم بما لله لكن بما للناس"));
arasuperobject.biblemap.put("b41_8_34", new arasuperobject(41,8,34,"ودعا الجمع مع تلاميذه وقال لهم من اراد ان يأتي ورائي فلينكر نفسه ويحمل صليبه ويتبعني"));
arasuperobject.biblemap.put("b41_8_35", new arasuperobject(41,8,35,"فان من اراد ان يخلّص نفسه يهلكها. ومن يهلك نفسه من اجلي ومن اجل الانجيل فهو يخلّصها"));
arasuperobject.biblemap.put("b41_8_36", new arasuperobject(41,8,36,"لانه ماذا ينتفع الانسان لو ربح العالم كله وخسر نفسه"));
arasuperobject.biblemap.put("b41_8_37", new arasuperobject(41,8,37,"او ماذا يعطي الانسان فداء عن نفسه"));
arasuperobject.biblemap.put("b41_8_38", new arasuperobject(41,8,38,"لان من استحى بي وبكلامي في هذا الجيل الفاسق الخاطئ فان ابن الانسان يستحي به متى جاء بمجد ابيه مع الملائكة القديسين"));
arasuperobject.biblemap.put("b41_9_1", new arasuperobject(41,9,1,"وقال لهم الحق اقول لكم ان من القيام ههنا قوما لا يذوقون الموت حتى يروا ملكوت الله قد أتى بقوة"));
arasuperobject.biblemap.put("b41_9_2", new arasuperobject(41,9,2,"وبعد ستة ايام اخذ يسوع بطرس ويعقوب ويوحنا وصعد بهم الى جبل عال منفردين وحدهم. وتغيّرت هيئته قدامهم"));
arasuperobject.biblemap.put("b41_9_3", new arasuperobject(41,9,3,"وصارت ثيابه تلمع بيضاء جدا كالثلج لا يقدر قصّار على الارض ان يبيض مثل ذلك"));
arasuperobject.biblemap.put("b41_9_4", new arasuperobject(41,9,4,"وظهر لهم ايليا مع موسى. وكانا يتكلمان مع يسوع"));
arasuperobject.biblemap.put("b41_9_5", new arasuperobject(41,9,5,"فجعل بطرس يقول ليسوع يا سيدي جيد ان نكون ههنا. فلنصنع ثلاث مظال. لك واحدة ولموسى واحدة ولايليا واحدة"));
arasuperobject.biblemap.put("b41_9_6", new arasuperobject(41,9,6,"لانه لم يكن يعلم ما يتكلم به اذ كانوا مرتعبين"));
arasuperobject.biblemap.put("b41_9_7", new arasuperobject(41,9,7,"وكانت سحابة تظللهم. فجاء صوت من السحابة قائلا هذا هو ابني الحبيب له اسمعوا"));
arasuperobject.biblemap.put("b41_9_8", new arasuperobject(41,9,8,"فنظروا حولهم بغتة ولم يروا احدا غير يسوع وحده معهم"));
arasuperobject.biblemap.put("b41_9_9", new arasuperobject(41,9,9,"وفيما هم نازلون من الجبل اوصاهم ان لا يحدثوا احد بما ابصروا الا متى قام ابن الانسان من الاموات"));
arasuperobject.biblemap.put("b41_9_10", new arasuperobject(41,9,10,"فحفظوا الكلمة لانفسهم يتساءلون ما هو القيام من الاموات"));
arasuperobject.biblemap.put("b41_9_11", new arasuperobject(41,9,11,"فسألوه قائلين لماذا يقول الكتبة ان ايليا ينبغي ان يأتي اولا"));
arasuperobject.biblemap.put("b41_9_12", new arasuperobject(41,9,12,"فاجاب وقال لهم ان ايليا يأتي اولا ويرد كل شيء. وكيف هو مكتوب عن ابن الانسان ان يتألم كثيرا ويرذل"));
arasuperobject.biblemap.put("b41_9_13", new arasuperobject(41,9,13,"لكن اقول لكم ان ايليا ايضا قد أتى وعملوا به كل ما ارادوا كما هو مكتوب عنه"));
arasuperobject.biblemap.put("b41_9_14", new arasuperobject(41,9,14,"ولما جاء الى التلاميذ رأى جمعا كثيرا حولهم وكتبة يحاورونهم"));
arasuperobject.biblemap.put("b41_9_15", new arasuperobject(41,9,15,"وللوقت كل الجمع لما رأوه تحيّروا وركضوا وسلموا عليه"));
arasuperobject.biblemap.put("b41_9_16", new arasuperobject(41,9,16,"فسأل الكتبة بماذا تحاورونهم"));
arasuperobject.biblemap.put("b41_9_17", new arasuperobject(41,9,17,"فاجاب واحد من الجمع وقال يا معلّم قد قدمت اليك ابني به روح اخرس"));
arasuperobject.biblemap.put("b41_9_18", new arasuperobject(41,9,18,"وحيثما ادركه يمزقه فيزبد ويصرّ باسنانه وييبس. فقلت لتلاميذك ان يخرجوه فلم يقدروا"));
arasuperobject.biblemap.put("b41_9_19", new arasuperobject(41,9,19,"فاجاب وقال لهم ايها الجيل غير المؤمن الى متى اكون معكم. الى متى احتملكم . ‎ قدموه اليّ"));
arasuperobject.biblemap.put("b41_9_20", new arasuperobject(41,9,20,"فقدموه اليه. فلما رآه للوقت صرعه الروح فوقع على الارض يتمرغ ويزبد"));
arasuperobject.biblemap.put("b41_9_21", new arasuperobject(41,9,21,"فسأل اباه كم من الزمان منذ اصابه هذا. فقال منذ صباه"));
arasuperobject.biblemap.put("b41_9_22", new arasuperobject(41,9,22,"وكثيرا ما ألقاه في النار وفي الماء ليهلكه. لكن ان كنت تستطيع شيئا فتحنن علينا واعنّا"));
arasuperobject.biblemap.put("b41_9_23", new arasuperobject(41,9,23,"فقال له يسوع ان كنت تستطيع ان تؤمن. كل شيء مستطاع للمؤمن"));
arasuperobject.biblemap.put("b41_9_24", new arasuperobject(41,9,24,"فللوقت صرخ ابو الولد بدموع وقال اؤمن يا سيد فاعن عدم ايماني"));
arasuperobject.biblemap.put("b41_9_25", new arasuperobject(41,9,25,"فلما رأى يسوع ان الجمع يتراكضون انتهر الروح النجس قائلا له ايها الروح الاخرس الاصم انا آمرك. اخرج منه ولا تدخله ايضا"));
arasuperobject.biblemap.put("b41_9_26", new arasuperobject(41,9,26,"فصرخ وصرعه شديدا وخرج. فصار كميت حتى قال كثيرون انه مات"));
arasuperobject.biblemap.put("b41_9_27", new arasuperobject(41,9,27,"فامسكه يسوع بيده واقامه فقام"));
arasuperobject.biblemap.put("b41_9_28", new arasuperobject(41,9,28,"ولما دخل بيتا سأله تلاميذه على انفراد لماذا لم نقدر نحن ان نخرجه"));
arasuperobject.biblemap.put("b41_9_29", new arasuperobject(41,9,29,"فقال لهم هذا الجنس لا يمكن ان يخرج بشيء الا بالصلاة والصوم"));
arasuperobject.biblemap.put("b41_9_30", new arasuperobject(41,9,30,"وخرجوا من هناك واجتازوا الجليل ولم يرد ان يعلم احد"));
arasuperobject.biblemap.put("b41_9_31", new arasuperobject(41,9,31,"لانه كان يعلّم تلاميذه ويقول لهم ان ابن الانسان يسلم الى ايدي الناس فيقتلونه. وبعد ان يقتل يقوم في اليوم الثالث"));
arasuperobject.biblemap.put("b41_9_32", new arasuperobject(41,9,32,"واما هم فلم يفهموا القول وخافوا ان يسألوه"));
arasuperobject.biblemap.put("b41_9_33", new arasuperobject(41,9,33,"وجاء الى كفرناحوم. واذ كان في البيت سألهم بماذا كنتم تتكالمون فيما بينكم في الطريق"));
arasuperobject.biblemap.put("b41_9_34", new arasuperobject(41,9,34,"فسكتوا. لانهم تحاجوا في الطريق بعضهم مع بعض في من هو اعظم"));
arasuperobject.biblemap.put("b41_9_35", new arasuperobject(41,9,35,"فجلس ونادى الاثني عشر وقال لهم اذا اراد احد ان يكون اولا فيكون آخر الكل وخادما للكل"));
arasuperobject.biblemap.put("b41_9_36", new arasuperobject(41,9,36,"فاخذ ولدا واقامه في وسطهم ثم احتضنه وقال لهم"));
arasuperobject.biblemap.put("b41_9_37", new arasuperobject(41,9,37,"من قبل واحدا من اولاد مثل هذا باسمي يقبلني ومن قبلني فليس يقبلني انا بل الذي ارسلني"));
arasuperobject.biblemap.put("b41_9_38", new arasuperobject(41,9,38,"فاجابه يوحنا قائلا يا معلّم رأينا واحدا يخرج شياطين باسمك وهو ليس يتبعنا. فمنعناه لانه ليس يتبعنا"));
arasuperobject.biblemap.put("b41_9_39", new arasuperobject(41,9,39,"فقال يسوع لا تمنعوه. لانه ليس احد يصنع قوة باسمي ويستطيع سريعا ان يقول علي شرا"));
arasuperobject.biblemap.put("b41_9_40", new arasuperobject(41,9,40,"لان من ليس علينا فهو معنا"));
arasuperobject.biblemap.put("b41_9_41", new arasuperobject(41,9,41,"لان من سقاكم كاس ماء باسمي لانكم للمسيح فالحق اقول لكم انه لا يضيع اجره"));
arasuperobject.biblemap.put("b41_9_42", new arasuperobject(41,9,42,"ومن اعثر احد الصغار المؤمنين بي فخير له لو طوق عنقه بحجر رحى وطرح في البحر"));
arasuperobject.biblemap.put("b41_9_43", new arasuperobject(41,9,43,"وان اعثرتك يدك فاقطعها. خير لك ان تدخل الحياة اقطع من ان تكون لك يدان وتمضي الى جهنم الى النار التي لا تطفأ"));
arasuperobject.biblemap.put("b41_9_44", new arasuperobject(41,9,44,"حيث دودهم لا يموت والنار لا تطفأ"));
arasuperobject.biblemap.put("b41_9_45", new arasuperobject(41,9,45,"وان اعثرتك رجلك فاقطعها. خير لك ان تدخل الحياة اعرج من ان تكون لك رجلان وتطرح في جهنم في النار التي لا تطفا"));
arasuperobject.biblemap.put("b41_9_46", new arasuperobject(41,9,46,"حيث دودهم لا يموت والنار لا تطفأ"));
arasuperobject.biblemap.put("b41_9_47", new arasuperobject(41,9,47,"وان اعثرتك عينك فاقلعها. خير لك ان تدخل ملكوت الله اعور من ان تكون لك عينان وتطرح في جهنم النار"));
arasuperobject.biblemap.put("b41_9_48", new arasuperobject(41,9,48,"حيث دودهم لا يموت والنار لا تطفأ"));
arasuperobject.biblemap.put("b41_9_49", new arasuperobject(41,9,49,"لان كل واحد يملّح بنار وكل ذبيحة تملّح بملح"));
arasuperobject.biblemap.put("b41_9_50", new arasuperobject(41,9,50,"الملح جيد. ولكن اذا صار الملح بلا ملوحة فبماذا تصلحونه. ليكن لكم في انفسكم ملح وسالموا بعضكم بعضا"));
arasuperobject.biblemap.put("b41_10_1", new arasuperobject(41,10,1,"وقام من هناك وجاء الى تخوم اليهودية من عبر الاردن. فاجتمع اليه جموع ايضا وكعادته كان ايضا يعلّمهم"));
arasuperobject.biblemap.put("b41_10_2", new arasuperobject(41,10,2,"فتقدم الفريسيون وسألوه. هل يحل للرجل ان يطلّق امرأته. ليجربوه"));
arasuperobject.biblemap.put("b41_10_3", new arasuperobject(41,10,3,"فاجاب وقال لهم بماذا اوصاكم موسى"));
arasuperobject.biblemap.put("b41_10_4", new arasuperobject(41,10,4,"فقالوا موسى أذن ان يكتب كتاب طلاق فتطلّق"));
arasuperobject.biblemap.put("b41_10_5", new arasuperobject(41,10,5,"فاجاب يسوع وقال لهم. من اجل قساوة قلوبكم كتب لكم هذه الوصية"));
arasuperobject.biblemap.put("b41_10_6", new arasuperobject(41,10,6,"ولكن من بدء الخليقة ذكرا وانثى خلقهما الله"));
arasuperobject.biblemap.put("b41_10_7", new arasuperobject(41,10,7,"من اجل هذا يترك الرجل اباه وامه ويلتصق بامرأته"));
arasuperobject.biblemap.put("b41_10_8", new arasuperobject(41,10,8,"ويكون الاثنان جسدا واحدا. اذا ليسا بعد اثنين بل جسد واحد"));
arasuperobject.biblemap.put("b41_10_9", new arasuperobject(41,10,9,"فالذي جمعه الله لا يفرقه انسان"));
arasuperobject.biblemap.put("b41_10_10", new arasuperobject(41,10,10,"ثم في البيت سأله تلاميذه ايضا عن ذلك"));
arasuperobject.biblemap.put("b41_10_11", new arasuperobject(41,10,11,"فقال لهم من طلّق امرأته وتزوج باخرى يزني عليها"));
arasuperobject.biblemap.put("b41_10_12", new arasuperobject(41,10,12,"وان طلّقت امرأة زوجها وتزوجت بآخر تزني"));
arasuperobject.biblemap.put("b41_10_13", new arasuperobject(41,10,13,"وقدموا اليه اولادا لكي يلمسهم. واما التلاميذ فانتهروا الذين قدموهم"));
arasuperobject.biblemap.put("b41_10_14", new arasuperobject(41,10,14,"فلما رأى يسوع ذلك اغتاظ وقال لهم دعوا الاولاد يأتون اليّ ولا تمنعوهم لان لمثل هؤلاء ملكوت الله"));
arasuperobject.biblemap.put("b41_10_15", new arasuperobject(41,10,15,"الحق اقول لكم من لا يقبل ملكوت الله مثل ولد فلن يدخله"));
arasuperobject.biblemap.put("b41_10_16", new arasuperobject(41,10,16,"فاحتضنهم ووضع يديه عليهم وباركهم"));
arasuperobject.biblemap.put("b41_10_17", new arasuperobject(41,10,17,"وفيما هو خارج الى الطريق ركض واحد وجثا له وسأله ايها المعلّم الصالح ماذا اعمل لارث الحياة الابدية"));
arasuperobject.biblemap.put("b41_10_18", new arasuperobject(41,10,18,"فقال له يسوع لماذا تدعوني صالحا. ليس احد صالحا الا واحد وهو الله"));
arasuperobject.biblemap.put("b41_10_19", new arasuperobject(41,10,19,"انت تعرف الوصايا. لا تزن. لا تقتل. لا تسرق. لا تشهد بالزور. لا تسلب. اكرم اباك وامك"));
arasuperobject.biblemap.put("b41_10_20", new arasuperobject(41,10,20,"فاجاب وقال له يا معلّم هذه كلها حفظتها منذ حداثتي"));
arasuperobject.biblemap.put("b41_10_21", new arasuperobject(41,10,21,"فنظر اليه يسوع واحبه وقال له يعوزك شيء واحد. اذهب بع كل مالك واعط الفقراء فيكون لك كنز في السماء وتعال اتبعني حاملا الصليب"));
arasuperobject.biblemap.put("b41_10_22", new arasuperobject(41,10,22,"فاغتم على القول ومضى حزينا لانه كان ذا اموال كثيرة"));
arasuperobject.biblemap.put("b41_10_23", new arasuperobject(41,10,23,"فنظر يسوع حوله وقال لتلاميذه ما اعسر دخول ذوي الاموال الى ملكوت الله"));
arasuperobject.biblemap.put("b41_10_24", new arasuperobject(41,10,24,"فتحيّر التلاميذ من كلامه. فاجاب يسوع ايضا وقال لهم يا بنيّ ما اعسر دخول المتكلين على الاموال الى ملكوت الله"));
arasuperobject.biblemap.put("b41_10_25", new arasuperobject(41,10,25,"مرور جمل من ثقب ابرة ايسر من ان يدخل غني الى ملكوت الله"));
arasuperobject.biblemap.put("b41_10_26", new arasuperobject(41,10,26,"فبهتوا الى الغاية قائلين بعضهم لبعض فمن يستطيع ان يخلص"));
arasuperobject.biblemap.put("b41_10_27", new arasuperobject(41,10,27,"فنظر اليهم يسوع وقال. عند الناس غير مستطاع. ولكن ليس عند الله. لان كل شيء مستطاع عند الله"));
arasuperobject.biblemap.put("b41_10_28", new arasuperobject(41,10,28,"وابتدأ بطرس يقول له ها نحن قد تركنا كل شيء وتبعناك"));
arasuperobject.biblemap.put("b41_10_29", new arasuperobject(41,10,29,"فاجاب يسوع وقال الحق اقول لكم ليس احد ترك بيتا او اخوة او اخوات او ابا او اما او امرأة او اولادا او حقولا لاجلي ولاجل الانجيل"));
arasuperobject.biblemap.put("b41_10_30", new arasuperobject(41,10,30,"الا وياخذ مئة ضعف الآن في هذا الزمان بيوتا واخوة واخوات وامهات واولادا وحقولا مع اضطهادات وفي الدهر الآتي الحياة الابدية"));
arasuperobject.biblemap.put("b41_10_31", new arasuperobject(41,10,31,"ولكن كثيرون اولون يكونون آخرين والآخرون اولين"));
arasuperobject.biblemap.put("b41_10_32", new arasuperobject(41,10,32,"وكانوا في الطريق صاعدين الى اورشليم ويتقدمهم يسوع. وكانوا يتحيّرون وفيما هم يتبعون كانوا يخافون. فاخذ الاثني عشر ايضا وابتدأ يقول لهم عما سيحدث له"));
arasuperobject.biblemap.put("b41_10_33", new arasuperobject(41,10,33,"ها نحن صاعدون الى اورشليم وابن الانسان يسلم الى رؤساء الكهنة والكتبة فيحكمون عليه بالموت ويسلمونه الى الامم"));
arasuperobject.biblemap.put("b41_10_34", new arasuperobject(41,10,34,"فيهزأون به ويجلدونه ويتفلون عليه ويقتلونه وفي اليوم الثالث يقوم"));
arasuperobject.biblemap.put("b41_10_35", new arasuperobject(41,10,35,"وتقدم اليه يعقوب ويوحنا ابنا زبدي قائلين يا معلّم نريد ان تفعل لنا كل ما طلبنا"));
arasuperobject.biblemap.put("b41_10_36", new arasuperobject(41,10,36,"فقال لهما ماذا تريدان ان افعل لكما"));
arasuperobject.biblemap.put("b41_10_37", new arasuperobject(41,10,37,"فقالا له اعطنا ان نجلس واحد عن يمينك والآخر عن يسارك في مجدك"));
arasuperobject.biblemap.put("b41_10_38", new arasuperobject(41,10,38,"فقال لهما يسوع لستما تعلمان ما تطلبان. أتستطيعان ان تشربا الكاس التي اشربها انا وان تصطبغا بالصبغة التي اصطبغ بها انا"));
arasuperobject.biblemap.put("b41_10_39", new arasuperobject(41,10,39,"فقالا له نستطيع. فقال لهما يسوع اما الكاس التي اشربها انا فتشربانها وبالصبغة التي اصطبغ بها انا تصطبغان"));
arasuperobject.biblemap.put("b41_10_40", new arasuperobject(41,10,40,"واما الجلوس عن يميني وعن يساري فليس لي ان اعطيه الا للذين أعدّ لهم"));
arasuperobject.biblemap.put("b41_10_41", new arasuperobject(41,10,41,"ولما سمع العشرة ابتدأوا يغتاظون من اجل يعقوب ويوحنا"));
arasuperobject.biblemap.put("b41_10_42", new arasuperobject(41,10,42,"فدعاهم يسوع وقال لهم انتم تعلمون ان الذين يحسبون رؤساء الامم يسودونهم وان عظماءهم يتسلطون عليهم"));
arasuperobject.biblemap.put("b41_10_43", new arasuperobject(41,10,43,"فلا يكون هكذا فيكم. بل من اراد ان يصير فيكم عظيما يكون لكم خادما"));
arasuperobject.biblemap.put("b41_10_44", new arasuperobject(41,10,44,"ومن اراد ان يصير فيكم اولا يكون للجميع عبدا"));
arasuperobject.biblemap.put("b41_10_45", new arasuperobject(41,10,45,"لان ابن الانسان ايضا لم يأت ليخدم بل ليخدم وليبذل نفسه فدية عن كثيرين"));
arasuperobject.biblemap.put("b41_10_46", new arasuperobject(41,10,46,"وجاءوا الى اريحا. وفيما هو خارج من اريحا مع تلاميذه وجمع غفير كان بارتيماوس الاعمى ابن تيماوس جالسا على الطريق يستعطي"));
arasuperobject.biblemap.put("b41_10_47", new arasuperobject(41,10,47,"فلما سمع انه يسوع الناصري ابتدأ يصرخ ويقول يا يسوع ابن داود ارحمني"));
arasuperobject.biblemap.put("b41_10_48", new arasuperobject(41,10,48,"فانتهره كثيرون ليسكت. فصرخ اكثر كثيرا يا ابن داود ارحمني"));
arasuperobject.biblemap.put("b41_10_49", new arasuperobject(41,10,49,"فوقف يسوع وامر ان ينادى. فنادوا الاعمى قائلين له ثق. قم. هوذا يناديك"));
arasuperobject.biblemap.put("b41_10_50", new arasuperobject(41,10,50,"فطرح رداءه وقام وجاء الى يسوع"));
arasuperobject.biblemap.put("b41_10_51", new arasuperobject(41,10,51,"فاجاب يسوع وقال له ماذا تريد ان افعل بك. فقال له الاعمى يا سيدي ان ابصر"));
arasuperobject.biblemap.put("b41_10_52", new arasuperobject(41,10,52,"فقال له يسوع اذهب. ايمانك قد شفاك. فللوقت ابصر وتبع يسوع في الطريق"));
arasuperobject.biblemap.put("b41_11_1", new arasuperobject(41,11,1,"ولما قربوا من اورشليم الى بيت فاجي وبيت عنيا عند جبل الزيتون ارسل اثنين من تلاميذه"));
arasuperobject.biblemap.put("b41_11_2", new arasuperobject(41,11,2,"وقال لهما اذهبا الى القرية التي امامكما فللوقت وانتما داخلان اليها تجدان جحشا مربوطا لم يجلس عليه احد من الناس. فحلاه وأتيا به"));
arasuperobject.biblemap.put("b41_11_3", new arasuperobject(41,11,3,"وان قال لكما احد لماذا تفعلان هذا فقولا الرب محتاج اليه. فللوقت يرسله الى هنا"));
arasuperobject.biblemap.put("b41_11_4", new arasuperobject(41,11,4,"فمضيا ووجدا الجحش مربوطا عند الباب خارجا على الطريق فحلاه"));
arasuperobject.biblemap.put("b41_11_5", new arasuperobject(41,11,5,"فقال لهما قوم من القيام هناك ماذا تفعلان تحلان الجحش"));
arasuperobject.biblemap.put("b41_11_6", new arasuperobject(41,11,6,"فقالا لهم كما اوصى يسوع. فتركوهما"));
arasuperobject.biblemap.put("b41_11_7", new arasuperobject(41,11,7,"فأتيا بالجحش الى يسوع وألقيا عليه ثيابهما فجلس عليه"));
arasuperobject.biblemap.put("b41_11_8", new arasuperobject(41,11,8,"وكثيرون فرشوا ثيابهم في الطريق. وآخرون قطعوا اغصانا من الشجر وفرشوها في الطريق"));
arasuperobject.biblemap.put("b41_11_9", new arasuperobject(41,11,9,"والذين تقدموا والذين تبعوا كانوا يصرخون قائلين أوصنا. مبارك الآتي باسم الرب"));
arasuperobject.biblemap.put("b41_11_10", new arasuperobject(41,11,10,"مباركة مملكة ابينا داود الآتية باسم الرب أوصنا في الاعالي"));
arasuperobject.biblemap.put("b41_11_11", new arasuperobject(41,11,11,"فدخل يسوع اورشليم والهيكل ولما نظر حوله الى كل شيء اذ كان الوقت قد امسى خرج الى بيت عنيا مع الاثني عشر"));
arasuperobject.biblemap.put("b41_11_12", new arasuperobject(41,11,12,"وفي الغد لما خرجوا من بيت عنيا جاع"));
arasuperobject.biblemap.put("b41_11_13", new arasuperobject(41,11,13,"فنظر شجرة تين من بعيد عليها ورق وجاء لعله يجد فيها شيئا فلما جاء اليها لم يجد شيئا الا ورقا. لانه لم يكن وقت التين"));
arasuperobject.biblemap.put("b41_11_14", new arasuperobject(41,11,14,"فاجاب يسوع وقال لها لا يأكل احد منك ثمرا بعد الى الابد. وكان تلاميذه يسمعون"));
arasuperobject.biblemap.put("b41_11_15", new arasuperobject(41,11,15,"وجاءوا الى اورشليم. ولما دخل يسوع الهيكل ابتدأ يخرج الذين كانوا يبيعون ويشترون في الهيكل وقلب موائد الصيارفة وكراسي باعة الحمام"));
arasuperobject.biblemap.put("b41_11_16", new arasuperobject(41,11,16,"ولم يدع احد يجتاز الهيكل بمتاع"));
arasuperobject.biblemap.put("b41_11_17", new arasuperobject(41,11,17,"وكان يعلّم قائلا لهم أليس مكتوبا بيتي بيت صلاة يدعى لجميع الامم. وانتم جعلتموه مغارة لصوص"));
arasuperobject.biblemap.put("b41_11_18", new arasuperobject(41,11,18,"وسمع الكتبة ورؤساء الكهنة فطلبوا كيف يهلكونه لانهم خافوه اذ بهت الجمع كله من تعليمه"));
arasuperobject.biblemap.put("b41_11_19", new arasuperobject(41,11,19,"ولما صار المساء خرج الى خارج المدينة"));
arasuperobject.biblemap.put("b41_11_20", new arasuperobject(41,11,20,"وفي الصباح اذ كانوا مجتازين رأوا التينة قد يبست من الاصول"));
arasuperobject.biblemap.put("b41_11_21", new arasuperobject(41,11,21,"فتذكر بطرس وقال له يا سيدي انظر. التينة التي لعنتها قد يبست"));
arasuperobject.biblemap.put("b41_11_22", new arasuperobject(41,11,22,"فاجاب يسوع وقال لهم ليكن لكم ايمان بالله"));
arasuperobject.biblemap.put("b41_11_23", new arasuperobject(41,11,23,"لاني الحق اقول لكم ان من قال لهذا الجبل انتقل وانطرح في البحر ولا يشك في قلبه بل يؤمن ان ما يقوله يكون فمهما قال يكون له"));
arasuperobject.biblemap.put("b41_11_24", new arasuperobject(41,11,24,"لذلك اقول لكم كل ما تطلبونه حينما تصلون فآمنوا ان تنالوه فيكون لكم"));
arasuperobject.biblemap.put("b41_11_25", new arasuperobject(41,11,25,"ومتى وقفتم تصلّون فاغفروا ان كان لكم على احد شيء لكي يغفر لكم ايضا ابوكم الذي في السموات زلاتكم"));
arasuperobject.biblemap.put("b41_11_26", new arasuperobject(41,11,26,"وان لم تغفروا انتم لا يغفر ابوكم الذي في السموات ايضا زلاتكم"));
arasuperobject.biblemap.put("b41_11_27", new arasuperobject(41,11,27,"وجاءوا ايضا الى اورشليم. وفيما هو يمشي في الهيكل اقبل اليه رؤساء الكهنة والكتبة والشيوخ"));
arasuperobject.biblemap.put("b41_11_28", new arasuperobject(41,11,28,"وقالوا له باي سلطان تفعل هذا ومن اعطاك هذا السلطان حتى تفعل هذا"));
arasuperobject.biblemap.put("b41_11_29", new arasuperobject(41,11,29,"فاجاب يسوع وقال لهم وانا ايضا اسألكم كلمة واحدة. اجيبوني فاقول لكم باي سلطان افعل هذا"));
arasuperobject.biblemap.put("b41_11_30", new arasuperobject(41,11,30,"معمودية يوحنا من السماء كانت ام من الناس. اجيبوني"));
arasuperobject.biblemap.put("b41_11_31", new arasuperobject(41,11,31,"ففكروا في انفسهم قائلين ان قلنا من السماء يقول فلماذا لم تؤمنوا به"));
arasuperobject.biblemap.put("b41_11_32", new arasuperobject(41,11,32,"وان قلنا من الناس فخافوا الشعب. لان يوحنا كان عند الجميع انه بالحقيقة نبي"));
arasuperobject.biblemap.put("b41_11_33", new arasuperobject(41,11,33,"فاجابوا وقالوا ليسوع لا نعلم. فاجاب يسوع وقال لهم ولا انا اقول لكم بأي سلطان افعل هذا"));
arasuperobject.biblemap.put("b41_12_1", new arasuperobject(41,12,1,"وابتدأ يقول لهم بامثال انسان غرس كرما واحاطه بسياج وحفر حوض معصرة وبنى برجا وسلمه الى كرامين وسافر"));
arasuperobject.biblemap.put("b41_12_2", new arasuperobject(41,12,2,"ثم ارسل الى الكرامين في الوقت عبدا ليأخذ من الكرامين من ثمر الكرم"));
arasuperobject.biblemap.put("b41_12_3", new arasuperobject(41,12,3,"فاخذوه وجلدوه وارسلوه فارغا"));
arasuperobject.biblemap.put("b41_12_4", new arasuperobject(41,12,4,"ثم ارسل اليهم ايضا عبدا آخر. فرجموه وشجوه وارسلوه مهانا"));
arasuperobject.biblemap.put("b41_12_5", new arasuperobject(41,12,5,"ثم ارسل ايضا آخر. فقتلوه. ثم آخرين كثيرين فجلدوا منهم بعضا وقتلوا بعضا"));
arasuperobject.biblemap.put("b41_12_6", new arasuperobject(41,12,6,"فاذ كان له ايضا ابن واحد حبيب اليه ارسله ايضا اليهم اخيرا قائلا انهم يهابون ابني"));
arasuperobject.biblemap.put("b41_12_7", new arasuperobject(41,12,7,"ولكن اولئك الكرامين قالوا فيما بينهم هذا هو الوارث. هلموا نقتله فيكون لنا الميراث"));
arasuperobject.biblemap.put("b41_12_8", new arasuperobject(41,12,8,"فأخذوه وقتلوه واخرجوه خارج الكرم"));
arasuperobject.biblemap.put("b41_12_9", new arasuperobject(41,12,9,"فماذا يفعل صاحب الكرم. يأتي ويهلك الكرامين ويعطي الكرم الى آخرين"));
arasuperobject.biblemap.put("b41_12_10", new arasuperobject(41,12,10,"أما قرأتم هذا المكتوب. الحجر الذي رفضه البناؤون هو قد صار راس الزاوية"));
arasuperobject.biblemap.put("b41_12_11", new arasuperobject(41,12,11,"من قبل الرب كان هذا وهو عجيب في اعيننا"));
arasuperobject.biblemap.put("b41_12_12", new arasuperobject(41,12,12,"فطلبوا ان يمسكوه ولكنهم خافوا من الجمع. لانهم عرفوا انه قال المثل عليهم. فتركوه ومضوا"));
arasuperobject.biblemap.put("b41_12_13", new arasuperobject(41,12,13,"ثم ارسلوا اليه قوما من الفريسيين والهيرودسيين لكي يصطادوه بكلمة"));
arasuperobject.biblemap.put("b41_12_14", new arasuperobject(41,12,14,"فلما جاءوا قالوا له يا معلّم نعلم انك صادق ولا تبالي باحد لانك لا تنظر الى وجوه الناس بل بالحق تعلّم طريق الله. أيجوز ان تعطى جزية لقيصر ام لا. نعطي ام لا نعطي"));
arasuperobject.biblemap.put("b41_12_15", new arasuperobject(41,12,15,"فعلم رياءهم وقال لهم لماذا تجربونني. ايتوني بدينار لانظره"));
arasuperobject.biblemap.put("b41_12_16", new arasuperobject(41,12,16,"فأتوا به. فقال لهم لمن هذه الصورة والكتابة. فقالوا له لقيصر"));
arasuperobject.biblemap.put("b41_12_17", new arasuperobject(41,12,17,"فاجاب يسوع وقال لهم اعطوا ما لقيصر لقيصر وما للّه للّه. فتعجبوا منه"));
arasuperobject.biblemap.put("b41_12_18", new arasuperobject(41,12,18,"وجاء اليه قوم من الصدوقيين الذين يقولون ليس قيامة وسألوه قائلين"));
arasuperobject.biblemap.put("b41_12_19", new arasuperobject(41,12,19,"يا معلّم كتب لنا موسى ان مات لاحد اخ وترك امرأة ولم يخلّف اولادا ان ياخذ اخوه امرأته ويقيم نسلا لاخيه"));
arasuperobject.biblemap.put("b41_12_20", new arasuperobject(41,12,20,"فكان سبعة اخوة. اخذ الاول امرأة ومات ولم يترك نسلا"));
arasuperobject.biblemap.put("b41_12_21", new arasuperobject(41,12,21,"فاخذها الثاني ومات ولم يترك هو ايضا نسلا. وهكذا الثالث"));
arasuperobject.biblemap.put("b41_12_22", new arasuperobject(41,12,22,"فاخذها السبعة ولم يتركوا نسلا. وآخر الكل ماتت المرأة ايضا"));
arasuperobject.biblemap.put("b41_12_23", new arasuperobject(41,12,23,"ففي القيامة متى قاموا لمن منهم تكون زوجة. لانها كانت زوجة للسبعة"));
arasuperobject.biblemap.put("b41_12_24", new arasuperobject(41,12,24,"فاجاب يسوع وقال لهم أليس لهذا تضلون اذ لا تعرفون الكتب ولا قوة الله"));
arasuperobject.biblemap.put("b41_12_25", new arasuperobject(41,12,25,"لانهم متى قاموا من الاموات لا يزوجون ولا يزوجون بل يكونون كملائكة في السموات"));
arasuperobject.biblemap.put("b41_12_26", new arasuperobject(41,12,26,"واما من جهة الاموات انهم يقومون أفما قرأتم في كتاب موسى في أمر العلّيقة كيف كلمه الله قائلا انا اله ابراهيم واله اسحق واله يعقوب"));
arasuperobject.biblemap.put("b41_12_27", new arasuperobject(41,12,27,"ليس هو اله اموات بل اله احياء. فانتم اذا تضلون كثيرا"));
arasuperobject.biblemap.put("b41_12_28", new arasuperobject(41,12,28,"فجاء واحد من الكتبة وسمعهم يتحاورون فلما رأى انه اجابهم حسنا سأله اية وصية هي اول الكل"));
arasuperobject.biblemap.put("b41_12_29", new arasuperobject(41,12,29,"فاجابه يسوع ان اول كل الوصايا هي اسمع يا اسرائيل. الرب الهنا رب واحد"));
arasuperobject.biblemap.put("b41_12_30", new arasuperobject(41,12,30,"وتحب الرب الهك من كل قلبك ومن كل نفسك ومن كل فكرك ومن كل قدرتك. هذه هي الوصية الاولى"));
arasuperobject.biblemap.put("b41_12_31", new arasuperobject(41,12,31,"وثانية مثلها هي تحب قريبك كنفسك. ليس وصية اخرى اعظم من هاتين"));
arasuperobject.biblemap.put("b41_12_32", new arasuperobject(41,12,32,"فقال له الكاتب جيدا يا معلّم. بالحق قلت لانه الله واحد وليس آخر سواه"));
arasuperobject.biblemap.put("b41_12_33", new arasuperobject(41,12,33,"ومحبته من كل القلب ومن كل الفهم ومن كل النفس ومن كل القدرة ومحبة القريب كالنفس هي افضل من جميع المحرقات والذبائح"));
arasuperobject.biblemap.put("b41_12_34", new arasuperobject(41,12,34,"فلما رآه يسوع انه اجاب بعقل قال له لست بعيدا عن ملكوت الله. ولم يجسر احد بعد ذلك ان يسأله"));
arasuperobject.biblemap.put("b41_12_35", new arasuperobject(41,12,35,"ثم اجاب يسوع وقال وهو يعلّم في الهيكل كيف يقول الكتبة ان المسيح ابن داود"));
arasuperobject.biblemap.put("b41_12_36", new arasuperobject(41,12,36,"لان داود نفسه قال بالروح القدس قال الرب لربي اجلس عن يميني حتى اضع اعداءك موطئا لقدميك"));
arasuperobject.biblemap.put("b41_12_37", new arasuperobject(41,12,37,"فداود نفسه يدعوه ربا. فمن اين هو ابنه. وكان الجمع الكثير يسمعه بسرور"));
arasuperobject.biblemap.put("b41_12_38", new arasuperobject(41,12,38,"وقال لهم في تعليمه تحرزوا من الكتبة الذين يرغبون المشي بالطيالسة والتحيات في الاسواق"));
arasuperobject.biblemap.put("b41_12_39", new arasuperobject(41,12,39,"والمجالس الاولى في المجامع والمتكآت الأولى في الولائم"));
arasuperobject.biblemap.put("b41_12_40", new arasuperobject(41,12,40,"الذين ياكلون بيوت الارامل ولعلة يطيلون الصلوات. هؤلاء يأخذون دينونة اعظم"));
arasuperobject.biblemap.put("b41_12_41", new arasuperobject(41,12,41,"وجلس يسوع تجاه الخزانة ونظر كيف يلقي الجمع نحاسا في الخزانة. كان اغنياء كثيرون يلقون كثيرا"));
arasuperobject.biblemap.put("b41_12_42", new arasuperobject(41,12,42,"فجاءت ارملة فقيرة والقت فلسين قيمتهما ربع"));
arasuperobject.biblemap.put("b41_12_43", new arasuperobject(41,12,43,"فدعا تلاميذه وقال لهم الحق اقول لكم ان هذه الارملة الفقيرة قد القت اكثر من جميع الذين القوا في الخزانة"));
arasuperobject.biblemap.put("b41_12_44", new arasuperobject(41,12,44,"لان الجميع من فضلتهم ألقوا. واما هذه فمن اعوازها القت كل ما عندها كل معيشتها"));
arasuperobject.biblemap.put("b41_13_1", new arasuperobject(41,13,1,"وفيما هو خارج من الهيكل قال له واحد من تلاميذه يا معلّم انظر ما هذه الحجارة وهذه الابنية"));
arasuperobject.biblemap.put("b41_13_2", new arasuperobject(41,13,2,"فاجاب يسوع وقال له أتنظر هذه الابنية العظيمة. لا يترك حجر على حجر لا ينقض"));
arasuperobject.biblemap.put("b41_13_3", new arasuperobject(41,13,3,"وفيما هو جالس على جبل الزيتون تجاه الهيكل سأله بطرس ويعقوب ويوحنا واندراوس على انفراد"));
arasuperobject.biblemap.put("b41_13_4", new arasuperobject(41,13,4,"قل لنا متى يكون هذا وما هي العلامة عندما يتم جميع هذا"));
arasuperobject.biblemap.put("b41_13_5", new arasuperobject(41,13,5,"فاجابهم يسوع وابتدأ يقول انظروا لا يضلكم احد"));
arasuperobject.biblemap.put("b41_13_6", new arasuperobject(41,13,6,"فان كثيرين سيأتون باسمي قائلين انا هو. ويضلون كثيرين"));
arasuperobject.biblemap.put("b41_13_7", new arasuperobject(41,13,7,"فاذا سمعتم بحروب واخبار حروب فلا ترتاعوا. لانها لا بد ان تكون. ولكن ليس المنتهى بعد"));
arasuperobject.biblemap.put("b41_13_8", new arasuperobject(41,13,8,"لانه تقوم امة على امة ومملكة على مملكة وتكون زلازل في اماكن وتكون مجاعات واضطرابات. هذه مبتدأ الاوجاع"));
arasuperobject.biblemap.put("b41_13_9", new arasuperobject(41,13,9,"فانظروا الى نفوسكم. لانهم سيسلمونكم الى مجالس وتجلدون في مجامع وتوقفون امام ولاة وملوك من اجلي شهادة لهم"));
arasuperobject.biblemap.put("b41_13_10", new arasuperobject(41,13,10,"وينبغي ان يكرز اولا بالانجيل في جميع الامم"));
arasuperobject.biblemap.put("b41_13_11", new arasuperobject(41,13,11,"فمتى ساقوكم ليسلموكم فلا تعتنوا من قبل بما تتكلمون ولا تهتموا. بل مهما أعطيتم في تلك الساعة فبذلك تكلموا. لان لستم انتم المتكلمين بل الروح القدس"));
arasuperobject.biblemap.put("b41_13_12", new arasuperobject(41,13,12,"وسيسلم الاخ اخاه الى الموت والاب ولده. ويقوم الاولاد على والديهم ويقتلونهم"));
arasuperobject.biblemap.put("b41_13_13", new arasuperobject(41,13,13,"وتكونون مبغضين من الجميع من اجل اسمي. ولكن الذي يصبر الى المنتهى فهذا يخلص"));
arasuperobject.biblemap.put("b41_13_14", new arasuperobject(41,13,14,"فمتى نظرتم رجسة الخراب التي قال عنها دانيال النبي قائمة حيث لا ينبغي. ليفهم القارئ. فحينئذ ليهرب الذين في اليهودية الى الجبال"));
arasuperobject.biblemap.put("b41_13_15", new arasuperobject(41,13,15,"والذي على السطح فلا ينزل الى البيت ولا يدخل ليأخذ من بيته شيئا"));
arasuperobject.biblemap.put("b41_13_16", new arasuperobject(41,13,16,"والذي في الحقل فلا يرجع الى الوراء ليأخذ ثوبه"));
arasuperobject.biblemap.put("b41_13_17", new arasuperobject(41,13,17,"وويل للحبالى والمرضعات في تلك الايام"));
arasuperobject.biblemap.put("b41_13_18", new arasuperobject(41,13,18,"وصلّوا لكي لا يكون هربكم في شتاء"));
arasuperobject.biblemap.put("b41_13_19", new arasuperobject(41,13,19,"لانه يكون في تلك الايام ضيق لم يكن مثله منذ ابتداء الخليقة التي خلقها الله الى الآن ولن يكون"));
arasuperobject.biblemap.put("b41_13_20", new arasuperobject(41,13,20,"ولو لم يقصّر الرب تلك الايام لم يخلص جسد. ولكن لاجل المختارين الذين اختارهم قصّر الايام"));
arasuperobject.biblemap.put("b41_13_21", new arasuperobject(41,13,21,"حينئذ ان قال لكم احد هوذا المسيح هنا او هوذا هناك فلا تصدقوا"));
arasuperobject.biblemap.put("b41_13_22", new arasuperobject(41,13,22,"لانه سيقوم مسحاء كذبة وانبياء كذبة ويعطون آيات وعجائب لكي يضلوا لو امكن المختارين ايضا"));
arasuperobject.biblemap.put("b41_13_23", new arasuperobject(41,13,23,"فانظروا انتم. ها انا قد سبقت واخبرتكم بكل شيء"));
arasuperobject.biblemap.put("b41_13_24", new arasuperobject(41,13,24,"واما في تلك الايام بعد ذلك الضيق فالشمس تظلم والقمر لا يعطي ضوءه"));
arasuperobject.biblemap.put("b41_13_25", new arasuperobject(41,13,25,"ونجوم السماء تتساقط والقوات التي في السموات تتزعزع"));
arasuperobject.biblemap.put("b41_13_26", new arasuperobject(41,13,26,"وحينئذ يبصرون ابن الانسان آتيا في سحاب بقوة كثيرة ومجد"));
arasuperobject.biblemap.put("b41_13_27", new arasuperobject(41,13,27,"فيرسل حينئذ ملائكته ويجمع مختاريه من الاربع الرياح من اقصاء الارض الى اقصاء السماء"));
arasuperobject.biblemap.put("b41_13_28", new arasuperobject(41,13,28,"فمن شجرة التين تعلّموا المثل. متى صار غصنها رخصا واخرجت اوراقا تعلمون ان الصيف قريب"));
arasuperobject.biblemap.put("b41_13_29", new arasuperobject(41,13,29,"هكذا انتم ايضا متى رأيتم هذه الاشياء صائرة فاعلموا انه قريب على الابواب"));
arasuperobject.biblemap.put("b41_13_30", new arasuperobject(41,13,30,"الحق اقول لكم لا يمضي هذا الجيل حتى يكون هذا كله"));
arasuperobject.biblemap.put("b41_13_31", new arasuperobject(41,13,31,"السماء والارض تزولان ولكن كلامي لا يزول"));
arasuperobject.biblemap.put("b41_13_32", new arasuperobject(41,13,32,"واما ذلك اليوم وتلك الساعة فلا يعلم بهما احد ولا الملائكة الذين في السماء ولا الابن الا الآب"));
arasuperobject.biblemap.put("b41_13_33", new arasuperobject(41,13,33,"انظروا. اسهروا وصلّوا لانكم لا تعلمون متى يكون الوقت"));
arasuperobject.biblemap.put("b41_13_34", new arasuperobject(41,13,34,"كانما انسان مسافر ترك بيته واعطى عبيده السلطان ولكل واحد عمله واوصى البواب ان يسهر"));
arasuperobject.biblemap.put("b41_13_35", new arasuperobject(41,13,35,"اسهروا اذا. لانكم لا تعلمون متى يأتي رب البيت أمساء ام نصف الليل أم صياح الديك ام صباحا"));
arasuperobject.biblemap.put("b41_13_36", new arasuperobject(41,13,36,"لئلا يأتي بغتة فيجدكم نياما"));
arasuperobject.biblemap.put("b41_13_37", new arasuperobject(41,13,37,"وما اقوله لكم اقوله للجميع اسهروا"));
arasuperobject.biblemap.put("b41_14_1", new arasuperobject(41,14,1,"وكان الفصح وايام الفطير بعد يومين. وكان رؤساء الكهنة والكتبة يطلبون كيف يمسكونه بمكر ويقتلونه"));
arasuperobject.biblemap.put("b41_14_2", new arasuperobject(41,14,2,"ولكنهم قالوا ليس في العيد لئلا يكون شغب في الشعب"));
arasuperobject.biblemap.put("b41_14_3", new arasuperobject(41,14,3,"وفيما هو في بيت عنيا في بيت سمعان الابرص وهو متكئ جاءت امرأة معها قارورة طيب ناردين خالص كثير الثمن. فكسرت القارورة وسكبته على راسه"));
arasuperobject.biblemap.put("b41_14_4", new arasuperobject(41,14,4,"وكان قوم مغتاظين في انفسهم فقالوا لماذا كان تلف الطيب هذا"));
arasuperobject.biblemap.put("b41_14_5", new arasuperobject(41,14,5,"لانه كان يمكن ان يباع هذا باكثر من ثلاث مئة دينار ويعطى للفقراء. وكانوا يؤنبونها"));
arasuperobject.biblemap.put("b41_14_6", new arasuperobject(41,14,6,"اما يسوع فقال اتركوها. لماذا تزعجونها. قد عملت بي عملا حسنا"));
arasuperobject.biblemap.put("b41_14_7", new arasuperobject(41,14,7,"لان الفقراء معكم في كل حين ومتى اردتم تقدرون ان تعملوا بهم خيرا. واما انا فلست معكم في كل حين"));
arasuperobject.biblemap.put("b41_14_8", new arasuperobject(41,14,8,"عملت ما عندها. قد سبقت ودهنت بالطيب جسدي للتكفين"));
arasuperobject.biblemap.put("b41_14_9", new arasuperobject(41,14,9,"الحق اقول لكم حيثما يكرز بهذا الانجيل في كل العالم يخبر ايضا بما فعلته هذه تذكارا لها"));
arasuperobject.biblemap.put("b41_14_10", new arasuperobject(41,14,10,"ثم ان يهوذا الاسخريوطي واحدا من الاثني عشر مضى الى رؤساء الكهنة ليسلمه اليهم"));
arasuperobject.biblemap.put("b41_14_11", new arasuperobject(41,14,11,"ولما سمعوا فرحوا ووعدوه ان يعطوه فضة. وكان يطلب كيف يسلمه في فرصة موافقة ‎"));
arasuperobject.biblemap.put("b41_14_12", new arasuperobject(41,14,12,"وفي اليوم الاول من الفطير حين كانوا يذبحون الفصح قال له تلاميذه اين تريد ان نمضي ونعد لتأكل الفصح"));
arasuperobject.biblemap.put("b41_14_13", new arasuperobject(41,14,13,"فارسل اثنين من تلاميذه وقال لهما اذهبا الى المدينة فيلاقيكما انسان حامل جرة ماء. اتبعاه"));
arasuperobject.biblemap.put("b41_14_14", new arasuperobject(41,14,14,"وحيثما يدخل فقولا لرب البيت ان المعلّم يقول اين المنزل حيث آكل الفصح مع تلاميذي"));
arasuperobject.biblemap.put("b41_14_15", new arasuperobject(41,14,15,"فهو يريكما علّية كبيرة مفروشة معدة. هناك اعدا لنا"));
arasuperobject.biblemap.put("b41_14_16", new arasuperobject(41,14,16,"فخرج تلميذاه وأتيا الى المدينة ووجدا كما قال لهما. فأعدا الفصح"));
arasuperobject.biblemap.put("b41_14_17", new arasuperobject(41,14,17,"ولما كان المساء جاء مع الاثني عشر"));
arasuperobject.biblemap.put("b41_14_18", new arasuperobject(41,14,18,"وفيما هم متكئون يأكلون قال يسوع الحق اقول لكم ان واحدا منكم يسلمني. الآكل معي"));
arasuperobject.biblemap.put("b41_14_19", new arasuperobject(41,14,19,"فابتدأوا يحزنون ويقولون له واحدا فواحدا هل انا. وآخر هل انا"));
arasuperobject.biblemap.put("b41_14_20", new arasuperobject(41,14,20,"فاجاب وقال لهم. هو واحد من الاثني عشر الذي يغمس معي في الصحفة"));
arasuperobject.biblemap.put("b41_14_21", new arasuperobject(41,14,21,"ان ابن الانسان ماض كما هو مكتوب عنه. ولكن ويل لذلك الرجل الذي به يسلم ابن الانسان. كان خيرا لذلك الرجل لو لم يولد"));
arasuperobject.biblemap.put("b41_14_22", new arasuperobject(41,14,22,"وفيما هم يأكلون اخذ يسوع خبزا وبارك وكسر واعطاهم وقال خذوا كلوا هذا هو جسدي"));
arasuperobject.biblemap.put("b41_14_23", new arasuperobject(41,14,23,"ثم اخذ الكاس وشكر واعطاهم فشربوا منها كلهم"));
arasuperobject.biblemap.put("b41_14_24", new arasuperobject(41,14,24,"وقال لهم هذا هو دمي الذي للعهد الجديد الذي يسفك من اجل كثيرين"));
arasuperobject.biblemap.put("b41_14_25", new arasuperobject(41,14,25,"الحق اقول لكم اني لا اشرب بعد من نتاج الكرمة الى ذلك اليوم حينما اشربه جديدا في ملكوت الله"));
arasuperobject.biblemap.put("b41_14_26", new arasuperobject(41,14,26,"ثم سبّحوا وخرجوا الى جبل الزيتون"));
arasuperobject.biblemap.put("b41_14_27", new arasuperobject(41,14,27,"وقال لهم يسوع ان كلكم تشكون فيّ في هذه الليلة. لانه مكتوب اني اضرب الراعي فتتبدد الخراف"));
arasuperobject.biblemap.put("b41_14_28", new arasuperobject(41,14,28,"ولكن بعد قيامي اسبقكم الى الجليل"));
arasuperobject.biblemap.put("b41_14_29", new arasuperobject(41,14,29,"فقال له بطرس وان شك الجميع فانا لا اشك"));
arasuperobject.biblemap.put("b41_14_30", new arasuperobject(41,14,30,"فقال له يسوع الحق اقول لك انك اليوم في هذه الليلة قبل ان يصيح الديك مرتين تنكرني ثلاث مرات"));
arasuperobject.biblemap.put("b41_14_31", new arasuperobject(41,14,31,"فقال باكثر تشديد ولو اضطررت ان اموت معك لا انكرك. وهكذا قال ايضا الجميع"));
arasuperobject.biblemap.put("b41_14_32", new arasuperobject(41,14,32,"وجاءوا الى ضيعة اسمها جثسيماني فقال لتلاميذه اجلسوا ههنا حتى اصلّي"));
arasuperobject.biblemap.put("b41_14_33", new arasuperobject(41,14,33,"ثم اخذ معه بطرس ويعقوب ويوحنا وابتدأ يدهش ويكتئب"));
arasuperobject.biblemap.put("b41_14_34", new arasuperobject(41,14,34,"فقال لهم نفسي حزينة جدا حتى الموت. امكثوا هنا واسهروا"));
arasuperobject.biblemap.put("b41_14_35", new arasuperobject(41,14,35,"ثم تقدم قليلا وخرّ على الارض وكان يصلّي لكي تعبر عنه الساعة ان امكن"));
arasuperobject.biblemap.put("b41_14_36", new arasuperobject(41,14,36,"وقال يا ابا الآب كل شيء مستطاع لك. فاجز عني هذه الكاس. ولكن ليكن لا ما اريد انا بل ما تريد انت"));
arasuperobject.biblemap.put("b41_14_37", new arasuperobject(41,14,37,"ثم جاء ووجدهم نياما فقال لبطرس يا سمعان انت نائم. أما قدرت ان تسهر ساعة واحدة"));
arasuperobject.biblemap.put("b41_14_38", new arasuperobject(41,14,38,"اسهروا وصلّوا لئلا تدخلوا في تجربة. اما الروح فنشيط واما الجسد فضعيف"));
arasuperobject.biblemap.put("b41_14_39", new arasuperobject(41,14,39,"ومضى ايضا وصلّى قائلا ذلك الكلام بعينه"));
arasuperobject.biblemap.put("b41_14_40", new arasuperobject(41,14,40,"ثم رجع ووجدهم ايضا نياما اذ كانت اعينهم ثقيلة فلم يعلموا بماذا يجيبونه"));
arasuperobject.biblemap.put("b41_14_41", new arasuperobject(41,14,41,"ثم جاء ثالثة وقال لهم ناموا الآن واستريحوا. يكفي. قد أتت الساعة. هوذا ابن الانسان يسلم الى ايدي الخطاة"));
arasuperobject.biblemap.put("b41_14_42", new arasuperobject(41,14,42,"قوموا لنذهب. هوذا الذي يسلمني قد اقترب"));
arasuperobject.biblemap.put("b41_14_43", new arasuperobject(41,14,43,"وللوقت فيما هو يتكلم اقبل يهوذا واحد من الاثني عشر ومعه جمع كثير بسيوف وعصي من عند رؤساء الكهنة والكتبة والشيوخ"));
arasuperobject.biblemap.put("b41_14_44", new arasuperobject(41,14,44,"وكان مسلمه قد اعطاهم علامة قائلا الذي اقبّله هو هو. امسكوه وامضوا به بحرص"));
arasuperobject.biblemap.put("b41_14_45", new arasuperobject(41,14,45,"فجاء للوقت وتقدم اليه قائلا يا سيدي يا سيدي. وقبله"));
arasuperobject.biblemap.put("b41_14_46", new arasuperobject(41,14,46,"فألقوا ايديهم عليه وامسكوه"));
arasuperobject.biblemap.put("b41_14_47", new arasuperobject(41,14,47,"فاستل واحد من الحاضرين السيف وضرب عبد رئيس الكهنة فقطع اذنه"));
arasuperobject.biblemap.put("b41_14_48", new arasuperobject(41,14,48,"فاجاب يسوع وقال لهم كانه على لص خرجتم بسيوف وعصي لتأخذوني"));
arasuperobject.biblemap.put("b41_14_49", new arasuperobject(41,14,49,"كل يوم كنت معكم في الهيكل اعلّم ولم تمسكوني. ولكن لكي تكمل الكتب"));
arasuperobject.biblemap.put("b41_14_50", new arasuperobject(41,14,50,"فتركه الجميع وهربوا"));
arasuperobject.biblemap.put("b41_14_51", new arasuperobject(41,14,51,"وتبعه شاب لابسا ازارا على عريه فامسكه الشبان"));
arasuperobject.biblemap.put("b41_14_52", new arasuperobject(41,14,52,"فترك الازار وهرب منهم عريانا"));
arasuperobject.biblemap.put("b41_14_53", new arasuperobject(41,14,53,"فمضوا بيسوع الى رئيس الكهنة فاجتمع معه جميع رؤساء الكهنة والشيوخ والكتبة"));
arasuperobject.biblemap.put("b41_14_54", new arasuperobject(41,14,54,"وكان بطرس قد تبعه من بعيد الى داخل دار رئيس الكهنة وكان جالسا بين الخدام يستدفئ عند النار"));
arasuperobject.biblemap.put("b41_14_55", new arasuperobject(41,14,55,"وكان رؤساء الكهنة والمجمع كله يطلبون شهادة على يسوع ليقتلوه فلم يجدوا"));
arasuperobject.biblemap.put("b41_14_56", new arasuperobject(41,14,56,"لان كثيرين شهدوا عليه زورا ولم تتفق شهاداتهم"));
arasuperobject.biblemap.put("b41_14_57", new arasuperobject(41,14,57,"ثم قام قوم وشهدوا عليه زورا قائلين"));
arasuperobject.biblemap.put("b41_14_58", new arasuperobject(41,14,58,"نحن سمعناه يقول اني انقض هذا الهيكل المصنوع بالايادي وفي ثلاثة ايام ابني آخر غير مصنوع باياد"));
arasuperobject.biblemap.put("b41_14_59", new arasuperobject(41,14,59,"ولا بهذا كانت شهادتهم تتفق"));
arasuperobject.biblemap.put("b41_14_60", new arasuperobject(41,14,60,"فقام رئيس الكهنة في الوسط وسأل يسوع قائلا أما تجيب بشيء. ماذا يشهد به هؤلاء عليك"));
arasuperobject.biblemap.put("b41_14_61", new arasuperobject(41,14,61,"اما هو فكان ساكتا ولم يجب بشيء. فسأله رئيس الكهنة ايضا وقال له أانت المسيح ابن المبارك"));
arasuperobject.biblemap.put("b41_14_62", new arasuperobject(41,14,62,"فقال يسوع انا هو. وسوف تبصرون ابن الانسان جالسا عن يمين القوة وآتيا في سحاب السماء"));
arasuperobject.biblemap.put("b41_14_63", new arasuperobject(41,14,63,"فمزّق رئيس الكهنة ثيابه وقال ما حاجتنا بعد الى شهود"));
arasuperobject.biblemap.put("b41_14_64", new arasuperobject(41,14,64,"قد سمعتم التجاديف. ما رايكم. فالجميع حكموا عليه انه مستوجب الموت"));
arasuperobject.biblemap.put("b41_14_65", new arasuperobject(41,14,65,"فابتدأ قوم يبصقون عليه ويغطون وجهه ويلكمونه ويقولون له تنبأ. وكان الخدام يلطمونه"));
arasuperobject.biblemap.put("b41_14_66", new arasuperobject(41,14,66,"وبينما كان بطرس في الدار اسفل جاءت احدى جواري رئيس الكهنة"));
arasuperobject.biblemap.put("b41_14_67", new arasuperobject(41,14,67,"فلما رأت بطرس يستدفئ نظرت اليه وقالت وانت كنت مع يسوع الناصري"));
arasuperobject.biblemap.put("b41_14_68", new arasuperobject(41,14,68,"فانكر قائلا لست ادري ولا افهم ما تقولين. وخرج خارجا الى الدهليز. فصاح الديك"));
arasuperobject.biblemap.put("b41_14_69", new arasuperobject(41,14,69,"فرأته الجارية ايضا وابتدأت تقول للحاضرين ان هذا منهم"));
arasuperobject.biblemap.put("b41_14_70", new arasuperobject(41,14,70,"فانكر ايضا. وبعد قليل ايضا قال الحاضرون لبطرس حقا انت منهم لانك جليلي ايضا ولغتك تشبه لغتهم"));
arasuperobject.biblemap.put("b41_14_71", new arasuperobject(41,14,71,"فابتدأ يلعن ويحلف اني لا اعرف هذا الرجل الذي تقولون عنه"));
arasuperobject.biblemap.put("b41_14_72", new arasuperobject(41,14,72,"وصاح الديك ثانية فتذكر بطرس القول الذي قاله له يسوع انك قبل ان يصيح الديك مرتين تنكرني ثلاث مرات. فلما تفكر به بكى"));
arasuperobject.biblemap.put("b41_15_1", new arasuperobject(41,15,1,"وللوقت في الصباح تشاور رؤساء الكهنة والشيوخ والكتبة والمجمع كله فأوثقوا يسوع ومضوا به واسلموه الى بيلاطس"));
arasuperobject.biblemap.put("b41_15_2", new arasuperobject(41,15,2,"فسأله بيلاطس انت ملك اليهود. فاجاب وقال له انت تقول"));
arasuperobject.biblemap.put("b41_15_3", new arasuperobject(41,15,3,"وكان رؤساء الكهنة يشتكون عليه كثيرا"));
arasuperobject.biblemap.put("b41_15_4", new arasuperobject(41,15,4,"فسأله بيلاطس ايضا قائلا أما تجيب بشيء. انظر كم يشهدون عليك"));
arasuperobject.biblemap.put("b41_15_5", new arasuperobject(41,15,5,"فلم يجب يسوع ايضا بشيء حتى تعجب بيلاطس"));
arasuperobject.biblemap.put("b41_15_6", new arasuperobject(41,15,6,"وكان يطلق لهم في كل عيد اسيرا واحدا من طلبوه"));
arasuperobject.biblemap.put("b41_15_7", new arasuperobject(41,15,7,"وكان المسمى باراباس موثقا مع رفقائه في الفتنة الذين في الفتنة فعلوا قتلا"));
arasuperobject.biblemap.put("b41_15_8", new arasuperobject(41,15,8,"فصرخ الجمع وابتدأوا يطلبون ان يفعل كما كان دائما يفعل لهم"));
arasuperobject.biblemap.put("b41_15_9", new arasuperobject(41,15,9,"فاجابهم بيلاطس قائلا أتريدون ان اطلق لكم ملك اليهود"));
arasuperobject.biblemap.put("b41_15_10", new arasuperobject(41,15,10,"لانه عرف ان رؤساء الكهنة كانوا قد اسلموه حسدا"));
arasuperobject.biblemap.put("b41_15_11", new arasuperobject(41,15,11,"فهيج رؤساء الكهنة الجمع لكي يطلق لهم بالحري باراباس"));
arasuperobject.biblemap.put("b41_15_12", new arasuperobject(41,15,12,"فاجاب بيلاطس ايضا وقال لهم فماذا تريدون ان افعل بالذي تدعونه ملك اليهود"));
arasuperobject.biblemap.put("b41_15_13", new arasuperobject(41,15,13,"فصرخوا ايضا اصلبه"));
arasuperobject.biblemap.put("b41_15_14", new arasuperobject(41,15,14,"فقال لهم بيلاطس واي شر عمل. فازدادوا جدا صراخا اصلبه"));
arasuperobject.biblemap.put("b41_15_15", new arasuperobject(41,15,15,"فبيلاطس اذ كان يريد ان يعمل للجمع ما يرضيهم اطلق لهم باراباس واسلم يسوع بعدما جلده ليصلب"));
arasuperobject.biblemap.put("b41_15_16", new arasuperobject(41,15,16,"فمضى به العسكر الى داخل الدار التي هي دار الولاية وجمعوا كل الكتيبة"));
arasuperobject.biblemap.put("b41_15_17", new arasuperobject(41,15,17,"وألبسوه ارجوانا وضفروا اكليلا من شوك ووضعوه عليه"));
arasuperobject.biblemap.put("b41_15_18", new arasuperobject(41,15,18,"وابتدأوا يسلمون عليه قائلين السلام يا ملك اليهود"));
arasuperobject.biblemap.put("b41_15_19", new arasuperobject(41,15,19,"وكانوا يضربونه على راسه بقصبة ويبصقون عليه ثم يسجدون له جاثين على ركبهم"));
arasuperobject.biblemap.put("b41_15_20", new arasuperobject(41,15,20,"وبعدما استهزأوا به نزعوا عنه الارجوان والبسوه ثيابه ثم خرجوا به ليصلبوه"));
arasuperobject.biblemap.put("b41_15_21", new arasuperobject(41,15,21,"فسخّروا رجلا مجتازا كان آتيا من الحقل وهو سمعان القيرواني ابو ألكسندرس وروفس ليحمل صليبه"));
arasuperobject.biblemap.put("b41_15_22", new arasuperobject(41,15,22,"وجاءوا به الى موضع جلجثة الذي تفسيره موضع جمجمة"));
arasuperobject.biblemap.put("b41_15_23", new arasuperobject(41,15,23,"واعطوه خمرا ممزوجة بمرّ ليشرب فلم يقبل"));
arasuperobject.biblemap.put("b41_15_24", new arasuperobject(41,15,24,"ولما صلبوه اقتسموا ثيابه مقترعين عليها ماذا يأخذ كل واحد"));
arasuperobject.biblemap.put("b41_15_25", new arasuperobject(41,15,25,"وكانت الساعة الثالثة فصلبوه"));
arasuperobject.biblemap.put("b41_15_26", new arasuperobject(41,15,26,"وكان عنوان علّته مكتوبا ملك اليهود"));
arasuperobject.biblemap.put("b41_15_27", new arasuperobject(41,15,27,"وصلبوا معه لصين واحدا عن يمينه وآخر عن يساره"));
arasuperobject.biblemap.put("b41_15_28", new arasuperobject(41,15,28,"فتم الكتاب القائل واحصي مع اثمة"));
arasuperobject.biblemap.put("b41_15_29", new arasuperobject(41,15,29,"وكان المجتازون يجدفون عليه وهم يهزون رؤوسهم قائلين آه يا ناقض الهيكل وبانيه في ثلاثة ايام"));
arasuperobject.biblemap.put("b41_15_30", new arasuperobject(41,15,30,"خلّص نفسك وانزل عن الصليب"));
arasuperobject.biblemap.put("b41_15_31", new arasuperobject(41,15,31,"وكذلك رؤساء الكهنة وهم مستهزئون فيما بينهم مع الكتبة قالوا خلّص آخرين واما نفسه فما يقدر ان يخلّصها"));
arasuperobject.biblemap.put("b41_15_32", new arasuperobject(41,15,32,"لينزل الآن المسيح ملك اسرائيل عن الصليب لنرى ونؤمن. واللذان صلبا معه كانا يعيّرانه"));
arasuperobject.biblemap.put("b41_15_33", new arasuperobject(41,15,33,"ولما كانت الساعة السادسة كانت ظلمة على الارض كلها الى الساعة التاسعة"));
arasuperobject.biblemap.put("b41_15_34", new arasuperobject(41,15,34,"وفي الساعة التاسعة صرخ يسوع بصوت عظيم قائلا ألوي ألوي لما شبقتني. الذي تفسيره الهي الهي لماذا تركتني"));
arasuperobject.biblemap.put("b41_15_35", new arasuperobject(41,15,35,"فقال قوم من الحاضرين لما سمعوا هوذا ينادي ايليا"));
arasuperobject.biblemap.put("b41_15_36", new arasuperobject(41,15,36,"فركض واحد وملأ اسفنجة خلا وجعلها على قصبة وسقاه قائلا اتركوا. لنر هل يأتي ايليا لينزله"));
arasuperobject.biblemap.put("b41_15_37", new arasuperobject(41,15,37,"فصرخ يسوع بصوت عظيم واسلم الروح"));
arasuperobject.biblemap.put("b41_15_38", new arasuperobject(41,15,38,"وانشق حجاب الهيكل الى اثنين من فوق الى اسفل"));
arasuperobject.biblemap.put("b41_15_39", new arasuperobject(41,15,39,"ولما رأى قائد المئة الواقف مقابله انه صرخ هكذا واسلم الروح قال حقا كان هذا الانسان ابن الله"));
arasuperobject.biblemap.put("b41_15_40", new arasuperobject(41,15,40,"وكانت ايضا نساء ينظرن من بعيد بينهنّ مريم المجدلية ومريم ام يعقوب الصغير ويوسي وسالومة"));
arasuperobject.biblemap.put("b41_15_41", new arasuperobject(41,15,41,"اللواتي ايضا تبعنه وخدمنه حين كان في الجليل. وأخر كثيرات اللواتي صعدن معه الى اورشليم"));
arasuperobject.biblemap.put("b41_15_42", new arasuperobject(41,15,42,"ولما كان المساء اذ كان الاستعداد. اي ما قبل السبت"));
arasuperobject.biblemap.put("b41_15_43", new arasuperobject(41,15,43,"جاء يوسف الذي من الرامة مشير شريف وكان هو ايضا منتظرا ملكوت الله فتجاسر ودخل الى بيلاطس وطلب جسد يسوع"));
arasuperobject.biblemap.put("b41_15_44", new arasuperobject(41,15,44,"فتعجب بيلاطس انه مات كذا سريعا فدعا قائد المئة وسأله هل له زمان قد مات"));
arasuperobject.biblemap.put("b41_15_45", new arasuperobject(41,15,45,"ولما عرف من قائد المئة وهب الجسد ليوسف"));
arasuperobject.biblemap.put("b41_15_46", new arasuperobject(41,15,46,"فاشترى كتانا فانزله وكفنه بالكتان ووضعه في قبر كان منحوتا في صخرة ودحرج حجرا على باب القبر"));
arasuperobject.biblemap.put("b41_15_47", new arasuperobject(41,15,47,"وكانت مريم المجدلية ومريم ام يوسي تنظران اين وضع"));
arasuperobject.biblemap.put("b41_16_1", new arasuperobject(41,16,1,"وبعد ما مضى السبت اشترت مريم المجدلية ومريم ام يعقوب وسالومة حنوطا ليأتين ويدهنّه"));
arasuperobject.biblemap.put("b41_16_2", new arasuperobject(41,16,2,"وباكرا جدا في اول الاسبوع أتين الى القبر اذ طلعت الشمس"));
arasuperobject.biblemap.put("b41_16_3", new arasuperobject(41,16,3,"وكنّ يقلن فيما بينهنّ من يدحرج لنا الحجر عن باب القبر"));
arasuperobject.biblemap.put("b41_16_4", new arasuperobject(41,16,4,"فتطلعن ورأين ان الحجر قد دحرج. لانه كان عظيما جدا"));
arasuperobject.biblemap.put("b41_16_5", new arasuperobject(41,16,5,"ولما دخلن القبر رأين شابا جالسا عن اليمين لابسا حلة بيضاء فاندهشن"));
arasuperobject.biblemap.put("b41_16_6", new arasuperobject(41,16,6,"فقال لهنّ لا تندهشن. انتنّ تطلبن يسوع الناصري المصلوب. قد قام. ليس هو ههنا. هوذا الموضع الذي وضعوه فيه"));
arasuperobject.biblemap.put("b41_16_7", new arasuperobject(41,16,7,"لكن اذهبن وقلن لتلاميذه ولبطرس انه يسبقكم الى الجليل. هناك ترونه كما قال لكم"));
arasuperobject.biblemap.put("b41_16_8", new arasuperobject(41,16,8,"فخرجن سريعا وهربن من القبر لان الرعدة والحيرة اخذتاهنّ ولم يقلن لاحد شيئا لانهنّ كنّ خائفات"));
arasuperobject.biblemap.put("b41_16_9", new arasuperobject(41,16,9,"وبعد ما قام باكرا في اول الاسبوع ظهر اولا لمريم المجدلية التي كان قد اخرج منها سبعة شياطين"));
arasuperobject.biblemap.put("b41_16_10", new arasuperobject(41,16,10,"فذهبت هذه واخبرت الذين كانوا معه وهم ينوحون ويبكون"));
arasuperobject.biblemap.put("b41_16_11", new arasuperobject(41,16,11,"فلما سمع اولئك انه حيّ وقد نظرته لم يصدقوا"));
arasuperobject.biblemap.put("b41_16_12", new arasuperobject(41,16,12,"وبعد ذلك ظهر بهيئة اخرى لاثنين منهم وهما يمشيان منطلقين الى البرية"));
arasuperobject.biblemap.put("b41_16_13", new arasuperobject(41,16,13,"وذهب هذان واخبرا الباقين فلم يصدقوا ولا هذين"));
arasuperobject.biblemap.put("b41_16_14", new arasuperobject(41,16,14,"اخيرا ظهر للاحد عشر وهم متكئون ووبخ عدم ايمانهم وقساوة قلوبهم لانهم لم يصدقوا الذين نظروه قد قام"));
arasuperobject.biblemap.put("b41_16_15", new arasuperobject(41,16,15,"وقال لهم اذهبوا الى العالم اجمع واكرزوا بالانجيل للخليقة كلها"));
arasuperobject.biblemap.put("b41_16_16", new arasuperobject(41,16,16,"من آمن واعتمد خلص. ومن لم يؤمن يدن"));
arasuperobject.biblemap.put("b41_16_17", new arasuperobject(41,16,17,"وهذه الآيات تتبع المؤمنين. يخرجون الشياطين باسمي ويتكلمون بألسنة جديدة"));
arasuperobject.biblemap.put("b41_16_18", new arasuperobject(41,16,18,"يحملون حيّات وان شربوا شيئا مميتا لا يضرهم ويضعون ايديهم على المرضى فيبرأون"));
arasuperobject.biblemap.put("b41_16_19", new arasuperobject(41,16,19,"ثم ان الرب بعدما كلمهم ارتفع الى السماء وجلس عن يمين الله"));
arasuperobject.biblemap.put("b41_16_20", new arasuperobject(41,16,20,"واما هم فخرجوا وكرزوا في كل مكان والرب يعمل معهم ويثبت الكلام بالآيات التابعة. آمين"));

