arasuperobject.biblemap.put("b42_2_35", new arasuperobject(42,2,35,"وانت ايضا يجوز في نفسك سيف. لتعلن افكار من قلوب كثيرة"));
arasuperobject.biblemap.put("b42_2_36", new arasuperobject(42,2,36,"وكانت نبية حنة بنت فنوئيل من سبط اشير. وهي متقدمة في ايام كثيرة. قد عاشت مع زوج سبع سنين بعد بكوريتها"));
arasuperobject.biblemap.put("b42_2_37", new arasuperobject(42,2,37,"وهي ارملة نحو اربعة وثمانين سنة لا تفارق الهيكل عابدة باصوام وطلبات ليلا ونهارا"));
arasuperobject.biblemap.put("b42_2_38", new arasuperobject(42,2,38,"فهي في تلك الساعة وقفت تسبح الرب وتكلمت عنه مع جميع المنتظرين فداء في اورشليم"));
arasuperobject.biblemap.put("b42_2_39", new arasuperobject(42,2,39,"ولما اكملوا كل شيء حسب ناموس الرب رجعوا الى الجليل الى مدينتهم الناصرة"));
arasuperobject.biblemap.put("b42_2_40", new arasuperobject(42,2,40,"وكان الصبي ينمو ويتقوى بالروح ممتلئا حكمة وكانت نعمة الله عليه"));
arasuperobject.biblemap.put("b42_2_41", new arasuperobject(42,2,41,"وكان ابواه يذهبان كل سنة الى اورشليم في عيد الفصح"));
arasuperobject.biblemap.put("b42_2_42", new arasuperobject(42,2,42,"ولما كانت له اثنتا عشرة سنة صعدوا الى اورشليم كعادة العيد"));
arasuperobject.biblemap.put("b42_2_43", new arasuperobject(42,2,43,"وبعدما اكملوا الايام بقي عند رجوعهما الصبي يسوع في اورشليم ويوسف وامه لم يعلما"));
arasuperobject.biblemap.put("b42_2_44", new arasuperobject(42,2,44,"واذ ظناه بين الرفقة ذهبا مسيرة يوم وكانا يطلبانه بين الاقرباء والمعارف"));
arasuperobject.biblemap.put("b42_2_45", new arasuperobject(42,2,45,"ولما لم يجداه رجعا الى اورشليم يطلبانه"));
arasuperobject.biblemap.put("b42_2_46", new arasuperobject(42,2,46,"وبعد ثلاثة ايام وجداه في الهيكل جالسا في وسط المعلمين يسمعهم ويسألهم"));
arasuperobject.biblemap.put("b42_2_47", new arasuperobject(42,2,47,"وكل الذين سمعوه بهتوا من فهمه واجوبته"));
arasuperobject.biblemap.put("b42_2_48", new arasuperobject(42,2,48,"فلما ابصراه اندهشا. وقالت له امه يا بنيّ لماذا فعلت بنا هكذا. هوذا ابوك وانا كنا نطلبك معذبين"));
arasuperobject.biblemap.put("b42_2_49", new arasuperobject(42,2,49,"فقال لهما لماذا كنتما تطلبانني ألم تعلما انه ينبغي ان اكون فيما لأبي"));
arasuperobject.biblemap.put("b42_2_50", new arasuperobject(42,2,50,"فلم يفهما الكلام الذي قاله لهما"));
arasuperobject.biblemap.put("b42_2_51", new arasuperobject(42,2,51,"ثم نزل معهما وجاء الى الناصرة وكان خاضعا لهما. وكانت امه تحفظ جميع هذه الأمور في قلبها"));
arasuperobject.biblemap.put("b42_2_52", new arasuperobject(42,2,52,"واما يسوع فكان يتقدم في الحكمة والقامة والنعمة عند الله والناس"));
arasuperobject.biblemap.put("b42_3_1", new arasuperobject(42,3,1,"وفي السنة الخامسة عشر من سلطنة طيباريوس قيصر اذ كان بيلاطس البنطي واليا على اليهودية وهيرودس رئيس ربع على الجليل وفيلبس اخوه رئيس ربع على ايطورية وكورة تراخونيتس وليسانيوس رئيس ربع على الابلية"));
arasuperobject.biblemap.put("b42_3_2", new arasuperobject(42,3,2,"في ايام رئيس الكهنة حنان وقيافا كانت كلمة الله على يوحنا بن زكريا في البرية"));
arasuperobject.biblemap.put("b42_3_3", new arasuperobject(42,3,3,"فجاء الى جميع الكورة المحيطة بالاردن يكرز بمعمودية التوبة لمغفرة الخطايا"));
arasuperobject.biblemap.put("b42_3_4", new arasuperobject(42,3,4,"كما هو مكتوب في سفر اقوال اشعياء النبي القائل صوت صارخ في البرية أعدّوا طريق الرب اصنعوا سبله مستقيمة"));
arasuperobject.biblemap.put("b42_3_5", new arasuperobject(42,3,5,"كل واد يمتلئ وكل جبل واكمة ينخفض وتصير المعوجات مستقيمة والشعاب طرقا سهلة"));
arasuperobject.biblemap.put("b42_3_6", new arasuperobject(42,3,6,"ويبصر كل بشر خلاص الله"));
arasuperobject.biblemap.put("b42_3_7", new arasuperobject(42,3,7,"وكان يقول للجموع الذين خرجوا ليعتمدوا منه يا اولاد الافاعي من اراكم ان تهربوا من الغضب الآتي"));
arasuperobject.biblemap.put("b42_3_8", new arasuperobject(42,3,8,"فاصنعوا اثمارا تليق بالتوبة. ولا تبتدئوا تقولون في انفسكم لنا ابراهيم ابا. لاني اقول لكم ان الله قادر ان يقيم من هذه الحجارة اولادا لابراهيم"));
arasuperobject.biblemap.put("b42_3_9", new arasuperobject(42,3,9,"والآن قد وضعت الفاس على اصل الشجر. فكل شجرة لا تصنع ثمرا جيدا تقطع وتلقى في النار"));
arasuperobject.biblemap.put("b42_3_10", new arasuperobject(42,3,10,"وسأله الجموع قائلين فماذا نفعل"));
arasuperobject.biblemap.put("b42_3_11", new arasuperobject(42,3,11,"فاجاب وقال لهم من له ثوبان فليعط من ليس له ومن له طعام فليفعل هكذا"));
arasuperobject.biblemap.put("b42_3_12", new arasuperobject(42,3,12,"وجاء عشارون ايضا ليعتمدوا فقالوا له يا معلّم ماذا نفعل"));
arasuperobject.biblemap.put("b42_3_13", new arasuperobject(42,3,13,"فقال لهم لا تستوفوا اكثر مما فرض لكم"));
arasuperobject.biblemap.put("b42_3_14", new arasuperobject(42,3,14,"وسأله جنديون ايضا قائلين وماذا نفعل نحن. فقال لهم لا تظلموا احدا ولا تشوا باحد واكتفوا بعلائفكم"));
arasuperobject.biblemap.put("b42_3_15", new arasuperobject(42,3,15,"واذ كان الشعب ينتظر والجميع يفكرون في قلوبهم عن يوحنا لعله المسيح"));
arasuperobject.biblemap.put("b42_3_16", new arasuperobject(42,3,16,"اجاب يوحنا الجميع قائلا انا اعمدكم بماء ولكن يأتي من هو اقوى مني الذي لست اهلا ان احل سيور حذائه. هو سيعمدكم بالروح القدس ونار"));
arasuperobject.biblemap.put("b42_3_17", new arasuperobject(42,3,17,"الذي رفشه في يده وسينقي بيدره ويجمع القمح الى مخزنه. واما التبن فيحرقه بنار لا تطفأ"));
arasuperobject.biblemap.put("b42_3_18", new arasuperobject(42,3,18,"وباشياء أخر كثيرة كان يعظ الشعب ويبشرهم"));
arasuperobject.biblemap.put("b42_3_19", new arasuperobject(42,3,19,"اما هيرودس رئيس الربع فاذ توبخ منه لسبب هيروديا امرأة فيلبّس اخيه ولسبب جميع الشرور التي كان هيرودس يفعلها"));
arasuperobject.biblemap.put("b42_3_20", new arasuperobject(42,3,20,"زاد هذا ايضا على الجميع انه حبس يوحنا في السجن"));
arasuperobject.biblemap.put("b42_3_21", new arasuperobject(42,3,21,"ولما اعتمد جميع الشعب اعتمد يسوع ايضا. واذ كان يصلّي انفتحت السماء"));
arasuperobject.biblemap.put("b42_3_22", new arasuperobject(42,3,22,"ونزل عليه الروح القدس بهيئة جسمية مثل حمامة وكان صوت من السماء قائلا انت ابني الحبيب بك سررت"));
arasuperobject.biblemap.put("b42_3_23", new arasuperobject(42,3,23,"ولما ابتدأ يسوع كان له نحو ثلاثين سنة وهو على ما كان يظن ابن يوسف بن هالي"));
arasuperobject.biblemap.put("b42_3_24", new arasuperobject(42,3,24,"بن متثات بن لاوي بن ملكي بن ينّا بن يوسف"));
arasuperobject.biblemap.put("b42_3_25", new arasuperobject(42,3,25,"بن متاثيا بن عاموص بن ناحوم بن حسلي بن نجّاي"));
arasuperobject.biblemap.put("b42_3_26", new arasuperobject(42,3,26,"بن مآث بن متاثيا بن شمعي بن يوسف بن يهوذا"));
arasuperobject.biblemap.put("b42_3_27", new arasuperobject(42,3,27,"بن يوحنا بن ريسا بن زربابل بن شألتيئيل بن نيري"));
