arasuperobject.biblemap.put("b42_3_37", new arasuperobject(42,3,37,"بن متوشالح بن اخنوخ بن يارد بن مهللئيل بن قينان"));
arasuperobject.biblemap.put("b42_3_38", new arasuperobject(42,3,38,"بن انوش بن شيت بن آدم ابن الله"));
arasuperobject.biblemap.put("b42_4_1", new arasuperobject(42,4,1,"اما يسوع فرجع من الاردن ممتلئا من الروح القدس وكان يقتاد بالروح في البرية"));
arasuperobject.biblemap.put("b42_4_2", new arasuperobject(42,4,2,"اربعين يوما يجرّب من ابليس. ولم ياكل شيئا في تلك الايام ولما تمت جاع اخيرا"));
arasuperobject.biblemap.put("b42_4_3", new arasuperobject(42,4,3,"وقال له ابليس ان كنت ابن الله فقل لهذا الحجر ان يصير خبزا"));
arasuperobject.biblemap.put("b42_4_4", new arasuperobject(42,4,4,"فاجابه يسوع قائلا مكتوب ان ليس بالخبز وحده يحيا الانسان بل بكل كلمة من الله"));
arasuperobject.biblemap.put("b42_4_5", new arasuperobject(42,4,5,"ثم اصعده ابليس الى جبل عال وأراه جميع ممالك المسكونة في لحظة من الزمان"));
arasuperobject.biblemap.put("b42_4_6", new arasuperobject(42,4,6,"وقال له ابليس لك اعطي هذا السلطان كله ومجدهنّ لانه اليّ قد دفع وانا اعطيه لمن اريد"));
arasuperobject.biblemap.put("b42_4_7", new arasuperobject(42,4,7,"فان سجدت امامي يكون لك الجميع"));
arasuperobject.biblemap.put("b42_4_8", new arasuperobject(42,4,8,"فاجابه يسوع وقال اذهب يا شيطان انه مكتوب للرب الهك تسجد واياه وحده تعبد"));
arasuperobject.biblemap.put("b42_4_9", new arasuperobject(42,4,9,"ثم جاء به الى اورشليم واقامه على جناح الهيكل وقال له ان كنت ابن الله فاطرح نفسك من هنا الى اسفل"));
arasuperobject.biblemap.put("b42_4_10", new arasuperobject(42,4,10,"لانه مكتوب انه يوصي ملائكته بك لكي يحفظوك"));
arasuperobject.biblemap.put("b42_4_11", new arasuperobject(42,4,11,"وانهم على اياديهم يحملونك لكي لا تصدم بحجر رجلك"));
arasuperobject.biblemap.put("b42_4_12", new arasuperobject(42,4,12,"فاجاب يسوع وقال له انه قيل لا تجرب الرب الهك"));
arasuperobject.biblemap.put("b42_4_13", new arasuperobject(42,4,13,"ولما اكمل ابليس كل تجربة فارقه الى حين"));
arasuperobject.biblemap.put("b42_4_14", new arasuperobject(42,4,14,"ورجع يسوع بقوة الروح الى الجليل وخرج خبر عنه في جميع الكورة المحيطة"));
arasuperobject.biblemap.put("b42_4_15", new arasuperobject(42,4,15,"وكان يعلّم في مجامعهم ممجدا من الجميع"));
arasuperobject.biblemap.put("b42_4_16", new arasuperobject(42,4,16,"وجاء الى الناصرة حيث كان قد تربى. ودخل المجمع حسب عادته يوم السبت وقام ليقرأ"));
arasuperobject.biblemap.put("b42_4_17", new arasuperobject(42,4,17,"فدفع اليه سفر اشعياء النبي. ولما فتح السفر وجد الموضع الذي كان مكتوبا فيه"));
arasuperobject.biblemap.put("b42_4_18", new arasuperobject(42,4,18,"روح الرب عليّ لانه مسحني لابشر المساكين ارسلني لاشفي المنكسري القلوب لانادي للمأسورين بالاطلاق وللعمي بالبصر وارسل المنسحقين في الحرية"));
arasuperobject.biblemap.put("b42_4_19", new arasuperobject(42,4,19,"واكرز بسنة الرب المقبولة"));
arasuperobject.biblemap.put("b42_4_20", new arasuperobject(42,4,20,"ثم طوى السفر وسلمه الى الخادم وجلس. وجميع الذين في المجمع كانت عيونهم شاخصة اليه"));
arasuperobject.biblemap.put("b42_4_21", new arasuperobject(42,4,21,"فابتدأ يقول لهم انه اليوم قد تم هذا المكتوب في مسامعكم"));
arasuperobject.biblemap.put("b42_4_22", new arasuperobject(42,4,22,"وكان الجميع يشهدون له ويتعجبون من كلمات النعمة الخارجة من فمه ويقولون أليس هذا ابن يوسف"));
arasuperobject.biblemap.put("b42_4_23", new arasuperobject(42,4,23,"فقال لهم. على كل حال تقولون لي هذا المثل ايها الطبيب اشفي نفسك. كم سمعنا انه جرى في كفر ناحوم فافعل ذلك هنا ايضا في وطنك"));
arasuperobject.biblemap.put("b42_4_24", new arasuperobject(42,4,24,"وقال الحق اقول لكم انه ليس نبي مقبولا في وطنه"));
arasuperobject.biblemap.put("b42_4_25", new arasuperobject(42,4,25,"وبالحق اقول لكم ان ارامل كثيرة كنّ في اسرائيل في ايام ايليا حين أغلقت السماء مدة ثلاث سنين وستة اشهر لما كان جوع عظيم في الارض كلها"));
arasuperobject.biblemap.put("b42_4_26", new arasuperobject(42,4,26,"ولم يرسل ايليا الى واحدة منها الا الى امرأة ارملة الى صرفة صيدا"));
arasuperobject.biblemap.put("b42_4_27", new arasuperobject(42,4,27,"وبرص كثيرون كانوا في اسرائيل في زمان اليشع النبي ولم يطهر واحد منهم الا نعمان السرياني"));
arasuperobject.biblemap.put("b42_4_28", new arasuperobject(42,4,28,"فامتلأ غضبا جميع الذين في المجمع حين سمعوا هذا"));
arasuperobject.biblemap.put("b42_4_29", new arasuperobject(42,4,29,"فقاموا واخرجوه خارج المدينة وجاءوا به الى حافة الجبل الذي كانت مدينتهم مبنية عليه حتى يطرحوه الى اسفل"));
arasuperobject.biblemap.put("b42_4_30", new arasuperobject(42,4,30,"اما هو فجاز في وسطهم ومضى"));
arasuperobject.biblemap.put("b42_4_31", new arasuperobject(42,4,31,"وانحدر الى كفرناحوم مدينة من الجليل. وكان يعلّمهم في السبوت"));
arasuperobject.biblemap.put("b42_4_32", new arasuperobject(42,4,32,"فبهتوا من تعليمه لان كلامه كان بسلطان"));
arasuperobject.biblemap.put("b42_4_33", new arasuperobject(42,4,33,"وكان في المجمع رجل به روح شيطان نجس فصرخ بصوت عظيم"));
arasuperobject.biblemap.put("b42_4_34", new arasuperobject(42,4,34,"قائلا آه ما لنا ولك يا يسوع الناصري. أتيت لتهلكنا. انا اعرفك من انت قدوس الله"));
arasuperobject.biblemap.put("b42_4_35", new arasuperobject(42,4,35,"فانتهره يسوع قائلا اخرس واخرج منه فصرعه الشيطان في الوسط وخرج منه ولم يضره شيئا"));
arasuperobject.biblemap.put("b42_4_36", new arasuperobject(42,4,36,"فوقعت دهشة على الجميع وكانوا يخاطبون بعضهم بعضا قائلين ما هذه الكلمة. لانه بسلطان وقوة يامر الارواح النجسة فتخرج"));
arasuperobject.biblemap.put("b42_4_37", new arasuperobject(42,4,37,"وخرج صيت عنه الى كل موضع في الكورة المحيطة"));
arasuperobject.biblemap.put("b42_4_38", new arasuperobject(42,4,38,"ولما قام من المجمع دخل بيت سمعان. وكانت حماة سمعان قد اخذتها حمّى شديدة. فسألوه من اجلها"));
arasuperobject.biblemap.put("b42_4_39", new arasuperobject(42,4,39,"فوقف فوقها وانتهر الحمّى فتركتها وفي الحال قامت وصارت تخدمهم"));
arasuperobject.biblemap.put("b42_4_40", new arasuperobject(42,4,40,"وعند غروب الشمس جميع الذين كان عندهم سقماء بامراض مختلفة قدموهم اليه فوضع يديه على كل واحد منهم وشفاهم"));
arasuperobject.biblemap.put("b42_4_41", new arasuperobject(42,4,41,"وكانت شياطين ايضا تخرج من كثيرين وهي تصرخ وتقول انت المسيح ابن الله. فانتهرهم ولم يدعهم يتكلمون لانهم عرفوه انه المسيح"));
arasuperobject.biblemap.put("b42_4_42", new arasuperobject(42,4,42,"ولما صار النهار خرج وذهب الى موضع خلاء وكان الجموع يفتشون عليه فجاءوا اليه وامسكوه لئلا يذهب عنهم"));
arasuperobject.biblemap.put("b42_4_43", new arasuperobject(42,4,43,"فقال لهم انه يبنغي لي ان ابشر المدن الأخر ايضا بملكوت الله لاني لهذا قد أرسلت"));
arasuperobject.biblemap.put("b42_4_44", new arasuperobject(42,4,44,"فكان يكرز في مجامع الجليل"));
arasuperobject.biblemap.put("b42_5_1", new arasuperobject(42,5,1,"واذ كان الجمع يزدحم عليه ليسمع كلمة الله كان واقفا عند بحيرة جنيسارت"));
arasuperobject.biblemap.put("b42_5_2", new arasuperobject(42,5,2,"فرأى سفينتين واقفتين عند البحيرة والصيادون قد خرجوا منهما وغسلوا الشباك"));
arasuperobject.biblemap.put("b42_5_3", new arasuperobject(42,5,3,"فدخل احدى السفينتين التي كانت لسمعان وسأله ان يبعد قليلا عن البر. ثم جلس وصار يعلّم الجموع من السفينة"));
arasuperobject.biblemap.put("b42_5_4", new arasuperobject(42,5,4,"ولما فرغ من الكلام قال لسمعان ابعد الى العمق وألقوا شباككم للصيد"));
arasuperobject.biblemap.put("b42_5_5", new arasuperobject(42,5,5,"فاجاب سمعان وقال له يا معلّم قد تعبنا الليل كله ولم نأخذ شيئا ولكن على كلمتك القي الشبكة"));
arasuperobject.biblemap.put("b42_5_6", new arasuperobject(42,5,6,"ولما فعلوا ذلك امسكوا سمكا كثيرا جدا فصارت شبكتهم تتخرق"));
arasuperobject.biblemap.put("b42_5_7", new arasuperobject(42,5,7,"فاشاروا الى شركائهم الذين في السفينة الاخرى ان يأتوا ويساعدوهم. فأتوا وملأوا السفينتين حتى اخذتا في الغرق"));
arasuperobject.biblemap.put("b42_5_8", new arasuperobject(42,5,8,"فلما رأى سمعان بطرس ذلك خرّ عند ركبتيّ يسوع قائلا اخرج من سفينتي يا رب لاني رجل خاطئ"));
arasuperobject.biblemap.put("b42_5_9", new arasuperobject(42,5,9,"اذ اعترته وجميع الذين معه دهشة على صيد السمك الذي اخذوه"));
arasuperobject.biblemap.put("b42_5_10", new arasuperobject(42,5,10,"وكذلك ايضا يعقوب ويوحنا ابنا زبدي اللذان كانا شريكي سمعان. فقال يسوع لسمعان لا تخف. من الآن تكون تصطاد الناس"));
arasuperobject.biblemap.put("b42_5_11", new arasuperobject(42,5,11,"ولما جاءوا بالسفينتين الى البر تركوا كل شيء وتبعوه"));
arasuperobject.biblemap.put("b42_5_12", new arasuperobject(42,5,12,"وكان في احدى المدن فاذا رجل مملوء برصا. فلما رأى يسوع خرّ على وجهه وطلب اليه قائلا يا سيد ان اردت تقدر ان تطهرني"));
arasuperobject.biblemap.put("b42_5_13", new arasuperobject(42,5,13,"فمدّ يده ولمسه قائلا اريد فاطهر. وللوقت ذهب عنه البرص"));
arasuperobject.biblemap.put("b42_5_14", new arasuperobject(42,5,14,"فاوصاه ان لا يقول لاحد بل امض وأر نفسك للكاهن وقدم عن تطهيرك كما أمر موسى شهادة لهم"));
arasuperobject.biblemap.put("b42_5_15", new arasuperobject(42,5,15,"فذاع الخبر عنه اكثر. فاجتمع جموع كثيرة لكي يسمعوا ويشفوا به من امراضهم"));
arasuperobject.biblemap.put("b42_5_16", new arasuperobject(42,5,16,"واما هو فكان يعتزل في البراري ويصلّي"));
arasuperobject.biblemap.put("b42_5_17", new arasuperobject(42,5,17,"وفي احدى الايام كان يعلم وكان فريسيون ومعلمون للناموس جالسين وهم قد أتوا من كل قرية من الجليل واليهودية واورشليم. وكانت قوة الرب لشفائهم"));
arasuperobject.biblemap.put("b42_5_18", new arasuperobject(42,5,18,"واذا برجال يحملون على فراش انسانا مفلوجا وكانوا يطلبون ان يدخلوا به ويضعوه امامه"));
arasuperobject.biblemap.put("b42_5_19", new arasuperobject(42,5,19,"ولما لم يجدوا من اين يدخلون به لسبب الجمع صعدوا على السطح ودلوه مع الفراش من بين الاجر الى الوسط قدام يسوع"));
arasuperobject.biblemap.put("b42_5_20", new arasuperobject(42,5,20,"فلما رأى ايمانهم قال له ايها الانسان مغفورة لك خطاياك"));
arasuperobject.biblemap.put("b42_5_21", new arasuperobject(42,5,21,"فابتدأ الكتبة والفريسيون يفكرون قائلين من هذا الذي يتكلم بتجاديف. من يقدر ان يغفر خطايا الا الله وحده"));
arasuperobject.biblemap.put("b42_5_22", new arasuperobject(42,5,22,"فشعر يسوع بافكارهم واجاب وقال لهم ماذا تفكرون في قلوبكم"));
arasuperobject.biblemap.put("b42_5_23", new arasuperobject(42,5,23,"أيما ايسر ان يقال مغفورة لك خطاياك. أم ان يقال قم وامش"));
arasuperobject.biblemap.put("b42_5_24", new arasuperobject(42,5,24,"ولكن لكي تعلموا ان لابن الانسان سلطانا على الارض ان يغفر الخطايا قال للمفلوج لك اقول قم واحمل فراشك واذهب الى بيتك"));
arasuperobject.biblemap.put("b42_5_25", new arasuperobject(42,5,25,"ففي الحال قام امامهم وحمل ما كان مضطجعا عليه ومضى الى بيته وهو يمجد الله"));
arasuperobject.biblemap.put("b42_5_26", new arasuperobject(42,5,26,"فأخذت الجميع حيرة ومجدوا الله وامتلأوا خوفا قائلين اننا قد رأينا اليوم عجائب"));
arasuperobject.biblemap.put("b42_5_27", new arasuperobject(42,5,27,"وبعد هذا خرج فنظر عشارا اسمه لاوي جالسا عند مكان الجباية. فقال له اتبعني"));
arasuperobject.biblemap.put("b42_5_28", new arasuperobject(42,5,28,"فترك كل شيء وقام وتبعه"));
arasuperobject.biblemap.put("b42_5_29", new arasuperobject(42,5,29,"وصنع له لاوي ضيافة كبيرة في بيته. والذين كانوا متكئين معهم كانوا جمعا كثيرا من عشارين وآخرين"));
arasuperobject.biblemap.put("b42_5_30", new arasuperobject(42,5,30,"فتذمر كتبتهم والفريسيون على تلاميذه قائلين لماذا تاكلون وتشربون مع عشارين وخطاة"));
arasuperobject.biblemap.put("b42_5_31", new arasuperobject(42,5,31,"فاجاب يسوع وقال لهم لا يحتاج الاصحاء الى طبيب بل المرضى"));
arasuperobject.biblemap.put("b42_5_32", new arasuperobject(42,5,32,"لم آت لأدعو ابرارا بل خطاة الى التوبة"));
arasuperobject.biblemap.put("b42_5_33", new arasuperobject(42,5,33,"وقالوا له لماذا يصوم تلاميذ يوحنا كثيرا ويقدمون طلبات وكذلك تلاميذ الفريسيين ايضا. واما تلاميذك فيأكلون ويشربون"));
arasuperobject.biblemap.put("b42_5_34", new arasuperobject(42,5,34,"فقال لهم أتقدرون ان تجعلوا بني العرس يصومون ما دام العريس معهم"));
arasuperobject.biblemap.put("b42_5_35", new arasuperobject(42,5,35,"ولكن ستأتي ايام حين يرفع العريس عنهم فحينئذ يصومون في تلك الايام"));
arasuperobject.biblemap.put("b42_5_36", new arasuperobject(42,5,36,"وقال لهم ايضا مثلا. ليس احد يضع رقعة من ثوب جديد على ثوب عتيق. وإلا فالجديد يشقّه والعتيق لا توافقه الرقعة التي من الجديد"));
arasuperobject.biblemap.put("b42_5_37", new arasuperobject(42,5,37,"وليس احد يجعل خمرا جديدة في زقاق عتيقة لئلا تشق الخمر الجديدة الزقاق فهي تهرق والزقاق تتلف"));
arasuperobject.biblemap.put("b42_5_38", new arasuperobject(42,5,38,"بل يجعلون خمرا جديدة في زقاق جديدة فتحفظ جميعا"));
arasuperobject.biblemap.put("b42_5_39", new arasuperobject(42,5,39,"وليس احد اذا شرب العتيق يريد للوقت الجديد لانه يقول العتيق اطيب"));
arasuperobject.biblemap.put("b42_6_1", new arasuperobject(42,6,1,"وفي السبت الثاني بعد الاول اجتاز بين الزروع. وكان تلاميذه يقطفون السنابل ويأكلون وهم يفركونها بايديهم"));
arasuperobject.biblemap.put("b42_6_2", new arasuperobject(42,6,2,"فقال لهم قوم من الفريسيين لماذا تفعلون ما لا يحل فعله في السبوت"));
arasuperobject.biblemap.put("b42_6_3", new arasuperobject(42,6,3,"فاجاب يسوع وقال لهم أما قرأتم ولا هذا الذي فعله داود حين جاع هو والذين كانوا معه"));
arasuperobject.biblemap.put("b42_6_4", new arasuperobject(42,6,4,"كيف دخل بيت الله واخذ خبز التقدمة وأكل واعطى الذين معه ايضا. الذي لا يحل اكله الا للكهنة فقط"));
arasuperobject.biblemap.put("b42_6_5", new arasuperobject(42,6,5,"وقال لهم ان ابن الانسان هو رب السبت ايضا"));
arasuperobject.biblemap.put("b42_6_6", new arasuperobject(42,6,6,"وفي سبت آخر دخل المجمع وصار يعلم. وكان هناك رجل يده اليمنى يابسة"));
arasuperobject.biblemap.put("b42_6_7", new arasuperobject(42,6,7,"وكان الكتبة والفريسيون يراقبونه هل يشفي في السبت لكي يجدوا عليه شكاية"));
arasuperobject.biblemap.put("b42_6_8", new arasuperobject(42,6,8,"اما هو فعلم افكارهم وقال للرجل الذي يده يابسة قم وقف في الوسط. فقام ووقف"));
arasuperobject.biblemap.put("b42_6_9", new arasuperobject(42,6,9,"ثم قال لهم يسوع اسألكم شيئا. هل يحل في السبت فعل الخير او فعل الشر. تخليص نفس او اهلاكها"));
arasuperobject.biblemap.put("b42_6_10", new arasuperobject(42,6,10,"ثم نظر حوله الى جميعهم وقال للرجل مدّ يدك. ففعل هكذا. فعادت يده صحيحة كالاخرى"));
arasuperobject.biblemap.put("b42_6_11", new arasuperobject(42,6,11,"فامتلأوا حمقا وصاروا يتكالمون فيما بينهم ماذا يفعلون بيسوع"));
arasuperobject.biblemap.put("b42_6_12", new arasuperobject(42,6,12,"وفي تلك الايام خرج الى الجبل ليصلّي. وقضى الليل كله في الصلاة لله"));
arasuperobject.biblemap.put("b42_6_13", new arasuperobject(42,6,13,"ولما كان النهار دعا تلاميذه واختار منهم اثني عشر الذين سماهم ايضا رسلا"));
arasuperobject.biblemap.put("b42_6_14", new arasuperobject(42,6,14,"سمعان الذي سماه ايضا بطرس واندراوس اخاه. يعقوب ويوحنا. فيلبس وبرثولماوس"));
arasuperobject.biblemap.put("b42_6_15", new arasuperobject(42,6,15,"متّى وتوما. يعقوب بن حلفى وسمعان الذي يدعى الغيور"));
arasuperobject.biblemap.put("b42_6_16", new arasuperobject(42,6,16,"يهوذا اخا يعقوب ويهوذا الاسخريوطي الذي صار مسلما ايضا"));
arasuperobject.biblemap.put("b42_6_17", new arasuperobject(42,6,17,"ونزل معهم ووقف في موضع سهل هو وجمع من تلاميذه وجمهور كثير من الشعب من جميع اليهودية واورشليم وساحل صور وصيدا الذين جاءوا ليسمعوه ويشفوا من امراضهم"));
arasuperobject.biblemap.put("b42_6_18", new arasuperobject(42,6,18,"والمعذبون من ارواح نجسة. وكانوا يبرأون"));
arasuperobject.biblemap.put("b42_6_19", new arasuperobject(42,6,19,"وكل الجمع طلبوا ان يلمسوه لان قوة كانت تخرج منه وتشفي الجميع"));
arasuperobject.biblemap.put("b42_6_20", new arasuperobject(42,6,20,"ورفع عينيه الى تلاميذه وقال طوباكم ايها المساكين لان لكم ملكوت الله"));
arasuperobject.biblemap.put("b42_6_21", new arasuperobject(42,6,21,"طوباكم ايها الجياع الآن لانكم تشبعون. طوباكم ايها الباكون الآن لانكم ستضحكون"));
arasuperobject.biblemap.put("b42_6_22", new arasuperobject(42,6,22,"طوباكم اذا ابغضكم الناس واذا افرزوكم وعيّروكم واخرجوا اسمكم كشرير من اجل ابن الانسان"));
arasuperobject.biblemap.put("b42_6_23", new arasuperobject(42,6,23,"افرحوا في ذلك اليوم وتهللوا. فهوذا اجركم عظيم في السماء. لان آباءهم هكذا كانوا يفعلون بالانبياء"));
arasuperobject.biblemap.put("b42_6_24", new arasuperobject(42,6,24,"ولكن ويل لكم ايها الاغنياء. لانكم قد نلتم عزاءكم"));
arasuperobject.biblemap.put("b42_6_25", new arasuperobject(42,6,25,"ويل لكم ايها الشباعى لانكم ستجوعون. ويل لكم ايها الضاحكون الآن لانكم ستحزنون وتبكون"));
arasuperobject.biblemap.put("b42_6_26", new arasuperobject(42,6,26,"ويل لكم اذا قال فيكم جميع الناس حسنا. لانه هكذا كان آباؤهم يفعلون بالانبياء الكذبة"));
arasuperobject.biblemap.put("b42_6_27", new arasuperobject(42,6,27,"لكني اقول لكم ايها السامعون احبوا اعداءكم. احسنوا الى مبغضيكم"));
arasuperobject.biblemap.put("b42_6_28", new arasuperobject(42,6,28,"باركوا لاعنيكم. وصلّوا لاجل الذين يسيئون اليكم"));
arasuperobject.biblemap.put("b42_6_29", new arasuperobject(42,6,29,"من ضربك على خدك فاعرض له الآخر ايضا. ومن اخذ رداءك فلا تمنعه ثوبك ايضا"));
arasuperobject.biblemap.put("b42_6_30", new arasuperobject(42,6,30,"وكل من سألك فاعطه. ومن اخذ الذي لك فلا تطالبه"));
arasuperobject.biblemap.put("b42_6_31", new arasuperobject(42,6,31,"وكما تريدون ان يفعل الناس بكم افعلوا انتم ايضا بهم هكذا"));
arasuperobject.biblemap.put("b42_6_32", new arasuperobject(42,6,32,"وان احببتم الذين يحبونكم فاي فضل لكم. فان الخطاة ايضا يحبون الذين يحبونهم"));
arasuperobject.biblemap.put("b42_6_33", new arasuperobject(42,6,33,"واذا احسنتم الى الذين يحسنون اليكم فاي فضل لكم. فان الخطاة ايضا يفعلون هكذا"));
arasuperobject.biblemap.put("b42_6_34", new arasuperobject(42,6,34,"وان اقرضتم الذين ترجون ان تستردوا منهم فاي فضل لكم. فان الخطاة ايضا يقرضون الخطاة لكي يستردوا منهم المثل"));
arasuperobject.biblemap.put("b42_6_35", new arasuperobject(42,6,35,"بل احبوا اعداءكم واحسنوا واقرضوا وانتم لا ترجون شيئا فيكون اجركم عظيما وتكونوا بني العلي فانه منعم على غير الشاكرين والاشرار"));
arasuperobject.biblemap.put("b42_6_36", new arasuperobject(42,6,36,"فكونوا رحماء كما ان اباكم ايضا رحيم"));
arasuperobject.biblemap.put("b42_6_37", new arasuperobject(42,6,37,"ولا تدينوا فلا تدانوا. لا تقضوا على احد فلا يقضى عليكم. اغفروا يغفر لكم"));
arasuperobject.biblemap.put("b42_6_38", new arasuperobject(42,6,38,"اعطوا تعطوا. كيلا جيدا ملبدا مهزوزا فائضا يعطون في احضانكم. لانه بنفس الكيل الذي به تكيلون يكال لكم"));
arasuperobject.biblemap.put("b42_6_39", new arasuperobject(42,6,39,"وضرب لهم مثلا. هل يقدر اعمى ان يقود اعمى. أما يسقط الاثنان في حفرة"));
arasuperobject.biblemap.put("b42_6_40", new arasuperobject(42,6,40,"ليس التلميذ افضل من معلمه. بل كل من صار كاملا يكون مثل معلمه"));
arasuperobject.biblemap.put("b42_6_41", new arasuperobject(42,6,41,"لماذا تنظر القذى الذي في عين اخيك. واما الخشبة التي في عينك فلا تفطن لها"));
arasuperobject.biblemap.put("b42_6_42", new arasuperobject(42,6,42,"او كيف تقدر ان تقول لاخيك يا اخي دعني اخرج القذى الذي في عينك. وانت لا تنظر الخشبة التي في عينك. يا مرائي اخرج اولا الخشبة من عينك وحينئذ تبصر جيدا ان تخرج القذى الذي في عين اخيك"));
arasuperobject.biblemap.put("b42_6_43", new arasuperobject(42,6,43,"لانه ما من شجرة جيدة تثمر ثمرا رديّا. ولا شجرة ردية تثمر ثمرا جيدا"));
arasuperobject.biblemap.put("b42_6_44", new arasuperobject(42,6,44,"لان كل شجرة تعرف من ثمرها. فانهم لا يجتنون من الشوك تينا ولا يقطفون من العليق عنبا"));
arasuperobject.biblemap.put("b42_6_45", new arasuperobject(42,6,45,"الانسان الصالح من كنز قلبه الصالح يخرج الصلاح. والانسان الشرير من كنز قلبه الشرير يخرج الشر. فانه من فضلة القلب يتكلم فمه"));
arasuperobject.biblemap.put("b42_6_46", new arasuperobject(42,6,46,"ولماذا تدعونني يا رب يا رب وانتم لا تفعلون ما اقوله"));
arasuperobject.biblemap.put("b42_6_47", new arasuperobject(42,6,47,"كل من يأتي اليّ ويسمع كلامي ويعمل به اريكم من يشبه"));
arasuperobject.biblemap.put("b42_6_48", new arasuperobject(42,6,48,"يشبه انسانا بنى بيتا وحفر وعمّق ووضع الاساس على الصخر. فلما حدث سيل صدم النهر ذلك البيت فلم يقدر ان يزعزعه لانه كان مؤسسا على الصخر"));
arasuperobject.biblemap.put("b42_6_49", new arasuperobject(42,6,49,"واما الذي يسمع ولا يعمل فيشبه انسانا بنى بيته على الارض من دون اساس. فصدمه النهر فسقط حالا وكان خراب ذلك البيت عظيما"));
arasuperobject.biblemap.put("b42_7_1", new arasuperobject(42,7,1,"ولما اكمل اقواله كلها في مسامع الشعب دخل كفرناحوم"));
arasuperobject.biblemap.put("b42_7_2", new arasuperobject(42,7,2,"وكان عبد لقائد مئة مريضا مشرفا على الموت وكان عزيزا عنده"));
arasuperobject.biblemap.put("b42_7_3", new arasuperobject(42,7,3,"فلما سمع عن يسوع ارسل اليه شيوخ اليهود يسأله ان يأتي ويشفي عبده"));
arasuperobject.biblemap.put("b42_7_4", new arasuperobject(42,7,4,"فلما جاءوا الى يسوع طلبوا اليه باجتهاد قائلين انه مستحق ان يفعل له هذا"));
arasuperobject.biblemap.put("b42_7_5", new arasuperobject(42,7,5,"لانه يحب امتنا وهو بنى لنا المجمع"));
arasuperobject.biblemap.put("b42_7_6", new arasuperobject(42,7,6,"فذهب يسوع معهم. واذ كان غير بعيد عن البيت ارسل اليه قائد المئة اصدقاء يقول له يا سيد لا تتعب. لاني لست مستحقا ان تدخل تحت سقفي"));
arasuperobject.biblemap.put("b42_7_7", new arasuperobject(42,7,7,"لذلك لم احسب نفسي اهلا ان آتي اليك. لكن قل كلمة فيبرأ غلامي"));
arasuperobject.biblemap.put("b42_7_8", new arasuperobject(42,7,8,"لاني انا ايضا انسان مرتّب تحت سلطان. لي جند تحت يدي. واقول لهذا اذهب فيذهب ولآخر ائت فيأتي ولعبدي افعل هذا فيفعل"));
arasuperobject.biblemap.put("b42_7_9", new arasuperobject(42,7,9,"ولما سمع يسوع هذا تعجب منه والتفت الى الجمع الذي يتبعه وقال اقول لكم لم اجد ولا في اسرائيل ايمانا بمقدار هذا"));
arasuperobject.biblemap.put("b42_7_10", new arasuperobject(42,7,10,"ورجع المرسلون الى البيت فوجدوا العبد المريض قد صحّ"));
arasuperobject.biblemap.put("b42_7_11", new arasuperobject(42,7,11,"وفي اليوم التالي ذهب الى مدينة تدعى نايين وذهب معه كثيرون من تلاميذه وجمع كثير"));
arasuperobject.biblemap.put("b42_7_12", new arasuperobject(42,7,12,"فلما اقترب الى باب المدينة اذا ميت محمول ابن وحيد لامّه وهي ارملة ومعها جمع كثير من المدينة"));
arasuperobject.biblemap.put("b42_7_13", new arasuperobject(42,7,13,"فلما رآها الرب تحنن عليها وقال لها لا تبكي"));
arasuperobject.biblemap.put("b42_7_14", new arasuperobject(42,7,14,"ثم تقدم ولمس النعش فوقف الحاملون. فقال ايها الشاب لك اقول قم"));
arasuperobject.biblemap.put("b42_7_15", new arasuperobject(42,7,15,"فجلس الميت وابتدأ يتكلم فدفعه الى امه"));
arasuperobject.biblemap.put("b42_7_16", new arasuperobject(42,7,16,"فاخذ الجميع خوف ومجدوا الله قائلين قد قام فينا نبي عظيم وافتقد الله شعبه"));
arasuperobject.biblemap.put("b42_7_17", new arasuperobject(42,7,17,"وخرج هذا الخبر عنه في كل اليهودية وفي جميع الكورة المحيطة"));
arasuperobject.biblemap.put("b42_7_18", new arasuperobject(42,7,18,"فاخبر يوحنا تلاميذه بهذا كله"));
arasuperobject.biblemap.put("b42_7_19", new arasuperobject(42,7,19,"فدعا يوحنا اثنين من تلاميذه وارسل الى يسوع قائلا انت هو الآتي ام ننتظر آخر"));
arasuperobject.biblemap.put("b42_7_20", new arasuperobject(42,7,20,"فلما جاء اليه الرجلان قالا يوحنا المعمدان قد ارسلنا اليك قائلا انت هو الآتي ام ننتظر آخر"));
arasuperobject.biblemap.put("b42_7_21", new arasuperobject(42,7,21,"وفي تلك الساعة شفى كثيرين من امراض وادواء وارواح شريرة ووهب البصر لعميان كثيرين"));
arasuperobject.biblemap.put("b42_7_22", new arasuperobject(42,7,22,"فاجاب يسوع وقال لهما اذهبا واخبرا يوحنا بما رأيتما وسمعتما. ان العمي يبصرون والعرج يمشون والبرص يطهرون والصم يسمعون والموتى يقومون والمساكين يبشرون"));
arasuperobject.biblemap.put("b42_7_23", new arasuperobject(42,7,23,"وطوبى لمن لا يعثر فيّ"));
arasuperobject.biblemap.put("b42_7_24", new arasuperobject(42,7,24,"فلما مضى رسولا يوحنا ابتدأ يقول للجموع عن يوحنا. ماذا خرجتم الى البرية لتنظروا. أقصبة تحركها الريح"));
arasuperobject.biblemap.put("b42_7_25", new arasuperobject(42,7,25,"بل ماذا خرجتم لتنظروا أانسانا لابسا ثيابا ناعمة. هوذا الذين في اللباس الفاخر والتنعم هم في قصور الملوك"));
arasuperobject.biblemap.put("b42_7_26", new arasuperobject(42,7,26,"بل ماذا خرجتم لتنظروا. أنبيا. نعم اقول لكم وافضل من نبي"));
arasuperobject.biblemap.put("b42_7_27", new arasuperobject(42,7,27,"هذا هو الذي كتب عنه ها انا ارسل امام وجهك ملاكي الذي يهيئ طريقك قدامك"));
arasuperobject.biblemap.put("b42_7_28", new arasuperobject(42,7,28,"لاني اقول لكم انه بين المولودين من النساء ليس نبي اعظم من يوحنا المعمدان. ولكن الاصغر في ملكوت الله اعظم منه"));
arasuperobject.biblemap.put("b42_7_29", new arasuperobject(42,7,29,"وجميع الشعب اذ سمعوا والعشارين برروا الله معتمدين بمعمودية يوحنا"));
arasuperobject.biblemap.put("b42_7_30", new arasuperobject(42,7,30,"واما الفريسيون والناموسيون فرفضوا مشورة الله من جهة انفسهم غير معتمدين منه"));
arasuperobject.biblemap.put("b42_7_31", new arasuperobject(42,7,31,"ثم قال الرب فبمن اشبه اناس هذا الجيل وماذا يشبهون"));
arasuperobject.biblemap.put("b42_7_32", new arasuperobject(42,7,32,"يشبهون اولادا جالسين في السوق ينادون بعضهم بعضا ويقولون زمرنا لكم فلم ترقصوا. نحنا لكم فلم تبكوا"));
arasuperobject.biblemap.put("b42_7_33", new arasuperobject(42,7,33,"لانه جاء يوحنا المعمدان لا يأكل خبزا ولا يشرب خمرا فتقولون به شيطان"));
arasuperobject.biblemap.put("b42_7_34", new arasuperobject(42,7,34,"جاء ابن الانسان يأكل ويشرب فتقولون هوذا انسان اكول وشريب خمر. محب للعشارين والخطاة"));
arasuperobject.biblemap.put("b42_7_35", new arasuperobject(42,7,35,"والحكمة تبررت من جميع بنيها"));
arasuperobject.biblemap.put("b42_7_36", new arasuperobject(42,7,36,"وسأله واحد من الفريسيين ان يأكل معه فدخل بيت الفريسي واتكأ"));
arasuperobject.biblemap.put("b42_7_37", new arasuperobject(42,7,37,"واذا امرأة في المدينة كانت خاطئة اذ علمت انه متكئ في بيت الفريسي جاءت بقارورة طيب"));
arasuperobject.biblemap.put("b42_7_38", new arasuperobject(42,7,38,"ووقفت عند قدميه من ورائه باكية وابتدأت تبل قدميه بالدموع وكانت تمسحهما بشعر راسها وتقبل قدميه وتدهنهما بالطيب"));
arasuperobject.biblemap.put("b42_7_39", new arasuperobject(42,7,39,"فلما رأى الفريسي الذي دعاه ذلك تكلم في نفسه قائلا لو كان هذا نبيا لعلم من هذه المرأة التي تلمسه وما هي. انها خاطئة"));
arasuperobject.biblemap.put("b42_7_40", new arasuperobject(42,7,40,"فاجاب يسوع وقال له يا سمعان عندي شيء اقوله لك. فقال قل يا معلّم"));
arasuperobject.biblemap.put("b42_7_41", new arasuperobject(42,7,41,"كان لمداين مديونان. على الواحد خمس مئة دينار وعلى الآخر خمسون"));
arasuperobject.biblemap.put("b42_7_42", new arasuperobject(42,7,42,"واذ لم يكن لهما ما يوفيان سامحهما جميعا. فقل. ايهما يكون اكثر حبا له"));
arasuperobject.biblemap.put("b42_7_43", new arasuperobject(42,7,43,"فاجاب سمعان وقال اظن الذي سامحه بالاكثر. فقال له بالصواب حكمت"));
arasuperobject.biblemap.put("b42_7_44", new arasuperobject(42,7,44,"ثم التفت الى المرأة وقال لسمعان أتنظر هذه المرأة. اني دخلت بيتك وماء لاجل رجلي لم تعط. واما هي فقد غسلت رجليّ بالدموع ومسحتهما بشعر راسها"));
arasuperobject.biblemap.put("b42_7_45", new arasuperobject(42,7,45,"قبلة لم تقبّلني. واما هي فمنذ دخلت لم تكف عن تقبيل رجليّ"));
arasuperobject.biblemap.put("b42_7_46", new arasuperobject(42,7,46,"بزيت لم تدهن راسي. واما هي فقد دهنت بالطيب رجليّ"));
arasuperobject.biblemap.put("b42_7_47", new arasuperobject(42,7,47,"من اجل ذلك اقول لك قد غفرت خطاياها الكثيرة لانها احبت كثيرا. والذي يغفر له قليل يحب قليلا"));
arasuperobject.biblemap.put("b42_7_48", new arasuperobject(42,7,48,"ثم قال لها مغفورة لك خطاياك"));
arasuperobject.biblemap.put("b42_7_49", new arasuperobject(42,7,49,"فابتدأ المتكئون معه يقولون في انفسهم من هذا الذي يغفر خطايا ايضا"));
arasuperobject.biblemap.put("b42_7_50", new arasuperobject(42,7,50,"فقال للمرأة ايمانك قد خلّصك. اذهبي بسلام"));
arasuperobject.biblemap.put("b42_8_1", new arasuperobject(42,8,1,"وعلى اثر ذلك كان يسير في مدينة وقرية يكرز ويبشر بملكوت الله ومعه الاثنا عشر"));
arasuperobject.biblemap.put("b42_8_2", new arasuperobject(42,8,2,"وبعض النساء كنّ قد شفين من ارواح شريرة وامراض. مريم التي تدعى المجدلية التي خرج منها سبعة شياطين"));
arasuperobject.biblemap.put("b42_8_3", new arasuperobject(42,8,3,"ويونّا امرأة خوزي وكيل هيرودس وسوسنة وأخر كثيرات كنّ يخدمنه من اموالهنّ"));
arasuperobject.biblemap.put("b42_8_4", new arasuperobject(42,8,4,"فلما اجتمع جمع كثير ايضا من الذين جاءوا اليه من كل مدينة قال بمثل"));
arasuperobject.biblemap.put("b42_8_5", new arasuperobject(42,8,5,"خرج الزارع ليزرع زرعه. وفيما هو يزرع سقط بعض على الطريق فانداس واكلته طيور السماء"));
arasuperobject.biblemap.put("b42_8_6", new arasuperobject(42,8,6,"وسقط آخر على الصخر فلما نبت جف لانه لم تكن له رطوبة"));
arasuperobject.biblemap.put("b42_8_7", new arasuperobject(42,8,7,"وسقط آخر في وسط الشوك. فنبت معه الشوك وخنقه"));
arasuperobject.biblemap.put("b42_8_8", new arasuperobject(42,8,8,"وسقط آخر في الارض الصالحة فلما نبت صنع ثمرا مئة ضعف. قال هذا ونادى من له اذنان للسمع فليسمع"));
arasuperobject.biblemap.put("b42_8_9", new arasuperobject(42,8,9,"فسأله تلاميذه قائلين ما عسى ان يكون هذا المثل"));
arasuperobject.biblemap.put("b42_8_10", new arasuperobject(42,8,10,"فقال. لكم قد أعطي ان تعرفوا اسرار ملكوت الله. واما للباقين فبامثال حتى انهم مبصرين لا يبصرون وسامعين لا يفهمون"));
arasuperobject.biblemap.put("b42_8_11", new arasuperobject(42,8,11,"وهذا هو المثل. الزرع هو كلام الله"));
arasuperobject.biblemap.put("b42_8_12", new arasuperobject(42,8,12,"والذين على الطريق هم الذين يسمعون ثم يأتي ابليس وينزع الكلمة من قلوبهم لئلا يؤمنوا فيخلصوا"));
arasuperobject.biblemap.put("b42_8_13", new arasuperobject(42,8,13,"والذين على الصخر هم الذين متى سمعوا يقبلون الكلمة بفرح. وهؤلاء ليس لهم اصل فيؤمنون الى حين وفي وقت التجربة يرتدون"));
arasuperobject.biblemap.put("b42_8_14", new arasuperobject(42,8,14,"والذي سقط بين الشوك هم الذين يسمعون ثم يذهبون فيختنقون من هموم الحياة وغناها ولذّاتها ولا ينضجون ثمرا"));
arasuperobject.biblemap.put("b42_8_15", new arasuperobject(42,8,15,"والذي في الارض الجيدة هو الذين يسمعون الكلمة فيحفظونها في قلب جيد صالح ويثمرون بالصبر"));
arasuperobject.biblemap.put("b42_8_16", new arasuperobject(42,8,16,"وليس احد يوقد سراجا ويغطيه باناء او يضعه تحت سرير بل يضعه على منارة لينظر الداخلون النور"));
arasuperobject.biblemap.put("b42_8_17", new arasuperobject(42,8,17,"لانه ليس خفي لا يظهر ولا مكتوم لا يعلم ويعلن"));
arasuperobject.biblemap.put("b42_8_18", new arasuperobject(42,8,18,"فانظروا كيف تسمعون. لان من له سيعطى. ومن ليس له فالذي يظنه له يؤخذ منه"));
arasuperobject.biblemap.put("b42_8_19", new arasuperobject(42,8,19,"وجاء اليه امه واخوته. ولم يقدروا ان يصلوا اليه لسبب الجمع"));
arasuperobject.biblemap.put("b42_8_20", new arasuperobject(42,8,20,"فاخبروه قائلين امك واخوتك واقفون خارجا يريدون ان يروك"));
arasuperobject.biblemap.put("b42_8_21", new arasuperobject(42,8,21,"فاجاب وقال لهم امي واخوتي هم الذين يسمعون كلمة الله ويعملون بها"));
arasuperobject.biblemap.put("b42_8_22", new arasuperobject(42,8,22,"وفي احد الايام دخل سفينة هو وتلاميذه. فقال لهم لنعبر الى عبر البحيرة. فاقلعوا"));
arasuperobject.biblemap.put("b42_8_23", new arasuperobject(42,8,23,"وفيما هم سائرون نام. فنزل نوء ريح في البحيرة. وكانوا يمتلئون ماء وصاروا في خطر"));
arasuperobject.biblemap.put("b42_8_24", new arasuperobject(42,8,24,"فتقدموا وايقظوه قائلين يا معلّم يا معلّم اننا نهلك. فقام وانتهر الريح وتموّج الماء فانتهيا وصار هدوء"));
arasuperobject.biblemap.put("b42_8_25", new arasuperobject(42,8,25,"ثم قال لهم اين ايمانكم. فخافوا وتعجبوا قائلين فيما بينهم من هو هذا. فانه يأمر الرياح ايضا والماء فتطيعه"));
arasuperobject.biblemap.put("b42_8_26", new arasuperobject(42,8,26,"وساروا الى كورة الجدريين التي هي مقابل الجليل"));
arasuperobject.biblemap.put("b42_8_27", new arasuperobject(42,8,27,"ولما خرج الى الارض استقبله رجل من المدينة كان فيه شياطين منذ زمان طويل وكان لا يلبس ثوبا ولا يقيم في بيت بل في القبور"));
arasuperobject.biblemap.put("b42_8_28", new arasuperobject(42,8,28,"فلما رأى يسوع صرخ وخرّ له وقال بصوت عظيم ما لي ولك يا يسوع ابن الله العلي. اطلب منك ان لا تعذبني"));
arasuperobject.biblemap.put("b42_8_29", new arasuperobject(42,8,29,"لانه امر الروح النجس ان يخرج من الانسان. لانه منذ زمان كثير كان يخطفه. وقد ربط بسلاسل وقيود محروسا. وكان يقطع الربط ويساق من الشيطان الى البراري"));
arasuperobject.biblemap.put("b42_8_30", new arasuperobject(42,8,30,"فسأله يسوع قائلا ما اسمك. فقال لجئون. لان شياطين كثيرة دخلت فيه"));
arasuperobject.biblemap.put("b42_8_31", new arasuperobject(42,8,31,"وطلب اليه ان لا يأمرهم بالذهاب الى الهاوية"));
arasuperobject.biblemap.put("b42_8_32", new arasuperobject(42,8,32,"وكان هناك قطيع خنازير كثيرة ترعى في الجبل. فطلبوا اليه ان يأذن لهم بالدخول فيها. فأذن لهم"));
arasuperobject.biblemap.put("b42_8_33", new arasuperobject(42,8,33,"فخرجت الشياطين من الانسان ودخلت في الخنازير. فاندفع القطيع من على الجرف الى البحيرة واختنق"));
arasuperobject.biblemap.put("b42_8_34", new arasuperobject(42,8,34,"فلما رأى الرعاة ما كان هربوا وذهبوا واخبروا في المدينة وفي الضياع"));
arasuperobject.biblemap.put("b42_8_35", new arasuperobject(42,8,35,"فخرجوا ليروا ما جرى. وجاءوا الى يسوع فوجدوا الانسان الذي كانت الشياطين قد خرجت منه لابسا وعاقلا جالسا عند قدمي يسوع فخافوا"));
arasuperobject.biblemap.put("b42_8_36", new arasuperobject(42,8,36,"فاخبرهم ايضا الذين رأوا كيف خلص المجنون"));
arasuperobject.biblemap.put("b42_8_37", new arasuperobject(42,8,37,"فطلب اليه كل جمهور كورة الجدريين ان يذهب عنهم. لانه اعتراهم خوف عظيم. فدخل السفينة ورجع"));
arasuperobject.biblemap.put("b42_8_38", new arasuperobject(42,8,38,"اما الرجل الذي خرجت منه الشياطين فطلب اليه ان يكون معه ولكن يسوع صرفه قائلا"));
arasuperobject.biblemap.put("b42_8_39", new arasuperobject(42,8,39,"ارجع الى بيتك وحدث بكم صنع الله بك. فمضى وهو ينادي في المدينة كلها بكم صنع به يسوع"));
arasuperobject.biblemap.put("b42_8_40", new arasuperobject(42,8,40,"ولما رجع يسوع قبله الجمع لانهم كانوا جميعهم ينتظرونه"));
arasuperobject.biblemap.put("b42_8_41", new arasuperobject(42,8,41,"واذا رجل اسمه يايروس قد جاء. وكان رئيس المجمع. فوقع عند قدمي يسوع وطلب اليه ان يدخل بيته"));
arasuperobject.biblemap.put("b42_8_42", new arasuperobject(42,8,42,"لانه كان له بنت وحيدة لها نحو اثنتي عشرة سنة وكانت في حال الموت. ففيما هو منطلق زحمته الجموع"));
arasuperobject.biblemap.put("b42_8_43", new arasuperobject(42,8,43,"وامرأة بنزف دم منذ اثنتي عشرة سنة وقد انفقت كل معيشتها للاطباء ولم تقدر ان تشفى من احد"));
arasuperobject.biblemap.put("b42_8_44", new arasuperobject(42,8,44,"جاءت من ورائه ولمست هدب ثوبه. ففي الحال وقف نزف دمها"));
arasuperobject.biblemap.put("b42_8_45", new arasuperobject(42,8,45,"فقال يسوع من الذي لمسني. واذ كان الجميع ينكرون قال بطرس والذين معه يا معلّم الجموع يضيّقون عليك ويزحمونك وتقول من الذي لمسني"));
arasuperobject.biblemap.put("b42_8_46", new arasuperobject(42,8,46,"فقال يسوع قد لمسني واحد لاني علمت ان قوة قد خرجت مني"));
arasuperobject.biblemap.put("b42_8_47", new arasuperobject(42,8,47,"فلما رأت المرأة انها لم تختف جاءت مرتعدة وخرّت له واخبرته قدام جميع الشعب لاي سبب لمسته وكيف برئت في الحال"));
arasuperobject.biblemap.put("b42_8_48", new arasuperobject(42,8,48,"فقال لها ثقي يا ابنة. ايمانك قد شفاك. اذهبي بسلام"));
arasuperobject.biblemap.put("b42_8_49", new arasuperobject(42,8,49,"وبينما هو يتكلم جاء واحد من دار رئيس المجمع قائلا له قد ماتت ابنتك. لا تتعب المعلّم"));
arasuperobject.biblemap.put("b42_8_50", new arasuperobject(42,8,50,"فسمع يسوع واجابه قائلا لا تخف. آمن فقط فهي تشفى"));
arasuperobject.biblemap.put("b42_8_51", new arasuperobject(42,8,51,"فلما جاء الى البيت لم يدع احدا يدخل الا بطرس ويعقوب ويوحنا وابا الصبيه وامها"));
arasuperobject.biblemap.put("b42_8_52", new arasuperobject(42,8,52,"وكان الجميع يبكون عليها ويلطمون. فقال لا تبكوا. لم تمت لكنها نائمة"));
arasuperobject.biblemap.put("b42_8_53", new arasuperobject(42,8,53,"فضحكوا عليه عارفين انها ماتت"));
arasuperobject.biblemap.put("b42_8_54", new arasuperobject(42,8,54,"فاخرج الجمع خارجا وامسك بيدها ونادى قائلا يا صبية قومي"));
arasuperobject.biblemap.put("b42_8_55", new arasuperobject(42,8,55,"فرجعت روحها وقامت في الحال. فامر ان تعطى لتاكل"));
arasuperobject.biblemap.put("b42_8_56", new arasuperobject(42,8,56,"فبهت والداها. فاوصاهما ان لا يقولا لاحد عما كان"));
arasuperobject.biblemap.put("b42_9_1", new arasuperobject(42,9,1,"ودعا تلاميذه الاثني عشر واعطاهم قوة وسلطانا على جميع الشياطين وشفاء امراض"));
arasuperobject.biblemap.put("b42_9_2", new arasuperobject(42,9,2,"وارسلهم ليكرزوا بملكوت الله ويشفوا المرضى"));
arasuperobject.biblemap.put("b42_9_3", new arasuperobject(42,9,3,"وقال لهم لا تحملوا شيئا للطريق لا عصا ولا مزودا ولا خبزا ولا فضة ولا يكون للواحد ثوبان"));
arasuperobject.biblemap.put("b42_9_4", new arasuperobject(42,9,4,"واي بيت دخلتموه فهناك اقيموا ومن هناك اخرجوا"));
arasuperobject.biblemap.put("b42_9_5", new arasuperobject(42,9,5,"وكل من لا يقبلكم فاخرجوا من تلك المدينة وانفضوا الغبار ايضا عن ارجلكم شهادة عليهم"));
arasuperobject.biblemap.put("b42_9_6", new arasuperobject(42,9,6,"فلما خرجوا كانوا يجتازون في كل قرية يبشرون ويشفون في كل موضع"));
arasuperobject.biblemap.put("b42_9_7", new arasuperobject(42,9,7,"فسمع هيرودس رئيس الربع بجميع ما كان منه وارتاب. لان قوما كانوا يقولون ان يوحنا قد قام من الاموات"));
arasuperobject.biblemap.put("b42_9_8", new arasuperobject(42,9,8,"وقوما ان ايليا ظهر. وآخرين ان نبيا من القدماء قام"));
arasuperobject.biblemap.put("b42_9_9", new arasuperobject(42,9,9,"فقال هيرودس يوحنا انا قطعت راسه. فمن هو هذا الذي اسمع عنه مثل هذا. وكان يطلب ان يراه"));
arasuperobject.biblemap.put("b42_9_10", new arasuperobject(42,9,10,"ولما رجع الرسل اخبروه بجميع ما فعلوا. فاخذهم وانصرف منفردا الى موضع خلاء لمدينة تسمى بيت صيدا"));
arasuperobject.biblemap.put("b42_9_11", new arasuperobject(42,9,11,"فالجموع اذ علموا تبعوه. فقبلهم وكلمهم عن ملكوت الله. والمحتاجون الى الشفاء شفاهم"));
arasuperobject.biblemap.put("b42_9_12", new arasuperobject(42,9,12,"فابتدأ النهار يميل. فتقدم الاثنا عشر وقالوا له اصرف الجمع ليذهبوا الى القرى والضياع حوالينا فيبيتوا ويجدوا طعاما لاننا ههنا في موضع خلاء"));
arasuperobject.biblemap.put("b42_9_13", new arasuperobject(42,9,13,"فقال لهم اعطوهم انتم ليأكلوا. فقالوا ليس عندنا اكثر من خمسة ارغفة وسمكتين الا ان نذهب ونبتاع طعاما لهذا الشعب كله"));
arasuperobject.biblemap.put("b42_9_14", new arasuperobject(42,9,14,"لانهم كانوا نحو خمسة آلاف رجل. فقال لتلاميذه اتكئوهم فرقا خمسين خمسين"));
arasuperobject.biblemap.put("b42_9_15", new arasuperobject(42,9,15,"ففعلوا هكذا واتكأوا الجميع"));
arasuperobject.biblemap.put("b42_9_16", new arasuperobject(42,9,16,"فاخذ الارغفة الخمسة والسمكتين ورفع نظره نحو السماء وباركهنّ ثم كسّر واعطى التلاميذ ليقدموا للجمع"));
arasuperobject.biblemap.put("b42_9_17", new arasuperobject(42,9,17,"فأكلوا وشبعوا جميعا. ثم رفع ما فضل عنهم من الكسر اثنتا عشرة قفة"));
arasuperobject.biblemap.put("b42_9_18", new arasuperobject(42,9,18,"وفيما هو يصلّي على انفراد كان التلاميذ معه. فسألهم قائلا من تقول الجموع اني انا"));
arasuperobject.biblemap.put("b42_9_19", new arasuperobject(42,9,19,"فاجابوا وقالوا يوحنا المعمدان. وآخرون ايليا. وآخرون ان نبيا من القدماء قام"));
arasuperobject.biblemap.put("b42_9_20", new arasuperobject(42,9,20,"فقال لهم وانتم من تقولون اني انا. فاجاب بطرس وقال مسيح الله"));
arasuperobject.biblemap.put("b42_9_21", new arasuperobject(42,9,21,"فانتهرهم واوصى ان لا يقولوا ذلك لاحد"));
arasuperobject.biblemap.put("b42_9_22", new arasuperobject(42,9,22,"قائلا انه ينبغي ان ابن الانسان يتألم كثيرا ويرفض من الشيوخ ورؤساء الكهنة والكتبة ويقتل وفي اليوم الثالث يقوم"));
arasuperobject.biblemap.put("b42_9_23", new arasuperobject(42,9,23,"وقال للجميع ان اراد احد ان يأتي ورائي فلينكر نفسه ويحمل صليبه كل يوم ويتبعني"));
arasuperobject.biblemap.put("b42_9_24", new arasuperobject(42,9,24,"فان من اراد ان يخلّص نفسه يهلكها. ومن يهلك نفسه من اجلي فهذا يخلّصها"));
arasuperobject.biblemap.put("b42_9_25", new arasuperobject(42,9,25,"لانه ماذا ينتفع الانسان لو ربح العالم كله واهلك نفسه او خسرها"));
arasuperobject.biblemap.put("b42_9_26", new arasuperobject(42,9,26,"لان من استحى بي وبكلامي فبهذا يستحي ابن الانسان متى جاء بمجده ومجد الآب والملائكة القديسين"));
arasuperobject.biblemap.put("b42_9_27", new arasuperobject(42,9,27,"حقا اقول لكم ان من القيام ههنا قوما لا يذوقون الموت حتى يروا ملكوت الله"));
arasuperobject.biblemap.put("b42_9_28", new arasuperobject(42,9,28,"وبعد هذا الكلام بنحو ثمانية ايام اخذ بطرس ويوحنا ويعقوب وصعد الى جبل ليصلّي"));
arasuperobject.biblemap.put("b42_9_29", new arasuperobject(42,9,29,"وفيما هو يصلّي صارت هيئة وجهه متغيرة ولباسه مبيضا لامعا"));
arasuperobject.biblemap.put("b42_9_30", new arasuperobject(42,9,30,"واذا رجلان يتكلمان معه وهما موسى وايليا"));
arasuperobject.biblemap.put("b42_9_31", new arasuperobject(42,9,31,"اللذان ظهرا بمجد وتكلما عن خروجه الذي كان عتيدا ان يكمله في اورشليم"));
arasuperobject.biblemap.put("b42_9_32", new arasuperobject(42,9,32,"واما بطرس واللذان معه فكانوا قد تثقلوا بالنوم. فلما استيقظوا رأوا مجده والرجلين الواقفين معه"));
arasuperobject.biblemap.put("b42_9_33", new arasuperobject(42,9,33,"وفيما هما يفارقانه قال بطرس ليسوع يا معلّم جيد ان نكون ههنا. فلنصنع ثلاثة مظال. لك واحدة ولموسى واحدة ولايليا واحدة. وهو لا يعلم ما يقول"));
arasuperobject.biblemap.put("b42_9_34", new arasuperobject(42,9,34,"وفيما هو يقول ذلك كانت سحابة فظللتهم. فخافوا عندما دخلوا في السحابة"));
arasuperobject.biblemap.put("b42_9_35", new arasuperobject(42,9,35,"وصار صوت من السحابة قائلا هذا هو ابني الحبيب. له اسمعوا"));
arasuperobject.biblemap.put("b42_9_36", new arasuperobject(42,9,36,"ولما كان الصوت وجد يسوع وحده. واما هم فسكتوا ولم يخبروا احدا في تلك الايام بشيء مما ابصروه"));
arasuperobject.biblemap.put("b42_9_37", new arasuperobject(42,9,37,"وفي اليوم التالي اذ نزلوا من الجبل استقبله جمع كثير"));
arasuperobject.biblemap.put("b42_9_38", new arasuperobject(42,9,38,"واذا رجل من الجمع صرخ قائلا يا معلّم اطلب اليك. انظر الى ابني. فانه وحيد لي"));
arasuperobject.biblemap.put("b42_9_39", new arasuperobject(42,9,39,"وها روح يأخذه فيصرخ بغتة فيصرعه مزبدا وبالجهد يفارقه مرضضا اياه"));
arasuperobject.biblemap.put("b42_9_40", new arasuperobject(42,9,40,"وطلبت الى تلاميذك ان يخرجوه فلم يقدروا"));
arasuperobject.biblemap.put("b42_9_41", new arasuperobject(42,9,41,"فاجاب يسوع وقال ايها الجيل غير المؤمن والملتوي. الى متى اكون معكم واحتملكم. قدم ابنك الى هنا"));
arasuperobject.biblemap.put("b42_9_42", new arasuperobject(42,9,42,"وبينما هو آت مزقه الشيطان وصرعه. فانتهر يسوع الروح النجس وشفى الصبي وسلمه الى ابيه"));
arasuperobject.biblemap.put("b42_9_43", new arasuperobject(42,9,43,"فبهت الجميع من عظمة الله واذ كان الجميع يتعجبون من كل ما فعل يسوع قال لتلاميذه"));
arasuperobject.biblemap.put("b42_9_44", new arasuperobject(42,9,44,"ضعوا انتم هذا الكلام في آذانكم. ان ابن الانسان سوف يسلم الى ايدي الناس"));
arasuperobject.biblemap.put("b42_9_45", new arasuperobject(42,9,45,"واما هم فلم يفهموا هذا القول وكان مخفى عنهم لكي لا يفهموه. وخافوا ان يسألوه عن هذا القول"));
arasuperobject.biblemap.put("b42_9_46", new arasuperobject(42,9,46,"وداخلهم فكر من عسى ان يكون اعظم فيهم"));
arasuperobject.biblemap.put("b42_9_47", new arasuperobject(42,9,47,"فعلم يسوع فكر قلبهم واخذ ولدا واقامه عنده"));
arasuperobject.biblemap.put("b42_9_48", new arasuperobject(42,9,48,"وقال لهم. من قبل هذا الولد باسمي يقبلني. ومن قبلني يقبل الذي ارسلني. لان الاصغر فيكم جميعا هو يكون عظيما"));
arasuperobject.biblemap.put("b42_9_49", new arasuperobject(42,9,49,"فاجاب يوحنا وقال يا معلّم رأينا واحد يخرج الشياطين باسمك فمنعناه لانه ليس يتبع معنا"));
arasuperobject.biblemap.put("b42_9_50", new arasuperobject(42,9,50,"فقال له يسوع لا تمنعوه. لان من ليس علينا فهو معنا"));
arasuperobject.biblemap.put("b42_9_51", new arasuperobject(42,9,51,"وحين تمت الايام لارتفاعه ثبّت وجهه لينطلق الى اورشليم"));
arasuperobject.biblemap.put("b42_9_52", new arasuperobject(42,9,52,"وارسل امام وجهه رسلا. فذهبوا ودخلوا قرية للسامريين حتى يعدوا له"));
arasuperobject.biblemap.put("b42_9_53", new arasuperobject(42,9,53,"فلم يقبلوه لان وجهه كان متجها نحو اورشليم"));
arasuperobject.biblemap.put("b42_9_54", new arasuperobject(42,9,54,"فلما رأى ذلك تلميذاه يعقوب ويوحنا قالا يا رب أتريد ان نقول ان تنزل نار من السماء فتفنيهم كما فعل ايليا ايضا"));
arasuperobject.biblemap.put("b42_9_55", new arasuperobject(42,9,55,"فالتفت وانتهرهما وقال لستما تعلمان من اي روح انتما"));
arasuperobject.biblemap.put("b42_9_56", new arasuperobject(42,9,56,"لان ابن الانسان لم يأت ليهلك انفس الناس بل ليخلّص. فمضوا الى قرية اخرى"));
arasuperobject.biblemap.put("b42_9_57", new arasuperobject(42,9,57,"وفيما هم سائرون في الطريق قال له واحد يا سيد اتبعك اينما تمضي"));
arasuperobject.biblemap.put("b42_9_58", new arasuperobject(42,9,58,"فقال له يسوع للثعالب اوجرة ولطيور السماء اوكار. واما ابن الانسان فليس له اين يسند راسه"));
arasuperobject.biblemap.put("b42_9_59", new arasuperobject(42,9,59,"وقال لآخر اتبعني. فقال يا سيد ائذن لي ان امضي اولا وادفن ابي"));
arasuperobject.biblemap.put("b42_9_60", new arasuperobject(42,9,60,"فقال له يسوع دع الموتى يدفنون موتاهم واما انت فاذهب وناد بملكوت الله"));
arasuperobject.biblemap.put("b42_9_61", new arasuperobject(42,9,61,"وقال آخر ايضا اتبعك يا سيد ولكن ائذن لي اولا ان اودع الذين في بيتي"));
arasuperobject.biblemap.put("b42_9_62", new arasuperobject(42,9,62,"فقال له يسوع ليس احد يضع يده على المحراث وينظر الى الوراء يصلح لملكوت الله"));
arasuperobject.biblemap.put("b42_10_1", new arasuperobject(42,10,1,"وبعد ذلك عيّن الرب سبعين آخرين ايضا وارسلهم اثنين اثنين امام وجهه الى كل مدينة وموضع حيث كان هو مزمعا ان يأتي"));
arasuperobject.biblemap.put("b42_10_2", new arasuperobject(42,10,2,"فقال لهم ان الحصاد كثير ولكن الفعلة قليلون. فاطلبوا من رب الحصاد ان يرسل فعلة الى حصاده"));
arasuperobject.biblemap.put("b42_10_3", new arasuperobject(42,10,3,"اذهبوا. ها انا ارسلكم مثل حملان بين ذئاب"));
arasuperobject.biblemap.put("b42_10_4", new arasuperobject(42,10,4,"لا تحملوا كيسا ولا مزودا ولا احذية ولا تسلموا على احد في الطريق"));
arasuperobject.biblemap.put("b42_10_5", new arasuperobject(42,10,5,"واي بيت دخلتموه فقولوا اولا سلام لهذا البيت"));
arasuperobject.biblemap.put("b42_10_6", new arasuperobject(42,10,6,"فان كان هناك ابن السلام يحل سلامكم عليه وإلا فيرجع اليكم"));
arasuperobject.biblemap.put("b42_10_7", new arasuperobject(42,10,7,"واقيموا في ذلك البيت آكلين وشاربين مما عندهم. لان الفاعل مستحق اجرته. لا تنتقلوا من بيت الى بيت"));
arasuperobject.biblemap.put("b42_10_8", new arasuperobject(42,10,8,"واية مدينة دخلتموها وقبلوكم فكلوا مما يقدّم لكم"));
arasuperobject.biblemap.put("b42_10_9", new arasuperobject(42,10,9,"واشفوا المرضى الذين فيها وقولوا لهم قد اقترب منكم ملكوت الله"));
arasuperobject.biblemap.put("b42_10_10", new arasuperobject(42,10,10,"واية مدينة دخلتموها ولم يقبلوكم فاخرجوا الى شوارعها وقولوا"));
arasuperobject.biblemap.put("b42_10_11", new arasuperobject(42,10,11,"حتى الغبار الذي لصق بنا من مدينتكم ننفضه لكم. ولكن اعلموا هذا انه قد اقترب منكم ملكوت الله"));
arasuperobject.biblemap.put("b42_10_12", new arasuperobject(42,10,12,"واقول لكم انه يكون لسدوم في ذلك اليوم حالة اكثر احتمالا مما لتلك المدينة"));
arasuperobject.biblemap.put("b42_10_13", new arasuperobject(42,10,13,"ويل لك يا كورزين. ويل لك يا بيت صيدا. لانه لو صنعت في صور وصيدا القوات المصنوعة فيكما لتابتا قديما جالستين في المسوح والرماد"));
arasuperobject.biblemap.put("b42_10_14", new arasuperobject(42,10,14,"ولكن صور وصيدا يكون لهما في الدين حالة اكثر احتمالا مما لكما"));
arasuperobject.biblemap.put("b42_10_15", new arasuperobject(42,10,15,"وانت يا كفرناحوم المرتفعة الى السماء ستهبطين الى الهاوية"));
arasuperobject.biblemap.put("b42_10_16", new arasuperobject(42,10,16,"الذي يسمع منكم يسمع مني. والذي يرذلكم يرذلني. والذي يرذلني يرذل الذي ارسلني"));
arasuperobject.biblemap.put("b42_10_17", new arasuperobject(42,10,17,"فرجع السبعون بفرح قائلين يا رب حتى الشياطين تخضع لنا باسمك"));
arasuperobject.biblemap.put("b42_10_18", new arasuperobject(42,10,18,"فقال لهم رأيت الشيطان ساقطا مثل البرق من السماء"));
arasuperobject.biblemap.put("b42_10_19", new arasuperobject(42,10,19,"ها انا اعطيكم سلطانا لتدوسوا الحيّات والعقارب وكل قوة العدو ولا يضركم شيء"));
arasuperobject.biblemap.put("b42_10_20", new arasuperobject(42,10,20,"ولكن لا تفرحوا بهذا ان الارواح تخضع لكم بل افرحوا بالحري ان اسماءكم كتبت في السموات"));
arasuperobject.biblemap.put("b42_10_21", new arasuperobject(42,10,21,"وفي تلك الساعة تهلل يسوع بالروح وقال احمدك ايها الآب رب السماء والارض لانك اخفيت هذه عن الحكماء والفهماء واعلنتها للاطفال. نعم ايها الآب لان هكذا صارت المسرة امامك"));
arasuperobject.biblemap.put("b42_10_22", new arasuperobject(42,10,22,"والتفت الى تلاميذه وقال كل شيء قد دفع اليّ من ابي. وليس احد يعرف من هو الابن الا الآب ولا من هو الآب الا الابن ومن اراد الابن ان يعلن له"));
arasuperobject.biblemap.put("b42_10_23", new arasuperobject(42,10,23,"والتفت الى تلاميذه على انفراد وقال طوبى للعيون التي تنظر ما تنظرونه"));
arasuperobject.biblemap.put("b42_10_24", new arasuperobject(42,10,24,"لاني اقول لكم ان انبياء كثيرين وملوكا ارادوا ان ينظروا ما انتم تنظرون ولم ينظروا وان يسمعوا ما انتم تسمعون ولم يسمعوا"));
arasuperobject.biblemap.put("b42_10_25", new arasuperobject(42,10,25,"واذا ناموسي قام يجربه قائلا يا معلّم ماذا اعمل لارث الحياة الابدية"));
arasuperobject.biblemap.put("b42_10_26", new arasuperobject(42,10,26,"فقال له ما هو مكتوب في الناموس. كيف تقرأ"));
arasuperobject.biblemap.put("b42_10_27", new arasuperobject(42,10,27,"فاجاب وقال تحب الرب الهك من كل قلبك ومن كل نفسك ومن كل قدرتك ومن كل فكرك وقريبك مثل نفسك"));
arasuperobject.biblemap.put("b42_10_28", new arasuperobject(42,10,28,"فقال له بالصواب اجبت. افعل هذا فتحيا"));
arasuperobject.biblemap.put("b42_10_29", new arasuperobject(42,10,29,"واما هو فاذ اراد ان يبرر نفسه قال ليسوع ومن هو قريبي"));
arasuperobject.biblemap.put("b42_10_30", new arasuperobject(42,10,30,"فاجاب يسوع وقال. انسان كان نازلا من اورشليم الى اريحا فوقع بين لصوص فعروه وجرحوه ومضوا وتركوه بين حيّ وميت"));
arasuperobject.biblemap.put("b42_10_31", new arasuperobject(42,10,31,"فعرض ان كاهنا نزل في تلك الطريق فرآه وجاز مقابله"));
arasuperobject.biblemap.put("b42_10_32", new arasuperobject(42,10,32,"وكذلك لاوي ايضا اذ صار عند المكان جاء ونظر وجاز مقابله"));
arasuperobject.biblemap.put("b42_10_33", new arasuperobject(42,10,33,"ولكن سامريا مسافرا جاء اليه ولما رآه تحنن"));
arasuperobject.biblemap.put("b42_10_34", new arasuperobject(42,10,34,"فتقدم وضمد جراحاته وصب عليها زيتا وخمرا واركبه على دابته وأتى به الى فندق واعتنى به"));
arasuperobject.biblemap.put("b42_10_35", new arasuperobject(42,10,35,"وفي الغد لما مضى اخرج دينارين واعطاهما لصاحب الفندق وقال له اعتن به ومهما انفقت اكثر فعند رجوعي اوفيك"));
arasuperobject.biblemap.put("b42_10_36", new arasuperobject(42,10,36,"فاي هؤلاء الثلاثة ترى صار قريبا للذي وقع بين اللصوص"));
arasuperobject.biblemap.put("b42_10_37", new arasuperobject(42,10,37,"فقال الذي صنع معه الرحمة. فقال له يسوع اذهب انت ايضا واصنع هكذا"));
arasuperobject.biblemap.put("b42_10_38", new arasuperobject(42,10,38,"وفيما هم سائرون دخل قرية فقبلته امرأة اسمها مرثا في بيتها"));
arasuperobject.biblemap.put("b42_10_39", new arasuperobject(42,10,39,"وكانت لهذه اخت تدعى مريم التي جلست عند قدمي يسوع وكانت تسمع كلامه"));
arasuperobject.biblemap.put("b42_10_40", new arasuperobject(42,10,40,"واما مرثا فكانت مرتبكة في خدمة كثيرة. فوقفت وقالت يا رب أما تبالي بان اختي قد تركتني اخدم وحدي. فقل لها ان تعينني"));
arasuperobject.biblemap.put("b42_10_41", new arasuperobject(42,10,41,"فاجاب يسوع وقال لها مرثا مرثا انت تهتمين وتضطربين لاجل امور كثيرة"));
arasuperobject.biblemap.put("b42_10_42", new arasuperobject(42,10,42,"ولكن الحاجة الى واحد. فاختارت مريم النصيب الصالح الذي لن ينزع منها"));
arasuperobject.biblemap.put("b42_11_1", new arasuperobject(42,11,1,"واذ كان يصلّي في موضع لما فرغ قال واحد من تلاميذه يا رب علّمنا ان نصلّي كما علّم يوحنا ايضا تلاميذه"));
arasuperobject.biblemap.put("b42_11_2", new arasuperobject(42,11,2,"فقال لهم متى صلّيتم فقولوا ابانا الذي في السموات. ليتقدس اسمك ليأت ملكوتك. لتكن مشيئتك كما في السماء كذلك على الارض"));
arasuperobject.biblemap.put("b42_11_3", new arasuperobject(42,11,3,"خبزنا كفافنا أعطنا كل يوم"));
arasuperobject.biblemap.put("b42_11_4", new arasuperobject(42,11,4,"واغفر لنا خطايانا لاننا نحن ايضا نغفر لكل من يذنب الينا. ولا تدخلنا في تجربة لكن نجنا من الشرير"));
arasuperobject.biblemap.put("b42_11_5", new arasuperobject(42,11,5,"ثم قال لهم من منكم يكون له صديق ويمضي اليه نصف الليل ويقول له يا صديق اقرضني ثلاثة ارغفة"));
arasuperobject.biblemap.put("b42_11_6", new arasuperobject(42,11,6,"لان صديقا لي جاءني من سفر وليس لي ما اقدم له"));
arasuperobject.biblemap.put("b42_11_7", new arasuperobject(42,11,7,"فيجيب ذلك من داخل ويقول لا تزعجني. الباب مغلق الآن واولادي معي في الفراش. لا اقدر ان اقوم واعطيك"));
arasuperobject.biblemap.put("b42_11_8", new arasuperobject(42,11,8,"اقول لكم وان كان لا يقوم ويعطيه لكونه صديقه فانه من اجل لجاجته يقوم ويعطيه قدر ما يحتاج"));
arasuperobject.biblemap.put("b42_11_9", new arasuperobject(42,11,9,"وانا اقول لكم اسألوا تعطوا. اطلبوا تجدوا. اقرعوا يفتح لكم"));
arasuperobject.biblemap.put("b42_11_10", new arasuperobject(42,11,10,"لان كل من يسأل يأخذ ومن يطلب يجد. ومن يقرع يفتح له"));
arasuperobject.biblemap.put("b42_11_11", new arasuperobject(42,11,11,"فمن منكم وهو اب يسأله ابنه خبزا أفيعطيه حجرا. او سمكة أفيعطيه حية بدل السمكة"));
arasuperobject.biblemap.put("b42_11_12", new arasuperobject(42,11,12,"او اذا سأله بيضة أفيعطيه عقربا"));
arasuperobject.biblemap.put("b42_11_13", new arasuperobject(42,11,13,"فان كنتم وانتم اشرار تعرفون ان تعطوا اولادكم عطايا جيدة فكم بالحري الآب الذي من السماء يعطي الروح القدس للذين يسألونه"));
arasuperobject.biblemap.put("b42_11_14", new arasuperobject(42,11,14,"وكان يخرج شيطانا وكان ذلك اخرس. فلما اخرج الشيطان تكلم الاخرس. فتعجب الجموع"));
arasuperobject.biblemap.put("b42_11_15", new arasuperobject(42,11,15,"واما قوم منهم فقالوا ببعلزبول رئيس الشياطين يخرج الشياطين"));
arasuperobject.biblemap.put("b42_11_16", new arasuperobject(42,11,16,"وآخرون طلبوا منه آية من السماء يجربونه"));
arasuperobject.biblemap.put("b42_11_17", new arasuperobject(42,11,17,"فعلم افكارهم وقال لهم كل مملكة منقسمة على ذاتها تخرب. وبيت منقسم على بيت يسقط"));
arasuperobject.biblemap.put("b42_11_18", new arasuperobject(42,11,18,"فان كان الشيطان ايضا ينقسم على ذاته فكيف تثبت مملكته. لانكم تقولون اني ببعلزبول اخرج الشياطين"));
arasuperobject.biblemap.put("b42_11_19", new arasuperobject(42,11,19,"فان كنت انا ببعلزبول اخرج الشياطين فابناؤكم بمن يخرجون. لذلك هم يكونون قضاتكم"));
arasuperobject.biblemap.put("b42_11_20", new arasuperobject(42,11,20,"ولكن ان كنت باصبع الله اخرج الشياطين فقد اقبل عليكم ملكوت الله"));
arasuperobject.biblemap.put("b42_11_21", new arasuperobject(42,11,21,"حينما يحفظ القوي داره متسلحا تكون امواله في امان"));
arasuperobject.biblemap.put("b42_11_22", new arasuperobject(42,11,22,"ولكن متى جاء من هو اقوى منه فانه يغلبه وينزع سلاحه الكامل الذي اتكل عليه ويوزع غنائمه"));
arasuperobject.biblemap.put("b42_11_23", new arasuperobject(42,11,23,"من ليس معي فهو عليّ. ومن لا يجمع معي فهو يفرق"));
arasuperobject.biblemap.put("b42_11_24", new arasuperobject(42,11,24,"متى خرج الروح النجس من الانسان يجتاز في اماكن ليس فيها ماء يطلب راحة. واذ لا يجد يقول ارجع الى بيتي الذي خرجت منه"));
arasuperobject.biblemap.put("b42_11_25", new arasuperobject(42,11,25,"فيأتي ويجده مكنوسا مزيّنا"));
arasuperobject.biblemap.put("b42_11_26", new arasuperobject(42,11,26,"ثم يذهب ويأخذ سبعة ارواح أخر اشر منه فتدخل وتسكن هناك. فتصير اواخر ذلك الانسان اشر من اوائله"));
arasuperobject.biblemap.put("b42_11_27", new arasuperobject(42,11,27,"وفيما هو يتكلم بهذا رفعت امرأة صوتها من الجمع وقالت له طوبى للبطن الذي حملك والثديين اللذين رضعتهما"));
arasuperobject.biblemap.put("b42_11_28", new arasuperobject(42,11,28,"اما هو فقال بل طوبى للذين يسمعون كلام الله ويحفظونه"));
arasuperobject.biblemap.put("b42_11_29", new arasuperobject(42,11,29,"وفيما كان الجموع مزدحمين ابتدأ يقول. هذا الجيل شرير. يطلب آية ولا تعطى له آية الا آية يونان النبي"));
arasuperobject.biblemap.put("b42_11_30", new arasuperobject(42,11,30,"لانه كما كان يونان آية لاهل نينوى كذلك يكون ابن الانسان ايضا لهذا الجيل"));
arasuperobject.biblemap.put("b42_11_31", new arasuperobject(42,11,31,"ملكة التيمن ستقوم في الدين مع رجال هذا الجيل وتدينهم. لانها أتت من اقاصي الارض لتسمع حكمة سليمان وهوذا اعظم من سليمان ههنا"));
arasuperobject.biblemap.put("b42_11_32", new arasuperobject(42,11,32,"رجال نينوى سيقومون في الدين مع هذا الجيل ويدينونه. لانهم تابوا بمناداة يونان. وهوذا اعظم من يونان ههنا"));
arasuperobject.biblemap.put("b42_11_33", new arasuperobject(42,11,33,"ليس احد يوقد سراجا ويضعه في خفية ولا تحت المكيال بل على المنارة لكي ينظر الداخلون النور"));
arasuperobject.biblemap.put("b42_11_34", new arasuperobject(42,11,34,"سراج الجسد هو العين. فمتى كانت عينك بسيطة فجسدك كله يكون نيّرا. ومتى كانت شريرة فجسدك يكون مظلما"));
arasuperobject.biblemap.put("b42_11_35", new arasuperobject(42,11,35,"انظر اذا لئلا يكون النور الذي فيك ظلمة"));
arasuperobject.biblemap.put("b42_11_36", new arasuperobject(42,11,36,"فان كان جسدك كله نيّرا ليس فيه جزء مظلم يكون نيّرا كله كما حينما يضيء لك السراج بلمعانه"));
arasuperobject.biblemap.put("b42_11_37", new arasuperobject(42,11,37,"وفيما هو يتكلم سأله فريسي ان يتغدى عنده. فدخل واتكأ"));
arasuperobject.biblemap.put("b42_11_38", new arasuperobject(42,11,38,"واما الفريسي فلما رأى ذلك تعجب انه لم يغتسل اولا قبل الغداء"));
arasuperobject.biblemap.put("b42_11_39", new arasuperobject(42,11,39,"فقال له الرب انتم الآن ايها الفريسيون تنقون خارج الكاس والقصعة واما باطنكم فمملوء اختطافا وخبثا"));
arasuperobject.biblemap.put("b42_11_40", new arasuperobject(42,11,40,"يا اغبياء أليس الذي صنع الخارج صنع الداخل ايضا"));
arasuperobject.biblemap.put("b42_11_41", new arasuperobject(42,11,41,"بل اعطوا ما عندكم صدقة فهوذا كل شيء يكون نقيا لكم"));
arasuperobject.biblemap.put("b42_11_42", new arasuperobject(42,11,42,"ولكن ويل لكم ايها الفريسيون لانكم تعشّرون النعنع والسذاب وكل بقل وتتجاوزون عن الحق ومحبة الله. كان ينبغي ان تعملوا هذه ولا تتركوا تلك"));
arasuperobject.biblemap.put("b42_11_43", new arasuperobject(42,11,43,"ويل لكم ايها الفريسيون لانكم تحبون المجلس الاول في المجامع والتحيات في الاسواق"));
arasuperobject.biblemap.put("b42_11_44", new arasuperobject(42,11,44,"ويل لكم ايها الكتبة والفريسيون المراؤون لانكم مثل القبور المختفية والذين يمشون عليها لا يعلمون"));
arasuperobject.biblemap.put("b42_11_45", new arasuperobject(42,11,45,"فاجاب واحد من الناموسيين وقال له يا معلّم حين تقول هذا تشتمنا نحن ايضا"));
arasuperobject.biblemap.put("b42_11_46", new arasuperobject(42,11,46,"فقال وويل لكم انتم ايها الناموسيون لانكم تحمّلون الناس احمالا عسرة الحمل وانتم لا تمسون الاحمال باحدى اصابعكم"));
arasuperobject.biblemap.put("b42_11_47", new arasuperobject(42,11,47,"ويل لكم لانكم تبنون قبور الانبياء وآباؤكم قتلوهم"));
arasuperobject.biblemap.put("b42_11_48", new arasuperobject(42,11,48,"اذا تشهدون وترضون باعمال آبائكم. لانهم هم قتلوهم وانتم تبنون قبورهم"));
arasuperobject.biblemap.put("b42_11_49", new arasuperobject(42,11,49,"لذلك ايضا قالت حكمة الله اني ارسل اليهم انبياء ورسلا فيقتلون منهم ويطردون"));
arasuperobject.biblemap.put("b42_11_50", new arasuperobject(42,11,50,"لكي يطلب من هذا الجيل دم جميع الانبياء المهرق منذ انشاء العالم"));
arasuperobject.biblemap.put("b42_11_51", new arasuperobject(42,11,51,"من دم هابيل الى دم زكريا الذي اهلك بين المذبح والبيت. نعم اقول لكم انه يطلب من هذا الجيل"));
arasuperobject.biblemap.put("b42_11_52", new arasuperobject(42,11,52,"ويل لكم ايها الناموسيون لانكم اخذتم مفتاح المعرفة. ما دخلتم انتم والداخلون منعتموهم"));
arasuperobject.biblemap.put("b42_11_53", new arasuperobject(42,11,53,"وفيما هو يكلمهم بهذا ابتدأ الكتبة والفريسيون يحنقون جدا ويصادرونه على أمور كثيرة"));
arasuperobject.biblemap.put("b42_11_54", new arasuperobject(42,11,54,"وهم يراقبونه طالبين ان يصطادوا شيئا من فمه لكي يشتكوا عليه"));
arasuperobject.biblemap.put("b42_12_1", new arasuperobject(42,12,1,"وفي اثناء ذلك اذ اجتمع ربوات الشعب حتى كان بعضهم يدوس بعضا ابتدأ يقول لتلاميذه اولا تحرزوا لانفسكم من خمير الفريسيين الذي هو الرياء"));
arasuperobject.biblemap.put("b42_12_2", new arasuperobject(42,12,2,"فليس مكتوم لن يستعلن ولا خفي لن يعرف"));
arasuperobject.biblemap.put("b42_12_3", new arasuperobject(42,12,3,"لذلك كل ما قلتموه في الظلمة يسمع في النور وما كلمتم به الاذن في المخادع ينادى به على السطوح"));
arasuperobject.biblemap.put("b42_12_4", new arasuperobject(42,12,4,"ولكن اقول لكم يا احبائي لا تخافوا من الذين يقتلون الجسد وبعد ذلك ليس لهم ما يفعلون اكثر"));
arasuperobject.biblemap.put("b42_12_5", new arasuperobject(42,12,5,"بل اريكم ممن تخافون. خافوا من الذي بعدما يقتل له سلطان ان يلقي في جهنم. نعم اقول لكم من هذا خافوا"));
arasuperobject.biblemap.put("b42_12_6", new arasuperobject(42,12,6,"أليست خمسة عصافير تباع بفلسين. وواحد منها ليس منسيا امام الله"));
arasuperobject.biblemap.put("b42_12_7", new arasuperobject(42,12,7,"بل شعور رؤوسكم ايضا جميعها محصاة. فلا تخافوا انتم افضل من عصافير كثيرة"));
arasuperobject.biblemap.put("b42_12_8", new arasuperobject(42,12,8,"واقول لكم كل من اعترف بي قدام الناس يعترف به ابن الانسان قدام ملائكة الله"));
arasuperobject.biblemap.put("b42_12_9", new arasuperobject(42,12,9,"ومن انكرني قدام الناس ينكر قدام ملائكة الله"));
arasuperobject.biblemap.put("b42_12_10", new arasuperobject(42,12,10,"وكل من قال كلمة على ابن الانسان يغفر له. واما من جدف على الروح القدس فلا يغفر له"));
arasuperobject.biblemap.put("b42_12_11", new arasuperobject(42,12,11,"ومتى قدموكم الى المجامع والرؤساء والسلاطين فلا تهتموا كيف او بما تحتجّون او بما تقولون"));
arasuperobject.biblemap.put("b42_12_12", new arasuperobject(42,12,12,"لان الروح القدس يعلّمكم في تلك الساعة ما يجب ان تقولوه"));
arasuperobject.biblemap.put("b42_12_13", new arasuperobject(42,12,13,"وقال له واحد من الجمع يا معلّم قل لاخي ان يقاسمني الميراث"));
arasuperobject.biblemap.put("b42_12_14", new arasuperobject(42,12,14,"فقال له يا انسان من اقامني عليكما قاضيا او مقسّما"));
arasuperobject.biblemap.put("b42_12_15", new arasuperobject(42,12,15,"وقال لهم انظروا وتحفّظوا من الطمع. فانه متى كان لاحد كثير فليست حياته من امواله"));
arasuperobject.biblemap.put("b42_12_16", new arasuperobject(42,12,16,"وضرب لهم مثلا قائلا. انسان غني اخصبت كورته"));
arasuperobject.biblemap.put("b42_12_17", new arasuperobject(42,12,17,"ففكر في نفسه قائلا ماذا اعمل لان ليس لي موضع اجمع فيه اثماري"));
arasuperobject.biblemap.put("b42_12_18", new arasuperobject(42,12,18,"وقال اعمل هذا. اهدم مخازني وابني اعظم واجمع هناك جميع غلاتي وخيراتي"));
arasuperobject.biblemap.put("b42_12_19", new arasuperobject(42,12,19,"واقول لنفسي يا نفس لك خيرات كثيرة موضوعة لسنين كثيرة. استريحي وكلي واشربي وافرحي"));
arasuperobject.biblemap.put("b42_12_20", new arasuperobject(42,12,20,"فقال له الله يا غبي هذه الليلة تطلب نفسك منك. فهذه التي اعددتها لمن تكون"));
arasuperobject.biblemap.put("b42_12_21", new arasuperobject(42,12,21,"هكذا الذي يكنز لنفسه وليس هو غنيا للّه"));
arasuperobject.biblemap.put("b42_12_22", new arasuperobject(42,12,22,"وقال لتلاميذه. من اجل هذا اقول لكم لا تهتموا لحياتكم بما تأكلون ولا للجسد بما تلبسون"));
arasuperobject.biblemap.put("b42_12_23", new arasuperobject(42,12,23,"الحياة افضل من الطعام والجسد افضل من اللباس"));
arasuperobject.biblemap.put("b42_12_24", new arasuperobject(42,12,24,"تأملوا الغربان. انها لا تزرع ولا تحصد وليس لها مخدع ولا مخزن والله يقيتها. كم انتم بالحري افضل من الطيور"));
arasuperobject.biblemap.put("b42_12_25", new arasuperobject(42,12,25,"ومن منكم اذا اهتم يقدر ان يزيد على قامته ذراعا واحدة"));
arasuperobject.biblemap.put("b42_12_26", new arasuperobject(42,12,26,"فان كنتم لا تقدرون ولا على الاصغر فلماذا تهتمون بالبواقي"));
arasuperobject.biblemap.put("b42_12_27", new arasuperobject(42,12,27,"تأملوا الزنابق كيف تنمو. لا تتعب ولا تغزل. ولكن اقول لكم انه ولا سليمان في كل مجده كان يلبس كواحدة منها"));
arasuperobject.biblemap.put("b42_12_28", new arasuperobject(42,12,28,"فان كان العشب الذي يوجد اليوم في الحقل ويطرح غدا في التنور يلبسه الله هكذا فكم بالحري يلبسكم انتم يا قليلي الايمان"));
arasuperobject.biblemap.put("b42_12_29", new arasuperobject(42,12,29,"فلا تطلبوا انتم ما تأكلون وما تشربون ولا تقلقوا"));
arasuperobject.biblemap.put("b42_12_30", new arasuperobject(42,12,30,"فان هذه كلها تطلبها امم العالم. واما انتم فابوكم يعلم انكم تحتاجون الى هذه"));
arasuperobject.biblemap.put("b42_12_31", new arasuperobject(42,12,31,"بل اطلبوا ملكوت الله وهذه كلها تزاد لكم"));
arasuperobject.biblemap.put("b42_12_32", new arasuperobject(42,12,32,"لا تخف ايها القطيع الصغير لان اباكم قد سرّ ان يعطيكم الملكوت"));
arasuperobject.biblemap.put("b42_12_33", new arasuperobject(42,12,33,"بيعوا مالكم واعطوا صدقة. اعملوا لكم اكياسا لا تفنى وكنزا لا ينفد في السموات حيث لا يقرب سارق ولا يبلي سوس"));
arasuperobject.biblemap.put("b42_12_34", new arasuperobject(42,12,34,"لانه حيث يكون كنزكم هناك يكون قلبكم ايضا"));
arasuperobject.biblemap.put("b42_12_35", new arasuperobject(42,12,35,"لتكن احقاؤكم ممنطقة وسرجكم موقدة"));
arasuperobject.biblemap.put("b42_12_36", new arasuperobject(42,12,36,"وانتم مثل اناس ينتظرون سيدهم متى يرجع من العرس حتى اذا جاء وقرع يفتحون له للوقت"));
arasuperobject.biblemap.put("b42_12_37", new arasuperobject(42,12,37,"طوبى لاولئك العبيد الذين اذا جاء سيدهم يجدهم ساهرين. الحق اقول لكم انه يتمنطق ويتكئهم ويتقدم ويخدمهم"));
arasuperobject.biblemap.put("b42_12_38", new arasuperobject(42,12,38,"وان أتى في الهزيع الثاني او أتى في الهزيع الثالث ووجدهم هكذا فطوبى لاولئك العبيد"));
arasuperobject.biblemap.put("b42_12_39", new arasuperobject(42,12,39,"وانما اعلموا هذا انه لو عرف رب البيت في اي ساعة يأتي السارق لسهر ولم يدع بيته ينقب"));
arasuperobject.biblemap.put("b42_12_40", new arasuperobject(42,12,40,"فكونوا انتم اذا مستعدين لانه في ساعة لا تظنون يأتي ابن الانسان"));
arasuperobject.biblemap.put("b42_12_41", new arasuperobject(42,12,41,"فقال له بطرس يا رب ألنا تقول هذا المثل ام للجميع ايضا"));
arasuperobject.biblemap.put("b42_12_42", new arasuperobject(42,12,42,"فقال الرب فمن هو الوكيل الامين الحكيم الذي يقيمه سيده على خدمه ليعطيهم العلوفة في حينها"));
arasuperobject.biblemap.put("b42_12_43", new arasuperobject(42,12,43,"طوبى لذلك العبد الذي اذا جاء سيده يجده يفعل هكذا"));
arasuperobject.biblemap.put("b42_12_44", new arasuperobject(42,12,44,"بالحق اقول لكم انه يقيمه على جميع امواله"));
arasuperobject.biblemap.put("b42_12_45", new arasuperobject(42,12,45,"ولكن ان قال ذلك العبد في قلبه سيدي يبطئ قدومه. فيبتدئ يضرب الغلمان والجواري ويأكل ويشرب ويسكر"));
arasuperobject.biblemap.put("b42_12_46", new arasuperobject(42,12,46,"يأتي سيد ذلك العبد في يوم لا ينتظره وفي ساعة لا يعرفها فيقطعه ويجعل نصيبه مع الخائنين"));
arasuperobject.biblemap.put("b42_12_47", new arasuperobject(42,12,47,"واما ذلك العبد الذي يعلم ارادة سيده ولا يستعد ولا يفعل بحسب ارادته فيضرب كثيرا"));
arasuperobject.biblemap.put("b42_12_48", new arasuperobject(42,12,48,"ولكن الذي لا يعلم ويفعل ما يستحق ضربات يضرب قليلا. فكل من أعطي كثيرا يطلب منه كثير ومن يودعونه كثيرا يطالبونه باكثر"));
arasuperobject.biblemap.put("b42_12_49", new arasuperobject(42,12,49,"جئت لألقي نارا على الارض. فماذا اريد لو اضطرمت"));
arasuperobject.biblemap.put("b42_12_50", new arasuperobject(42,12,50,"ولي صبغة اصطبغها وكيف انحصر حتى تكمل"));
arasuperobject.biblemap.put("b42_12_51", new arasuperobject(42,12,51,"أتظنون اني جئت لاعطي سلاما على الارض. كلا اقول لكم. بل انقساما"));
arasuperobject.biblemap.put("b42_12_52", new arasuperobject(42,12,52,"لانه يكون من الآن خمسة في بيت واحد منقسمين ثلثة على اثنين واثنان على ثلثة"));
arasuperobject.biblemap.put("b42_12_53", new arasuperobject(42,12,53,"ينقسم الاب على الابن والابن على الاب. والام على البنت والبنت على الام. والحماة على كنتها والكنة على حماتها"));
arasuperobject.biblemap.put("b42_12_54", new arasuperobject(42,12,54,"ثم قال ايضا للجموع. اذا رأيتم السحاب تطلع من المغارب فللوقت تقولون انه يأتي مطر. فيكون هكذا"));
arasuperobject.biblemap.put("b42_12_55", new arasuperobject(42,12,55,"واذا رأيتم ريح الجنوب تهب تقولون انه سيكون حر. فيكون"));
arasuperobject.biblemap.put("b42_12_56", new arasuperobject(42,12,56,"يا مراؤون تعرفون ان تميّزوا وجه الارض والسماء واما هذا الزمان فكيف لا تميّزونه"));
arasuperobject.biblemap.put("b42_12_57", new arasuperobject(42,12,57,"ولماذا لا تحكمون بالحق من قبل نفوسكم"));
arasuperobject.biblemap.put("b42_12_58", new arasuperobject(42,12,58,"حينما تذهب مع خصمك الى الحاكم ابذل الجهد وانت في الطريق لتتخلّص منه. لئلا يجرك الى القاضي ويسلمك القاضي الى الحاكم فيلقيك الحاكم في السجن"));
arasuperobject.biblemap.put("b42_12_59", new arasuperobject(42,12,59,"اقول لك لا تخرج من هناك حتى توفي الفلس الاخير"));
arasuperobject.biblemap.put("b42_13_1", new arasuperobject(42,13,1,"وكان حاضرا في ذلك الوقت قوم يخبرونه عن الجليليين الذين خلط بيلاطس دمهم بذبائحهم"));
arasuperobject.biblemap.put("b42_13_2", new arasuperobject(42,13,2,"فاجاب يسوع وقال لهم أتظنون ان هؤلاء الجليليين كانوا خطاة اكثر من كل الجليليين لانهم كابدوا مثل هذا"));
arasuperobject.biblemap.put("b42_13_3", new arasuperobject(42,13,3,"كلا اقول لكم. بل ان لم تتوبوا فجميعكم كذلك تهلكون"));
arasuperobject.biblemap.put("b42_13_4", new arasuperobject(42,13,4,"او اولئك الثمانية عشر الذين سقط عليهم البرج في سلوام وقتلهم أتظنون ان هؤلاء كانوا مذنبين اكثر من جميع الناس الساكنين في اورشليم"));
arasuperobject.biblemap.put("b42_13_5", new arasuperobject(42,13,5,"كلا اقول لكم. بل ان لم تتوبوا فجميعكم كذلك تهلكون"));
arasuperobject.biblemap.put("b42_13_6", new arasuperobject(42,13,6,"وقال هذا المثل. كانت لواحد شجرة تين مغروسة في كرمه. فأتى يطلب فيها ثمرا ولم يجد"));
arasuperobject.biblemap.put("b42_13_7", new arasuperobject(42,13,7,"فقال للكرام هوذا ثلاثة سنين آتي اطلب ثمرا في هذه التينة ولم اجد. اقطعها. لماذا تبطل الارض ايضا"));
arasuperobject.biblemap.put("b42_13_8", new arasuperobject(42,13,8,"فاجاب وقال له يا سيد اتركها هذه السنة ايضا حتى انقب حولها واضع زبلا"));
arasuperobject.biblemap.put("b42_13_9", new arasuperobject(42,13,9,"فان صنعت ثمرا وإلا ففيما بعد تقطعها"));
arasuperobject.biblemap.put("b42_13_10", new arasuperobject(42,13,10,"وكان يعلّم في احد المجامع في السبت"));
arasuperobject.biblemap.put("b42_13_11", new arasuperobject(42,13,11,"واذا امرأة كان بها روح ضعف ثماني عشرة سنة وكانت منحنية ولم تقدر ان تنتصب البتة"));
arasuperobject.biblemap.put("b42_13_12", new arasuperobject(42,13,12,"فلما رآها يسوع دعاها وقال لها يا امرأة انك محلولة من ضعفك"));
arasuperobject.biblemap.put("b42_13_13", new arasuperobject(42,13,13,"ووضع عليها يديه ففي الحال استقامت ومجدت الله"));
arasuperobject.biblemap.put("b42_13_14", new arasuperobject(42,13,14,"فاجاب رئيس المجمع وهو مغتاظ لان يسوع ابرأ في السبت وقال للجمع هي ستة ايام ينبغي فيها العمل ففي هذه ائتوا واستشفوا وليس في يوم السبت"));
arasuperobject.biblemap.put("b42_13_15", new arasuperobject(42,13,15,"فاجابه الرب وقال يا مرائي ألا يحل كل واحد في السبت ثوره او حماره من المذود ويمضي به ويسقيه"));
arasuperobject.biblemap.put("b42_13_16", new arasuperobject(42,13,16,"وهذه وهي ابنة ابراهيم قد ربطها الشيطان ثماني عشرة سنة أما كان ينبغي ان تحل من هذا الرباط في يوم السبت"));
arasuperobject.biblemap.put("b42_13_17", new arasuperobject(42,13,17,"واذ قال هذا اخجل جميع الذين كانوا يعاندونه وفرح كل الجمع بجميع الاعمال المجيدة الكائنة منه"));
arasuperobject.biblemap.put("b42_13_18", new arasuperobject(42,13,18,"فقال ماذا يشبه ملكوت الله وبماذا اشبهه"));
arasuperobject.biblemap.put("b42_13_19", new arasuperobject(42,13,19,"يشبه حبة خردل اخذها انسان والقاها في بستانه فنمت وصارت شجرة كبيرة وتآوت طيور السماء في اغصانها"));
arasuperobject.biblemap.put("b42_13_20", new arasuperobject(42,13,20,"وقال ايضا بماذا اشبه ملكوت الله"));
arasuperobject.biblemap.put("b42_13_21", new arasuperobject(42,13,21,"يشبه خميرة اخذتها امرأة وخبأتها في ثلاثة اكيال دقيق حتى اختمر الجميع"));
arasuperobject.biblemap.put("b42_13_22", new arasuperobject(42,13,22,"واجتاز في مدن وقرى يعلّم ويسافر نحو اورشليم"));
arasuperobject.biblemap.put("b42_13_23", new arasuperobject(42,13,23,"فقال له واحد يا سيد أقليل هم الذين يخلصون. فقال لهم"));
arasuperobject.biblemap.put("b42_13_24", new arasuperobject(42,13,24,"اجتهدوا ان تدخلوا من الباب الضيق. فاني اقول لكم ان كثيرين سيطلبون ان يدخلوا ولا يقدرون"));
arasuperobject.biblemap.put("b42_13_25", new arasuperobject(42,13,25,"من بعدما يكون رب البيت قد قام واغلق الباب وابتدأتم تقفون خارجا وتقرعون الباب قائلين يا رب يا رب افتح لنا يجيب ويقول لكم لا اعرفكم من اين انتم"));
arasuperobject.biblemap.put("b42_13_26", new arasuperobject(42,13,26,"حينئذ تبتدئون تقولون اكلنا قدامك وشربنا وعلّمت في شوارعنا"));
arasuperobject.biblemap.put("b42_13_27", new arasuperobject(42,13,27,"فيقول اقول لكم لا اعرفكم من اين انتم. تباعدوا عني يا جميع فاعلي الظلم"));
arasuperobject.biblemap.put("b42_13_28", new arasuperobject(42,13,28,"هناك يكون البكاء وصرير الاسنان متى رأيتم ابراهيم واسحق ويعقوب وجميع الانبياء في ملكوت الله وانتم مطروحون خارجا"));
arasuperobject.biblemap.put("b42_13_29", new arasuperobject(42,13,29,"يأتون من المشارق ومن المغارب ومن الشمال والجنوب ويتكئون في ملكوت الله"));
arasuperobject.biblemap.put("b42_13_30", new arasuperobject(42,13,30,"وهوذا آخرون يكونون اولين واولون يكونون آخرين"));
arasuperobject.biblemap.put("b42_13_31", new arasuperobject(42,13,31,"في ذلك اليوم تقدم بعض الفريسيين قائلين له اخرج واذهب من ههنا لان هيرودس يريد ان يقتلك"));
arasuperobject.biblemap.put("b42_13_32", new arasuperobject(42,13,32,"فقال لهم امضوا وقولوا لهذا الثعلب ها انا اخرج شياطين واشفي اليوم وغدا وفي اليوم الثالث اكمل"));
arasuperobject.biblemap.put("b42_13_33", new arasuperobject(42,13,33,"بل ينبغي ان اسير اليوم وغدا وما يليه لانه لا يمكن ان يهلك نبي خارجا عن اورشليم"));
arasuperobject.biblemap.put("b42_13_34", new arasuperobject(42,13,34,"يا اورشليم يا اورشليم يا قاتلة الانبياء وراجمة المرسلين اليها كم مرة اردت ان اجمع اولادك كما تجمع الدجاجة فراخها تحت جناحيها ولم تريدوا"));
arasuperobject.biblemap.put("b42_13_35", new arasuperobject(42,13,35,"هوذا بيتكم يترك لكم خرابا. والحق اقول لكم انكم لا ترونني حتى يأتي وقت تقولون فيه مبارك الآتي باسم الرب"));
arasuperobject.biblemap.put("b42_14_1", new arasuperobject(42,14,1,"واذ جاء الى بيت احد رؤساء الفريسيين في السبت ليأكل خبزا كانوا يراقبونه"));
arasuperobject.biblemap.put("b42_14_2", new arasuperobject(42,14,2,"واذا انسان مستسق كان قدامه"));
arasuperobject.biblemap.put("b42_14_3", new arasuperobject(42,14,3,"فاجاب يسوع وكلم الناموسيين والفريسيين قائلا هل يحل الابراء في السبت"));
arasuperobject.biblemap.put("b42_14_4", new arasuperobject(42,14,4,"فسكتوا. فامسكه وابرأه واطلقه"));
arasuperobject.biblemap.put("b42_14_5", new arasuperobject(42,14,5,"ثم اجابهم وقال من منكم يسقط حماره او ثوره في بئر ولا ينشله حالا في يوم السبت"));
arasuperobject.biblemap.put("b42_14_6", new arasuperobject(42,14,6,"فلم يقدروا ان يجيبوه عن ذلك"));
arasuperobject.biblemap.put("b42_14_7", new arasuperobject(42,14,7,"وقال للمدعوين مثلا وهو يلاحظ كيف اختاروا المتكآت الاولى قائلا لهم"));
arasuperobject.biblemap.put("b42_14_8", new arasuperobject(42,14,8,"متى دعيت من احد الى عرس فلا تتكئ في المتكإ الاول لعل اكرم منك يكون قد دعي منه"));
arasuperobject.biblemap.put("b42_14_9", new arasuperobject(42,14,9,"فيأتي الذي دعاك واياه ويقول لك اعطي مكانا لهذا. فحينئذ تبتدئ بخجل تأخذ الموضع الاخير"));
arasuperobject.biblemap.put("b42_14_10", new arasuperobject(42,14,10,"بل متى دعيت فاذهب واتكئ في الموضع الاخير حتى اذا جاء الذي دعاك يقول لك يا صديق ارتفع الى فوق. حينئذ يكون لك مجد امام المتكئين معك"));
arasuperobject.biblemap.put("b42_14_11", new arasuperobject(42,14,11,"لان كل من يرفع نفسه يتضع ومن يضع نفسه يرتفع"));
arasuperobject.biblemap.put("b42_14_12", new arasuperobject(42,14,12,"وقال ايضا للذي دعاه اذا صنعت غذاء او عشاء فلا تدع اصدقاءك ولا اخوتك ولا اقرباءك ولا الجيران الاغنياء لئلا يدعوك هم ايضا فتكون لك مكافاة"));
arasuperobject.biblemap.put("b42_14_13", new arasuperobject(42,14,13,"بل اذا صنعت ضيافة فادع المساكين الجدع العرج العمي"));
arasuperobject.biblemap.put("b42_14_14", new arasuperobject(42,14,14,"فيكون لك الطوبى اذ ليس لهم حتى يكافوك. لانك تكافى في قيامة الابرار"));
arasuperobject.biblemap.put("b42_14_15", new arasuperobject(42,14,15,"فلما سمع ذلك واحد من المتكئين قال له طوبى لمن يأكل خبزا في ملكوت الله"));
arasuperobject.biblemap.put("b42_14_16", new arasuperobject(42,14,16,"فقال له. انسان صنع عشاء عظيما ودعا كثيرين"));
arasuperobject.biblemap.put("b42_14_17", new arasuperobject(42,14,17,"وارسل عبده في ساعة العشاء ليقول للمدعوين تعالوا لان كل شيء قد أعد"));
arasuperobject.biblemap.put("b42_14_18", new arasuperobject(42,14,18,"فابتدأ الجميع برأي واحد يستعفون. قال له الاول اني اشتريت حقلا وانا مضطر ان اخرج وانظره. اسألك ان تعفيني"));
arasuperobject.biblemap.put("b42_14_19", new arasuperobject(42,14,19,"وقال آخر اني اشتريت خمسة ازواج بقر وانا ماض لامتحنها. اسألك ان تعفيني"));
arasuperobject.biblemap.put("b42_14_20", new arasuperobject(42,14,20,"وقال آخر اني تزوجت بامرأة فلذلك لا اقدر ان اجيء"));
arasuperobject.biblemap.put("b42_14_21", new arasuperobject(42,14,21,"فأتى ذلك العبد واخبر سيده بذلك. حينئذ غضب رب البيت وقال لعبده اخرج عاجلا الى شوارع المدينة وازقتها وادخل الى هنا المساكين والجدع والعرج والعمي"));
arasuperobject.biblemap.put("b42_14_22", new arasuperobject(42,14,22,"فقال العبد يا سيد قد صار كما امرت ويوجد ايضا مكان"));
arasuperobject.biblemap.put("b42_14_23", new arasuperobject(42,14,23,"فقال السيد للعبد اخرج الى الطرق والسياجات والزمهم بالدخول حتى يمتلئ بيتي"));
arasuperobject.biblemap.put("b42_14_24", new arasuperobject(42,14,24,"لاني اقول لكم انه ليس واحد من اولئك الرجال المدعوين يذوق عشائي"));
arasuperobject.biblemap.put("b42_14_25", new arasuperobject(42,14,25,"وكان جموع كثيرة سائرين معه فالتفت وقال لهم"));
arasuperobject.biblemap.put("b42_14_26", new arasuperobject(42,14,26,"ان كان احد يأتي اليّ ولا يبغض اباه وامه وامرأته واولاده واخوته واخواته حتى نفسه ايضا فلا يقدر ان يكون لي تلميذا"));
arasuperobject.biblemap.put("b42_14_27", new arasuperobject(42,14,27,"ومن لا يحمل صليبه ويأتي ورائي فلا يقدر ان يكون لي تلميذا"));
arasuperobject.biblemap.put("b42_14_28", new arasuperobject(42,14,28,"ومن منكم وهو يريد ان يبني برجا لا يجلس اولا ويحسب النفقة هل عنده ما يلزم لكماله"));
arasuperobject.biblemap.put("b42_14_29", new arasuperobject(42,14,29,"لئلا يضع الاساس ولا يقدر ان يكمل فيبتدئ جميع الناظرين يهزأون به"));
arasuperobject.biblemap.put("b42_14_30", new arasuperobject(42,14,30,"قائلين هذا الانسان ابتدأ يبني ولم يقدر ان يكمل"));
arasuperobject.biblemap.put("b42_14_31", new arasuperobject(42,14,31,"واي ملك ان ذهب لمقاتلة ملك آخر في حرب لا يجلس اولا ويتشاور هل يستطيع ان يلاقي بعشرة آلاف الذي يأتي عليه بعشرين الفا"));
arasuperobject.biblemap.put("b42_14_32", new arasuperobject(42,14,32,"وإلا فما دام ذلك بعيدا يرسل سفارة ويسأل ما هو للصلح"));
arasuperobject.biblemap.put("b42_14_33", new arasuperobject(42,14,33,"فكذلك كل واحد منكم لا يترك جميع امواله لا يقدر ان يكون لي تلميذا"));
arasuperobject.biblemap.put("b42_14_34", new arasuperobject(42,14,34,"الملح جيد. ولكن اذا فسد الملح فبماذا يصلح"));
arasuperobject.biblemap.put("b42_14_35", new arasuperobject(42,14,35,"لا يصلح لارض ولا لمزبلة فيطرحونه خارجا. من له اذنان للسمع فليسمع"));
arasuperobject.biblemap.put("b42_15_1", new arasuperobject(42,15,1,"وكان جميع العشارين والخطاة يدنون منه ليسمعوه"));
arasuperobject.biblemap.put("b42_15_2", new arasuperobject(42,15,2,"فتذمر الفريسيون والكتبة قائلين هذا يقبل خطاة وياكل معهم"));
arasuperobject.biblemap.put("b42_15_3", new arasuperobject(42,15,3,"فكلمهم بهذا المثل قائلا"));
arasuperobject.biblemap.put("b42_15_4", new arasuperobject(42,15,4,"اي انسان منكم له مئة خروف واضاع واحدا منها ألا يترك التسعة والتسعين في البرية ويذهب لاجل الضال حتى يجده"));
arasuperobject.biblemap.put("b42_15_5", new arasuperobject(42,15,5,"واذا وجده يضعه على منكبيه فرحا"));
arasuperobject.biblemap.put("b42_15_6", new arasuperobject(42,15,6,"ويأتي الى بيته ويدعو الاصدقاء والجيران قائلا لهم افرحوا معي لاني وجدت خروفي الضال"));
arasuperobject.biblemap.put("b42_15_7", new arasuperobject(42,15,7,"اقول لكم انه هكذا يكون فرح في السماء بخاطئ واحد يتوب اكثر من تسعة وتسعين بارا لا يحتاجون الى توبة"));
arasuperobject.biblemap.put("b42_15_8", new arasuperobject(42,15,8,"او اية امرأة لها عشرة دراهم ان اضاعت درهما واحدا ألا توقد سراجا وتكنس البيت وتفتش باجتهاد حتى تجده"));
arasuperobject.biblemap.put("b42_15_9", new arasuperobject(42,15,9,"واذا وجدته تدعو الصديقات والجارات قائلة افرحن معي لاني وجدت الدرهم الذي اضعته"));
arasuperobject.biblemap.put("b42_15_10", new arasuperobject(42,15,10,"هكذا اقول لكم يكون فرح قدام ملائكة الله بخاطئ واحد يتوب"));
arasuperobject.biblemap.put("b42_15_11", new arasuperobject(42,15,11,"وقال. انسان كان له ابنان"));
arasuperobject.biblemap.put("b42_15_12", new arasuperobject(42,15,12,"فقال اصغرهما لابيه يا ابي اعطني القسم الذي يصيبني من المال. فقسم لهما معيشته"));
arasuperobject.biblemap.put("b42_15_13", new arasuperobject(42,15,13,"وبعد ايام ليست بكثيرة جمع الابن الاصغر كل شيء وسافر الى كورة بعيدة وهناك بذّر ماله بعيش مسرف"));
arasuperobject.biblemap.put("b42_15_14", new arasuperobject(42,15,14,"فلما انفق كل شيء حدث جوع شديد في تلك الكورة فابتدأ يحتاج"));
arasuperobject.biblemap.put("b42_15_15", new arasuperobject(42,15,15,"فمضى والتصق بواحد من اهل تلك الكورة فارسله الى حقوله ليرعى خنازير"));
arasuperobject.biblemap.put("b42_15_16", new arasuperobject(42,15,16,"وكان يشتهي ان يملأ بطنه من الخرنوب الذي كانت الخنازير تأكله. فلم يعطه احد"));
arasuperobject.biblemap.put("b42_15_17", new arasuperobject(42,15,17,"فرجع الى نفسه وقال كم من اجير لابي يفضل عنه الخبز وانا اهلك جوعا"));
arasuperobject.biblemap.put("b42_15_18", new arasuperobject(42,15,18,"اقوم واذهب الى ابي واقول له يا ابي اخطأت الى السماء وقدامك"));
arasuperobject.biblemap.put("b42_15_19", new arasuperobject(42,15,19,"ولست مستحقا بعد ان ادعى لك ابنا. اجعلني كاحد اجراك"));
arasuperobject.biblemap.put("b42_15_20", new arasuperobject(42,15,20,"فقام وجاء الى ابيه. واذ كان لم يزل بعيدا رآه ابوه فتحنن وركض ووقع على عنقه وقبّله"));
arasuperobject.biblemap.put("b42_15_21", new arasuperobject(42,15,21,"فقال له الابن يا ابي اخطأت الى السماء وقدامك ولست مستحقا بعد ان أدعى لك ابنا"));
arasuperobject.biblemap.put("b42_15_22", new arasuperobject(42,15,22,"فقال الاب لعبيده اخرجوا الحلّة الاولى والبسوه واجعلوا خاتما في يده وحذاء في رجليه"));
arasuperobject.biblemap.put("b42_15_23", new arasuperobject(42,15,23,"وقدّموا العجل المسمن واذبحوه فنأكل ونفرح"));
arasuperobject.biblemap.put("b42_15_24", new arasuperobject(42,15,24,"لان ابني هذا كان ميتا فعاش وكان ضالا فوجد. فابتدأوا يفرحون"));
arasuperobject.biblemap.put("b42_15_25", new arasuperobject(42,15,25,"وكان ابنه الاكبر في الحقل. فلما جاء وقرب من البيت سمع صوت آلات طرب ورقصا"));
arasuperobject.biblemap.put("b42_15_26", new arasuperobject(42,15,26,"فدعا واحدا من الغلمان وسأله ما عسى ان يكون هذا"));
arasuperobject.biblemap.put("b42_15_27", new arasuperobject(42,15,27,"فقال له. اخوك جاء فذبح ابوك العجل المسمن لانه قبله سالما"));
arasuperobject.biblemap.put("b42_15_28", new arasuperobject(42,15,28,"فغضب ولم يرد ان يدخل. فخرج ابوه يطلب اليه"));
arasuperobject.biblemap.put("b42_15_29", new arasuperobject(42,15,29,"فاجاب وقال لابيه ها انا اخدمك سنين هذا عددها وقط لم اتجاوز وصيتك وجديا لم تعطني قط لافرح مع اصدقائي"));
arasuperobject.biblemap.put("b42_15_30", new arasuperobject(42,15,30,"ولكن لما جاء ابنك هذا الذي اكل معيشتك مع الزواني ذبحت له العجل المسمن"));
arasuperobject.biblemap.put("b42_15_31", new arasuperobject(42,15,31,"فقال له يا بنيّ انت معي في كل حين وكل ما لي فهو لك"));
arasuperobject.biblemap.put("b42_15_32", new arasuperobject(42,15,32,"ولكن كان ينبغي ان نفرح ونسرّ لان اخاك هذا كان ميتا فعاش وكان ضالا فوجد"));
arasuperobject.biblemap.put("b42_16_1", new arasuperobject(42,16,1,"وقال ايضا لتلاميذه كان انسان غني له وكيل فوشي به اليه بانه يبذّر امواله"));
arasuperobject.biblemap.put("b42_16_2", new arasuperobject(42,16,2,"فدعاه وقال له ما هذا الذي اسمع عنك. اعط حساب وكالتك لانك لا تقدر ان تكون وكيلا بعد"));
arasuperobject.biblemap.put("b42_16_3", new arasuperobject(42,16,3,"فقال الوكيل في نفسه ماذا افعل. لان سيدي يأخذ مني الوكالة. لست استطيع ان انقب واستحي ان استعطي"));
arasuperobject.biblemap.put("b42_16_4", new arasuperobject(42,16,4,"قد علمت ماذا افعل حتى اذا عزلت عن الوكالة يقبلوني في بيوتهم"));
arasuperobject.biblemap.put("b42_16_5", new arasuperobject(42,16,5,"فدعا كل واحد من مديوني سيده وقال للأول كم عليك لسيدي"));
arasuperobject.biblemap.put("b42_16_6", new arasuperobject(42,16,6,"فقال مئة بث زيت. فقال له خذ صكك واجلس عاجلا واكتب خمسين"));
arasuperobject.biblemap.put("b42_16_7", new arasuperobject(42,16,7,"ثم قال لآخر وانت كم عليك. فقال مئة كرّ قمح. فقال له خذ صكك واكتب ثمانين"));
arasuperobject.biblemap.put("b42_16_8", new arasuperobject(42,16,8,"فمدح السيد وكيل الظلم اذ بحكمة فعل. لان ابناء هذا الدهر احكم من ابناء النور في جيلهم"));
arasuperobject.biblemap.put("b42_16_9", new arasuperobject(42,16,9,"وانا اقول لكم اصنعوا لكم اصدقاء بمال الظلم حتى اذا فنيتم يقبلونكم في المظال الابدية"));
arasuperobject.biblemap.put("b42_16_10", new arasuperobject(42,16,10,"الامين في القليل امين ايضا في الكثير. والظالم في القليل ظالم ايضا في الكثير"));
arasuperobject.biblemap.put("b42_16_11", new arasuperobject(42,16,11,"فان لم تكونوا امناء في مال الظلم فمن يأتمنكم على الحق"));
arasuperobject.biblemap.put("b42_16_12", new arasuperobject(42,16,12,"وان لم تكونوا امناء في ما هو للغير فمن يعطيكم ما هو لكم"));
arasuperobject.biblemap.put("b42_16_13", new arasuperobject(42,16,13,"لا يقدر خادم ان يخدم سيدين. لانه اما ان يبغض الواحد ويحب الآخر او يلازم الواحد ويحتقر الآخر. لا تقدرون ان تخدموا الله والمال"));
arasuperobject.biblemap.put("b42_16_14", new arasuperobject(42,16,14,"وكان الفريسيون ايضا يسمعون هذا كله وهم محبون للمال فاستهزأوا به"));
arasuperobject.biblemap.put("b42_16_15", new arasuperobject(42,16,15,"فقال لهم انتم الذين تبررون انفسكم قدام الناس. ولكن الله يعرف قلوبكم. ان المستعلي عند الناس هو رجس قدام الله"));
arasuperobject.biblemap.put("b42_16_16", new arasuperobject(42,16,16,"كان الناموس والانبياء الى يوحنا. ومن ذلك الوقت يبشر بملكوت الله وكل واحد يغتصب نفسه اليه"));
arasuperobject.biblemap.put("b42_16_17", new arasuperobject(42,16,17,"ولكن زوال السماء والارض ايسر من ان تسقط نقطة واحدة من الناموس"));
arasuperobject.biblemap.put("b42_16_18", new arasuperobject(42,16,18,"كل من يطلّق امرأته ويتزوج باخرى يزني وكل من يتزوج بمطلّقة من رجل يزني"));
arasuperobject.biblemap.put("b42_16_19", new arasuperobject(42,16,19,"كان انسان غني وكان يلبس الارجوان والبز وهو يتنعم كل يوم مترفها"));
arasuperobject.biblemap.put("b42_16_20", new arasuperobject(42,16,20,"وكان مسكين اسمه لعازر الذي طرح عند بابه مضروبا بالقروح"));
arasuperobject.biblemap.put("b42_16_21", new arasuperobject(42,16,21,"ويشتهي ان يشبع من الفتات الساقط من مائدة الغني. بل كانت الكلاب تأتي وتلحس قروحه"));
arasuperobject.biblemap.put("b42_16_22", new arasuperobject(42,16,22,"فمات المسكين وحملته الملائكة الى حضن ابراهيم. ومات الغني ايضا ودفن"));
arasuperobject.biblemap.put("b42_16_23", new arasuperobject(42,16,23,"فرفع عينيه في الهاوية وهو في العذاب ورأى ابراهيم من بعيد ولعازر في حضنه"));
arasuperobject.biblemap.put("b42_16_24", new arasuperobject(42,16,24,"فنادى وقال يا ابي ابراهيم ارحمني وارسل لعازر ليبل طرف اصبعه بماء ويبرّد لساني لاني معذب في هذا اللهيب"));
arasuperobject.biblemap.put("b42_16_25", new arasuperobject(42,16,25,"فقال ابراهيم يا ابني اذكر انك استوفيت خيراتك في حياتك وكذلك لعازر البلايا. والآن هو يتعزى وانت تتعذب"));
arasuperobject.biblemap.put("b42_16_26", new arasuperobject(42,16,26,"وفوق هذا كله بيننا وبينكم هوّة عظيمة قد أثبتت حتى ان الذين يريدون العبور من ههنا اليكم لا يقدرون ولا الذين من هناك يجتازون الينا"));
arasuperobject.biblemap.put("b42_16_27", new arasuperobject(42,16,27,"فقال اسألك اذا يا ابت ان ترسله الى بيت ابي"));
arasuperobject.biblemap.put("b42_16_28", new arasuperobject(42,16,28,"لان لي خمسة اخوة. حتى يشهد لهم لكي لا يأتوا هم ايضا الى موضع العذاب هذا"));
arasuperobject.biblemap.put("b42_16_29", new arasuperobject(42,16,29,"قال له ابراهيم عندهم موسى والانبياء. ليسمعوا منهم"));
arasuperobject.biblemap.put("b42_16_30", new arasuperobject(42,16,30,"فقال لا يا ابي ابراهيم. بل اذا مضى اليهم واحد من الاموات يتوبون"));
arasuperobject.biblemap.put("b42_16_31", new arasuperobject(42,16,31,"فقال له ان كانوا لا يسمعون من موسى والانبياء ولا ان قام واحد من الاموات يصدقون"));
arasuperobject.biblemap.put("b42_17_1", new arasuperobject(42,17,1,"وقال لتلاميذه لا يمكن الا ان تأتي العثرات. ولكن ويل للذي تأتي بواسطته"));
arasuperobject.biblemap.put("b42_17_2", new arasuperobject(42,17,2,"خير له لو طوق عنقه بحجر رحى وطرح في البحر من ان يعثر احد هؤلاء الصغار"));
arasuperobject.biblemap.put("b42_17_3", new arasuperobject(42,17,3,"احترزوا لانفسكم. وان اخطأ اليك اخوك فوبخه. وان تاب فاغفر له"));
arasuperobject.biblemap.put("b42_17_4", new arasuperobject(42,17,4,"وان اخطأ اليك سبع مرات في اليوم ورجع اليك سبع مرات في اليوم قائلا انا تائب فاغفر له"));
arasuperobject.biblemap.put("b42_17_5", new arasuperobject(42,17,5,"فقال الرسل للرب زد ايماننا"));
arasuperobject.biblemap.put("b42_17_6", new arasuperobject(42,17,6,"فقال الرب لو كان لكم ايمان مثل حبة خردل لكنتم تقولون لهذه الجميزة انقلعي وانغرسي في البحر فتطيعكم"));
arasuperobject.biblemap.put("b42_17_7", new arasuperobject(42,17,7,"ومن منكم له عبد يحرث او يرعى يقول له اذا دخل من الحقل تقدم سريعا واتكئ"));
arasuperobject.biblemap.put("b42_17_8", new arasuperobject(42,17,8,"بل ألا يقول له اعدد ما اتعشى به وتمنطق واخدمني حتى آكل واشرب وبعد ذلك تأكل وتشرب انت"));
arasuperobject.biblemap.put("b42_17_9", new arasuperobject(42,17,9,"فهل لذلك العبد فضل لانه فعل ما امر به لا اظن"));
arasuperobject.biblemap.put("b42_17_10", new arasuperobject(42,17,10,"كذلك انتم ايضا متى فعلتم كل ما أمرتم به فقولوا اننا عبيد بطالون. لاننا انما عملنا ما كان يجب علينا"));
arasuperobject.biblemap.put("b42_17_11", new arasuperobject(42,17,11,"وفي ذهابه الى اورشليم اجتاز في وسط السامرة والجليل"));
arasuperobject.biblemap.put("b42_17_12", new arasuperobject(42,17,12,"وفيما هو داخل الى قرية استقبله عشرة رجال برص فوقفوا من بعيد"));
arasuperobject.biblemap.put("b42_17_13", new arasuperobject(42,17,13,"ورفعوا صوتا قائلين يا يسوع يا معلّم ارحمنا"));
arasuperobject.biblemap.put("b42_17_14", new arasuperobject(42,17,14,"فنظر وقال لهم اذهبوا وأروا انفسكم للكهنة. وفيما هم منطلقون طهروا"));
arasuperobject.biblemap.put("b42_17_15", new arasuperobject(42,17,15,"فواحد منهم لما رأى انه شفي رجع يمجد الله بصوت عظيم"));
arasuperobject.biblemap.put("b42_17_16", new arasuperobject(42,17,16,"وخرّ على وجهه عند رجليه شاكرا له. وكان سامريا"));
arasuperobject.biblemap.put("b42_17_17", new arasuperobject(42,17,17,"فاجاب يسوع وقال أليس العشرة قد طهروا. فاين التسعة"));
arasuperobject.biblemap.put("b42_17_18", new arasuperobject(42,17,18,"ألم يوجد من يرجع ليعطي مجدا للّه غير هذا الغريب الجنس"));
arasuperobject.biblemap.put("b42_17_19", new arasuperobject(42,17,19,"ثم قال له قم وامض. ايمانك خلصك"));
arasuperobject.biblemap.put("b42_17_20", new arasuperobject(42,17,20,"ولما سأله الفريسيون متى يأتي ملكوت الله اجابهم وقال لا يأتي ملكوت الله بمراقبة"));
arasuperobject.biblemap.put("b42_17_21", new arasuperobject(42,17,21,"ولا يقولون هوذا ههنا او هوذا هناك لان ها ملكوت الله داخلكم"));
arasuperobject.biblemap.put("b42_17_22", new arasuperobject(42,17,22,"وقال للتلاميذ ستأتي ايام فيها تشتهون ان تروا يوما واحدا من ايام ابن الانسان ولا ترون"));
arasuperobject.biblemap.put("b42_17_23", new arasuperobject(42,17,23,"ويقولون لكم هوذا ههنا او هوذا هناك. لا تذهبوا ولا تتبعوا"));
arasuperobject.biblemap.put("b42_17_24", new arasuperobject(42,17,24,"لانه كما ان البرق الذي يبرق من ناحية تحت السماء يضيء الى ناحية تحت السماء كذلك يكون ايضا ابن الانسان في يومه"));
arasuperobject.biblemap.put("b42_17_25", new arasuperobject(42,17,25,"ولكن ينبغي اولا ان يتألم كثيرا ويرفض من هذا الجيل"));
arasuperobject.biblemap.put("b42_17_26", new arasuperobject(42,17,26,"وكما كان في ايام نوح كذلك يكون ايضا في ايام ابن الانسان"));
arasuperobject.biblemap.put("b42_17_27", new arasuperobject(42,17,27,"كانوا يأكلون ويشربون ويزوجون ويتزوجون الى اليوم الذي فيه دخل نوح الفلك وجاء الطوفان واهلك الجميع"));
arasuperobject.biblemap.put("b42_17_28", new arasuperobject(42,17,28,"كذلك ايضا كما كان في ايام لوط كانوا يأكلون ويشربون ويشترون ويبيعون ويغرسون ويبنون"));
arasuperobject.biblemap.put("b42_17_29", new arasuperobject(42,17,29,"ولكن اليوم الذي فيه خرج لوط من سدوم امطر نارا وكبريتا من السماء فاهلك الجميع"));
arasuperobject.biblemap.put("b42_17_30", new arasuperobject(42,17,30,"هكذا يكون في اليوم الذي فيه يظهر ابن الانسان"));
arasuperobject.biblemap.put("b42_17_31", new arasuperobject(42,17,31,"في ذلك اليوم من كان على السطح وامتعته في البيت فلا ينزل ليأخذها. والذي في الحقل كذلك لا يرجع الى الوراء"));
arasuperobject.biblemap.put("b42_17_32", new arasuperobject(42,17,32,"اذكروا امرأة لوط"));
arasuperobject.biblemap.put("b42_17_33", new arasuperobject(42,17,33,"من طلب ان يخلّص نفسه يهلكها ومن اهلكها يحييها"));
arasuperobject.biblemap.put("b42_17_34", new arasuperobject(42,17,34,"اقول لكم انه في تلك الليلة يكون اثنان على فراش واحد فيؤخذ الواحد ويترك الآخر"));
arasuperobject.biblemap.put("b42_17_35", new arasuperobject(42,17,35,"تكون اثنتان تطحنان معا فتؤخذ الواحدة وتترك الاخرى"));
arasuperobject.biblemap.put("b42_17_36", new arasuperobject(42,17,36,"يكون اثنان في الحقل فيؤخذ الواحد ويترك الآخر"));
arasuperobject.biblemap.put("b42_17_37", new arasuperobject(42,17,37,"فاجابوا وقالوا له اين يا رب. فقال لهم حيث تكون الجثّة هناك تجتمع النسور"));
arasuperobject.biblemap.put("b42_18_1", new arasuperobject(42,18,1,"وقال لهم ايضا مثلا في انه ينبغي ان يصلّى كل حين ولا يمل"));
arasuperobject.biblemap.put("b42_18_2", new arasuperobject(42,18,2,"قائلا. كان في مدينة قاض لا يخاف الله ولا يهاب انسانا"));
arasuperobject.biblemap.put("b42_18_3", new arasuperobject(42,18,3,"وكان في تلك المدينة ارملة. وكانت تأتي اليه قائلة انصفني من خصمي"));
arasuperobject.biblemap.put("b42_18_4", new arasuperobject(42,18,4,"وكان لا يشاء الى زمان. ولكن بعد ذلك قال في نفسه وان كنت لا اخاف الله ولا اهاب انسانا"));
arasuperobject.biblemap.put("b42_18_5", new arasuperobject(42,18,5,"فاني لاجل ان هذه الارملة تزعجني انصفها لئلا تأتي دائما فتقمعني"));
arasuperobject.biblemap.put("b42_18_6", new arasuperobject(42,18,6,"وقال الرب اسمعوا ما يقول قاضي الظلم"));
arasuperobject.biblemap.put("b42_18_7", new arasuperobject(42,18,7,"أفلا ينصف الله مختاريه الصارخين اليه نهارا وليلا وهو متمهل عليهم"));
arasuperobject.biblemap.put("b42_18_8", new arasuperobject(42,18,8,"اقول لكم انه ينصفهم سريعا. ولكن متى جاء ابن الانسان ألعله يجد الايمان على الارض"));
arasuperobject.biblemap.put("b42_18_9", new arasuperobject(42,18,9,"وقال لقوم واثقين بانفسهم انهم ابرار ويحتقرون الآخرين هذا المثل"));
arasuperobject.biblemap.put("b42_18_10", new arasuperobject(42,18,10,"انسانان صعدا الى الهيكل ليصلّيا واحد فريسي والآخر عشار"));
arasuperobject.biblemap.put("b42_18_11", new arasuperobject(42,18,11,"اما الفريسي فوقف يصلّي في نفسه هكذا. اللهم انا اشكرك اني لست مثل باقي الناس الخاطفين الظالمين الزناة ولا مثل هذا العشار"));
arasuperobject.biblemap.put("b42_18_12", new arasuperobject(42,18,12,"اصوم مرتين في الاسبوع واعشر كل ما اقتنيه"));
arasuperobject.biblemap.put("b42_18_13", new arasuperobject(42,18,13,"واما العشار فوقف من بعيد لا يشاء ان يرفع عينيه نحو السماء. بل قرع على صدره قائلا اللهم ارحمني انا الخاطئ"));
arasuperobject.biblemap.put("b42_18_14", new arasuperobject(42,18,14,"اقول لكم ان هذا نزل الى بيته مبررا دون ذاك. لان كل من يرفع نفسه يتضع ومن يضع نفسه يرتفع"));
arasuperobject.biblemap.put("b42_18_15", new arasuperobject(42,18,15,"فقدّموا اليه الاطفال ايضا ليلمسهم فلما رآهم التلاميذ انتهروهم"));
arasuperobject.biblemap.put("b42_18_16", new arasuperobject(42,18,16,"اما يسوع فدعاهم وقال دعوا الاولاد يأتون اليّ ولا تمنعوهم لان لمثل هؤلاء ملكوت الله"));
arasuperobject.biblemap.put("b42_18_17", new arasuperobject(42,18,17,"الحق اقول لكم من لا يقبل ملكوت الله مثل ولد فلن يدخله"));
arasuperobject.biblemap.put("b42_18_18", new arasuperobject(42,18,18,"وسأله رئيس قائلا ايها المعلّم الصالح ماذا اعمل لأرث الحياة الابدية"));
arasuperobject.biblemap.put("b42_18_19", new arasuperobject(42,18,19,"فقال له يسوع لماذا تدعوني صالحا. ليس احد صالحا الا واحد وهو الله"));
arasuperobject.biblemap.put("b42_18_20", new arasuperobject(42,18,20,"انت تعرف الوصايا. لا تزن. لا تقتل. لا تسرق. لا تشهد بالزور. اكرم اباك وامك"));
arasuperobject.biblemap.put("b42_18_21", new arasuperobject(42,18,21,"فقال هذه كلها حفظتها منذ حداثتي"));
arasuperobject.biblemap.put("b42_18_22", new arasuperobject(42,18,22,"فلما سمع يسوع ذلك قال له يعوزك ايضا شيء. بع كل مالك ووزع على الفقراء فيكون لك كنز في السماء وتعال اتبعني"));
arasuperobject.biblemap.put("b42_18_23", new arasuperobject(42,18,23,"فلما سمع ذلك حزن لانه كان غنيا جدا"));
arasuperobject.biblemap.put("b42_18_24", new arasuperobject(42,18,24,"فلما رآه يسوع قد حزن قال ما اعسر دخول ذوي الاموال الى ملكوت الله"));
arasuperobject.biblemap.put("b42_18_25", new arasuperobject(42,18,25,"لان دخول جمل من ثقب ابرة ايسر من ان يدخل غني الى ملكوت الله"));
arasuperobject.biblemap.put("b42_18_26", new arasuperobject(42,18,26,"فقال الذين سمعوا فمن يستطيع ان يخلص"));
arasuperobject.biblemap.put("b42_18_27", new arasuperobject(42,18,27,"فقال غير المستطاع عند الناس مستطاع عند الله"));
arasuperobject.biblemap.put("b42_18_28", new arasuperobject(42,18,28,"فقال بطرس ها نحن قد تركنا كل شيء وتبعناك"));
arasuperobject.biblemap.put("b42_18_29", new arasuperobject(42,18,29,"فقال لهم الحق اقول لكم ان ليس احدا ترك بيتا او والدين او اخوة او امرأة او اولادا من اجل ملكوت الله"));
arasuperobject.biblemap.put("b42_18_30", new arasuperobject(42,18,30,"الا ويأخذ في هذا الزمان اضعافا كثيرة وفي الدهر الآتي الحياة الابدية"));
arasuperobject.biblemap.put("b42_18_31", new arasuperobject(42,18,31,"واخذ الاثني عشر وقال لهم ها نحن صاعدون الى اورشليم وسيتم كل ما هو مكتوب بالانبياء عن ابن الانسان"));
arasuperobject.biblemap.put("b42_18_32", new arasuperobject(42,18,32,"لانه يسلم الى الامم ويستهزأ به ويشتم ويتفل عليه"));
arasuperobject.biblemap.put("b42_18_33", new arasuperobject(42,18,33,"ويجلدونه ويقتلونه وفي اليوم الثالث يقوم"));
arasuperobject.biblemap.put("b42_18_34", new arasuperobject(42,18,34,"واما هم فلم يفهموا من ذلك شيئا وكان هذا الأمر مخفى عنهم ولم يعلموا ما قيل"));
arasuperobject.biblemap.put("b42_18_35", new arasuperobject(42,18,35,"ولما اقترب من اريحا كان اعمى جالسا على الطريق يستعطي"));
arasuperobject.biblemap.put("b42_18_36", new arasuperobject(42,18,36,"فلما سمع الجمع مجتازا سأل ما عسى ان يكون هذا"));
arasuperobject.biblemap.put("b42_18_37", new arasuperobject(42,18,37,"فاخبروه ان يسوع الناصري مجتاز"));
arasuperobject.biblemap.put("b42_18_38", new arasuperobject(42,18,38,"فصرخ قائلا يا يسوع ابن داود ارحمني"));
arasuperobject.biblemap.put("b42_18_39", new arasuperobject(42,18,39,"فانتهره المتقدمون ليسكت. اما هو فصرخ اكثر كثيرا يا ابن داود ارحمني"));
arasuperobject.biblemap.put("b42_18_40", new arasuperobject(42,18,40,"فوقف يسوع وامر ان يقدم اليه. ولما اقترب سأله"));
arasuperobject.biblemap.put("b42_18_41", new arasuperobject(42,18,41,"قائلا ماذا تريد ان افعل بك. فقال يا سيد ان ابصر"));
arasuperobject.biblemap.put("b42_18_42", new arasuperobject(42,18,42,"فقال له يسوع ابصر. ايمانك قد شفاك"));
arasuperobject.biblemap.put("b42_18_43", new arasuperobject(42,18,43,"وفي الحال ابصر وتبعه وهو يمجد الله. وجميع الشعب اذ رأوا سبحوا الله"));
arasuperobject.biblemap.put("b42_19_1", new arasuperobject(42,19,1,"ثم دخل واجتاز في اريحا"));
arasuperobject.biblemap.put("b42_19_2", new arasuperobject(42,19,2,"واذا رجل اسمه زكّا وهو رئيس للعشارين وكان غنيا"));
arasuperobject.biblemap.put("b42_19_3", new arasuperobject(42,19,3,"وطلب ان يرى يسوع من هو ولم يقدر من الجمع لانه كان قصير القامة"));
arasuperobject.biblemap.put("b42_19_4", new arasuperobject(42,19,4,"فركض متقدما وصعد الى جميزة لكي يراه. لانه كان مزمعا ان يمرّ من هناك"));
arasuperobject.biblemap.put("b42_19_5", new arasuperobject(42,19,5,"فلما جاء يسوع الى المكان نظر الى فوق فرآه وقال له يا زكّا اسرع وانزل لانه ينبغي ان امكث اليوم في بيتك"));
arasuperobject.biblemap.put("b42_19_6", new arasuperobject(42,19,6,"فاسرع ونزل وقبله فرحا"));
arasuperobject.biblemap.put("b42_19_7", new arasuperobject(42,19,7,"فلما رأى الجميع ذلك تذمروا قائلين انه دخل ليبيت عند رجل خاطئ"));
arasuperobject.biblemap.put("b42_19_8", new arasuperobject(42,19,8,"فوقف زكا وقال للرب ها انا يا رب اعطي نصف اموالي للمساكين وان كنت قد وشيت باحد ارد اربعة اضعاف"));
arasuperobject.biblemap.put("b42_19_9", new arasuperobject(42,19,9,"فقال له يسوع اليوم حصل خلاص لهذا البيت اذ هو ايضا ابن ابراهيم"));
arasuperobject.biblemap.put("b42_19_10", new arasuperobject(42,19,10,"لان ابن الانسان قد جاء لكي يطلب ويخلّص ما قد هلك"));
arasuperobject.biblemap.put("b42_19_11", new arasuperobject(42,19,11,"واذ كانوا يسمعون هذا عاد فقال مثلا لانه كان قريبا من اورشليم وكانوا يظنون ان ملكوت الله عتيد ان يظهر في الحال"));
arasuperobject.biblemap.put("b42_19_12", new arasuperobject(42,19,12,"فقال. انسان شريف الجنس ذهب الى كورة بعيدة ليأخذ لنفسه ملكا ويرجع"));
arasuperobject.biblemap.put("b42_19_13", new arasuperobject(42,19,13,"فدعا عشرة عبيد له واعطاهم عشرة أمناء وقال لهم تاجروا حتى آتي"));
arasuperobject.biblemap.put("b42_19_14", new arasuperobject(42,19,14,"واما اهل مدينته فكانوا يبغضونه فارسلوا وراءه سفارة قائلين لا نريد ان هذا يملك علينا"));
arasuperobject.biblemap.put("b42_19_15", new arasuperobject(42,19,15,"ولما رجع بعد ما اخذ الملك امر ان يدعى اليه اولئك العبيد الذين اعطاهم الفضة ليعرف بما تاجر كل واحد"));
arasuperobject.biblemap.put("b42_19_16", new arasuperobject(42,19,16,"فجاء الاول قائلا يا سيد مناك ربح عشرة أمناء"));
arasuperobject.biblemap.put("b42_19_17", new arasuperobject(42,19,17,"فقال له نعما ايها العبد الصالح. لانك كنت امينا في القليل فليكن لك سلطان على عشر مدن"));
arasuperobject.biblemap.put("b42_19_18", new arasuperobject(42,19,18,"ثم جاء الثاني قائلا يا سيد مناك عمل خمسة امناء"));
arasuperobject.biblemap.put("b42_19_19", new arasuperobject(42,19,19,"فقال لهذا ايضا وكن انت على خمس مدن"));
arasuperobject.biblemap.put("b42_19_20", new arasuperobject(42,19,20,"ثم جاء آخر قائلا يا سيد هوذا مناك الذي كان عندي موضوعا في منديل"));
arasuperobject.biblemap.put("b42_19_21", new arasuperobject(42,19,21,"لاني كنت اخاف منك اذ انت انسان صارم تأخذ ما لم تضع وتحصد ما لم تزرع"));
arasuperobject.biblemap.put("b42_19_22", new arasuperobject(42,19,22,"فقال له من فمك ادينك ايها العبد الشرير. عرفت اني انسان صارم آخذ ما لم اضع واحصد ما لم ازرع"));
arasuperobject.biblemap.put("b42_19_23", new arasuperobject(42,19,23,"فلماذا لم تضع فضتي على مائدة الصيارفة فكنت متى جئت استوفيها مع ربا"));
arasuperobject.biblemap.put("b42_19_24", new arasuperobject(42,19,24,"ثم قال للحاضرين خذوا منه المنا واعطوه للذي عنده العشرة الامناء"));
arasuperobject.biblemap.put("b42_19_25", new arasuperobject(42,19,25,"فقالوا له يا سيد عنده عشرة أمناء"));
arasuperobject.biblemap.put("b42_19_26", new arasuperobject(42,19,26,"لاني اقول لكم ان كل من له يعطى. ومن ليس له فالذي عنده يؤخذ منه"));
arasuperobject.biblemap.put("b42_19_27", new arasuperobject(42,19,27,"اما اعدائي اولئك الذين لم يريدوا ان املك عليهم فأتوا بهم الى هنا واذبحوهم قدامي"));
arasuperobject.biblemap.put("b42_19_28", new arasuperobject(42,19,28,"ولما قال هذا تقدم صاعدا الى اورشليم"));
arasuperobject.biblemap.put("b42_19_29", new arasuperobject(42,19,29,"واذ قرب من بيت فاجي وبيت عنيا عند الجبل الذي يدعى جبل الزيتون ارسل اثنين من تلاميذه"));
arasuperobject.biblemap.put("b42_19_30", new arasuperobject(42,19,30,"قائلا. اذهبا الى القرية التي امامكما وحين تدخلانها تجدان جحشا مربوطا لم يجلس عليه احد من الناس قط. فحلاه وأتيا به"));
arasuperobject.biblemap.put("b42_19_31", new arasuperobject(42,19,31,"وان سألكما احد لماذا تحلانه فقولا له هكذا ان الرب محتاج اليه"));
arasuperobject.biblemap.put("b42_19_32", new arasuperobject(42,19,32,"فمضى المرسلان ووجدا كما قال لهما"));
arasuperobject.biblemap.put("b42_19_33", new arasuperobject(42,19,33,"وفيما هما يحلان الجحش قال لهما اصحابه لماذا تحلان الجحش"));
arasuperobject.biblemap.put("b42_19_34", new arasuperobject(42,19,34,"فقالا الرب محتاج اليه"));
arasuperobject.biblemap.put("b42_19_35", new arasuperobject(42,19,35,"وأتيا به الى يسوع وطرحا ثيابهما على الجحش واركبا يسوع"));
arasuperobject.biblemap.put("b42_19_36", new arasuperobject(42,19,36,"وفيما هو سائر فرشوا ثيابهم في الطريق"));
arasuperobject.biblemap.put("b42_19_37", new arasuperobject(42,19,37,"ولما قرب عند منحدر جبل الزيتون ابتدأ كل جمهور التلاميذ يفرحون ويسبحون الله بصوت عظيم لاجل جميع القوات التي نظروا"));
arasuperobject.biblemap.put("b42_19_38", new arasuperobject(42,19,38,"قائلين مبارك الملك الآتي باسم الرب. سلام في السماء ومجد في الاعالي"));
arasuperobject.biblemap.put("b42_19_39", new arasuperobject(42,19,39,"واما بعض الفريسيين من الجمع فقالوا له يا معلّم انتهر تلاميذك"));
arasuperobject.biblemap.put("b42_19_40", new arasuperobject(42,19,40,"فاجاب وقال لهم اقول لكم انه ان سكت هؤلاء فالحجارة تصرخ"));
arasuperobject.biblemap.put("b42_19_41", new arasuperobject(42,19,41,"وفيما هو يقترب نظر الى المدينة وبكى عليها"));
arasuperobject.biblemap.put("b42_19_42", new arasuperobject(42,19,42,"قائلا انك لو علمت انت ايضا حتى في يومك هذا ما هو لسلامك. ولكن الآن قد أخفي عن عينيك"));
arasuperobject.biblemap.put("b42_19_43", new arasuperobject(42,19,43,"فانه ستأتي ايام ويحيط بك اعداؤك بمترسة ويحدقون بك ويحاصرونك من كل جهة"));
arasuperobject.biblemap.put("b42_19_44", new arasuperobject(42,19,44,"ويهدمونك وبنيك فيك ولا يتركون فيك حجرا على حجر لانك لم تعرفي زمان افتقادك"));
arasuperobject.biblemap.put("b42_19_45", new arasuperobject(42,19,45,"ولما دخل الهيكل ابتدأ يخرج الذين كانوا يبيعون ويشترون فيه"));
arasuperobject.biblemap.put("b42_19_46", new arasuperobject(42,19,46,"قائلا لهم مكتوب ان بيتي بيت الصلاة. وانتم جعلتموه مغارة لصوص"));
arasuperobject.biblemap.put("b42_19_47", new arasuperobject(42,19,47,"وكان يعلّم كل يوم في الهيكل وكان رؤساء الكهنة والكتبة مع وجوه الشعب يطلبون ان يهلكوه"));
arasuperobject.biblemap.put("b42_19_48", new arasuperobject(42,19,48,"ولم يجدوا ما يفعلون لان الشعب كله كان متعلقا به يسمع منه"));
arasuperobject.biblemap.put("b42_20_1", new arasuperobject(42,20,1,"وفي احدى تلك الايام اذ كان يعلّم الشعب في الهيكل ويبشر وقف رؤساء الكهنة والكتبة مع الشيوخ"));
arasuperobject.biblemap.put("b42_20_2", new arasuperobject(42,20,2,"وكلموه قائلين قل لنا باي سلطان تفعل هذا. او من هو الذي اعطاك هذا السلطان"));
arasuperobject.biblemap.put("b42_20_3", new arasuperobject(42,20,3,"فاجاب وقال لهم وانا ايضا اسألكم كلمة واحدة فقولوا لي"));
arasuperobject.biblemap.put("b42_20_4", new arasuperobject(42,20,4,"معمودية يوحنا من السماء كانت ام من الناس"));
arasuperobject.biblemap.put("b42_20_5", new arasuperobject(42,20,5,"فتآمروا فيما بينهم قائلين ان قلنا من السماء يقول فلماذا لم تؤمنوا به"));
arasuperobject.biblemap.put("b42_20_6", new arasuperobject(42,20,6,"وان قلنا من الناس فجميع الشعب يرجموننا لانهم واثقون بان يوحنا نبي"));
arasuperobject.biblemap.put("b42_20_7", new arasuperobject(42,20,7,"فاجابوا انهم لا يعلمون من اين"));
arasuperobject.biblemap.put("b42_20_8", new arasuperobject(42,20,8,"فقال لهم يسوع ولا انا اقول لكم باي سلطان افعل هذا"));
arasuperobject.biblemap.put("b42_20_9", new arasuperobject(42,20,9,"وابتدأ يقول للشعب هذا المثل. انسان غرس كرما وسلمه الى كرامين وسافر زمانا طويلا"));
arasuperobject.biblemap.put("b42_20_10", new arasuperobject(42,20,10,"وفي الوقت ارسل الى الكرامين عبدا لكي يعطوه من ثمر الكرم. فجلده الكرامون وارسلوه فارغا"));
arasuperobject.biblemap.put("b42_20_11", new arasuperobject(42,20,11,"فعاد وارسل عبدا آخر. فجلدوا ذلك ايضا واهانوه وارسلوه فارغا"));
arasuperobject.biblemap.put("b42_20_12", new arasuperobject(42,20,12,"ثم عاد فارسل ثالثا. فجرحوا هذا ايضا واخرجوه"));
arasuperobject.biblemap.put("b42_20_13", new arasuperobject(42,20,13,"فقال صاحب الكرم ماذا افعل. أرسل ابني الحبيب. لعلهم اذا رأوه يهابون"));
arasuperobject.biblemap.put("b42_20_14", new arasuperobject(42,20,14,"فلما رآه الكرامون تآمروا فيما بينهم قائلين هذا هو الوارث. هلموا نقتله لكي يصير لنا الميراث"));
arasuperobject.biblemap.put("b42_20_15", new arasuperobject(42,20,15,"فاخرجوه خارج الكرم وقتلوه. فماذا يفعل بهم صاحب الكرم"));
arasuperobject.biblemap.put("b42_20_16", new arasuperobject(42,20,16,"يأتي ويهلك هؤلاء الكرامين ويعطي الكرم لآخرين. فلما سمعوا قالوا حاشا"));
arasuperobject.biblemap.put("b42_20_17", new arasuperobject(42,20,17,"فنظر اليهم وقال اذا ما هو هذا المكتوب الحجر الذي رفضه البناؤون هو قد صار راس الزاوية"));
arasuperobject.biblemap.put("b42_20_18", new arasuperobject(42,20,18,"كل من يسقط على ذلك الحجر يترضض. ومن سقط هو عليه يسحقه"));
arasuperobject.biblemap.put("b42_20_19", new arasuperobject(42,20,19,"فطلب رؤساء الكهنة والكتبة ان يلقوا الايادي عليه في تلك الساعة ولكنهم خافوا الشعب. لانهم عرفوا انه قال هذا المثل عليهم"));
arasuperobject.biblemap.put("b42_20_20", new arasuperobject(42,20,20,"فراقبوه وارسلوا جواسيس يتراءون انهم ابرار لكي يمسكوه بكلمة حتى يسلموه الى حكم الوالي وسلطانه"));
arasuperobject.biblemap.put("b42_20_21", new arasuperobject(42,20,21,"فسألوه قائلين يا معلّم نعلم انك بالاستقامة تتكلم وتعلّم ولا تقبل الوجوه بل بالحق تعلّم طريق الله"));
arasuperobject.biblemap.put("b42_20_22", new arasuperobject(42,20,22,"أيجوز لنا ان نعطي جزية لقيصر ام لا"));
arasuperobject.biblemap.put("b42_20_23", new arasuperobject(42,20,23,"فشعر بمكرهم وقال لهم لماذا تجربونني"));
arasuperobject.biblemap.put("b42_20_24", new arasuperobject(42,20,24,"أروني دينارا. لمن الصورة والكتابة. فاجابوا وقالوا لقيصر"));
arasuperobject.biblemap.put("b42_20_25", new arasuperobject(42,20,25,"فقال لهم اعطوا اذا ما لقيصر لقيصر وما للّه للّه"));
arasuperobject.biblemap.put("b42_20_26", new arasuperobject(42,20,26,"فلم يقدروا ان يمسكوه بكلمة قدام الشعب. وتعجبوا من جوابه وسكتوا"));
arasuperobject.biblemap.put("b42_20_27", new arasuperobject(42,20,27,"وحضر قوم من الصدوقيين الذين يقاومون امر القيامة وسألوه"));
arasuperobject.biblemap.put("b42_20_28", new arasuperobject(42,20,28,"قائلين يا معلّم كتب لنا موسى ان مات لاحد اخ وله امرأة ومات بغير ولد يأخذ اخوه المرأة ويقيم نسلا لاخيه"));
arasuperobject.biblemap.put("b42_20_29", new arasuperobject(42,20,29,"فكان سبعة اخوة. واخذ الاول امرأة ومات بغير ولد"));
arasuperobject.biblemap.put("b42_20_30", new arasuperobject(42,20,30,"فأخذ الثاني المرأة ومات بغير ولد"));
arasuperobject.biblemap.put("b42_20_31", new arasuperobject(42,20,31,"ثم اخذها الثالث وهكذا السبعة. ولم يتركوا ولدا وماتوا"));
arasuperobject.biblemap.put("b42_20_32", new arasuperobject(42,20,32,"وآخر الكل ماتت المرأة ايضا"));
arasuperobject.biblemap.put("b42_20_33", new arasuperobject(42,20,33,"ففي القيامة لمن منهم تكون زوجة. لانها كانت زوجة للسبعة"));
arasuperobject.biblemap.put("b42_20_34", new arasuperobject(42,20,34,"فاجاب وقال لهم يسوع ابناء هذا الدهر يزوجون ويزوجون"));
arasuperobject.biblemap.put("b42_20_35", new arasuperobject(42,20,35,"ولكن الذين حسبوا اهلا للحصول على ذلك الدهر والقيامة من الاموات لا يزوجون ولا يزوجون"));
arasuperobject.biblemap.put("b42_20_36", new arasuperobject(42,20,36,"اذ لا يستطيعون ان يموتوا ايضا لانهم مثل الملائكة وهم ابناء الله اذ هم ابناء القيامة"));
arasuperobject.biblemap.put("b42_20_37", new arasuperobject(42,20,37,"واما ان الموتى يقومون فقد دل عليه موسى ايضا في امر العليقة كما يقول. الرب اله ابراهيم واله اسحق واله يعقوب"));
arasuperobject.biblemap.put("b42_20_38", new arasuperobject(42,20,38,"وليس هو اله اموات بل اله احياء لان الجميع عنده احياء"));
arasuperobject.biblemap.put("b42_20_39", new arasuperobject(42,20,39,"فاجاب قوما من الكتبة وقالوا يا معلّم حسنا قلت"));
arasuperobject.biblemap.put("b42_20_40", new arasuperobject(42,20,40,"ولم يتجاسروا ايضا ان يسألوه عن شيء"));
arasuperobject.biblemap.put("b42_20_41", new arasuperobject(42,20,41,"وقال لهم كيف يقولون ان المسيح ابن داود"));
arasuperobject.biblemap.put("b42_20_42", new arasuperobject(42,20,42,"وداود نفسه يقول في كتاب المزامير قال الرب لربي اجلس عن يميني"));
arasuperobject.biblemap.put("b42_20_43", new arasuperobject(42,20,43,"حتى اضع اعداءك موطئا لقدميك"));
arasuperobject.biblemap.put("b42_20_44", new arasuperobject(42,20,44,"فاذا داود يدعوه ربا فكيف يكون ابنه"));
arasuperobject.biblemap.put("b42_20_45", new arasuperobject(42,20,45,"وفيما كان جميع الشعب يسمعون قال لتلاميذه"));
arasuperobject.biblemap.put("b42_20_46", new arasuperobject(42,20,46,"احذروا من الكتبة الذين يرغبون المشي بالطيالسة ويحبون التحيات في الاسواق والمجالس الاولى في المجامع والمتكآت الأولى في الولائم"));
arasuperobject.biblemap.put("b42_20_47", new arasuperobject(42,20,47,"الذين يأكلون بيوت الارامل ولعلة يطيلون الصلوات. هؤلاء يأخذون دينونة اعظم"));
arasuperobject.biblemap.put("b42_21_1", new arasuperobject(42,21,1,"وتطلع فرأى الاغنياء يلقون قرابينهم في الخزانة"));
arasuperobject.biblemap.put("b42_21_2", new arasuperobject(42,21,2,"ورأى ايضا ارملة مسكينة ألقت هناك فلسين"));
arasuperobject.biblemap.put("b42_21_3", new arasuperobject(42,21,3,"فقال بالحق اقول لكم ان هذه الارملة الفقيرة ألقت اكثر من الجميع"));
arasuperobject.biblemap.put("b42_21_4", new arasuperobject(42,21,4,"لان هؤلاء من فضلتهم ألقوا في قرابين الله. واما هذه فمن اعوازها ألقت كل المعيشة التي لها"));
arasuperobject.biblemap.put("b42_21_5", new arasuperobject(42,21,5,"واذ كان قوم يقولون عن الهيكل انه مزين بحجارة حسنة وتحف قال"));
arasuperobject.biblemap.put("b42_21_6", new arasuperobject(42,21,6,"هذه التي ترونها ستأتي ايام لا يترك فيها حجر على حجر لا ينقض"));
arasuperobject.biblemap.put("b42_21_7", new arasuperobject(42,21,7,"فسألوه قائلين يا معلّم متى يكون هذا وما هي العلامة عندما يصير هذا"));
arasuperobject.biblemap.put("b42_21_8", new arasuperobject(42,21,8,"فقال انظروا لا تضلوا. فان كثيرين سيأتون باسمي قائلين اني انا هو والزمان قد قرب. فلا تذهبوا وراءهم"));
arasuperobject.biblemap.put("b42_21_9", new arasuperobject(42,21,9,"فاذا سمعتم بحروب وقلاقل فلا تجزعوا لانه لا بد ان يكون هذا اولا. ولكن لا يكون المنتهى سريعا"));
arasuperobject.biblemap.put("b42_21_10", new arasuperobject(42,21,10,"ثم قال لهم تقوم امة على امة ومملكة على مملكة"));
arasuperobject.biblemap.put("b42_21_11", new arasuperobject(42,21,11,"وتكون زلازل عظيمة في اماكن ومجاعات واوبئة. وتكون مخاوف وعلامات عظيمة من السماء"));
arasuperobject.biblemap.put("b42_21_12", new arasuperobject(42,21,12,"وقبل هذا كله يلقون ايديهم عليكم ويطردونكم ويسلمونكم الى مجامع وسجون وتساقون امام ملوك وولاة لاجل اسمي"));
arasuperobject.biblemap.put("b42_21_13", new arasuperobject(42,21,13,"فيؤول ذلك لكم شهادة"));
arasuperobject.biblemap.put("b42_21_14", new arasuperobject(42,21,14,"فضعوا في قلوبكم ان لا تهتموا من قبل لكي تحتجوا"));
arasuperobject.biblemap.put("b42_21_15", new arasuperobject(42,21,15,"لاني انا اعطيكم فما وحكمة لا يقدر جميع معانديكم ان يقاوموها او يناقضوها"));
arasuperobject.biblemap.put("b42_21_16", new arasuperobject(42,21,16,"وسوف تسلمون من الوالدين والاخوة والاقرباء والاصدقاء. ويقتلون منكم"));
arasuperobject.biblemap.put("b42_21_17", new arasuperobject(42,21,17,"وتكونون مبغضين من الجميع من اجل اسمي"));
arasuperobject.biblemap.put("b42_21_18", new arasuperobject(42,21,18,"ولكن شعرة من رؤوسكم لا تهلك"));
arasuperobject.biblemap.put("b42_21_19", new arasuperobject(42,21,19,"بصبركم اقتنوا انفسكم"));
arasuperobject.biblemap.put("b42_21_20", new arasuperobject(42,21,20,"ومتى رأيتم اورشليم محاطة بجيوش فحينئذ اعلموا انه قد اقترب خرابها"));
arasuperobject.biblemap.put("b42_21_21", new arasuperobject(42,21,21,"حينئذ ليهرب الذين في اليهودية الى الجبال. والذين في وسطها فليفروا خارجا. والذين في الكور فلا يدخلوها"));
arasuperobject.biblemap.put("b42_21_22", new arasuperobject(42,21,22,"لان هذه ايام انتقام ليتم كل ما هو مكتوب"));
arasuperobject.biblemap.put("b42_21_23", new arasuperobject(42,21,23,"وويل للحبالى والمرضعات في تلك الايام لانه يكون ضيق عظيم على الارض وسخط على هذا الشعب"));
arasuperobject.biblemap.put("b42_21_24", new arasuperobject(42,21,24,"ويقعون بفم السيف ويسبون الى جميع الامم. وتكون اورشليم مدوسة من الامم حتى تكمل ازمنة الامم"));
arasuperobject.biblemap.put("b42_21_25", new arasuperobject(42,21,25,"وتكون علامات في الشمس والقمر والنجوم. وعلى الارض كرب امم بحيرة. البحر والامواج تضج"));
arasuperobject.biblemap.put("b42_21_26", new arasuperobject(42,21,26,"والناس يغشى عليهم من خوف وانتظار ما يأتي على المسكونة لان قوات السموات تتزعزع"));
arasuperobject.biblemap.put("b42_21_27", new arasuperobject(42,21,27,"وحينئذ يبصرون ابن الانسان آتيا في سحابة بقوة ومجد كثير"));
arasuperobject.biblemap.put("b42_21_28", new arasuperobject(42,21,28,"ومتى ابتدأت هذه تكون فانتصبوا وارفعوا رؤوسكم لان نجاتكم تقترب"));
arasuperobject.biblemap.put("b42_21_29", new arasuperobject(42,21,29,"وقال لهم مثلا. انظروا الى شجرة التين وكل الاشجار"));
arasuperobject.biblemap.put("b42_21_30", new arasuperobject(42,21,30,"متى افرخت تنظرون وتعلمون من انفسكم ان الصيف قد قرب"));
arasuperobject.biblemap.put("b42_21_31", new arasuperobject(42,21,31,"هكذا انتم ايضا متى رأيتم هذه الاشياء صائرة فاعلموا ان ملكوت الله قريب"));
arasuperobject.biblemap.put("b42_21_32", new arasuperobject(42,21,32,"الحق اقول لكم انه لا يمضي هذا الجيل حتى يكون الكل"));
arasuperobject.biblemap.put("b42_21_33", new arasuperobject(42,21,33,"السماء والارض تزولان ولكن كلامي لا يزول"));
arasuperobject.biblemap.put("b42_21_34", new arasuperobject(42,21,34,"فاحترزوا لانفسكم لئلا تثقل قلوبكم في خمار وسكر وهموم الحياة فيصادفكم ذلك اليوم بغتة"));
arasuperobject.biblemap.put("b42_21_35", new arasuperobject(42,21,35,"لانه كالفخ يأتي على جميع الجالسين على وجه كل الارض"));
arasuperobject.biblemap.put("b42_21_36", new arasuperobject(42,21,36,"اسهروا اذا وتضرعوا في كل حين لكي تحسبوا اهلا للنجاة من جميع هذا المزمع ان يكون وتقفوا قدام ابن الانسان"));
arasuperobject.biblemap.put("b42_21_37", new arasuperobject(42,21,37,"وكان في النهار يعلّم في الهيكل وفي الليل يخرج ويبيت في الجبل الذي يدعى جبل الزيتون"));
arasuperobject.biblemap.put("b42_21_38", new arasuperobject(42,21,38,"وكان كل الشعب يبكرون اليه في الهيكل ليسمعوه"));
arasuperobject.biblemap.put("b42_22_1", new arasuperobject(42,22,1,"وقرب عيد الفطر الذي يقال له الفصح"));
arasuperobject.biblemap.put("b42_22_2", new arasuperobject(42,22,2,"وكان رؤساء الكهنة والكتبة يطلبون كيف يقتلونه. لانهم خافوا الشعب"));
arasuperobject.biblemap.put("b42_22_3", new arasuperobject(42,22,3,"فدخل الشيطان في يهوذا الذي يدعى الاسخريوطي وهو من جملة الاثني عشر"));
arasuperobject.biblemap.put("b42_22_4", new arasuperobject(42,22,4,"فمضى وتكلم مع رؤساء الكهنة وقواد الجند كيف يسلمه اليهم"));
arasuperobject.biblemap.put("b42_22_5", new arasuperobject(42,22,5,"ففرحوا وعاهدوه ان يعطوه فضة"));
arasuperobject.biblemap.put("b42_22_6", new arasuperobject(42,22,6,"فواعدهم. وكان يطلب فرصة ليسلمه اليهم خلوا من جمع"));
arasuperobject.biblemap.put("b42_22_7", new arasuperobject(42,22,7,"وجاء يوم الفطير الذي كان ينبغي ان يذبح فيه الفصح"));
arasuperobject.biblemap.put("b42_22_8", new arasuperobject(42,22,8,"فارسل بطرس ويوحنا قائلا اذهبا واعدّا لنا الفصح لناكل"));
arasuperobject.biblemap.put("b42_22_9", new arasuperobject(42,22,9,"فقالا له اين تريد ان نعدّ"));
arasuperobject.biblemap.put("b42_22_10", new arasuperobject(42,22,10,"فقال لهما اذا دخلتما المدينة يستقبلكما انسان حامل جرة ماء. اتبعاه الى البيت حيث يدخل"));
arasuperobject.biblemap.put("b42_22_11", new arasuperobject(42,22,11,"وقولا لرب البيت يقول لك المعلّم اين المنزل حيث آكل الفصح مع تلاميذي"));
arasuperobject.biblemap.put("b42_22_12", new arasuperobject(42,22,12,"فذاك يريكما عليّة كبيرة مفروشة. هناك اعدا"));
arasuperobject.biblemap.put("b42_22_13", new arasuperobject(42,22,13,"فانطلقا ووجدا كما قال لهما. فاعدّا الفصح"));
arasuperobject.biblemap.put("b42_22_14", new arasuperobject(42,22,14,"ولما كانت الساعة اتكأ والاثنا عشر رسولا معه"));
arasuperobject.biblemap.put("b42_22_15", new arasuperobject(42,22,15,"وقال لهم شهوة اشتهيت ان آكل هذا الفصح معكم قبل ان اتألم"));
arasuperobject.biblemap.put("b42_22_16", new arasuperobject(42,22,16,"لاني اقول لكم اني لا آكل منه بعد حتى يكمل في ملكوت الله"));
arasuperobject.biblemap.put("b42_22_17", new arasuperobject(42,22,17,"ثم تناول كاسا وشكر وقال خذوا هذه واقتسموها بينكم"));
arasuperobject.biblemap.put("b42_22_18", new arasuperobject(42,22,18,"لاني اقول لكم اني لا اشرب من نتاج الكرمة حتى يأتي ملكوت الله"));
arasuperobject.biblemap.put("b42_22_19", new arasuperobject(42,22,19,"واخذ خبزا وشكر وكسر واعطاهم قائلا هذا هو جسدي الذي يبذل عنكم. اصنعوا هذا لذكري"));
arasuperobject.biblemap.put("b42_22_20", new arasuperobject(42,22,20,"وكذلك الكاس ايضا بعد العشاء قائلا هذه الكاس هي العهد الجديد بدمي الذي يسفك عنكم"));
arasuperobject.biblemap.put("b42_22_21", new arasuperobject(42,22,21,"ولكن هوذا يد الذي يسلمني هي معي على المائدة"));
arasuperobject.biblemap.put("b42_22_22", new arasuperobject(42,22,22,"وابن الانسان ماض كما هو محتوم. ولكن ويل لذلك الانسان الذي يسلمه"));
arasuperobject.biblemap.put("b42_22_23", new arasuperobject(42,22,23,"فابتدأوا يتساءلون فيما بينهم من ترى منهم هو المزمع ان يفعل هذا"));
arasuperobject.biblemap.put("b42_22_24", new arasuperobject(42,22,24,"وكانت بينهم ايضا مشاجرة من منهم يظن انه يكون اكبر"));
arasuperobject.biblemap.put("b42_22_25", new arasuperobject(42,22,25,"فقال لهم. ملوك الامم يسودونهم والمتسلطون عليهم يدعون محسنين"));
arasuperobject.biblemap.put("b42_22_26", new arasuperobject(42,22,26,"واما انتم فليس هكذا. بل الكبير فيكم ليكن كالاصغر. والمتقدم كالخادم"));
arasuperobject.biblemap.put("b42_22_27", new arasuperobject(42,22,27,"لان من هو اكبر. الذي يتكئ ام الذي يخدم. أليس الذي يتكئ. ولكني انا بينكم كالذي يخدم"));
arasuperobject.biblemap.put("b42_22_28", new arasuperobject(42,22,28,"انتم الذين ثبتوا معي في تجاربي"));
arasuperobject.biblemap.put("b42_22_29", new arasuperobject(42,22,29,"وانا اجعل لكم كما جعل لي ابي ملكوتا"));
arasuperobject.biblemap.put("b42_22_30", new arasuperobject(42,22,30,"لتأكلوا وتشربوا على مائدتي في ملكوتي وتجلسوا على كراسي تدينون اسباط اسرائيل الاثني عشر"));
arasuperobject.biblemap.put("b42_22_31", new arasuperobject(42,22,31,"وقال الرب سمعان سمعان هوذا الشيطان طلبكم لكي يغربلكم كالحنطة"));
arasuperobject.biblemap.put("b42_22_32", new arasuperobject(42,22,32,"ولكني طلبت من اجلك لكي لا يفنى ايمانك. وانت متى رجعت ثبت اخوتك"));
arasuperobject.biblemap.put("b42_22_33", new arasuperobject(42,22,33,"فقال له يا رب اني مستعد ان امضي معك حتى الى السجن والى الموت"));
arasuperobject.biblemap.put("b42_22_34", new arasuperobject(42,22,34,"فقال اقول لك يا بطرس لا يصيح الديك اليوم قبل ان تنكر ثلاث مرات انك تعرفني"));
arasuperobject.biblemap.put("b42_22_35", new arasuperobject(42,22,35,"ثم قال لهم حين ارسلتكم بلا كيس ولا مزود ولا احذية هل اعوزكم شيء. فقالوا لا"));
arasuperobject.biblemap.put("b42_22_36", new arasuperobject(42,22,36,"فقال لهم لكن الآن من له كيس فليأخذه ومزود كذلك. ومن ليس له فليبع ثوبه ويشتر سيفا"));
arasuperobject.biblemap.put("b42_22_37", new arasuperobject(42,22,37,"لاني اقول لكم انه ينبغي ان يتم فيّ ايضا هذا المكتوب وأحصي مع اثمة. لان ما هو من جهتي له انقضاء"));
arasuperobject.biblemap.put("b42_22_38", new arasuperobject(42,22,38,"فقالوا يا رب هوذا هنا سيفان. فقال لهم يكفي"));
arasuperobject.biblemap.put("b42_22_39", new arasuperobject(42,22,39,"وخرج ومضى كالعادة الى جبل الزيتون. وتبعه ايضا تلاميذه"));
arasuperobject.biblemap.put("b42_22_40", new arasuperobject(42,22,40,"ولما صار الى المكان قال لهم صلّوا لكي لا تدخلوا في تجربة"));
arasuperobject.biblemap.put("b42_22_41", new arasuperobject(42,22,41,"وانفصل عنهم نحو رمية حجر وجثا على ركبتيه وصلّى"));
arasuperobject.biblemap.put("b42_22_42", new arasuperobject(42,22,42,"قائلا يا ابتاه ان شئت ان تجيز عني هذه الكاس. ولكن لتكن لا ارادتي بل ارادتك"));
arasuperobject.biblemap.put("b42_22_43", new arasuperobject(42,22,43,"وظهر له ملاك من السماء يقويه"));
arasuperobject.biblemap.put("b42_22_44", new arasuperobject(42,22,44,"واذ كان في جهاد كان يصلّي باشد لجاجة وصار عرقه كقطرات دم نازلة على الارض"));
arasuperobject.biblemap.put("b42_22_45", new arasuperobject(42,22,45,"ثم قام من الصلاة وجاء الى تلاميذه فوجدهم نياما من الحزن"));
arasuperobject.biblemap.put("b42_22_46", new arasuperobject(42,22,46,"فقال لهم لماذا انتم نيام. قوموا وصلّوا لئلا تدخلوا في تجربة"));
arasuperobject.biblemap.put("b42_22_47", new arasuperobject(42,22,47,"وبينما هو يتكلم اذا جمع والذي يدعى يهوذا احد الاثني عشر يتقدمهم فدنا من يسوع ليقبله"));
arasuperobject.biblemap.put("b42_22_48", new arasuperobject(42,22,48,"فقال له يسوع يا يهوذا أبقبلة تسلم ابن الانسان"));
arasuperobject.biblemap.put("b42_22_49", new arasuperobject(42,22,49,"فلما رأى الذين حوله ما يكون قالوا يا رب انضرب بالسيف"));
arasuperobject.biblemap.put("b42_22_50", new arasuperobject(42,22,50,"وضرب واحد منهم عبد رئيس الكهنة فقطع اذنه اليمنى"));
arasuperobject.biblemap.put("b42_22_51", new arasuperobject(42,22,51,"فاجاب يسوع وقال دعوا الى هذا. ولمس اذنه وابرأها"));
arasuperobject.biblemap.put("b42_22_52", new arasuperobject(42,22,52,"ثم قال يسوع لرؤساء الكهنة وقواد جند الهيكل والشيوخ المقبلين عليه. كانه على لص خرجتم بسيوف وعصي"));
arasuperobject.biblemap.put("b42_22_53", new arasuperobject(42,22,53,"اذ كنت معكم كل يوم في الهيكل لم تمدوا عليّ الايادي. ولكن هذه ساعتكم وسلطان الظلمة"));
arasuperobject.biblemap.put("b42_22_54", new arasuperobject(42,22,54,"فأخذوه وساقوه وادخلوه الى بيت رئيس الكهنة واما بطرس فتبعه من بعيد"));
arasuperobject.biblemap.put("b42_22_55", new arasuperobject(42,22,55,"ولما اضرموا نارا في وسط الدار وجلسوا معا جلس بطرس بينهم"));
arasuperobject.biblemap.put("b42_22_56", new arasuperobject(42,22,56,"فرأته جارية جالسا عند النار فتفرست فيه وقالت وهذا كان معه"));
arasuperobject.biblemap.put("b42_22_57", new arasuperobject(42,22,57,"فانكره قائلا لست اعرفه يا امرأة"));
arasuperobject.biblemap.put("b42_22_58", new arasuperobject(42,22,58,"وبعد قليل رآه آخر وقال وانت منهم. فقال بطرس يا انسان لست انا"));
arasuperobject.biblemap.put("b42_22_59", new arasuperobject(42,22,59,"ولما مضى نحو ساعة واحدة اكّد آخر قائلا بالحق ان هذا ايضا كان معه لانه جليلي ايضا"));
arasuperobject.biblemap.put("b42_22_60", new arasuperobject(42,22,60,"فقال بطرس يا انسان لست اعرف ما تقول. وفي الحال بينما هو يتكلم صاح الديك"));
arasuperobject.biblemap.put("b42_22_61", new arasuperobject(42,22,61,"فالتفت الرب ونظر الى بطرس. فتذكر بطرس كلام الرب كيف قال له انك قبل ان يصيح الديك تنكرني ثلاث مرات"));
arasuperobject.biblemap.put("b42_22_62", new arasuperobject(42,22,62,"فخرج بطرس الى خارج وبكى بكاء مرا"));
arasuperobject.biblemap.put("b42_22_63", new arasuperobject(42,22,63,"والرجال الذين كانوا ضابطين يسوع كانوا يستهزئون به وهم يجلدونه"));
arasuperobject.biblemap.put("b42_22_64", new arasuperobject(42,22,64,"وغطوه وكانوا يضربون وجهه ويسألونه قائلين تنبأ. من هو الذي ضربك"));
arasuperobject.biblemap.put("b42_22_65", new arasuperobject(42,22,65,"واشياء أخر كثيرة كانوا يقولون عليه مجدفين"));
arasuperobject.biblemap.put("b42_22_66", new arasuperobject(42,22,66,"ولما كان النهار اجتمعت مشيخة الشعب رؤساء الكهنة والكتبة واصعدوه الى مجمعهم"));
arasuperobject.biblemap.put("b42_22_67", new arasuperobject(42,22,67,"قائلين ان كنت انت المسيح فقل لنا. فقال لهم ان قلت لكم لا تصدقون"));
arasuperobject.biblemap.put("b42_22_68", new arasuperobject(42,22,68,"وان سألت لا تجيبونني ولا تطلقونني"));
arasuperobject.biblemap.put("b42_22_69", new arasuperobject(42,22,69,"منذ الآن يكون ابن الانسان جالسا عن يمين قوّة الله"));
arasuperobject.biblemap.put("b42_22_70", new arasuperobject(42,22,70,"فقال الجميع أفانت ابن الله. فقال لهم انتم تقولون اني انا هو"));
arasuperobject.biblemap.put("b42_22_71", new arasuperobject(42,22,71,"فقالوا ما حاجتنا بعد الى شهادة لاننا نحن سمعنا من فمه"));
arasuperobject.biblemap.put("b42_23_1", new arasuperobject(42,23,1,"فقام كل جمهورهم وجاءوا به الى بيلاطس"));
arasuperobject.biblemap.put("b42_23_2", new arasuperobject(42,23,2,"وابتدأوا يشتكون عليه قائلين اننا وجدنا هذا يفسد الامة ويمنع ان تعطى جزية لقيصر قائلا انه هو مسيح ملك"));
arasuperobject.biblemap.put("b42_23_3", new arasuperobject(42,23,3,"فسأله بيلاطس قائلا انت ملك اليهود. فاجابه وقال انت تقول"));
arasuperobject.biblemap.put("b42_23_4", new arasuperobject(42,23,4,"فقال بيلاطس لرؤساء الكهنة والجموع اني لا اجد علّة في هذا الانسان"));
arasuperobject.biblemap.put("b42_23_5", new arasuperobject(42,23,5,"فكانوا يشددون قائلين انه يهيج الشعب وهو يعلّم في كل اليهودية مبتدئا من الجليل الى هنا"));
arasuperobject.biblemap.put("b42_23_6", new arasuperobject(42,23,6,"فلما سمع بيلاطس ذكر الجليل سأل هل الرجل جليلي"));
arasuperobject.biblemap.put("b42_23_7", new arasuperobject(42,23,7,"وحين علم انه من سلطنة هيرودس ارسله الى هيرودس اذ كان هو ايضا تلك الايام في اورشليم"));
arasuperobject.biblemap.put("b42_23_8", new arasuperobject(42,23,8,"واما هيرودس فلما رأى يسوع فرح جدا لانه كان يريد من زمان طويل ان يراه لسماعه عنه اشياء كثيرة وترجى ان يرى آية تصنع منه"));
arasuperobject.biblemap.put("b42_23_9", new arasuperobject(42,23,9,"وسأله بكلام كثير فلم يجبه بشيء"));
arasuperobject.biblemap.put("b42_23_10", new arasuperobject(42,23,10,"ووقف رؤساء الكهنة والكتبة يشتكون عليه باشتداد"));
arasuperobject.biblemap.put("b42_23_11", new arasuperobject(42,23,11,"فاحتقره هيرودس مع عسكره واستهزأ به والبسه لباسا لامعا ورده الى بيلاطس"));
arasuperobject.biblemap.put("b42_23_12", new arasuperobject(42,23,12,"فصار بيلاطس وهيرودس صديقين مع بعضهما في ذلك اليوم لانهما كانا من قبل في عداوة بينهما"));
arasuperobject.biblemap.put("b42_23_13", new arasuperobject(42,23,13,"فدعا بيلاطس رؤساء الكهنة والعظماء والشعب"));
arasuperobject.biblemap.put("b42_23_14", new arasuperobject(42,23,14,"وقال لهم. قد قدمتم اليّ هذا الانسان كمن يفسد الشعب. وها انا قد فحصت قدامكم ولم اجد في هذا الانسان علّة مما تشتكون به عليه"));
arasuperobject.biblemap.put("b42_23_15", new arasuperobject(42,23,15,"ولا هيرودس ايضا. لاني ارسلتكم اليه. وها لا شيء يستحق الموت صنع منه"));
arasuperobject.biblemap.put("b42_23_16", new arasuperobject(42,23,16,"فانا أؤدبه واطلقه"));
arasuperobject.biblemap.put("b42_23_17", new arasuperobject(42,23,17,"وكان مضطرا ان يطلق لهم كل عيد واحدا"));
arasuperobject.biblemap.put("b42_23_18", new arasuperobject(42,23,18,"فصرخوا بجملتهم قائلين خذ هذا واطلق لنا باراباس"));
arasuperobject.biblemap.put("b42_23_19", new arasuperobject(42,23,19,"وذاك كان قد طرح في السجن لاجل فتنة حدثت في المدينة وقتل"));
arasuperobject.biblemap.put("b42_23_20", new arasuperobject(42,23,20,"فناداهم ايضا بيلاطس وهو يريد ان يطلق يسوع"));
arasuperobject.biblemap.put("b42_23_21", new arasuperobject(42,23,21,"فصرخوا قائلين اصلبه اصلبه"));
arasuperobject.biblemap.put("b42_23_22", new arasuperobject(42,23,22,"فقال لهم ثالثة فاي شر عمل هذا. اني لم اجد فيه علّة للموت. فانا أؤدبه واطلقه"));
arasuperobject.biblemap.put("b42_23_23", new arasuperobject(42,23,23,"فكانوا يلجّون باصوات عظيمة طالبين ان يصلب. فقويت اصواتهم واصوات رؤساء الكهنة"));
arasuperobject.biblemap.put("b42_23_24", new arasuperobject(42,23,24,"فحكم بيلاطس ان تكون طلبتهم"));
arasuperobject.biblemap.put("b42_23_25", new arasuperobject(42,23,25,"فاطلق لهم الذي طرح في السجن لاجل فتنة وقتل الذي طلبوه واسلم يسوع لمشيئتهم"));
arasuperobject.biblemap.put("b42_23_26", new arasuperobject(42,23,26,"ولما مضوا به امسكوا سمعان رجلا قيروانيا كان آتيا من الحقل ووضعوا عليه الصليب ليحمله خلف يسوع"));
arasuperobject.biblemap.put("b42_23_27", new arasuperobject(42,23,27,"وتبعه جمهور كثير من الشعب والنساء اللواتي كنّ يلطمن ايضا وينحن عليه"));
arasuperobject.biblemap.put("b42_23_28", new arasuperobject(42,23,28,"فالتفت اليهنّ يسوع وقال. يا بنات اورشليم لا تبكين عليّ بل ابكين على انفسكنّ وعلى اولادكنّ"));
arasuperobject.biblemap.put("b42_23_29", new arasuperobject(42,23,29,"لانه هوذا ايام تأتي يقولون فيها طوبى للعواقر والبطون التي لم تلد والثدي التي لم ترضع"));
arasuperobject.biblemap.put("b42_23_30", new arasuperobject(42,23,30,"حينئذ يبتدئون يقولون للجبال اسقطي علينا وللآكام غطينا"));
arasuperobject.biblemap.put("b42_23_31", new arasuperobject(42,23,31,"لانه ان كانوا بالعود الرطب يفعلون هذا فماذا يكون باليابس"));
arasuperobject.biblemap.put("b42_23_32", new arasuperobject(42,23,32,"وجاءوا ايضا باثنين آخرين مذنبين ليقتلا معه"));
arasuperobject.biblemap.put("b42_23_33", new arasuperobject(42,23,33,"ولما مضوا به الى الموضع الذي يدعى جمجمة صلبوه هناك مع المذنبين واحدا عن يمينه والآخر عن يساره"));
arasuperobject.biblemap.put("b42_23_34", new arasuperobject(42,23,34,"فقال يسوع يا ابتاه اغفر لهم لانهم لا يعلمون ماذا يفعلون. واذ اقتسموا ثيابه اقترعوا عليها"));
arasuperobject.biblemap.put("b42_23_35", new arasuperobject(42,23,35,"وكان الشعب واقفين ينظرون. والرؤساء ايضا معهم يسخرون به قائلين خلّص آخرين فليخلّص نفسه ان كان هو المسيح مختار الله"));
arasuperobject.biblemap.put("b42_23_36", new arasuperobject(42,23,36,"والجند ايضا استهزأوا به وهم يأتون ويقدمون له خلا"));
arasuperobject.biblemap.put("b42_23_37", new arasuperobject(42,23,37,"قائلين ان كنت انت ملك اليهود فخلّص نفسك"));
arasuperobject.biblemap.put("b42_23_38", new arasuperobject(42,23,38,"وكان عنوان مكتوب فوقه باحرف يونانية ورومانية وعبرانية هذا هو ملك اليهود"));
arasuperobject.biblemap.put("b42_23_39", new arasuperobject(42,23,39,"وكان واحد من المذنبين المعلقين يجدف عليه قائلا ان كنت انت المسيح فخلّص نفسك وإيانا"));
arasuperobject.biblemap.put("b42_23_40", new arasuperobject(42,23,40,"فاجاب الآخر وانتهره قائلا أولا انت تخاف الله اذ انت تحت هذا الحكم بعينه"));
arasuperobject.biblemap.put("b42_23_41", new arasuperobject(42,23,41,"اما نحن فبعدل لاننا ننال استحقاق ما فعلنا. واما هذا فلم يفعل شيئا ليس في محله"));
arasuperobject.biblemap.put("b42_23_42", new arasuperobject(42,23,42,"ثم قال ليسوع اذكرني يا رب متى جئت في ملكوتك"));
arasuperobject.biblemap.put("b42_23_43", new arasuperobject(42,23,43,"فقال له يسوع الحق اقول لك انك اليوم تكون معي في الفردوس"));
arasuperobject.biblemap.put("b42_23_44", new arasuperobject(42,23,44,"وكان نحو الساعة السادسة. فكانت ظلمة على الارض كلها الى الساعة التاسعة"));
arasuperobject.biblemap.put("b42_23_45", new arasuperobject(42,23,45,"واظلمت الشمس وانشقّ حجاب الهيكل من وسطه"));
arasuperobject.biblemap.put("b42_23_46", new arasuperobject(42,23,46,"ونادى يسوع بصوت عظيم وقال يا ابتاه في يديك استودع روحي. ولما قال هذا اسلم الروح"));
arasuperobject.biblemap.put("b42_23_47", new arasuperobject(42,23,47,"فلما رأى قائد المئة ما كان مجّد الله قائلا بالحقيقة كان هذا الانسان بارا"));
arasuperobject.biblemap.put("b42_23_48", new arasuperobject(42,23,48,"وكل الجموع الذين كانوا مجتمعين لهذا المنظر لما ابصروا ما كان رجعوا وهم يقرعون صدورهم"));
arasuperobject.biblemap.put("b42_23_49", new arasuperobject(42,23,49,"وكان جميع معارفه ونساء كنّ قد تبعنه من الجليل واقفين من بعيد ينظرون ذلك"));
arasuperobject.biblemap.put("b42_23_50", new arasuperobject(42,23,50,"واذا رجل اسمه يوسف وكان مشيرا ورجلا صالحا بارا"));
arasuperobject.biblemap.put("b42_23_51", new arasuperobject(42,23,51,"هذا لم يكن موافقا لرأيهم وعملهم. وهو من الرامة مدينة لليهود. وكان هو ايضا ينتظر ملكوت الله"));
arasuperobject.biblemap.put("b42_23_52", new arasuperobject(42,23,52,"هذا تقدم الى بيلاطس وطلب جسد يسوع"));
arasuperobject.biblemap.put("b42_23_53", new arasuperobject(42,23,53,"وانزله ولفه بكتان ووضعه في قبر منحوت حيث لم يكن احد وضع قط"));
arasuperobject.biblemap.put("b42_23_54", new arasuperobject(42,23,54,"وكان يوم الاستعداد والسبت يلوح"));
arasuperobject.biblemap.put("b42_23_55", new arasuperobject(42,23,55,"وتبعته نساء كنّ قد أتين معه من الجليل ونظرن القبر وكيف وضع جسده"));
arasuperobject.biblemap.put("b42_23_56", new arasuperobject(42,23,56,"فرجعن وأعددن حنوطا واطيابا. وفي السبت استرحن حسب الوصية"));
arasuperobject.biblemap.put("b42_24_1", new arasuperobject(42,24,1,"ثم في اول الاسبوع اول الفجر أتين الى القبر حاملات الحنوط الذي أعددنه ومعهنّ اناس"));
arasuperobject.biblemap.put("b42_24_2", new arasuperobject(42,24,2,"فوجدن الحجر مدحرجا عن القبر"));
arasuperobject.biblemap.put("b42_24_3", new arasuperobject(42,24,3,"فدخلن ولم يجدن جسد الرب يسوع"));
arasuperobject.biblemap.put("b42_24_4", new arasuperobject(42,24,4,"وفيما هنّ محتارات في ذلك اذا رجلان وقفا بهنّ بثياب براقة"));
arasuperobject.biblemap.put("b42_24_5", new arasuperobject(42,24,5,"واذ كنّ خائفات ومنكسات وجوههنّ الى الارض قالا لهنّ. لماذا تطلبن الحي بين الاموات"));
arasuperobject.biblemap.put("b42_24_6", new arasuperobject(42,24,6,"ليس هو ههنا لكنه قام. اذكرن كيف كلمكنّ وهو بعد في الجليل"));
arasuperobject.biblemap.put("b42_24_7", new arasuperobject(42,24,7,"قائلا انه ينبغي ان يسلّم ابن الانسان في ايدي اناس خطاة ويصلب وفي اليوم الثالث يقوم"));
arasuperobject.biblemap.put("b42_24_8", new arasuperobject(42,24,8,"فتذكرن كلامه"));
arasuperobject.biblemap.put("b42_24_9", new arasuperobject(42,24,9,"ورجعن من القبر واخبرن الاحد عشر وجميع الباقين بهذا كله"));
arasuperobject.biblemap.put("b42_24_10", new arasuperobject(42,24,10,"وكانت مريم المجدلية ويونّا ومريم ام يعقوب والباقيات معهنّ اللواتي قلن هذا للرسل"));
arasuperobject.biblemap.put("b42_24_11", new arasuperobject(42,24,11,"فتراءى كلامهنّ لهم كالهذيان ولم يصدقوهنّ"));
arasuperobject.biblemap.put("b42_24_12", new arasuperobject(42,24,12,"فقام بطرس وركض الى القبر فانحنى ونظر الاكفان موضوعة وحدها فمضى متعجبا في نفسه مما كان"));
arasuperobject.biblemap.put("b42_24_13", new arasuperobject(42,24,13,"واذا اثنان منهم كانا منطلقين في ذلك اليوم الى قرية بعيدة عن اورشليم ستين غلوة اسمها عمواس"));
arasuperobject.biblemap.put("b42_24_14", new arasuperobject(42,24,14,"وكانا يتكلمان بعضهما مع بعض عن جميع هذه الحوادث"));
arasuperobject.biblemap.put("b42_24_15", new arasuperobject(42,24,15,"وفيما هما يتكلمان ويتحاوران اقترب اليهما يسوع نفسه وكان يمشي معهما"));
arasuperobject.biblemap.put("b42_24_16", new arasuperobject(42,24,16,"ولكن أمسكت اعينهما عن معرفته"));
arasuperobject.biblemap.put("b42_24_17", new arasuperobject(42,24,17,"فقال لهما ما هذا الكلام الذي تتطارحان به وانتما ماشيان عابسين"));
arasuperobject.biblemap.put("b42_24_18", new arasuperobject(42,24,18,"فاجاب احدهما الذي اسمه كليوباس وقال له هل انت متغرب وحدك في اورشليم ولم تعلم الامور التي حدثت فيها في هذه الايام"));
arasuperobject.biblemap.put("b42_24_19", new arasuperobject(42,24,19,"فقال لهما وما هي. فقالا المختصة بيسوع الناصري الذي كان انسانا نبيا مقتدرا في الفعل والقول امام الله وجميع الشعب"));
arasuperobject.biblemap.put("b42_24_20", new arasuperobject(42,24,20,"كيف اسلمه رؤساء الكهنة وحكامنا لقضاء الموت وصلبوه"));
arasuperobject.biblemap.put("b42_24_21", new arasuperobject(42,24,21,"ونحن كنا نرجو انه هو المزمع ان يفدي اسرائيل. ولكن مع هذا كله اليوم له ثلاثة ايام منذ حدث ذلك"));
arasuperobject.biblemap.put("b42_24_22", new arasuperobject(42,24,22,"بل بعض النساء منا حيرننا اذ كنّ باكرا عند القبر"));
arasuperobject.biblemap.put("b42_24_23", new arasuperobject(42,24,23,"ولما لم يجدن جسده أتين قائلات انهنّ رأين منظر ملائكة قالوا انه حيّ"));
arasuperobject.biblemap.put("b42_24_24", new arasuperobject(42,24,24,"ومضى قوم من الذين معنا الى القبر فوجدوا هكذا كما قالت ايضا النساء واما هو فلم يروه"));
arasuperobject.biblemap.put("b42_24_25", new arasuperobject(42,24,25,"فقال لهما ايها الغبيان والبطيئا القلوب في الايمان بجميع ما تكلم به الانبياء"));
arasuperobject.biblemap.put("b42_24_26", new arasuperobject(42,24,26,"أما كان ينبغي ان المسيح يتألم بهذا ويدخل الى مجده"));
arasuperobject.biblemap.put("b42_24_27", new arasuperobject(42,24,27,"ثم ابتدأ من موسى ومن جميع الانبياء يفسر لهما الأمور المختصة به في جميع الكتب"));
arasuperobject.biblemap.put("b42_24_28", new arasuperobject(42,24,28,"ثم اقتربوا الى القرية التي كانا منطلقين اليها وهو تظاهر كانه منطلق الى مكان ابعد"));
arasuperobject.biblemap.put("b42_24_29", new arasuperobject(42,24,29,"فالزماه قائلين امكث معنا لانه نحو المساء وقد مال النهار. فدخل ليمكث معهما"));
arasuperobject.biblemap.put("b42_24_30", new arasuperobject(42,24,30,"فلما اتكأ معهما اخذ خبزا وبارك وكسّر وناولهما"));
arasuperobject.biblemap.put("b42_24_31", new arasuperobject(42,24,31,"فانفتحت اعينهما وعرفاه ثم اختفى عنهما"));
arasuperobject.biblemap.put("b42_24_32", new arasuperobject(42,24,32,"فقال بعضهما لبعض ألم يكن قلبنا ملتهبا فينا اذ كان يكلمنا في الطريق ويوضح لنا الكتب"));
arasuperobject.biblemap.put("b42_24_33", new arasuperobject(42,24,33,"فقاما في تلك الساعة ورجعا الى اورشليم ووجدا الاحد عشر مجتمعين هم والذين معهم"));
arasuperobject.biblemap.put("b42_24_34", new arasuperobject(42,24,34,"وهم يقولون ان الرب قام بالحقيقة وظهر لسمعان"));
arasuperobject.biblemap.put("b42_24_35", new arasuperobject(42,24,35,"واما هما فكانا يخبران بما حدث في الطريق وكيف عرفاه عند كسر الخبز"));
arasuperobject.biblemap.put("b42_24_36", new arasuperobject(42,24,36,"وفيما هم يتكلمون بهذا وقف يسوع نفسه في وسطهم وقال لهم سلام لكم"));
arasuperobject.biblemap.put("b42_24_37", new arasuperobject(42,24,37,"فجزعوا وخافوا وظنوا انهم نظروا روحا"));
arasuperobject.biblemap.put("b42_24_38", new arasuperobject(42,24,38,"فقال لهم ما بالكم مضطربين ولماذا تخطر افكار في قلوبكم"));
arasuperobject.biblemap.put("b42_24_39", new arasuperobject(42,24,39,"انظروا يديّ ورجليّ اني انا هو. جسوني وانظروا فان الروح ليس له لحم وعظام كما ترون لي"));
arasuperobject.biblemap.put("b42_24_40", new arasuperobject(42,24,40,"وحين قال هذا أراهم يديه ورجليه"));
arasuperobject.biblemap.put("b42_24_41", new arasuperobject(42,24,41,"وبينما هم غير مصدقين من الفرح ومتعجبون قال لهم أعندكم ههنا طعام"));
arasuperobject.biblemap.put("b42_24_42", new arasuperobject(42,24,42,"فناولوه جزءا من سمك مشوي وشيئا من شهد عسل"));
arasuperobject.biblemap.put("b42_24_43", new arasuperobject(42,24,43,"فأخذ وأكل قدامهم"));
arasuperobject.biblemap.put("b42_24_44", new arasuperobject(42,24,44,"وقال لهم هذا هو الكلام الذي كلمتكم به وانا بعد معكم انه لا بد ان يتم جميع ما هو مكتوب عني في ناموس موسى والانبياء والمزامير"));
arasuperobject.biblemap.put("b42_24_45", new arasuperobject(42,24,45,"حينئذ فتح ذهنهم ليفهموا الكتب"));
arasuperobject.biblemap.put("b42_24_46", new arasuperobject(42,24,46,"وقال لهم هكذا هو مكتوب وهكذا كان ينبغي ان المسيح يتألم ويقوم من الاموات في اليوم الثالث"));
arasuperobject.biblemap.put("b42_24_47", new arasuperobject(42,24,47,"وان يكرز باسمه بالتوبة ومغفرة الخطايا لجميع الامم مبتدأ من اورشليم"));
arasuperobject.biblemap.put("b42_24_48", new arasuperobject(42,24,48,"وانتم شهود لذلك"));
arasuperobject.biblemap.put("b42_24_49", new arasuperobject(42,24,49,"وها انا ارسل اليكم موعد ابي. فاقيموا في مدينة اورشليم الى ان تلبسوا قوة من الاعالي"));
arasuperobject.biblemap.put("b42_24_50", new arasuperobject(42,24,50,"واخرجهم خارجا الى بيت عنيا. ورفع يديه وباركهم"));
arasuperobject.biblemap.put("b42_24_51", new arasuperobject(42,24,51,"وفيما هو يباركهم انفرد عنهم وأصعد الى السماء"));
arasuperobject.biblemap.put("b42_24_52", new arasuperobject(42,24,52,"فسجدوا له ورجعوا الى اورشليم بفرح عظيم"));
arasuperobject.biblemap.put("b42_24_53", new arasuperobject(42,24,53,"وكانوا كل حين في الهيكل يسبّحون ويباركون الله آمين"));

