arasuperobject.biblemap.put("b45_4_11", new arasuperobject(45,4,11,"واخذ علامة الختان ختما لبر الايمان الذي كان في الغرلة ليكون ابا لجميع الذين يؤمنون وهم في الغرلة كي يحسب لهم ايضا البر"));
arasuperobject.biblemap.put("b45_4_12", new arasuperobject(45,4,12,"وابا للختان للذين ليسوا من الختان فقط بل ايضا يسلكون في خطوات ايمان ابينا ابراهيم الذي كان وهو في الغرلة"));
arasuperobject.biblemap.put("b45_4_13", new arasuperobject(45,4,13,"فانه ليس بالناموس كان الوعد لابراهيم او لنسله ان يكون وارثا للعالم بل ببر الايمان"));
arasuperobject.biblemap.put("b45_4_14", new arasuperobject(45,4,14,"لانه ان كان الذين من الناموس هم ورثة فقد تعطل الايمان وبطل الوعد"));
arasuperobject.biblemap.put("b45_4_15", new arasuperobject(45,4,15,"لان الناموس ينشئ غضبا اذ حيث ليس ناموس ليس ايضا تعدّ"));
arasuperobject.biblemap.put("b45_4_16", new arasuperobject(45,4,16,"لهذا هو من الايمان كي يكون على سبيل النعمة ليكون الوعد وطيدا لجميع النسل ليس لمن هو من الناموس فقط بل ايضا لمن هو من ايمان ابراهيم الذي هو اب لجميعنا"));
arasuperobject.biblemap.put("b45_4_17", new arasuperobject(45,4,17,"كما هو مكتوب اني قد جعلتك ابا لامم كثيرة. امام الله الذي آمن به الذي يحيي الموتى ويدعو الاشياء غير الموجودة كانها موجودة"));
arasuperobject.biblemap.put("b45_4_18", new arasuperobject(45,4,18,"فهو على خلاف الرجاء آمن على الرجاء لكي يصير ابا لامم كثيرة كما قيل هكذا يكون نسلك"));
arasuperobject.biblemap.put("b45_4_19", new arasuperobject(45,4,19,"واذ لم يكن ضعيفا في الايمان لم يعتبر جسده وهو قد صار مماتا اذ كان ابن نحو مئة سنة ولا مماتية مستودع سارة"));
arasuperobject.biblemap.put("b45_4_20", new arasuperobject(45,4,20,"ولا بعدم ايمان ارتاب في وعد الله بل تقوّى بالايمان معطيا مجدا للّه"));
