arasuperobject.biblemap.put("b45_4_23", new arasuperobject(45,4,23,"ولكن لم يكتب من اجله وحده انه حسب له"));
arasuperobject.biblemap.put("b45_4_24", new arasuperobject(45,4,24,"بل من اجلنا نحن ايضا الذين سيحسب لنا الذين نؤمن بمن اقام يسوع ربنا من الاموات"));
