arasuperobject.biblemap.put("b45_5_1", new arasuperobject(45,5,1,"فاذ قد تبررنا بالايمان لنا سلام مع الله بربنا يسوع المسيح"));
arasuperobject.biblemap.put("b45_5_2", new arasuperobject(45,5,2,"الذي به ايضا قد صار لنا الدخول بالايمان الى هذه النعمة التي نحن فيها مقيمون ونفتخر على رجاء مجد الله"));
arasuperobject.biblemap.put("b45_5_3", new arasuperobject(45,5,3,"وليس ذلك فقط بل نفتخر ايضا في الضيقات عالمين ان الضيق ينشئ صبرا"));
