arasuperobject.biblemap.put("b45_5_9", new arasuperobject(45,5,9,"فبالأولى كثيرا ونحن متبرّرون الآن بدمه نخلص به من الغضب"));
arasuperobject.biblemap.put("b45_5_10", new arasuperobject(45,5,10,"لانه ان كنا ونحن اعداء قد صولحنا مع الله بموت ابنه فبالأولى كثيرا ونحن مصالحون نخلص بحياته"));
arasuperobject.biblemap.put("b45_5_11", new arasuperobject(45,5,11,"وليس ذلك فقط بل نفتخر ايضا بالله بربنا يسوع المسيح الذي نلنا به الآن المصالحة"));
arasuperobject.biblemap.put("b45_5_12", new arasuperobject(45,5,12,"من اجل ذلك كأنما بانسان واحد دخلت الخطية الى العالم وبالخطية الموت وهكذا اجتاز الموت الى جميع الناس اذ اخطأ الجميع"));
arasuperobject.biblemap.put("b45_5_13", new arasuperobject(45,5,13,"فانه حتى الناموس كانت الخطية في العالم. على ان الخطية لا تحسب ان لم يكن ناموس"));
