arasuperobject.biblemap.put("b45_5_15", new arasuperobject(45,5,15,"ولكن ليس كالخطية هكذا ايضا الهبة. لانه ان كان بخطية واحد مات الكثيرون فبالأولى كثيرا نعمة الله والعطية بالنعمة التي بالانسان الواحد يسوع المسيح قد ازدادت للكثيرين"));
arasuperobject.biblemap.put("b45_5_16", new arasuperobject(45,5,16,"وليس كما بواحد قد اخطأ هكذا العطية. لان الحكم من واحد للدينونة. واما الهبة فمن جرى خطايا كثيرة للتبرير"));
arasuperobject.biblemap.put("b45_5_17", new arasuperobject(45,5,17,"لانه ان كان بخطية الواحد قد ملك الموت بالواحد فبالأولى كثيرا الذين ينالون فيض النعمة وعطية البر سيملكون في الحياة بالواحد يسوع المسيح"));
arasuperobject.biblemap.put("b45_5_18", new arasuperobject(45,5,18,"فاذا كما بخطية واحدة صار الحكم الى جميع الناس للدينونة هكذا ببر واحد صارت الهبة الى جميع الناس لتبرير الحياة"));
arasuperobject.biblemap.put("b45_5_19", new arasuperobject(45,5,19,"لانه كما بمعصية الانسان الواحد جعل الكثيرون خطاة هكذا ايضا باطاعة الواحد سيجعل الكثيرون ابرارا"));
arasuperobject.biblemap.put("b45_5_20", new arasuperobject(45,5,20,"واما الناموس فدخل لكي تكثر الخطية. ولكن حيث كثرت الخطية ازدادت النعمة جدا"));
arasuperobject.biblemap.put("b45_5_21", new arasuperobject(45,5,21,"حتى كما ملكت الخطية في الموت هكذا تملك النعمة بالبر للحياة الابدية بيسوع المسيح ربنا"));
