arasuperobject.biblemap.put("b45_6_3", new arasuperobject(45,6,3,"ام تجهلون اننا كل من اعتمد ليسوع المسيح اعتمدنا لموته"));
arasuperobject.biblemap.put("b45_6_4", new arasuperobject(45,6,4,"فدفنا معه بالمعمودية للموت حتى كما أقيم المسيح من الاموات بمجد الآب هكذا نسلك نحن ايضا في جدة الحياة"));
arasuperobject.biblemap.put("b45_6_5", new arasuperobject(45,6,5,"لانه ان كنا قد صرنا متحدين معه بشبه موته نصير ايضا بقيامته"));
arasuperobject.biblemap.put("b45_6_6", new arasuperobject(45,6,6,"عالمين هذا ان انساننا العتيق قد صلب معه ليبطل جسد الخطية كي لا نعود نستعبد ايضا للخطية"));
arasuperobject.biblemap.put("b45_6_7", new arasuperobject(45,6,7,"لان الذي مات قد تبرأ من الخطية"));
