arasuperobject.biblemap.put("b45_6_23", new arasuperobject(45,6,23,"لان اجرة الخطية هي موت. واما هبة الله فهي حياة ابدية بالمسيح يسوع ربنا"));
arasuperobject.biblemap.put("b45_7_1", new arasuperobject(45,7,1,"ام تجهلون ايها الاخوة. لاني اكلم العارفين بالناموس. ان الناموس يسود على الانسان ما دام حيّا"));
arasuperobject.biblemap.put("b45_7_2", new arasuperobject(45,7,2,"فان المرأة التي تحت رجل هي مرتبطة بالناموس بالرجل الحي. ولكن ان مات الرجل فقد تحررت من ناموس الرجل"));
arasuperobject.biblemap.put("b45_7_3", new arasuperobject(45,7,3,"فاذا ما دام الرجل حيّا تدعى زانية ان صارت لرجل آخر. ولكن ان مات الرجل فهي حرة من الناموس حتى انها ليست زانية ان صارت لرجل آخر"));
arasuperobject.biblemap.put("b45_7_4", new arasuperobject(45,7,4,"اذا يا اخوتي انتم ايضا قد متم للناموس بجسد المسيح لكي تصيروا لآخر للذي قد أقيم من الاموات لنثمر للّه"));
arasuperobject.biblemap.put("b45_7_5", new arasuperobject(45,7,5,"لانه لما كنا في الجسد كانت اهواء الخطايا التي بالناموس تعمل في اعضائنا لكي نثمر للموت"));
arasuperobject.biblemap.put("b45_7_6", new arasuperobject(45,7,6,"واما الآن فقد تحررنا من الناموس اذ مات الذي كنا ممسكين فيه حتى نعبد بجدة الروح لا بعتق الحرف"));
arasuperobject.biblemap.put("b45_7_7", new arasuperobject(45,7,7,"فماذا نقول. هل الناموس خطية. حاشا. بل لم اعرف الخطية الا بالناموس. فانني لم اعرف الشهوة لو لم يقل الناموس لا تشته"));
arasuperobject.biblemap.put("b45_7_8", new arasuperobject(45,7,8,"ولكن الخطية وهي متخذة فرصة بالوصية انشأت في كل شهوة. لان بدون الناموس الخطية ميتة"));
arasuperobject.biblemap.put("b45_7_9", new arasuperobject(45,7,9,"اما انا فكنت بدون الناموس عائشا قبلا. ولكن لما جاءت الوصية عاشت الخطية فمتّ انا"));
arasuperobject.biblemap.put("b45_7_10", new arasuperobject(45,7,10,"فوجدت الوصية التي للحياة هي نفسها لي للموت"));
arasuperobject.biblemap.put("b45_7_11", new arasuperobject(45,7,11,"لان الخطية وهي متخذة فرصة بالوصية خدعتني بها وقتلتني"));
arasuperobject.biblemap.put("b45_7_12", new arasuperobject(45,7,12,"اذا الناموس مقدس والوصية مقدسة وعادلة وصالحة"));
arasuperobject.biblemap.put("b45_7_13", new arasuperobject(45,7,13,"فهل صار لي الصالح موتا. حاشا. بل الخطية. لكي تظهر خطية منشئة لي بالصالح موتا لكي تصير الخطية خاطئة جدا بالوصية"));
arasuperobject.biblemap.put("b45_7_14", new arasuperobject(45,7,14,"فاننا نعلم ان الناموس روحي واما انا فجسدي مبيع تحت الخطية"));
arasuperobject.biblemap.put("b45_7_15", new arasuperobject(45,7,15,"لاني لست اعرف ما انا افعله اذ لست افعل ما اريده بل ما ابغضه فاياه افعل"));
arasuperobject.biblemap.put("b45_7_16", new arasuperobject(45,7,16,"فان كنت افعل ما لست اريده فاني اصادق الناموس انه حسن"));
arasuperobject.biblemap.put("b45_7_17", new arasuperobject(45,7,17,"فالآن لست بعد افعل ذلك انا بل الخطية الساكنة فيّ"));
arasuperobject.biblemap.put("b45_7_18", new arasuperobject(45,7,18,"فاني اعلم انه ليس ساكن فيّ اي في جسدي شيء صالح. لان الارادة حاضرة عندي واما ان افعل الحسنى فلست اجد"));
arasuperobject.biblemap.put("b45_7_19", new arasuperobject(45,7,19,"لاني لست افعل الصالح الذي اريده بل الشر الذي لست اريده فاياه افعل"));
arasuperobject.biblemap.put("b45_7_20", new arasuperobject(45,7,20,"فان كنت ما لست اريده اياه افعل فلست بعد افعله انا بل الخطية الساكنة فيّ"));
arasuperobject.biblemap.put("b45_7_21", new arasuperobject(45,7,21,"اذا اجد الناموس لي حينما اريد ان افعل الحسنى ان الشر حاضر عندي"));
arasuperobject.biblemap.put("b45_7_22", new arasuperobject(45,7,22,"فاني اسرّ بناموس الله بحسب الانسان الباطن"));
arasuperobject.biblemap.put("b45_7_23", new arasuperobject(45,7,23,"ولكني ارى ناموسا آخر في اعضائي يحارب ناموس ذهني ويسبيني الى ناموس الخطية الكائن في اعضائي"));
arasuperobject.biblemap.put("b45_7_24", new arasuperobject(45,7,24,"ويحي انا الانسان الشقي. من ينقذني من جسد هذا الموت"));
arasuperobject.biblemap.put("b45_7_25", new arasuperobject(45,7,25,"اشكر الله بيسوع المسيح ربنا. اذا انا نفسي بذهني اخدم ناموس الله ولكن بالجسد ناموس الخطية"));
arasuperobject.biblemap.put("b45_8_1", new arasuperobject(45,8,1,"اذا لا شيء من الدينونة الآن على الذين هم في المسيح يسوع السالكين ليس حسب الجسد بل حسب الروح"));
arasuperobject.biblemap.put("b45_8_2", new arasuperobject(45,8,2,"لان ناموس روح الحياة في المسيح يسوع قد اعتقني من ناموس الخطية والموت"));
arasuperobject.biblemap.put("b45_8_3", new arasuperobject(45,8,3,"لانه ما كان الناموس عاجزا عنه في ما كان ضعيفا بالجسد فالله اذ ارسل ابنه في شبه جسد الخطية ولاجل الخطية دان الخطية في الجسد"));
arasuperobject.biblemap.put("b45_8_4", new arasuperobject(45,8,4,"لكي يتم حكم الناموس فينا نحن السالكين ليس حسب الجسد بل حسب الروح"));
arasuperobject.biblemap.put("b45_8_5", new arasuperobject(45,8,5,"فان الذين هم حسب الجسد فبما للجسد يهتمون ولكن الذين حسب الروح فبما للروح"));
arasuperobject.biblemap.put("b45_8_6", new arasuperobject(45,8,6,"لان اهتمام الجسد هو موت ولكن اهتمام الروح هو حياة وسلام"));
arasuperobject.biblemap.put("b45_8_7", new arasuperobject(45,8,7,"لان اهتمام الجسد هو عداوة للّه اذ ليس هو خاضعا لناموس الله لانه ايضا لا يستطيع"));
arasuperobject.biblemap.put("b45_8_8", new arasuperobject(45,8,8,"فالذين هم في الجسد لا يستطيعون ان يرضوا الله"));
arasuperobject.biblemap.put("b45_8_9", new arasuperobject(45,8,9,"واما انتم فلستم في الجسد بل في الروح ان كان روح الله ساكنا فيكم. ولكن ان كان احد ليس له روح المسيح فذلك ليس له"));
arasuperobject.biblemap.put("b45_8_10", new arasuperobject(45,8,10,"وان كان المسيح فيكم فالجسد ميت بسبب الخطية واما الروح فحياة بسبب البر"));
arasuperobject.biblemap.put("b45_8_11", new arasuperobject(45,8,11,"وان كان روح الذي اقام يسوع من الاموات ساكنا فيكم فالذي اقام المسيح من الاموات سيحيي اجسادكم المائتة ايضا بروحه الساكن فيكم"));
arasuperobject.biblemap.put("b45_8_12", new arasuperobject(45,8,12,"فاذا ايها الاخوة نحن مديونون ليس للجسد لنعيش حسب الجسد"));
arasuperobject.biblemap.put("b45_8_13", new arasuperobject(45,8,13,"لانه ان عشتم حسب الجسد فستموتون. ولكن ان كنتم بالروح تميتون اعمال الجسد فستحيون"));
arasuperobject.biblemap.put("b45_8_14", new arasuperobject(45,8,14,"لان كل الذين ينقادون بروح الله فاولئك هم ابناء الله"));
arasuperobject.biblemap.put("b45_8_15", new arasuperobject(45,8,15,"اذ لم تأخذوا روح العبودية ايضا للخوف بل اخذتم روح التبني الذي به نصرخ يا أبا الآب"));
arasuperobject.biblemap.put("b45_8_16", new arasuperobject(45,8,16,"الروح نفسه ايضا يشهد لارواحنا اننا اولاد الله"));
arasuperobject.biblemap.put("b45_8_17", new arasuperobject(45,8,17,"فان كنا اولادا فاننا ورثة ايضا ورثة الله ووارثون مع المسيح. ان كنا نتألم معه لكي نتمجد ايضا معه"));
arasuperobject.biblemap.put("b45_8_18", new arasuperobject(45,8,18,"فاني احسب ان آلام الزمان الحاضر لا تقاس بالمجد العتيد ان يستعلن فينا"));
arasuperobject.biblemap.put("b45_8_19", new arasuperobject(45,8,19,"لان انتظار الخليقة يتوقع استعلان ابناء الله"));
arasuperobject.biblemap.put("b45_8_20", new arasuperobject(45,8,20,"اذ أخضعت الخليقة للبطل. ليس طوعا بل من اجل الذي اخضعها. على الرجاء"));
arasuperobject.biblemap.put("b45_8_21", new arasuperobject(45,8,21,"لان الخليقة نفسها ايضا ستعتق من عبودية الفساد الى حرية مجد اولاد الله"));
arasuperobject.biblemap.put("b45_8_22", new arasuperobject(45,8,22,"فاننا نعلم ان كل الخليقة تئن وتتمخض معا الى الآن"));
arasuperobject.biblemap.put("b45_8_23", new arasuperobject(45,8,23,"وليس هكذا فقط بل نحن الذين لنا باكورة الروح نحن انفسنا ايضا نئن في انفسنا متوقعين التبني فداء اجسادنا"));
arasuperobject.biblemap.put("b45_8_24", new arasuperobject(45,8,24,"لاننا بالرجاء خلصنا. ولكن الرجاء المنظور ليس رجاء. لان ما ينظره احد كيف يرجوه ايضا"));
arasuperobject.biblemap.put("b45_8_25", new arasuperobject(45,8,25,"ولكن ان كنا نرجو ما لسنا ننظره فاننا نتوقعه بالصبر"));
arasuperobject.biblemap.put("b45_8_26", new arasuperobject(45,8,26,"وكذلك الروح ايضا يعين ضعفاتنا. لاننا لسنا نعلم ما نصلّي لاجله كما ينبغي ولكن الروح نفسه يشفع فينا بأنّات لا ينطق بها"));
arasuperobject.biblemap.put("b45_8_27", new arasuperobject(45,8,27,"ولكن الذي يفحص القلوب يعلم ما هو اهتمام الروح. لانه بحسب مشيئة الله يشفع في القديسين"));
arasuperobject.biblemap.put("b45_8_28", new arasuperobject(45,8,28,"ونحن نعلم ان كل الاشياء تعمل معا للخير للذين يحبون الله الذين هم مدعوون حسب قصده"));
arasuperobject.biblemap.put("b45_8_29", new arasuperobject(45,8,29,"لان الذين سبق فعرفهم سبق فعيّنهم ليكونوا مشابهين صورة ابنه ليكون هو بكرا بين اخوة كثيرين"));
arasuperobject.biblemap.put("b45_8_30", new arasuperobject(45,8,30,"والذين سبق فعيّنهم فهؤلاء دعاهم ايضا. والذين دعاهم فهؤلاء بررهم ايضا. والذين بررهم فهؤلاء مجدهم ايضا"));
arasuperobject.biblemap.put("b45_8_31", new arasuperobject(45,8,31,"فماذا نقول لهذا. ان كان الله معنا فمن علينا"));
arasuperobject.biblemap.put("b45_8_32", new arasuperobject(45,8,32,"الذي لم يشفق على ابنه بل بذله لاجلنا اجمعين كيف لا يهبنا ايضا معه كل شيء"));
arasuperobject.biblemap.put("b45_8_33", new arasuperobject(45,8,33,"من سيشتكي على مختاري الله. الله هو الذي يبرر"));
arasuperobject.biblemap.put("b45_8_34", new arasuperobject(45,8,34,"من هو الذي يدين. المسيح هو الذي مات بل بالحري قام ايضا الذي هو ايضا عن يمين الله الذي ايضا يشفع فينا"));
arasuperobject.biblemap.put("b45_8_35", new arasuperobject(45,8,35,"من سيفصلنا عن محبة المسيح. أشدّة ام ضيق ام اضطهاد ام جوع ام عري ام خطر ام سيف"));
arasuperobject.biblemap.put("b45_8_36", new arasuperobject(45,8,36,"كما هو مكتوب اننا من اجلك نمات كل النهار. قد حسبنا مثل غنم للذبح"));
arasuperobject.biblemap.put("b45_8_37", new arasuperobject(45,8,37,"ولكننا في هذه جميعها يعظم انتصارنا بالذي احبنا"));
arasuperobject.biblemap.put("b45_8_38", new arasuperobject(45,8,38,"فاني متيقن انه لا موت ولا حياة ولا ملائكة ولا رؤساء ولا قوات ولا امور حاضرة ولا مستقبلة"));
arasuperobject.biblemap.put("b45_8_39", new arasuperobject(45,8,39,"ولا علو ولا عمق ولا خليقة اخرى تقدر ان تفصلنا عن محبة الله التي في المسيح يسوع ربنا"));
arasuperobject.biblemap.put("b45_9_1", new arasuperobject(45,9,1,"اقول الصدق في المسيح. لا اكذب وضميري شاهد لي بالروح القدس"));
arasuperobject.biblemap.put("b45_9_2", new arasuperobject(45,9,2,"ان لي حزنا عظيما ووجعا في قلبي لا ينقطع"));
arasuperobject.biblemap.put("b45_9_3", new arasuperobject(45,9,3,"فاني كنت اود لو اكون انا نفسي محروما من المسيح لاجل اخوتي انسبائي حسب الجسد"));
arasuperobject.biblemap.put("b45_9_4", new arasuperobject(45,9,4,"الذين هم اسرائيليون ولهم التبني والمجد والعهود والاشتراع والعبادة والمواعيد"));
arasuperobject.biblemap.put("b45_9_5", new arasuperobject(45,9,5,"ولهم الآباء ومنهم المسيح حسب الجسد الكائن على الكل الها مباركا الى الابد آمين"));
arasuperobject.biblemap.put("b45_9_6", new arasuperobject(45,9,6,"ولكن ليس هكذا حتى ان كلمة الله قد سقطت. لان ليس جميع الذين من اسرائيل هم اسرائيليون"));
arasuperobject.biblemap.put("b45_9_7", new arasuperobject(45,9,7,"ولا لانهم من نسل ابراهيم هم جميعا اولاد. بل باسحق يدعى لك نسل"));
arasuperobject.biblemap.put("b45_9_8", new arasuperobject(45,9,8,"اي ليس اولاد الجسد هم اولاد الله بل اولاد الموعد يحسبون نسلا"));
arasuperobject.biblemap.put("b45_9_9", new arasuperobject(45,9,9,"لان كلمة الموعد هي هذه. انا آتي نحو هذا الوقت ويكون لسارة ابن"));
arasuperobject.biblemap.put("b45_9_10", new arasuperobject(45,9,10,"وليس ذلك فقط بل رفقة ايضا وهي حبلى من واحد وهو اسحق ابونا"));
arasuperobject.biblemap.put("b45_9_11", new arasuperobject(45,9,11,"لانه وهما لم يولدا بعد ولا فعلا خيرا او شرا لكي يثبت قصد الله حسب الاختيار ليس من الاعمال بل من الذي يدعو"));
arasuperobject.biblemap.put("b45_9_12", new arasuperobject(45,9,12,"قيل لها ان الكبير يستعبد للصغير"));
arasuperobject.biblemap.put("b45_9_13", new arasuperobject(45,9,13,"كما هو مكتوب احببت يعقوب وابغضت عيسو"));
arasuperobject.biblemap.put("b45_9_14", new arasuperobject(45,9,14,"فماذا نقول. ألعل عند الله ظلما. حاشا"));
arasuperobject.biblemap.put("b45_9_15", new arasuperobject(45,9,15,"لانه يقول لموسى اني ارحم من ارحم واتراءف على من اتراءف"));
arasuperobject.biblemap.put("b45_9_16", new arasuperobject(45,9,16,"فاذا ليس لمن يشاء ولا لمن يسعى بل للّه الذي يرحم"));
arasuperobject.biblemap.put("b45_9_17", new arasuperobject(45,9,17,"لانه يقول الكتاب لفرعون اني لهذا بعينه اقمتك لكي اظهر فيك قوتي ولكي ينادى باسمي في كل الارض"));
arasuperobject.biblemap.put("b45_9_18", new arasuperobject(45,9,18,"فاذا هو يرحم من يشاء ويقسي من يشاء"));
arasuperobject.biblemap.put("b45_9_19", new arasuperobject(45,9,19,"فستقول لي لماذا يلوم بعد. لان من يقاوم مشيئته"));
arasuperobject.biblemap.put("b45_9_20", new arasuperobject(45,9,20,"بل من انت ايها الانسان الذي تجاوب الله. ألعل الجبلة تقول لجابلها لماذا صنعتني هكذا"));
arasuperobject.biblemap.put("b45_9_21", new arasuperobject(45,9,21,"ام ليس للخزاف سلطان على الطين ان يصنع من كتلة واحدة اناء للكرامة وآخر للهوان"));
arasuperobject.biblemap.put("b45_9_22", new arasuperobject(45,9,22,"فماذا ان كان الله وهو يريد ان يظهر غضبه ويبيّن قوته احتمل باناة كثيرة آنية غضب مهيأة للهلاك"));
arasuperobject.biblemap.put("b45_9_23", new arasuperobject(45,9,23,"ولكي يبيّن غنى مجده على آنية رحمة قد سبق فاعدّها للمجد"));
arasuperobject.biblemap.put("b45_9_24", new arasuperobject(45,9,24,"التي ايضا دعانا نحن اياها ليس من اليهود فقط بل من الامم ايضا"));
arasuperobject.biblemap.put("b45_9_25", new arasuperobject(45,9,25,"كما يقول في هوشع ايضا سادعو الذي ليس شعبي شعبي والتي ليست محبوبة محبوبة"));
arasuperobject.biblemap.put("b45_9_26", new arasuperobject(45,9,26,"ويكون في الموضع الذي قيل لهم فيه لستم شعبي انه هناك يدعون ابناء الله الحي"));
arasuperobject.biblemap.put("b45_9_27", new arasuperobject(45,9,27,"واشعياء يصرخ من جهة اسرائيل وان كان عدد بني اسرائيل كرمل البحر فالبقية ستخلص"));
arasuperobject.biblemap.put("b45_9_28", new arasuperobject(45,9,28,"لانه متمم امر وقاض بالبر. لان الرب يصنع امرا مقضيا به على الارض"));
arasuperobject.biblemap.put("b45_9_29", new arasuperobject(45,9,29,"وكما سبق اشعياء فقال لولا ان رب الجنود ابقى لنا نسلا لصرنا مثل سدوم وشابهنا عمورة"));
arasuperobject.biblemap.put("b45_9_30", new arasuperobject(45,9,30,"فماذا نقول. ان الامم الذين لم يسعوا في اثر البر ادركوا البر. البر الذي بالايمان"));
arasuperobject.biblemap.put("b45_9_31", new arasuperobject(45,9,31,"ولكن اسرائيل وهو يسعى في اثر ناموس البر لم يدرك ناموس البر"));
arasuperobject.biblemap.put("b45_9_32", new arasuperobject(45,9,32,"لماذا. لانه فعل ذلك ليس بالايمان بل كانه باعمال الناموس. فانهم اصطدموا بحجر الصدمة"));
arasuperobject.biblemap.put("b45_9_33", new arasuperobject(45,9,33,"كما هو مكتوب ها انا اضع في صهيون حجر صدمة وصخرة عثرة وكل من يؤمن به لا يخزى"));
arasuperobject.biblemap.put("b45_10_1", new arasuperobject(45,10,1,"ايها الاخوة ان مسرّة قلبي وطلبتي الى الله لاجل اسرائيل هي للخلاص"));
arasuperobject.biblemap.put("b45_10_2", new arasuperobject(45,10,2,"لاني اشهد لهم ان لهم غيرة الله ولكن ليس حسب المعرفة"));
arasuperobject.biblemap.put("b45_10_3", new arasuperobject(45,10,3,"لانهم اذ كانوا يجهلون بر الله ويطلبون ان يثبتوا بر انفسهم لم يخضعوا لبر الله"));
arasuperobject.biblemap.put("b45_10_4", new arasuperobject(45,10,4,"لان غاية الناموس هي المسيح للبر لكل من يؤمن"));
arasuperobject.biblemap.put("b45_10_5", new arasuperobject(45,10,5,"لان موسى يكتب في البر الذي بالناموس ان الانسان الذي يفعلها سيحيا بها"));
arasuperobject.biblemap.put("b45_10_6", new arasuperobject(45,10,6,"واما البر الذي بالايمان فيقول هكذا لا تقل في قلبك من يصعد الى السماء اي ليحدر المسيح"));
arasuperobject.biblemap.put("b45_10_7", new arasuperobject(45,10,7,"او من يهبط الى الهاوية اي ليصعد المسيح من الاموات"));
arasuperobject.biblemap.put("b45_10_8", new arasuperobject(45,10,8,"لكن ماذا يقول. الكلمة قريبة منك في فمك وفي قلبك اي كلمة الايمان التي نكرز بها"));
arasuperobject.biblemap.put("b45_10_9", new arasuperobject(45,10,9,"لانك ان اعترفت بفمك بالرب يسوع وآمنت بقلبك ان الله اقامه من الاموات خلصت"));
arasuperobject.biblemap.put("b45_10_10", new arasuperobject(45,10,10,"لان القلب يؤمن به للبر والفم يعترف به للخلاص"));
arasuperobject.biblemap.put("b45_10_11", new arasuperobject(45,10,11,"لان الكتاب يقول كل من يؤمن به لا يخزى"));
arasuperobject.biblemap.put("b45_10_12", new arasuperobject(45,10,12,"لانه لا فرق بين اليهودي واليوناني لان ربا واحدا للجميع غنيا لجميع الذين يدعون به"));
arasuperobject.biblemap.put("b45_10_13", new arasuperobject(45,10,13,"لان كل من يدعو باسم الرب يخلص"));
arasuperobject.biblemap.put("b45_10_14", new arasuperobject(45,10,14,"فكيف يدعون بمن لم يؤمنوا به. وكيف يؤمنون بمن لم يسمعوا به. وكيف يسمعون بلا كارز"));
arasuperobject.biblemap.put("b45_10_15", new arasuperobject(45,10,15,"وكيف يكرزون ان لم يرسلوا. كما هو مكتوب ما اجمل اقدام المبشرين بالسلام المبشرين بالخيرات"));
arasuperobject.biblemap.put("b45_10_16", new arasuperobject(45,10,16,"لكن ليس الجميع قد اطاعوا الانجيل لان اشعياء يقول يا رب من صدق خبرنا"));
arasuperobject.biblemap.put("b45_10_17", new arasuperobject(45,10,17,"اذا الايمان بالخبر والخبر بكلمة الله"));
arasuperobject.biblemap.put("b45_10_18", new arasuperobject(45,10,18,"لكنني اقول ألعلهم لم يسمعوا. بلى. الى كل الارض خرج صوتهم والى اقاصي المسكونة اقوالهم"));
arasuperobject.biblemap.put("b45_10_19", new arasuperobject(45,10,19,"لكني اقول ألعل اسرائيل لم يعلم. اولا موسى يقول انا أغيركم بما ليس امة. بامة غبية اغيظكم"));
arasuperobject.biblemap.put("b45_10_20", new arasuperobject(45,10,20,"ثم اشعياء يتجاسر ويقول وجدت من الذين لم يطلبوني وصرت ظاهرا للذين لم يسألوا عني"));
arasuperobject.biblemap.put("b45_10_21", new arasuperobject(45,10,21,"اما من جهة اسرائيل فيقول طول النهار بسطت يديّ الى شعب معاند ومقاوم"));
arasuperobject.biblemap.put("b45_11_1", new arasuperobject(45,11,1,"فاقول ألعل الله رفض شعبه. حاشا. لاني انا ايضا اسرائيلي من نسل ابراهيم من سبط بنيامين"));
arasuperobject.biblemap.put("b45_11_2", new arasuperobject(45,11,2,"لم يرفض الله شعبه الذي سبق فعرفه. ام لستم تعلمون ماذا يقول الكتاب في ايليا كيف يتوسل الى الله ضد اسرائيل قائلا"));
arasuperobject.biblemap.put("b45_11_3", new arasuperobject(45,11,3,"يا رب قتلوا انبياءك وهدموا مذابحك وبقيت انا وحدي وهم يطلبون نفسي"));
arasuperobject.biblemap.put("b45_11_4", new arasuperobject(45,11,4,"لكن ماذا يقول له الوحي. ابقيت لنفسي سبعة آلاف رجل لم يحنوا ركبة لبعل"));
arasuperobject.biblemap.put("b45_11_5", new arasuperobject(45,11,5,"فكذلك في الزمان الحاضر ايضا قد حصلت بقية حسب اختيار النعمة"));
arasuperobject.biblemap.put("b45_11_6", new arasuperobject(45,11,6,"فان كان بالنعمة فليس بعد بالاعمال. وإلا فليست النعمة بعد نعمة. وان كان بالاعمال فليس بعد نعمة. وإلا فالعمل لا يكون بعد عملا"));
arasuperobject.biblemap.put("b45_11_7", new arasuperobject(45,11,7,"فماذا. ما يطلبه اسرائيل ذلك لم ينله. ولكن المختارون نالوه. واما الباقون فتقسوا"));
arasuperobject.biblemap.put("b45_11_8", new arasuperobject(45,11,8,"كما هو مكتوب اعطاهم الله روح سبات وعيونا حتى لا يبصروا وآذانا حتى لا يسمعوا الى هذا اليوم"));
arasuperobject.biblemap.put("b45_11_9", new arasuperobject(45,11,9,"وداود يقول لتصر مائدتهم فخا وقنصا وعثرة ومجازاة لهم"));
arasuperobject.biblemap.put("b45_11_10", new arasuperobject(45,11,10,"لتظلم اعينهم كي لا يبصروا ولتحن ظهورهم في كل حين"));
arasuperobject.biblemap.put("b45_11_11", new arasuperobject(45,11,11,"فاقول ألعلهم عثروا لكي يسقطوا. حاشا. بل بزلتهم صار الخلاص للامم لاغارتهم"));
arasuperobject.biblemap.put("b45_11_12", new arasuperobject(45,11,12,"فان كانت زلتهم غنى للعالم ونقصانهم غنى للامم فكم بالحري ملؤهم"));
arasuperobject.biblemap.put("b45_11_13", new arasuperobject(45,11,13,"فاني اقول لكم ايها الامم. بما اني انا رسول للامم امجد خدمتي"));
arasuperobject.biblemap.put("b45_11_14", new arasuperobject(45,11,14,"لعلي أغير انسبائي واخلّص اناسا منهم"));
arasuperobject.biblemap.put("b45_11_15", new arasuperobject(45,11,15,"لانه ان كان رفضهم هو مصالحة العالم فماذا يكون اقتبالهم الا حياة من الاموات"));
arasuperobject.biblemap.put("b45_11_16", new arasuperobject(45,11,16,"وان كانت الباكورة مقدسة فكذلك العجين. وان كان الاصل مقدسا فكذلك الاغصان"));
arasuperobject.biblemap.put("b45_11_17", new arasuperobject(45,11,17,"فان كان قد قطع بعض الاغصان وانت زيتونة برية طعّمت فيها فصرت شريكا في اصل الزيتونة ودسمها"));
arasuperobject.biblemap.put("b45_11_18", new arasuperobject(45,11,18,"فلا تفتخر على الاغصان. وان افتخرت فانت لست تحمل الاصل بل الاصل اياك يحمل"));
arasuperobject.biblemap.put("b45_11_19", new arasuperobject(45,11,19,"فستقول قطعت الاغصان لأطعم انا"));
arasuperobject.biblemap.put("b45_11_20", new arasuperobject(45,11,20,"حسنا. من اجل عدم الايمان قطعت وانت بالايمان ثبتّ. لا تستكبر بل خف"));
arasuperobject.biblemap.put("b45_11_21", new arasuperobject(45,11,21,"لانه ان كان الله لم يشفق على الاغصان الطبيعية فلعله لا يشفق عليك ايضا"));
arasuperobject.biblemap.put("b45_11_22", new arasuperobject(45,11,22,"فهوذا لطف الله وصرامته. اما الصرامة فعلى الذين سقطوا. واما اللطف فلك ان ثبت في اللطف وإلا فانت ايضا ستقطع"));
arasuperobject.biblemap.put("b45_11_23", new arasuperobject(45,11,23,"وهم ان لم يثبتوا في عدم الايمان سيطعمون. لان الله قادر ان يطعمهم ايضا"));
arasuperobject.biblemap.put("b45_11_24", new arasuperobject(45,11,24,"لانه ان كنت انت قد قطعت من الزيتونة البرية حسب الطبيعة وطعمت بخلاف الطبيعة في زيتونة جيدة فكم بالحري يطعّم هؤلاء الذين هم حسب الطبيعة في زيتونتهم الخاصة"));
arasuperobject.biblemap.put("b45_11_25", new arasuperobject(45,11,25,"فاني لست اريد ايها الاخوة ان تجهلوا هذا السر. لئلا تكونوا عند انفسكم حكماء. ان القساوة قد حصلت جزئيا لاسرائيل الى ان يدخل ملؤ الامم"));
arasuperobject.biblemap.put("b45_11_26", new arasuperobject(45,11,26,"وهكذا سيخلص جميع اسرائيل. كما هو مكتوب سيخرج من صهيون المنقذ ويرد الفجور عن يعقوب"));
arasuperobject.biblemap.put("b45_11_27", new arasuperobject(45,11,27,"وهذا هو العهد من قبلي لهم متى نزعت خطاياهم"));
arasuperobject.biblemap.put("b45_11_28", new arasuperobject(45,11,28,"من جهة الانجيل هم اعداء من اجلكم. واما من جهة الاختيار فهم احباء من اجل الآباء"));
arasuperobject.biblemap.put("b45_11_29", new arasuperobject(45,11,29,"لان هبات الله ودعوته هي بلا ندامة"));
arasuperobject.biblemap.put("b45_11_30", new arasuperobject(45,11,30,"فانه كما كنتم انتم مرة لا تطيعون الله ولكن الآن رحمتم بعصيان هؤلاء"));
arasuperobject.biblemap.put("b45_11_31", new arasuperobject(45,11,31,"هكذا هؤلاء ايضا الآن لم يطيعوا لكي يرحموا هم ايضا برحمتكم"));
arasuperobject.biblemap.put("b45_11_32", new arasuperobject(45,11,32,"لان الله اغلق على الجميع معا في العصيان لكي يرحم الجميع"));
arasuperobject.biblemap.put("b45_11_33", new arasuperobject(45,11,33,"يا لعمق غنى الله وحكمته وعلمه. ما ابعد احكامه عن الفحص وطرقه عن الاستقصاء"));
arasuperobject.biblemap.put("b45_11_34", new arasuperobject(45,11,34,"لان من عرف فكر الرب او من صار له مشيرا"));
arasuperobject.biblemap.put("b45_11_35", new arasuperobject(45,11,35,"او من سبق فاعطاه فيكافأ"));
arasuperobject.biblemap.put("b45_11_36", new arasuperobject(45,11,36,"لان منه وبه وله كل الاشياء. له المجد الى الابد. آمين"));
arasuperobject.biblemap.put("b45_12_1", new arasuperobject(45,12,1,"فاطلب اليكم ايها الاخوة برأفة الله ان تقدموا اجسادكم ذبيحة حية مقدسة مرضية عند الله عبادتكم العقلية"));
arasuperobject.biblemap.put("b45_12_2", new arasuperobject(45,12,2,"ولا تشاكلوا هذا الدهر. بل تغيّروا عن شكلكم بتجديد اذهانكم لتختبروا ما هي ارادة الله الصالحة المرضية الكاملة"));
arasuperobject.biblemap.put("b45_12_3", new arasuperobject(45,12,3,"فاني اقول بالنعمة المعطاة لي لكل من هو بينكم ان لا يرتئي فوق ما ينبغي ان يرتئي بل يرتئي الى التعقل كما قسم الله لكل واحد مقدارا من الايمان"));
arasuperobject.biblemap.put("b45_12_4", new arasuperobject(45,12,4,"فانه كما في جسد واحد لنا اعضاء كثيرة ولكن ليس جميع الاعضاء لها عمل واحد"));
arasuperobject.biblemap.put("b45_12_5", new arasuperobject(45,12,5,"هكذا نحن الكثيرين جسد واحد في المسيح واعضاء بعضا لبعض كل واحد للآخر"));
arasuperobject.biblemap.put("b45_12_6", new arasuperobject(45,12,6,"ولكن لنا مواهب مختلفة بحسب النعمة المعطاة لنا. أنبوّة فبالنسبة الى الايمان"));
arasuperobject.biblemap.put("b45_12_7", new arasuperobject(45,12,7,"ام خدمة ففي الخدمة. ام المعلّم ففي التعليم"));
arasuperobject.biblemap.put("b45_12_8", new arasuperobject(45,12,8,"ام الواعظ ففي الوعظ. المعطي فبسخاء. المدبر فباجتهاد. الراحم فبسرور"));
arasuperobject.biblemap.put("b45_12_9", new arasuperobject(45,12,9,"المحبة فلتكن بلا رياء. كونوا كارهين الشر ملتصقين بالخير"));
arasuperobject.biblemap.put("b45_12_10", new arasuperobject(45,12,10,"وادّين بعضكم بعضا بالمحبة الاخوية. مقدمين بعضكم بعضا في الكرامة"));
arasuperobject.biblemap.put("b45_12_11", new arasuperobject(45,12,11,"غير متكاسلين في الاجتهاد. حارّين في الروح. عابدين الرب"));
arasuperobject.biblemap.put("b45_12_12", new arasuperobject(45,12,12,"فرحين في الرجاء. صابرين في الضيق. مواظبين على الصلاة"));
arasuperobject.biblemap.put("b45_12_13", new arasuperobject(45,12,13,"مشتركين في احتياجات القديسين. عاكفين على اضافة الغرباء"));
arasuperobject.biblemap.put("b45_12_14", new arasuperobject(45,12,14,"باركوا على الذين يضطهدونكم. باركوا ولا تلعنوا"));
arasuperobject.biblemap.put("b45_12_15", new arasuperobject(45,12,15,"فرحا مع الفرحين وبكاء مع الباكين"));
arasuperobject.biblemap.put("b45_12_16", new arasuperobject(45,12,16,"مهتمين بعضكم لبعض اهتماما واحدا غير مهتمين بالامور العالية بل منقادين الى المتضعين. لا تكونوا حكماء عند انفسكم"));
arasuperobject.biblemap.put("b45_12_17", new arasuperobject(45,12,17,"لا تجازوا احدا عن شر بشر. معتنين بامور حسنة قدام جميع الناس"));
arasuperobject.biblemap.put("b45_12_18", new arasuperobject(45,12,18,"ان كان ممكنا فحسب طاقتكم سالموا جميع الناس"));
arasuperobject.biblemap.put("b45_12_19", new arasuperobject(45,12,19,"لا تنتقموا لانفسكم ايها الاحباء بل اعطوا مكانا للغضب. لانه مكتوب لي النقمة انا اجازي يقول الرب"));
arasuperobject.biblemap.put("b45_12_20", new arasuperobject(45,12,20,"فان جاع عدوك فاطعمه. وان عطش فاسقه. لانك ان فعلت هذا تجمع جمر نار على راسه"));
arasuperobject.biblemap.put("b45_12_21", new arasuperobject(45,12,21,"لا يغلبنك الشر بل اغلب الشر بالخير"));
arasuperobject.biblemap.put("b45_13_1", new arasuperobject(45,13,1,"لتخضع كل نفس للسلاطين الفائقة. لانه ليس سلطان الا من الله والسلاطين الكائنة هي مرتبة من الله"));
arasuperobject.biblemap.put("b45_13_2", new arasuperobject(45,13,2,"حتى ان من يقاوم السلطان يقاوم ترتيب الله والمقاومون سيأخذون لانفسهم دينونة"));
arasuperobject.biblemap.put("b45_13_3", new arasuperobject(45,13,3,"فان الحكام ليسوا خوفا للاعمال الصالحة بل للشريرة. أفتريد ان لا تخاف السلطان. افعل الصلاح فيكون لك مدح منه"));
arasuperobject.biblemap.put("b45_13_4", new arasuperobject(45,13,4,"لانه خادم الله للصلاح. ولكن ان فعلت الشر فخف. لانه لا يحمل السيف عبثا اذ هو خادم الله منتقم للغضب من الذي يفعل الشر"));
arasuperobject.biblemap.put("b45_13_5", new arasuperobject(45,13,5,"لذلك يلزم ان يخضع له ليس بسبب الغضب فقط بل ايضا بسبب الضمير"));
arasuperobject.biblemap.put("b45_13_6", new arasuperobject(45,13,6,"فانكم لاجل هذا توفون الجزية ايضا. اذ هم خدام الله مواظبون على ذلك بعينه"));
arasuperobject.biblemap.put("b45_13_7", new arasuperobject(45,13,7,"فاعطوا الجميع حقوقهم. الجزية لمن له الجزية. الجباية لمن له الجباية. والخوف لمن له الخوف والاكرام لمن له الاكرام"));
arasuperobject.biblemap.put("b45_13_8", new arasuperobject(45,13,8,"لا تكونوا مديونين لاحد بشيء الا بان يحب بعضكم بعضا. لان من احب غيره فقد اكمل الناموس"));
arasuperobject.biblemap.put("b45_13_9", new arasuperobject(45,13,9,"لانه لا تزن لا تقتل لا تسرق لا تشهد بالزور لا تشته وان كانت وصية اخرى هي مجموعة في هذه الكلمة ان تحب قريبك كنفسك"));
arasuperobject.biblemap.put("b45_13_10", new arasuperobject(45,13,10,"المحبة لا تصنع شرا للقريب. فالمحبة هي تكميل الناموس"));
arasuperobject.biblemap.put("b45_13_11", new arasuperobject(45,13,11,"هذا وانكم عارفون الوقت انها الآن ساعة لنستيقظ من النوم. فان خلاصنا الآن اقرب مما كان حين آمنّا"));
arasuperobject.biblemap.put("b45_13_12", new arasuperobject(45,13,12,"قد تناهى الليل وتقارب النهار فلنخلع اعمال الظلمة ونلبس اسلحة النور"));
arasuperobject.biblemap.put("b45_13_13", new arasuperobject(45,13,13,"لنسلك بلياقة كما في النهار لا بالبطر والسكر لا بالمضاجع والعهر لا بالخصام والحسد"));
arasuperobject.biblemap.put("b45_13_14", new arasuperobject(45,13,14,"بل البسوا الرب يسوع المسيح ولا تصنعوا تدبيرا للجسد لاجل الشهوات"));
arasuperobject.biblemap.put("b45_14_1", new arasuperobject(45,14,1,"ومن هو ضعيف في الايمان فاقبلوه لا لمحاكمة الافكار"));
arasuperobject.biblemap.put("b45_14_2", new arasuperobject(45,14,2,"واحد يؤمن ان يأكل كل شيء واما الضعيف فيأكل بقولا"));
arasuperobject.biblemap.put("b45_14_3", new arasuperobject(45,14,3,"لا يزدر من يأكل بمن لا يأكل. ولا يدن من لا يأكل من يأكل. لان الله قبله"));
arasuperobject.biblemap.put("b45_14_4", new arasuperobject(45,14,4,"من انت الذي تدين عبد غيرك. هو لمولاه يثبت او يسقط. ولكنه سيثبت لان الله قادر ان يثبته"));
arasuperobject.biblemap.put("b45_14_5", new arasuperobject(45,14,5,"واحد يعتبر يوما دون يوم وآخر يعتبر كل يوم. فليتيقن كل واحد في عقله"));
arasuperobject.biblemap.put("b45_14_6", new arasuperobject(45,14,6,"الذي يهتم باليوم فللرب يهتم. والذي لا يهتم باليوم فللرب لا يهتم. والذي يأكل فللرب يأكل لانه يشكر الله. والذي لا يأكل فللرب لا يأكل ويشكر الله"));
arasuperobject.biblemap.put("b45_14_7", new arasuperobject(45,14,7,"لان ليس احد منا يعيش لذاته ولا احد يموت لذاته"));
arasuperobject.biblemap.put("b45_14_8", new arasuperobject(45,14,8,"لاننا ان عشنا فللرب نعيش وان متنا فللرب نموت. فان عشنا وان متنا فللرب نحن"));
arasuperobject.biblemap.put("b45_14_9", new arasuperobject(45,14,9,"لانه لهذا مات المسيح وقام وعاش لكي يسود على الاحياء والاموات"));
arasuperobject.biblemap.put("b45_14_10", new arasuperobject(45,14,10,"واما انت فلماذا تدين اخاك. او انت ايضا لماذا تزدري باخيك. لاننا جميعا سوف نقف امام كرسي المسيح"));
arasuperobject.biblemap.put("b45_14_11", new arasuperobject(45,14,11,"لانه مكتوب انا حيّ يقول الرب انه لي ستجثو كل ركبة وكل لسان سيحمد الله"));
arasuperobject.biblemap.put("b45_14_12", new arasuperobject(45,14,12,"فاذا كل واحد منا سيعطي عن نفسه حسابا لله"));
arasuperobject.biblemap.put("b45_14_13", new arasuperobject(45,14,13,"فلا نحاكم ايضا بعضنا بعضا بل بالحري احكموا بهذا ان لا يوضع للاخ مصدمة او معثرة"));
arasuperobject.biblemap.put("b45_14_14", new arasuperobject(45,14,14,"اني عالم ومتيقن في الرب يسوع ان ليس شيء نجسا بذاته الا من يحسب شيئا نجسا فله هو نجس"));
arasuperobject.biblemap.put("b45_14_15", new arasuperobject(45,14,15,"فان كان اخوك بسبب طعامك يحزن فلست تسلك بعد حسب المحبة. لا تهلك بطعامك ذلك الذي مات المسيح لاجله"));
arasuperobject.biblemap.put("b45_14_16", new arasuperobject(45,14,16,"فلا يفتر على صلاحكم"));
arasuperobject.biblemap.put("b45_14_17", new arasuperobject(45,14,17,"لان ليس ملكوت الله اكلا وشربا. بل هو بر وسلام وفرح في الروح القدس"));
arasuperobject.biblemap.put("b45_14_18", new arasuperobject(45,14,18,"لان من خدم المسيح في هذه فهو مرضي عند الله ومزكى عند الناس"));
arasuperobject.biblemap.put("b45_14_19", new arasuperobject(45,14,19,"فلنعكف اذا على ما هو للسلام وما هو للبنيان بعضنا لبعض"));
arasuperobject.biblemap.put("b45_14_20", new arasuperobject(45,14,20,"لا تنقض لاجل الطعام عمل الله. كل الاشياء طاهرة لكنه شر للانسان الذي يأكل بعثرة"));
arasuperobject.biblemap.put("b45_14_21", new arasuperobject(45,14,21,"حسن ان لا تأكل لحما ولا تشرب خمرا ولا شيئا يصطدم به اخوك او يعثر او يضعف"));
arasuperobject.biblemap.put("b45_14_22", new arasuperobject(45,14,22,"ألك ايمان. فليكن لك بنفسك امام الله. طوبى لمن لا يدين نفسه في ما يستحسنه"));
arasuperobject.biblemap.put("b45_14_23", new arasuperobject(45,14,23,"واما الذي يرتاب فان اكل يدان لان ذلك ليس من الايمان. وكل ما ليس من الايمان فهو خطية"));
arasuperobject.biblemap.put("b45_15_1", new arasuperobject(45,15,1,"فيجب علينا نحن الاقوياء ان نحتمل اضعاف الضعفاء ولا نرضي انفسنا"));
arasuperobject.biblemap.put("b45_15_2", new arasuperobject(45,15,2,"فليرض كل واحد منا قريبه للخير لاجل البنيان"));
arasuperobject.biblemap.put("b45_15_3", new arasuperobject(45,15,3,"لان المسيح ايضا لم يرض نفسه بل كما هو مكتوب تعييرات معيّريك وقعت عليّ"));
arasuperobject.biblemap.put("b45_15_4", new arasuperobject(45,15,4,"لان كل ما سبق فكتب كتب لاجل تعليمنا حتى بالصبر والتعزية بما في الكتب يكون لنا رجاء"));
arasuperobject.biblemap.put("b45_15_5", new arasuperobject(45,15,5,"وليعطكم اله الصبر والتعزية ان تهتموا اهتماما واحدا فيما بينكم بحسب المسيح يسوع"));
arasuperobject.biblemap.put("b45_15_6", new arasuperobject(45,15,6,"لكي تمجدوا الله ابا ربنا يسوع المسيح بنفس واحدة وفم واحد"));
arasuperobject.biblemap.put("b45_15_7", new arasuperobject(45,15,7,"لذلك اقبلوا بعضكم بعضا كما ان المسيح ايضا قبلنا لمجد الله"));
arasuperobject.biblemap.put("b45_15_8", new arasuperobject(45,15,8,"واقول ان يسوع المسيح قد صار خادم الختان من اجل صدق الله حتى يثبت مواعيد الآباء"));
arasuperobject.biblemap.put("b45_15_9", new arasuperobject(45,15,9,"واما الامم فمجّدوا الله من اجل الرحمة كما هو مكتوب من اجل ذلك ساحمدك في الامم وارتل لاسمك"));
arasuperobject.biblemap.put("b45_15_10", new arasuperobject(45,15,10,"ويقول ايضا تهللوا ايها الامم مع شعبه"));
arasuperobject.biblemap.put("b45_15_11", new arasuperobject(45,15,11,"وايضا سبحوا الرب يا جميع الامم وامدحوه يا جميع الشعوب"));
arasuperobject.biblemap.put("b45_15_12", new arasuperobject(45,15,12,"وايضا يقول اشعياء سيكون اصل يسّى والقائم ليسود على الامم عليه سيكون رجاء الامم"));
arasuperobject.biblemap.put("b45_15_13", new arasuperobject(45,15,13,"وليملأكم اله الرجاء كل سرور وسلام في الايمان لتزدادوا في الرجاء بقوة الروح القدس"));
arasuperobject.biblemap.put("b45_15_14", new arasuperobject(45,15,14,"وانا نفسي ايضا متيقن من جهتكم يا اخوتي انكم انتم مشحونون صلاحا ومملوؤون كل علم. قادرون ان ينذر بعضكم بعضا"));
arasuperobject.biblemap.put("b45_15_15", new arasuperobject(45,15,15,"ولكن باكثر جسارة كتبت اليكم جزئيا ايها الاخوة كمذكر لكم بسبب النعمة التي وهبت لي من الله"));
