arasuperobject.biblemap.put("b5_3_8", new arasuperobject(5,3,8,"واخذنا في ذلك الوقت من يد ملكي الاموريين الارض التي في عبر الاردن من وادي ارنون الى جبل حرمون"));
arasuperobject.biblemap.put("b5_3_9", new arasuperobject(5,3,9,"والصيدونيون يدعون حرمون سريون والاموريون يدعونه سنير"));
arasuperobject.biblemap.put("b5_3_10", new arasuperobject(5,3,10,"كل مدن السهل وكل جلعاد وكل باشان الى سلخة واذرعي مدينتي مملكة عوج في باشان"));
arasuperobject.biblemap.put("b5_3_11", new arasuperobject(5,3,11,"ان عوج ملك باشان وحده بقي من بقية الرفائيين. هوذا سريره سرير من حديد. أليس هو في ربّة بني عمون. طوله تسع اذرع وعرضه اربع اذرع بذراع رجل"));
arasuperobject.biblemap.put("b5_3_12", new arasuperobject(5,3,12,"فهذه الارض امتلكناها في ذلك الوقت من عروعير التي على وادي ارنون ونصف جبل جلعاد ومدنه اعطيت للرأوبينيين والجاديين"));
arasuperobject.biblemap.put("b5_3_13", new arasuperobject(5,3,13,"وبقية جلعاد وكل باشان مملكة عوج اعطيت لنصف سبط منسّى. كل كورة ارجوب مع كل باشان وهي تدعى ارض الرفائيين"));
arasuperobject.biblemap.put("b5_3_14", new arasuperobject(5,3,14,"يائير ابن منسّى اخذ كل كورة ارجوب الى تخم الجشوريين والمعكيين ودعاها على اسمه باشان حوّوث يائير الى هذا اليوم"));
arasuperobject.biblemap.put("b5_3_15", new arasuperobject(5,3,15,"ولماكير اعطيت جلعاد"));
arasuperobject.biblemap.put("b5_3_16", new arasuperobject(5,3,16,"وللرأوبينيين والجاديين اعطيت من جلعاد الى وادي ارنون وسط الوادي تخما. والى وادي يبوق تخم بني عمون"));
arasuperobject.biblemap.put("b5_3_17", new arasuperobject(5,3,17,"والعربة والاردن تخما من كنّارة الى بحر العربة بحر الملح تحت سفوح الفسجة نحو الشرق"));
arasuperobject.biblemap.put("b5_3_18", new arasuperobject(5,3,18,"وأمرتكم في ذلك الوقت قائلا الرب الهكم قد اعطاكم هذه الارض لتمتلكوها. متجردين تعبرون امام اخوتكم بني اسرائيل كل ذوي بأس"));
arasuperobject.biblemap.put("b5_3_19", new arasuperobject(5,3,19,"اما نساؤكم واطفالكم ومواشيكم. قد عرفت ان لكم مواشي كثيرة. فتمكث في مدنكم التي اعطيتكم"));
arasuperobject.biblemap.put("b5_3_20", new arasuperobject(5,3,20,"حتى يريح الرب اخوتكم مثلكم ويمتلكوا هم ايضا الارض التي الرب الهكم يعطيهم في عبر الاردن. ثم ترجعون كل واحد الى ملكه الذي اعطيتكم"));
arasuperobject.biblemap.put("b5_3_21", new arasuperobject(5,3,21,"وأمرت يشوع في ذلك الوقت قائلا. عيناك قد ابصرتا كل ما فعل الرب الهكم بهذين الملكين. هكذا يفعل الرب بجميع الممالك التي انت عابر اليها"));
arasuperobject.biblemap.put("b5_3_22", new arasuperobject(5,3,22,"لا تخافوا منهم لان الرب الهكم هو المحارب عنكم"));
arasuperobject.biblemap.put("b5_3_23", new arasuperobject(5,3,23,"وتضرعت الى الرب في ذلك الوقت قائلا"));
arasuperobject.biblemap.put("b5_3_24", new arasuperobject(5,3,24,"يا سيد الرب انت قد ابتدأت تري عبدك عظمتك ويدك الشديدة. فانه اي اله في السماء وعلى الارض يعمل كاعمالك وكجبروتك"));
arasuperobject.biblemap.put("b5_3_25", new arasuperobject(5,3,25,"دعني اعبر وأرى الارض الجيدة التي في عبر الاردن هذا الجبل الجيد ولبنان"));
arasuperobject.biblemap.put("b5_3_26", new arasuperobject(5,3,26,"لكن الرب غضب عليّ بسببكم ولم يسمع لي بل قال لي الرب كفاك. لا تعد تكلمني ايضا في هذا الامر"));
arasuperobject.biblemap.put("b5_3_27", new arasuperobject(5,3,27,"اصعد الى راس الفسجة وارفع عينيك الى الغرب والشمال والجنوب والشرق وانظر بعينيك لكن لا تعبر هذا الاردن"));
arasuperobject.biblemap.put("b5_3_28", new arasuperobject(5,3,28,"واما يشوع فاوصه وشدده وشجعه لانه هو يعبر امام هذا الشعب وهو يقسم لهم الارض التي تراها"));
arasuperobject.biblemap.put("b5_3_29", new arasuperobject(5,3,29,"فمكثنا في الجواء مقابل بيت فغور"));
arasuperobject.biblemap.put("b5_4_1", new arasuperobject(5,4,1,"فالآن يا اسرائيل اسمع الفرائض والاحكام التي انا اعلمكم لتعملوها لكي تحيوا وتدخلوا وتمتلكوا الارض التي الرب اله آبائكم يعطيكم"));
arasuperobject.biblemap.put("b5_4_2", new arasuperobject(5,4,2,"لا تزيدوا على الكلام الذي انا اوصيكم به ولا تنقصوا منه لكي تحفظوا وصايا الرب الهكم التي انا اوصيكم بها"));
arasuperobject.biblemap.put("b5_4_3", new arasuperobject(5,4,3,"اعينكم قد ابصرت ما فعله الرب ببعل فغور. ان كل من ذهب وراء بعل فغور اباده الرب الهكم من وسطكم"));
arasuperobject.biblemap.put("b5_4_4", new arasuperobject(5,4,4,"واما انتم الملتصقون بالرب الهكم فجميعكم احياء اليوم"));
arasuperobject.biblemap.put("b5_4_5", new arasuperobject(5,4,5,"انظر. قد علمتكم فرائض واحكاما كما امرني الرب الهي لكي تعملوا هكذا في الارض التي انتم داخلون اليها لكي تمتلكوها"));
arasuperobject.biblemap.put("b5_4_6", new arasuperobject(5,4,6,"فاحفظوا واعملوا. لان ذلك حكمتكم وفطنتكم امام اعين الشعوب الذين يسمعون كل هذه الفرائض فيقولون هذا الشعب العظيم انما هو شعب حكيم وفطن"));
arasuperobject.biblemap.put("b5_4_7", new arasuperobject(5,4,7,"لانه اي شعب هو عظيم له آلهة قريبة منه كالرب الهنا في كل ادعيتنا اليه"));
arasuperobject.biblemap.put("b5_4_8", new arasuperobject(5,4,8,"واي شعب هو عظيم له فرائض واحكام عادلة مثل كل هذه الشريعة التي انا واضع امامكم اليوم"));
arasuperobject.biblemap.put("b5_4_9", new arasuperobject(5,4,9,"انما احترز واحفظ نفسك جدا لئلا تنسى الامور التي ابصرت عيناك ولئلا تزول من قلبك كل ايام حياتك وعلمها اولادك واولاد اولادك"));
arasuperobject.biblemap.put("b5_4_10", new arasuperobject(5,4,10,"في اليوم الذي وقفت فيه امام الرب الهك في حوريب حين قال لي الرب اجمع لي الشعب فاسمعهم كلامي لكي يتعلموا ان يخافوني كل الايام التي هم فيها احياء على الارض ويعلّموا اولادهم"));
arasuperobject.biblemap.put("b5_4_11", new arasuperobject(5,4,11,"فتقدمتم ووقفتم في اسفل الجبل والجبل يضطرم بالنار الى كبد السماء بظلام وسحاب وضباب"));
arasuperobject.biblemap.put("b5_4_12", new arasuperobject(5,4,12,"فكلمكم الرب من وسط النار وانتم سامعون صوت كلام ولكن لم تروا صورة بل صوتا"));
arasuperobject.biblemap.put("b5_4_13", new arasuperobject(5,4,13,"واخبركم بعهده الذي امركم ان تعملوا به الكلمات العشر وكتبه على لوحي حجر"));
arasuperobject.biblemap.put("b5_4_14", new arasuperobject(5,4,14,"واياي امر الرب في ذلك الوقت ان اعلمكم فرائض واحكاما لكي تعملوها في الارض التي انتم عابرون اليها لتمتلكوها"));
arasuperobject.biblemap.put("b5_4_15", new arasuperobject(5,4,15,"فاحتفظوا جدا لانفسكم. فانكم لم تروا صورة ما يوم كلمكم الرب في حوريب من وسط النار"));
arasuperobject.biblemap.put("b5_4_16", new arasuperobject(5,4,16,"لئلا تفسدوا وتعملوا لانفسكم تمثالا منحوتا صورة مثال ما شبه ذكر او انثى"));
arasuperobject.biblemap.put("b5_4_17", new arasuperobject(5,4,17,"شبه بهيمة ما مما على الارض شبه طير ما ذي جناح مما يطير في السماء"));
arasuperobject.biblemap.put("b5_4_18", new arasuperobject(5,4,18,"شبه دبيب ما على الارض شبه سمك ما مما في الماء من تحت الارض"));
arasuperobject.biblemap.put("b5_4_19", new arasuperobject(5,4,19,"ولئلا ترفع عينيك الى السماء وتنظر الشمس والقمر والنجوم كل جند السماء التي قسمها الرب الهك لجميع الشعوب التي تحت كل السماء فتغترّ وتسجد لها وتعبدها"));
arasuperobject.biblemap.put("b5_4_20", new arasuperobject(5,4,20,"وانتم قد اخذكم الرب واخرجكم من كور الحديد من مصر لكي تكونوا له شعب ميراث كما في هذا اليوم"));
arasuperobject.biblemap.put("b5_4_21", new arasuperobject(5,4,21,"وغضب الرب عليّ بسببكم واقسم اني لا اعبر الاردن ولا ادخل الارض الجيدة التي الرب الهك يعطيك نصيبا"));
arasuperobject.biblemap.put("b5_4_22", new arasuperobject(5,4,22,"فاموت انا في هذه الارض. لا اعبر الاردن. واما انتم فتعبرون وتمتلكون تلك الارض الجيدة"));
arasuperobject.biblemap.put("b5_4_23", new arasuperobject(5,4,23,"احترزوا من ان تنسوا عهد الرب الهكم الذي قطعه معكم وتصنعوا لانفسكم تمثالا منحوتا صورة كل ما نهاك عنه الرب الهك"));
arasuperobject.biblemap.put("b5_4_24", new arasuperobject(5,4,24,"لان الرب الهك هو نار آكلة اله غيور"));
arasuperobject.biblemap.put("b5_4_25", new arasuperobject(5,4,25,"اذا ولدتم اولادا واولاد اولاد واطلتم الزمان في الارض وفسدتم وصنعتم تمثالا منحوتا صورة شيء ما وفعلتم الشر في عيني الرب الهكم لاغاظته"));
arasuperobject.biblemap.put("b5_4_26", new arasuperobject(5,4,26,"اشهد عليكم اليوم السماء والارض انكم تبيدون سريعا عن الارض التي انتم عابرون الاردن اليها لتمتلكوها. لا تطيلون الايام عليها بل تهلكون لا محالة"));
arasuperobject.biblemap.put("b5_4_27", new arasuperobject(5,4,27,"ويبددكم الرب في الشعوب فتبقون عددا قليلا بين الامم التي يسوقكم الرب اليها"));
arasuperobject.biblemap.put("b5_4_28", new arasuperobject(5,4,28,"وتصنعون هناك آلهة صنعة ايدي الناس من خشب وحجر مما لا يبصر ولا يسمع ولا ياكل ولا يشمّ"));
arasuperobject.biblemap.put("b5_4_29", new arasuperobject(5,4,29,"ثم ان طلبت من هناك الرب الهك تجده اذا التمسته بكل قلبك وبكل نفسك"));
arasuperobject.biblemap.put("b5_4_30", new arasuperobject(5,4,30,"عندما ضيق عليك واصابتك كل هذه الامور في آخر الايام ترجع الى الرب الهك وتسمع لقوله"));
arasuperobject.biblemap.put("b5_4_31", new arasuperobject(5,4,31,"لان الرب الهك اله رحيم لا يتركك ولا يهلكك ولا ينسى عهد آبائك الذي اقسم لهم عليه"));
arasuperobject.biblemap.put("b5_4_32", new arasuperobject(5,4,32,"فاسأل عن الايام الاولى التي كانت قبلك من اليوم الذي خلق الله فيه الانسان على الارض ومن اقصاء السماء الى اقصائها هل جرى مثل هذا الامر العظيم او هل سمع نظيره"));
arasuperobject.biblemap.put("b5_4_33", new arasuperobject(5,4,33,"هل سمع شعب صوت الله يتكلم من وسط النار كما سمعت انت وعاش"));
arasuperobject.biblemap.put("b5_4_34", new arasuperobject(5,4,34,"او هل شرع الله ان ياتي وياخذ لنفسه شعبا من وسط شعب بتجارب وآيات وعجائب وحرب ويد شديدة وذراع رفيعة ومخاوف عظيمة مثل كل ما فعل لكم الرب الهكم في مصر امام اعينكم"));
arasuperobject.biblemap.put("b5_4_35", new arasuperobject(5,4,35,"انك قد أريت لتعلم ان الرب هو الاله. ليس آخر سواه"));
arasuperobject.biblemap.put("b5_4_36", new arasuperobject(5,4,36,"من السماء اسمعك صوته لينذرك. وعلى الارض اراك ناره العظيمة وسمعت كلامه من وسط النار"));
arasuperobject.biblemap.put("b5_4_37", new arasuperobject(5,4,37,"ولاجل انه احب آباءك واختار نسلهم من بعدهم اخرجك بحضرته بقوته العظيمة من مصر"));
arasuperobject.biblemap.put("b5_4_38", new arasuperobject(5,4,38,"لكي يطرد من امامك شعوبا اكبر واعظم منك ويأتي بك ويعطيك ارضهم نصيبا كما في هذا اليوم"));
arasuperobject.biblemap.put("b5_4_39", new arasuperobject(5,4,39,"فاعلم اليوم وردد في قلبك ان الرب هو الاله في السماء من فوق وعلى الارض من اسفل. ليس سواه"));
arasuperobject.biblemap.put("b5_4_40", new arasuperobject(5,4,40,"واحفظ فرائضه ووصاياه التي انا اوصيك بها اليوم لكي يحسن اليك والى اولادك من بعدك ولكي تطيل ايامك على الارض التي الرب الهك يعطيك الى الابد"));
arasuperobject.biblemap.put("b5_4_41", new arasuperobject(5,4,41,"حينئذ افرز موسى ثلاث مدن في عبر الاردن نحو شروق الشمس"));
arasuperobject.biblemap.put("b5_4_42", new arasuperobject(5,4,42,"لكي يهرب اليها القاتل الذي يقتل صاحبه بغير علم وهو غير مبغض له منذ امس وما قبله. يهرب الى احدى تلك المدن فيحيا"));
arasuperobject.biblemap.put("b5_4_43", new arasuperobject(5,4,43,"باصر في البرية في ارض السهل للرأوبينيين وراموت في جلعاد للجاديين وجولان في باشان للمنسّيّين"));
arasuperobject.biblemap.put("b5_4_44", new arasuperobject(5,4,44,"وهذه هي الشريعة التي وضعها موسى امام بني اسرائيل"));
arasuperobject.biblemap.put("b5_4_45", new arasuperobject(5,4,45,"هذه هي الشهادات والفرائض والاحكام التي كلم بها موسى بني اسرائيل عند خروجهم من مصر"));
arasuperobject.biblemap.put("b5_4_46", new arasuperobject(5,4,46,"في عبر الاردن في الجواء مقابل بيت فغور في ارض سيحون ملك الاموريين الذي كان ساكنا في حشبون الذي ضربه موسى وبنو اسرائيل عند خروجهم من مصر"));
arasuperobject.biblemap.put("b5_4_47", new arasuperobject(5,4,47,"وامتلكوا ارضه وارض عوج ملك باشان ملكي الاموريين اللذين في عبر الاردن نحو شروق الشمس"));
arasuperobject.biblemap.put("b5_4_48", new arasuperobject(5,4,48,"من عروعير التي على حافة وادي ارنون الى جبل سيئون الذي هو حرمون"));
arasuperobject.biblemap.put("b5_4_49", new arasuperobject(5,4,49,"وكل العربة في عبر الاردن نحو الشروق الى بحر العربة تحت سفوح الفسجة"));
arasuperobject.biblemap.put("b5_5_1", new arasuperobject(5,5,1,"ودعا موسى جميع اسرائيل وقال لهم. اسمع يا اسرائيل الفرائض والاحكام التي اتكلم بها في مسامعكم اليوم وتعلّموها واحترزوا لتعملوها"));
arasuperobject.biblemap.put("b5_5_2", new arasuperobject(5,5,2,"الرب الهنا قطع معنا عهدا في حوريب"));
arasuperobject.biblemap.put("b5_5_3", new arasuperobject(5,5,3,"ليس مع آبائنا قطع الرب هذا العهد بل معنا نحن الذين هنا اليوم جميعا احياء"));
arasuperobject.biblemap.put("b5_5_4", new arasuperobject(5,5,4,"وجها لوجه تكلم الرب معنا في الجبل من وسط النار"));
arasuperobject.biblemap.put("b5_5_5", new arasuperobject(5,5,5,"انا كنت واقف بين الرب وبينكم في ذلك الوقت لكي اخبركم بكلام الرب. لانكم خفتم من اجل النار ولم تصعدوا الى الجبل. فقال"));
arasuperobject.biblemap.put("b5_5_6", new arasuperobject(5,5,6,"انا هو الرب الهك الذي اخرجك من ارض مصر من بيت العبودية"));
arasuperobject.biblemap.put("b5_5_7", new arasuperobject(5,5,7,"لا يكن لك آلهة اخرى امامي"));
arasuperobject.biblemap.put("b5_5_8", new arasuperobject(5,5,8,"لا تصنع لك تمثالا منحوتا صورة ما مما في السماء من فوق وما في الارض من اسفل وما في الماء من تحت الارض"));
arasuperobject.biblemap.put("b5_5_9", new arasuperobject(5,5,9,"لا تسجد لهنّ ولا تعبدهنّ لاني انا الرب الهك اله غيور. افتقد ذنوب الآباء في الابناء وفي الجيل الثالث والرابع من الذين يبغضونني"));
arasuperobject.biblemap.put("b5_5_10", new arasuperobject(5,5,10,"واصنع احسانا الى الوف من محبيّ وحافظي وصاياي"));
arasuperobject.biblemap.put("b5_5_11", new arasuperobject(5,5,11,"لا تنطق باسم الرب الهك باطلا. لان الرب لا يبرئ من نطق باسمه باطلا"));
arasuperobject.biblemap.put("b5_5_12", new arasuperobject(5,5,12,"احفظ يوم السبت لتقدسه كما اوصاك الرب الهك"));
arasuperobject.biblemap.put("b5_5_13", new arasuperobject(5,5,13,"ستة ايام تشتغل وتعمل جميع اعمالك"));
arasuperobject.biblemap.put("b5_5_14", new arasuperobject(5,5,14,"واما اليوم السابع فسبت للرب الهك لا تعمل فيه عملا ما انت وابنك وابنتك وعبدك وامتك وثورك وحمارك وكل بهائمك ونزيلك الذي في ابوابك لكي يستريح عبدك وامتك مثلك"));
arasuperobject.biblemap.put("b5_5_15", new arasuperobject(5,5,15,"واذكر انك كنت عبدا في ارض مصر فاخرجك الرب الهك من هناك بيد شديدة وذراع ممدودة. لاجل ذلك اوصاك الرب الهك ان تحفظ يوم السبت"));
arasuperobject.biblemap.put("b5_5_16", new arasuperobject(5,5,16,"اكرم اباك وامك كما اوصاك الرب الهك لكي تطول ايامك ولكي يكون لك خير على الارض التي يعطيك الرب الهك"));
arasuperobject.biblemap.put("b5_5_17", new arasuperobject(5,5,17,"لا تقتل"));
arasuperobject.biblemap.put("b5_5_18", new arasuperobject(5,5,18,"ولا تزن"));
arasuperobject.biblemap.put("b5_5_19", new arasuperobject(5,5,19,"ولا تسرق"));
arasuperobject.biblemap.put("b5_5_20", new arasuperobject(5,5,20,"ولا تشهد على قريبك شهادة زور"));
arasuperobject.biblemap.put("b5_5_21", new arasuperobject(5,5,21,"ولا تشته امرأة قريبك ولا تشته بيت قريبك ولا حقله ولا عبده ولا امته ولا ثوره ولا حماره ولا كل ما لقريبك"));
arasuperobject.biblemap.put("b5_5_22", new arasuperobject(5,5,22,"هذه الكلمات كلم بها الرب كل جماعتكم في الجبل من وسط النار والسحاب والضباب وصوت عظيم ولم يزد. وكتبها على لوحين من حجر واعطاني اياها"));
arasuperobject.biblemap.put("b5_5_23", new arasuperobject(5,5,23,"فلما سمعتم الصوت من وسط الظلام والجبل يشتعل بالنار تقدمتم اليّ جميع رؤساء اسباطكم وشيوخكم"));
arasuperobject.biblemap.put("b5_5_24", new arasuperobject(5,5,24,"وقلتم هوذا الرب الهنا قد ارانا مجده وعظمته وسمعنا صوته من وسط النار. هذا اليوم قد رأينا ان الله يكلم الانسان ويحيا"));
arasuperobject.biblemap.put("b5_5_25", new arasuperobject(5,5,25,"واما الآن فلماذا نموت. لان هذه النار العظيمة تاكلنا. ان عدنا نسمع صوت الرب الهنا ايضا نموت"));
arasuperobject.biblemap.put("b5_5_26", new arasuperobject(5,5,26,"لانه من هو من جميع البشر الذي سمع صوت الله الحي يتكلم من وسط النار مثلنا وعاش"));
arasuperobject.biblemap.put("b5_5_27", new arasuperobject(5,5,27,"تقدم انت واسمع كل ما يقول لك الرب الهنا وكلمنا بكل ما يكلمك به الرب الهنا فنسمع ونعمل"));
arasuperobject.biblemap.put("b5_5_28", new arasuperobject(5,5,28,"فسمع الرب صوت كلامكم حين كلمتموني وقال لي الرب سمعت صوت كلام هؤلاء الشعب الذي كلموك به. قد احسنوا في كل ما تكلموا"));
arasuperobject.biblemap.put("b5_5_29", new arasuperobject(5,5,29,"يا ليت قلبهم كان هكذا فيهم حتى يتّقوني ويحفظوا جميع وصاياي كل الايام لكي يكون لهم ولاولادهم خير الى الابد"));
arasuperobject.biblemap.put("b5_5_30", new arasuperobject(5,5,30,"اذهب قل لهم. ارجعوا الى خيامكم"));
arasuperobject.biblemap.put("b5_5_31", new arasuperobject(5,5,31,"واما انت فقف هنا معي فاكلمك بجميع الوصايا والفرائض والاحكام التي تعلّمهم فيعملونها في الارض التي انا اعطيهم ليمتلكوها"));
arasuperobject.biblemap.put("b5_5_32", new arasuperobject(5,5,32,"فاحترزوا لتعملوا كما امركم الرب الهكم. لا تزيغوا يمينا ولا يسارا"));
arasuperobject.biblemap.put("b5_5_33", new arasuperobject(5,5,33,"في جميع الطريق التي اوصاكم بها الرب الهكم تسلكون لكي تحيوا ويكون لكم خير وتطيلوا الايام في الارض التي تمتلكونها"));
arasuperobject.biblemap.put("b5_6_1", new arasuperobject(5,6,1,"وهذه هي الوصايا والفرائض والاحكام التي امر الرب الهكم ان اعلمكم لتعملوها في الارض التي انتم عابرون اليها لتمتلكوها"));
arasuperobject.biblemap.put("b5_6_2", new arasuperobject(5,6,2,"لكي تتقي الرب الهك وتحفظ جميع فرائضه ووصاياه التي انا اوصيك بها انت وابنك وابن ابنك كل ايام حياتك ولكي تطول ايامك"));
arasuperobject.biblemap.put("b5_6_3", new arasuperobject(5,6,3,"فاسمع يا اسرائيل واحترز لتعمل لكي يكون لك خير وتكثر جدا كما كلمك الرب اله آبائك في ارض تفيض لبنا وعسلا"));
arasuperobject.biblemap.put("b5_6_4", new arasuperobject(5,6,4,"اسمع يا اسرائيل. الرب الهنا رب واحد"));
arasuperobject.biblemap.put("b5_6_5", new arasuperobject(5,6,5,"فتحب الرب الهك من كل قلبك ومن كل نفسك ومن كل قوتك"));
arasuperobject.biblemap.put("b5_6_6", new arasuperobject(5,6,6,"ولتكن هذه الكلمات التي انا اوصيك بها اليوم على قلبك"));
arasuperobject.biblemap.put("b5_6_7", new arasuperobject(5,6,7,"وقصّها على اولادك وتكلم بها حين تجلس في بيتك وحين تمشي في الطريق وحين تنام وحين تقوم"));
arasuperobject.biblemap.put("b5_6_8", new arasuperobject(5,6,8,"واربطها علامة على يدك ولتكن عصائب بين عينيك"));
arasuperobject.biblemap.put("b5_6_9", new arasuperobject(5,6,9,"واكتبها على قوائم ابواب بيتك وعلى ابوابك"));
arasuperobject.biblemap.put("b5_6_10", new arasuperobject(5,6,10,"ومتى أتى بك الرب الهك الى الارض التي حلف لآبائك ابراهيم واسحق ويعقوب ان يعطيك. الى مدن عظيمة جيدة لم تبنها"));
arasuperobject.biblemap.put("b5_6_11", new arasuperobject(5,6,11,"وبيوت مملوءة كل خير لم تملأها وآبار محفورة لم تحفرها وكروم وزيتون لم تغرسها واكلت وشبعت"));
arasuperobject.biblemap.put("b5_6_12", new arasuperobject(5,6,12,"فاحترز لئلا تنسى الرب الذي اخرجك من ارض مصر من بيت العبودية"));
arasuperobject.biblemap.put("b5_6_13", new arasuperobject(5,6,13,"الرب الهك تتقي واياه تعبد وباسمه تحلف"));
arasuperobject.biblemap.put("b5_6_14", new arasuperobject(5,6,14,"لا تسيروا وراء آلهة اخرى من آلهة الامم التي حولكم"));
arasuperobject.biblemap.put("b5_6_15", new arasuperobject(5,6,15,"لان الرب الهكم اله غيور في وسطكم لئلا يحمى غضب الرب الهكم عليكم فيبيدكم عن وجه الارض"));
arasuperobject.biblemap.put("b5_6_16", new arasuperobject(5,6,16,"لا تجربوا الرب الهكم كما جربتموه في مسّة"));
arasuperobject.biblemap.put("b5_6_17", new arasuperobject(5,6,17,"احفظوا وصايا الرب الهكم وشهاداته وفرائضه التي اوصاكم بها"));
arasuperobject.biblemap.put("b5_6_18", new arasuperobject(5,6,18,"واعمل الصالح والحسن في عيني الرب لكي يكون لك خير وتدخل وتمتلك الارض الجيدة التي حلف الرب لآبائك"));
arasuperobject.biblemap.put("b5_6_19", new arasuperobject(5,6,19,"ان ينفي جميع اعدائك من امامك. كما تكلم الرب"));
arasuperobject.biblemap.put("b5_6_20", new arasuperobject(5,6,20,"اذا سألك ابنك غدا قائلا. ما هي الشهادات والفرائض والاحكام التي اوصاكم بها الرب الهنا"));
arasuperobject.biblemap.put("b5_6_21", new arasuperobject(5,6,21,"تقول لابنك. كنا عبيدا لفرعون في مصر فاخرجنا الرب من مصر بيد شديدة"));
arasuperobject.biblemap.put("b5_6_22", new arasuperobject(5,6,22,"وصنع الرب آيات وعجائب عظيمة ورديئة بمصر بفرعون وجميع بيته امام اعيننا"));
arasuperobject.biblemap.put("b5_6_23", new arasuperobject(5,6,23,"واخرجنا من هناك لكي ياتي بنا ويعطينا الارض التي حلف لآبائنا"));
arasuperobject.biblemap.put("b5_6_24", new arasuperobject(5,6,24,"فامرنا الرب ان نعمل جميع هذه الفرائض ونتقي الرب الهنا ليكون لنا خير كل الايام ويستبقينا كما في هذا اليوم"));
arasuperobject.biblemap.put("b5_6_25", new arasuperobject(5,6,25,"وانه يكون لنا بر اذا حفظنا جميع هذه الوصايا لنعملها امام الرب الهنا كما اوصانا"));
arasuperobject.biblemap.put("b5_7_1", new arasuperobject(5,7,1,"متى أتى بك الرب الهك الى الارض التي انت داخل اليها لتمتلكها وطرد شعوبا كثيرة من امامك الحثيين والجرجاشيين والاموريين والكنعانيين والفرزّيين والحوّيين واليبوسيين سبع شعوب اكثر واعظم منك"));
arasuperobject.biblemap.put("b5_7_2", new arasuperobject(5,7,2,"ودفعهم الرب الهك امامك وضربتهم فانك تحرّمهم. لا تقطع لهم عهدا ولا تشفق عليهم"));
arasuperobject.biblemap.put("b5_7_3", new arasuperobject(5,7,3,"ولا تصاهرهم. بنتك لا تعطي لابنه وبنته لا تأخذ لابنك"));
arasuperobject.biblemap.put("b5_7_4", new arasuperobject(5,7,4,"لانه يرد ابنك من ورائي فيعبد آلهة اخرى فيحمى غضب الرب عليكم ويهلككم سريعا"));
arasuperobject.biblemap.put("b5_7_5", new arasuperobject(5,7,5,"ولكن هكذا تفعلون بهم تهدمون مذابحهم وتكسّرون انصابهم وتقطعون سواريهم وتحرقون تماثيلهم بالنار"));
arasuperobject.biblemap.put("b5_7_6", new arasuperobject(5,7,6,"لانك انت شعب مقدس للرب الهك. اياك قد اختار الرب الهك لتكون له شعبا اخصّ من جميع الشعوب الذين على وجه الارض"));
arasuperobject.biblemap.put("b5_7_7", new arasuperobject(5,7,7,"ليس من كونكم اكثر من سائر الشعوب التصق الرب بكم واختاركم لانكم اقل من سائر الشعوب"));
arasuperobject.biblemap.put("b5_7_8", new arasuperobject(5,7,8,"بل من محبة الرب اياكم وحفظه القسم الذي اقسم لآبائكم اخرجكم الرب بيد شديدة وفداكم من بيت العبودية من يد فرعون ملك مصر"));
arasuperobject.biblemap.put("b5_7_9", new arasuperobject(5,7,9,"فاعلم ان الرب الهك هو الله الاله الامين الحافظ العهد والاحسان للذين يحبونه ويحفظون وصاياه الى الف جيل"));
arasuperobject.biblemap.put("b5_7_10", new arasuperobject(5,7,10,"والمجازي الذين يبغضونه بوجوههم ليهلكهم. لا يمهل من يبغضه. بوجهه يجازيه"));
arasuperobject.biblemap.put("b5_7_11", new arasuperobject(5,7,11,"فاحفظ الوصايا والفرائض والاحكام التي انا اوصيك اليوم لتعملها"));
arasuperobject.biblemap.put("b5_7_12", new arasuperobject(5,7,12,"ومن اجل انكم تسمعون هذه الاحكام وتحفظون وتعملونها يحفظ لك الرب الهك العهد والاحسان اللذين اقسم لآبائك"));
arasuperobject.biblemap.put("b5_7_13", new arasuperobject(5,7,13,"ويحبك ويباركك ويكثرك ويبارك ثمرة بطنك وثمرة ارضك قمحك وخمرك وزيتك ونتاج بقرك واناث غنمك على الارض التي اقسم لآبائك انه يعطيك اياها"));
arasuperobject.biblemap.put("b5_7_14", new arasuperobject(5,7,14,"مباركا تكون فوق جميع الشعوب. لا يكون عقيم ولا عاقر فيك ولا في بهائمك"));
arasuperobject.biblemap.put("b5_7_15", new arasuperobject(5,7,15,"ويرد الرب عنك كل مرض وكل ادواء مصر الرديئة التي عرفتها لا يضعها عليك بل يجعلها على كل مبغضيك"));
arasuperobject.biblemap.put("b5_7_16", new arasuperobject(5,7,16,"وتاكل كل الشعوب الذين الرب الهك يدفع اليك. لا تشفق عيناك عليهم ولا تعبد آلهتهم لان ذلك شرك لك"));
arasuperobject.biblemap.put("b5_7_17", new arasuperobject(5,7,17,"ان قلت في قلبك هؤلاء الشعوب اكثر مني كيف اقدر ان اطردهم"));
arasuperobject.biblemap.put("b5_7_18", new arasuperobject(5,7,18,"فلا تخف منهم. اذكر ما فعله الرب الهك بفرعون وبجميع المصريين"));
arasuperobject.biblemap.put("b5_7_19", new arasuperobject(5,7,19,"التجارب العظيمة التي ابصرتها عيناك والآيات والعجائب واليد الشديدة والذراع الرفيعة التي بها اخرجك الرب الهك. هكذا يفعل الرب الهك بجميع الشعوب التي انت خائف من وجهها"));
arasuperobject.biblemap.put("b5_7_20", new arasuperobject(5,7,20,"والزنابير ايضا يرسلها الرب الهك عليهم حتى يفنى الباقون والمختفون من امامك"));
arasuperobject.biblemap.put("b5_7_21", new arasuperobject(5,7,21,"لا ترهب وجوههم لان الرب الهك في وسطك اله عظيم ومخوف"));
arasuperobject.biblemap.put("b5_7_22", new arasuperobject(5,7,22,"ولكن الرب الهك يطرد هؤلاء الشعوب من امامك قليلا قليلا. لا تستطيع ان تفنيهم سريعا لئلا تكثر عليك وحوش البرية"));
arasuperobject.biblemap.put("b5_7_23", new arasuperobject(5,7,23,"ويدفعهم الرب الهك امامك ويوقع بهم اضطرابا عظيما حتى يفنوا"));
arasuperobject.biblemap.put("b5_7_24", new arasuperobject(5,7,24,"ويدفع ملوكهم الى يدك فتمحو اسمهم من تحت السماء. لا يقف انسان في وجهك حتى تفنيهم"));
arasuperobject.biblemap.put("b5_7_25", new arasuperobject(5,7,25,"وتماثيل آلهتهم تحرقون بالنار. لا تشته فضة ولا ذهبا مما عليها لتاخذ لك لئلا تصاد به لانه رجس عند الرب الهك"));
arasuperobject.biblemap.put("b5_7_26", new arasuperobject(5,7,26,"ولا تدخل رجسا الى بيتك لئلا تكون محرّما مثله. تستقبحه وتكرهه لانه محرّم"));
arasuperobject.biblemap.put("b5_8_1", new arasuperobject(5,8,1,"جميع الوصايا التي انا اوصيكم بها اليوم تحفظون لتعملوها لكي تحيوا وتكثروا وتدخلوا وتمتلكوا الارض التي اقسم الرب لآبائكم"));
arasuperobject.biblemap.put("b5_8_2", new arasuperobject(5,8,2,"وتتذكر كل الطريق التي فيها سار بك الرب الهك هذه الاربعين سنة في القفر لكي يذلّك ويجربك ليعرف ما في قلبك أتحفظ وصاياه ام لا"));
arasuperobject.biblemap.put("b5_8_3", new arasuperobject(5,8,3,"فاذلّك واجاعك واطعمك المنّ الذي لم تكن تعرفه ولا عرفه آبائك لكي يعلّمك انه ليس بالخبز وحده يحيا الانسان بل بكل ما يخرج من فم الرب يحيا الانسان"));
arasuperobject.biblemap.put("b5_8_4", new arasuperobject(5,8,4,"ثيابك لم تبلى عليك ورجلك لم تتورّم هذه الاربعين سنة"));
arasuperobject.biblemap.put("b5_8_5", new arasuperobject(5,8,5,"فاعلم في قلبك انه كما يؤدب الانسان ابنه قد ادبك الرب الهك"));
arasuperobject.biblemap.put("b5_8_6", new arasuperobject(5,8,6,"واحفظ وصايا الرب الهك لتسلك في طرقه وتتّقيه"));
arasuperobject.biblemap.put("b5_8_7", new arasuperobject(5,8,7,"لان الرب الهك آت بك الى ارض جيدة ارض انهار من عيون وغمار تنبع في البقاع والجبال"));
arasuperobject.biblemap.put("b5_8_8", new arasuperobject(5,8,8,"ارض حنطة وشعير وكرم وتين ورمان. ارض زيتون زيت وعسل"));
arasuperobject.biblemap.put("b5_8_9", new arasuperobject(5,8,9,"ارض ليس بالمسكنة تاكل فيها خبزا ولا يعوزك فيها شيء. ارض حجارتها حديد ومن جبالها تحفر نحاسا"));
arasuperobject.biblemap.put("b5_8_10", new arasuperobject(5,8,10,"فمتى اكلت وشبعت تبارك الرب الهك لاجل الارض الجيّدة التي اعطاك"));
arasuperobject.biblemap.put("b5_8_11", new arasuperobject(5,8,11,"احترز من ان تنسى الرب الهك ولا تحفظ وصاياه واحكامه وفرائضه التي انا اوصيك بها اليوم"));
arasuperobject.biblemap.put("b5_8_12", new arasuperobject(5,8,12,"لئلا اذا اكلت وشبعت وبنيت بيوتا جيّدة وسكنت"));
arasuperobject.biblemap.put("b5_8_13", new arasuperobject(5,8,13,"وكثرت بقرك وغنمك وكثرت لك الفضة والذهب وكثر كل ما لك"));
arasuperobject.biblemap.put("b5_8_14", new arasuperobject(5,8,14,"يرتفع قلبك وتنسى الرب الهك الذي اخرجك من ارض مصر من بيت العبودية"));
arasuperobject.biblemap.put("b5_8_15", new arasuperobject(5,8,15,"الذي سار بك في القفر العظيم المخوف مكان حيّات محرقة وعقارب وعطش حيث ليس ماء. الذي اخرج لك ماء من صخرة الصوّان"));
arasuperobject.biblemap.put("b5_8_16", new arasuperobject(5,8,16,"الذي اطعمك في البرية المنّ الذي لم يعرفه آباؤك لكي يذلّك ويجربك لكي يحسن اليك في آخرتك"));
arasuperobject.biblemap.put("b5_8_17", new arasuperobject(5,8,17,"ولئلا تقول في قلبك قوتي وقدرة يدي اصطنعت لي هذه الثروة"));
arasuperobject.biblemap.put("b5_8_18", new arasuperobject(5,8,18,"بل اذكر الرب الهك انه هو الذي يعطيك قوة لاصطناع الثروة لكي يفي بعهده الذي اقسم به لآبائك كما في هذا اليوم"));
arasuperobject.biblemap.put("b5_8_19", new arasuperobject(5,8,19,"وان نسيت الرب الهك وذهبت وراء آلهة اخرى وعبدتها وسجدت لها أشهد عليكم اليوم انكم تبيدون لا محالة"));
arasuperobject.biblemap.put("b5_8_20", new arasuperobject(5,8,20,"كالشعوب الذين يبيدهم الرب من امامكم كذلك تبيدون لاجل انكم لم تسمعوا لقول الرب الهكم"));
arasuperobject.biblemap.put("b5_9_1", new arasuperobject(5,9,1,"اسمع يا اسرائيل. انت اليوم عابر الاردن لكي تدخل وتمتلك شعوبا اكبر واعظم منك ومدنا عظيمة ومحصّنة الى السماء"));
arasuperobject.biblemap.put("b5_9_2", new arasuperobject(5,9,2,"قوما عظاما وطوالا بني عناق الذين عرفتهم وسمعت من يقف في وجه بني عناق"));
arasuperobject.biblemap.put("b5_9_3", new arasuperobject(5,9,3,"فاعلم اليوم ان الرب الهك هو العابر امامك نارا آكلة. هو يبيدهم ويذلهم امامك فتطردهم وتهلكهم سريعا كما كلمك الرب"));
arasuperobject.biblemap.put("b5_9_4", new arasuperobject(5,9,4,"لا تقل في قلبك حين ينفيهم الرب الهك من امامك قائلا. لاجل بري ادخلني الرب لامتلك هذه الارض. ولاجل اثم هؤلاء الشعوب يطردهم الرب من امامك"));
arasuperobject.biblemap.put("b5_9_5", new arasuperobject(5,9,5,"ليس لاجل برك وعدالة قلبك تدخل لتمتلك ارضهم بل لاجل اثم اولئك الشعوب يطردهم الرب الهك من امامك ولكي يفي بالكلام الذي اقسم الرب عليه لآبائك ابراهيم واسحق ويعقوب"));
arasuperobject.biblemap.put("b5_9_6", new arasuperobject(5,9,6,"فاعلم انه ليس لاجل برك يعطيك الرب الهك هذه الارض الجيّدة لتمتلكها لانك شعب صلب الرقبة"));
arasuperobject.biblemap.put("b5_9_7", new arasuperobject(5,9,7,"اذكر لا تنسى كيف اسخطت الرب الهك في البرية. من اليوم الذي خرجت فيه من ارض مصر حتى اتيتم الى هذا المكان كنتم تقاومون الرب"));
arasuperobject.biblemap.put("b5_9_8", new arasuperobject(5,9,8,"حتى في حوريب اسخطتم الرب فغضب الرب عليكم ليبيدكم"));
arasuperobject.biblemap.put("b5_9_9", new arasuperobject(5,9,9,"حين صعدت الى الجبل لكي آخذ لوحي الحجر لوحي العهد الذي قطعه الرب معكم اقمت في الجبل اربعين نهارا واربعين ليلة لا آكل خبزا ولا اشرب ماء"));
arasuperobject.biblemap.put("b5_9_10", new arasuperobject(5,9,10,"واعطاني الرب لوحي الحجر المكتوبين باصبع الله وعليهما مثل جميع الكلمات التي كلمكم بها الرب في الجبل من وسط النار في يوم الاجتماع"));
arasuperobject.biblemap.put("b5_9_11", new arasuperobject(5,9,11,"وفي نهاية الاربعين نهارا والاربعين ليلة لما اعطاني الرب لوحي الحجر لوحي العهد"));
arasuperobject.biblemap.put("b5_9_12", new arasuperobject(5,9,12,"قال الرب لي قم انزل عاجلا من هنا لانه قد فسد شعبك الذي اخرجته من مصر. زاغوا سريعا عن الطريق التي اوصيتهم. صنعوا لانفسهم تمثالا مسبوكا"));
arasuperobject.biblemap.put("b5_9_13", new arasuperobject(5,9,13,"وكلمني الرب قائلا. رأيت هذا الشعب واذا هو شعب صلب الرقبة"));
arasuperobject.biblemap.put("b5_9_14", new arasuperobject(5,9,14,"اتركني فابيدهم وامحو اسمهم من تحت السماء واجعلك شعبا اعظم واكثر منهم"));
arasuperobject.biblemap.put("b5_9_15", new arasuperobject(5,9,15,"فانصرفت ونزلت من الجبل والجبل يشتعل بالنار ولوحا العهد في يديّ"));
arasuperobject.biblemap.put("b5_9_16", new arasuperobject(5,9,16,"فنظرت واذا انتم قد اخطأتم الى الرب الهكم وصنعتم لانفسكم عجلا مسبوكا وزغتم سريعا عن الطريق التي اوصاكم بها الرب"));
arasuperobject.biblemap.put("b5_9_17", new arasuperobject(5,9,17,"فاخذت اللوحين وطرحتهما من يديّ وكسّرتهما امام اعينكم"));
arasuperobject.biblemap.put("b5_9_18", new arasuperobject(5,9,18,"ثم سقطت امام الرب كالاول اربعين نهارا واربعين ليلة لا آكل خبزا ولا اشرب ماء من اجل كل خطاياكم التي اخطأتم بها بعملكم الشر امام الرب لاغاظته"));
arasuperobject.biblemap.put("b5_9_19", new arasuperobject(5,9,19,"لاني فزعت من الغضب والغيظ الذي سخطه الرب عليكم ليبيدكم. فسمع لي الرب تلك المرة ايضا"));
arasuperobject.biblemap.put("b5_9_20", new arasuperobject(5,9,20,"وعلى هرون غضب الرب جدا ليبيده. فصلّيت ايضا من اجل هرون في ذلك الوقت"));
arasuperobject.biblemap.put("b5_9_21", new arasuperobject(5,9,21,"واما خطيتكم العجل الذي صنعتموه فأخذته واحرقته بالنار ورضضته وطحنته جيدا حتى نعم كالغبار. ثم طرحت غباره في النهر المنحدر من الجبل"));
arasuperobject.biblemap.put("b5_9_22", new arasuperobject(5,9,22,"وفي تبعيرة ومسّة وقبروت هتّأوة اسخطتم الرب"));
arasuperobject.biblemap.put("b5_9_23", new arasuperobject(5,9,23,"وحين ارسلكم الرب من قادش برنيع قائلا. اصعدوا امتلكوا الارض التي اعطيتكم عصيتم قول الرب الهكم ولم تصدقوه ولم تسمعوا لقوله"));
arasuperobject.biblemap.put("b5_9_24", new arasuperobject(5,9,24,"قد كنتم تعصون الرب منذ يوم عرفتكم"));
arasuperobject.biblemap.put("b5_9_25", new arasuperobject(5,9,25,"فسقطت امام الرب الاربعين نهارا والاربعين ليلة التي سقطتها لان الرب قال انه يهلككم"));
arasuperobject.biblemap.put("b5_9_26", new arasuperobject(5,9,26,"وصليت للرب وقلت يا سيد الرب لا تهلك شعبك وميراثك الذي فديته بعظمتك الذي اخرجته من مصر بيد شديدة"));
arasuperobject.biblemap.put("b5_9_27", new arasuperobject(5,9,27,"اذكر عبيدك ابراهيم واسحق ويعقوب. لا تلتفت الى غلاظة هذا الشعب واثمه وخطيته"));
arasuperobject.biblemap.put("b5_9_28", new arasuperobject(5,9,28,"لئلا تقول الارض التي اخرجتنا منها لاجل ان الرب لم يقدر ان يدخلهم الارض التي كلمهم عنها ولاجل انه ابغضهم اخرجهم لكي يميتهم في البرية"));
arasuperobject.biblemap.put("b5_9_29", new arasuperobject(5,9,29,"وهم شعبك وميراثك الذي اخرجته بقوتك العظيمة وبذراعك الرفيعة"));
arasuperobject.biblemap.put("b5_10_1", new arasuperobject(5,10,1,"في ذلك الوقت قال لي الرب انحت لك لوحين من حجر مثل الاولين واصعد اليّ الى الجبل واصنع لك تابوتا من خشب"));
arasuperobject.biblemap.put("b5_10_2", new arasuperobject(5,10,2,"فاكتب على اللوحين الكلمات التي كانت على اللوحين الاولين اللذين كسرتهما وتضعهما في التابوت"));
arasuperobject.biblemap.put("b5_10_3", new arasuperobject(5,10,3,"فصنعت تابوتا من خشب السنط ونحتّ لوحين من حجر مثل الاولين وصعدت الى الجبل واللوحان في يدي"));
arasuperobject.biblemap.put("b5_10_4", new arasuperobject(5,10,4,"فكتب على اللوحين مثل الكتابة الاولى الكلمات العشر التي كلمكم بها الرب في الجبل من وسط النار في يوم الاجتماع واعطاني الرب اياها"));
arasuperobject.biblemap.put("b5_10_5", new arasuperobject(5,10,5,"ثم انصرفت ونزلت من الجبل ووضعت اللوحين في التابوت الذي صنعت فكانا هناك كما امرني الرب"));
arasuperobject.biblemap.put("b5_10_6", new arasuperobject(5,10,6,"وبنو اسرائيل ارتحلوا من ابآر بني يعقان الى موسير. هناك مات هرون وهناك دفن. فكهن العازار ابنه عوضا عنه"));
arasuperobject.biblemap.put("b5_10_7", new arasuperobject(5,10,7,"من هناك ارتحلوا الى الجدجود ومن الجدجود الى يطبات ارض انهار ماء"));
arasuperobject.biblemap.put("b5_10_8", new arasuperobject(5,10,8,"في ذلك الوقت افرز الرب سبط لاوي ليحملوا تابوت عهد الرب ولكي يقفوا امام الرب ليخدموه ويباركوا باسمه الى هذا اليوم"));
arasuperobject.biblemap.put("b5_10_9", new arasuperobject(5,10,9,"لاجل ذلك لم يكن للاوي قسم ولا نصيب مع اخوته. الرب هو نصيبه كما كلمه الرب الهك"));
arasuperobject.biblemap.put("b5_10_10", new arasuperobject(5,10,10,"وانا مكثت في الجبل كالايام الاولى اربعين نهارا واربعين ليلة وسمع الرب لي تلك المرة ايضا ولم يشإ الرب ان يهلكك"));
arasuperobject.biblemap.put("b5_10_11", new arasuperobject(5,10,11,"ثم قال لي الرب قم اذهب للارتحال امام الشعب فيدخلوا ويمتلكوا الارض التي حلفت لآبائهم ان اعطيهم"));
arasuperobject.biblemap.put("b5_10_12", new arasuperobject(5,10,12,"فالآن يا اسرائيل ماذا يطلب منك الرب الهك الا ان تتقي الرب الهك لتسلك في كل طرقه وتحبه وتعبد الرب الهك من كل قلبك ومن كل نفسك"));
arasuperobject.biblemap.put("b5_10_13", new arasuperobject(5,10,13,"وتحفظ وصايا الرب وفرائضه التي انا اوصيك بها اليوم لخيرك"));
arasuperobject.biblemap.put("b5_10_14", new arasuperobject(5,10,14,"هوذا للرب الهك السموات وسماء السموات والارض وكل ما فيها"));
arasuperobject.biblemap.put("b5_10_15", new arasuperobject(5,10,15,"ولكن الرب انما التصق بآبائك ليحبهم فاختار من بعدهم نسلهم الذي هو انتم فوق جميع الشعوب كما في هذا اليوم"));
arasuperobject.biblemap.put("b5_10_16", new arasuperobject(5,10,16,"فاختنوا غرلة قلوبكم ولا تصلّبوا رقابكم بعد"));
arasuperobject.biblemap.put("b5_10_17", new arasuperobject(5,10,17,"لان الرب الهكم هو اله الآلهة ورب الارباب الاله العظيم الجبار المهيب الذي لا يأخذ بالوجوه ولا يقبل رشوة"));
arasuperobject.biblemap.put("b5_10_18", new arasuperobject(5,10,18,"الصانع حق اليتيم والارملة والمحب الغريب ليعطيه طعاما ولباسا"));
arasuperobject.biblemap.put("b5_10_19", new arasuperobject(5,10,19,"فاحبوا الغريب لانكم كنتم غرباء في ارض مصر"));
arasuperobject.biblemap.put("b5_10_20", new arasuperobject(5,10,20,"الرب الهك تتقي. اياه تعبد وبه تلتصق وباسمه تحلف"));
arasuperobject.biblemap.put("b5_10_21", new arasuperobject(5,10,21,"هو فخرك وهو الهك الذي صنع معك تلك العظائم والمخاوف التي ابصرتها عيناك"));
arasuperobject.biblemap.put("b5_10_22", new arasuperobject(5,10,22,"سبعين نفسا نزل آباؤك الى مصر والآن قد جعلك الرب الهك كنجوم السماء في الكثرة"));
arasuperobject.biblemap.put("b5_11_1", new arasuperobject(5,11,1,"فاحبب الرب الهك واحفظ حقوقه وفرائضه واحكامه ووصاياه كل الايام"));
arasuperobject.biblemap.put("b5_11_2", new arasuperobject(5,11,2,"واعلموا اليوم اني لست اريد بنيكم الذين لم يعرفوا ولا رأوا تأديب الرب الهكم عظمته ويده الشديدة وذراعه الرفيعة"));
arasuperobject.biblemap.put("b5_11_3", new arasuperobject(5,11,3,"وآياته وصنائعه التي عملها في مصر بفرعون ملك مصر وبكل ارضه"));
arasuperobject.biblemap.put("b5_11_4", new arasuperobject(5,11,4,"والتي عملها بجيش مصر بخليهم ومركباتهم حيث اطاف مياه بحر سوف على وجوهم حين سعوا وراءكم فابادهم الرب الى هذا اليوم"));
arasuperobject.biblemap.put("b5_11_5", new arasuperobject(5,11,5,"والتي عملها لكم في البرية حتى جئتم الى هذا المكان"));
arasuperobject.biblemap.put("b5_11_6", new arasuperobject(5,11,6,"والتي عملها بداثان وابيرام ابني اليآب ابن رأوبين اللذين فتحت الارض فاها وابتلعتهما مع بيوتهما وخيامهما وكل الموجودات التابعة لهما في وسط كل اسرائيل"));
arasuperobject.biblemap.put("b5_11_7", new arasuperobject(5,11,7,"لان اعينكم هي التي ابصرت كل صنائع الرب العظيمة التي عملها"));
arasuperobject.biblemap.put("b5_11_8", new arasuperobject(5,11,8,"فاحفظوا كل الوصايا التي انا اوصيكم بها اليوم لكي تتشددوا وتدخلوا وتمتلكوا الارض التي انتم عابرون اليها لتمتلكوها"));
arasuperobject.biblemap.put("b5_11_9", new arasuperobject(5,11,9,"ولكي تطيلوا الايام على الارض التي اقسم الرب لآبائكم ان يعطيها لهم ولنسلهم ارض تفيض لبنا وعسلا"));
arasuperobject.biblemap.put("b5_11_10", new arasuperobject(5,11,10,"لان الارض التي انت داخل اليها لكي تمتلكها ليست مثل ارض مصر التي خرجت منها حيث كنت تزرع زرعك وتسقيه برجلك كبستان بقول"));
arasuperobject.biblemap.put("b5_11_11", new arasuperobject(5,11,11,"بل الارض التي انتم عابرون اليها لكي تمتلكوها هي ارض جبال وبقاع. من مطر السماء تشرب ماء"));
arasuperobject.biblemap.put("b5_11_12", new arasuperobject(5,11,12,"ارض يعتني بها الرب الهك. عينا الرب الهك عليها دائما من اول السنة الى آخرها"));
arasuperobject.biblemap.put("b5_11_13", new arasuperobject(5,11,13,"فاذا سمعتم لوصاياي التي انا اوصيكم بها اليوم لتحبوا الرب الهكم وتعبدوه من كل قلوبكم ومن كل انفسكم"));
arasuperobject.biblemap.put("b5_11_14", new arasuperobject(5,11,14,"اعطي مطر ارضكم في حينه المبكر والمتأخر. فتجمع حنطتك وخمرك وزيتك"));
arasuperobject.biblemap.put("b5_11_15", new arasuperobject(5,11,15,"واعطي لبهائمك عشبا في حقلك فتأكل انت وتشبع"));
arasuperobject.biblemap.put("b5_11_16", new arasuperobject(5,11,16,"فاحترزوا من ان تنغوي قلوبكم فتزيغوا وتعبدوا آلهة اخرى وتسجدوا لها"));
arasuperobject.biblemap.put("b5_11_17", new arasuperobject(5,11,17,"فيحمى غضب الرب عليكم ويغلق السماء فلا يكون مطر ولا تعطي الارض غلتها. فتبيدون سريعا عن الارض الجيّدة التي يعطيكم الرب"));
arasuperobject.biblemap.put("b5_11_18", new arasuperobject(5,11,18,"فضعوا كلماتي هذه على قلوبكم ونفوسكم واربطوها علامة على ايديكم ولتكن عصائب بين عيونكم"));
arasuperobject.biblemap.put("b5_11_19", new arasuperobject(5,11,19,"وعلّموها اولادكم متكلمين بها حين تجلسون في بيوتكم وحين تمشون في الطريق وحين تنامون وحين تقومون"));
arasuperobject.biblemap.put("b5_11_20", new arasuperobject(5,11,20,"واكتبها على قوائم ابواب بيتك وعلى ابوابك"));
arasuperobject.biblemap.put("b5_11_21", new arasuperobject(5,11,21,"لكي تكثر ايامك وايام اولادك على الارض التي اقسم الرب لآبائك ان يعطيهم اياها كايام السماء على الارض"));
arasuperobject.biblemap.put("b5_11_22", new arasuperobject(5,11,22,"لانه اذا حفظتم جميع هذه الوصايا التي انا اوصيكم بها لتعملوها. لتحبوا الرب الهكم وتسلكوا في جميع طرقه وتلتصقوا به"));
arasuperobject.biblemap.put("b5_11_23", new arasuperobject(5,11,23,"يطرد الرب جميع هؤلاء الشعوب من امامكم فترثون شعوبا اكبر واعظم منكم"));
arasuperobject.biblemap.put("b5_11_24", new arasuperobject(5,11,24,"كل مكان تدوسه بطون اقدامكم يكون لكم. من البرية ولبنان. من النهر نهر الفرات الى البحر الغربي يكون تخمكم"));
arasuperobject.biblemap.put("b5_11_25", new arasuperobject(5,11,25,"لا يقف انسان في وجهكم. الرب الهكم يجعل خشيتكم ورعبكم على كل الارض التي تدوسونها كما كلمكم"));
arasuperobject.biblemap.put("b5_11_26", new arasuperobject(5,11,26,"انظر. انا واضع امامكم اليوم بركة ولعنة"));
arasuperobject.biblemap.put("b5_11_27", new arasuperobject(5,11,27,"البركة اذا سمعتم لوصايا الرب الهكم التي انا اوصيكم بها اليوم"));
arasuperobject.biblemap.put("b5_11_28", new arasuperobject(5,11,28,"واللعنة اذا لم تسمعوا لوصايا الرب الهكم وزغتم عن الطريق التي انا اوصيكم بها اليوم لتذهبوا وراء آلهة اخرى لم تعرفوها"));
arasuperobject.biblemap.put("b5_11_29", new arasuperobject(5,11,29,"واذا جاء بك الرب الهك الى الارض التي انت داخل اليها لكي تمتلكها فاجعل البركة على جبل جرزيم واللعنة على جبل عيبال"));
arasuperobject.biblemap.put("b5_11_30", new arasuperobject(5,11,30,"اما هما في عبر الاردن وراء طريق غروب الشمس في ارض الكنعانيين الساكنين في العربة مقابل الجلجال بجانب بلوطات مورة"));
arasuperobject.biblemap.put("b5_11_31", new arasuperobject(5,11,31,"لانكم عابرون الاردن لتدخلوا وتمتلكوا الارض التي الرب الهكم يعطيكم. تمتلكونها وتسكنونها"));
arasuperobject.biblemap.put("b5_11_32", new arasuperobject(5,11,32,"فاحفظوا جميع الفرائض والاحكام التي انا واضع امامكم اليوم لتعملوها"));
arasuperobject.biblemap.put("b5_12_1", new arasuperobject(5,12,1,"هذه هي الفرائض والاحكام التي تحفظون لتعملوها في الارض التي اعطاك الرب اله آبائك لتمتلكها كل الايام التي تحيون على الارض"));
arasuperobject.biblemap.put("b5_12_2", new arasuperobject(5,12,2,"تخربون جميع الاماكن حيث عبدت الامم التي ترثونها آلهتها على الجبال الشامخة وعلى التلال وتحت كل شجرة خضراء"));
arasuperobject.biblemap.put("b5_12_3", new arasuperobject(5,12,3,"وتهدمون مذابحهم وتكسّرون انصابهم وتحرقون سواريهم بالنار وتقطعون تماثيل آلهتهم وتمحون اسمهم من ذلك المكان"));
arasuperobject.biblemap.put("b5_12_4", new arasuperobject(5,12,4,"لا تفعلوا هكذا للرب الهكم"));
arasuperobject.biblemap.put("b5_12_5", new arasuperobject(5,12,5,"بل المكان الذي يختاره الرب الهكم من جميع اسباطكم ليضع اسمه فيه سكناه تطلبون والى هناك تأتون"));
arasuperobject.biblemap.put("b5_12_6", new arasuperobject(5,12,6,"وتقدمون الى هناك محرقاتكم وذبائحكم وعشوركم ورفائع ايديكم ونذوركم ونوافلكم وابكار بقركم وغنمكم"));
arasuperobject.biblemap.put("b5_12_7", new arasuperobject(5,12,7,"وتأكلون هناك امام الرب الهكم وتفرحون بكل ما تمتد اليه ايديكم انتم وبيوتكم كما بارككم الرب الهكم"));
arasuperobject.biblemap.put("b5_12_8", new arasuperobject(5,12,8,"لا تعملوا حسب كل ما نحن عاملون هنا اليوم اي كل انسان مهما صلح في عينيه"));
arasuperobject.biblemap.put("b5_12_9", new arasuperobject(5,12,9,"لانكم لم تدخلوا حتى الآن الى المقرّ والنصيب اللذين يعطيكم الرب الهكم"));
arasuperobject.biblemap.put("b5_12_10", new arasuperobject(5,12,10,"فمتى عبرتم الاردن وسكنتم الارض التي يقسمها لكم الرب الهكم واراحكم من جميع اعدائكم الذين حواليكم وسكنتم آمنين"));
arasuperobject.biblemap.put("b5_12_11", new arasuperobject(5,12,11,"فالمكان الذي يختاره الرب الهكم ليحل اسمه فيه تحملون اليه كل ما انا اوصيكم به محرقاتكم وذبائحكم وعشوركم ورفائع ايديكم وكل خيار نذوركم التي تنذرونها للرب"));
arasuperobject.biblemap.put("b5_12_12", new arasuperobject(5,12,12,"تفرحون امام الرب الهكم انتم وبنوكم وبناتكم وعبيدكم واماؤكم واللاوي الذي في ابوابكم لانه ليس له قسم ولا نصيب معكم"));
arasuperobject.biblemap.put("b5_12_13", new arasuperobject(5,12,13,"احترز من ان تصعد محرقاتك في كل مكان تراه"));
arasuperobject.biblemap.put("b5_12_14", new arasuperobject(5,12,14,"بل في المكان الذي يختاره الرب في احد اسباطك. هناك تصعد محرقاتك وهناك تعمل كل ما انا اوصيك به"));
arasuperobject.biblemap.put("b5_12_15", new arasuperobject(5,12,15,"ولكن من كل ما تشتهي نفسك تذبح وتأكل لحما في جميع ابوابك حسب بركة الرب الهك التي اعطاك. النجس والطاهر ياكلانه كالضبي والايل"));
arasuperobject.biblemap.put("b5_12_16", new arasuperobject(5,12,16,"واما الدم فلا تاكله. على الارض تسفكه كالماء"));
arasuperobject.biblemap.put("b5_12_17", new arasuperobject(5,12,17,"لا يحل لك ان تاكل في ابوابك عشر حنطتك وخمرك وزيتك ولا ابكار بقرك وغنمك ولا شيئا من نذورك التي تنذر ونوافلك ورفائع يدك"));
arasuperobject.biblemap.put("b5_12_18", new arasuperobject(5,12,18,"بل امام الرب الهك تأكلها في المكان الذي يختاره الرب الهك انت وابنك وابنتك وعبدك وامتك واللاوي الذي في ابوابك وتفرح امام الرب الهك بكل ما امتدّت اليه يدك"));
arasuperobject.biblemap.put("b5_12_19", new arasuperobject(5,12,19,"احترز من ان تترك اللاوي كل ايامك على ارضك"));
arasuperobject.biblemap.put("b5_12_20", new arasuperobject(5,12,20,"اذا وسّع الرب الهك تخومك كما كلمك وقلت آكل لحما. لان نفسك تشتهي ان تأكل لحما. فمن كل ما تشتهي نفسك تأكل لحما"));
arasuperobject.biblemap.put("b5_12_21", new arasuperobject(5,12,21,"اذا كان المكان الذي يختاره الرب الهك ليضع اسمه فيه بعيدا عنك فاذبح من بقرك وغنمك التي اعطاك الرب كما اوصيتك وكل في ابوابك من كل ما اشتهت نفسك"));
arasuperobject.biblemap.put("b5_12_22", new arasuperobject(5,12,22,"كما يؤكل الضبي والايل هكذا تاكله. النجس والطاهر ياكلانه سواء"));
arasuperobject.biblemap.put("b5_12_23", new arasuperobject(5,12,23,"لكن احترز ان لا تأكل الدم لان الدم هو النفس فلا تاكل النفس مع اللحم"));
arasuperobject.biblemap.put("b5_12_24", new arasuperobject(5,12,24,"لا تاكله. على الارض تسفكه كالماء"));
arasuperobject.biblemap.put("b5_12_25", new arasuperobject(5,12,25,"لا تاكله لكي يكون لك ولاولادك من بعدك خير اذا عملت الحق في عيني الرب"));
arasuperobject.biblemap.put("b5_12_26", new arasuperobject(5,12,26,"واما اقداسك التي لك ونذورك فتحملها وتذهب الى المكان الذي يختاره الرب"));
arasuperobject.biblemap.put("b5_12_27", new arasuperobject(5,12,27,"فتعمل محرقاتك اللحم والدم على مذبح الرب الهك. واما ذبائحك فيسفك دمها على مذبح الرب الهك واللحم تاكله"));
arasuperobject.biblemap.put("b5_12_28", new arasuperobject(5,12,28,"احفظ واسمع جميع هذه الكلمات التي انا اوصيك بها لكي يكون لك ولاولادك من بعدك خير الى الابد اذا عملت الصالح والحق في عيني الرب الهك"));
arasuperobject.biblemap.put("b5_12_29", new arasuperobject(5,12,29,"متى قرض الرب الهك من امامك الامم الذين انت ذاهب اليهم لترثهم وورثتهم وسكنت ارضهم"));
arasuperobject.biblemap.put("b5_12_30", new arasuperobject(5,12,30,"فاحترز من ان تصاد وراءهم من بعد ما بادوا من امامك ومن ان تسأل عن آلهتهم قائلا كيف عبد هؤلاء الامم آلهتهم فانا ايضا افعل هكذا"));
arasuperobject.biblemap.put("b5_12_31", new arasuperobject(5,12,31,"لا تعمل هكذا للرب الهك لانهم قد عملوا لآلهتهم كل رجس لدى الرب مما يكرهه اذ احرقوا حتى بنيهم وبناتهم بالنار لآلهتهم"));
arasuperobject.biblemap.put("b5_12_32", new arasuperobject(5,12,32,"كل الكلام الذي اوصيكم به احرصوا لتعملوه. لا تزد عليه ولا تنقص منه"));
arasuperobject.biblemap.put("b5_13_1", new arasuperobject(5,13,1,"اذا قام في وسطك نبي او حالم حلما واعطاك آية او اعجوبة"));
arasuperobject.biblemap.put("b5_13_2", new arasuperobject(5,13,2,"ولو حدثت الآية او الاعجوبة التي كلمك عنها قائلا لنذهب وراء آلهة اخرى لم تعرفها ونعبدها"));
arasuperobject.biblemap.put("b5_13_3", new arasuperobject(5,13,3,"فلا تسمع لكلام ذلك النبي او الحالم ذلك الحلم لان الرب الهكم يمتحنكم لكي يعلم هل تحبون الرب الهكم من كل قلوبكم ومن كل انفسكم"));
arasuperobject.biblemap.put("b5_13_4", new arasuperobject(5,13,4,"وراء الرب الهكم تسيرون واياه تتقون ووصاياه تحفظون وصوته تسمعون واياه تعبدون وبه تلتصقون"));
arasuperobject.biblemap.put("b5_13_5", new arasuperobject(5,13,5,"وذلك النبي او الحالم ذلك الحلم يقتل لانه تكلم بالزيغ من وراء الرب الهكم الذي اخرجكم من ارض مصر وفداكم من بيت العبودية لكي يطوّحكم عن الطريق التي امركم الرب الهكم ان تسلكوا فيها. فتنزعون الشر من بينكم"));
arasuperobject.biblemap.put("b5_13_6", new arasuperobject(5,13,6,"واذا اغواك سرا اخوك ابن امك او ابنك او ابنتك او امرأة حضنك او صاحبك الذي مثل نفسك قائلا نذهب ونعبد آلهة اخرى لم تعرفها انت ولا آباؤك"));
arasuperobject.biblemap.put("b5_13_7", new arasuperobject(5,13,7,"من آلهة الشعوب الذين حولك القريبين منك او البعيدين عنك من اقصاء الارض الى اقصائها"));
arasuperobject.biblemap.put("b5_13_8", new arasuperobject(5,13,8,"فلا ترض منه ولا تسمع له ولا تشفق عينك عليه ولا ترقّ له ولا تستره"));
arasuperobject.biblemap.put("b5_13_9", new arasuperobject(5,13,9,"بل قتلا تقتله. يدك تكون عليه اولا لقتله ثم ايدي جميع الشعب اخيرا"));
arasuperobject.biblemap.put("b5_13_10", new arasuperobject(5,13,10,"ترجمه بالحجارة حتى يموت. لانه التمس ان يطوّحك عن الرب الهك الذي اخرجك من ارض مصر من بيت العبودية"));
arasuperobject.biblemap.put("b5_13_11", new arasuperobject(5,13,11,"فيسمع جميع اسرائيل ويخافون ولا يعودون يعملون مثل هذا الامر الشرير في وسطك"));
arasuperobject.biblemap.put("b5_13_12", new arasuperobject(5,13,12,"ان سمعت عن احدى مدنك التي يعطيك الرب الهك لتسكن فيها قولا"));
arasuperobject.biblemap.put("b5_13_13", new arasuperobject(5,13,13,"قد خرج اناس بنو لئيم من وسطك وطوّحوا سكان مدينتهم قائلين نذهب ونعبد آلهة اخرى لم تعرفوها"));
arasuperobject.biblemap.put("b5_13_14", new arasuperobject(5,13,14,"وفحصت وفتشت وسألت جيدا واذا الامر صحيح واكيد قد عمل ذلك الرجس في وسطك"));
arasuperobject.biblemap.put("b5_13_15", new arasuperobject(5,13,15,"فضربا تضرب سكان تلك المدينة بحد السيف وتحرّمها بكل ما فيها مع بهائمها بحد السيف"));
arasuperobject.biblemap.put("b5_13_16", new arasuperobject(5,13,16,"تجمع كل امتعتها الى وسط ساحتها وتحرق بالنار المدينة وكل امتعتها كاملة للرب الهك فتكون تلا الى الابد لا تبنى بعد"));
arasuperobject.biblemap.put("b5_13_17", new arasuperobject(5,13,17,"ولا يلتصق بيدك شيء من المحرّم. لكي يرجع الرب من حمو غضبه ويعطيك رحمة. يرحمك ويكثرك كما حلف لآبائك"));
arasuperobject.biblemap.put("b5_13_18", new arasuperobject(5,13,18,"اذا سمعت لصوت الرب الهك لتحفظ جميع وصاياه التي انا اوصيك بها اليوم لتعمل الحق في عيني الرب الهك"));
arasuperobject.biblemap.put("b5_14_1", new arasuperobject(5,14,1,"انتم اولاد للرب الهكم. لا تخمشوا اجسامكم ولا تجعلوا قرعة بين اعينكم لاجل ميت"));
arasuperobject.biblemap.put("b5_14_2", new arasuperobject(5,14,2,"لانك شعب مقدس للرب الهك وقد اختارك الرب لكي تكون له شعبا خاصّا فوق جميع الشعوب الذين على وجه الارض"));
arasuperobject.biblemap.put("b5_14_3", new arasuperobject(5,14,3,"لا تأكل رجسا ما"));
arasuperobject.biblemap.put("b5_14_4", new arasuperobject(5,14,4,"هذه هي البهائم التي تأكلونها. البقر والضأن والمعز"));
arasuperobject.biblemap.put("b5_14_5", new arasuperobject(5,14,5,"والايل والظبي واليحمور والوعل والرئم والثيتل والمهاة"));
arasuperobject.biblemap.put("b5_14_6", new arasuperobject(5,14,6,"وكل بهيمة من البهائم تشق ظلفا وتقسمه ظلفين وتجترّ فاياها تاكلون"));
arasuperobject.biblemap.put("b5_14_7", new arasuperobject(5,14,7,"الا هذه فلا تأكلوها مما يجترّ ومما يشق الظلف المنقسم. الجمل والارنب والوبر لانها تجترّ لكنها لا تشق ظلفا فهي نجسة لكم"));
arasuperobject.biblemap.put("b5_14_8", new arasuperobject(5,14,8,"والخنزير لانه يشق الظلف لكنه لا يجترّ فهو نجس لكم. فمن لحمها لا تأكلوا وجثثها لا تلمسوا"));
arasuperobject.biblemap.put("b5_14_9", new arasuperobject(5,14,9,"وهذا تأكلونه من كل ما في المياه. كل ما له زعانف وحرشف تأكلونه"));
arasuperobject.biblemap.put("b5_14_10", new arasuperobject(5,14,10,"لكن كل ما ليس له زعانف وحرشف لا تأكلوه. انه نجس لكم"));
arasuperobject.biblemap.put("b5_14_11", new arasuperobject(5,14,11,"كل طير طاهر تأكلون"));
arasuperobject.biblemap.put("b5_14_12", new arasuperobject(5,14,12,"وهذا ما لا تأكلون منه. النسر والانوق والعقاب"));
arasuperobject.biblemap.put("b5_14_13", new arasuperobject(5,14,13,"والحدأة والباشق والشاهين على اجناسه"));
arasuperobject.biblemap.put("b5_14_14", new arasuperobject(5,14,14,"وكل غراب على اجناسه"));
arasuperobject.biblemap.put("b5_14_15", new arasuperobject(5,14,15,"والنعامة والظليم والسأف والباز على اجناسه"));
arasuperobject.biblemap.put("b5_14_16", new arasuperobject(5,14,16,"والبوم والكركي والبجع"));
arasuperobject.biblemap.put("b5_14_17", new arasuperobject(5,14,17,"والقوق والرّخم والغوّاص"));
arasuperobject.biblemap.put("b5_14_18", new arasuperobject(5,14,18,"واللقلق والببغا على اجناسه والهدهد والخفّاش"));
arasuperobject.biblemap.put("b5_14_19", new arasuperobject(5,14,19,"وكل دبيب الطير نجس لكم. لا يؤكل"));
arasuperobject.biblemap.put("b5_14_20", new arasuperobject(5,14,20,"كل طير طاهر تأكلون"));
arasuperobject.biblemap.put("b5_14_21", new arasuperobject(5,14,21,"لا تأكلوا جثة ما. تعطيها للغريب الذي في ابوابك فيأكلها او يبيعها لاجنبيّ لانك شعب مقدس للرب الهك. لا تطبخ جديا بلبن امه"));
arasuperobject.biblemap.put("b5_14_22", new arasuperobject(5,14,22,"تعشيرا تعشر كل محصول زرعك الذي يخرج من الحقل سنة بسنة"));
arasuperobject.biblemap.put("b5_14_23", new arasuperobject(5,14,23,"وتأكل امام الرب الهك في المكان الذي يختاره ليحل اسمه فيه عشر حنطتك وخمرك وزيتك وابكار بقرك وغنمك لكي تتعلم ان تتقي الرب الهك كل الايام"));
arasuperobject.biblemap.put("b5_14_24", new arasuperobject(5,14,24,"ولكن اذا طال عليك الطريق حتى لا تقدر ان تحمله. اذا كان بعيدا عليك المكان الذي يختاره الرب الهك ليجعل اسمه فيه اذ يباركك الرب الهك"));
arasuperobject.biblemap.put("b5_14_25", new arasuperobject(5,14,25,"فبعه بفضة وصرّ الفضة في يدك واذهب الى المكان الذي يختاره الرب الهك"));
arasuperobject.biblemap.put("b5_14_26", new arasuperobject(5,14,26,"وانفق الفضة في كل ما تشته نفسك في البقر والغنم والخمر والمسكر وكل ما تطلب منك نفسك وكل هناك امام الرب الهك وافرح انت وبيتك"));
arasuperobject.biblemap.put("b5_14_27", new arasuperobject(5,14,27,"واللاوي الذي في ابوابك لا تتركه لانه ليس له قسم ولا نصيب معك"));
arasuperobject.biblemap.put("b5_14_28", new arasuperobject(5,14,28,"في آخر ثلاث سنين تخرج كل عشر محصولك في تلك السنة وتضعه في ابوابك"));
arasuperobject.biblemap.put("b5_14_29", new arasuperobject(5,14,29,"فيأتي اللاوي لانه ليس له قسم ولا نصيب معك والغريب واليتيم والارملة الذين في ابوابك وياكلون ويشبعون لكي يباركك الرب الهك في كل عمل يدك الذي تعمل"));
arasuperobject.biblemap.put("b5_15_1", new arasuperobject(5,15,1,"في آخر سبع سنين تعمل ابراء"));
arasuperobject.biblemap.put("b5_15_2", new arasuperobject(5,15,2,"وهذا هو حكم الابراء. يبرئ كل صاحب دين يده مما اقرض صاحبه. لا يطالب صاحبه ولا اخاه لانه قد نودي بابراء للرب"));
arasuperobject.biblemap.put("b5_15_3", new arasuperobject(5,15,3,"الاجنبي تطالب واما ما كان لك عند اخيك فتبرئه يدك منه"));
arasuperobject.biblemap.put("b5_15_4", new arasuperobject(5,15,4,"الا ان لم يكن فيك فقير. لان الرب انما يباركك في الارض التي يعطيك الرب الهك نصيبا لتمتلكها"));
arasuperobject.biblemap.put("b5_15_5", new arasuperobject(5,15,5,"اذا سمعت صوت الرب الهك لتحفظ وتعمل كل هذه الوصايا التي انا اوصيك اليوم"));
arasuperobject.biblemap.put("b5_15_6", new arasuperobject(5,15,6,"يباركك الرب الهك كما قال لك. فتقرض امما كثيرة وانت لا تقترض وتتسلط على امم كثيرة وهم عليك لا يتسلطون"));
arasuperobject.biblemap.put("b5_15_7", new arasuperobject(5,15,7,"ان كان فيك فقير احد من اخوتك في احد ابوابك في ارضك التي يعطيك الرب الهك فلا تقسّ قلبك ولا تقبض يدك عن اخيك الفقير"));
arasuperobject.biblemap.put("b5_15_8", new arasuperobject(5,15,8,"بل افتح يدك له واقرضه مقدار ما يحتاج اليه"));
arasuperobject.biblemap.put("b5_15_9", new arasuperobject(5,15,9,"احترز من ان يكون مع قلبك كلام لئيم قائلا قد قربت السنة السابعة سنة الابراء وتسوء عينك باخيك الفقير ولا تعطيه فيصرخ عليك الى الرب فتكون عليك خطية"));
arasuperobject.biblemap.put("b5_15_10", new arasuperobject(5,15,10,"اعطه ولا يسؤ قلبك عندما تعطيه لانه بسبب هذا الامر يباركك الرب الهك في كل اعمالك وجميع ما تمتد اليه يدك"));
arasuperobject.biblemap.put("b5_15_11", new arasuperobject(5,15,11,"لانه لا تفقد الفقراء من الارض. لذلك انا اوصيك قائلا افتح يدك لاخيك المسكين والفقير في ارضك"));
arasuperobject.biblemap.put("b5_15_12", new arasuperobject(5,15,12,"اذا بيع لك اخوك العبراني او اختك العبرانية وخدمك ست سنين ففي السنة السابعة تطلقه حرا من عندك"));
arasuperobject.biblemap.put("b5_15_13", new arasuperobject(5,15,13,"وحين تطلقه حرا من عندك لا تطلقه فارغا"));
arasuperobject.biblemap.put("b5_15_14", new arasuperobject(5,15,14,"تزوده من غنمك ومن بيدرك ومن معصرتك. كما باركك الرب الهك تعطيه"));
arasuperobject.biblemap.put("b5_15_15", new arasuperobject(5,15,15,"واذكر انك كنت عبدا في ارض مصر ففداك الرب الهك. لذلك انا اوصيك بهذا الامر اليوم"));
arasuperobject.biblemap.put("b5_15_16", new arasuperobject(5,15,16,"ولكن اذا قال لك لا اخرج من عندك لانه قد احبك وبيتك اذ كان له خير عندك"));
arasuperobject.biblemap.put("b5_15_17", new arasuperobject(5,15,17,"فخذ المخرز واجعله في اذنه وفي الباب فيكون لك عبدا مؤبدا. وهكذا تفعل لأمتك ايضا"));
arasuperobject.biblemap.put("b5_15_18", new arasuperobject(5,15,18,"لا يصعب عليك ان تطلقه حرا من عندك لانه ضعفي اجرة الاجير خدمك ست سنين. فيباركك الرب الهك في كل ما تعمل"));
arasuperobject.biblemap.put("b5_15_19", new arasuperobject(5,15,19,"كل بكر ذكر يولد من بقرك ومن غنمك تقدسه للرب الهك. لا تشتغل على بكر بقرك ولا تجزّ بكر غنمك"));
arasuperobject.biblemap.put("b5_15_20", new arasuperobject(5,15,20,"امام الرب الهك تأكله سنة بسنة في المكان الذي يختاره الرب انت وبيتك"));
arasuperobject.biblemap.put("b5_15_21", new arasuperobject(5,15,21,"ولكن اذا كان فيه عيب عرج او عمى عيب ما رديء فلا تذبحه للرب الهك"));
arasuperobject.biblemap.put("b5_15_22", new arasuperobject(5,15,22,"في ابوابك تاكله. النجس والطاهر سواء كالضبي والايل"));
arasuperobject.biblemap.put("b5_15_23", new arasuperobject(5,15,23,"واما دمه فلا تأكله. على الارض تسفكه كالماء"));
arasuperobject.biblemap.put("b5_16_1", new arasuperobject(5,16,1,"احفظ شهر ابيب واعمل فصحا للرب الهك لانه في شهر ابيب اخرجك الرب الهك من مصر ليلا"));
arasuperobject.biblemap.put("b5_16_2", new arasuperobject(5,16,2,"فتذبح الفصح للرب الهك غنما وبقرا في المكان الذي يختاره الرب ليحل اسمه فيه"));
arasuperobject.biblemap.put("b5_16_3", new arasuperobject(5,16,3,"لا تأكل عليه خميرا. سبعة ايام تاكل عليه فطيرا خبز المشقّة لانك بعجلة خرجت من ارض مصر. لكي تذكر يوم خروجك من ارض مصر كل ايام حياتك"));
arasuperobject.biblemap.put("b5_16_4", new arasuperobject(5,16,4,"ولا يرى عندك خمير في جميع تخومك سبعة ايام ولا يبت شيء من اللحم الذي تذبح مساء في اليوم الاول الى الغد"));
arasuperobject.biblemap.put("b5_16_5", new arasuperobject(5,16,5,"لا يحل لك ان تذبح الفصح في احد ابوابك التي يعطيك الرب الهك"));
arasuperobject.biblemap.put("b5_16_6", new arasuperobject(5,16,6,"بل في المكان الذي يختاره الرب الهك ليحل اسمه فيه. هناك تذبح الفصح مساء نحو غروب الشمس في ميعاد خروجك من مصر"));
arasuperobject.biblemap.put("b5_16_7", new arasuperobject(5,16,7,"وتطبخ وتأكل في المكان الذي يختاره الرب الهك ثم تنصرف في الغد وتذهب الى خيامك"));
arasuperobject.biblemap.put("b5_16_8", new arasuperobject(5,16,8,"ستة ايام تأكل فطيرا وفي اليوم السابع اعتكاف للرب الهك. لا تعمل فيه عملا"));
arasuperobject.biblemap.put("b5_16_9", new arasuperobject(5,16,9,"سبعة اسابيع تحسب لك من ابتداء المنجل في الزرع تبتدئ ان تحسب سبعة اسابيع"));
arasuperobject.biblemap.put("b5_16_10", new arasuperobject(5,16,10,"وتعمل عيد اسابيع للرب الهك على قدر ما تسمح يدك ان تعطي كما يباركك الرب الهك"));
arasuperobject.biblemap.put("b5_16_11", new arasuperobject(5,16,11,"وتفرح امام الرب الهك انت وابنك وابنتك وعبدك وامتك واللاوي الذي في ابوابك والغريب واليتيم والارملة الذين في وسطك في المكان الذي يختاره الرب الهك ليحل اسمه فيه"));
arasuperobject.biblemap.put("b5_16_12", new arasuperobject(5,16,12,"وتذكر انك كنت عبدا في مصر وتحفظ وتعمل هذه الفرائض"));
arasuperobject.biblemap.put("b5_16_13", new arasuperobject(5,16,13,"تعمل لنفسك عيد المظال سبعة ايام عندما تجمع من بيدرك ومن معصرتك"));
arasuperobject.biblemap.put("b5_16_14", new arasuperobject(5,16,14,"وتفرح في عيدك انت وابنك وابنتك وعبدك وامتك واللاوي والغريب واليتيم والارملة الذين في ابوابك"));
arasuperobject.biblemap.put("b5_16_15", new arasuperobject(5,16,15,"سبعة ايام تعيّد للرب الهك في المكان الذي يختاره الرب لان الرب الهك يبارك في كل محصولك وفي كل عمل يديك فلا تكون الا فرحا"));
arasuperobject.biblemap.put("b5_16_16", new arasuperobject(5,16,16,"ثلاث مرات في السنة يحضر جميع ذكورك امام الرب الهك في المكان الذي يختاره في عيد الفطير وعيد الاسابيع وعيد المظال. ولا يحضروا امام الرب فارغين"));
arasuperobject.biblemap.put("b5_16_17", new arasuperobject(5,16,17,"كل واحد حسبما تعطي يده كبركة الرب الهك التي اعطاك"));
arasuperobject.biblemap.put("b5_16_18", new arasuperobject(5,16,18,"قضاة وعرفاء تجعل لك في جميع ابوابك التي يعطيك الرب الهك حسب اسباطك فيقضون للشعب قضاء عادلا"));
arasuperobject.biblemap.put("b5_16_19", new arasuperobject(5,16,19,"لا تحرّف القضاء ولا تنظر الى الوجوه ولا تأخذ رشوة لان الرشوة تعمي اعين الحكماء وتعوج كلام الصديقين"));
arasuperobject.biblemap.put("b5_16_20", new arasuperobject(5,16,20,"العدل العدل تتبع لكي تحيا وتمتلك الارض التي يعطيك الرب الهك"));
arasuperobject.biblemap.put("b5_16_21", new arasuperobject(5,16,21,"لا تنصب لنفسك سارية من شجرة ما بجانب مذبح الرب الهك الذي تصنعه لك"));
arasuperobject.biblemap.put("b5_16_22", new arasuperobject(5,16,22,"ولا تقم لك نصبا. الشيء الذي يبغضه الرب الهك"));
arasuperobject.biblemap.put("b5_17_1", new arasuperobject(5,17,1,"لا تذبح للرب الهك ثورا او شاة فيه عيب شيء ما رديء. لان ذلك رجس لدى الرب الهك"));
arasuperobject.biblemap.put("b5_17_2", new arasuperobject(5,17,2,"اذا وجد في وسطك في احد ابوابك التي يعطيك الرب الهك رجل او امرأة يفعل شرا في عيني الرب الهك بتجاوز عهده"));
arasuperobject.biblemap.put("b5_17_3", new arasuperobject(5,17,3,"ويذهب ويعبد آلهة اخرى ويسجد لها او للشمس او للقمر او لكل من جند السماء. الشيء الذي لم أوص به"));
arasuperobject.biblemap.put("b5_17_4", new arasuperobject(5,17,4,"وأخبرت وسمعت وفحصت جيدا واذا الأمر صحيح اكيد قد عمل ذلك الرجس في اسرائيل"));
arasuperobject.biblemap.put("b5_17_5", new arasuperobject(5,17,5,"فاخرج ذلك الرجل او تلك المرأة الذي فعل ذلك الامر الشرير الى ابوابك الرجل او المرأة وارجمه بالحجارة حتى يموت"));
arasuperobject.biblemap.put("b5_17_6", new arasuperobject(5,17,6,"على فم شاهدين او ثلاثة شهود يقتل الذي يقتل. لا يقتل على فم شاهد واحد"));
arasuperobject.biblemap.put("b5_17_7", new arasuperobject(5,17,7,"ايدي الشهود تكون عليه اولا لقتله ثم ايدي جميع الشعب اخيرا فتنزع الشر من وسطك"));
arasuperobject.biblemap.put("b5_17_8", new arasuperobject(5,17,8,"اذا عسر عليك امر في القضاء بين دم ودم او بين دعوى ودعوى او بين ضربة وضربة من امور الخصومات في ابوابك فقم واصعد الى المكان الذي يختاره الرب الهك"));
arasuperobject.biblemap.put("b5_17_9", new arasuperobject(5,17,9,"واذهب الى الكهنة اللاويين والى القاضي الذي يكون في تلك الايام واسال فيخبروك بامر القضاء"));
arasuperobject.biblemap.put("b5_17_10", new arasuperobject(5,17,10,"فتعمل حسب الامر الذي يخبرونك به من ذلك المكان الذي يختاره الرب وتحرص ان تعمل حسب كل ما يعلّمونك"));
arasuperobject.biblemap.put("b5_17_11", new arasuperobject(5,17,11,"حسب الشريعة التي يعلّمونك والقضاء الذي يقولونه لك تعمل. لا تحد عن الأمر الذي يخبرونك به يمينا او شمالا"));
arasuperobject.biblemap.put("b5_17_12", new arasuperobject(5,17,12,"والرجل الذي يعمل بطغيان فلا يسمع للكاهن الواقف هناك ليخدم الرب الهك او للقاضي يقتل ذلك الرجل فتنزع الشر من اسرائيل"));
arasuperobject.biblemap.put("b5_17_13", new arasuperobject(5,17,13,"فيسمع جميع الشعب ويخافون ولا يطغون بعد"));
arasuperobject.biblemap.put("b5_17_14", new arasuperobject(5,17,14,"متى أتيت الى الارض التي يعطيك الرب الهك وامتلكتها وسكنت فيها فان قلت اجعل عليّ ملكا كجميع الامم الذين حولي"));
arasuperobject.biblemap.put("b5_17_15", new arasuperobject(5,17,15,"فانك تجعل عليك ملكا الذي يختاره الرب الهك. من وسط اخوتك تجعل عليك ملكا. لا يحل لك ان تجعل عليك رجلا اجنبيا ليس هو اخاك"));
arasuperobject.biblemap.put("b5_17_16", new arasuperobject(5,17,16,"ولكن لا يكثر له الخيل ولا يرد الشعب الى مصر لكي يكثر الخيل والرب قال لكم لا تعودوا ترجعون في هذه الطريق ايضا"));
arasuperobject.biblemap.put("b5_17_17", new arasuperobject(5,17,17,"ولا يكثر له نساء لئلا يزيغ قلبه وفضة وذهبا لا يكثر له كثيرا"));
arasuperobject.biblemap.put("b5_17_18", new arasuperobject(5,17,18,"وعندما يجلس على كرسي مملكته يكتب لنفسه نسخة من هذه الشريعة في كتاب من عند الكهنة اللاويين"));
arasuperobject.biblemap.put("b5_17_19", new arasuperobject(5,17,19,"فتكون معه ويقرأ فيها كل ايام حياته لكي يتعلم ان يتقي الرب الهه ويحفظ جميع كلمات هذه الشريعة وهذه الفرائض ليعمل بها"));
arasuperobject.biblemap.put("b5_17_20", new arasuperobject(5,17,20,"لئلا يرتفع قلبه على اخوته ولئلا يحيد عن الوصية يمينا او شمالا. لكي يطيل الايام على مملكته هو وبنوه في وسط اسرائيل"));
arasuperobject.biblemap.put("b5_18_1", new arasuperobject(5,18,1,"لا يكون للكهنة اللاويين كل سبط لاوي قسم ولا نصيب مع اسرائيل. ياكلون وقائد الرب ونصيبه"));
arasuperobject.biblemap.put("b5_18_2", new arasuperobject(5,18,2,"فلا يكون له نصيب في وسط اخوته. الرب هو نصيبه كما قال له"));
arasuperobject.biblemap.put("b5_18_3", new arasuperobject(5,18,3,"وهذا يكون حق الكهنة من الشعب من الذين يذبحون الذبائح بقرا كانت او غنما. يعطون الكاهن الساعد والفكين والكرش"));
arasuperobject.biblemap.put("b5_18_4", new arasuperobject(5,18,4,"وتعطيه اول حنطتك وخمرك وزيتك واول جزاز غنمك"));
arasuperobject.biblemap.put("b5_18_5", new arasuperobject(5,18,5,"لان الرب الهك قد اختاره من جميع اسباطك لكي يقف ليخدم باسم الرب هو وبنوه كل الايام"));
arasuperobject.biblemap.put("b5_18_6", new arasuperobject(5,18,6,"واذا جاء لاوي من احد ابوابك من جميع اسرائيل حيث هو متغرب وجاء بكل رغبة نفسه الى المكان الذي يختاره الرب"));
arasuperobject.biblemap.put("b5_18_7", new arasuperobject(5,18,7,"وخدم باسم الرب الهك مثل جميع اخوته اللاويين الواقفين هناك امام الرب"));
arasuperobject.biblemap.put("b5_18_8", new arasuperobject(5,18,8,"يأكلون اقساما متساوية عدا ما يبيعه عن آبائه"));
arasuperobject.biblemap.put("b5_18_9", new arasuperobject(5,18,9,"متى دخلت الارض التي يعطيك الرب الهك لا تتعلم ان تفعل مثل رجس اولئك الامم"));
arasuperobject.biblemap.put("b5_18_10", new arasuperobject(5,18,10,"لا يوجد فيك من يجيز ابنه او ابنته في النار ولا من يعرف عرافة ولا عائف ولا متفائل ولا ساحر"));
arasuperobject.biblemap.put("b5_18_11", new arasuperobject(5,18,11,"ولا من يرقي رقية ولا من يسأل جانا او تابعة ولا من يستشير الموتى"));
arasuperobject.biblemap.put("b5_18_12", new arasuperobject(5,18,12,"لان كل من يفعل ذلك مكروه عند الرب. وبسبب هذه الارجاس الرب الهك طاردهم من امامك"));
arasuperobject.biblemap.put("b5_18_13", new arasuperobject(5,18,13,"تكون كاملا لدى الرب الهك"));
arasuperobject.biblemap.put("b5_18_14", new arasuperobject(5,18,14,"ان هؤلاء الامم الذين تخلفهم يسمعون للعائفين والعرّافين. واما انت فلم يسمح لك الرب الهك هكذا"));
arasuperobject.biblemap.put("b5_18_15", new arasuperobject(5,18,15,"يقيم لك الرب الهك نبيا من وسطك من اخوتك مثلي. له تسمعون"));
arasuperobject.biblemap.put("b5_18_16", new arasuperobject(5,18,16,"حسب كل ما طلبت من الرب الهك في حوريب يوم الاجتماع قائلا لا اعود اسمع صوت الرب الهي ولا ارى هذه النار العظيمة ايضا لئلا اموت"));
arasuperobject.biblemap.put("b5_18_17", new arasuperobject(5,18,17,"قال لي الرب قد احسنوا فيما تكلموا"));
arasuperobject.biblemap.put("b5_18_18", new arasuperobject(5,18,18,"اقيم لهم نبيا من وسط اخوتهم مثلك واجعل كلامي في فمه فيكلمهم بكل ما اوصيه به"));
arasuperobject.biblemap.put("b5_18_19", new arasuperobject(5,18,19,"ويكون ان الانسان الذي لا يسمع لكلامي الذي يتكلم به باسمي انا اطالبه"));
arasuperobject.biblemap.put("b5_18_20", new arasuperobject(5,18,20,"واما النبي الذي يطغي فيتكلم باسمي كلاما لم اوصه ان يتكلم به او الذي يتكلم باسم آلهة اخرى فيموت ذلك النبي"));
arasuperobject.biblemap.put("b5_18_21", new arasuperobject(5,18,21,"وان قلت في قلبك كيف نعرف الكلام الذي لم يتكلم به الرب"));
arasuperobject.biblemap.put("b5_18_22", new arasuperobject(5,18,22,"فما تكلم به النبي باسم الرب ولم يحدث ولم يصر فهو الكلام الذي لم يتكلم به الرب بل بطغيان تكلم به النبي فلا تخف منه"));
arasuperobject.biblemap.put("b5_19_1", new arasuperobject(5,19,1,"متى قرض الرب الهك الامم الذين الرب الهك يعطيك ارضهم وورثتهم وسكنت مدنهم وبيوتهم"));
arasuperobject.biblemap.put("b5_19_2", new arasuperobject(5,19,2,"تفرز لنفسك ثلاث مدن في وسط ارضك التي يعطيك الرب الهك لتمتلكها"));
arasuperobject.biblemap.put("b5_19_3", new arasuperobject(5,19,3,"تصلح الطريق وتثلث تخوم ارضك التي يقسم لك الرب الهك فتكون لكي يهرب اليها كل قاتل"));
arasuperobject.biblemap.put("b5_19_4", new arasuperobject(5,19,4,"وهذا هو حكم القاتل الذي يهرب الى هناك فيحيا. من ضرب صاحبه بغير علم وهو غير مبغض له منذ امس وما قبله"));
arasuperobject.biblemap.put("b5_19_5", new arasuperobject(5,19,5,"ومن ذهب مع صاحبه في الوعر ليحتطب حطبا فاندفعت يده بالفاس ليقطع الحطب وافلت الحديد من الخشب واصاب صاحبه فمات فهو يهرب الى احدى تلك المدن فيحيا"));
arasuperobject.biblemap.put("b5_19_6", new arasuperobject(5,19,6,"لئلا يسعى ولي الدم وراء القاتل حين يحمى قلبه ويدركه اذا طال الطريق ويقتله وليس عليه حكم الموت لانه غير مبغض له منذ امس وما قبله"));
arasuperobject.biblemap.put("b5_19_7", new arasuperobject(5,19,7,"لاجل ذلك انا آمرك قائلا ثلاث مدن تفرز لنفسك"));
arasuperobject.biblemap.put("b5_19_8", new arasuperobject(5,19,8,"وان وسّع الرب الهك تخومك كما حلف لآبائك واعطاك جميع الارض التي قال انه يعطي لآبائك"));
arasuperobject.biblemap.put("b5_19_9", new arasuperobject(5,19,9,"اذا حفظت كل هذه الوصايا لتعملها كما انا اوصيك اليوم لتحب الرب الهك وتسلك في طرقه كل الايام فزد لنفسك ايضا ثلاث مدن على هذه الثلاث"));
arasuperobject.biblemap.put("b5_19_10", new arasuperobject(5,19,10,"حتى لا يسفك دم بري في وسط ارضك التي يعطيك الرب الهك نصيبا فيكون عليك دم"));
arasuperobject.biblemap.put("b5_19_11", new arasuperobject(5,19,11,"ولكن اذا كان انسان مبغضا لصاحبه فكمن له وقام عليه وضربه ضربة قاتلة فمات ثم هرب الى احدى تلك المدن"));
arasuperobject.biblemap.put("b5_19_12", new arasuperobject(5,19,12,"يرسل شيوخ مدينته وياخذونه من هناك ويدفعونه الى يد ولي الدم فيموت"));
arasuperobject.biblemap.put("b5_19_13", new arasuperobject(5,19,13,"لا تشفق عينك عليه. فتنزع دم البري من اسرائيل فيكون لك خير"));
arasuperobject.biblemap.put("b5_19_14", new arasuperobject(5,19,14,"لا تنقل تخم صاحبك الذي نصبه الاولون في نصيبك الذي تناله في الارض التي يعطيك الرب الهك لكي تمتلكها"));
arasuperobject.biblemap.put("b5_19_15", new arasuperobject(5,19,15,"لا يقوم شاهد واحد على انسان في ذنب ما او خطية ما من جميع الخطايا التي يخطئ بها. على فم شاهدين او على فم ثلاثة شهود يقوم الامر"));
arasuperobject.biblemap.put("b5_19_16", new arasuperobject(5,19,16,"اذا قام شاهد زور على انسان ليشهد عليه بزيغ"));
arasuperobject.biblemap.put("b5_19_17", new arasuperobject(5,19,17,"يقف الرجلان اللذان بينهما الخصومة امام الرب امام الكهنة والقضاة الذين يكونون في تلك الايام"));
arasuperobject.biblemap.put("b5_19_18", new arasuperobject(5,19,18,"فان فحص القضاة جيدا واذا الشاهد شاهد كاذب قد شهد بالكذب على اخيه"));
arasuperobject.biblemap.put("b5_19_19", new arasuperobject(5,19,19,"فافعلوا به كما نوى ان يفعل باخيه. فتنزعون الشر من وسطكم"));
arasuperobject.biblemap.put("b5_19_20", new arasuperobject(5,19,20,"ويسمع الباقون فيخافون ولا يعودون يفعلون مثل ذلك الامر الخبيث في وسطك"));
arasuperobject.biblemap.put("b5_19_21", new arasuperobject(5,19,21,"لا تشفق عينك. نفس بنفس. عين بعين. سن بسن. يد بيد. رجل برجل"));
arasuperobject.biblemap.put("b5_20_1", new arasuperobject(5,20,1,"اذا خرجت للحرب على عدوك ورأيت خيلا ومراكب قوما اكثر منك فلا تخف منهم لان معك الرب الهك الذي اصعدك من ارض مصر"));
arasuperobject.biblemap.put("b5_20_2", new arasuperobject(5,20,2,"وعندما تقربون من الحرب يتقدم الكاهن ويخاطب الشعب"));
arasuperobject.biblemap.put("b5_20_3", new arasuperobject(5,20,3,"ويقول لهم اسمع يا اسرائيل. انتم قربتم اليوم من الحرب على اعدائكم. لا تضعف قلوبكم. لا تخافوا ولا ترتعدوا ولا ترهبوا وجوههم"));
arasuperobject.biblemap.put("b5_20_4", new arasuperobject(5,20,4,"لان الرب الهكم سائر معكم لكي يحارب عنكم اعداءكم ليخلصكم"));
arasuperobject.biblemap.put("b5_20_5", new arasuperobject(5,20,5,"ثم يخاطب العرفاء الشعب قائلين من هو الرجل الذي بنى بيتا جديدا ولم يدشنه. ليذهب ويرجع الى بيته لئلا يموت في الحرب فيدشنه رجل آخر"));
arasuperobject.biblemap.put("b5_20_6", new arasuperobject(5,20,6,"ومن هو الرجل الذي غرس كرما ولم يبتكره. ليذهب ويرجع الى بيته لئلا يموت في الحرب فيبتكره رجل آخر"));
arasuperobject.biblemap.put("b5_20_7", new arasuperobject(5,20,7,"ومن هو الرجل الذي خطب امرأة ولم يأخذها. ليذهب ويرجع الى بيته لئلا يموت في الحرب فياخذها رجل آخر"));
arasuperobject.biblemap.put("b5_20_8", new arasuperobject(5,20,8,"ثم يعود العرفاء يخاطبون الشعب ويقولون من هو الرجل الخائف والضعيف القلب. ليذهب ويرجع الى بيته لئلا تذوب قلوب اخوته مثل قلبه"));
arasuperobject.biblemap.put("b5_20_9", new arasuperobject(5,20,9,"وعند فراغ العرفاء من مخاطبة الشعب يقيمون رؤساء جنود على راس الشعب"));
arasuperobject.biblemap.put("b5_20_10", new arasuperobject(5,20,10,"حين تقرب من مدينة لكي تحاربها استدعها الى الصلح"));
arasuperobject.biblemap.put("b5_20_11", new arasuperobject(5,20,11,"فان اجابتك الى الصلح وفتحت لك فكل الشعب الموجود فيها يكون لك للتسخير ويستعبد لك"));
arasuperobject.biblemap.put("b5_20_12", new arasuperobject(5,20,12,"وان لم تسالمك بل عملت معك حربا فحاصرها"));
arasuperobject.biblemap.put("b5_20_13", new arasuperobject(5,20,13,"واذا دفعها الرب الهك الى يدك فاضرب جميع ذكورها بحد السيف"));
arasuperobject.biblemap.put("b5_20_14", new arasuperobject(5,20,14,"واما النساء والاطفال والبهائم وكل ما في المدينة كل غنيمتها فتغتنمها لنفسك وتأكل غنيمة اعدائك التي اعطاك الرب الهك"));
arasuperobject.biblemap.put("b5_20_15", new arasuperobject(5,20,15,"هكذا تفعل بجميع المدن البعيدة منك جدا التي ليست من مدن هؤلاء الامم هنا"));
arasuperobject.biblemap.put("b5_20_16", new arasuperobject(5,20,16,"واما مدن هؤلاء الشعوب التي يعطيك الرب الهك نصيبا فلا تستبق منها نسمة ما"));
arasuperobject.biblemap.put("b5_20_17", new arasuperobject(5,20,17,"بل تحرّمها تحريما الحثيين والاموريين والكنعانيين والفرزّيين والحوّيين واليبوسيين كما امرك الرب الهك"));
arasuperobject.biblemap.put("b5_20_18", new arasuperobject(5,20,18,"لكي لا يعلّموكم ان تعملوا حسب جميع ارجاسهم التي عملوا لآلهتهم فتخطئوا الى الرب الهكم"));
arasuperobject.biblemap.put("b5_20_19", new arasuperobject(5,20,19,"اذا حاصرت مدينة اياما كثيرة محاربا اياها لكي تأخذها فلا تتلف شجرها بوضع فاس عليه. انك منه تأكل. فلا تقطعه. لانه هل شجرة الحقل انسان حتى يذهب قدامك في الحصار"));
arasuperobject.biblemap.put("b5_20_20", new arasuperobject(5,20,20,"واما الشجر الذي تعرف انه ليس شجرا يؤكل منه فاياه تتلف وتقطع وتبني حصنا على المدينة التي تعمل معك حربا حتى تسقط"));
arasuperobject.biblemap.put("b5_21_1", new arasuperobject(5,21,1,"اذا وجد قتيل في الارض التي يعطيك الرب الهك لتمتلكها واقعا في الحقل لا يعلم من قتله"));
arasuperobject.biblemap.put("b5_21_2", new arasuperobject(5,21,2,"يخرج شيوخك وقضاتك ويقيسون الى المدن التي حول القتيل"));
arasuperobject.biblemap.put("b5_21_3", new arasuperobject(5,21,3,"فالمدينة القربى من القتيل ياخذ شيوخ تلك المدينة عجلة من البقر لم يحرث عليها لم تجرّ بالنير"));
arasuperobject.biblemap.put("b5_21_4", new arasuperobject(5,21,4,"وينحدر شيوخ تلك المدينة بالعجلة الى واد دائم السيلان لم يحرث فيه ولم يزرع ويكسرون عنق العجلة في الوادي"));
arasuperobject.biblemap.put("b5_21_5", new arasuperobject(5,21,5,"ثم يتقدم الكهنة بنو لاوي. لانه اياهم اختار الرب الهك ليخدموه ويباركوا باسم الرب وحسب قولهم تكون كل خصومة وكل ضربة"));
arasuperobject.biblemap.put("b5_21_6", new arasuperobject(5,21,6,"ويغسل جميع شيوخ تلك المدينة القريبين من القتيل ايديهم على العجلة المكسورة العنق في الوادي"));
arasuperobject.biblemap.put("b5_21_7", new arasuperobject(5,21,7,"ويصرّحون ويقولون ايدينا لم تسفك هذا الدم واعيننا لم تبصر"));
arasuperobject.biblemap.put("b5_21_8", new arasuperobject(5,21,8,"اغفر لشعبك اسرائيل الذي فديت يا رب ولا تجعل دم بري في وسط شعبك اسرائيل. فيغفر لهم الدم"));
arasuperobject.biblemap.put("b5_21_9", new arasuperobject(5,21,9,"فتنزع الدم البري من وسطك اذا عملت الصالح في عيني الرب"));
arasuperobject.biblemap.put("b5_21_10", new arasuperobject(5,21,10,"اذا خرجت لمحاربة اعدائك ودفعهم الرب الهك الى يدك وسبيت منهم سبيا"));
arasuperobject.biblemap.put("b5_21_11", new arasuperobject(5,21,11,"ورأيت في السبي امرأة جميلة الصورة والتصقت بها واتخذتها لك زوجة"));
arasuperobject.biblemap.put("b5_21_12", new arasuperobject(5,21,12,"فحين تدخلها الى بيتك تحلق راسها وتقلم اظفارها"));
arasuperobject.biblemap.put("b5_21_13", new arasuperobject(5,21,13,"وتنزع ثياب سبيها عنها وتقعد في بيتك وتبكي اباها وامها شهرا من الزمان ثم بعد ذلك تدخل عليها وتتزوج بها فتكون لك زوجة"));
arasuperobject.biblemap.put("b5_21_14", new arasuperobject(5,21,14,"وان لم تسرّ بها فاطلقها لنفسها. لا تبعها بيعا بفضة ولا تسترقها من اجل انك قد اذللتها"));
arasuperobject.biblemap.put("b5_21_15", new arasuperobject(5,21,15,"اذا كان لرجل امرأتان احداهما محبوبة والاخرى مكروهة فولدتا له بنين المحبوبة والمكروهة. فان كان الابن البكر للمكروهة"));
arasuperobject.biblemap.put("b5_21_16", new arasuperobject(5,21,16,"فيوم يقسم لبنيه ما كان له لا يحل له ان يقدم ابن المحبوبة بكرا على ابن المكروهة البكر"));
arasuperobject.biblemap.put("b5_21_17", new arasuperobject(5,21,17,"بل يعرف ابن المكروهة بكرا ليعطيه نصيب اثنين من كل ما يوجد عنده لانه هو اول قدرته له حق البكورية"));
arasuperobject.biblemap.put("b5_21_18", new arasuperobject(5,21,18,"اذا كان لرجل ابن معاند ومارد لا يسمع لقول ابيه ولا لقول امه ويؤدبانه فلا يسمع لهما"));
arasuperobject.biblemap.put("b5_21_19", new arasuperobject(5,21,19,"يمسكه ابوه وامه ويأتيان به الى شيوخ مدينته والى باب مكانه"));
arasuperobject.biblemap.put("b5_21_20", new arasuperobject(5,21,20,"ويقولان لشيوخ مدينته. ابننا هذا معاند ومارد لا يسمع لقولنا وهو مسرف وسكير"));
arasuperobject.biblemap.put("b5_21_21", new arasuperobject(5,21,21,"فيرجمه جميع رجال مدينته بحجارة حتى يموت. فتنزع الشر من بينكم ويسمع كل اسرائيل ويخافون"));
arasuperobject.biblemap.put("b5_21_22", new arasuperobject(5,21,22,"واذا كان على انسان خطية حقها الموت فقتل وعلقته على خشبة"));
arasuperobject.biblemap.put("b5_21_23", new arasuperobject(5,21,23,"فلا تبت جثته على الخشبة بل تدفنه في ذلك اليوم. لان المعلّق ملعون من الله. فلا تنجس ارضك التي يعطيك الرب الهك نصيبا"));
arasuperobject.biblemap.put("b5_22_1", new arasuperobject(5,22,1,"لا تنظر ثور اخيك او شاته شاردا وتتغاضى عنه بل ترده الى اخيك لا محالة"));
arasuperobject.biblemap.put("b5_22_2", new arasuperobject(5,22,2,"وان لم يكن اخوك قريبا منك او لم تعرفه فضمّه الى داخل بيتك ويكون عندك حتى يطلبه اخوك حينئذ ترده اليه"));
arasuperobject.biblemap.put("b5_22_3", new arasuperobject(5,22,3,"وهكذا تفعل بحماره وهكذا تفعل بثيابه. وهكذا تفعل بكل مفقود لاخيك يفقد منه وتجده. لا يحل لك ان تتغاضى"));
arasuperobject.biblemap.put("b5_22_4", new arasuperobject(5,22,4,"لا تنظر حمار اخيك او ثوره واقعا في الطريق وتتغافل عنه بل تقيمه معه لا محالة"));
arasuperobject.biblemap.put("b5_22_5", new arasuperobject(5,22,5,"لا يكون متاع رجل على امرأة ولا يلبس رجل ثوب امرأة لان كل من يعمل ذلك مكروه لدى الرب الهك"));
arasuperobject.biblemap.put("b5_22_6", new arasuperobject(5,22,6,"اذا اتفق قدامك عش طائر في الطريق في شجرة ما او على الارض فيه فراخ او بيض والام حاضنة الفراخ او البيض فلا تأخذ الام مع الاولاد"));
arasuperobject.biblemap.put("b5_22_7", new arasuperobject(5,22,7,"اطلق الام وخذ لنفسك الاولاد لكي يكون لك خير وتطيل الايام"));
arasuperobject.biblemap.put("b5_22_8", new arasuperobject(5,22,8,"اذا بنيت بيتا جديدا فاعمل حائطا لسطحك لئلا تجلب دما على بيتك اذا سقط عنه ساقط"));
arasuperobject.biblemap.put("b5_22_9", new arasuperobject(5,22,9,"لا تزرع حقلك صنفين لئلا يتقدس الملء الزرع الذي تزرع ومحصول الحقل"));
arasuperobject.biblemap.put("b5_22_10", new arasuperobject(5,22,10,"لا تحرث على ثور وحمار معا"));
arasuperobject.biblemap.put("b5_22_11", new arasuperobject(5,22,11,"لا تلبس ثوبا مختلطا صوفا وكتانا معا"));
arasuperobject.biblemap.put("b5_22_12", new arasuperobject(5,22,12,"اعمل لنفسك جدائل على اربعة اطراف ثوبك الذي تتغطى به"));
arasuperobject.biblemap.put("b5_22_13", new arasuperobject(5,22,13,"اذا اتخذ رجل امرأة وحين دخل عليها ابغضها"));
arasuperobject.biblemap.put("b5_22_14", new arasuperobject(5,22,14,"ونسب اليها اسباب كلام واشاع عنها اسما رديا وقال هذه المرأة اتخذتها ولما دنوت منها لم اجد لها عذرة"));
arasuperobject.biblemap.put("b5_22_15", new arasuperobject(5,22,15,"ياخذ الفتاة ابوها وامها ويخرجان علامة عذرتها الى شيوخ المدينة الى الباب"));
arasuperobject.biblemap.put("b5_22_16", new arasuperobject(5,22,16,"ويقول ابو الفتاة للشيوخ اعطيت هذا الرجل ابنتي زوجة فابغضها"));
arasuperobject.biblemap.put("b5_22_17", new arasuperobject(5,22,17,"وها هو قد جعل اسباب كلام قائلا لم اجد لبنتك عذرة وهذه علامة عذرة ابنتي ويبسطان الثوب امام شيوخ المدينة"));
arasuperobject.biblemap.put("b5_22_18", new arasuperobject(5,22,18,"فيأخذ شيوخ تلك المدينة الرجل ويؤدبونه"));
arasuperobject.biblemap.put("b5_22_19", new arasuperobject(5,22,19,"ويغرمونه بمئة من الفضة ويعطونها لابي الفتاة لانه اشاع اسما رديا عن عذراء من اسرائيل. فتكون له زوجة لا يقدر ان يطلقها كل ايامه"));
arasuperobject.biblemap.put("b5_22_20", new arasuperobject(5,22,20,"ولكن ان كان هذا الامر صحيحا ولم توجد عذرة للفتاة"));
arasuperobject.biblemap.put("b5_22_21", new arasuperobject(5,22,21,"يخرجون الفتاة الى باب بيت ابيها ويرجمها رجال مدينتها بالحجارة حتى تموت لانها عملت قباحة في اسرائيل بزناها في بيت ابيها. فتنزع الشر من وسطك"));
arasuperobject.biblemap.put("b5_22_22", new arasuperobject(5,22,22,"اذا وجد رجل مضطجعا مع امرأة زوجة بعل يقتل الاثنان الرجل المضطجع مع المرأة والمرأة. فتنزع الشر من اسرائيل"));
arasuperobject.biblemap.put("b5_22_23", new arasuperobject(5,22,23,"اذا كانت فتاة عذراء مخطوبة لرجل فوجدها رجل في المدينة واضطجع معها"));
arasuperobject.biblemap.put("b5_22_24", new arasuperobject(5,22,24,"فاخرجوهما كليهما الى باب تلك المدينة وارجموهما بالحجارة حتى يموتا الفتاة من اجل انها لم تصرخ في المدينة والرجل من اجل انه اذل امرأة صاحبه فتنزع الشر من وسطك"));
arasuperobject.biblemap.put("b5_22_25", new arasuperobject(5,22,25,"ولكن ان وجد الرجل الفتاة المخطوبة في الحقل وامسكها الرجل واضطجع معها يموت الرجل الذي اضطجع معها وحده"));
arasuperobject.biblemap.put("b5_22_26", new arasuperobject(5,22,26,"واما الفتاة فلا تفعل بها شيئا. ليس على الفتاة خطية للموت بل كما يقوم رجل على صاحبه ويقتله قتلا هكذا هذا الامر"));
arasuperobject.biblemap.put("b5_22_27", new arasuperobject(5,22,27,"انه في الحقل وجدها فصرخت الفتاة المخطوبة فلم يكن من يخلّصها"));
arasuperobject.biblemap.put("b5_22_28", new arasuperobject(5,22,28,"اذا وجد رجل فتاة عذراء غير مخطوبة فامسكها واضطجع معها فوجدا"));
arasuperobject.biblemap.put("b5_22_29", new arasuperobject(5,22,29,"يعطي الرجل الذي اضطجع معها لابي الفتاة خمسين من الفضة وتكون هي له زوجة من اجل انه قد اذلّها. لا يقدر ان يطلقها كل ايامه"));
arasuperobject.biblemap.put("b5_22_30", new arasuperobject(5,22,30,"لا يتخذ رجل امرأة ابيه ولا يكشف ذيل ابيه"));
arasuperobject.biblemap.put("b5_23_1", new arasuperobject(5,23,1,"لا يدخل مخصيّ بالرضّ او مجبوب في جماعة الرب"));
arasuperobject.biblemap.put("b5_23_2", new arasuperobject(5,23,2,"لا يدخل ابن زنى في جماعة الرب. حتى الجيل العاشر لا يدخل منه احد في جماعة الرب"));
arasuperobject.biblemap.put("b5_23_3", new arasuperobject(5,23,3,"لا يدخل عموني ولا موآبي في جماعة الرب. حتى الجيل العاشر لا يدخل منهم احد في جماعة الرب الى الابد"));
arasuperobject.biblemap.put("b5_23_4", new arasuperobject(5,23,4,"من اجل انهم لم يلاقوكم بالخبز والماء في الطريق عند خروجكم من مصر ولانهم استأجروا عليك بلعام بن بعور من فتور ارام النهرين لكي يلعنك"));
arasuperobject.biblemap.put("b5_23_5", new arasuperobject(5,23,5,"ولكن لم يشإ الرب الهك ان يسمع لبلعام فحول لاجلك الرب الهك اللعنة الى بركة لان الرب الهك قد احبك"));
arasuperobject.biblemap.put("b5_23_6", new arasuperobject(5,23,6,"لا تلتمس سلامهم ولا خيرهم كل ايامك الى الابد"));
arasuperobject.biblemap.put("b5_23_7", new arasuperobject(5,23,7,"لا تكره ادوميا لانه اخوك لا تكره مصريا لانك كنت نزيلا في ارضه"));
arasuperobject.biblemap.put("b5_23_8", new arasuperobject(5,23,8,"الاولاد الذين يولدون لهم في الجيل الثالث يدخلون منهم في جماعة الرب"));
arasuperobject.biblemap.put("b5_23_9", new arasuperobject(5,23,9,"اذا خرجت في جيش على اعدائك فاحترز من كل شيء رديء"));
arasuperobject.biblemap.put("b5_23_10", new arasuperobject(5,23,10,"ان كان فيك رجل غير طاهر من عارض الليل يخرج الى خارج المحلّة لا يدخل الى داخل المحلّة"));
arasuperobject.biblemap.put("b5_23_11", new arasuperobject(5,23,11,"ونحو اقبال المساء يغتسل بماء وعند غروب الشمس يدخل الى داخل المحلّة"));
arasuperobject.biblemap.put("b5_23_12", new arasuperobject(5,23,12,"ويكون لك موضع خارج المحلّة لتخرج اليه خارجا"));
arasuperobject.biblemap.put("b5_23_13", new arasuperobject(5,23,13,"ويكون لك وتد مع عدّتك لتحفر به عندما تجلس خارجا وترجع وتغطي برازك"));
arasuperobject.biblemap.put("b5_23_14", new arasuperobject(5,23,14,"لان الرب الهك سائر في وسط محلّتك لكي ينقذك ويدفع اعداءك امامك. فلتكن محلّتك مقدسة لئلا يرى فيك قذر شيء فيرجع عنك"));
arasuperobject.biblemap.put("b5_23_15", new arasuperobject(5,23,15,"عبدا ابق اليك من مولاه لا تسلم الى مولاه"));
arasuperobject.biblemap.put("b5_23_16", new arasuperobject(5,23,16,"عندك يقيم في وسطك في المكان الذي يختاره في احد ابوابك حيث يطيب له. لا تظلمه"));
arasuperobject.biblemap.put("b5_23_17", new arasuperobject(5,23,17,"لا تكن زانية من بنات اسرائيل ولا يكون مأبونون من بني اسرائيل"));
arasuperobject.biblemap.put("b5_23_18", new arasuperobject(5,23,18,"لا تدخل اجرة زانية ولا ثمن كلب الى بيت الرب الهك عن نذر ما لانهما كليهما رجس لدى الرب الهك"));
arasuperobject.biblemap.put("b5_23_19", new arasuperobject(5,23,19,"لا تقرض اخاك بربا ربا فضة او ربا طعام او ربا شيء ما مما يقرض بربا"));
arasuperobject.biblemap.put("b5_23_20", new arasuperobject(5,23,20,"للاجنبي تقرض بربا ولكن لاخيك لا تقرض بربا لكي يباركك الرب الهك في كل ما تمتد اليه يدك في الارض التي انت داخل اليها لتمتلكها"));
arasuperobject.biblemap.put("b5_23_21", new arasuperobject(5,23,21,"اذا نذرت نذرا للرب الهك فلا تؤخر وفاءه. لان الرب الهك يطلبه منك فتكون عليك خطية"));
arasuperobject.biblemap.put("b5_23_22", new arasuperobject(5,23,22,"ولكن اذا امتنعت ان تنذر لا تكون عليك خطية"));
arasuperobject.biblemap.put("b5_23_23", new arasuperobject(5,23,23,"ما خرج من شفتيك احفظ واعمل كما نذرت للرب الهك تبرعا كما تكلم فمك"));
arasuperobject.biblemap.put("b5_23_24", new arasuperobject(5,23,24,"اذا دخلت كرم صاحبك فكل عنبا حسب شهوة نفسك شبعتك ولكن في وعائك لا تجعل"));
arasuperobject.biblemap.put("b5_23_25", new arasuperobject(5,23,25,"اذا دخلت زرع صاحبك فاقطف سنابل بيدك ولكن منجلا لا ترفع على زرع صاحبك"));
arasuperobject.biblemap.put("b5_24_1", new arasuperobject(5,24,1,"اذا اخذ رجل امرأة وتزوج بها فان لم تجد نعمة في عينيه لانه وجد فيها عيب شيء وكتب لها كتاب طلاق ودفعه الى يدها واطلقها من بيته"));
arasuperobject.biblemap.put("b5_24_2", new arasuperobject(5,24,2,"ومتى خرجت من بيته ذهبت وصارت لرجل آخر"));
arasuperobject.biblemap.put("b5_24_3", new arasuperobject(5,24,3,"فان ابغضها الرجل الاخير وكتب لها كتاب طلاق ودفعه الى يدها واطلقها من بيته او اذا مات الرجل الاخير الذي اتخذها له زوجة"));
arasuperobject.biblemap.put("b5_24_4", new arasuperobject(5,24,4,"لا يقدر زوجها الاول الذي طلقها ان يعود ياخذها لتصير له زوجة بعد ان تنجست. لان ذلك رجس لدى الرب. فلا تجلب خطية على الارض التي يعطيك الرب الهك نصيبا"));
arasuperobject.biblemap.put("b5_24_5", new arasuperobject(5,24,5,"اذا اتخذ رجل امرأة جديدة فلا يخرج في الجند ولا يحمل عليه امر ما. حرا يكون في بيته سنة واحدة ويسرّ امرأته التي اخذها"));
arasuperobject.biblemap.put("b5_24_6", new arasuperobject(5,24,6,"لا يسترهن احد رحى او مرداتها لانه انما يسترهن حياة"));
arasuperobject.biblemap.put("b5_24_7", new arasuperobject(5,24,7,"اذا وجد رجل قد سرق نفسا من اخوته بني اسرائيل واسترقه وباعه يموت ذلك السارق فتنزع الشر من وسطك"));
arasuperobject.biblemap.put("b5_24_8", new arasuperobject(5,24,8,"احرص في ضربة البرص لتحفظ جدا وتعمل حسب كل ما يعلّمك الكهنة واللاويون. كما امرتهم تحرصون ان تعملوا"));
arasuperobject.biblemap.put("b5_24_9", new arasuperobject(5,24,9,"اذكر ما صنع الرب الهك بمريم في الطريق عند خروجكم من مصر"));
arasuperobject.biblemap.put("b5_24_10", new arasuperobject(5,24,10,"اذا اقرضت صاحبك قرضا ما فلا تدخل بيته لكي ترتهن رهنا منه"));
arasuperobject.biblemap.put("b5_24_11", new arasuperobject(5,24,11,"في الخارج تقف والرجل الذي تقرضه يخرج اليك الرهن الى الخارج"));
arasuperobject.biblemap.put("b5_24_12", new arasuperobject(5,24,12,"وان كان رجلا فقيرا فلا تنم في رهنه"));
arasuperobject.biblemap.put("b5_24_13", new arasuperobject(5,24,13,"رد اليه الرهن عند غروب الشمس لكي ينام في ثوبه ويباركك فيكون لك بر لدى الرب الهك"));
arasuperobject.biblemap.put("b5_24_14", new arasuperobject(5,24,14,"لا تظلم اجيرا مسكينا وفقيرا من اخوتك او من الغرباء الذين في ارضك في ابوابك"));
arasuperobject.biblemap.put("b5_24_15", new arasuperobject(5,24,15,"في يومه تعطيه اجرته ولا تغرب عليها الشمس لانه فقير واليها حامل نفسه لئلا يصرخ عليك الى الرب فتكون عليك خطية"));
arasuperobject.biblemap.put("b5_24_16", new arasuperobject(5,24,16,"لا يقتل الآباء عن الاولاد ولا يقتل الاولاد عن الآباء. كل انسان بخطيته يقتل"));
arasuperobject.biblemap.put("b5_24_17", new arasuperobject(5,24,17,"لا تعوج حكم الغريب واليتيم ولا تسترهن ثوب الارملة"));
arasuperobject.biblemap.put("b5_24_18", new arasuperobject(5,24,18,"واذكر انك كنت عبدا في مصر ففداك الرب الهك من هناك. لذلك انا اوصيك ان تعمل هذا الامر"));
arasuperobject.biblemap.put("b5_24_19", new arasuperobject(5,24,19,"اذا حصدت حصيدك في حقلك ونسيت حزمة في الحقل فلا ترجع لتأخذها. للغريب واليتيم والارملة تكون لكي يباركك الرب الهك في كل عمل يديك"));
arasuperobject.biblemap.put("b5_24_20", new arasuperobject(5,24,20,"واذا خبطت زيتونك فلا تراجع الاغصان وراءك. للغريب واليتيم والارملة يكون"));
arasuperobject.biblemap.put("b5_24_21", new arasuperobject(5,24,21,"اذا قطفت كرمك فلا تعلله وراءك. للغريب واليتيم والارملة يكون"));
arasuperobject.biblemap.put("b5_24_22", new arasuperobject(5,24,22,"واذكر انك كنت عبدا في ارض مصر. لذلك انا اوصيك ان تعمل هذا الامر"));
arasuperobject.biblemap.put("b5_25_1", new arasuperobject(5,25,1,"اذا كانت خصومة بين اناس وتقدموا الى القضاء ليقضي القضاة بينهم فليبرروا البار ويحكموا على المذنب"));
arasuperobject.biblemap.put("b5_25_2", new arasuperobject(5,25,2,"فان كان المذنب مستوجب الضرب يطرحه القاضي ويجلدونه امامه على قدر ذنبه بالعدد"));
arasuperobject.biblemap.put("b5_25_3", new arasuperobject(5,25,3,"اربعين يجلده لا يزد لئلا اذا زاد في جلده على هذه ضربات كثيرة يحتقر اخوك في عينيك"));
arasuperobject.biblemap.put("b5_25_4", new arasuperobject(5,25,4,"لا تكمّ الثور في دراسه"));
arasuperobject.biblemap.put("b5_25_5", new arasuperobject(5,25,5,"اذا سكن اخوة معا ومات واحد منهم وليس له ابن فلا تصر امرأة الميت الى خارج لرجل اجنبي. اخو زوجها يدخل عليها ويتخذها لنفسه زوجة ويقوم لها بواجب اخي الزوج"));
arasuperobject.biblemap.put("b5_25_6", new arasuperobject(5,25,6,"والبكر الذي تلده يقوم باسم اخيه الميت لئلا يمحى اسمه من اسرائيل"));
arasuperobject.biblemap.put("b5_25_7", new arasuperobject(5,25,7,"وان لم يرضى الرجل ان يأخذ امرأة اخيه تصعد امرأة اخيه الى الباب الى الشيوخ وتقول قد ابى اخو زوجي ان يقيم لاخيه اسما في اسرائيل. لم يشأ ان يقوم لي بواجب اخي الزوج"));
arasuperobject.biblemap.put("b5_25_8", new arasuperobject(5,25,8,"فيدعوه شيوخ مدينته ويتكلمون معه فان اصرّ وقال لا ارضى ان اتخذها"));
arasuperobject.biblemap.put("b5_25_9", new arasuperobject(5,25,9,"تتقدم امرأة اخيه اليه امام اعين الشيوخ وتخلع نعله من رجله وتبصق في وجهه وتصرح وتقول هكذا يفعل بالرجل الذي لا يبني بيت اخيه"));
arasuperobject.biblemap.put("b5_25_10", new arasuperobject(5,25,10,"فيدعى اسمه في اسرائيل بيت مخلوع النعل"));
arasuperobject.biblemap.put("b5_25_11", new arasuperobject(5,25,11,"اذا تخاصم رجلان بعضهما بعضا رجل واخوه وتقدمت امرأة احدهما لكي تخلّص رجلها من يد ضاربه ومدّت يدها وامسكت بعورته"));
arasuperobject.biblemap.put("b5_25_12", new arasuperobject(5,25,12,"فاقطع يدها ولا تشفق عينك"));
arasuperobject.biblemap.put("b5_25_13", new arasuperobject(5,25,13,"لا يكن لك في كيسك اوزان مختلفة كبيرة وصغيرة"));
arasuperobject.biblemap.put("b5_25_14", new arasuperobject(5,25,14,"لا يكن لك في بيتك مكاييل مختلفة كبيرة وصغيرة"));
arasuperobject.biblemap.put("b5_25_15", new arasuperobject(5,25,15,"وزن صحيح وحق يكون لك ومكيال صحيح وحق يكون لك لكي تطول ايامك على الارض التي يعطيك الرب الهك"));
arasuperobject.biblemap.put("b5_25_16", new arasuperobject(5,25,16,"لان كل من عمل ذلك كل من عمل غشا مكروه لدى الرب الهك"));
arasuperobject.biblemap.put("b5_25_17", new arasuperobject(5,25,17,"اذكر ما فعله بك عماليق في الطريق عند خروجك من مصر"));
arasuperobject.biblemap.put("b5_25_18", new arasuperobject(5,25,18,"كيف لاقاك في الطريق وقطع من مؤخرك كل المستضعفين وراءك وانت كليل ومتعب ولم يخف الله"));
arasuperobject.biblemap.put("b5_25_19", new arasuperobject(5,25,19,"فمتى اراحك الرب الهك من جميع اعدائك حولك في الارض التي يعطيك الرب الهك نصيبا لكي تمتلكها تمحو ذكر عماليق من تحت السماء. لا تنسى"));
arasuperobject.biblemap.put("b5_26_1", new arasuperobject(5,26,1,"ومتى أتيت الى الارض التي يعطيك الرب الهك نصيبا وامتلكتها وسكنت فيها"));
arasuperobject.biblemap.put("b5_26_2", new arasuperobject(5,26,2,"فتأخذ من اول كل ثمر الارض الذي تحصل من ارضك التي يعطيك الرب الهك وتضعه في سلة وتذهب الى المكان الذي يختاره الرب الهك ليحل اسمه فيه"));
arasuperobject.biblemap.put("b5_26_3", new arasuperobject(5,26,3,"وتأتي الى الكاهن الذي يكون في تلك الايام وتقول له. اعترف اليوم للرب الهك اني قد دخلت الارض التي حلف الرب لآبائنا ان يعطينا اياها"));
arasuperobject.biblemap.put("b5_26_4", new arasuperobject(5,26,4,"فيأخذ الكاهن السلة من يدك ويضعها امام مذبح الرب الهك"));
arasuperobject.biblemap.put("b5_26_5", new arasuperobject(5,26,5,"ثم تصرّح وتقول امام الرب الهك. اراميا تائها كان ابي فانحدر الى مصر وتغرب هناك في نفر قليل فصار هناك امة كبيرة وعظيمة وكثيرة"));
arasuperobject.biblemap.put("b5_26_6", new arasuperobject(5,26,6,"فاساء الينا المصريون وثقلوا علينا وجعلوا علينا عبودية قاسية"));
arasuperobject.biblemap.put("b5_26_7", new arasuperobject(5,26,7,"فلما صرخنا الى الرب اله آبائنا سمع الرب صوتنا ورأى مشقتنا وتعبنا وضيقنا"));
arasuperobject.biblemap.put("b5_26_8", new arasuperobject(5,26,8,"فاخرجنا الرب من مصر بيد شديدة وذراع رفيعة ومخاوف عظيمة وآيات وعجائب"));
arasuperobject.biblemap.put("b5_26_9", new arasuperobject(5,26,9,"وادخلنا هذا المكان واعطانا هذه الارض ارضا تفيض لبنا وعسلا"));
arasuperobject.biblemap.put("b5_26_10", new arasuperobject(5,26,10,"فالآن هانذا قد اتيت باول ثمر الارض التي اعطيتني يا رب. ثم تضعه امام الرب الهك وتسجد امام الرب الهك"));
arasuperobject.biblemap.put("b5_26_11", new arasuperobject(5,26,11,"وتفرح بجميع الخير الذي اعطاه الرب الهك لك ولبيتك انت واللاوي والغريب الذي في وسطك"));
arasuperobject.biblemap.put("b5_26_12", new arasuperobject(5,26,12,"متى فرغت من تعشير كل عشور محصولك في السنة الثالثة سنة العشور واعطيت اللاوي والغريب واليتيم والارملة فأكلوا في ابوابك وشبعوا"));
arasuperobject.biblemap.put("b5_26_13", new arasuperobject(5,26,13,"تقول امام الرب الهك. قد نزعت المقدس من البيت وايضا اعطيته للاوي والغريب واليتيم والارملة حسب كل وصيتك التي اوصيتني بها. لم اتجاوز وصاياك ولا نسيتها"));
arasuperobject.biblemap.put("b5_26_14", new arasuperobject(5,26,14,"لم آكل منه في حزني ولا اخذت منه في نجاسة ولا اعطيت منه لاجل ميت بل سمعت لصوت الرب الهي وعملت حسب كل ما اوصيتني"));
arasuperobject.biblemap.put("b5_26_15", new arasuperobject(5,26,15,"اطلع من مسكن قدسك من السماء وبارك شعبك اسرائيل والارض التي اعطيتنا كما حلفت لآبائنا ارضا تفيض لبنا وعسلا"));
arasuperobject.biblemap.put("b5_26_16", new arasuperobject(5,26,16,"هذا اليوم قد امرك الرب الهك ان تعمل بهذه الفرائض والاحكام فاحفظ واعمل بها من كل قلبك ومن كل نفسك"));
arasuperobject.biblemap.put("b5_26_17", new arasuperobject(5,26,17,"قد واعدت الرب اليوم ان يكون لك الها وان تسلك في طرقه وتحفظ فرائضه ووصاياه واحكامه وتسمع لصوته"));
arasuperobject.biblemap.put("b5_26_18", new arasuperobject(5,26,18,"وواعدك الرب اليوم ان تكون له شعبا خاصّا كما قال لك وتحفظ جميع وصاياه"));
arasuperobject.biblemap.put("b5_26_19", new arasuperobject(5,26,19,"وان يجعلك مستعليا على جميع القبائل التي عملها في الثناء والاسم والبهاء وان تكون شعبا مقدسا للرب الهك كما قال"));
arasuperobject.biblemap.put("b5_27_1", new arasuperobject(5,27,1,"واوصى موسى وشيوخ اسرائيل الشعب قائلا. احفظوا جميع الوصايا التي انا اوصيكم بها اليوم"));
arasuperobject.biblemap.put("b5_27_2", new arasuperobject(5,27,2,"فيوم تعبرون الاردن الى الارض التي يعطيك الرب الهك تقيم لنفسك حجارة كبيرة وتشيدها بالشيد"));
arasuperobject.biblemap.put("b5_27_3", new arasuperobject(5,27,3,"وتكتب عليها جميع كلمات هذا الناموس حين تعبر لكي تدخل الارض التي يعطيك الرب الهك ارضا تفيض لبنا وعسلا كما قال لك الرب اله آبائك"));
arasuperobject.biblemap.put("b5_27_4", new arasuperobject(5,27,4,"حين تعبرون الاردن تقيمون هذه الحجارة التي انا اوصيكم بها اليوم في جبل عيبال وتكلسها بالكلس"));
arasuperobject.biblemap.put("b5_27_5", new arasuperobject(5,27,5,"وتبني هناك مذبحا للرب الهك مذبحا من حجارة لا ترفع عليها حديدا"));
