arasuperobject.biblemap.put("b5_27_9", new arasuperobject(5,27,9,"ثم كلم موسى والكهنة اللاويون جميع اسرائيل قائلين. انصت واسمع يا اسرائيل. اليوم صرت شعبا للرب الهك"));
arasuperobject.biblemap.put("b5_27_10", new arasuperobject(5,27,10,"فاسمع لصوت الرب الهك واعمل بوصاياه وفرائضه التي انا اوصيك بها اليوم"));
