arasuperobject.biblemap.put("b5_27_12", new arasuperobject(5,27,12,"هؤلاء يقفون على جبل جرزيم لكي يباركوا الشعب حين تعبرون الاردن. شمعون ولاوي ويهوذا ويساكر ويوسف وبنيامين"));
arasuperobject.biblemap.put("b5_27_13", new arasuperobject(5,27,13,"وهؤلاء يقفون على جبل عيبال للعنة. رأوبين وجاد واشير وزبولون ودان ونفتالي"));
arasuperobject.biblemap.put("b5_27_14", new arasuperobject(5,27,14,"فيصرح اللاويون ويقولون لجميع قوم اسرائيل بصوت عال"));
arasuperobject.biblemap.put("b5_27_15", new arasuperobject(5,27,15,"ملعون الانسان الذي يصنع تمثالا منحوتا او مسبوكا رجسا لدى الرب عمل يدي نحات ويضعه في الخفاء. ويجيب جميع الشعب ويقولون آمين"));
arasuperobject.biblemap.put("b5_27_16", new arasuperobject(5,27,16,"ملعون من يستخف بابيه او امه. ويقول جميع الشعب آمين"));
arasuperobject.biblemap.put("b5_27_17", new arasuperobject(5,27,17,"ملعون من ينقل تخم صاحبه. ويقول جميع الشعب آمين"));
