arasuperobject.biblemap.put("b5_27_22", new arasuperobject(5,27,22,"ملعون من يضطجع مع اخته بنت ابيه او بنت امه. ويقول جميع الشعب آمين"));
arasuperobject.biblemap.put("b5_27_23", new arasuperobject(5,27,23,"ملعون من يضطجع مع حماته. ويقول جميع الشعب آمين"));
arasuperobject.biblemap.put("b5_27_24", new arasuperobject(5,27,24,"ملعون من يقتل قريبه في الخفاء. ويقول جميع الشعب آمين"));
arasuperobject.biblemap.put("b5_27_25", new arasuperobject(5,27,25,"ملعون من يأخذ رشوة لكي يقتل نفس دم بريء. ويقول جميع الشعب آمين"));
