arasuperobject.biblemap.put("b5_28_1", new arasuperobject(5,28,1,"وان سمعت سمعا لصوت الرب الهك لتحرص ان تعمل بجميع وصاياه التي انا اوصيك بها اليوم يجعلك الرب الهك مستعليا على جميع قبائل الارض"));
arasuperobject.biblemap.put("b5_28_2", new arasuperobject(5,28,2,"وتاتي عليك جميع هذه البركات وتدركك اذا سمعت لصوت الرب الهك"));
arasuperobject.biblemap.put("b5_28_3", new arasuperobject(5,28,3,"مباركا تكون في المدينة ومباركا تكون في الحقل"));
arasuperobject.biblemap.put("b5_28_4", new arasuperobject(5,28,4,"ومباركة تكون ثمرة بطنك وثمرة ارضك وثمرة بهائمك نتاج بقرك واناث غنمك"));
arasuperobject.biblemap.put("b5_28_5", new arasuperobject(5,28,5,"مباركة تكون سلتك ومعجنك"));
arasuperobject.biblemap.put("b5_28_6", new arasuperobject(5,28,6,"مباركا تكون في دخولك ومباركا تكون في خروجك"));
arasuperobject.biblemap.put("b5_28_7", new arasuperobject(5,28,7,"يجعل الرب اعداءك القائمين عليك منهزمين امامك. في طريق واحد يخرجون عليك وفي سبع طرق يهربون امامك"));
arasuperobject.biblemap.put("b5_28_8", new arasuperobject(5,28,8,"يأمر لك الرب بالبركة في خزائنك وفي كل ما تمتد اليه يدك ويباركك في الارض التي يعطيك الرب الهك"));
arasuperobject.biblemap.put("b5_28_9", new arasuperobject(5,28,9,"يقيمك الرب لنفسه شعبا مقدسا كما حلف لك اذا حفظت وصايا الرب الهك وسلكت في طرقه"));
arasuperobject.biblemap.put("b5_28_10", new arasuperobject(5,28,10,"فيرى جميع شعوب الارض ان اسم الرب قد سمي عليك ويخافون منك"));
arasuperobject.biblemap.put("b5_28_11", new arasuperobject(5,28,11,"ويزيدك الرب خيرا في ثمرة بطنك وثمرة بهائمك وثمرة ارضك على الارض التي حلف الرب لآبائك ان يعطيك"));
arasuperobject.biblemap.put("b5_28_12", new arasuperobject(5,28,12,"يفتح لك الرب كنزه الصالح السماء ليعطي مطر ارضك في حينه وليبارك كل عمل يدك فتقرض امما كثيرة وانت لا تقترض"));
arasuperobject.biblemap.put("b5_28_13", new arasuperobject(5,28,13,"ويجعلك الرب راسا لا ذنبا وتكون في الارتفاع فقط ولا تكون في الانحطاط اذا سمعت لوصايا الرب الهك التي انا اوصيك بها اليوم لتحفظ وتعمل"));
arasuperobject.biblemap.put("b5_28_14", new arasuperobject(5,28,14,"ولا تزيغ عن جميع الكلمات التي انا اوصيك بها اليوم يمينا او شمالا لكي تذهب وراء آلهة اخرى لتعبدها"));
arasuperobject.biblemap.put("b5_28_15", new arasuperobject(5,28,15,"ولكن ان لم تسمع لصوت الرب الهك لتحرص ان تعمل بجميع وصاياه وفرائضه التي انا اوصيك بها اليوم تاتي عليك جميع هذه اللعنات وتدركك"));
arasuperobject.biblemap.put("b5_28_16", new arasuperobject(5,28,16,"ملعونا تكون في المدينة وملعونا تكون في الحقل"));
arasuperobject.biblemap.put("b5_28_17", new arasuperobject(5,28,17,"ملعونة تكون سلتك ومعجنك"));
arasuperobject.biblemap.put("b5_28_18", new arasuperobject(5,28,18,"ملعونة تكون ثمرة بطنك وثمرة ارضك نتاج بقرك واناث غنمك"));
arasuperobject.biblemap.put("b5_28_19", new arasuperobject(5,28,19,"ملعونا تكون في دخولك وملعونا تكون في خروجك"));
arasuperobject.biblemap.put("b5_28_20", new arasuperobject(5,28,20,"يرسل الرب عليك اللعن والاضطراب والزجر في كل ما تمتد اليه يدك لتعمله حتى تهلك وتفنى سريعا من اجل سوء افعالك اذ تركتني"));
arasuperobject.biblemap.put("b5_28_21", new arasuperobject(5,28,21,"يلصق بك الرب الوبأ حتى يبيدك عن الارض التي انت داخل اليها لكي تمتلكها"));
arasuperobject.biblemap.put("b5_28_22", new arasuperobject(5,28,22,"يضربك الرب بالسل والحمى والبرداء والالتهاب والجفاف واللفح والذبول فتتبعك حتى تفنيك"));
arasuperobject.biblemap.put("b5_28_23", new arasuperobject(5,28,23,"وتكون سماؤك التي فوق راسك نحاسا والارض التي تحتك حديدا"));
arasuperobject.biblemap.put("b5_28_24", new arasuperobject(5,28,24,"ويجعل الرب مطر ارضك غبارا وترابا ينزل عليك من السماء حتى تهلك"));
arasuperobject.biblemap.put("b5_28_25", new arasuperobject(5,28,25,"يجعلك الرب منهزما امام اعدائك. في طريق واحدة تخرج عليهم وفي سبع طرق تهرب امامهم وتكون قلقا في جميع ممالك الارض"));
arasuperobject.biblemap.put("b5_28_26", new arasuperobject(5,28,26,"وتكون جثتك طعاما لجميع طيور السماء ووحوش الارض وليس من يزعجها"));
