arasuperobject.biblemap.put("b5_28_32", new arasuperobject(5,28,32,"يسلم بنوك وبناتك لشعب آخر وعيناك تنظران اليهم طول النهار فتكلان وليس في يدك طائلة"));
arasuperobject.biblemap.put("b5_28_33", new arasuperobject(5,28,33,"ثمر ارضك وكل تعبك ياكله شعب لا تعرفه فلا تكون الا مظلوما ومسحوقا كل الايام"));
arasuperobject.biblemap.put("b5_28_34", new arasuperobject(5,28,34,"وتكون مجنونا من منظر عينيك الذي تنظر"));
arasuperobject.biblemap.put("b5_28_35", new arasuperobject(5,28,35,"يضربك الرب بقرح خبيث على الركبتين وعلى الساقين حتى لا تستطيع الشفاء من اسفل قدمك الى قمة راسك"));
arasuperobject.biblemap.put("b5_28_36", new arasuperobject(5,28,36,"يذهب بك الرب وبملكك الذي تقيمه عليك الى امة لم تعرفها انت ولا آباؤك وتعبد هناك آلهة اخرى من خشب وحجر"));
arasuperobject.biblemap.put("b5_28_37", new arasuperobject(5,28,37,"وتكون دهشا ومثلا وهزأة في جميع الشعوب الذين يسوقك الرب اليهم"));
