arasuperobject.biblemap.put("b5_28_39", new arasuperobject(5,28,39,"كروما تغرس وتشتغل وخمرا لا تشرب ولا تجني لان الدود ياكلها"));
arasuperobject.biblemap.put("b5_28_40", new arasuperobject(5,28,40,"يكون لك زيتون في جميع تخومك وبزيت لا تدّهن لان زيتونك ينتثر"));
arasuperobject.biblemap.put("b5_28_41", new arasuperobject(5,28,41,"بنين وبنات تلد ولا يكونون لك لانهم الى السبي يذهبون"));
