arasuperobject.biblemap.put("b54_1_3", new arasuperobject(54,1,3,"كما طلبت اليك ان تمكث في افسس اذ كنت انا ذاهبا الى مكدونية لكي توصي قوما ان لا يعلّموا تعليما آخر"));
arasuperobject.biblemap.put("b54_1_4", new arasuperobject(54,1,4,"ولا يصغوا الى خرافات وانساب لا حد لها تسبب مباحثات دون بنيان الله الذي في الايمان"));
arasuperobject.biblemap.put("b54_1_5", new arasuperobject(54,1,5,"واما غاية الوصية فهي المحبة من قلب طاهر وضمير صالح وايمان بلا رياء"));
