arasuperobject.biblemap.put("b54_1_7", new arasuperobject(54,1,7,"يريدون ان يكونوا معلمي الناموس وهم لا يفهمون ما يقولون ولا ما يقررونه"));
arasuperobject.biblemap.put("b54_1_8", new arasuperobject(54,1,8,"ولكننا نعلم ان الناموس صالح ان كان احد يستعمله ناموسيا"));
