arasuperobject.biblemap.put("b54_1_11", new arasuperobject(54,1,11,"حسب انجيل مجد الله المبارك الذي اؤتمنت انا عليه"));
arasuperobject.biblemap.put("b54_1_12", new arasuperobject(54,1,12,"وانا اشكر المسيح يسوع ربنا الذي قواني انه حسبني امينا اذ جعلني للخدمة"));
arasuperobject.biblemap.put("b54_1_13", new arasuperobject(54,1,13,"انا الذي كنت قبلا مجدفا ومضطهدا ومفتريا. ولكنني رحمت لاني فعلت بجهل في عدم ايمان"));
arasuperobject.biblemap.put("b54_1_14", new arasuperobject(54,1,14,"وتفاضلت نعمة ربنا جدا مع الايمان والمحبة التي في المسيح يسوع"));
arasuperobject.biblemap.put("b54_1_15", new arasuperobject(54,1,15,"صادقة هي الكلمة ومستحقة كل قبول ان المسيح يسوع جاء الى العالم ليخلّص الخطاة الذين اولهم انا"));
arasuperobject.biblemap.put("b54_1_16", new arasuperobject(54,1,16,"لكنني لهذا رحمت ليظهر يسوع المسيح فيّ انا اولا كل اناة مثالا للعتيدين ان يؤمنوا به للحياة الابدية"));
arasuperobject.biblemap.put("b54_1_17", new arasuperobject(54,1,17,"وملك الدهور الذي لا يفنى ولا يرى الاله الحكيم وحده له الكرامة والمجد الى دهر الدهور. آمين"));
arasuperobject.biblemap.put("b54_1_18", new arasuperobject(54,1,18,"هذه الوصية ايها الابن تيموثاوس استودعك اياها حسب النبوات التي سبقت عليك لكي تحارب فيها المحاربة الحسنة"));
arasuperobject.biblemap.put("b54_1_19", new arasuperobject(54,1,19,"ولك ايمان وضمير صالح الذي اذ رفضه قوم انكسرت بهم السفينة من جهة الايمان ايضا"));
arasuperobject.biblemap.put("b54_1_20", new arasuperobject(54,1,20,"الذين منهم هيمينايس والاسكندر اللذان اسلمتهما للشيطان لكي يؤدبا حتى لا يجدفا"));
arasuperobject.biblemap.put("b54_2_1", new arasuperobject(54,2,1,"فاطلب اول كل شيء ان تقام طلبات وصلوات وابتهالات وتشكرات لاجل جميع الناس"));
arasuperobject.biblemap.put("b54_2_2", new arasuperobject(54,2,2,"لاجل الملوك وجميع الذين هم في منصب لكي نقضي حياة مطمئنة هادئة في كل تقوى ووقار"));
