arasuperobject.biblemap.put("b54_2_4", new arasuperobject(54,2,4,"الذي يريد ان جميع الناس يخلصون والى معرفة الحق يقبلون"));
arasuperobject.biblemap.put("b54_2_5", new arasuperobject(54,2,5,"لانه يوجد اله واحد ووسيط واحد بين الله والناس الانسان يسوع المسيح"));
arasuperobject.biblemap.put("b54_2_6", new arasuperobject(54,2,6,"الذي بذل نفسه فدية لاجل الجميع الشهادة في اوقاتها الخاصة"));
arasuperobject.biblemap.put("b54_2_7", new arasuperobject(54,2,7,"التي جعلت انا لها كارزا ورسولا. الحق اقول في المسيح ولا اكذب. معلّما للامم في الايمان والحق"));
arasuperobject.biblemap.put("b54_2_8", new arasuperobject(54,2,8,"فاريد ان يصلّي الرجال في كل مكان رافعين ايادي طاهرة بدون غضب ولا جدال"));
arasuperobject.biblemap.put("b54_2_9", new arasuperobject(54,2,9,"وكذلك ان النساء يزيّنّ ذواتهنّ بلباس الحشمة مع ورع وتعقل لا بضفائر او ذهب او لآلىء او ملابس كثيرة الثمن"));
arasuperobject.biblemap.put("b54_2_10", new arasuperobject(54,2,10,"بل كما يليق بنساء متعاهدات بتقوى الله باعمال صالحة"));
