arasuperobject.biblemap.put("b54_2_12", new arasuperobject(54,2,12,"ولكن لست آذن للمرأة ان تعلّم ولا تتسلط على الرجل بل تكون في سكوت"));
arasuperobject.biblemap.put("b54_2_13", new arasuperobject(54,2,13,"لان آدم جبل اولا ثم حواء"));
