arasuperobject.biblemap.put("b54_3_1", new arasuperobject(54,3,1,"صادقة هي الكلمة ان ابتغى احد الاسقفية فيشتهي عملا صالحا"));
arasuperobject.biblemap.put("b54_3_2", new arasuperobject(54,3,2,"فيجب ان يكون الاسقف بلا لوم بعل امرأة واحدة صاحيا عاقلا محتشما مضيفا للغرباء صالحا للتعليم"));
arasuperobject.biblemap.put("b54_3_3", new arasuperobject(54,3,3,"غير مدمن الخمر ولا ضرّاب ولا طامع بالربح القبيح بل حليما غير مخاصم ولا محب للمال"));
arasuperobject.biblemap.put("b54_3_4", new arasuperobject(54,3,4,"يدبر بيته حسنا له اولاد في الخضوع بكل وقار"));
arasuperobject.biblemap.put("b54_3_5", new arasuperobject(54,3,5,"وانما ان كان احد لا يعرف ان يدبر بيته فكيف يعتني بكنيسة الله"));
arasuperobject.biblemap.put("b54_3_6", new arasuperobject(54,3,6,"غير حديث الايمان لئلا يتصلف فيسقط في دينونة ابليس"));
arasuperobject.biblemap.put("b54_3_7", new arasuperobject(54,3,7,"ويجب ايضا ان تكون له شهادة حسنة من الذين هم من خارج لئلا يسقط في تعيير وفخ ابليس"));
arasuperobject.biblemap.put("b54_3_8", new arasuperobject(54,3,8,"كذلك يجب ان يكون الشمامسة ذوي وقار لا ذوي لسانين غير مولعين بالخمر الكثير ولا طامعين بالربح القبيح"));
arasuperobject.biblemap.put("b54_3_9", new arasuperobject(54,3,9,"ولهم سرّ الايمان بضمير طاهر"));
arasuperobject.biblemap.put("b54_3_10", new arasuperobject(54,3,10,"وانما هؤلاء ايضا ليختبروا اولا ثم يتشمسوا ان كانوا بلا لوم"));
