arasuperobject.biblemap.put("b54_3_12", new arasuperobject(54,3,12,"ليكن الشمامسة كل بعل امرأة واحدة مدبرين اولادهم وبيوتهم حسنا"));
arasuperobject.biblemap.put("b54_3_13", new arasuperobject(54,3,13,"لان الذين تشمسوا حسنا يقتنون لانفسهم درجة حسنة وثقة كثيرة في الايمان الذي بالمسيح يسوع"));
