arasuperobject.biblemap.put("b54_3_15", new arasuperobject(54,3,15,"ولكن ان كنت ابطىء فلكي تعلم كيف يجب ان تتصرف في بيت الله الذي هو كنيسة الله الحي عمود الحق وقاعدته"));
arasuperobject.biblemap.put("b54_3_16", new arasuperobject(54,3,16,"وبالاجماع عظيم هو سرّ التقوى الله ظهر في الجسد تبرر في الروح تراءى لملائكة كرز به بين الامم أومن به في العالم رفع في المجد"));
arasuperobject.biblemap.put("b54_4_1", new arasuperobject(54,4,1,"ولكن الروح يقول صريحا انه في الازمنة الاخيرة يرتد قوم عن الايمان تابعين ارواحا مضلة وتعاليم شياطين"));
arasuperobject.biblemap.put("b54_4_2", new arasuperobject(54,4,2,"في رياء اقوال كاذبة موسومة ضمائرهم"));
arasuperobject.biblemap.put("b54_4_3", new arasuperobject(54,4,3,"مانعين عن الزواج وآمرين ان يمتنع عن اطعمة قد خلقها الله لتتناول بالشكر من المؤمنين وعارفي الحق"));
arasuperobject.biblemap.put("b54_4_4", new arasuperobject(54,4,4,"لان كل خليقة الله جيده ولا يرفض شيء اذا اخذ مع الشكر"));
arasuperobject.biblemap.put("b54_4_5", new arasuperobject(54,4,5,"لانه يقدس بكلمة الله والصلاة"));
arasuperobject.biblemap.put("b54_4_6", new arasuperobject(54,4,6,"ان فكّرت الاخوة بهذا تكون خادما صالحا ليسوع المسيح متربيا بكلام الايمان والتعليم الحسن الذي تتبّعته"));
arasuperobject.biblemap.put("b54_4_7", new arasuperobject(54,4,7,"واما الخرافات الدنسة العجائزية فارفضها وروّض نفسك للتقوى"));
arasuperobject.biblemap.put("b54_4_8", new arasuperobject(54,4,8,"لان الرياضة الجسدية نافعة لقليل ولكن التقوى نافعة لكل شيء اذ لها موعد الحياة الحاضرة والعتيدة"));
arasuperobject.biblemap.put("b54_4_9", new arasuperobject(54,4,9,"صادقة هي الكلمة ومستحقة كل قبول"));
arasuperobject.biblemap.put("b54_4_10", new arasuperobject(54,4,10,"لاننا لهذا نتعب ونعيّر لاننا قد القينا رجاءنا على الله الحي الذي هو مخلّص جميع الناس ولا سيما المؤمنين"));
arasuperobject.biblemap.put("b54_4_11", new arasuperobject(54,4,11,"اوص بهذا وعلّم"));
arasuperobject.biblemap.put("b54_4_12", new arasuperobject(54,4,12,"لا يستهن احد بحداثتك بل كن قدوة للمؤمنين في الكلام في التصرف في المحبة في الروح في الايمان في الطهارة"));
arasuperobject.biblemap.put("b54_4_13", new arasuperobject(54,4,13,"الى ان اجيء اعكف على القراءة والوعظ والتعليم"));
arasuperobject.biblemap.put("b54_4_14", new arasuperobject(54,4,14,"لا تهمل الموهبة التي فيك المعطاة لك بالنبوة مع وضع ايدي المشيخة"));
arasuperobject.biblemap.put("b54_4_15", new arasuperobject(54,4,15,"اهتم بهذا. كن فيه لكي يكون تقدمك ظاهرا في كل شيء"));
arasuperobject.biblemap.put("b54_4_16", new arasuperobject(54,4,16,"لاحظ نفسك والتعليم وداوم على ذلك. لانك اذا فعلت هذا تخلّص نفسك والذين يسمعونك ايضا"));
arasuperobject.biblemap.put("b54_5_1", new arasuperobject(54,5,1,"لا تزجر شيخا بل عظه كأب والاحداث كاخوة"));
arasuperobject.biblemap.put("b54_5_2", new arasuperobject(54,5,2,"والعجائز كامّهات والحدثات كاخوات بكل طهارة"));
arasuperobject.biblemap.put("b54_5_3", new arasuperobject(54,5,3,"اكرم الارامل اللواتي هنّ بالحقيقة ارامل"));
arasuperobject.biblemap.put("b54_5_4", new arasuperobject(54,5,4,"ولكن ان كانت ارملة لها اولاد او حفدة فليتعلّموا اولا ان يوقّروا اهل بيتهم ويوفوا والديهم المكافاة. لان هذا صالح ومقبول امام الله"));
arasuperobject.biblemap.put("b54_5_5", new arasuperobject(54,5,5,"ولكن التي هي بالحقيقة ارملة ووحيدة فقد ألقت رجاءها على الله وهي تواظب الطلبات والصلوات ليلا ونهارا"));
arasuperobject.biblemap.put("b54_5_6", new arasuperobject(54,5,6,"واما المتنعمة فقد ماتت وهي حية"));
arasuperobject.biblemap.put("b54_5_7", new arasuperobject(54,5,7,"فأوص بهذا لكي يكنّ بلا لوم"));
arasuperobject.biblemap.put("b54_5_8", new arasuperobject(54,5,8,"وان كان احد لا يعتني بخاصته ولا سيما اهل بيته فقد انكر الايمان وهو شر من غير المؤمن"));
arasuperobject.biblemap.put("b54_5_9", new arasuperobject(54,5,9,"لتكتتب ارملة ان لم يكن عمرها اقل من ستين سنة امرأة رجل واحد"));
arasuperobject.biblemap.put("b54_5_10", new arasuperobject(54,5,10,"مشهودا لها في اعمال صالحة ان تكن قد ربّت الاولاد اضافت الغرباء غسلت ارجل القديسين ساعدت المتضايقين اتبعت كل عمل صالح"));
arasuperobject.biblemap.put("b54_5_11", new arasuperobject(54,5,11,"اما الارامل الحدثات فارفضهنّ لانهنّ متى بطرن على المسيح يردن ان يتزوجن"));
arasuperobject.biblemap.put("b54_5_12", new arasuperobject(54,5,12,"ولهنّ دينونة لانهنّ رفضنّ الايمان الاول"));
arasuperobject.biblemap.put("b54_5_13", new arasuperobject(54,5,13,"ومع ذلك ايضا يتعلمن ان يكنّ بطالات يطفن في البيوت ولسن بطالات فقط بل مهذارات ايضا وفضوليات يتكلمن بما لا يجب"));
arasuperobject.biblemap.put("b54_5_14", new arasuperobject(54,5,14,"فاريد ان الحدثات يتزوجن ويلدن الاولاد ويدبّرن البيوت ولا يعطين علة للمقاوم من اجل الشتم"));
arasuperobject.biblemap.put("b54_5_15", new arasuperobject(54,5,15,"فان بعضهنّ قد انحرفن وراء الشيطان"));
arasuperobject.biblemap.put("b54_5_16", new arasuperobject(54,5,16,"ان كان لمؤمن او مؤمنة ارامل فليساعدهنّ ولا يثقّل على الكنيسة لكي تساعد هي اللواتي هنّ بالحقيقة ارامل"));
arasuperobject.biblemap.put("b54_5_17", new arasuperobject(54,5,17,"اما الشيوخ المدبرون حسنا فليحسبوا اهلا لكرامة مضاعفة ولا سيما الذين يتعبون في الكلمة والتعليم"));
arasuperobject.biblemap.put("b54_5_18", new arasuperobject(54,5,18,"لان الكتاب يقول لا تكمّ ثورا دارسا. والفاعل مستحق اجرته"));
arasuperobject.biblemap.put("b54_5_19", new arasuperobject(54,5,19,"لا تقبل شكاية على شيخ الا على شاهدين او ثلاثة شهود"));
arasuperobject.biblemap.put("b54_5_20", new arasuperobject(54,5,20,"الذين يخطئون وبّخهم امام الجميع لكي يكون عند الباقين خوف"));
arasuperobject.biblemap.put("b54_5_21", new arasuperobject(54,5,21,"اناشدك امام الله والرب يسوع المسيح والملائكة المختارين ان تحفظ هذا بدون غرض ولا تعمل شيئا بمحاباة"));
arasuperobject.biblemap.put("b54_5_22", new arasuperobject(54,5,22,"لا تضع يدا على احد بالعجلة ولا تشترك في خطايا الآخرين. احفظ نفسك طاهرا"));
arasuperobject.biblemap.put("b54_5_23", new arasuperobject(54,5,23,"لا تكن في ما بعد شراب ماء بل استعمل خمرا قليلا من اجل معدتك واسقامك الكثيرة"));
arasuperobject.biblemap.put("b54_5_24", new arasuperobject(54,5,24,"خطايا بعض الناس واضحة تتقدم الى القضاء. واما البعض فتتبعهم"));
arasuperobject.biblemap.put("b54_5_25", new arasuperobject(54,5,25,"كذلك ايضا الاعمال الصالحة واضحة والتي هي خلاف ذلك لا يمكن ان تخفى"));
arasuperobject.biblemap.put("b54_6_1", new arasuperobject(54,6,1,"جميع الذين هم عبيد تحت نير فليحسبوا سادتهم مستحقين كل اكرام لئلا يفترى على اسم الله وتعليمه"));
arasuperobject.biblemap.put("b54_6_2", new arasuperobject(54,6,2,"والذين لهم سادة مؤمنون لا يستهينوا بهم لانهم اخوة بل ليخدموهم اكثر لان الذين يتشاركون في الفائدة هم مؤمنون ومحبوبون. علّم وعظ بهذا"));
arasuperobject.biblemap.put("b54_6_3", new arasuperobject(54,6,3,"ان كان احد يعلّم تعليما آخر ولا يوافق كلمات ربنا يسوع المسيح الصحيحة والتعليم الذي هو حسب التقوى"));
arasuperobject.biblemap.put("b54_6_4", new arasuperobject(54,6,4,"فقد تصلّف وهو لا يفهم شيئا بل هو متعلل بمباحثات ومماحكات الكلام التي منها يحصل الحسد والخصام والافتراء والظنون الرديّة"));
arasuperobject.biblemap.put("b54_6_5", new arasuperobject(54,6,5,"ومنازعات اناس فاسدي الذهن وعادمي الحق يظنون ان التقوى تجارة. تجنب مثل هؤلاء"));
arasuperobject.biblemap.put("b54_6_6", new arasuperobject(54,6,6,"واما التقوى مع القناعة فهي تجارة عظيمة"));
arasuperobject.biblemap.put("b54_6_7", new arasuperobject(54,6,7,"لاننا لم ندخل العالم بشيء وواضح اننا لا نقدر ان نخرج منه بشيء"));
arasuperobject.biblemap.put("b54_6_8", new arasuperobject(54,6,8,"فان كان لنا قوت وكسوة فلنكتف بهما"));
arasuperobject.biblemap.put("b54_6_9", new arasuperobject(54,6,9,"واما الذين يريدون ان يكونوا اغنياء فيسقطون في تجربة وفخ وشهوات كثيرة غبية ومضرة تغرق الناس في العطب والهلاك"));
arasuperobject.biblemap.put("b54_6_10", new arasuperobject(54,6,10,"لان محبة المال اصل لكل الشرور الذي اذ ابتغاه قوم ضلّوا عن الايمان وطعنوا انفسهم باوجاع كثيرة"));
arasuperobject.biblemap.put("b54_6_11", new arasuperobject(54,6,11,"واما انت يا انسان الله فاهرب من هذا واتبع البر والتقوى والايمان والمحبة والصبر والوداعة"));
arasuperobject.biblemap.put("b54_6_12", new arasuperobject(54,6,12,"جاهد جهاد الايمان الحسن وامسك بالحياة الابدية التي اليها دعيت ايضا واعترفت الاعتراف الحسن امام شهود كثيرين"));
arasuperobject.biblemap.put("b54_6_13", new arasuperobject(54,6,13,"اوصيك امام الله الذي يحيي الكل والمسيح يسوع الذي شهد لدى بيلاطس البنطي بالاعتراف الحسن"));
arasuperobject.biblemap.put("b54_6_14", new arasuperobject(54,6,14,"ان تحفظ الوصية بلا دنس ولا لوم الى ظهور ربنا يسوع المسيح"));
arasuperobject.biblemap.put("b54_6_15", new arasuperobject(54,6,15,"الذي سيبيّنه في اوقاته المبارك العزيز الوحيد ملك الملوك ورب الارباب"));
arasuperobject.biblemap.put("b54_6_16", new arasuperobject(54,6,16,"الذي وحده له عدم الموت ساكنا في نور لا يدنى منه الذي لم يره احد من الناس ولا يقدر ان يراه الذي له الكرامة والقدرة الابدية. آمين"));
arasuperobject.biblemap.put("b54_6_17", new arasuperobject(54,6,17,"اوص الاغنياء في الدهر الحاضر ان لا يستكبروا ولا يلقوا رجاءهم على غير يقينية الغنى بل على الله الحي الذي يمنحنا كل شيء بغنى للتمتع"));
arasuperobject.biblemap.put("b54_6_18", new arasuperobject(54,6,18,"وان يصنعوا صلاحا وان يكونوا اغنياء في اعمال صالحة وان يكونوا اسخياء في العطاء كرماء في التوزيع"));
arasuperobject.biblemap.put("b54_6_19", new arasuperobject(54,6,19,"مدّخرين لانفسهم اساسا حسنا للمستقبل لكي يمسكوا بالحياة الابدية"));
arasuperobject.biblemap.put("b54_6_20", new arasuperobject(54,6,20,"يا تيموثاوس احفظ الوديعة معرضا عن الكلام الباطل الدنس ومخالفات العلم الكاذب الاسم"));
arasuperobject.biblemap.put("b54_6_21", new arasuperobject(54,6,21,"الذي اذ تظاهر به قوم زاغوا من جهة الايمان. ( [ (I Timothy 6:22) ) النعمة معك. آمين ]"));

