private void putmapbible55() {

arasuperobject.biblemap.put("b55_1_1", new arasuperobject(55,1,1,"بولس رسول يسوع المسيح بمشيئة الله لاجل وعد الحياة التي في يسوع المسيح"));
arasuperobject.biblemap.put("b55_1_2", new arasuperobject(55,1,2,"الى تيموثاوس الابن الحبيب. نعمة ورحمة وسلام من الله الآب والمسيح يسوع ربنا"));
arasuperobject.biblemap.put("b55_1_3", new arasuperobject(55,1,3,"اني اشكر الله الذي اعبده من اجدادي بضمير طاهر كما اذكرك بلا انقطاع في طلباتي ليلا ونهارا"));
arasuperobject.biblemap.put("b55_1_4", new arasuperobject(55,1,4,"مشتاقا ان اراك ذاكرا دموعك لكي امتلئ فرحا"));
arasuperobject.biblemap.put("b55_1_5", new arasuperobject(55,1,5,"اذ اتذكر الايمان العديم الرياء الذي فيك الذي سكن اولا في جدتك لوئيس وامك افنيكي ولكني موقن انه فيك ايضا"));
arasuperobject.biblemap.put("b55_1_6", new arasuperobject(55,1,6,"فلهذا السبب اذكّرك ان تضرم ايضا موهبة الله التي فيك بوضع يديّ"));
arasuperobject.biblemap.put("b55_1_7", new arasuperobject(55,1,7,"لان الله لم يعطنا روح الفشل بل روح القوة والمحبة والنصح"));
arasuperobject.biblemap.put("b55_1_8", new arasuperobject(55,1,8,"فلا تخجل بشهادة ربنا ولا بي انا اسيره بل اشترك في احتمال المشقات لاجل الانجيل بحسب قوة الله"));
arasuperobject.biblemap.put("b55_1_9", new arasuperobject(55,1,9,"الذي خلّصنا ودعانا دعوة مقدسة لا بمقتضى اعمالنا بل بمقتضى القصد والنعمة التي أعطيت لنا في المسيح يسوع قبل الازمنة الازلية"));
arasuperobject.biblemap.put("b55_1_10", new arasuperobject(55,1,10,"وانما أظهرت الآن بظهور مخلّصنا يسوع المسيح الذي ابطل الموت وانار الحياة والخلود بواسطة الانجيل"));
arasuperobject.biblemap.put("b55_1_11", new arasuperobject(55,1,11,"الذي جعلت انا له كارزا ورسولا ومعلّما للامم"));
arasuperobject.biblemap.put("b55_1_12", new arasuperobject(55,1,12,"لهذا السبب احتمل هذه الامور ايضا لكنني لست اخجل لانني عالم بمن آمنت وموقن انه قادر ان يحفظ وديعتي الى ذلك اليوم"));
arasuperobject.biblemap.put("b55_1_13", new arasuperobject(55,1,13,"تمسك بصورة الكلام الصحيح الذي سمعته مني في الايمان والمحبة التي في المسيح يسوع"));
arasuperobject.biblemap.put("b55_1_14", new arasuperobject(55,1,14,"احفظ الوديعة الصالحة بالروح القدس الساكن فينا"));
arasuperobject.biblemap.put("b55_1_15", new arasuperobject(55,1,15,"انت تعلم هذا ان جميع الذين في اسيا ارتدّوا عني الذين منهم فيجلّس وهرموجانس"));
arasuperobject.biblemap.put("b55_1_16", new arasuperobject(55,1,16,"ليعط الرب رحمة لبيت انيسيفورس لانه مرارا كثيرة اراحني ولم يخجل بسلسلتي"));
arasuperobject.biblemap.put("b55_1_17", new arasuperobject(55,1,17,"بل لما كان في رومية طلبني باوفر اجتهاد فوجدني"));
arasuperobject.biblemap.put("b55_1_18", new arasuperobject(55,1,18,"ليعطه الرب ان يجد رحمة من الرب في ذلك اليوم. وكل ما كان يخدم في افسس انت تعرفه جيدا"));
arasuperobject.biblemap.put("b55_2_1", new arasuperobject(55,2,1,"فتقوّ انت يا ابني بالنعمة التي في المسيح يسوع"));
arasuperobject.biblemap.put("b55_2_2", new arasuperobject(55,2,2,"وما سمعته مني بشهود كثيرين اودعه اناسا امناء يكونون اكفاء ان يعلّموا آخرين ايضا"));
arasuperobject.biblemap.put("b55_2_3", new arasuperobject(55,2,3,"فاشترك انت في احتمال المشقات كجندي صالح ليسوع المسيح"));
arasuperobject.biblemap.put("b55_2_4", new arasuperobject(55,2,4,"ليس احد وهو يتجند يرتبك باعمال الحياة لكي يرضي من جنّده"));
arasuperobject.biblemap.put("b55_2_5", new arasuperobject(55,2,5,"وايضا ان كان احد يجاهد لا يكلّل ان لم يجاهد قانونيا"));
arasuperobject.biblemap.put("b55_2_6", new arasuperobject(55,2,6,"يجب ان الحرّاث الذي يتعب يشترك هو اولا في الاثمار"));
arasuperobject.biblemap.put("b55_2_7", new arasuperobject(55,2,7,"افهم ما اقول. فليعطك الرب فهما في كل شيء"));
arasuperobject.biblemap.put("b55_2_8", new arasuperobject(55,2,8,"اذكر يسوع المسيح المقام من الاموات من نسل داود بحسب انجيلي"));
arasuperobject.biblemap.put("b55_2_9", new arasuperobject(55,2,9,"الذي فيه احتمل المشقات حتى القيود كمذنب. لكن كلمة الله لا تقيّد"));
arasuperobject.biblemap.put("b55_2_10", new arasuperobject(55,2,10,"لاجل ذلك انا اصبر على كل شيء لاجل المختارين لكي يحصلوا هم ايضا على الخلاص الذي في المسيح يسوع مع مجد ابدي"));
arasuperobject.biblemap.put("b55_2_11", new arasuperobject(55,2,11,"صادقة هي الكلمة انه ان كنا قد متنا معه فسنحيا ايضا معه"));
arasuperobject.biblemap.put("b55_2_12", new arasuperobject(55,2,12,"ان كنا نصبر فسنملك ايضا معه. ان كنا ننكره فهو ايضا سينكرنا"));
arasuperobject.biblemap.put("b55_2_13", new arasuperobject(55,2,13,"ان كنا غير امناء فهو يبقى امينا لن يقدر ان ينكر نفسه"));
arasuperobject.biblemap.put("b55_2_14", new arasuperobject(55,2,14,"فكّر بهذه الامور مناشدا قدام الرب ان لا يتماحكوا بالكلام. الامر غير النافع لشيء. لهدم السامعين"));
arasuperobject.biblemap.put("b55_2_15", new arasuperobject(55,2,15,"اجتهد ان تقيم نفسك للّه مزكّى عاملا لا يخزى مفصّلا كلمة الحق بالاستقامة"));
arasuperobject.biblemap.put("b55_2_16", new arasuperobject(55,2,16,"واما الاقوال الباطلة الدنسة فاجتنبها لانهم يتقدمون الى اكثر فجور"));
arasuperobject.biblemap.put("b55_2_17", new arasuperobject(55,2,17,"وكلمتهم ترعى كآكلة. الذين منهم هيمينايس وفيليتس"));
arasuperobject.biblemap.put("b55_2_18", new arasuperobject(55,2,18,"اللذان زاغا عن الحق قائلين ان القيامة قد صارت فيقلبان ايمان قوم"));
arasuperobject.biblemap.put("b55_2_19", new arasuperobject(55,2,19,"ولكن اساس الله الراسخ قد ثبت اذ له هذا الختم. يعلم الرب الذين هم له. وليتجنب الاثم كل من يسمّي اسم المسيح"));
arasuperobject.biblemap.put("b55_2_20", new arasuperobject(55,2,20,"ولكن في بيت كبير ليس آنية من ذهب وفضة فقط بل من خشب وخزف ايضا وتلك للكرامة وهذه للهوان"));
arasuperobject.biblemap.put("b55_2_21", new arasuperobject(55,2,21,"فان طهّر احد نفسه من هذه يكون اناء للكرامة مقدسا نافعا للسيد مستعدا لكل عمل صالح"));
arasuperobject.biblemap.put("b55_2_22", new arasuperobject(55,2,22,"اما الشهوات الشبابية فاهرب منها واتبع البر والايمان والمحبة والسلام مع الذين يدعون الرب من قلب نقي"));
arasuperobject.biblemap.put("b55_2_23", new arasuperobject(55,2,23,"والمباحثات الغبية والسخيفة اجتنبها عالما انها تولّد خصومات"));
arasuperobject.biblemap.put("b55_2_24", new arasuperobject(55,2,24,"وعبد الرب لا يجب ان يخاصم بل يكون مترفقا بالجميع صالحا للتعليم صبورا على المشقات"));
arasuperobject.biblemap.put("b55_2_25", new arasuperobject(55,2,25,"مؤدبا بالوداعة المقاومين عسى ان يعطيهم الله توبة لمعرفة الحق"));
arasuperobject.biblemap.put("b55_2_26", new arasuperobject(55,2,26,"فيستفيقوا من فخ ابليس اذ قد اقتنصهم لارادته"));
arasuperobject.biblemap.put("b55_3_1", new arasuperobject(55,3,1,"ولكن اعلم هذا انه في الايام الاخيرة ستأتي ازمنة صعبة"));
arasuperobject.biblemap.put("b55_3_2", new arasuperobject(55,3,2,"لان الناس يكونون محبين لانفسهم محبين للمال متعظمين مستكبرين مجدّفين غير طائعين لوالديهم غير شاكرين دنسين"));
arasuperobject.biblemap.put("b55_3_3", new arasuperobject(55,3,3,"بلا حنو بلا رضى ثالبين عديمي النزاهة شرسين غير محبين للصلاح"));
arasuperobject.biblemap.put("b55_3_4", new arasuperobject(55,3,4,"خائنين مقتحمين متصلفين محبين للذات دون محبة لله"));
arasuperobject.biblemap.put("b55_3_5", new arasuperobject(55,3,5,"لهم صورة التقوى ولكنهم منكرون قوّتها. فاعرض عن هؤلاء"));
arasuperobject.biblemap.put("b55_3_6", new arasuperobject(55,3,6,"فانه من هؤلاء هم الذين يدخلون البيوت ويسبون نسيّات محمّلات خطايا منساقات بشهوات مختلفة"));
arasuperobject.biblemap.put("b55_3_7", new arasuperobject(55,3,7,"يتعلمن في كل حين ولا يستطعن ان يقبلن الى معرفة الحق ابدا"));
arasuperobject.biblemap.put("b55_3_8", new arasuperobject(55,3,8,"وكما قاوم ينّيس ويمبريس موسى كذلك هؤلاء ايضا يقاومون الحق. اناس فاسدة اذهانهم ومن جهة الايمان مرفوضون"));
arasuperobject.biblemap.put("b55_3_9", new arasuperobject(55,3,9,"لكنهم لا يتقدمون اكثر لان حمقهم سيكون واضحا للجميع كما كان حمق ذينك ايضا"));
arasuperobject.biblemap.put("b55_3_10", new arasuperobject(55,3,10,"واما انت فقد تبعت تعليمي وسيرتي وقصدي وايماني واناتي ومحبتي وصبري"));
arasuperobject.biblemap.put("b55_3_11", new arasuperobject(55,3,11,"واضطهاداتي وآلامي مثل ما اصابني في انطاكية وايقونية ولسترة. ايّة اضطهادات احتملت. ومن الجميع انقذني الرب"));
arasuperobject.biblemap.put("b55_3_12", new arasuperobject(55,3,12,"وجميع الذين يريدون ان يعيشوا بالتقوى في المسيح يسوع يضطهدون"));
arasuperobject.biblemap.put("b55_3_13", new arasuperobject(55,3,13,"ولكن الناس الاشرار المزوّرين سيتقدمون الى اردأ مضلّين ومضلّين"));
arasuperobject.biblemap.put("b55_3_14", new arasuperobject(55,3,14,"واما انت فاثبت على ما تعلمت وايقنت عارفا ممن تعلمت"));
arasuperobject.biblemap.put("b55_3_15", new arasuperobject(55,3,15,"وانك منذ الطفولية تعرف الكتب المقدسة القادرة ان تحكّمك للخلاص بالايمان الذي في المسيح يسوع"));
arasuperobject.biblemap.put("b55_3_16", new arasuperobject(55,3,16,"كل الكتاب هو موحى به من الله ونافع للتعليم والتوبيخ للتقويم والتأديب الذي في البر"));
arasuperobject.biblemap.put("b55_3_17", new arasuperobject(55,3,17,"لكي يكون انسان الله كاملا متأهبا لكل عمل صالح"));
arasuperobject.biblemap.put("b55_4_1", new arasuperobject(55,4,1,"انا اناشدك اذا امام الله والرب يسوع المسيح العتيد ان يدين الاحياء والاموات عند ظهوره وملكوته"));
arasuperobject.biblemap.put("b55_4_2", new arasuperobject(55,4,2,"اكرز بالكلمة اعكف على ذلك في وقت مناسب وغير مناسب. وبّخ انتهر عظ بكل اناة وتعليم"));
arasuperobject.biblemap.put("b55_4_3", new arasuperobject(55,4,3,"لانه سيكون وقت لا يحتملون فيه التعليم الصحيح بل حسب شهواتهم الخاصة يجمعون لهم معلّمين مستحكة مسامعهم"));
arasuperobject.biblemap.put("b55_4_4", new arasuperobject(55,4,4,"فيصرفون مسامعهم عن الحق وينحرفون الى الخرافات"));
arasuperobject.biblemap.put("b55_4_5", new arasuperobject(55,4,5,"واما انت فاصح في كل شيء. احتمل المشقات. اعمل عمل المبشر. تمم خدمتك"));
arasuperobject.biblemap.put("b55_4_6", new arasuperobject(55,4,6,"فاني انا الآن اسكب سكيبا ووقت انحلالي قد حضر"));
arasuperobject.biblemap.put("b55_4_7", new arasuperobject(55,4,7,"قد جاهدت الجهاد الحسن اكملت السعي حفظت الايمان"));
arasuperobject.biblemap.put("b55_4_8", new arasuperobject(55,4,8,"واخيرا قد وضع لي اكليل البر الذي يهبه لي في ذلك اليوم الرب الديان العادل وليس لي فقط بل لجميع الذين يحبون ظهوره ايضا"));
arasuperobject.biblemap.put("b55_4_9", new arasuperobject(55,4,9,"بادر ان تجيء اليّ سريعا"));
arasuperobject.biblemap.put("b55_4_10", new arasuperobject(55,4,10,"لان ديماس قد تركني اذ احب العالم الحاضر وذهب الى تسالونيكي وكريسكيس الى غلاطية وتيطس الى دلماطية"));
arasuperobject.biblemap.put("b55_4_11", new arasuperobject(55,4,11,"لوقا وحده معي. خذ مرقس واحضره معك لانه نافع لي للخدمة"));
arasuperobject.biblemap.put("b55_4_12", new arasuperobject(55,4,12,"اما تيخيكس فقد ارسلته الى افسس"));
arasuperobject.biblemap.put("b55_4_13", new arasuperobject(55,4,13,"الرداء الذي تركته في ترواس عند كاربس احضره متى جئت والكتب ايضا ولا سيما الرقوق"));
arasuperobject.biblemap.put("b55_4_14", new arasuperobject(55,4,14,"اسكندر النحّاس اظهر لي شرورا كثيرة. ليجازه الرب حسب اعماله"));
arasuperobject.biblemap.put("b55_4_15", new arasuperobject(55,4,15,"فاحتفظ منه انت ايضا لانه قاوم اقوالنا جدا"));
arasuperobject.biblemap.put("b55_4_16", new arasuperobject(55,4,16,"في احتجاجي الاول لم يحضر احد معي بل الجميع تركوني. لا يحسب عليهم"));
arasuperobject.biblemap.put("b55_4_17", new arasuperobject(55,4,17,"ولكن الرب وقف معي وقواني لكي تتم بي الكرازة ويسمع جميع الامم فأنقذت من فم الاسد"));
arasuperobject.biblemap.put("b55_4_18", new arasuperobject(55,4,18,"وسينقذني الرب من كل عمل رديء ويخلّصني لملكوته السماوي. الذي له المجد الى دهر الدهور. آمين"));
arasuperobject.biblemap.put("b55_4_19", new arasuperobject(55,4,19,"سلم على فرسكا واكيلا وبيت انيسيفورس"));
arasuperobject.biblemap.put("b55_4_20", new arasuperobject(55,4,20,"اراستس بقي في كورنثوس. واما تروفيمس فتركته في ميليتس مريضا"));
arasuperobject.biblemap.put("b55_4_21", new arasuperobject(55,4,21,"بادر ان تجيء قبل الشتاء. يسلم عليك افبولس وبوديس ولينس وكلافدية والاخوة جميعا"));
arasuperobject.biblemap.put("b55_4_22", new arasuperobject(55,4,22,"الرب يسوع المسيح مع روحك. النعمة معكم. آمين"));

}

