arasuperobject.biblemap.put("b59_1_1", new arasuperobject(59,1,1,"يعقوب عبد الله والرب يسوع المسيح يهدي السلام الى الاثني عشر سبطا الذين في الشتات"));
arasuperobject.biblemap.put("b59_1_2", new arasuperobject(59,1,2,"احسبوه كل فرح يا اخوتي حينما تقعون في تجارب متنوعة"));
arasuperobject.biblemap.put("b59_1_3", new arasuperobject(59,1,3,"عالمين ان امتحان ايمانكم ينشئ صبرا"));
arasuperobject.biblemap.put("b59_1_4", new arasuperobject(59,1,4,"واما الصبر فليكن له عمل تام لكي تكونوا تامّين وكاملين غير ناقصين في شيء"));
arasuperobject.biblemap.put("b59_1_5", new arasuperobject(59,1,5,"وانما ان كان احد تعوزه حكمة فليطلب من الله الذي يعطي الجميع بسخاء ولا يعيّر فسيعطى له"));
arasuperobject.biblemap.put("b59_1_6", new arasuperobject(59,1,6,"ولكن ليطلب بايمان غير مرتاب البتة لان المرتاب يشبه موجا من البحر تخبطه الريح وتدفعه"));
arasuperobject.biblemap.put("b59_1_7", new arasuperobject(59,1,7,"فلا يظن ذلك الانسان انه ينال شيئا من عند الرب"));
arasuperobject.biblemap.put("b59_1_8", new arasuperobject(59,1,8,"رجل ذو رايين هو متقلقل في جميع طرقه"));
arasuperobject.biblemap.put("b59_1_9", new arasuperobject(59,1,9,"وليفتخر الاخ المتضع بارتفاعه"));
arasuperobject.biblemap.put("b59_1_10", new arasuperobject(59,1,10,"واما الغني فباتضاعه لانه كزهر العشب يزول"));
arasuperobject.biblemap.put("b59_1_11", new arasuperobject(59,1,11,"لان الشمس اشرقت بالحرّ فيبّست العشب فسقط زهره وفني جمال منظره. هكذا يذبل الغني ايضا في طرقه"));
arasuperobject.biblemap.put("b59_1_12", new arasuperobject(59,1,12,"طوبى للرجل الذي يحتمل التجربة. لانه اذا تزكى ينال اكليل الحياة الذي وعد به الرب للذين يحبونه"));
arasuperobject.biblemap.put("b59_1_13", new arasuperobject(59,1,13,"لا يقل احد اذا جرّب اني أجرّب من قبل الله. لان الله غير مجرّب بالشرور وهو لا يجرّب احدا"));
arasuperobject.biblemap.put("b59_1_14", new arasuperobject(59,1,14,"ولكن كل واحد يجرّب اذا انجذب وانخدع من شهوته"));
arasuperobject.biblemap.put("b59_1_15", new arasuperobject(59,1,15,"ثم الشهوة اذا حبلت تلد خطية والخطية اذا كملت تنتج موتا"));
arasuperobject.biblemap.put("b59_1_16", new arasuperobject(59,1,16,"لا تضلّوا يا اخوتي الاحباء"));
arasuperobject.biblemap.put("b59_1_17", new arasuperobject(59,1,17,"كل عطية صالحة وكل موهبة تامة هي من فوق نازلة من عند ابي الانوار الذي ليس عنده تغيير ولا ظل دوران"));
arasuperobject.biblemap.put("b59_1_18", new arasuperobject(59,1,18,"شاء فولدنا بكلمة الحق لكي نكون باكورة من خلائقه"));
arasuperobject.biblemap.put("b59_1_19", new arasuperobject(59,1,19,"اذا يا اخوتي الاحباء ليكن كل انسان مسرعا في الاستماع مبطئا في التكلم مبطئا في الغضب"));
arasuperobject.biblemap.put("b59_1_20", new arasuperobject(59,1,20,"لان غضب الانسان لا يصنع بر الله"));
arasuperobject.biblemap.put("b59_1_21", new arasuperobject(59,1,21,"لذلك اطرحوا كل نجاسة وكثرة شر فاقبلوا بوداعة الكلمة المغروسة القادرة ان تخلّص نفوسكم"));
arasuperobject.biblemap.put("b59_1_22", new arasuperobject(59,1,22,"ولكن كونوا عاملين بالكلمة لا سامعين فقط خادعين نفوسكم"));
arasuperobject.biblemap.put("b59_1_23", new arasuperobject(59,1,23,"لانه ان كان احد سامعا للكلمة وليس عاملا فذاك يشبه رجلا ناظرا وجه خلقته في مرآة"));
arasuperobject.biblemap.put("b59_1_24", new arasuperobject(59,1,24,"فانه نظر ذاته ومضى وللوقت نسي ما هو"));
arasuperobject.biblemap.put("b59_1_25", new arasuperobject(59,1,25,"ولكن من اطّلع على الناموس الكامل ناموس الحرية وثبت وصار ليس سامعا ناسيا بل عاملا بالكلمة فهذا يكون مغبوطا في عمله"));
arasuperobject.biblemap.put("b59_1_26", new arasuperobject(59,1,26,"ان كان احد فيكم يظن انه ديّن وهو ليس يلجم لسانه بل يخدع قلبه فديانة هذا باطلة"));
arasuperobject.biblemap.put("b59_1_27", new arasuperobject(59,1,27,"الديانة الطاهرة النقية عند الله الآب هي هذه افتقاد اليتامى والارامل في ضيقتهم وحفظ الانسان نفسه بلا دنس من العالم"));
arasuperobject.biblemap.put("b59_2_1", new arasuperobject(59,2,1,"يا اخوتي لا يكن لكم ايمان ربنا يسوع المسيح رب المجد في المحاباة"));
arasuperobject.biblemap.put("b59_2_2", new arasuperobject(59,2,2,"فانه ان دخل الى مجمعكم رجل بخواتم ذهب في لباس بهي ودخل ايضا فقير بلباس وسخ"));
arasuperobject.biblemap.put("b59_2_3", new arasuperobject(59,2,3,"فنظرتم الى اللابس اللباس البهي وقلتم له اجلس انت هنا حسنا وقلتم للفقير قف انت هناك او اجلس هنا تحت موطئ قدميّ"));
arasuperobject.biblemap.put("b59_2_4", new arasuperobject(59,2,4,"فهل لا ترتابون في انفسكم وتصيرون قضاة افكار شريرة"));
arasuperobject.biblemap.put("b59_2_5", new arasuperobject(59,2,5,"اسمعوا يا اخوتي الاحباء أما اختار الله فقراء هذا العالم اغنياء في الايمان وورثة الملكوت الذي وعد به الذين يحبونه"));
arasuperobject.biblemap.put("b59_2_6", new arasuperobject(59,2,6,"واما انتم فاهنتم الفقير. أليس الاغنياء يتسلطون عليكم وهم يجرّونكم الى المحاكم"));
arasuperobject.biblemap.put("b59_2_7", new arasuperobject(59,2,7,"أما هم يجدّفون على الاسم الحسن الذي دعي به عليكم"));
arasuperobject.biblemap.put("b59_2_8", new arasuperobject(59,2,8,"فان كنتم تكملون الناموس الملوكي حسب الكتاب. تحب قريبك كنفسك. فحسنا تفعلون"));
arasuperobject.biblemap.put("b59_2_9", new arasuperobject(59,2,9,"ولكن ان كنتم تحابون تفعلون خطية موبّخين من الناموس كمتعدّين"));
arasuperobject.biblemap.put("b59_2_10", new arasuperobject(59,2,10,"لان من حفظ كل الناموس وانما عثر في واحدة فقد صار مجرما في الكل"));
arasuperobject.biblemap.put("b59_2_11", new arasuperobject(59,2,11,"لان الذي قال لا تزن قال ايضا لا تقتل. فان لم تزن ولكن قتلت فقد صرت متعديا الناموس"));
arasuperobject.biblemap.put("b59_2_12", new arasuperobject(59,2,12,"هكذا تكلموا وهكذا افعلوا كعتيدين ان تحاكموا بناموس الحرية"));
arasuperobject.biblemap.put("b59_2_13", new arasuperobject(59,2,13,"لان الحكم هو بلا رحمة لمن لم يعمل رحمة. والرحمة تفتخر على الحكم"));
arasuperobject.biblemap.put("b59_2_14", new arasuperobject(59,2,14,"ما المنفعة يا اخوتي ان قال احد ان له ايمانا ولكن ليس له اعمال. هل يقدر الايمان ان يخلّصه"));
arasuperobject.biblemap.put("b59_2_15", new arasuperobject(59,2,15,"ان كان اخ واخت عريانين ومعتازين للقوت اليومي"));
arasuperobject.biblemap.put("b59_2_16", new arasuperobject(59,2,16,"فقال لهما احدكم امضيا بسلام استدفيا واشبعا ولكن لم تعطوهما حاجات الجسد فما المنفعة ‎"));
arasuperobject.biblemap.put("b59_2_17", new arasuperobject(59,2,17,"هكذا الايمان ايضا ان لم يكن له اعمال ميت في ذاته"));
arasuperobject.biblemap.put("b59_2_18", new arasuperobject(59,2,18,"لكن يقول قائل انت لك ايمان وانا لي اعمال. أرني ايمانك بدون اعمالك وانا اريك باعمالي ايماني"));
arasuperobject.biblemap.put("b59_2_19", new arasuperobject(59,2,19,"انت تؤمن ان الله واحد. حسنا تفعل. والشياطين يؤمنون ويقشعرون"));
arasuperobject.biblemap.put("b59_2_20", new arasuperobject(59,2,20,"ولكن هل تريد ان تعلم ايها الانسان الباطل ان الايمان بدون اعمال ميت"));
arasuperobject.biblemap.put("b59_2_21", new arasuperobject(59,2,21,"ألم يتبرر ابراهيم ابونا بالاعمال اذ قدم اسحق ابنه على المذبح"));
arasuperobject.biblemap.put("b59_2_22", new arasuperobject(59,2,22,"فترى ان الايمان عمل مع اعماله وبالاعمال اكمل الايمان"));
arasuperobject.biblemap.put("b59_2_23", new arasuperobject(59,2,23,"وتم الكتاب القائل فآمن ابراهيم بالله فحسب له برا ودعي خليل الله"));
arasuperobject.biblemap.put("b59_2_24", new arasuperobject(59,2,24,"ترون اذا انه بالاعمال يتبرر الانسان لا بالايمان وحده"));
arasuperobject.biblemap.put("b59_2_25", new arasuperobject(59,2,25,"كذلك راحاب الزانية ايضا أما تبررت بالاعمال اذ قبلت الرسل واخرجتهم في طريق آخر"));
arasuperobject.biblemap.put("b59_2_26", new arasuperobject(59,2,26,"لانه كما ان الجسد بدون روح ميت هكذا الايمان ايضا بدون اعمال ميت"));
arasuperobject.biblemap.put("b59_3_1", new arasuperobject(59,3,1,"لا تكونوا معلّمين كثيرين يا اخوتي عالمين اننا نأخذ دينونة اعظم"));
arasuperobject.biblemap.put("b59_3_2", new arasuperobject(59,3,2,"لاننا في اشياء كثيرة نعثر جميعنا. ان كان احد لا يعثر في الكلام فذاك رجل كامل قادر ان يلجم كل الجسد ايضا"));
arasuperobject.biblemap.put("b59_3_3", new arasuperobject(59,3,3,"هوذا الخيل نضع اللجم في افواهها لكي تطاوعنا فندير جسمها كله"));
arasuperobject.biblemap.put("b59_3_4", new arasuperobject(59,3,4,"هوذا السفن ايضا وهي عظيمة بهذا المقدار وتسوقها رياح عاصفة تديرها دفة صغيرة جدا الى حيثما شاء قصد المدير"));
arasuperobject.biblemap.put("b59_3_5", new arasuperobject(59,3,5,"هكذا اللسان ايضا هو عضو صغير ويفتخر متعظما. هوذا نار قليلة اي وقود تحرق"));
arasuperobject.biblemap.put("b59_3_6", new arasuperobject(59,3,6,"فاللسان نار. عالم الاثم. هكذا جعل في اعضائنا اللسان الذي يدنس الجسم كله ويضرم دائرة الكون ويضرم من جهنم"));
arasuperobject.biblemap.put("b59_3_7", new arasuperobject(59,3,7,"لان كل طبع للوحوش والطيور والزحافات والبحريات يذلل وقد تذلل للطبع البشري"));
arasuperobject.biblemap.put("b59_3_8", new arasuperobject(59,3,8,"واما اللسان فلا يستطيع احد من الناس ان يذلله. هو شر لا يضبط مملوء سمّا مميتا"));
arasuperobject.biblemap.put("b59_3_9", new arasuperobject(59,3,9,"به نبارك الله الآب وبه نلعن الناس الذين قد تكوّنوا على شبه الله"));
arasuperobject.biblemap.put("b59_3_10", new arasuperobject(59,3,10,"من الفم الواحد تخرج بركة ولعنة. لا يصلح يا اخوتي ان تكون هذه الامور هكذا"));
arasuperobject.biblemap.put("b59_3_11", new arasuperobject(59,3,11,"ألعل ينبوعا ينبع من نفس عين واحدة العذب والمر"));
arasuperobject.biblemap.put("b59_3_12", new arasuperobject(59,3,12,"هل تقدر يا اخوتي تينة ان تصنع زيتونا او كرمة تينا. ولا كذلك ينبوع يصنع ماء مالحا وعذبا"));
arasuperobject.biblemap.put("b59_3_13", new arasuperobject(59,3,13,"من هو حكيم وعالم بينكم فلير اعماله بالتصرف الحسن في وداعة الحكمة"));
arasuperobject.biblemap.put("b59_3_14", new arasuperobject(59,3,14,"ولكن ان كان لكم غيرة مرة وتحزب في قلوبكم فلا تفتخروا وتكذبوا على الحق"));
arasuperobject.biblemap.put("b59_3_15", new arasuperobject(59,3,15,"ليست هذه الحكمة نازلة من فوق بل هي ارضية نفسانية شيطانية"));
arasuperobject.biblemap.put("b59_3_16", new arasuperobject(59,3,16,"لانه حيث الغيرة والتحزب هناك التشويش وكل امر رديء"));
arasuperobject.biblemap.put("b59_3_17", new arasuperobject(59,3,17,"واما الحكمة التي من فوق فهي اولا طاهرة ثم مسالمة مترفقة مذعنة مملوءة رحمة واثمارا صالحة عديمة الريب والرياء"));
arasuperobject.biblemap.put("b59_3_18", new arasuperobject(59,3,18,"وثمر البر يزرع في السلام من الذين يفعلون السلام"));
arasuperobject.biblemap.put("b59_4_1", new arasuperobject(59,4,1,"من اين الحروب والخصومات بينكم أليست من هنا من لذّاتكم المحاربة في اعضائكم"));
arasuperobject.biblemap.put("b59_4_2", new arasuperobject(59,4,2,"تشتهون ولستم تمتلكون. تقتلون وتحسدون ولستم تقدرون ان تنالوا. تخاصمون وتحاربون ولستم تمتلكون لانكم لا تطلبون"));
arasuperobject.biblemap.put("b59_4_3", new arasuperobject(59,4,3,"تطلبون ولستم تأخذون لانكم تطلبون رديّا لكي تنفقوا في لذّاتكم"));
arasuperobject.biblemap.put("b59_4_4", new arasuperobject(59,4,4,"ايها الزناة والزواني أما تعلمون ان محبة العالم عداوة للّه. فمن اراد ان يكون محبا للعالم فقد صار عدوا للّه"));
arasuperobject.biblemap.put("b59_4_5", new arasuperobject(59,4,5,"ام تظنون ان الكتاب يقول باطلا. الروح الذي حل فينا يشتاق الى الحسد"));
arasuperobject.biblemap.put("b59_4_6", new arasuperobject(59,4,6,"ولكنه يعطي نعمة اعظم. لذلك يقول يقاوم الله المستكبرين واما المتواضعون فيعطيهم نعمة"));
arasuperobject.biblemap.put("b59_4_7", new arasuperobject(59,4,7,"فاخضعوا للّه. قاوموا ابليس فيهرب منكم"));
arasuperobject.biblemap.put("b59_4_8", new arasuperobject(59,4,8,"اقتربوا الى الله فيقترب اليكم. نقوا ايديكم ايها الخطاة وطهروا قلوبكم يا ذوي الرأيين"));
arasuperobject.biblemap.put("b59_4_9", new arasuperobject(59,4,9,"اكتئبوا ونوحوا وابكوا. ليتحول ضحككم الى نوح وفرحكم الى غم"));
arasuperobject.biblemap.put("b59_4_10", new arasuperobject(59,4,10,"اتضعوا قدام الرب فيرفعكم"));
arasuperobject.biblemap.put("b59_4_11", new arasuperobject(59,4,11,"لا يذم بعضكم بعضا ايها الاخوة. الذي يذم اخاه ويدين اخاه يذم الناموس ويدين الناموس. وان كنت تدين الناموس فلست عاملا بالناموس بل ديانا له"));
arasuperobject.biblemap.put("b59_4_12", new arasuperobject(59,4,12,"واحد هو واضع الناموس القادر ان يخلّص ويهلك. فمن انت يا من تدين غيرك"));
arasuperobject.biblemap.put("b59_4_13", new arasuperobject(59,4,13,"هلم الآن ايها القائلون نذهب اليوم او غدا الى هذه المدينة او تلك وهناك نصرف سنة واحدة ونتجر ونربح"));
arasuperobject.biblemap.put("b59_4_14", new arasuperobject(59,4,14,"انتم الذين لا تعرفون امر الغد. لانه ما هي حياتكم. انها بخار يظهر قليلا ثم يضمحل"));
arasuperobject.biblemap.put("b59_4_15", new arasuperobject(59,4,15,"عوض ان تقولوا ان شاء الرب وعشنا نفعل هذا او ذاك"));
arasuperobject.biblemap.put("b59_4_16", new arasuperobject(59,4,16,"واما الآن فانكم تفتخرون في تعظمكم. كل افتخار مثل هذا رديء"));
arasuperobject.biblemap.put("b59_4_17", new arasuperobject(59,4,17,"فمن يعرف ان يعمل حسنا ولا يعمل فذلك خطية له"));
arasuperobject.biblemap.put("b59_5_1", new arasuperobject(59,5,1,"هلم الآن ايها الاغنياء ابكوا مولولين على شقاوتكم القادمة"));
arasuperobject.biblemap.put("b59_5_2", new arasuperobject(59,5,2,"غناكم قد تهرأ وثيابكم قد اكلها العث"));
arasuperobject.biblemap.put("b59_5_3", new arasuperobject(59,5,3,"ذهبكم وفضتكم قد صدئا وصدأهما يكون شهادة عليكم ويأكل لحومكم كنار. قد كنزتم في الايام الاخيرة"));
arasuperobject.biblemap.put("b59_5_4", new arasuperobject(59,5,4,"هوذا اجرة الفعلة الذين حصدوا حقولكم المبخوسة منكم تصرخ وصياح الحصادين قد دخل الى أذني رب الجنود"));
arasuperobject.biblemap.put("b59_5_5", new arasuperobject(59,5,5,"قد ترفهتم على الارض وتنعمتم وربيتم قلوبكم كما في يوم الذبح"));
arasuperobject.biblemap.put("b59_5_6", new arasuperobject(59,5,6,"حكمتم على البار. قتلتموه. لا يقاومكم"));
arasuperobject.biblemap.put("b59_5_7", new arasuperobject(59,5,7,"فتأنوا ايها الاخوة الى مجيء الرب. هوذا الفلاح ينتظر ثمر الارض الثمين متأنيا عليه حتى ينال المطر المبكر والمتأخر"));
arasuperobject.biblemap.put("b59_5_8", new arasuperobject(59,5,8,"فتأنوا انتم وثبتوا قلوبكم لان مجيء الرب قد اقترب"));
arasuperobject.biblemap.put("b59_5_9", new arasuperobject(59,5,9,"لا يئن بعضكم على بعض ايها الاخوة لئلا تدانوا. هوذا الديان واقف قدام الباب"));
arasuperobject.biblemap.put("b59_5_10", new arasuperobject(59,5,10,"خذوا يا اخوتي مثالا لاحتمال المشقات والاناة الانبياء الذين تكلموا باسم الرب"));
arasuperobject.biblemap.put("b59_5_11", new arasuperobject(59,5,11,"ها نحن نطوّب الصابرين. قد سمعتم بصبر ايوب ورأيتم عاقبة الرب. لان الرب كثير الرحمة ورأوف"));
arasuperobject.biblemap.put("b59_5_12", new arasuperobject(59,5,12,"ولكن قبل كل شيء يا اخوتي لا تحلفوا لا بالسماء ولا بالارض ولا بقسم آخر. بل لتكن نعمكم نعم ولاكم لا لئلا تقعوا تحت دينونة"));
arasuperobject.biblemap.put("b59_5_13", new arasuperobject(59,5,13,"أعلى احد بينكم مشقات فليصلّ. أمسرور احد فليرتل"));
arasuperobject.biblemap.put("b59_5_14", new arasuperobject(59,5,14,"أمريض احد بينكم فليدع شيوخ الكنيسة فيصلّوا عليه ويدهنوه بزيت باسم الرب"));
arasuperobject.biblemap.put("b59_5_15", new arasuperobject(59,5,15,"وصلاة الايمان تشفي المريض والرب يقيمه وان كان قد فعل خطية تغفر له"));
arasuperobject.biblemap.put("b59_5_16", new arasuperobject(59,5,16,"اعترفوا بعضكم لبعض بالزلات وصلّوا بعضكم لاجل بعض لكي تشفوا. طلبة البار تقتدر كثيرا في فعلها"));
arasuperobject.biblemap.put("b59_5_17", new arasuperobject(59,5,17,"كان ايليا انسانا تحت الآلام مثلنا وصلّى صلاة ان لا تمطر فلم تمطر على الارض ثلاث سنين وستة اشهر"));
arasuperobject.biblemap.put("b59_5_18", new arasuperobject(59,5,18,"ثم صلّى ايضا فاعطت السماء مطرا واخرجت الارض ثمرها"));
arasuperobject.biblemap.put("b59_5_19", new arasuperobject(59,5,19,"ايها الاخوة ان ضل احد بينكم عن الحق فردّه احد"));
arasuperobject.biblemap.put("b59_5_20", new arasuperobject(59,5,20,"فليعلم ان من رد خاطئا عن ضلال طريقه يخلّص نفسا من الموت ويستر كثرة من الخطايا ٍ"));

