arasuperobject.biblemap.put("b6_16_10", new arasuperobject(6,16,10,"فلم يطردوا الكنعانيين الساكنين في جازر. فسكن الكنعانيون في وسط افرايم الى هذا اليوم وكانوا عبيدا تحت الجزية"));
arasuperobject.biblemap.put("b6_17_1", new arasuperobject(6,17,1,"وكانت القرعة لسبط منسّى. لانه هو بكر يوسف. لماكير بكر منسّى ابي جلعاد لانه كان رجل حرب وكانت جلعاد وباشان له"));
arasuperobject.biblemap.put("b6_17_2", new arasuperobject(6,17,2,"وكانت لبني منسّى الباقين حسب عشائرهم. لبني ابيعزر ولبني حالق ولبني اسريئيل ولبني شكم ولبني حافر ولبني شميداع هؤلاء هم بنو منسّى بن يوسف الذكور حسب عشائرهم"));
arasuperobject.biblemap.put("b6_17_3", new arasuperobject(6,17,3,"واما صلفحاد بن حافر بن جلعاد بن ماكير بن منسّى فلم يكن له بنون بل بنات. وهذه اسماء بناته محلة ونوعة وحجلة وملكة وترصة"));
arasuperobject.biblemap.put("b6_17_4", new arasuperobject(6,17,4,"فتقدمن امام العازار الكاهن وامام يشوع بن نون وامام الرؤساء وقلن. الرب أمر موسى ان يعطينا نصيبا بين اخوتنا. فاعطاهنّ حسب قول الرب نصيبا بين اخوة ابيهنّ"));
arasuperobject.biblemap.put("b6_17_5", new arasuperobject(6,17,5,"فاصاب منسّى عشر حصص ما عدا ارض جلعاد وباشان التي في عبر الاردن"));
arasuperobject.biblemap.put("b6_17_6", new arasuperobject(6,17,6,"لان بنات منسّى اخذن نصيبا بين بنيه وكانت ارض جلعاد لبني منسّى الباقين"));
arasuperobject.biblemap.put("b6_17_7", new arasuperobject(6,17,7,"وكان تخم منسّى من اشير الى المكمتة التي مقابل شكيم وامتد التخم نحو اليمين الى سكان عين تفوح"));
arasuperobject.biblemap.put("b6_17_8", new arasuperobject(6,17,8,"كان لمنسّى ارض تفوح. واما تفوح الى تخم منسّى هي لبني افرايم"));
arasuperobject.biblemap.put("b6_17_9", new arasuperobject(6,17,9,"ونزل التخم الى وادي قانة جنوبي الوادي. هذه مدن افرايم بين مدن منسّى. وتخم منسّى شمالي الوادي وكانت مخارجه عند البحر"));
arasuperobject.biblemap.put("b6_17_10", new arasuperobject(6,17,10,"من الجنوب لافرايم ومن الشمال لمنسّى وكان البحر تخمه. ووصل الى اشير شمالا والى يساكر نحو الشروق"));
arasuperobject.biblemap.put("b6_17_11", new arasuperobject(6,17,11,"وكان لمنسّى في يساكر وفي اشير بيت شان وقراها ويبلعام وقراها وسكان دور وقراها وسكان عين دور وقراها وسكان تعنك وقراها وسكان مجدّو وقراها المرتفعات الثلاث"));
arasuperobject.biblemap.put("b6_17_12", new arasuperobject(6,17,12,"ولم يقدر بنو منسّى ان يملكوا هذه المدن فعزم الكنعانيون على السكن في تلك الارض"));
arasuperobject.biblemap.put("b6_17_13", new arasuperobject(6,17,13,"وكان لما تشدد بنو اسرائيل انهم جعلوا الكنعانيين تحت الجزية ولم يطردوهم طردا"));
arasuperobject.biblemap.put("b6_17_14", new arasuperobject(6,17,14,"وكلم بنو يوسف يشوع قائلين. لماذا اعطيتني قرعة واحدة وحصّة واحدة نصيبا وانا شعب عظيم لانه الى الآن قد باركني الرب"));
arasuperobject.biblemap.put("b6_17_15", new arasuperobject(6,17,15,"فقال لهم يشوع ان كنت شعبا عظيما فاصعد الى الوعر واقطع لنفسك هناك في ارض الفرزّيين والرفائيين. اذا ضاق عليك جبل افرايم"));
arasuperobject.biblemap.put("b6_17_16", new arasuperobject(6,17,16,"فقال بنو يوسف لا يكفينا الجبل. ولجميع الكنعانيين الساكنين في ارض الوادي مركبات حديد. للذين في بيت شان وقراها وللذين في وادي يزرعيل"));
arasuperobject.biblemap.put("b6_17_17", new arasuperobject(6,17,17,"فكلم يشوع بيت يوسف افرايم ومنسّى قائلا. انت شعب عظيم ولك قوة عظيمة لا تكون لك قرعة واحدة"));
arasuperobject.biblemap.put("b6_17_18", new arasuperobject(6,17,18,"بل يكون لك الجبل لانه وعر فتقطعه وتكون لك مخارجه. فتطرد الكنعانيين لان لهم مركبات حديد لانهم اشداء"));
arasuperobject.biblemap.put("b6_18_1", new arasuperobject(6,18,1,"واجتمع كل جماعة بني اسرائيل في شيلوه ونصبوا هناك خيمة الاجتماع. وأخضعت الارض قدامهم"));
arasuperobject.biblemap.put("b6_18_2", new arasuperobject(6,18,2,"وبقي من بني اسرائيل ممن لم يقسموا نصيبهم سبعة اسباط"));
arasuperobject.biblemap.put("b6_18_3", new arasuperobject(6,18,3,"فقال يشوع لبني اسرائيل حتى متى انتم متراخون عن الدخول لامتلاك الارض التي اعطاكم اياها الرب اله آبائكم"));
arasuperobject.biblemap.put("b6_18_4", new arasuperobject(6,18,4,"هاتوا ثلاثة رجال من كل سبط فارسلهم فيقوموا ويسيروا في الارض ويكتبوها بحسب انصبتهم ثم يأتوا اليّ"));
arasuperobject.biblemap.put("b6_18_5", new arasuperobject(6,18,5,"وليقسموها الى سبعة اقسام فيقيم يهوذا على تخمه من الجنوب ويقيم بيت يوسف على تخمهم من الشمال"));
arasuperobject.biblemap.put("b6_18_6", new arasuperobject(6,18,6,"وانتم تكتبون الارض سبعة اقسام ثم تأتون اليّ هنا فالقي لكم قرعة ههنا امام الرب الهنا"));
arasuperobject.biblemap.put("b6_18_7", new arasuperobject(6,18,7,"لانه ليس للاويين قسم في وسطكم لان كهنوت الرب هو نصيبهم وجاد ورأوبين ونصف سبط منسّى قد اخذوا نصيبهم في عبر الاردن نحو الشروق الذي اعطاهم اياه موسى عبد الرب"));
arasuperobject.biblemap.put("b6_18_8", new arasuperobject(6,18,8,"فقام الرجال وذهبوا. واوصى يشوع الذاهبين لكتابة الارض قائلا. اذهبوا وسيروا في الارض واكتبوها ثم ارجعوا اليّ فالقي لكم هنا قرعة امام الرب في شيلوه"));
arasuperobject.biblemap.put("b6_18_9", new arasuperobject(6,18,9,"فسار الرجال وعبروا في الارض وكتبوها حسب المدن سبعة اقسام في سفر ثم جاءوا الى يشوع الى المحلّة في شيلوه"));
arasuperobject.biblemap.put("b6_18_10", new arasuperobject(6,18,10,"فالقى لهم يشوع قرعة في شيلوه امام الرب وهناك قسم يشوع الارض لبني اسرائيل حسب فرقهم"));
arasuperobject.biblemap.put("b6_18_11", new arasuperobject(6,18,11,"وطلعت قرعة سبط بني بنيامين حسب عشائرهم. وخرج تخم قرعتهم بين بني يهوذا وبني يوسف"));
arasuperobject.biblemap.put("b6_18_12", new arasuperobject(6,18,12,"وكان تخمهم من جهة الشمال من الاردن. وصعد التخم الى جانب اريحا من الشمال وصعد في الجبل غربا وكانت مخارجه عند برية بيت آون"));
arasuperobject.biblemap.put("b6_18_13", new arasuperobject(6,18,13,"وعبر التخم من هناك الى لوز الى جانب لوز الجنوبي. هي بيت ايل. ونزل التخم الى عطاروت ادّار على الجبل الذي الى جنوب بيت حورون السفلى"));
arasuperobject.biblemap.put("b6_18_14", new arasuperobject(6,18,14,"وامتد التخم ودار الى جهة الغرب جنوبا من الجبل الذي مقابل بيت حورون جنوبا. وكانت مخارجه عند قرية بعل. هي قرية يعاريم. مدينة لبني يهوذا. هذه هي جهة الغرب"));
arasuperobject.biblemap.put("b6_18_15", new arasuperobject(6,18,15,"وجهة الجنوب هي اقصى قرية يعاريم وخرج التخم غربا وخرج الى منبع مياه نفتوح"));
arasuperobject.biblemap.put("b6_18_16", new arasuperobject(6,18,16,"ونزل التخم الى طرف الجبل الذي مقابل وادي ابن هنوم الذي في وادي الرفائيين شمالا ونزل الى وادي هنوم الى جانب اليبوسيين من الجنوب ونزل الى عين روجل"));
arasuperobject.biblemap.put("b6_18_17", new arasuperobject(6,18,17,"وامتد من الشمال وخرج الى عين شمس وخرج الى جليلوت التي مقابل عقبة ادميم ونزل الى حجر بوهن بن رأوبين"));
arasuperobject.biblemap.put("b6_18_18", new arasuperobject(6,18,18,"وعبر الى الكتف مقابل العربة شمالا ونزل الى العربة"));
arasuperobject.biblemap.put("b6_18_19", new arasuperobject(6,18,19,"وعبر التخم الى جانب بيت حجلة شمالا وكانت مخارج التخم عند لسان بحر الملح شمالا الى طرف الاردن جنوبا هذا هو تخم الجنوب"));
arasuperobject.biblemap.put("b6_18_20", new arasuperobject(6,18,20,"والاردن يتخمه من جهة الشرق. فهذا هو نصيب بني بنيامين مع تخومه مستديرا حسب عشائرهم"));
arasuperobject.biblemap.put("b6_18_21", new arasuperobject(6,18,21,"وكانت مدن سبط بني بنيامين حسب عشائرهم اريحا وبيت حجلة ووادي قصيص"));
arasuperobject.biblemap.put("b6_18_22", new arasuperobject(6,18,22,"وبيت العربة وصمارايم وبيت ايل"));
arasuperobject.biblemap.put("b6_18_23", new arasuperobject(6,18,23,"والعوّيم والفارة وعفرة"));
arasuperobject.biblemap.put("b6_18_24", new arasuperobject(6,18,24,"وكفر العموني والعفني وجبع ست عشرة مدينة مع ضياعها"));
arasuperobject.biblemap.put("b6_18_25", new arasuperobject(6,18,25,"جبعون والرامة وبئيروت"));
arasuperobject.biblemap.put("b6_18_26", new arasuperobject(6,18,26,"والمصفاة والكفيرة والموصة"));
arasuperobject.biblemap.put("b6_18_27", new arasuperobject(6,18,27,"وراقم ويرفئيل وترالة"));
arasuperobject.biblemap.put("b6_18_28", new arasuperobject(6,18,28,"وصيلع وآلف واليبوسي. هي اورشليم. وجبعة وقرية. اربع عشرة مدينة مع ضياعها. هذا هو نصيب بني بنيامين حسب عشائرهم"));
arasuperobject.biblemap.put("b6_19_1", new arasuperobject(6,19,1,"وخرجت القرعة الثانية لشمعون لسبط بني شمعون حسب عشائرهم وكان نصيبهم داخل نصيب بني يهوذا"));
arasuperobject.biblemap.put("b6_19_2", new arasuperobject(6,19,2,"فكان لهم في نصيبهم بير سبع وشبع ومولادة"));
arasuperobject.biblemap.put("b6_19_3", new arasuperobject(6,19,3,"وحصر شوعال وبالة وعاصم"));
arasuperobject.biblemap.put("b6_19_4", new arasuperobject(6,19,4,"والتولد وبتول وحرمة"));
arasuperobject.biblemap.put("b6_19_5", new arasuperobject(6,19,5,"وصقلغ وبيت المركبوت وحصر سوسة"));
arasuperobject.biblemap.put("b6_19_6", new arasuperobject(6,19,6,"وبيت لباوت وشاروحين. ثلاث عشرة مدينة مع ضياعها"));
arasuperobject.biblemap.put("b6_19_7", new arasuperobject(6,19,7,"عين ورمون وعاتر وعاشان. اربع مدن مع ضياعها"));
arasuperobject.biblemap.put("b6_19_8", new arasuperobject(6,19,8,"وجميع الضياع التي حوالي هذه المدن الى بعلة بير رامة الجنوب. هذا هو نصيب سبط بني شمعون حسب عشائرهم"));
arasuperobject.biblemap.put("b6_19_9", new arasuperobject(6,19,9,"ومن قسم بني يهوذا كان نصيب بني شمعون. لان قسم بني يهوذا كان كثيرا عليهم فملك بنو شمعون داخل نصيبهم"));
arasuperobject.biblemap.put("b6_19_10", new arasuperobject(6,19,10,"وطلعت القرعة الثالثة لبني زبولون حسب عشائرهم. وكان تخم نصيبهم الى ساريد"));
arasuperobject.biblemap.put("b6_19_11", new arasuperobject(6,19,11,"وصعد تخمهم نحو الغرب ومرعلة ووصل الى دبّاشة ووصل الى الوادي الذي مقابل يقنعام"));
arasuperobject.biblemap.put("b6_19_12", new arasuperobject(6,19,12,"ودار من ساريد شرقا نحو شروق الشمس على تخم كسلوت تابور وخرج الى الدبرة وصعد الى يافيع"));
arasuperobject.biblemap.put("b6_19_13", new arasuperobject(6,19,13,"ومن هناك عبر شرقا نحو الشروق الى جتّ حافر الى عتّ قاصين وخرج الى رمّون وامتد الى نيعة"));
arasuperobject.biblemap.put("b6_19_14", new arasuperobject(6,19,14,"ودار بها التخم شمالا الى حناتون وكانت مخارجه عند وادي يفتحئيل"));
arasuperobject.biblemap.put("b6_19_15", new arasuperobject(6,19,15,"وقطّة ونهلال وشمرون ويدالة وبيت لحم. اثنتا عشرة مدينة مع ضياعها"));
arasuperobject.biblemap.put("b6_19_16", new arasuperobject(6,19,16,"هذا هو نصيب بني زبولون حسب عشائرهم. هذه المدن مع ضياعها"));
arasuperobject.biblemap.put("b6_19_17", new arasuperobject(6,19,17,"وخرجت القرعة الرابعة ليساكر. لبني يساكر حسب عشائرهم"));
arasuperobject.biblemap.put("b6_19_18", new arasuperobject(6,19,18,"وكان تخمهم الى يزرعيل والكسلوت وشونم"));
arasuperobject.biblemap.put("b6_19_19", new arasuperobject(6,19,19,"وحفارايم وشيئون واناحرة"));
arasuperobject.biblemap.put("b6_19_20", new arasuperobject(6,19,20,"ورّبيت وقشيون وآبص"));
arasuperobject.biblemap.put("b6_19_21", new arasuperobject(6,19,21,"ورمة وعين جنّيم وعين حدّة وبيت فصّيص"));
arasuperobject.biblemap.put("b6_19_22", new arasuperobject(6,19,22,"ووصل التخم الى تابور وشحصيمة وبيت شمس وكانت مخارج تخمهم عند الاردن. ست عشرة مدينة مع ضياعها"));
arasuperobject.biblemap.put("b6_19_23", new arasuperobject(6,19,23,"هذا هو نصيب بني يساكر حسب عشائرهم. المدن مع ضياعها"));
arasuperobject.biblemap.put("b6_19_24", new arasuperobject(6,19,24,"وخرجت القرعة الخامسة لسبط بني اشير حسب عشائرهم"));
arasuperobject.biblemap.put("b6_19_25", new arasuperobject(6,19,25,"وكان تخمهم حلقة وحلي وباطن واكشاف"));
arasuperobject.biblemap.put("b6_19_26", new arasuperobject(6,19,26,"وألّمّلك وعمعاد ومشآل ووصل الى كرمل غربا والى شيحور لبنة"));
arasuperobject.biblemap.put("b6_19_27", new arasuperobject(6,19,27,"ورجع نحو مشرق الشمس الى بيت داجون ووصل الى زبولون والى وادي يفتحئيل شمالي بيت العامق ونعيئيل وخرج الى كابول عن اليسار"));
arasuperobject.biblemap.put("b6_19_28", new arasuperobject(6,19,28,"وعبرون ورحوب وحمون وقانة الى صيدون العظيمة"));
arasuperobject.biblemap.put("b6_19_29", new arasuperobject(6,19,29,"ورجع التخم الى الرامة والى المدينة المحصّنة صور ثم رجع التخم الى حوصة وكانت مخارجه عند البحر في كورة اكزيب"));
arasuperobject.biblemap.put("b6_19_30", new arasuperobject(6,19,30,"وعمّة وافيق ورحوب. اثنتان وعشرون مدينة مع ضياعها"));
arasuperobject.biblemap.put("b6_19_31", new arasuperobject(6,19,31,"هذا هو نصيب سبط بني اشير حسب عشائرهم. هذه المدن مع ضياعها"));
arasuperobject.biblemap.put("b6_19_32", new arasuperobject(6,19,32,"لبني نفتالي خرجت القرعة السادسة. لبني نفتالي حسب عشائرهم"));
arasuperobject.biblemap.put("b6_19_33", new arasuperobject(6,19,33,"وكان تخمهم من حالف من البلوطة عند صعننيم وادامي الناقب ويبنئيل الى لقّوم. وكانت مخارجه عند الاردن"));
arasuperobject.biblemap.put("b6_19_34", new arasuperobject(6,19,34,"ورجع التخم غربا الى ازنوت تابور وخرج من هناك الى حقوق ووصل الى زبولون جنوبا ووصل الى اشير غربا والى يهوذا الاردن نحو شروق الشمس"));
arasuperobject.biblemap.put("b6_19_35", new arasuperobject(6,19,35,"ومدن محصّنة الصدّيم وصير وحمة ورقة وكنّارة"));
arasuperobject.biblemap.put("b6_19_36", new arasuperobject(6,19,36,"وادامة والرامة وحاصور"));
arasuperobject.biblemap.put("b6_19_37", new arasuperobject(6,19,37,"وقادش واذرعي وعين حاصور"));
arasuperobject.biblemap.put("b6_19_38", new arasuperobject(6,19,38,"ويرأون ومجدل ايل وحوريم وبيت عناة وبيت شمس تسع عشرة مدينة مع ضياعها"));
arasuperobject.biblemap.put("b6_19_39", new arasuperobject(6,19,39,"هذا هو نصيب سبط بني نفتالي حسب عشائرهم المدن مع ضياعها"));
arasuperobject.biblemap.put("b6_19_40", new arasuperobject(6,19,40,"لسبط بني دان حسب عشائرهم خرجت القرعة السابعة"));
arasuperobject.biblemap.put("b6_19_41", new arasuperobject(6,19,41,"وكان تخم نصيبهم صرعة واشتأول وعير شمس"));
arasuperobject.biblemap.put("b6_19_42", new arasuperobject(6,19,42,"وشعلبّين وأيلون ويتلة"));
arasuperobject.biblemap.put("b6_19_43", new arasuperobject(6,19,43,"وايلون وتمنة وعقرون"));
arasuperobject.biblemap.put("b6_19_44", new arasuperobject(6,19,44,"وإلتقيه وجبثون وبعلة"));
arasuperobject.biblemap.put("b6_19_45", new arasuperobject(6,19,45,"ويهود وبني برق وجتّ رمون"));
arasuperobject.biblemap.put("b6_19_46", new arasuperobject(6,19,46,"ومياه اليرقون والرقون مع التخوم التي مقابل يافا"));
arasuperobject.biblemap.put("b6_19_47", new arasuperobject(6,19,47,"وخرج تخم بني دان منهم وصعد بنو دان وحاربوا لشم واخذوها وضربوها بحد السيف وملكوها وسكنوها ودعو لشم دان كاسم دان ابيهم"));
