arasuperobject.biblemap.put("b6_21_1", new arasuperobject(6,21,1,"ثم تقدم رؤساء آباء اللاويين الى العازار الكاهن والى يشوع بن نون والى رؤساء آباء اسباط بني اسرائيل"));
arasuperobject.biblemap.put("b6_21_2", new arasuperobject(6,21,2,"وكلموهم في شيلوه في ارض كنعان قائلين. قد أمر الرب على يد موسى ان نعطى مدنا للسكن مع مسارحها لبهائمنا"));
arasuperobject.biblemap.put("b6_21_3", new arasuperobject(6,21,3,"فاعطى بنو اسرائيل اللاويين من نصيبهم حسب قول الرب هذه المدن مع مسارحها"));
