arasuperobject.biblemap.put("b6_22_27", new arasuperobject(6,22,27,"بل ليكون هو شاهدا بيننا وبينكم وبين اجيالنا بعدنا لكي نخدم خدمة الرب امامه بمحرقاتنا وذبائحنا وذبائح سلامتنا ولا يقول بنوكم غدا لبنينا ليس لكم قسم في الرب"));
arasuperobject.biblemap.put("b6_22_28", new arasuperobject(6,22,28,"وقلنا يكون متى قالوا كذا لنا ولاجيالنا غدا اننا نقول. انظروا شبه مذبح الرب الذي عمل آباؤنا لا للمحرقة ولا للذبيحة بل هو شاهد بيننا وبينكم"));
arasuperobject.biblemap.put("b6_22_29", new arasuperobject(6,22,29,"حاشا لنا منه ان نتمرد على الرب ونرجع اليوم عن الرب لبناء مذبح للمحرقة او التقدمة او الذبيحة عدا مذبح الرب الهنا الذي هو قدام مسكنه"));
arasuperobject.biblemap.put("b6_22_30", new arasuperobject(6,22,30,"فسمع فينحاس الكاهن ورؤساء الجماعة ورؤوس الوف اسرائيل الذين معه الكلام الذي تكلم به بنو رأوبين وبنو جاد وبنو منسّى فحسن في اعينهم"));
arasuperobject.biblemap.put("b6_22_31", new arasuperobject(6,22,31,"فقال فينحاس بن العازار الكاهن لبني رأوبين وبني جاد وبني منسّى. اليوم علمنا ان الرب بيننا لانكم لم تخونوا الرب بهذه الخيانة. فالآن قد انقذتم بني اسرائيل من يد الرب"));
arasuperobject.biblemap.put("b6_22_32", new arasuperobject(6,22,32,"ثم رجع فينحاس بن العازار الكاهن والرؤساء من عند بني رأوبين وبني جاد من ارض جلعاد الى ارض كنعان الى بني اسرائيل وردوا عليهم خبرا"));
arasuperobject.biblemap.put("b6_22_33", new arasuperobject(6,22,33,"فحسن الامر في اعين بني اسرائيل وبارك بنو اسرائيل الله ولم يفتكروا بالصعود اليهم للحرب وتخريب الارض التي كان بنو رأوبين وبنو جاد ساكنين بها. جاد ساكنين بها"));
arasuperobject.biblemap.put("b6_22_34", new arasuperobject(6,22,34,"وسمّى بنو رأوبين وبنو جاد المذبح عيدا لانه شاهد بيننا ان الرب هو الله"));
arasuperobject.biblemap.put("b6_23_1", new arasuperobject(6,23,1,"وكان غبّ ايام كثيرة بعدما اراح الرب اسرائيل من اعدائهم حواليهم ان يشوع شاخ. تقدم في الايام"));
arasuperobject.biblemap.put("b6_23_2", new arasuperobject(6,23,2,"فدعا يشوع جميع اسرائيل وشيوخه ورؤساءه وقضاته وعرفاءه وقال لهم. انا قد شخت. تقدمت في الايام"));
arasuperobject.biblemap.put("b6_23_3", new arasuperobject(6,23,3,"وانتم قد رأيتم كل ما عمل الرب الهكم بجميع اولئك الشعوب من اجلكم. لان الرب الهكم هو المحارب عنكم"));
arasuperobject.biblemap.put("b6_23_4", new arasuperobject(6,23,4,"انظروا. قد قسمت لكم بالقرعة هؤلاء الشعوب الباقين ملكا حسب اسباطكم من الاردن وجميع الشعوب التي قرضتها والبحر العظيم نحو غروب الشمس"));
arasuperobject.biblemap.put("b6_23_5", new arasuperobject(6,23,5,"والرب الهكم هو ينفيهم من امامكم ويطردهم من قدامكم فتملكون ارضهم كما كلمكم الرب الهكم"));
arasuperobject.biblemap.put("b6_23_6", new arasuperobject(6,23,6,"فتشددوا جدا لتحفظوا وتعملوا كل المكتوب في سفر شريعة موسى حتى لا تحيدوا عنها يمينا او شمالا"));
arasuperobject.biblemap.put("b6_23_7", new arasuperobject(6,23,7,"حتى لا تدخلوا الى هؤلاء الشعوب اولئك الباقين معكم ولا تذكروا اسم آلهتهم ولا تحلفوا بها ولا تعبدوها ولا تسجدوا لها"));
arasuperobject.biblemap.put("b6_23_8", new arasuperobject(6,23,8,"ولكن الصقوا بالرب الهكم كما فعلتم الى هذا اليوم"));
arasuperobject.biblemap.put("b6_23_9", new arasuperobject(6,23,9,"قد طرد الرب من امامكم شعوبا عظيمة وقوية واما انتم فلم يقف احد قدامكم الى هذا اليوم"));
arasuperobject.biblemap.put("b6_23_10", new arasuperobject(6,23,10,"رجل واحد منكم يطرد الفا لان الرب الهكم هو المحارب عنكم كما كلمكم"));
arasuperobject.biblemap.put("b6_23_11", new arasuperobject(6,23,11,"فاحتفظوا جدا لانفسكم ان تحبوا الرب الهكم"));
arasuperobject.biblemap.put("b6_23_12", new arasuperobject(6,23,12,"ولكن اذا رجعتم ولصقتم ببقية هؤلاء الشعوب اولئك الباقين معكم وصاهرتموهم ودخلتم اليهم وهم اليكم"));
arasuperobject.biblemap.put("b6_23_13", new arasuperobject(6,23,13,"فاعلموا يقينا ان الرب الهكم لا يعود يطرد اولئك الشعوب من امامكم فيكونوا لكم فخا وشركا وسوطا على جوانبكم وشوكا في اعينكم حتى تبيدوا عن تلك الارض الصالحة التي اعطاكم اياها الرب الهكم"));
arasuperobject.biblemap.put("b6_23_14", new arasuperobject(6,23,14,"وها انا اليوم ذاهب في طريق الارض كلها. وتعلمون بكل قلوبكم وكل انفسكم انه لم تسقط كلمة واحدة من جميع الكلام الصالح الذي تكلم به الرب عنكم. الكل صار لكم. لم تسقط منه كلمة واحدة"));
arasuperobject.biblemap.put("b6_23_15", new arasuperobject(6,23,15,"ويكون كما انه اتى عليكم كل الكلام الصالح الذي تكلم به الرب الهكم عنكم كذلك يجلب عليكم الرب كل الكلام الرديء حتى يبيدكم عن هذه الارض الصالحة التي اعطاكم الرب الهكم"));
arasuperobject.biblemap.put("b6_23_16", new arasuperobject(6,23,16,"حينما تتعدون عهد الرب الهكم الذي امركم به وتسيرون وتعبدون آلهة اخرى وتسجدون لها يحمى غضب الرب عليكم فتبيدون سريعا عن الارض الصالحة التي اعطاكم"));
arasuperobject.biblemap.put("b6_24_1", new arasuperobject(6,24,1,"وجمع يشوع جميع اسباط اسرائيل الى شكيم. ودعا شيوخ اسرائيل ورؤساءهم وقضاتهم وعرفاءهم فمثلوا امام الرب"));
arasuperobject.biblemap.put("b6_24_2", new arasuperobject(6,24,2,"وقال يشوع لجميع الشعب. هكذا قال الرب اله اسرائيل. آباؤكم سكنوا في عبر النهر منذ الدهر. تارح ابو ابراهيم وابو ناحور وعبدوا آلهة اخرى"));
arasuperobject.biblemap.put("b6_24_3", new arasuperobject(6,24,3,"فأخذت ابراهيم اباكم من عبر النهر وسرت به في كل ارض كنعان واكثرت نسله واعطيته اسحق"));
arasuperobject.biblemap.put("b6_24_4", new arasuperobject(6,24,4,"واعطيت اسحق يعقوب وعيسو واعطيت عيسو جبل سعير ليملكه. واما يعقوب وبنوه فنزلوا الى مصر"));
arasuperobject.biblemap.put("b6_24_5", new arasuperobject(6,24,5,"وارسلت موسى وهرون وضربت مصر حسب ما فعلت في وسطها ثم اخرجتكم"));
arasuperobject.biblemap.put("b6_24_6", new arasuperobject(6,24,6,"فاخرجت آباءكم من مصر ودخلتم البحر وتبع المصريون آباءكم بمركبات وفرسان الى بحر سوف"));
arasuperobject.biblemap.put("b6_24_7", new arasuperobject(6,24,7,"فصرخوا الى الرب فجعل ظلاما بينكم وبين المصريين وجلب عليهم البحر فغطّاهم. ورأت اعينكم ما فعلت في مصر واقمتم في القفر اياما كثيرة"));
arasuperobject.biblemap.put("b6_24_8", new arasuperobject(6,24,8,"ثم أتيت بكم الى ارض الاموريين الساكنين في عبر الاردن فحاربوكم ودفعتهم بيدكم فملكتم ارضهم واهلكتهم من امامكم"));
arasuperobject.biblemap.put("b6_24_9", new arasuperobject(6,24,9,"وقام بالاق بن صفور ملك موآب وحارب اسرائيل وارسل ودعا بلعام بن بعور لكي يلعنكم"));
arasuperobject.biblemap.put("b6_24_10", new arasuperobject(6,24,10,"ولم اشأ ان اسمع لبلعام فبارككم بركة وانقذتكم من يده"));
arasuperobject.biblemap.put("b6_24_11", new arasuperobject(6,24,11,"ثم عبرتم الاردن واتيتم الى اريحا. فحاربكم اصحاب اريحا الاموريون والفرزّيون والكنعانيون والحثّيون والجرجاشيون والحويون واليبوسيون فدفعتهم بيدكم"));
arasuperobject.biblemap.put("b6_24_12", new arasuperobject(6,24,12,"وارسلت قدامكم الزنابير وطردتهم من امامكم اي ملكي الاموريين. لا بسيفك ولا بقوسك"));
arasuperobject.biblemap.put("b6_24_13", new arasuperobject(6,24,13,"واعطيتكم ارضا لم تتعبوا عليها ومدنا لم تبنوها وتسكنون بها ومن كروم وزيتون لم تغرسوها تاكلون"));
arasuperobject.biblemap.put("b6_24_14", new arasuperobject(6,24,14,"فالآن اخشوا الرب واعبدوه بكمال وامانة وانزعوا الآلهة الذين عبدهم آباؤكم في عبر النهر وفي مصر واعبدوا الرب"));
arasuperobject.biblemap.put("b6_24_15", new arasuperobject(6,24,15,"وان ساء في اعينكم ان تعبدوا الرب. فاختاروا لانفسكم اليوم من تعبدون ان كان الآلهة الذين عبدهم آباؤكم الذين في عبر النهر وان كان آلهة الاموريين الذين انتم ساكنون في ارضهم. واما انا وبيتي فنعبد الرب"));
arasuperobject.biblemap.put("b6_24_16", new arasuperobject(6,24,16,"فاجاب الشعب وقالوا حاشا لنا ان نترك الرب لنعبد آلهة اخرى"));
arasuperobject.biblemap.put("b6_24_17", new arasuperobject(6,24,17,"لان الرب الهنا هو الذي اصعدنا وآباءنا من ارض مصر من بيت العبودية والذي عمل امام اعيننا تلك الآيات العظيمة وحفظنا في كل الطريق التي سرنا فيها وفي جميع الشعوب الذين عبرنا في وسطهم"));
arasuperobject.biblemap.put("b6_24_18", new arasuperobject(6,24,18,"وطرد الرب من امامنا جميع الشعوب والاموريين الساكنين الارض. فنحن ايضا نعبد الرب لانه هو الهنا"));
arasuperobject.biblemap.put("b6_24_19", new arasuperobject(6,24,19,"فقال يشوع للشعب لا تقدرون ان تعبدوا الرب لانه اله قدوس واله غيور هو. لا يغفر ذنوبكم وخطاياكم"));
arasuperobject.biblemap.put("b6_24_20", new arasuperobject(6,24,20,"واذا تركتم الرب وعبدتم آلهة غريبة يرجع فيسيء اليكم ويفنيكم بعد ان احسن اليكم"));
arasuperobject.biblemap.put("b6_24_21", new arasuperobject(6,24,21,"فقال الشعب ليشوع لا. بل الرب نعبد"));
arasuperobject.biblemap.put("b6_24_22", new arasuperobject(6,24,22,"فقال يشوع للشعب انتم شهود على انفسكم انكم قد اخترتم لانفسكم الرب لتعبدوه. فقالوا نحن شهود"));
arasuperobject.biblemap.put("b6_24_23", new arasuperobject(6,24,23,"فالآن انزعوا الآلهة الغريبة التي في وسطكم واميلوا قلوبكم الى الرب اله اسرائيل"));
arasuperobject.biblemap.put("b6_24_24", new arasuperobject(6,24,24,"فقال الشعب ليشوع. الرب الهنا نعبد ولصوته نسمع"));
arasuperobject.biblemap.put("b6_24_25", new arasuperobject(6,24,25,"وقطع يشوع عهدا للشعب في ذلك اليوم وجعل لهم فريضة وحكما في شكيم"));
arasuperobject.biblemap.put("b6_24_26", new arasuperobject(6,24,26,"وكتب يشوع هذا الكلام في سفر شريعة الله. واخذ حجرا كبيرا ونصبه هناك تحت البلوطة التي عند مقدس الرب"));
arasuperobject.biblemap.put("b6_24_27", new arasuperobject(6,24,27,"ثم قال يشوع لجميع الشعب. ان هذا الحجر يكون شاهدا علينا لانه قد سمع كل كلام الرب الذي كلمنا به فيكون شاهدا عليكم لئلا تجحدوا الهكم"));
arasuperobject.biblemap.put("b6_24_28", new arasuperobject(6,24,28,"ثم صرف يشوع الشعب كل واحد الى ملكه"));
arasuperobject.biblemap.put("b6_24_29", new arasuperobject(6,24,29,"وكان بعد هذا الكلام انه مات يشوع بن نون عبد الرب ابن مئة وعشر سنين"));
arasuperobject.biblemap.put("b6_24_30", new arasuperobject(6,24,30,"فدفنوه في تخم ملكه في تمنة سارح التي في جبل افرايم شمالي جبل جاعش"));
arasuperobject.biblemap.put("b6_24_31", new arasuperobject(6,24,31,"وعبد اسرائيل الرب كل ايام يشوع وكل ايام الشيوخ الذين طالت ايامهم بعد يشوع والذين عرفوا كل عمل الرب الذي عمله لاسرائيل"));
arasuperobject.biblemap.put("b6_24_32", new arasuperobject(6,24,32,"وعظام يوسف التي اصعدها بنو اسرائيل من مصر دفنوها في شكيم في قطعة الحقل التي اشتراها يعقوب من بني حمور ابي شكيم بمئة قسيطة فصارت لبني يوسف ملكا"));
arasuperobject.biblemap.put("b6_24_33", new arasuperobject(6,24,33,"ومات العازار بن هرون فدفنوه في جبعة فينحاس ابنه التي أعطيت له في جبل افرايم"));

