arasuperobject.biblemap.put("b60_3_4", new arasuperobject(60,3,4,"بل انسان القلب الخفي في العديمة الفساد زينة الروح الوديع الهادئ الذي هو قدام الله كثير الثمن"));
arasuperobject.biblemap.put("b60_3_5", new arasuperobject(60,3,5,"فانه هكذا كانت قديما النساء القديسات ايضا المتوكلات على الله يزيّن انفسهن خاضعات لرجالهن"));
arasuperobject.biblemap.put("b60_3_6", new arasuperobject(60,3,6,"كما كانت سارة تطيع ابراهيم داعية اياه سيدها. التي صرتنّ اولادها صانعات خيرا وغير خائفات خوفا البتة"));
arasuperobject.biblemap.put("b60_3_7", new arasuperobject(60,3,7,"كذلكم ايها الرجال كونوا ساكنين بحسب الفطنة مع الاناء النسائي كالاضعف معطين اياهنّ كرامة كالوارثات ايضا معكم نعمة الحياة لكي لا تعاق صلواتكم"));
arasuperobject.biblemap.put("b60_3_8", new arasuperobject(60,3,8,"والنهاية كونوا جميعا متحدي الرأي بحس واحد ذوي محبة اخوية مشفقين لطفاء"));
arasuperobject.biblemap.put("b60_3_9", new arasuperobject(60,3,9,"غير مجازين عن شر بشر او عن شتيمة بشتيمة بل بالعكس مباركين عالمين انكم لهذا دعيتم لكي ترثوا بركة"));
arasuperobject.biblemap.put("b60_3_10", new arasuperobject(60,3,10,"لان من اراد ان يحب الحياة ويرى اياما صالحة فليكفف لسانه عن الشر وشفتيه ان تتكلما بالمكر"));
arasuperobject.biblemap.put("b60_3_11", new arasuperobject(60,3,11,"ليعرض عن الشر ويصنع الخير ليطلب السلام ويجدّ في اثره"));
arasuperobject.biblemap.put("b60_3_12", new arasuperobject(60,3,12,"لان عيني الرب على الابرار واذنيه الى طلبتهم. ولكن وجه الرب ضد فاعلي الشر"));
