arasuperobject.biblemap.put("b60_3_15", new arasuperobject(60,3,15,"بل قدسوا الرب الاله في قلوبكم مستعدين دائما لمجاوبة كل من يسألكم عن سبب الرجاء الذي فيكم بوداعة وخوف"));
arasuperobject.biblemap.put("b60_3_16", new arasuperobject(60,3,16,"ولكم ضمير صالح لكي يكون الذين يشتمون سيرتكم الصالحة في المسيح يخزون في ما يفترون عليكم كفاعلي شر"));
arasuperobject.biblemap.put("b60_3_17", new arasuperobject(60,3,17,"لان تألمكم ان شاءت مشيئة الله وانتم صانعون خيرا افضل منه وانتم صانعون شرا"));
arasuperobject.biblemap.put("b60_3_18", new arasuperobject(60,3,18,"فان المسيح ايضا تألم مرة واحدة من اجل الخطايا البار من اجل الاثمة لكي يقربنا الى الله مماتا في الجسد ولكن محيى في الروح"));
arasuperobject.biblemap.put("b60_3_19", new arasuperobject(60,3,19,"الذي فيه ايضا ذهب فكرز للارواح التي في السجن"));
